-- Migración 18: Seed de Objetivos de Aprendizaje - Lengua y Literatura  
-- Descripción: Carga de 23 OAs del currículo oficial chileno (Primero Medio)
-- Fecha: 2025-11-22

-- ============================================================================
-- OBJETIVOS DE APRENDIZAJE (OAs) - LENGUA Y LITERATURA
-- ============================================================================


-- ============================================================================
-- VERIFICACIÓN
-- ============================================================================

DO $$
DECLARE
    oas_count INTEGER;
BEGIN
    SELECT COUNT(*) INTO oas_count FROM objetivos_aprendizaje WHERE materia_id = 2;

    IF oas_count != 23 THEN
        RAISE EXCEPTION 'Error en verificación: se esperaban 23 OAs de Lengua, pero hay %', oas_count;
    END IF;

    RAISE NOTICE 'Seed completado: 23 OAs de Lengua y Literatura cargados correctamente';
END $$;
