INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (1, 34, 'multiple_choice', 'diagnostico', '{"opciones": {"A": "Valparaiso", "B": "Santiago", "C": "Concepcion", "D": "Antofagasta"}, "pregunta": "Cual es la capital de Chile?", "explicacion": "Santiago es la capital de Chile desde 1541"}', '{"respuesta_correcta": "B"}', 2, 2, true, '{geografia,chile}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (2, 34, 'multiple_choice', 'diagnostico', '{"opciones": {"A": "Valparaiso", "B": "Santiago", "C": "Concepcion", "D": "Antofagasta"}, "pregunta": "Cual es la capital de Chile?", "explicacion": "Santiago es la capital de Chile desde 1541"}', '{"respuesta_correcta": "B"}', 2, 2, true, '{geografia,chile}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (3, 34, 'multiple_choice', 'diagnostico', '{"opciones": {"A": "Valparaiso", "B": "Santiago", "C": "Concepcion", "D": "Antofagasta"}, "pregunta": "Cual es la capital de Chile?", "explicacion": "Santiago es la capital de Chile desde 1541"}', '{"respuesta_correcta": "B"}', 2, 2, true, '{geografia,chile}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (33, 13, 'multiple_choice', 'all', '{"opciones": {"A": "Anotar todos los detalles y ejemplos que aparecen en el texto.", "B": "Seleccionar las ideas que resumen el contenido central del texto.", "C": "Copiar solo las palabras que no entiendas para buscarlas después.", "D": "Listar las partes que te parecen menos importantes."}, "pregunta": "Después de escuchar o leer un texto, ¿cuál es la mejor manera de identificar las ideas principales?", "explicacion": "La opción B es correcta porque las ideas principales son aquellas que resumen el contenido central del texto y ayudan a entender el mensaje global. La opción A es incorrecta porque anotar todos los detalles incluye información secundaria, lo que dificulta identificar lo esencial. La opción C no ayuda a identificar ideas principales, sino a comprender vocabulario. La opción D es errónea porque las partes menos importantes no reflejan el núcleo del texto."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"identificación de ideas principales","lectura comprensiva","nivel recordar"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (34, 13, 'multiple_choice', 'all', '{"opciones": {"A": "Seleccionar todas las palabras difíciles para entender mejor el texto", "B": "Buscar las oraciones que resumen lo más importante del texto", "C": "Copiar todas las frases textuales sin distinguir su importancia", "D": "Leer rápidamente sin detenerse en ningún detalle"}, "pregunta": "Después de leer un texto, ¿cuál es la mejor forma de identificar las ideas principales para luego listarlas?", "explicacion": "La respuesta correcta es la opción B porque identificar las oraciones que resumen lo más importante permite distinguir las ideas principales del texto. La opción A es un error común, ya que enfocarse solo en palabras difíciles no asegura comprender las ideas principales. La opción C es incorrecta porque copiar frases sin seleccionar no ayuda a sintetizar ni ordenar la información. La opción D es un error frecuente, ya que leer sin prestar atención dificulta la identificación de ideas principales."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{identificación,"ideas principales","lectura guiada"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (124, 31, 'multiple_choice', 'all', '{"opciones": {"A": "El lugar donde se desarrolla la historia", "B": "El problema o desafío que enfrentan los personajes", "C": "La descripción física de los personajes", "D": "El tipo de narrador que cuenta la historia"}, "pregunta": "¿Cuál de los siguientes elementos corresponde al conflicto principal en una narración?", "explicacion": "La respuesta correcta es B, porque el conflicto principal es el problema o desafío que enfrentan los personajes en la narración, lo que impulsa la trama. La opción A es un error común, ya que el lugar es un elemento del escenario, no el conflicto. La opción C confunde características de los personajes con el conflicto. La opción D se refiere al narrador, que es otro elemento narrativo, pero no el conflicto."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{conflicto,"elementos narrativos","primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (35, 13, 'multiple_choice', 'all', '{"opciones": {"A": "Pablo Neruda vivió en varios países y escribió poemas sobre la naturaleza y el amor.", "B": "Pablo Neruda tenía muchos amigos y disfrutaba de la música clásica.", "C": "Pablo Neruda prefería escribir novelas en lugar de poemas.", "D": "Pablo Neruda era un pintor famoso durante el siglo XX."}, "pregunta": "Después de leer un texto sobre la vida de Pablo Neruda, ¿cuál de las siguientes opciones corresponde a una idea principal del texto?", "explicacion": "La opción A es correcta porque resume la información clave sobre Pablo Neruda: su vida en varios países y su obra poética centrada en la naturaleza y el amor, que suelen ser ideas principales en textos biográficos. La opción B es incorrecta porque habla de amigos y música clásica, detalles no centrales para un texto sobre su vida y obra. La opción C es errónea porque Neruda es conocido por su poesía, no por escribir novelas. La opción D es un error conceptual ya que Neruda fue un poeta y no un pintor."}', '{"respuesta_correcta": "A"}', 3, 0, true, '{"Lengua y Literatura","Identificación de ideas principales","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (36, 13, 'true_false', 'all', '{"afirmacion": "Las ideas principales de un texto son las que resumen el mensaje o el tema central del contenido leído o escuchado.", "explicacion": "Esta afirmación es verdadera porque las ideas principales son aquellas que capturan el mensaje o tema central del texto, ayudando a sintetizar y comprender su contenido esencial."}', '{"es_verdadero": true}', 1, 0, true, '{"ideas principales","comprensión lectora",síntesis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (37, 13, 'fill_blanks', 'all', '{"texto": "Para comprender un texto, es fundamental identificar las [BLANK_1] principales y luego [BLANK_2] estas ideas en un orden lógico para facilitar su estudio o recuerdo.", "pistas": ["Estas son las ideas más importantes que resumen el contenido del texto.", "Esta acción implica registrar y organizar la información para facilitar su uso."], "explicacion": "Identificar las ideas principales y ordenarlas adecuadamente permite sintetizar la información de un texto para diversos propósitos, como estudiar o investigar."}', '{"respuestas_correctas": {"BLANK_1": ["ideas", "ideas principales"], "BLANK_2": ["ordenar", "organizar"]}}', 2, 0, true, '{lectura,comprensión,síntesis,"primeros medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (38, 14, 'multiple_choice', 'all', '{"opciones": {"A": "El reciclaje es una moda que solo algunos países siguen para cuidar el medio ambiente.", "B": "El reciclaje ayuda a reducir la contaminación al reutilizar materiales y proteger la naturaleza.", "C": "El reciclaje es un proceso complicado que requiere muchas máquinas y no es útil para la mayoría de las personas.", "D": "El reciclaje consiste en tirar la basura en diferentes contenedores sin importar lo que contienen."}, "pregunta": "Después de escuchar un texto sobre la importancia del reciclaje, ¿cuál de las siguientes frases explica mejor la idea principal del texto con tus propias palabras?", "explicacion": "La opción B es correcta porque sintetiza claramente la idea principal del texto: el reciclaje contribuye a reducir la contaminación y proteger el medio ambiente mediante la reutilización de materiales. La opción A es incorrecta porque presenta el reciclaje como una moda limitada, minimizando su importancia. La opción C es un error conceptual que presenta el reciclaje como algo complicado y poco útil, lo que no es el mensaje principal. La opción D confunde el reciclaje con una acción superficial y poco precisa, sin capturar la razón detrás de la práctica."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"comprensión lectora",síntesis,"ideas principales",reciclaje}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (39, 14, 'multiple_choice', 'all', '{"opciones": {"A": "El reciclaje es una actividad que solo beneficia a las empresas grandes.", "B": "El reciclaje ayuda a reducir la contaminación y protege el medio ambiente.", "C": "Reciclar es solo una moda pasajera que no tiene efectos reales.", "D": "El reciclaje consiste en separar la basura sin importar qué tipo de materiales son."}, "pregunta": "Después de escuchar un texto sobre la importancia del reciclaje, ¿cuál de las siguientes opciones resume mejor la idea principal del texto?", "explicacion": "La respuesta correcta es B, ya que resume claramente la idea principal del texto: que el reciclaje contribuye a disminuir la contaminación y protege el medio ambiente. La opción A es incorrecta porque limita el beneficio del reciclaje a las empresas grandes, lo cual es un error conceptual común. La opción C minimiza la importancia del reciclaje, lo que contradice la idea principal. La opción D describe una acción relacionada con el reciclaje, pero no captura la idea principal del texto."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"comprensión lectora","idea principal",reciclaje,"primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (40, 14, 'true_false', 'all', '{"afirmacion": "Sintetizar las ideas principales de un texto implica identificar y explicar con tus propias palabras los puntos más importantes del contenido leído o escuchado.", "explicacion": "Esta afirmación es verdadera porque sintetizar significa resumir y organizar la información esencial del texto, lo que requiere comprender y expresar con palabras propias las ideas principales para facilitar la comprensión y el estudio."}', '{"es_verdadero": true}', 2, 0, true, '{síntesis,"comprensión lectora","ideas principales"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (41, 14, 'drag_drop_matching', 'all', '{"explicacion": "La ''Idea principal'' es la frase central del texto; el ''Resumen'' es una síntesis breve con solo lo esencial; el ''Detalle'' aporta información específica que apoya la idea principal; la ''Paráfrasis'' consiste en reexpresar el contenido con tus propias palabras; el ''Texto informativo'' entrega datos basados en hechos; y la ''Opinión'' refleja un juicio o punto de vista personal.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Frase que expresa el mensaje central de un texto", "Reexpresar con tus propias palabras lo que se entendió", "Información específica que apoya la idea principal", "Síntesis breve de un texto que incluye solo lo esencial", "Texto que entrega datos y explicaciones basadas en hechos", "Juicio personal o punto de vista del autor"], "columna_izquierda": ["Idea principal", "Resumen", "Detalle", "Paráfrasis", "Texto informativo", "Opinión"]}', '{"emparejamientos_correctos": {"Detalle": "Información específica que apoya la idea principal", "Resumen": "Síntesis breve de un texto que incluye solo lo esencial", "Opinión": "Juicio personal o punto de vista del autor", "Paráfrasis": "Reexpresar con tus propias palabras lo que se entendió", "Idea principal": "Frase que expresa el mensaje central de un texto", "Texto informativo": "Texto que entrega datos y explicaciones basadas en hechos"}}', 2, 0, true, '{comprensión,"lengua y literatura","primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (125, 31, 'true_false', 'all', '{"afirmacion": "El conflicto principal de una narración es el problema central que enfrentan los personajes y que impulsa la trama de la historia.", "explicacion": "Esta afirmación es verdadera porque el conflicto principal es el elemento que genera tensión y mueve la acción de la narración, permitiendo que la historia se desarrolle y que los personajes tomen decisiones."}', '{"es_verdadero": true}', 1, 0, true, '{conflicto,narración,"elementos básicos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (42, 14, 'sequencing', 'all', '{"explicacion": "El orden correcto comienza con la lectura completa para comprender el texto, luego se identifican las ideas principales, después se escriben esas ideas con palabras propias para sintetizar y finalmente se revisa el resumen para garantizar claridad y coherencia.", "instruccion": "Ordena los siguientes pasos para realizar un resumen de un texto leído.", "elementos_desordenados": ["Escribir las ideas principales con tus propias palabras.", "Leer el texto completo para entender su contenido.", "Identificar y subrayar las ideas principales del texto.", "Revisar el resumen para asegurarte de que sea claro y coherente."]}', '{"orden_correcto": ["Leer el texto completo para entender su contenido.", "Identificar y subrayar las ideas principales del texto.", "Escribir las ideas principales con tus propias palabras.", "Revisar el resumen para asegurarte de que sea claro y coherente."]}', 3, 0, true, '{"comprensión lectora",síntesis,resumen}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (43, 15, 'multiple_choice', 'all', '{"opciones": {"A": "Listar todos los detalles y ejemplos en orden de aparición sin relacionarlos entre sí.", "B": "Identificar las ideas principales como nodos y conectarlas con relaciones que expliquen cómo se interrelacionan.", "C": "Escribir un resumen largo que incluya todas las oraciones del texto sin dividirlas en categorías.", "D": "Copiar textualmente párrafos completos del texto sin hacer ninguna clasificación."}, "pregunta": "¿Cuál de las siguientes formas es la más adecuada para organizar las ideas principales de un texto leído sobre la contaminación ambiental en un mapa conceptual?", "explicacion": "La opción B es correcta porque un mapa conceptual debe mostrar las ideas principales como nodos conectados mediante relaciones que explican cómo se vinculan, facilitando la comprensión y síntesis del contenido. La opción A es incorrecta porque solo listar detalles sin conexiones no permite organizar ni relacionar las ideas principales. La opción C es errónea porque un resumen largo sin organizar no cumple con la función de un esquema o mapa conceptual. La opción D tampoco es adecuada porque copiar párrafos textuales no implica síntesis ni organización visual de las ideas."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{síntesis,"organización de ideas","mapa conceptual","OA 6611","Lengua y Literatura","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (44, 15, 'multiple_choice', 'all', '{"opciones": {"A": "Escribir todas las frases completas del texto en orden, sin destacar ideas principales ni secundarias.", "B": "Identificar las ideas principales y agruparlas en categorías relacionadas, conectándolas con palabras que expliquen su relación.", "C": "Listar solo los datos numéricos y estadísticas del texto, sin incluir explicaciones o causas.", "D": "Copiar los párrafos más largos y complejos tal como aparecen en el texto para conservar toda la información."}, "pregunta": "Después de leer un texto informativo sobre el cambio climático, ¿cuál es la mejor forma de organizar las ideas principales en un esquema o mapa conceptual?", "explicacion": "La opción B es correcta porque implica identificar y organizar las ideas principales en categorías relacionadas, utilizando conexiones que reflejan las relaciones entre ellas, lo que caracteriza un buen esquema o mapa conceptual. La opción A es incorrecta porque solo transcribe el texto sin organizar ni sintetizar la información. La opción C es un error común que consiste en enfocarse solo en datos específicos sin considerar su contexto o relación. La opción D refleja un malentendido sobre la síntesis, ya que copiar párrafos largos no ayuda a ordenar ni resumir las ideas principales."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"organización de ideas","mapa conceptual",síntesis,"lengua y literatura","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (45, 15, 'drag_drop_matching', 'all', '{"explicacion": "La idea principal es el punto central que se extrae del texto; los detalles secundarios apoyan esa idea; el resumen sintetiza las ideas importantes; el esquema organiza visualmente las ideas en forma jerárquica; el mapa conceptual muestra relaciones entre conceptos mediante nodos y conexiones; la opinión personal no es parte de la organización de ideas del texto.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Representa la información organizada en nodos y conexiones para mostrar relaciones", "Información que apoya o explica la idea principal", "Texto breve que sintetiza las ideas más importantes de un texto", "Frase o conjunto de frases que expresan el punto central de un texto", "Organización visual y jerárquica de ideas para facilitar la comprensión", "Juicio o valoración subjetiva sin base en hechos del texto"], "columna_izquierda": ["Idea principal", "Detalle secundario", "Esquema", "Mapa conceptual", "Resumen", "Distractor: Opinión personal"]}', '{"emparejamientos_correctos": {"Esquema": "Organización visual y jerárquica de ideas para facilitar la comprensión", "Resumen": "Texto breve que sintetiza las ideas más importantes de un texto", "Idea principal": "Frase o conjunto de frases que expresan el punto central de un texto", "Mapa conceptual": "Representa la información organizada en nodos y conexiones para mostrar relaciones", "Detalle secundario": "Información que apoya o explica la idea principal"}}', 3, 0, true, '{"organización de ideas","mapas conceptuales","primeros medios","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (46, 15, 'sequencing', 'all', '{"explicacion": "El orden correcto inicia con leer o escuchar el texto para comprenderlo, luego se identifica la idea principal, después se agrupan las ideas relacionadas para estructurar el contenido, y finalmente se dibuja el mapa conceptual para visualizar la organización de las ideas.", "instruccion": "Ordena los siguientes pasos para crear un mapa conceptual correctamente", "elementos_desordenados": ["Identificar la idea principal del texto", "Agrupar las ideas relacionadas bajo la idea principal", "Leer o escuchar el texto atentamente", "Dibujar el esquema o mapa conceptual con las ideas organizadas"]}', '{"orden_correcto": ["Leer o escuchar el texto atentamente", "Identificar la idea principal del texto", "Agrupar las ideas relacionadas bajo la idea principal", "Dibujar el esquema o mapa conceptual con las ideas organizadas"]}', 3, 0, true, '{"organización de ideas","mapa conceptual","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (56, 17, 'open_ended', 'all', '{"pregunta": "Luego de leer un texto informativo sobre un tema de tu interés, selecciona y ordena las ideas principales que consideras más relevantes para preparar un informe de investigación. Justifica por qué elegiste esas ideas y el orden en que las presentaste, explicando cómo cumplen con el propósito de estudiar o informar.", "explicacion": "El docente debe evaluar que el estudiante pueda identificar las ideas más relevantes para el propósito definido, organizarlas de manera lógica y coherente, y justificar sus decisiones con argumentos claros que muestren comprensión crítica del texto y del objetivo de la actividad.", "contexto_adicional": "El estudiante debe basar su respuesta en un texto informativo previamente leído en clase o de su propia elección, asegurándose de que el tema sea adecuado para un informe de investigación en Lengua y Literatura.", "criterios_evaluacion": ["Claridad y pertinencia en la selección de ideas principales relacionadas con el propósito (peso: 40%)", "Coherencia y lógica en el ordenamiento de las ideas para facilitar la comprensión y el objetivo del informe (peso: 35%)", "Justificación argumentada y fundamentada que explique la selección y el orden según el propósito específico (peso: 25%)"]}', '{"puntos_clave": ["Identificación clara de ideas principales relacionadas con el tema", "Organización lógica y coherente de las ideas seleccionadas", "Justificación argumentada que conecta la selección y el orden con el propósito del informe"], "respuesta_modelo": "Después de leer el texto sobre la historia del cine, seleccioné las ideas que explican primero el origen del cine, luego el desarrollo de las técnicas de filmación y finalmente la influencia cultural que ha tenido en distintas épocas. Elegí este orden porque permite entender la evolución histórica de manera cronológica, facilitando el aprendizaje progresivo del tema. Estas ideas son las más relevantes porque cubren los aspectos fundamentales para un informe que busca informar y analizar la importancia del cine. Justifico la selección porque omito detalles secundarios que no aportan directamente al propósito de estudiar la evolución y relevancia cultural del cine.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Primero Medio",Evaluar,Síntesis,"Pensamiento Crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (47, 15, 'open_ended', 'all', '{"pregunta": "Después de leer el texto proporcionado sobre la importancia de la conservación del agua, organiza las ideas principales en un mapa conceptual que refleje las causas, consecuencias y posibles soluciones al problema. Explica brevemente cómo estructuraste tu mapa y por qué ordenaste las ideas de esa manera.", "explicacion": "El docente debe evaluar si el estudiante identifica las ideas centrales, si las organiza de manera lógica en el mapa conceptual y si puede justificar la estructura empleada, evidenciando comprensión y aplicación de la información.", "contexto_adicional": "Se entrega un texto sobre la conservación del agua, donde se abordan causas del problema, consecuencias ambientales y sociales, y posibles soluciones.", "criterios_evaluacion": ["Claridad en la identificación y selección de las ideas principales del texto (peso: 30%)", "Organización coherente y lógica de las ideas en el mapa conceptual (peso: 40%)", "Capacidad para explicar la estructura y el orden elegido en el mapa, justificando sus decisiones (peso: 30%)"]}', '{"puntos_clave": ["Identificación de causas, consecuencias y soluciones como ideas principales", "Estructura clara con relaciones lógicas entre las ideas", "Justificación coherente del orden y la organización del mapa"], "respuesta_modelo": "El estudiante construye un mapa conceptual donde se identifica claramente como ideas principales: las causas del problema (como el uso excesivo y la contaminación), las consecuencias (escasez de agua, impacto en la biodiversidad) y las soluciones propuestas (uso racional, educación ambiental). Organiza el mapa con las causas en la parte superior, que conducen a las consecuencias en el centro, y las soluciones como respuesta en la parte inferior. Explica que eligió este orden porque refleja la relación causal y la respuesta al problema, facilitando la comprensión del texto.", "requiere_revision_humana": true}', 3, 0, true, '{"lengua y literatura","primeros medios","mapa conceptual","organización de ideas"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (48, 16, 'compare_contrast', 'all', '{"conceptos": ["Idea principal", "Idea secundaria"], "criterios": ["Función en el texto", "Nivel de detalle", "Importancia para la comprensión"], "explicacion": "Las ideas principales resumen el mensaje central del texto, mientras que las ideas secundarias aportan detalles o ejemplos que apoyan o explican las ideas principales. Ambas son esenciales, pero difieren en su función, nivel de detalle y grado de importancia para entender el texto.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Idea principal": {"Nivel de detalle": "General, poco detallado", "Función en el texto": "Resumir el mensaje central", "Importancia para la comprensión": "Alta, esencial para entender el texto"}, "Idea secundaria": {"Nivel de detalle": "Específico, detallado", "Función en el texto": "Apoyar o explicar ideas principales", "Importancia para la comprensión": "Complementaria, ayuda a profundizar"}}}', 3, 0, true, '{comparar,"ideas principales","ideas secundarias","análisis textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (49, 16, 'compare_contrast', 'all', '{"conceptos": ["Idea principal", "Idea secundaria"], "criterios": ["Importancia en el texto", "Nivel de detalle", "Función en la comprensión"], "explicacion": "La idea principal representa el mensaje central o el punto más importante del texto, mientras que las ideas secundarias ofrecen detalles o explicaciones que apoyan o amplían la idea principal. Ambas son esenciales para la comprensión, pero difieren en su nivel de importancia y detalle.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Idea principal": {"Nivel de detalle": "General, no entra en detalles específicos", "Importancia en el texto": "Es el mensaje central o tema principal del texto", "Función en la comprensión": "Guía la interpretación global del texto"}, "Idea secundaria": {"Nivel de detalle": "Específica, incluye datos, ejemplos o explicaciones", "Importancia en el texto": "Apoya y complementa la idea principal", "Función en la comprensión": "Amplía y clarifica la idea principal"}}}', 4, 0, true, '{comparar,"ideas principales","ideas secundarias","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (50, 16, 'open_ended', 'all', '{"pregunta": "Después de leer dos textos que abordan el mismo tema desde perspectivas diferentes, ¿cuáles son las ideas principales que comparten y en qué se diferencian? Analiza cómo estas similitudes y diferencias afectan la comprensión global del tema.", "explicacion": "El docente debe verificar que el estudiante haya reconocido correctamente las ideas principales de ambos textos, que establezca comparaciones claras y bien fundamentadas entre ellas, y que realice un análisis que muestre comprensión crítica del impacto de esas similitudes y diferencias en la visión general del tema.", "contexto_adicional": "Se proporcionan dos textos breves que tratan un mismo tema desde diferentes enfoques. Los estudiantes deben leerlos cuidadosamente antes de responder.", "criterios_evaluacion": ["Identificación precisa de las ideas principales en ambos textos (peso: 30%)", "Capacidad para comparar y contrastar las ideas principales señalando similitudes y diferencias claras (peso: 40%)", "Análisis crítico sobre cómo las similitudes y diferencias influyen en la comprensión del tema (peso: 30%)"]}', '{"puntos_clave": ["Identificación de la urgencia y causa humana del cambio climático en ambos textos", "Diferenciación entre el enfoque ambiental del primer texto y el social-económico del segundo", "Reconocimiento de cómo ambos enfoques complementan la comprensión global del problema"], "respuesta_modelo": "En ambos textos se aborda el tema del cambio climático. Ambos coinciden en que es un problema urgente causado principalmente por la actividad humana. Sin embargo, el primer texto enfatiza el impacto ambiental, destacando la pérdida de biodiversidad y el aumento de desastres naturales, mientras que el segundo se centra en las consecuencias económicas y sociales, como la migración y la pobreza. Estas diferencias amplían la comprensión del tema al mostrar que el cambio climático no solo afecta el medio ambiente, sino también a las sociedades y economías, lo que refuerza la necesidad de soluciones integrales.", "requiere_revision_humana": true}', 4, 0, true, '{análisis,comparación,"pensamiento crítico","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (51, 16, 'concept_map', 'all', '{"conceptos": ["Idea principal", "Idea secundaria", "Texto argumentativo", "Texto expositivo", "Comparación"], "explicacion": "Se espera que el estudiante conecte ''Idea principal'' como el núcleo de un texto, diferenciándola de ''Idea secundaria''. Además, debe relacionar ''Texto argumentativo'' y ''Texto expositivo'' como tipos de textos que contienen estas ideas, y utilizar ''Comparación'' para identificar similitudes y diferencias entre las ideas principales de ambos tipos.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de identificar y clasificar las ideas principales de diferentes tipos de textos?"}', '{"conceptos_centrales": ["Idea principal", "Comparación"], "relaciones_esperadas": [{"origen": "Texto argumentativo", "destino": "Idea principal", "relacion": "contiene"}, {"origen": "Texto expositivo", "destino": "Idea principal", "relacion": "contiene"}, {"origen": "Idea principal", "destino": "Idea secundaria", "relacion": "se diferencia de"}, {"origen": "Comparación", "destino": "Idea principal", "relacion": "permite identificar"}, {"origen": "Comparación", "destino": "Idea secundaria", "relacion": "permite identificar"}], "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Mapa Conceptual",Análisis,"Primer Año Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (52, 16, 'concept_map', 'all', '{"conceptos": ["Idea principal", "Idea secundaria", "Comparación", "Similitud", "Diferencia"], "explicacion": "Se espera que el estudiante identifique que la ''Idea principal'' se distingue de la ''Idea secundaria'', y que la ''Comparación'' sirve para encontrar ''Similitudes'' y ''Diferencias'' entre ideas principales de distintos textos, permitiendo así organizarlas y clasificarlas adecuadamente.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de analizar y sintetizar ideas principales de diferentes textos?"}', '{"conceptos_centrales": ["Idea principal", "Comparación"], "relaciones_esperadas": [{"origen": "Idea principal", "destino": "Idea secundaria", "relacion": "difiere de"}, {"origen": "Comparación", "destino": "Similitud", "relacion": "identifica"}, {"origen": "Comparación", "destino": "Diferencia", "relacion": "identifica"}, {"origen": "Similitud", "destino": "Idea principal", "relacion": "relaciona con"}, {"origen": "Diferencia", "destino": "Idea principal", "relacion": "contrasta con"}], "requiere_revision_humana": true}', 4, 0, true, '{mapa_conceptual,análisis,síntesis,lengua_y_literatura,primer_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (53, 17, 'criteria_evaluation', 'all', '{"rubrica": {"Orden y coherencia de las ideas": {"Excelente": "Ordena las ideas de forma lógica y coherente, facilitando la comprensión y el estudio del contenido.", "Satisfactorio": "Ordena las ideas de forma aceptable, aunque puede haber leves problemas de coherencia o secuencia.", "Necesita mejorar": "Ordena las ideas de manera desorganizada, dificultando la comprensión del texto."}, "Selección de ideas principales": {"Excelente": "Selecciona ideas relevantes, claras y directamente relacionadas con el tema principal del texto.", "Satisfactorio": "Selecciona ideas mayormente relevantes, aunque algunas podrían ser menos centrales o claras.", "Necesita mejorar": "Selecciona ideas poco relevantes o confusas que no reflejan correctamente el texto."}, "Adecuación al propósito de estudio": {"Excelente": "El resumen está claramente orientado a facilitar el estudio, incluyendo detalles relevantes y evitando información innecesaria.", "Satisfactorio": "El resumen cumple con el propósito de estudio, aunque incluye alguna información menos relevante o falta de detalle en aspectos importantes.", "Necesita mejorar": "El resumen no cumple con el propósito de estudio, incluyendo información irrelevante o faltando ideas importantes."}}, "explicacion": "Se espera que el estudiante identifique la relevancia de las ideas seleccionadas, analice el orden lógico para el estudio y valore si el resumen cumple su propósito, justificando sus evaluaciones con argumentos claros.", "instruccion": "Evalúa el resumen del estudiante según la rúbrica y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Un estudiante ha preparado un resumen escrito sobre un texto expositivo acerca de los cambios climáticos, donde selecciona y ordena cinco ideas principales con el propósito de estudiar para un examen."}', '{"evaluacion_modelo": {"Orden y coherencia de las ideas": "Satisfactorio", "Selección de ideas principales": "Excelente", "Adecuación al propósito de estudio": "Excelente"}, "justificacion_modelo": "El estudiante seleccionó ideas claras y relevantes, demostrando comprensión del texto. Sin embargo, el orden presenta pequeños desajustes que no impiden el entendimiento pero podrían mejorarse. El resumen se orienta claramente al estudio, con información pertinente y sin datos superfluos.", "requiere_revision_humana": true}', 4, 0, true, '{evaluación,resumen,"comprensión lectora",primeromedia,lenguayliteratura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (54, 17, 'criteria_evaluation', 'all', '{"rubrica": {"Ordenamiento de las ideas": {"Excelente": "Las ideas están ordenadas de manera lógica y coherente que facilita la comprensión y cumple con el propósito de estudiar o investigar.", "Satisfactorio": "El orden de las ideas es en general lógico, aunque puede presentar pequeñas inconsistencias que dificultan ligeramente la comprensión.", "Necesita mejorar": "El orden es confuso o ilógico, lo que impide comprender el propósito del resumen."}, "Claridad y coherencia en la explicación": {"Excelente": "La justificación es clara, coherente y bien estructurada, demostrando un pensamiento crítico sobre la selección y el orden de las ideas.", "Satisfactorio": "La justificación es comprensible pero puede ser poco detallada o con algunos saltos en el razonamiento.", "Necesita mejorar": "La justificación es confusa, incompleta o carece de coherencia."}, "Justificación de la selección de ideas": {"Excelente": "El estudiante selecciona claramente las ideas más relevantes y pertinentes para el propósito de la investigación, explicando con precisión por qué cada idea fue incluida.", "Satisfactorio": "El estudiante selecciona ideas relevantes, aunque algunas podrían no ser las más importantes para el propósito, y ofrece una justificación general.", "Necesita mejorar": "El estudiante incluye ideas irrelevantes o importantes que faltan, con poca o ninguna justificación."}}, "explicacion": "Se espera que el estudiante pueda identificar y explicar por qué seleccionó ciertas ideas principales para el resumen, cómo las ordenó para cumplir con un propósito específico y que su justificación sea clara y coherente, demostrando habilidades de evaluación crítica.", "instruccion": "Evalúa el resumen según la rúbrica proporcionada y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Se presenta un resumen elaborado por un estudiante sobre un texto expositivo acerca del cambio climático. El resumen contiene varias ideas principales ordenadas para preparar un informe de investigación."}', '{"evaluacion_modelo": {"Ordenamiento de las ideas": "Satisfactorio", "Claridad y coherencia en la explicación": "Satisfactorio", "Justificación de la selección de ideas": "Excelente"}, "justificacion_modelo": "El estudiante seleccionó las ideas principales más relevantes para el propósito del resumen, pero el orden de las ideas presenta algunas inconsistencias que dificultan un poco la comprensión. La justificación es clara, aunque podría profundizar más en la explicación del orden.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Evaluación,Resumen,"Pensamiento Crítico","Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (55, 17, 'criteria_evaluation', 'all', '{"rubrica": {"Orden y coherencia de las ideas": {"Excelente": "Ordena las ideas de manera lógica y coherente, facilitando la comprensión y el estudio del contenido.", "Satisfactorio": "Ordena las ideas en forma bastante lógica, aunque puede haber pequeños saltos o desorden que no dificultan mucho la comprensión.", "Necesita mejorar": "Ordena las ideas de forma desorganizada o confusa, dificultando la comprensión y el uso del resumen."}, "Selección de ideas principales": {"Excelente": "Selecciona todas las ideas que son relevantes y esenciales para el propósito de estudio, sin incluir información irrelevante.", "Satisfactorio": "Selecciona la mayoría de las ideas principales, aunque incluye alguna información secundaria o irrelevante.", "Necesita mejorar": "Selecciona pocas ideas principales o incluye muchas ideas irrelevantes que dificultan el propósito del resumen."}, "Justificación de la selección y el orden": {"Excelente": "Ofrece una justificación clara, detallada y convincente que explica por qué eligió y ordenó las ideas según el propósito de estudiar.", "Satisfactorio": "Presenta una justificación adecuada, aunque poco profunda o general, sobre la selección y orden de las ideas.", "Necesita mejorar": "No ofrece una justificación clara o la justificación no corresponde al propósito planteado."}}, "explicacion": "Se espera que el estudiante identifique las ideas principales relevantes para estudiar, las ordene de manera lógica y pueda argumentar claramente sus elecciones y organización para cumplir el propósito de facilitar el aprendizaje.", "instruccion": "Evalúa tu resumen según la rúbrica proporcionada y justifica la calificación que te das en cada criterio, explicando tus decisiones.", "caso_a_evaluar": "Has leído un texto expositivo sobre el cambio climático. Debes seleccionar y ordenar las ideas principales para preparar un resumen que facilitará a tus compañeros estudiar el tema para un examen."}', '{"evaluacion_modelo": {"Orden y coherencia de las ideas": "Excelente", "Selección de ideas principales": "Satisfactorio", "Justificación de la selección y el orden": "Excelente"}, "justificacion_modelo": "El resumen incluye la mayoría de las ideas principales relevantes, aunque se omite alguna información secundaria importante (Satisfactorio). El orden de las ideas es lógico y coherente, facilitando la comprensión (Excelente). La justificación es clara y explica con detalle cómo la selección y orden ayudan al propósito de estudio (Excelente).", "requiere_revision_humana": true}', 5, 0, true, '{evaluación,resumen,pensamiento_critico,lengua_y_literatura,primer_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (595, 126, 'fill_blanks', 'all', '{"texto": "En un discurso argumentativo, el emisor presenta una [BLANK_1] para convencer al receptor, y para apoyarla utiliza diferentes [BLANK_2] que fortalecen su postura.", "pistas": ["BLANK_1 es la idea principal que se quiere defender.", "BLANK_2 son las razones o evidencias que sustentan la idea principal."], "explicacion": "Un discurso argumentativo se estructura en torno a una tesis o idea principal que el emisor quiere defender, y para ello utiliza argumentos que respaldan dicha tesis."}', '{"respuestas_correctas": {"BLANK_1": ["tesis", "idea principal"], "BLANK_2": ["argumentos", "razones"]}}', 2, 0, true, '{"discurso argumentativo","elementos básicos","escucha activa"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (57, 17, 'open_ended', 'all', '{"pregunta": "Después de leer un texto informativo sobre un tema de tu interés, selecciona las ideas principales que consideras más relevantes para preparar un resumen que te ayude a estudiar para un examen. Justifica por qué elegiste esas ideas y el orden en que las presentaste, explicando cómo contribuyen a cumplir el propósito de estudiar eficientemente.", "explicacion": "El docente debe valorar que el estudiante identifique adecuadamente las ideas centrales del texto, que pueda argumentar con fundamentos claros la selección de esas ideas y que presente un orden que facilite el aprendizaje, demostrando comprensión y capacidad crítica en la organización de la información.", "contexto_adicional": "Se sugiere que el estudiante lea un texto informativo proporcionado en clase o un texto de su elección, relacionado con un tema visto en el curso, para realizar la actividad.", "criterios_evaluacion": ["Selección pertinente de ideas principales que reflejen la esencia del texto (peso: 40%)", "Justificación clara y coherente del porqué de la selección de cada idea (peso: 35%)", "Organización lógica y eficaz de las ideas para facilitar el estudio y la comprensión (peso: 25%)"]}', '{"puntos_clave": ["Identificación de las ideas principales relevantes para el propósito de estudio", "Justificación que conecta la selección con la utilidad para el examen", "Orden lógico que facilita la comprensión y memorización"], "respuesta_modelo": "Después de leer el texto sobre la contaminación ambiental, seleccioné las siguientes ideas principales: 1) la definición y causas de la contaminación, 2) los efectos que tiene en la salud humana y los ecosistemas, y 3) las medidas para prevenirla. Elegí estas ideas porque resumen los aspectos fundamentales que necesito entender para explicar el tema y preparar un examen. Primero presento la definición y causas para contextualizar, luego los efectos para comprender la gravedad del problema, y finalmente las medidas para concluir con posibles soluciones. Este orden permite un aprendizaje progresivo y claro sobre el tema.", "requiere_revision_humana": true}', 5, 0, true, '{sintesis,pensamiento_critico,organizacion_de_ideas}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (58, 18, 'open_ended', 'all', '{"pregunta": "Después de leer el texto proporcionado, diseña un infográfico que sintetice y organice las ideas principales, considerando un público específico (por ejemplo, tus compañeros de curso o un grupo de jóvenes interesados en el tema). Explica cómo seleccionaste y jerarquizaste la información y por qué crees que tu diseño es efectivo para comunicar el mensaje.", "explicacion": "El docente debe valorar cómo el estudiante identifica la información esencial, la sintetiza y la presenta visualmente de manera clara y atractiva para un público determinado. También es fundamental que el estudiante justifique sus elecciones de diseño, mostrando reflexión crítica y creatividad.", "contexto_adicional": "Se entrega un texto informativo sobre un tema actual de interés social o cultural para que los estudiantes elaboren su infográfico.", "criterios_evaluacion": ["Selección y síntesis de las ideas principales del texto, demostrando comprensión profunda (peso: 40%)", "Organización clara y coherente de la información en el producto visual, facilitando la comprensión para el público objetivo (peso: 35%)", "Justificación argumentada sobre las decisiones de diseño y la adecuación al público, evidenciando pensamiento crítico y creativo (peso: 25%)"]}', '{"puntos_clave": ["Identificación clara de las ideas principales del texto", "Estructura visual coherente y jerarquizada", "Justificación clara y fundamentada del diseño y público objetivo"], "respuesta_modelo": "El infográfico presenta las tres ideas principales del texto: el contexto histórico, los impactos sociales y las posibles soluciones. La información está organizada en secciones con títulos claros, usando colores y gráficos que facilitan la comprensión. Elegí destacar primero el contexto para situar al público, luego los impactos para generar conciencia, y finalicé con soluciones para motivar la acción. El diseño está pensado para mis compañeros, usando un lenguaje sencillo y elementos visuales atractivos para captar su atención.", "requiere_revision_humana": true}', 4, 0, true, '{síntesis,creación,"pensamiento crítico",infográfico,"primer medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (59, 18, 'open_ended', 'all', '{"pregunta": "Después de leer el texto proporcionado sobre la importancia de la lectura en la formación personal, diseña una infografía que sintetice y ordene las ideas principales para convencer a tus compañeros de curso de incorporar el hábito de la lectura diaria. Explica brevemente cómo seleccionaste y organizaste la información para cumplir con este propósito.", "explicacion": "El docente debe evaluar si el estudiante logra identificar correctamente las ideas principales, si la organización de la información en la infografía es clara y ordenada, y si el producto final es creativo y adecuado para persuadir a sus pares, considerando el propósito y el público objetivo.", "contexto_adicional": "Se entrega un texto informativo sobre la importancia de la lectura en el desarrollo personal y académico. Los estudiantes deben diseñar una infografía que sintetice y ordene las ideas para motivar a sus compañeros.", "criterios_evaluacion": ["Claridad y precisión en la selección de las ideas principales del texto (peso: 30%)", "Organización coherente y lógica de la información en la infografía (peso: 40%)", "Creatividad y adecuación del producto final para el público y propósito establecido (peso: 30%)"]}', '{"puntos_clave": ["Identificación precisa de las ideas principales del texto", "Organización lógica y clara de la información en la infografía", "Adecuación creativa del diseño para el público y propósito"], "respuesta_modelo": "La infografía presenta tres secciones principales: beneficios cognitivos, emocionales y sociales de la lectura. Se seleccionaron las ideas más relevantes que explican cómo la lectura mejora la concentración, reduce el estrés y fomenta la empatía. La información está ordenada de manera que primero se explica qué es la lectura, luego sus beneficios y finalmente consejos para incorporar el hábito. Se utilizaron colores llamativos y gráficos que facilitan la comprensión y mantienen el interés del público joven.", "requiere_revision_humana": true}', 5, 0, true, '{síntesis,infografía,"pensamiento crítico","primero medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (60, 18, 'concept_map', 'all', '{"conceptos": ["Idea principal", "Idea secundaria", "Resumen", "Organización", "Propósito comunicativo"], "explicacion": "Se espera que el estudiante muestre cómo la idea principal se apoya en las ideas secundarias para construir un resumen, que debe estar organizado de manera coherente y alineado con un propósito comunicativo específico.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el proceso de sintetizar y organizar la información de un texto?"}', '{"conceptos_centrales": ["Idea principal", "Resumen"], "relaciones_esperadas": [{"origen": "Idea principal", "destino": "Idea secundaria", "relacion": "se apoya en"}, {"origen": "Idea secundaria", "destino": "Resumen", "relacion": "contribuye a"}, {"origen": "Resumen", "destino": "Organización", "relacion": "requiere"}, {"origen": "Resumen", "destino": "Propósito comunicativo", "relacion": "se adapta a"}], "requiere_revision_humana": true}', 4, 0, true, '{"mapa conceptual",síntesis,organización,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (68, 20, 'multiple_choice', 'all', '{"opciones": {"A": "Entretener al lector con una historia divertida.", "B": "Explicar paso a paso cómo realizar una tarea específica.", "C": "Convencer al lector de que compre un producto.", "D": "Describir características de un personaje ficticio."}, "pregunta": "¿Cuál es el propósito principal de un texto instructivo, como una receta de cocina?", "explicacion": "La respuesta correcta es B porque un texto instructivo tiene como intención principal guiar al lector en la realización de una tarea mediante pasos claros y precisos. La opción A corresponde a textos narrativos o humorísticos, la C a textos persuasivos o publicitarios, y la D a textos descriptivos o narrativos. Estos distractores reflejan confusiones comunes sobre los propósitos textuales."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{comprensión,"propósito textual","textos instructivos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (61, 18, 'open_ended', 'all', '{"pregunta": "Después de leer el texto proporcionado, diseña un afiche informativo que sintetice y ordene las ideas principales para explicar su mensaje central a un público joven. Justifica las decisiones que tomaste en la organización y presentación de la información en tu afiche.", "explicacion": "El docente debe evaluar si el estudiante logra identificar y sintetizar las ideas centrales, cómo organiza visualmente la información para facilitar la comprensión y si puede explicar de manera clara y reflexiva las elecciones realizadas en el diseño del afiche, evidenciando pensamiento crítico y creativo.", "contexto_adicional": "El texto seleccionado aborda un tema relevante para jóvenes, como la importancia de la lectura o el cuidado del medio ambiente. El afiche debe ser atractivo y funcional para captar la atención y facilitar la comprensión.", "criterios_evaluacion": ["Claridad y pertinencia en la síntesis de las ideas principales del texto (peso: 40%).", "Creatividad y coherencia en el diseño visual y organización del afiche (peso: 30%).", "Capacidad para justificar las decisiones tomadas en la presentación y orden de la información (peso: 30%)."]}', '{"puntos_clave": ["Identificación precisa de las ideas principales del texto.", "Organización visual coherente y atractiva del afiche.", "Justificación clara y fundamentada de las decisiones de diseño."], "respuesta_modelo": "El afiche presenta un título claro y llamativo que refleja el mensaje principal del texto. Las ideas principales están sintetizadas en tres bloques de información, ordenados de forma lógica y jerárquica, utilizando colores y tipografías que resaltan lo más importante. En la justificación, se explica que se eligieron colores vivos para captar la atención del público joven y se organizaron los contenidos de manera secuencial para facilitar la comprensión. Se destaca además el uso de imágenes simbólicas que apoyan el mensaje central.", "requiere_revision_humana": true}', 5, 0, true, '{síntesis,creación,"lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (62, 19, 'multiple_choice', 'all', '{"opciones": {"A": "Una carta donde alguien explica cómo se siente.", "B": "Un cuento que relata las aventuras de un niño en el bosque.", "C": "Un artículo de periódico que informa sobre un evento reciente.", "D": "Una receta de cocina que explica cómo preparar un pastel."}, "pregunta": "¿Cuál de los siguientes textos corresponde a un texto narrativo?", "explicacion": "La opción B es correcta porque un texto narrativo cuenta una historia con personajes y sucesos, como un cuento. La opción A es un texto personal o epistolar, la C es un texto informativo, y la D corresponde a un texto instructivo o procedural. Estas diferencias permiten identificar claramente el tipo de texto."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"tipos de textos","texto narrativo","identificación de textos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (63, 19, 'multiple_choice', 'all', '{"opciones": {"A": "Un poema que expresa sentimientos mediante versos.", "B": "Un cuento que relata la historia de un personaje en una aventura.", "C": "Un manual de instrucciones para armar un mueble.", "D": "Un artículo de opinión sobre un tema de actualidad."}, "pregunta": "¿Cuál de los siguientes textos corresponde a un texto narrativo?", "explicacion": "La opción B es correcta porque un texto narrativo cuenta una historia con personajes y una secuencia de eventos. La opción A es un texto poético, que se enfoca en la expresión de emociones y el ritmo. La opción C es un texto instructivo, diseñado para guiar en la realización de tareas. La opción D es un texto argumentativo, que presenta opiniones y razones sobre un tema."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"tipos de texto","texto narrativo","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (64, 19, 'multiple_choice', 'all', '{"opciones": {"A": "Se centra en explicar conceptos y utilizar vocabulario técnico.", "B": "Cuenta una historia con personajes, tiempo y lugar definidos.", "C": "Presenta instrucciones paso a paso para realizar una tarea.", "D": "Expresa opiniones o emociones personales del autor."}, "pregunta": "¿Cuál de las siguientes características corresponde a un texto narrativo?", "explicacion": "La opción B es correcta porque un texto narrativo relata hechos o historias que incluyen personajes, un tiempo y un lugar específicos. La opción A describe un texto expositivo, la opción C corresponde a un texto instructivo, y la opción D se relaciona con un texto argumentativo o lírico, por lo tanto, son distractores que representan otros tipos de textos."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{tipos_de_texto,texto_narrativo,identificacion_textos}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (65, 19, 'true_false', 'all', '{"afirmacion": "Los textos narrativos cuentan una historia con personajes, un lugar y un tiempo definidos.", "explicacion": "Esta afirmación es verdadera porque los textos narrativos se caracterizan por relatar hechos o historias que incluyen elementos como personajes, un espacio donde ocurren los eventos y un tiempo determinado, lo que permite al lector comprender y seguir la secuencia de la narración."}', '{"es_verdadero": true}', 1, 0, true, '{"texto narrativo","características de textos","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (66, 19, 'fill_blanks', 'all', '{"texto": "Los textos narrativos, descriptivos y argumentativos son ejemplos de [BLANK_1], cada uno con características propias que los distinguen. Para comprender un texto, es importante identificar su tipo y reconocer su [BLANK_2], como el propósito o la intención del autor.", "pistas": ["Tipo de escritos que se diferencian por su estructura y función.", "Razón o motivo por el cual se escribe un texto."], "explicacion": "Los tipos de textos son categorías que agrupan escritos según su estructura y función comunicativa. Identificar el tipo de texto y su propósito permite una mejor comprensión y selección según las preferencias y objetivos del lector."}', '{"respuestas_correctas": {"BLANK_1": ["tipos de texto", "clases de texto", "géneros textuales"], "BLANK_2": ["propósito", "intención", "objetivo"]}}', 2, 0, true, '{"lengua y literatura","tipos de texto","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (67, 20, 'multiple_choice', 'all', '{"opciones": {"A": "Entretener al lector con una historia emocionante.", "B": "Guiar al lector para realizar una acción específica.", "C": "Persuadir al lector para que cambie de opinión.", "D": "Informar sobre hechos históricos de manera objetiva."}, "pregunta": "¿Cuál es el propósito principal de un texto instructivo, como una receta de cocina?", "explicacion": "La opción B es correcta porque un texto instructivo, como una receta, tiene la intención de guiar al lector paso a paso para realizar una tarea específica. La opción A confunde con textos narrativos que buscan entretener. La opción C corresponde a textos persuasivos o argumentativos, y la opción D describe textos informativos o expositivos, no instructivos."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"Lengua y Literatura","Comprensión de textos","Propósito textual","Primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (69, 20, 'true_false', 'all', '{"afirmacion": "El propósito principal de un texto narrativo es informar hechos reales de manera objetiva.", "explicacion": "Esta afirmación es falsa porque el propósito principal de un texto narrativo es contar una historia, ya sea real o ficticia, con la intención de entretener o transmitir experiencias, no simplemente informar hechos de manera objetiva."}', '{"es_verdadero": false}', 2, 0, true, '{"propósito del texto","tipo de texto","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (70, 20, 'drag_drop_matching', 'all', '{"explicacion": "Cada tipo de texto tiene una función específica: El texto narrativo cuenta historias; el informativo entrega datos; el publicitario persuade; el poético expresa emociones; el instructivo guía con pasos; y el de opinión presenta puntos de vista personales. El texto que describe un paisaje es un distractor, no tiene pareja.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Relato que cuenta una historia con personajes y acciones", "Texto que presenta datos y hechos para informar al lector", "Anuncio que busca persuadir para comprar un producto o servicio", "Expresa sentimientos y emociones mediante versos o lenguaje figurado", "Indica pasos o instrucciones para realizar una actividad", "Artículo donde se expresa el punto de vista personal sobre un tema", "Texto que describe un paisaje natural"], "columna_izquierda": ["Texto narrativo", "Texto informativo", "Texto publicitario", "Texto poético", "Texto instructivo", "Texto de opinión"]}', '{"emparejamientos_correctos": {"Texto poético": "Expresa sentimientos y emociones mediante versos o lenguaje figurado", "Texto narrativo": "Relato que cuenta una historia con personajes y acciones", "Texto de opinión": "Artículo donde se expresa el punto de vista personal sobre un tema", "Texto informativo": "Texto que presenta datos y hechos para informar al lector", "Texto instructivo": "Indica pasos o instrucciones para realizar una actividad", "Texto publicitario": "Anuncio que busca persuadir para comprar un producto o servicio"}}', 2, 0, true, '{"objetivo de aprendizaje","comprensión lectora","tipos de texto"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (71, 20, 'sequencing', 'all', '{"explicacion": "El orden correcto comienza con seleccionar un texto que te interese, luego leerlo atentamente para comprender su contenido. Después, identificas el tema principal para tener claridad sobre el enfoque del texto. Posteriormente, determinas si el propósito es informar, persuadir o entretener, y finalmente reflexionas sobre la intención del autor para comprender mejor el mensaje.", "instruccion": "Ordena los siguientes pasos para identificar el propósito de un texto:", "elementos_desordenados": ["Identificar el tema principal del texto", "Leer atentamente el texto seleccionado", "Determinar si el texto busca informar, persuadir o entretener", "Seleccionar un texto acorde a tus intereses", "Reflexionar sobre la intención del autor detrás del texto"]}', '{"orden_correcto": ["Seleccionar un texto acorde a tus intereses", "Leer atentamente el texto seleccionado", "Identificar el tema principal del texto", "Determinar si el texto busca informar, persuadir o entretener", "Reflexionar sobre la intención del autor detrás del texto"]}', 3, 0, true, '{comprensión,"lectura guiada","propósito textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (72, 21, 'multiple_choice', 'all', '{"opciones": {"A": "Un artículo científico especializado sobre la economía chilena actual.", "B": "Un capítulo de un libro de historia que aborda los principales eventos históricos de Chile.", "C": "Una novela histórica ambientada en Chile durante el siglo XIX.", "D": "Un blog personal con opiniones sobre las tradiciones culturales chilenas."}, "pregunta": "¿Cuál de los siguientes textos sería más adecuado para un estudiante de Primero Medio que necesita preparar un resumen para una presentación académica sobre la historia de Chile?", "explicacion": "La opción B es la correcta porque un capítulo de un libro de historia proporciona información clara, estructurada y relevante para preparar un resumen académico sobre la historia de Chile. La opción A, aunque es un artículo científico, trata sobre economía y no sobre historia, por lo que no se ajusta al propósito. La opción C es una novela, que aunque relacionada con la historia, es un texto literario que no presenta hechos de manera objetiva para un trabajo académico. La opción D es un blog con opiniones personales, lo que no es adecuado para una presentación académica que requiere información objetiva y comprobable."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"selección de textos","uso académico","historia de Chile"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (73, 21, 'multiple_choice', 'all', '{"opciones": {"A": "Una novela de ciencia ficción que describe un futuro apocalíptico", "B": "Un artículo científico publicado en una revista especializada sobre cambio climático", "C": "Un blog personal donde alguien cuenta sus experiencias en la playa", "D": "Un poema que habla sobre la naturaleza y sus cambios"}, "pregunta": "¿Cuál de los siguientes textos es el más adecuado para preparar un informe académico sobre los efectos del cambio climático?", "explicacion": "La opción B es correcta porque un artículo científico en una revista especializada proporciona información precisa, actualizada y confiable, adecuada para un informe académico. La opción A, aunque relacionada temáticamente, es ficción y no aporta datos reales. La opción C es subjetiva y anecdótica, por lo que no es fiable para un informe académico. La opción D es un texto literario que puede inspirar, pero no es útil para un análisis científico o académico."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"selección de textos","evaluación de fuentes","informes académicos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (74, 21, 'drag_drop_matching', 'all', '{"explicacion": "Cada tipo de texto tiene un propósito específico: el artículo de opinión busca expresar una postura; la biografía relata hechos de una persona; las instrucciones de un juego explican cómo jugar; la novela de aventuras entretiene con ficción; el informe científico presenta resultados; y la receta da recomendaciones para preparar un plato.", "instruccion": "Empareja cada tipo de texto con su propósito adecuado", "columna_derecha": ["Relatar hechos de la vida de una persona", "Dar recomendaciones para preparar un plato", "Expresar una postura o punto de vista sobre un tema", "Explicar paso a paso cómo realizar una actividad", "Presentar resultados de una investigación", "Entretener con una historia ficticia"], "columna_izquierda": ["Artículo de opinión", "Biografía", "Instrucciones de un juego", "Novela de aventuras", "Informe científico", "Receta de cocina"]}', '{"emparejamientos_correctos": {"Biografía": "Relatar hechos de la vida de una persona", "Receta de cocina": "Dar recomendaciones para preparar un plato", "Informe científico": "Presentar resultados de una investigación", "Novela de aventuras": "Entretener con una historia ficticia", "Artículo de opinión": "Expresar una postura o punto de vista sobre un tema", "Instrucciones de un juego": "Explicar paso a paso cómo realizar una actividad"}}', 3, 0, true, '{"selección de textos","propósitos de lectura","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (474, 102, 'multiple_choice', 'all', '{"opciones": {"A": "María dijo: «Me gusta el chocolate».", "B": "María dijo que le gustaba el chocolate.", "C": "«¿Te gusta el chocolate?», preguntó María.", "D": "María dijo: «Voy a comer chocolate ahora»."}, "pregunta": "¿Cuál de las siguientes oraciones corresponde a un ejemplo de estilo indirecto?", "explicacion": "La opción B es correcta porque presenta el estilo indirecto, donde el mensaje se reporta sin usar comillas y con cambios en los tiempos verbales (de ''me gusta'' a ''le gustaba''). Las opciones A, C y D son ejemplos de estilo directo, pues usan comillas y reproducen textualmente lo dicho."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"estilo directo","estilo indirecto","lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (75, 21, 'sequencing', 'all', '{"explicacion": "Primero se debe definir para qué se quiere leer, luego se evalúan opciones considerando la dificultad y se revisa la sinopsis para asegurarse que el texto cumple con el propósito, finalmente se selecciona el texto para iniciar la lectura.", "instruccion": "Ordena los siguientes pasos para seleccionar un texto adecuado según su propósito", "elementos_desordenados": ["Definir el propósito de lectura (recreación o aprendizaje)", "Elegir un texto que se adapte al propósito definido", "Considerar el nivel de dificultad del texto", "Leer la sinopsis o resumen del texto", "Seleccionar el texto final para comenzar la lectura"]}', '{"orden_correcto": ["Definir el propósito de lectura (recreación o aprendizaje)", "Considerar el nivel de dificultad del texto", "Leer la sinopsis o resumen del texto", "Elegir un texto que se adapte al propósito definido", "Seleccionar el texto final para comenzar la lectura"]}', 3, 0, true, '{"selección de textos","comprensión lectora","estrategias de lectura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (76, 21, 'open_ended', 'all', '{"pregunta": "Imagina que debes preparar una presentación para tus compañeros sobre un tema de tu interés académico. Describe el proceso que seguirías para seleccionar los textos que utilizarás, considerando el propósito de tu presentación y tus preferencias personales. ¿Qué tipos de textos elegirías y por qué?", "explicacion": "Para evaluar esta pregunta se debe considerar cómo el estudiante articula el proceso de selección, si entiende la relación entre el tipo de texto y el propósito de la presentación, y si justifica adecuadamente sus elecciones con argumentos claros que incluyan sus preferencias y criterios académicos.", "contexto_adicional": "El estudiante debe aplicar conocimientos sobre selección de textos para un propósito específico, integrando sus intereses personales y necesidades académicas.", "criterios_evaluacion": ["Claridad y coherencia en la explicación del proceso de selección de textos (peso: 30%)", "Capacidad para relacionar el propósito de la presentación con la elección de textos adecuados (peso: 40%)", "Justificación de la selección de textos basada en preferencias personales y relevancia académica (peso: 30%)"]}', '{"puntos_clave": ["Definición clara del tema y propósito de la presentación", "Selección de tipos de textos adecuados al objetivo académico", "Incorporación de preferencias personales en la elección de textos", "Justificación coherente y fundamentada de las elecciones"], "respuesta_modelo": "Para preparar mi presentación, primero definiría el tema específico que quiero abordar y el objetivo de mi exposición. Luego, buscaría diferentes tipos de textos que se ajusten a ese propósito, como artículos científicos para información precisa, ensayos para opiniones fundamentadas, y textos narrativos o ejemplos para hacer más atractiva la presentación. Consideraría también mis preferencias personales, eligiendo textos que sean claros y que me resulten interesantes para mantenerme motivado. Por ejemplo, si el tema es sobre cambio climático, elegiría un artículo científico reciente para datos actualizados, un ensayo para analizar diferentes perspectivas y una infografía para facilitar la comprensión visual. Así, logro una presentación bien fundamentada y entretenida para mis compañeros.", "requiere_revision_humana": true}', 3, 0, true, '{"selección de textos","pensamiento crítico","presentación académica"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (77, 22, 'compare_contrast', 'all', '{"conceptos": ["Lectura para aprender", "Lectura para recrearse"], "criterios": ["Propósito", "Tipo de texto", "Actitud del lector"], "explicacion": "La lectura para aprender busca adquirir conocimientos y suele involucrar textos informativos o académicos, mientras que la lectura para recrearse tiene como objetivo el disfrute y el entretenimiento, utilizando textos literarios o narrativos. En cuanto a la actitud del lector, la lectura para aprender es más analítica y crítica, mientras que la lectura recreativa es más relajada y emocional.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Lectura para aprender": {"Propósito": "Adquirir conocimientos y comprender información", "Tipo de texto": "Textos informativos, académicos, científicos", "Actitud del lector": "Analítica, crítica, concentrada"}, "Lectura para recrearse": {"Propósito": "Disfrutar y entretenerse", "Tipo de texto": "Textos literarios, narrativos, poéticos", "Actitud del lector": "Relajada, emocional, abierta"}}}', 3, 0, true, '{comparar,lectura,"objetivos de lectura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (78, 22, 'compare_contrast', 'all', '{"conceptos": ["Texto Literario", "Texto Informativo"], "criterios": ["Propósito de lectura", "Características del lenguaje", "Estructura del texto"], "explicacion": "Los textos literarios buscan recrear y provocar emociones a través de un lenguaje figurado y estructuras narrativas o poéticas, mientras que los textos informativos tienen como propósito principal transmitir conocimientos o datos de forma clara y objetiva, utilizando un lenguaje directo y una estructura organizada en secciones o párrafos explicativos.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Texto Literario": {"Estructura del texto": "Narrativa, poesía o drama con estructura flexible", "Propósito de lectura": "Recrear y emocionar al lector", "Características del lenguaje": "Lenguaje figurado y expresivo"}, "Texto Informativo": {"Estructura del texto": "Organizada en secciones, con introducción, desarrollo y conclusión", "Propósito de lectura": "Transmitir información y conocimientos", "Características del lenguaje": "Lenguaje claro, objetivo y directo"}}}', 4, 0, true, '{comparar,textos,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (79, 22, 'open_ended', 'all', '{"pregunta": "Lee dos textos diferentes: uno narrativo y otro expositivo. Analiza y compara cómo cada texto cumple con distintos propósitos de lectura, considerando también tus preferencias personales. ¿Por qué elegirías uno u otro para aprender o para recrearte? Justifica tu respuesta con ejemplos específicos de los textos.", "explicacion": "El docente debe evaluar que el estudiante no solo reconozca las diferencias entre los tipos de texto y sus funciones, sino que también pueda analizar cómo estas diferencias se relacionan con sus preferencias personales y objetivos de lectura, demostrando pensamiento crítico y capacidad de argumentación.", "contexto_adicional": "Se proporcionarán dos textos breves: uno narrativo (por ejemplo, un fragmento de cuento) y otro expositivo (un artículo informativo corto).", "criterios_evaluacion": ["Identificación clara de las características y propósitos de cada texto (peso: 30%)", "Capacidad para comparar y contrastar los textos con argumentos fundamentados (peso: 40%)", "Explicación reflexiva que incluye preferencias personales y justificación coherente (peso: 30%)"]}', '{"puntos_clave": ["Diferenciación entre texto narrativo y expositivo", "Reconocimiento de propósitos: recreación vs. aprendizaje", "Justificación personal basada en preferencias y ejemplos textuales"], "respuesta_modelo": "El texto narrativo es un cuento que busca entretener y recrear al lector a través de una historia con personajes y un conflicto. En cambio, el texto expositivo presenta información clara y organizada para que el lector aprenda sobre un tema específico. Personalmente, elegiría el texto narrativo para recrearme porque disfruto sumergirme en historias y emociones, mientras que seleccionaría el texto expositivo para aprender, ya que ofrece datos concretos y explicaciones. Por ejemplo, en el cuento, la forma en que se describen los personajes me atrapa, mientras que en el artículo, las definiciones y datos ayudan a entender el tema de manera clara.", "requiere_revision_humana": true}', 4, 0, true, '{análisis,comparación,"lectura crítica","Lengua y Literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (89, 24, 'concept_map', 'all', '{"conceptos": ["Leer habitualmente", "Aprender", "Recrearse", "Selección de textos", "Preferencias y propósitos"], "explicacion": "Se espera que el estudiante conecte ''Leer habitualmente'' como una práctica que favorece tanto ''Aprender'' como ''Recrearse''. La ''Selección de textos'' debe estar guiada por las ''Preferencias y propósitos'' personales, mostrando cómo estas decisiones influyen en la experiencia lectora para fines de aprendizaje y recreación.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la lectura para aprender y recrearse?"}', '{"conceptos_centrales": ["Leer habitualmente", "Selección de textos"], "relaciones_esperadas": [{"origen": "Leer habitualmente", "destino": "Aprender", "relacion": "permite"}, {"origen": "Leer habitualmente", "destino": "Recrearse", "relacion": "permite"}, {"origen": "Selección de textos", "destino": "Preferencias y propósitos", "relacion": "se basa en"}, {"origen": "Selección de textos", "destino": "Leer habitualmente", "relacion": "influye en"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",Lectura,"Mapa Conceptual",Aprendizaje,Recreación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (80, 22, 'concept_map', 'all', '{"conceptos": ["Texto Narrativo", "Texto Expositivo", "Propósito de Lectura", "Preferencia del Lector", "Características del Texto"], "explicacion": "Se espera que el estudiante identifique que los textos narrativos y expositivos tienen diferentes características que influyen en el propósito de lectura. Además, la preferencia del lector afecta la selección del tipo de texto según sus intereses y objetivos, estableciendo relaciones entre características textuales y propósitos específicos.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la selección y clasificación de textos según las preferencias y propósitos de lectura?"}', '{"conceptos_centrales": ["Propósito de Lectura", "Preferencia del Lector"], "relaciones_esperadas": [{"origen": "Texto Narrativo", "destino": "Características del Texto", "relacion": "posee"}, {"origen": "Texto Expositivo", "destino": "Características del Texto", "relacion": "posee"}, {"origen": "Características del Texto", "destino": "Propósito de Lectura", "relacion": "influyen en"}, {"origen": "Preferencia del Lector", "destino": "Selección de Texto", "relacion": "determina"}, {"origen": "Propósito de Lectura", "destino": "Selección de Texto", "relacion": "guía"}], "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Mapa Conceptual","Análisis de Textos","Primer Año Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (81, 22, 'concept_map', 'all', '{"conceptos": ["Texto literario", "Texto informativo", "Propósito de lectura", "Preferencias del lector", "Estrategias de comprensión"], "explicacion": "Se espera que el estudiante relacione cómo los textos literarios y textos informativos se diferencian según sus características y propósitos de lectura. Además, debe conectar cómo las preferencias del lector influyen en la selección del tipo de texto y cómo se aplican distintas estrategias de comprensión para analizar y disfrutar cada tipo según su propósito.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la selección y análisis de textos según preferencias y propósitos de lectura?"}', '{"conceptos_centrales": ["Propósito de lectura", "Preferencias del lector"], "relaciones_esperadas": [{"origen": "Texto literario", "destino": "Propósito de lectura", "relacion": "tiene"}, {"origen": "Texto informativo", "destino": "Propósito de lectura", "relacion": "tiene"}, {"origen": "Preferencias del lector", "destino": "Selección de texto literario", "relacion": "influyen en"}, {"origen": "Preferencias del lector", "destino": "Selección de texto informativo", "relacion": "influyen en"}, {"origen": "Estrategias de comprensión", "destino": "Texto literario", "relacion": "aplican a"}, {"origen": "Estrategias de comprensión", "destino": "Texto informativo", "relacion": "aplican a"}], "requiere_revision_humana": true}', 4, 0, true, '{mapa_conceptual,analizar,crear,lectura,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (82, 23, 'criteria_evaluation', 'all', '{"rubrica": {"Adecuación al propósito de lectura": {"Excelente": "La selección de textos corresponde claramente a los propósitos indicados, mostrando comprensión profunda de cómo cada texto cumple con la función informativa o recreativa.", "Satisfactorio": "La selección de textos corresponde en general a los propósitos, aunque la relación entre texto y propósito podría ser más clara o precisa.", "Necesita mejorar": "La selección de textos no está alineada con los propósitos de lectura indicados o la justificación es confusa o insuficiente."}, "Capacidad crítica en la evaluación": {"Excelente": "El estudiante demuestra una reflexión crítica sobre las características de los textos y su efectividad para cumplir los propósitos de lectura, considerando aspectos específicos del contenido y la forma.", "Satisfactorio": "El estudiante muestra alguna reflexión crítica, pero de manera superficial o general.", "Necesita mejorar": "No se evidencia reflexión crítica sobre la selección o se limita a enunciados simples sin fundamentación."}, "Claridad y coherencia en la justificación": {"Excelente": "La justificación está bien estructurada, con argumentos claros, coherentes y relevantes que explican la elección de cada texto.", "Satisfactorio": "La justificación es comprensible pero presenta algunas ideas poco claras o argumentos poco desarrollados.", "Necesita mejorar": "La justificación es confusa, poco coherente o presenta argumentos irrelevantes o insuficientes."}}, "explicacion": "Se espera que el estudiante demuestre capacidad para elegir textos adecuados a distintos propósitos de lectura y argumentar con claridad y profundidad la efectividad de su elección, mostrando pensamiento crítico sobre el contenido y función de los textos.", "instruccion": "Evalúa la selección y justificación de los textos según la rúbrica propuesta y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Se te presenta una lista de cinco textos variados (un cuento, un artículo de divulgación científica, un poema, una noticia y un ensayo breve). Debes seleccionar dos textos que elegirías para leer con el propósito de informarte sobre un tema actual y para disfrutar de la literatura. Luego, justifica tu elección explicando cómo cada texto cumple con el propósito de lectura seleccionado."}', '{"evaluacion_modelo": {"Adecuación al propósito de lectura": "Excelente", "Capacidad crítica en la evaluación": "Excelente", "Claridad y coherencia en la justificación": "Satisfactorio"}, "justificacion_modelo": "La selección de textos es adecuada y corresponde claramente a los propósitos planteados, demostrando comprensión del propósito informativo y recreativo. La justificación es clara en la mayoría de los argumentos, aunque podría ser más profunda en algunos puntos. Se evidencia una reflexión crítica sólida sobre las características de los textos y su efectividad para cumplir los propósitos, señalando aspectos específicos del contenido y la forma.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",Evaluación,"Comprensión lectora",Justificación,"Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (83, 23, 'criteria_evaluation', 'all', '{"rubrica": {"Adecuación al propósito de lectura": {"Excelente": "Selecciona el texto que mejor cumple con el propósito indicado y explica claramente cómo el texto cumple ese propósito.", "Satisfactorio": "Selecciona un texto que cumple el propósito, pero la explicación es general o poco detallada.", "Necesita mejorar": "Selecciona un texto que no corresponde al propósito o no justifica la elección."}, "Razonamiento crítico y justificación": {"Excelente": "Justifica la elección con argumentos sólidos y ejemplos concretos del texto.", "Satisfactorio": "Presenta una justificación con argumentos, pero sin ejemplos claros o con razonamientos poco profundos.", "Necesita mejorar": "La justificación es débil, vaga o no presenta argumentos relevantes."}, "Claridad y coherencia en la argumentación": {"Excelente": "La explicación es clara, coherente y bien estructurada, facilitando la comprensión del razonamiento.", "Satisfactorio": "La explicación es entendible pero presenta algunas ideas poco claras o desordenadas.", "Necesita mejorar": "La argumentación es confusa, incoherente o difícil de seguir."}}, "explicacion": "Se espera que el estudiante demuestre capacidad para seleccionar un texto adecuado según el propósito de lectura, justificar su elección con argumentos claros y ejemplos, y presentar una argumentación coherente y bien estructurada.", "instruccion": "Evalúa la elección del texto para el trabajo según la rúbrica y justifica tu calificación en cada criterio.", "caso_a_evaluar": "Una estudiante debe elegir entre tres textos para preparar un trabajo sobre la importancia de la diversidad cultural: un artículo académico, un cuento literario y una entrevista periodística. Cada texto tiene un propósito diferente: informar, recrear y presentar opiniones, respectivamente."}', '{"evaluacion_modelo": {"Adecuación al propósito de lectura": "Excelente", "Razonamiento crítico y justificación": "Satisfactorio", "Claridad y coherencia en la argumentación": "Excelente"}, "justificacion_modelo": "La estudiante seleccionó correctamente el artículo académico para informar, cumpliendo con el propósito. Explicó por qué el texto es adecuado pero no incluyó ejemplos específicos. Su argumentación fue clara y bien organizada, facilitando la comprensión de su elección.", "requiere_revision_humana": true}', 5, 0, true, '{"lectura crítica",justificación,evaluación,"Lengua y Literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (90, 24, 'concept_map', 'all', '{"conceptos": ["Lectura habitual", "Propósitos de lectura", "Textos para aprender", "Textos para recrearse", "Selección de textos"], "explicacion": "Se espera que el estudiante muestre cómo la lectura habitual influye en la selección de textos, diferenciando entre textos para aprender y para recrearse, y cómo los propósitos de lectura guían esta selección.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la creación de una lista personal de lectura?"}', '{"conceptos_centrales": ["Lectura habitual", "Selección de textos"], "relaciones_esperadas": [{"origen": "Lectura habitual", "destino": "Selección de textos", "relacion": "favorece"}, {"origen": "Propósitos de lectura", "destino": "Selección de textos", "relacion": "determinan"}, {"origen": "Selección de textos", "destino": "Textos para aprender", "relacion": "incluye"}, {"origen": "Selección de textos", "destino": "Textos para recrearse", "relacion": "incluye"}, {"origen": "Textos para aprender", "destino": "Textos para recrearse", "relacion": "se diferencian de"}], "requiere_revision_humana": true}', 5, 0, true, '{lectura,mapa_conceptual,creación,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (84, 23, 'criteria_evaluation', 'all', '{"rubrica": {"Argumentación y uso de ejemplos": {"Excelente": "Se utilizan argumentos sólidos y ejemplos específicos del texto para respaldar la justificación.", "Satisfactorio": "Los argumentos son válidos pero poco elaborados y los ejemplos son generales o limitados.", "Necesita mejorar": "Faltan argumentos claros y ejemplos que apoyen la justificación."}, "Capacidad para evaluar alternativas": {"Excelente": "Se analiza críticamente por qué los otros textos son menos adecuados para el propósito.", "Satisfactorio": "Se menciona brevemente por qué otras opciones no son las mejores, pero sin profundidad.", "Necesita mejorar": "No se evalúan las otras opciones o la evaluación es superficial."}, "Coherencia de la elección con el propósito": {"Excelente": "La elección del texto está claramente alineada con el propósito de informarse y se explica por qué es la mejor opción.", "Satisfactorio": "La elección del texto es adecuada, pero la justificación es poco profunda o general.", "Necesita mejorar": "La elección del texto no se corresponde con el propósito o la justificación es confusa o insuficiente."}}, "explicacion": "Se espera que el estudiante identifique la novela histórica como menos adecuada para informarse sobre un hecho reciente, reconozca que el artículo periodístico es el texto más pertinente y explique su elección justificando con ejemplos, además de valorar críticamente las otras opciones.", "instruccion": "Evalúa la selección del texto para informarte sobre un hecho histórico reciente según la rúbrica y justifica tu calificación detalladamente.", "caso_a_evaluar": "Un estudiante selecciona tres textos distintos para leer: una novela histórica, un artículo periodístico de opinión y un poema contemporáneo. Debe justificar cuál de estos textos es el más adecuado para cumplir el propósito de informarse sobre un hecho histórico reciente."}', '{"evaluacion_modelo": {"Argumentación y uso de ejemplos": "Excelente", "Capacidad para evaluar alternativas": "Satisfactorio", "Coherencia de la elección con el propósito": "Excelente"}, "justificacion_modelo": "La elección del artículo periodístico es coherente con el propósito de informarse sobre un hecho reciente, justificado con ejemplos específicos del contenido. Se argumenta sólidamente la preferencia, aunque la evaluación de las otras opciones es breve y podría profundizarse más.", "requiere_revision_humana": true}', 5, 0, true, '{"lectura crítica",justificación,"evaluación de textos","OA lectura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (85, 23, 'open_ended', 'all', '{"pregunta": "Elige dos textos diferentes que hayas leído recientemente y que hayas seleccionado para propósitos distintos (por ejemplo, uno para aprender y otro para recrearte). Justifica cuál de ellos consideras que fue más efectivo para cumplir su propósito y explica por qué, considerando aspectos como el contenido, el estilo, la estructura y tus preferencias personales.", "explicacion": "El docente debe evaluar si el estudiante logra justificar con argumentos claros y fundamentados por qué un texto fue más efectivo que otro para un propósito específico, observando la profundidad del análisis y la conexión con sus propias preferencias y experiencias lectoras.", "contexto_adicional": "Se sugiere que el estudiante base su respuesta en textos leídos durante el curso o en su tiempo libre, fomentando la reflexión sobre la selección de textos acorde a sus intereses y objetivos.", "criterios_evaluacion": ["Claridad y coherencia en la justificación de la elección de los textos (peso: 30%)", "Capacidad para analizar y comparar los aspectos formales y de contenido que hacen efectivo cada texto para su propósito (peso: 40%)", "Incorporación de preferencias personales y reflexión crítica sobre la experiencia de lectura (peso: 30%)"]}', '{"puntos_clave": ["Identificación clara de dos textos con propósitos distintos", "Análisis comparativo de contenido, estilo y estructura", "Reflexión personal sobre la experiencia y preferencias lectoras"], "respuesta_modelo": "Seleccioné un artículo científico sobre cambio climático para aprender y una novela juvenil para recrearme. Considero que el artículo fue más efectivo para cumplir su propósito de aprendizaje porque presenta información clara, organizada y con evidencias que me permitieron comprender mejor el tema. En cambio, la novela juvenil fue efectiva para recrearme debido a su lenguaje sencillo y trama emocionante, que captó mi atención y me hizo disfrutar la lectura. Ambos textos cumplen bien sus propósitos, pero el artículo destaca por su estructura y contenido riguroso, mientras la novela lo hace por su estilo narrativo y capacidad de entretenerme.", "requiere_revision_humana": true}', 4, 0, true, '{evaluar,"lectura crítica","selección de textos","Lengua y Literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (86, 23, 'open_ended', 'all', '{"pregunta": "Elige dos textos que hayas leído recientemente con propósitos diferentes (por ejemplo, uno para aprender y otro para recrearte). Justifica detalladamente por qué cada texto fue efectivo para cumplir su propósito, considerando aspectos como el contenido, el estilo, el lenguaje y la estructura. ¿En qué se diferencian estos textos en cuanto a su capacidad para satisfacer tus necesidades lectoras?", "explicacion": "El docente debe evaluar si el estudiante puede identificar y argumentar las razones por las cuales un texto es adecuado para determinado propósito lector, analizando elementos literarios y estructurales, y si puede organizar sus ideas de manera coherente y fundamentada.", "contexto_adicional": "Considera los textos que hayas leído en clases o por iniciativa propia, y reflexiona sobre cómo cada uno cumplió con el propósito de aprendizaje o recreación.", "criterios_evaluacion": ["Claridad y profundidad en la justificación de la elección de cada texto según su propósito (peso: 40%)", "Análisis comparativo de los aspectos formales y de contenido que hacen efectivos los textos para sus propósitos (peso: 40%)", "Organización y coherencia en la argumentación presentada (peso: 20%)"]}', '{"puntos_clave": ["Identificación clara de los propósitos de lectura (aprender y recrearse).", "Justificación basada en contenido, lenguaje, estilo y estructura.", "Comparación entre los textos en función de su efectividad para cumplir propósitos."], "respuesta_modelo": "Elegí leer un artículo científico sobre cambio climático para aprender, y una novela de fantasía para recrearme. El artículo fue efectivo para aprender porque presenta información clara, datos precisos y un lenguaje técnico adecuado que me permitió entender y profundizar en el tema. Su estructura con secciones bien definidas facilitó la comprensión. En cambio, la novela de fantasía fue ideal para recrearme porque usa un lenguaje creativo y descriptivo, con un estilo narrativo atractivo que despierta la imaginación. La estructura de capítulos y el desarrollo de personajes me mantuvieron entretenido y motivado a seguir leyendo. Estos textos difieren en que el primero prioriza la claridad y la precisión para informar, mientras que el segundo enfatiza la creatividad y emoción para divertir y transportar al lector.", "requiere_revision_humana": true}', 5, 0, true, '{evaluar,lectura,justificación,primeromedia,lenguayliteratura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (87, 24, 'open_ended', 'all', '{"pregunta": "Diseña una lista personal de lectura que incluya al menos tres textos para aprender y tres textos para recrearte. Explica por qué seleccionaste cada texto y cómo crees que contribuirán a tu aprendizaje y a tu disfrute personal.", "explicacion": "El docente debe evaluar que el estudiante no solo seleccione textos variados y pertinentes, sino que también explique con profundidad sus elecciones, demostrando reflexión crítica sobre sus preferencias y necesidades de aprendizaje. Se valorará la creatividad y claridad en la presentación final.", "contexto_adicional": "El estudiante debe combinar textos de diferentes géneros y temáticas, considerando sus intereses y propósitos personales.", "criterios_evaluacion": ["Selección adecuada y equilibrada de textos para aprender y recrearse (peso: 40%)", "Justificación clara y coherente de la elección de cada texto, vinculando con objetivos personales de aprendizaje y recreación (peso: 40%)", "Organización, claridad y creatividad en la presentación de la lista personal de lectura (peso: 20%)"]}', '{"puntos_clave": ["Selección de textos divididos claramente en para aprender y para recrearse", "Justificación explícita de la elección con relación a objetivos personales", "Diversidad en géneros y temáticas, mostrando reflexión personal"], "respuesta_modelo": "He seleccionado tres textos para aprender: un artículo científico sobre cambio climático para entender temas actuales y desarrollar mi conocimiento en ciencias; un ensayo literario sobre la identidad cultural chilena para profundizar en mi contexto social; y un texto histórico sobre la independencia de Chile para fortalecer mi comprensión de la historia nacional. Para recrearme, escogí una novela juvenil de fantasía que disfruto por su trama envolvente; una colección de poemas que me ayudan a relajarme y conectar con mis emociones; y una obra de teatro clásica que me interesa por su lenguaje y representación escénica. Cada texto aporta a mis objetivos personales, alternando el aprendizaje con el placer de la lectura.", "requiere_revision_humana": true}', 4, 0, true, '{lectura,creatividad,pensamiento_critico,proyecto_corto}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (88, 24, 'open_ended', 'all', '{"pregunta": "Diseña una lista de lectura personal que incluya al menos tres textos para aprender y tres textos para recrearte. Explica por qué seleccionaste cada texto, cómo cada uno se relaciona con tus intereses y propósitos de lectura, y cómo esperas que te beneficien tanto en el aprendizaje como en el disfrute personal.", "explicacion": "El docente debe evaluar si el estudiante ha seleccionado textos que realmente respondan a los dos propósitos planteados, si sus justificaciones son coherentes y reflejan un análisis personal, y si la presentación es clara y ordenada. Se debe valorar la creatividad y reflexión crítica en la construcción de la lista, no solo la cantidad o popularidad de los textos.", "contexto_adicional": "El estudiante debe considerar diferentes tipos de textos literarios y no literarios que le permitan cumplir con ambos propósitos: aprender y recrearse. Puede incluir libros, artículos, poemas, cuentos, entre otros.", "criterios_evaluacion": ["Coherencia y pertinencia en la selección de textos para aprendizaje y recreación, demostrando que se ajustan a los intereses y propósitos del estudiante (peso: 40%)", "Capacidad para justificar la elección de cada texto con argumentos claros que reflejen reflexión crítica sobre su utilidad y atractivo personal (peso: 40%)", "Organización y claridad en la presentación de la lista y las explicaciones, facilitando la comprensión de la propuesta (peso: 20%)"]}', '{"puntos_clave": ["Incluye al menos tres textos para aprender y tres para recrearse", "Justificación clara y reflexiva para cada texto seleccionado", "Relación explícita entre los textos y los intereses y propósitos personales"], "respuesta_modelo": "Mi lista de lectura personal incluye para aprender: 1) \"Sapiens\" de Yuval Noah Harari, porque me interesa entender la evolución de la humanidad y sus implicancias actuales; 2) un artículo científico sobre cambio climático, para profundizar en un tema relevante y actual; 3) \"Breve historia de la literatura chilena\" para conocer las raíces culturales de mi país. Para recrearme, elegí: 1) \"El principito\" de Antoine de Saint-Exupéry, por su narrativa sencilla y reflexiva; 2) una colección de cuentos cortos de Isabel Allende, porque disfruto de sus historias y estilo; 3) poesía de Pablo Neruda, que me inspira y me conecta con mis emociones. Estas selecciones combinan aprendizaje y disfrute, permitiéndome crecer intelectualmente y emocionalmente según mis intereses personales.", "requiere_revision_humana": true}', 5, 0, true, '{lectura,"pensamiento crítico",creación,"lengua y literatura","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (91, 24, 'open_ended', 'all', '{"pregunta": "Diseña una lista personal de lectura que incluya al menos tres textos para aprender y tres textos para recrearte. Explica por qué seleccionaste cada texto y cómo cada uno contribuye a tus objetivos de aprendizaje o recreación.", "explicacion": "El docente debe evaluar que el estudiante haya seleccionado una variedad equilibrada de textos que cumplan con las dos finalidades (aprender y recrearse), que haya argumentado adecuadamente cada selección relacionándola con propósitos personales, y que la presentación sea clara y atractiva. Se recomienda valorar la originalidad y profundidad de las justificaciones.", "contexto_adicional": "El estudiante debe considerar sus intereses personales y metas educativas al momento de seleccionar los textos, combinando textos informativos o académicos con textos literarios o de entretenimiento.", "criterios_evaluacion": ["Selección adecuada y equilibrada de textos para aprender y para recrearse, demostrando variedad y pertinencia (peso: 40%)", "Justificación clara y coherente de la elección de cada texto, vinculando la selección con sus objetivos personales de aprendizaje o recreación (peso: 40%)", "Organización y presentación de la lista de manera clara y creativa, facilitando la comprensión del propósito de cada texto (peso: 20%)"]}', '{"puntos_clave": ["Selección de al menos tres textos para aprender y tres para recrearse", "Justificación clara que vincula cada texto con un objetivo personal", "Presentación organizada y coherente de la lista"], "respuesta_modelo": "Mi lista personal de lectura incluye para aprender: 1) ''Breve historia de Chile'' de Sergio Villalobos, porque me interesa conocer más sobre la historia de mi país para entender mejor el presente; 2) un artículo científico sobre cambio climático para informarme sobre problemas ambientales actuales; 3) ''El arte de la oratoria'' que me ayudará a mejorar mis habilidades de comunicación. Para recrearme, elegí: 1) ''Cuentos de la selva'' de Horacio Quiroga, porque disfruto las historias cortas y me gusta la naturaleza; 2) ''Harry Potter y la piedra filosofal'' para entretenerme con una historia fantástica; 3) una colección de poemas de Pablo Neruda que me inspiran y me relajan. Cada texto fue seleccionado pensando en mis intereses y cómo puedo aprender o disfrutar con ellos, equilibrando conocimiento y entretenimiento.", "requiere_revision_humana": true}', 5, 0, true, '{lectura,creación,"lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (92, 25, 'multiple_choice', 'all', '{"opciones": {"A": "La estructura gramatical de un texto", "B": "Las emociones y sentimientos de los personajes", "C": "El número de páginas de la obra", "D": "El tipo de letra utilizado en la publicación"}, "pregunta": "¿Cuál de las siguientes opciones representa una dimensión de la experiencia humana que se puede encontrar en obras literarias y otros textos culturales?", "explicacion": "La respuesta correcta es B, porque las emociones y sentimientos de los personajes reflejan una dimensión importante de la experiencia humana en la literatura. La opción A se refiere a un aspecto técnico del lenguaje, no a la experiencia humana. La opción C es una característica física de la obra, sin relación con la experiencia humana. La opción D es un aspecto gráfico que no representa experiencias humanas."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"experiencia humana",literatura,dimensiones,"nivel recordar"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (93, 25, 'multiple_choice', 'all', '{"opciones": {"A": "La estructura gramatical de un texto", "B": "Los sentimientos y emociones de los personajes", "C": "Los tipos de letra utilizados en la edición", "D": "El costo de producción del libro"}, "pregunta": "¿Cuál de las siguientes opciones corresponde a una dimensión de la experiencia humana que suele explorarse en obras literarias?", "explicacion": "La respuesta correcta es B, ya que las obras literarias suelen reflejar y explorar las emociones y sentimientos de los personajes, una dimensión fundamental de la experiencia humana. La opción A se refiere a un aspecto técnico del lenguaje, no a la experiencia humana; la opción C menciona un aspecto físico del libro, que no tiene relación con la experiencia humana; y la opción D trata un aspecto económico, que no es una dimensión de la experiencia humana en la lectura."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"experiencia humana",literatura,emociones,"OA nivel 1"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (94, 25, 'multiple_choice', 'all', '{"opciones": {"A": "La estructura gramatical de un texto", "B": "Las emociones y sentimientos que viven los personajes", "C": "El número de páginas del libro", "D": "El tamaño de la letra utilizada en el texto"}, "pregunta": "¿Cuál de las siguientes opciones representa una dimensión de la experiencia humana que puede encontrarse en obras literarias y otros textos culturales?", "explicacion": "La respuesta correcta es B, ya que las emociones y sentimientos que experimentan los personajes son una dimensión fundamental de la experiencia humana reflejada en la literatura. Esta opción está relacionada directamente con la comprensión de la experiencia humana propia y ajena. La opción A se refiere a un aspecto técnico del lenguaje, no a la experiencia humana; la C y la D son características físicas del texto que no tienen relación con las dimensiones de la experiencia humana."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"experiencia humana",literatura,dimensiones,"primer medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (95, 25, 'true_false', 'all', '{"afirmacion": "Las dimensiones de la experiencia humana en obras literarias incluyen aspectos emocionales, sociales y culturales.", "explicacion": "Esta afirmación es verdadera porque las obras literarias reflejan diversas dimensiones de la experiencia humana, tales como las emociones personales, las relaciones sociales y el contexto cultural en el que se desarrollan, permitiendo así una comprensión integral de la vida humana."}', '{"es_verdadero": true}', 1, 0, true, '{"dimensiones experiencia humana","obras literarias","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (96, 25, 'fill_blanks', 'all', '{"texto": "En las obras literarias y otros textos culturales, es posible identificar diferentes dimensiones de la experiencia humana, tales como la [BLANK_1], que se refiere a los sentimientos y emociones, y la [BLANK_2], que aborda las creencias y valores que guían a las personas.", "pistas": ["Esta dimensión está relacionada con los sentimientos y emociones internas.", "Esta dimensión está vinculada a normas, creencias y principios éticos."], "explicacion": "Las dimensiones de la experiencia humana incluyen aspectos emocionales y éticos, entre otros, que permiten comprender tanto lo interno como lo social en las obras literarias y textos culturales."}', '{"respuestas_correctas": {"BLANK_1": ["dimensión afectiva", "afectiva"], "BLANK_2": ["dimensión ética", "ética"]}}', 2, 0, true, '{"dimensiones experiencia humana",literatura,"primer medio","completar espacios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (97, 26, 'multiple_choice', 'all', '{"opciones": {"A": "Permite entender únicamente el contexto histórico en que se escribió la obra.", "B": "Ayuda a comprender cómo se expresan emociones, valores y conflictos universales a través del texto.", "C": "Sirve para memorizar las características biográficas del autor sin relacionarlas con el texto.", "D": "Garantiza que se recuerden todas las palabras difíciles del texto sin importar su significado."}, "pregunta": "¿Cuál es la importancia de identificar las diferentes dimensiones de la experiencia humana en una obra literaria?", "explicacion": "La respuesta correcta es la B, ya que identificar las diferentes dimensiones de la experiencia humana en una obra literaria permite comprender cómo se expresan emociones, valores y conflictos universales, facilitando una reflexión profunda sobre la propia experiencia y la ajena. La opción A es un error común porque reduce la comprensión solo al contexto histórico, sin abordar la experiencia humana. La C confunde la biografía del autor con la interpretación del texto, lo que limita la comprensión. La D se enfoca en aspectos superficiales como el vocabulario, sin relacionarlo con el significado o relevancia de la experiencia humana."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"Lengua y Literatura","Comprensión lectora","Experiencia humana","Análisis literario"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (98, 26, 'multiple_choice', 'all', '{"opciones": {"A": "Identificar únicamente los hechos históricos presentes en el texto.", "B": "Comprender cómo las emociones, valores y contextos sociales influyen en las vivencias de los personajes y en el mensaje de la obra.", "C": "Reconocer solo la estructura narrativa sin considerar el trasfondo cultural.", "D": "Describir el vocabulario utilizado sin relacionarlo con la experiencia humana."}, "pregunta": "¿Cuál es la principal función de explorar las diferentes dimensiones de la experiencia humana en una obra literaria?", "explicacion": "La opción B es correcta porque entender las emociones, valores y contextos sociales permite una comprensión profunda de la experiencia humana reflejada en la obra, lo que ayuda a captar su significado y relevancia. La opción A es un error conceptual común, pues limita la experiencia humana solo a hechos históricos, ignorando aspectos emocionales y sociales. La opción C falla al separar la estructura narrativa del contexto cultural, lo cual es fundamental para interpretar la experiencia humana. La opción D se centra en aspectos lingüísticos superficiales sin conectar con la dimensión humana del texto."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"experiencia humana","comprensión lectora",literatura,"enseñanza media","nivel 2 bloom"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (99, 26, 'true_false', 'all', '{"afirmacion": "Las obras literarias permiten comprender diferentes dimensiones de la experiencia humana, como las emociones, valores y contextos sociales, a través de sus personajes y tramas.", "explicacion": "Esta afirmación es verdadera porque la literatura refleja la diversidad de la experiencia humana. A través de personajes, situaciones y contextos, las obras literarias nos muestran emociones, conflictos, valores y realidades sociales, facilitando una comprensión más profunda de nosotros mismos y de otros."}', '{"es_verdadero": true}', 2, 0, true, '{"experiencia humana",literatura,comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (100, 26, 'drag_drop_matching', 'all', '{"explicacion": "La dimensión emocional se relaciona con los sentimientos de los personajes; la social con las interacciones y roles en la sociedad; la cultural con las tradiciones y valores representados; la histórica con el contexto temporal y eventos; y la individual con el crecimiento y conflictos internos del personaje.", "instruccion": "Empareja cada dimensión de la experiencia humana con su ejemplo o significado en obras literarias.", "columna_derecha": ["Contexto de una sociedad y sus tradiciones reflejadas en el texto", "Sentimientos y emociones que experimentan los personajes", "El desarrollo personal y las decisiones del protagonista", "Relación entre personajes y grupos sociales en la obra", "Época y acontecimientos históricos que influyen en la trama"], "columna_izquierda": ["Dimensión emocional", "Dimensión social", "Dimensión cultural", "Dimensión histórica", "Dimensión individual"]}', '{"emparejamientos_correctos": {"Dimensión social": "Relación entre personajes y grupos sociales en la obra", "Dimensión cultural": "Contexto de una sociedad y sus tradiciones reflejadas en el texto", "Dimensión emocional": "Sentimientos y emociones que experimentan los personajes", "Dimensión histórica": "Época y acontecimientos históricos que influyen en la trama", "Dimensión individual": "El desarrollo personal y las decisiones del protagonista"}}', 2, 0, true, '{"experiencia humana",dimensiones,literatura,comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (101, 26, 'sequencing', 'all', '{"explicacion": "El análisis comienza con la lectura de la obra literaria para comprender su contenido. Luego, se identifican los personajes y sus vivencias, lo que permite reconocer las diferentes dimensiones de la experiencia humana presentes. Finalmente, se explica la relevancia de estas dimensiones reflejando experiencias propias y ajenas.", "instruccion": "Ordena los siguientes pasos para analizar las dimensiones de la experiencia humana en una obra literaria", "elementos_desordenados": ["Identificar los personajes y sus vivencias", "Reconocer las diferentes dimensiones de la experiencia humana presentes", "Leer atentamente la obra literaria", "Explicar cómo estas dimensiones reflejan aspectos de la experiencia propia y ajena"]}', '{"orden_correcto": ["Leer atentamente la obra literaria", "Identificar los personajes y sus vivencias", "Reconocer las diferentes dimensiones de la experiencia humana presentes", "Explicar cómo estas dimensiones reflejan aspectos de la experiencia propia y ajena"]}', 3, 0, true, '{comprensión,"experiencia humana","análisis literario"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (102, 27, 'multiple_choice', 'all', '{"opciones": {"A": "La experiencia de la soledad y el aislamiento.", "B": "La búsqueda de la felicidad en la vida cotidiana.", "C": "La importancia de la amistad y la cooperación.", "D": "El conflicto entre el bien y el mal en la sociedad."}, "pregunta": "En la lectura del cuento \"La casa de Asterión\" de Jorge Luis Borges, ¿cuál de las siguientes dimensiones de la experiencia humana se refleja principalmente a través del personaje del Minotauro?", "explicacion": "La opción A es la correcta porque el cuento refleja la soledad y el aislamiento del Minotauro, quien vive encerrado y separado del mundo, mostrando una dimensión profunda de su experiencia humana. La opción B es incorrecta porque el relato no se centra en una búsqueda de felicidad cotidiana, sino en el aislamiento existencial. La opción C es un error conceptual común, ya que no hay un enfoque en la amistad ni la cooperación en la historia. La opción D también es incorrecta porque el cuento no aborda un conflicto social entre el bien y el mal, sino una reflexión más introspectiva."}', '{"respuesta_correcta": "A"}', 3, 0, true, '{"experiencia humana","análisis literario","La casa de Asterión","primero medio","Lengua y Literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (103, 27, 'multiple_choice', 'all', '{"opciones": {"A": "Identificando que Asterión es un personaje feliz y social, lo que refleja la importancia de la comunidad en la experiencia humana.", "B": "Reconociendo que la narración refleja la soledad y el aislamiento de Asterión, lo que permite entender cómo estas dimensiones influyen en su percepción del mundo y su identidad.", "C": "Considerando que el cuento es solo una historia fantástica sin relación con experiencias humanas reales.", "D": "Interpretando que la experiencia humana está representada únicamente por la acción violenta que ocurre al final del cuento."}, "pregunta": "En la lectura del cuento \"La casa de Asterión\" de Jorge Luis Borges, ¿cómo se aplica el análisis de las dimensiones de la experiencia humana para interpretar el tema de la soledad y el aislamiento del personaje principal?", "explicacion": "La opción B es correcta porque aplica el análisis de las dimensiones de la experiencia humana (soledad y aislamiento) para interpretar el tema central del cuento y cómo estas influyen en la identidad del personaje. La opción A es incorrecta porque contradice la experiencia vivida por Asterión. La opción C es un error conceptual común que desconoce el valor simbólico y existencial de la literatura. La opción D reduce la experiencia humana a un acto puntual, sin considerar la complejidad del personaje y su vivencia."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"experiencia humana","análisis literario","interpretación de textos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (104, 27, 'drag_drop_matching', 'all', '{"explicacion": "Cada dimensión se relaciona con una forma particular de entender la experiencia humana: la emocional con los sentimientos; la social con las relaciones comunitarias; la cultural con las tradiciones y creencias; la ética con los valores y juicios morales; y la personal con la identidad individual.", "instruccion": "Empareja cada dimensión de la experiencia humana con su descripción correspondiente", "columna_derecha": ["Relación entre valores y juicios sobre lo que está bien o mal", "Sentimientos y estados afectivos que experimenta un personaje", "Costumbres, tradiciones y creencias que influyen en la vida de los personajes", "Interacciones y relaciones entre los personajes dentro de una comunidad", "Aspectos individuales y únicos que conforman la identidad de un personaje"], "columna_izquierda": ["Dimensión emocional", "Dimensión social", "Dimensión cultural", "Dimensión ética", "Dimensión personal"]}', '{"emparejamientos_correctos": {"Dimensión social": "Interacciones y relaciones entre los personajes dentro de una comunidad", "Dimensión ética": "Relación entre valores y juicios sobre lo que está bien o mal", "Dimensión cultural": "Costumbres, tradiciones y creencias que influyen en la vida de los personajes", "Dimensión personal": "Aspectos individuales y únicos que conforman la identidad de un personaje", "Dimensión emocional": "Sentimientos y estados afectivos que experimenta un personaje"}}', 3, 0, true, '{"dimensiones experiencia humana","análisis literario","lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (105, 27, 'sequencing', 'all', '{"explicacion": "El análisis comienza con la lectura atenta de la obra, luego se reconocen las emociones y situaciones de los personajes. Posteriormente, se analiza el contexto cultural para comprender mejor las experiencias. Después, se relacionan estas experiencias con temas universales y finalmente se extrae una interpretación personal basada en todo el análisis.", "instruccion": "Ordena los siguientes pasos para analizar las dimensiones de la experiencia humana en un texto literario", "elementos_desordenados": ["Identificar las emociones y situaciones que viven los personajes", "Leer atentamente el texto literario seleccionado", "Relacionar las experiencias de los personajes con temas universales", "Reconocer el contexto cultural y social de la obra", "Extraer una interpretación personal basada en el análisis realizado"]}', '{"orden_correcto": ["Leer atentamente el texto literario seleccionado", "Identificar las emociones y situaciones que viven los personajes", "Reconocer el contexto cultural y social de la obra", "Relacionar las experiencias de los personajes con temas universales", "Extraer una interpretación personal basada en el análisis realizado"]}', 3, 0, true, '{"análisis literario","dimensiones de la experiencia humana",interpretación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (106, 27, 'open_ended', 'all', '{"pregunta": "Elige una obra literaria estudiada en el curso y analiza cómo se reflejan en ella las diferentes dimensiones de la experiencia humana (emocional, social, cultural, moral). Explica cómo este análisis te ayuda a interpretar los temas principales de la obra.", "explicacion": "El docente debe evaluar que el estudiante no solo reconozca las dimensiones de la experiencia humana, sino que también las aplique para interpretar los temas de la obra. Se valorará la profundidad del análisis, la capacidad de argumentación y la expresión escrita clara y organizada.", "contexto_adicional": "Se sugiere que el estudiante seleccione una obra literaria estudiada en el primer año medio, como un cuento, poema o fragmento de novela, para realizar el análisis solicitado.", "criterios_evaluacion": ["Identificación clara y precisa de al menos dos dimensiones de la experiencia humana presentes en la obra (peso: 30%)", "Análisis coherente y fundamentado que relaciona las dimensiones identificadas con los temas principales de la obra (peso: 40%)", "Claridad y organización en la exposición de ideas, con uso adecuado del lenguaje y estructura (peso: 30%)"]}', '{"puntos_clave": ["Mención de al menos dos dimensiones de la experiencia humana (emocional, social, cultural, moral)", "Relación entre las dimensiones y los temas principales de la obra", "Interpretación fundamentada que evidencia comprensión de la obra"], "respuesta_modelo": "En la obra \"La casa de los espíritus\" de Isabel Allende, se reflejan varias dimensiones de la experiencia humana. Por ejemplo, en la dimensión emocional, se evidencian sentimientos intensos como el amor y el dolor a través de las relaciones entre los personajes. En la dimensión social, se muestra la lucha de clases y las tensiones políticas que afectan a la familia protagonista. Este análisis permite interpretar que uno de los temas principales de la obra es la complejidad de las relaciones humanas en un contexto histórico cargado de conflictos sociales y personales. Así, la obra invita a reflexionar sobre cómo el pasado y las emociones influyen en el presente y futuro de las personas.", "requiere_revision_humana": true}', 3, 0, true, '{literatura,"análisis literario","experiencia humana","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (107, 28, 'compare_contrast', 'all', '{"conceptos": ["Dimensión Emocional", "Dimensión Social"], "criterios": ["Naturaleza de la experiencia", "Manifestación en la literatura", "Impacto en el desarrollo del personaje"], "explicacion": "La dimensión emocional se relaciona con los sentimientos internos y subjetivos de los personajes, mientras que la dimensión social involucra las interacciones y relaciones con otros. Ambas dimensiones se manifiestan en la literatura como motores del conflicto y desarrollo, pero difieren en foco: la emocional en el mundo interno, la social en el entorno externo. En cuanto al impacto, la dimensión emocional suele influir en las decisiones personales, mientras que la social afecta la integración y rol dentro de la comunidad.", "instruccion": "Compara y contrasta las dimensiones emocional y social de la experiencia humana según los criterios dados"}', '{"tabla_correcta": {"Dimensión Social": {"Naturaleza de la experiencia": "Interacciones y relaciones con otros", "Manifestación en la literatura": "Relaciones, normas sociales y conflictos comunitarios", "Impacto en el desarrollo del personaje": "Determina integración y rol social"}, "Dimensión Emocional": {"Naturaleza de la experiencia": "Sentimientos internos y subjetivos", "Manifestación en la literatura": "Reflejo de emociones, dilemas internos y conflictos personales", "Impacto en el desarrollo del personaje": "Guía decisiones y evolución personal"}}}', 3, 0, true, '{"experiencia humana",comparación,"dimensiones emocional y social"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (112, 29, 'criteria_evaluation', 'all', '{"rubrica": {"Relación con la herencia cultural chilena": {"Excelente": "Establece conexiones claras y precisas entre las dimensiones de la experiencia humana y la herencia cultural chilena reflejada en la obra.", "Satisfactorio": "Muestra conexiones generales entre las dimensiones y la herencia cultural chilena, aunque con algunas imprecisiones.", "Necesita mejorar": "No logra relacionar adecuadamente las dimensiones con la herencia cultural chilena o presenta información incorrecta."}, "Identificación de dimensiones de la experiencia humana": {"Excelente": "Identifica claramente y de manera detallada las dimensiones emocionales, sociales, históricas y culturales presentes en el texto, mostrando comprensión profunda.", "Satisfactorio": "Reconoce la mayoría de las dimensiones de la experiencia humana, aunque con algunos detalles poco claros o superficiales.", "Necesita mejorar": "Identifica pocas o ninguna dimensión de la experiencia humana, con falta de comprensión o confusión en el análisis."}, "Justificación de la importancia de comprender estas dimensiones para interpretar la obra": {"Excelente": "Ofrece una justificación sólida y bien argumentada sobre por qué comprender estas dimensiones es crucial para interpretar la obra en el contexto de la herencia cultural chilena.", "Satisfactorio": "Proporciona una justificación adecuada pero general, con argumentos poco profundos o incompletos.", "Necesita mejorar": "Presenta una justificación débil o confusa, sin relacionar claramente la importancia de las dimensiones con la interpretación de la obra."}}, "explicacion": "Una evaluación completa debe identificar con claridad las dimensiones de la experiencia humana, justificar por qué su comprensión es importante para interpretar la obra y conectar estos aspectos con la herencia cultural chilena, mostrando un nivel de análisis crítico acorde al nivel evaluativo pedido.", "instruccion": "Evalúa el análisis del fragmento literario según la rúbrica propuesta y justifica la calificación que otorgas para cada criterio.", "caso_a_evaluar": "Lee el siguiente fragmento de la obra literaria \"La casa de los espíritus\" de Isabel Allende y analiza cómo se reflejan las diferentes dimensiones de la experiencia humana (emocional, social, histórica y cultural) presentes en el texto, especialmente en relación con la herencia cultural chilena."}', '{"evaluacion_modelo": {"Relación con la herencia cultural chilena": "Excelente", "Identificación de dimensiones de la experiencia humana": "Excelente", "Justificación de la importancia de comprender estas dimensiones para interpretar la obra": "Satisfactorio"}, "justificacion_modelo": "El análisis muestra una identificación clara y detallada de las dimensiones emocionales, sociales, históricas y culturales presentes en el texto, demostrando comprensión profunda. Sin embargo, la justificación sobre la importancia de comprender estas dimensiones para interpretar la obra es adecuada pero algo general, con argumentos no del todo profundizados. Finalmente, la relación con la herencia cultural chilena es precisa y bien desarrollada, evidenciando un buen nivel de conexión crítica.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",Evaluar,"Experiencia Humana","Herencia Cultural Chilena","Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (108, 28, 'compare_contrast', 'all', '{"conceptos": ["Dimensión Emocional", "Dimensión Social"], "criterios": ["Manifestación en la obra literaria", "Impacto en el personaje principal", "Relación con el contexto cultural"], "explicacion": "La dimensión emocional se refiere a los sentimientos y estados internos de los personajes, mientras que la dimensión social se relaciona con las interacciones y relaciones entre personajes dentro de su entorno cultural. Ambas dimensiones son fundamentales para entender la experiencia humana, pero se manifiestan y afectan a los personajes de maneras diferentes.", "instruccion": "Compara y contrasta las dimensiones emocional y social de la experiencia humana según los criterios dados"}', '{"tabla_correcta": {"Dimensión Social": {"Impacto en el personaje principal": "Condiciona sus acciones y su posición en la sociedad", "Relación con el contexto cultural": "Evidencia las estructuras sociales y tradiciones culturales", "Manifestación en la obra literaria": "Representación de relaciones, roles sociales y normas comunitarias"}, "Dimensión Emocional": {"Impacto en el personaje principal": "Influye en sus decisiones y desarrollo interno", "Relación con el contexto cultural": "Puede reflejar valores o tabúes emocionales propios de la cultura", "Manifestación en la obra literaria": "Exploración de sentimientos, conflictos internos y emociones personales"}}}', 4, 0, true, '{"dimensiones experiencia humana","comparar y contrastar","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (109, 28, 'open_ended', 'all', '{"pregunta": "Analiza dos obras literarias estudiadas en el curso que aborden diferentes dimensiones de la experiencia humana (por ejemplo, la identidad y la conflictividad social). Completa una tabla comparativa identificando y clasificando cómo cada obra representa estas dimensiones, y argumenta cuál te parece que ofrece una visión más profunda o compleja de la experiencia humana y por qué.", "explicacion": "El docente debe evaluar que el estudiante no solo identifique las dimensiones presentes en cada obra, sino que también las compare de manera crítica y argumente con fundamentos sólidos su elección, demostrando un análisis profundo y claro.", "contexto_adicional": "Se consideran obras literarias y textos culturales trabajados durante el curso de Primero Medio, que aborden temáticas centrales como identidad, conflicto social, familia, memoria, entre otros.", "criterios_evaluacion": ["Identificación clara y precisa de las dimensiones de la experiencia humana presentes en cada obra (peso: 30%)", "Capacidad de comparación y clasificación adecuada y estructurada en la tabla, mostrando comprensión crítica de las obras (peso: 40%)", "Argumentación coherente y fundamentada sobre cuál obra ofrece una visión más profunda o compleja, con ejemplos textuales o contextuales (peso: 30%)"]}', '{"puntos_clave": ["Identificación de dimensiones como identidad, memoria, conflicto social, familia, etc.", "Comparación estructurada y clara en formato de tabla o similar", "Argumentación que incluya ejemplos concretos y justifique la elección de la obra con visión más profunda"], "respuesta_modelo": "En la obra \"La Casa de los Espíritus\" de Isabel Allende, se aborda la dimensión de la memoria familiar y la identidad a través de la historia de varias generaciones, mostrando cómo el pasado influye en la construcción personal. En contraste, \"Los Detectives Salvajes\" de Roberto Bolaño explora la conflictividad social y la búsqueda de sentido en un contexto urbano y literario. En la tabla, se clasifica \"La Casa de los Espíritus\" bajo las dimensiones de memoria y familia, mientras que \"Los Detectives Salvajes\" se clasifica en conflicto social y búsqueda existencial. Considero que \"La Casa de los Espíritus\" ofrece una visión más profunda porque entrelaza experiencias personales y colectivas, mostrando cómo la historia y la identidad se construyen en múltiples niveles y tiempos, lo que se evidencia en los relatos intergeneracionales y en la mezcla de lo real con lo fantástico.", "requiere_revision_humana": true}', 4, 0, true, '{lengua_y_literatura,comparacion,analisis,experiencia_humana}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (110, 28, 'concept_map', 'all', '{"conceptos": ["Dimensiones de la experiencia humana", "Obras literarias", "Textos culturales", "Emociones", "Contexto histórico", "Valores", "Identidad", "Conflictos"], "explicacion": "Se espera que el estudiante organice los conceptos mostrando cómo las obras literarias y textos culturales reflejan distintas dimensiones de la experiencia humana, tales como emociones, valores, identidad y conflictos, y cómo estas están influenciadas por el contexto histórico.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la experiencia humana reflejada en obras literarias y textos culturales?"}', '{"conceptos_centrales": ["Dimensiones de la experiencia humana", "Obras literarias"], "relaciones_esperadas": [{"origen": "Obras literarias", "destino": "Dimensiones de la experiencia humana", "relacion": "reflejan"}, {"origen": "Textos culturales", "destino": "Dimensiones de la experiencia humana", "relacion": "reflejan"}, {"origen": "Dimensiones de la experiencia humana", "destino": "Emociones", "relacion": "incluyen"}, {"origen": "Dimensiones de la experiencia humana", "destino": "Valores", "relacion": "incluyen"}, {"origen": "Dimensiones de la experiencia humana", "destino": "Identidad", "relacion": "incluyen"}, {"origen": "Dimensiones de la experiencia humana", "destino": "Conflictos", "relacion": "incluyen"}, {"origen": "Contexto histórico", "destino": "Obras literarias", "relacion": "influye en"}, {"origen": "Contexto histórico", "destino": "Textos culturales", "relacion": "influye en"}], "requiere_revision_humana": true}', 3, 0, true, '{"experiencia humana","mapa conceptual",análisis,literatura,"textos culturales"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (111, 28, 'concept_map', 'all', '{"conceptos": ["Dimensiones de la experiencia humana", "Emociones", "Contexto cultural", "Conflicto", "Resolución", "Personajes", "Temas literarios"], "explicacion": "Se espera que el estudiante organice los conceptos para mostrar cómo las dimensiones de la experiencia humana se manifiestan a través de emociones, conflictos y personajes dentro de un contexto cultural, y cómo estos elementos contribuyen al desarrollo de los temas literarios y las resoluciones en las obras.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la experiencia humana en obras literarias y textos culturales?"}', '{"conceptos_centrales": ["Dimensiones de la experiencia humana", "Conflicto"], "relaciones_esperadas": [{"origen": "Dimensiones de la experiencia humana", "destino": "Emociones", "relacion": "se manifiestan en"}, {"origen": "Dimensiones de la experiencia humana", "destino": "Conflicto", "relacion": "se expresan en"}, {"origen": "Contexto cultural", "destino": "Personajes", "relacion": "influye en"}, {"origen": "Conflicto", "destino": "Personajes", "relacion": "es enfrentado por"}, {"origen": "Conflicto", "destino": "Resolución", "relacion": "conduce a"}, {"origen": "Emociones", "destino": "Temas literarios", "relacion": "impactan en"}, {"origen": "Personajes", "destino": "Dimensiones de la experiencia humana", "relacion": "representan"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Mapa Conceptual","Experiencia Humana","Análisis Literario"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (475, 102, 'true_false', 'all', '{"afirmacion": "El estilo directo reproduce exactamente las palabras del hablante, mientras que el estilo indirecto las reformula en tercera persona y puede modificar los tiempos verbales.", "explicacion": "Esta afirmación es verdadera porque el estilo directo cita literalmente lo que dijo alguien, usando comillas o guiones para indicar la reproducción exacta. En cambio, el estilo indirecto parafrasea lo dicho, suele usar tercera persona y ajusta los tiempos verbales para mantener la coherencia temporal."}', '{"es_verdadero": true}', 1, 0, true, '{"estilo directo","estilo indirecto","Lengua y Literatura","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (113, 29, 'criteria_evaluation', 'all', '{"rubrica": {"Claridad y coherencia en la argumentación": {"Excelente": "El análisis es claro, coherente y bien estructurado, con argumentos consistentes y bien fundamentados.", "Satisfactorio": "El análisis es generalmente claro, pero presenta algunas inconsistencias o falta de profundidad en la argumentación.", "Necesita mejorar": "El análisis es confuso, incoherente o carece de una estructura lógica que sustente los argumentos."}, "Capacidad de relacionar la obra literaria con la herencia cultural chilena": {"Excelente": "El análisis establece conexiones claras y precisas entre la obra y aspectos relevantes de la herencia cultural chilena, demostrando comprensión profunda del contexto cultural.", "Satisfactorio": "El estudiante hace algunas conexiones con la herencia cultural chilena, pero estas son generales o poco detalladas.", "Necesita mejorar": "El estudiante no logra relacionar la obra con la herencia cultural chilena o las conexiones son confusas o incorrectas."}, "Justificación de la importancia de las dimensiones de la experiencia humana": {"Excelente": "El estudiante explica claramente y con profundidad cómo cada dimensión de la experiencia humana es fundamental para interpretar la obra, relacionándola coherentemente con la herencia cultural chilena.", "Satisfactorio": "El estudiante menciona algunas dimensiones de la experiencia humana y su importancia para la interpretación, pero con explicaciones superficiales o poco desarrolladas.", "Necesita mejorar": "El estudiante identifica pocas o ninguna dimensión de la experiencia humana y no logra justificar su importancia para la interpretación de la obra."}}, "explicacion": "Se espera que el estudiante demuestre una comprensión profunda de las dimensiones de la experiencia humana y su relevancia para interpretar la obra literaria en el contexto de la herencia cultural chilena, así como que presente una argumentación clara y coherente que sustente su evaluación.", "instruccion": "Evalúa el análisis presentado por el estudiante según la rúbrica y justifica tu calificación en cada criterio.", "caso_a_evaluar": "Un estudiante presenta un análisis crítico sobre la novela ''La Casa de los Espíritus'' de Isabel Allende, enfocándose en cómo las diferentes dimensiones de la experiencia humana (social, histórica, emocional y cultural) se reflejan en la obra y explicando su relevancia para entender la herencia cultural chilena."}', '{"evaluacion_modelo": {"Claridad y coherencia en la argumentación": "Satisfactorio", "Capacidad de relacionar la obra literaria con la herencia cultural chilena": "Excelente", "Justificación de la importancia de las dimensiones de la experiencia humana": "Excelente"}, "justificacion_modelo": "El análisis presenta una explicación profunda y bien fundamentada sobre cómo las dimensiones social, histórica, emocional y cultural son esenciales para interpretar ''La Casa de los Espíritus'', conectando claramente estos elementos con la herencia cultural chilena. Sin embargo, aunque la argumentación es en general clara, en algunas partes presenta ligeras inconsistencias o falta de profundidad, lo que justifica la calificación satisfactoria en claridad y coherencia.", "requiere_revision_humana": true}', 5, 0, true, '{evaluación,"literatura chilena","experiencia humana","herencia cultural","debate guiado"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (114, 29, 'criteria_evaluation', 'all', '{"rubrica": {"Claridad y coherencia en la argumentación": {"Excelente": "La argumentación es clara, coherente y bien estructurada, con uso adecuado del lenguaje y ejemplos pertinentes.", "Satisfactorio": "La argumentación es generalmente clara pero presenta algunas incoherencias o falta de profundidad en la estructura o ejemplos.", "Necesita mejorar": "La argumentación es confusa, desorganizada o carece de ejemplos relevantes que sustenten el análisis."}, "Conexión con la herencia cultural chilena": {"Excelente": "El estudiante establece con precisión y argumentos sólidos la relación entre la obra literaria y la herencia cultural chilena, demostrando comprensión del contexto histórico y social.", "Satisfactorio": "El estudiante hace una conexión general con la herencia cultural chilena, pero con explicaciones poco detalladas o ejemplos limitados.", "Necesita mejorar": "El estudiante no logra relacionar adecuadamente la obra con la herencia cultural chilena o sus argumentos son confusos o insuficientes."}, "Justificación de la importancia de las dimensiones de la experiencia humana": {"Excelente": "El estudiante identifica con claridad y profundidad múltiples dimensiones de la experiencia humana presentes en la obra, explicando su relevancia para la interpretación literaria y cultural.", "Satisfactorio": "El estudiante reconoce algunas dimensiones de la experiencia humana y su importancia para la interpretación, aunque con argumentos menos desarrollados o ejemplos limitados.", "Necesita mejorar": "El estudiante menciona superficialmente las dimensiones de la experiencia humana o no logra conectar su importancia con la interpretación de la obra."}}, "explicacion": "Se espera que el estudiante demuestre capacidad para identificar y justificar la importancia de las dimensiones de la experiencia humana en la interpretación literaria, vinculando la obra con la herencia cultural chilena, y presentando una argumentación clara y coherente.", "instruccion": "Evalúa el análisis presentado según la rúbrica y justifica tu calificación en cada criterio, explicando por qué otorgas ese nivel de desempeño.", "caso_a_evaluar": "Se presenta un análisis escrito sobre la obra \"La casa de los espíritus\" de Isabel Allende, donde el estudiante argumenta cómo las diferentes dimensiones de la experiencia humana (emocional, social, histórica y cultural) se reflejan en la narrativa y cómo su comprensión permite una interpretación más profunda de la obra como parte de la herencia cultural chilena."}', '{"evaluacion_modelo": {"Claridad y coherencia en la argumentación": "Excelente", "Conexión con la herencia cultural chilena": "Excelente", "Justificación de la importancia de las dimensiones de la experiencia humana": "Satisfactorio"}, "justificacion_modelo": "El análisis reconoce algunas dimensiones de la experiencia humana pero no las desarrolla en profundidad, por lo que se califica como satisfactorio. En cambio, la conexión con la herencia cultural chilena es clara y bien fundamentada, así como la argumentación, que se presenta coherente y bien estructurada.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Evaluación,"Experiencia Humana","Herencia Cultural Chilena"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (115, 29, 'open_ended', 'all', '{"pregunta": "Considerando una obra literaria chilena que hayas leído en clase, ¿por qué es fundamental comprender las diferentes dimensiones de la experiencia humana (emocional, social, histórica, cultural) para interpretar adecuadamente la obra y su aporte a nuestra herencia cultural? Justifica tu respuesta con ejemplos específicos del texto.", "explicacion": "El docente debe evaluar si el alumno comprende la complejidad de la experiencia humana en la obra y cómo esta comprensión enriquece la interpretación literaria y cultural. Se valorará que el estudiante justifique con ejemplos concretos y que establezca conexiones claras con la herencia cultural chilena.", "contexto_adicional": "El estudiante debe basar su respuesta en una obra literaria chilena abordada durante el curso, considerando las distintas dimensiones de la experiencia humana que se presentan en ella.", "criterios_evaluacion": ["Claridad y profundidad en la justificación de la importancia de comprender las dimensiones de la experiencia humana para la interpretación de la obra (peso: 40%)", "Uso adecuado y pertinente de ejemplos específicos de la obra literaria para apoyar la argumentación (peso: 35%)", "Capacidad para relacionar la interpretación de la obra con su significado dentro de la herencia cultural chilena (peso: 25%)"]}', '{"puntos_clave": ["Importancia de las dimensiones de la experiencia humana para la interpretación literaria", "Ejemplos específicos de la obra que evidencien esas dimensiones", "Relación entre la obra y su aporte a la herencia cultural chilena"], "respuesta_modelo": "Comprender las dimensiones de la experiencia humana, como las emocionales, sociales, históricas y culturales, es fundamental para interpretar correctamente la obra ''La casa de los espíritus'' de Isabel Allende. Por ejemplo, el contexto histórico de la dictadura chilena influye en las decisiones y sufrimientos de los personajes, reflejando las tensiones sociales del país. Además, las emociones profundas y los conflictos familiares muestran cómo la experiencia humana atraviesa las generaciones y condiciona sus relaciones. Esta comprensión permite valorar la obra no solo como una narrativa personal, sino también como un reflejo significativo de la herencia cultural chilena que ayuda a entender la historia y la identidad nacional.", "requiere_revision_humana": true}', 4, 0, true, '{"lengua y literatura",evaluar,"experiencia humana","herencia cultural","interpretación literaria"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (116, 29, 'open_ended', 'all', '{"pregunta": "Analiza cómo la comprensión de las distintas dimensiones de la experiencia humana en la cultura chilena contribuye a una interpretación más profunda de una obra literaria estudiada en clase. Justifica por qué es importante considerar estas dimensiones para valorar la herencia cultural reflejada en el texto.", "explicacion": "El docente debe evaluar si el estudiante logra identificar correctamente las dimensiones humanas (como social, histórica, emocional, cultural) en la obra y si explica con argumentos sólidos por qué su comprensión enriquece la interpretación y valoración de la herencia cultural. Se considera además la organización y claridad en la respuesta para asegurar una comunicación efectiva.", "contexto_adicional": "Puede basarse en cualquier obra literaria chilena estudiada durante el curso, considerando temas culturales y sociales presentes en ella.", "criterios_evaluacion": ["Capacidad para identificar y explicar las dimensiones de la experiencia humana presentes en la obra literaria (peso: 30%)", "Argumentación y justificación clara sobre la importancia de comprender estas dimensiones para interpretar la obra y valorar la herencia cultural chilena (peso: 40%)", "Estructura, coherencia y claridad en la presentación de la respuesta (peso: 30%)"]}', '{"puntos_clave": ["Identificación de dimensiones humanas presentes en la obra (social, histórica, emocional, cultural).", "Justificación de cómo estas dimensiones aportan a la interpretación profunda del texto.", "Relación clara entre la comprensión de la experiencia humana y la valoración de la herencia cultural chilena."], "respuesta_modelo": "La comprensión de las dimensiones de la experiencia humana, como el contexto histórico, social y cultural, es fundamental para interpretar correctamente la obra «La casa de los espíritus» de Isabel Allende. Por ejemplo, entender la realidad política y social de Chile durante el siglo XX permite captar las tensiones y conflictos que atraviesan a los personajes y que reflejan la historia nacional. Además, reconocer las emociones y vivencias personales de los personajes ayuda a comprender los temas universales de amor, pérdida y memoria que se entrelazan con la cultura chilena. Por lo tanto, considerar estas dimensiones no solo enriquece la interpretación literaria sino que también permite valorar cómo la obra preserva y transmite elementos esenciales de la herencia cultural chilena.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Evaluación,"Experiencia Humana","Herencia Cultural Chilena"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (117, 30, 'open_ended', 'all', '{"pregunta": "Después de leer las obras y textos estudiados en este curso, crea una reflexión escrita en la que integres al menos tres dimensiones de la experiencia humana (por ejemplo: emocional, social, cultural, ética, histórica). Explica cómo estas dimensiones se manifiestan en las obras y cómo te conectan con experiencias propias o ajenas.", "explicacion": "El docente debe evaluar que la reflexión no solo identifique dimensiones de la experiencia humana, sino que también las conecte críticamente con las obras y experiencias personales o ajenas, valorando la originalidad y la calidad expresiva del texto.", "contexto_adicional": "Los estudiantes pueden basarse en las obras literarias y textos culturales abordados durante el curso para fundamentar su reflexión.", "criterios_evaluacion": ["Integración de dimensiones de la experiencia humana: identificación clara y coherente de al menos tres dimensiones y su relación con las obras (peso: 40%)", "Profundidad y creatividad en la reflexión: análisis crítico y originalidad en la conexión entre las obras y las experiencias humanas (peso: 35%)", "Claridad y coherencia en la expresión escrita: organización, gramática y uso adecuado del lenguaje (peso: 25%)"]}', '{"puntos_clave": ["Identificación de al menos tres dimensiones de la experiencia humana", "Análisis de cómo estas dimensiones se presentan en las obras", "Conexión personal o con experiencias ajenas que muestre reflexión crítica"], "respuesta_modelo": "En las obras estudiadas, se pueden observar claramente diversas dimensiones de la experiencia humana. Por ejemplo, la dimensión emocional se refleja en los conflictos internos de los personajes, como la tristeza y la esperanza que atraviesan en sus circunstancias. La dimensión social se manifiesta en las relaciones entre los personajes y el entorno en que viven, mostrando tensiones y solidaridades propias de su comunidad. Finalmente, la dimensión histórica y cultural aparece en el contexto en que se desarrollan las historias, permitiendo entender cómo la tradición y la época influyen en las decisiones y valores de los personajes. Esta reflexión me conecta con experiencias personales, ya que también he vivido momentos de incertidumbre emocional y he aprendido sobre la importancia de la comunidad en el desarrollo personal. Así, las obras no solo narran historias, sino que nos invitan a comprendernos a nosotros mismos y a otros desde múltiples perspectivas.", "requiere_revision_humana": true}', 4, 0, true, '{lengua_y_literatura,primero_medio,escritura_creativa,experiencia_humana,reflexion}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (118, 30, 'open_ended', 'all', '{"pregunta": "Elige una o dos obras literarias estudiadas en el curso y diseña una reflexión creativa (puede ser un poema, una carta, un monólogo, o un ensayo breve) que integre y muestre las diferentes dimensiones de la experiencia humana presentes en ellas, tales como las emociones, las relaciones sociales, los conflictos internos y el contexto cultural. Explica cómo estas dimensiones se relacionan y contribuyen a una comprensión más profunda de la condición humana.", "explicacion": "El docente debe valorar en qué medida el estudiante logra conectar y analizar las diferentes dimensiones de la experiencia humana a partir de las obras, la originalidad en la expresión creativa y la capacidad de organizar sus ideas de manera clara y coherente.", "contexto_adicional": "Se espera que el estudiante utilice elementos y temas de las obras literarias estudiadas en Primero Medio para construir una reflexión personal y creativa que evidencie un análisis profundo de la experiencia humana.", "criterios_evaluacion": ["Integración y profundidad en la reflexión sobre las dimensiones de la experiencia humana (peso: 40%)", "Creatividad y originalidad en la forma y el contenido de la reflexión escrita (peso: 30%)", "Claridad, coherencia y organización del texto (peso: 30%)"]}', '{"puntos_clave": ["Mención y análisis de dimensiones de la experiencia humana presentes en la(s) obra(s)", "Uso de una forma creativa (poema, carta, monólogo, ensayo) para expresar la reflexión", "Relación clara entre emociones, relaciones, conflictos y contexto cultural", "Coherencia y organización en la presentación de ideas"], "respuesta_modelo": "En la obra \"La casa de los espíritus\" de Isabel Allende, se exploran múltiples dimensiones de la experiencia humana, como el amor, la memoria, el poder y la injusticia social. A través de un monólogo ficticio desde la perspectiva de Clara, la protagonista, expreso cómo sus emociones profundas y sus relaciones familiares complejas reflejan los conflictos internos y externos que marcan su vida y la de su entorno. La historia se enmarca en un contexto histórico turbulento, lo que añade una capa cultural que influye en sus decisiones y sentimientos. Esta reflexión creativa busca mostrar cómo las experiencias personales y sociales están entrelazadas y cómo juntas conforman la riqueza de la condición humana.", "requiere_revision_humana": true}', 5, 0, true, '{escritura_creativa,reflexión,experiencia_humana,análisis_literario}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (119, 30, 'concept_map', 'all', '{"conceptos": ["Dimensiones de la experiencia humana", "Obras literarias", "Contexto cultural", "Emociones y sentimientos", "Reflexión personal", "Herencia cultural", "Identidad"], "explicacion": "Se espera que el estudiante conecte cómo las obras literarias, en su contexto cultural y como parte de nuestra herencia cultural, permiten explorar diferentes dimensiones de la experiencia humana, incluyendo las emociones y sentimientos, que a su vez influencian la identidad y fomentan una reflexión personal.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la reflexión sobre la experiencia humana a partir de la lectura literaria?"}', '{"conceptos_centrales": ["Obras literarias", "Dimensiones de la experiencia humana"], "relaciones_esperadas": [{"origen": "Obras literarias", "destino": "Dimensiones de la experiencia humana", "relacion": "representan"}, {"origen": "Dimensiones de la experiencia humana", "destino": "Emociones y sentimientos", "relacion": "incluyen"}, {"origen": "Obras literarias", "destino": "Contexto cultural", "relacion": "se enmarcan en"}, {"origen": "Contexto cultural", "destino": "Herencia cultural", "relacion": "forma parte de"}, {"origen": "Herencia cultural", "destino": "Identidad", "relacion": "contribuye a"}, {"origen": "Dimensiones de la experiencia humana", "destino": "Reflexión personal", "relacion": "alimentan"}, {"origen": "Reflexión personal", "destino": "Identidad", "relacion": "modifica"}], "requiere_revision_humana": true}', 4, 0, true, '{mapa_conceptual,creatividad,reflexion,experiencia_humana,literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (120, 30, 'concept_map', 'all', '{"conceptos": ["Dimensiones de la experiencia humana", "Identidad", "Emoción", "Contexto cultural", "Obras literarias", "Reflexión personal"], "explicacion": "Se espera que el estudiante organice los conceptos mostrando cómo las obras literarias reflejan distintas dimensiones de la experiencia humana, tales como la identidad y la emoción, las cuales están influenciadas por el contexto cultural. Además, debe conectar cómo la reflexión personal permite integrar y comprender estas dimensiones a partir de la lectura.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la experiencia humana reflejada en las obras literarias estudiadas?"}', '{"conceptos_centrales": ["Dimensiones de la experiencia humana", "Obras literarias"], "relaciones_esperadas": [{"origen": "Obras literarias", "destino": "Dimensiones de la experiencia humana", "relacion": "reflejan"}, {"origen": "Dimensiones de la experiencia humana", "destino": "Identidad", "relacion": "incluyen"}, {"origen": "Dimensiones de la experiencia humana", "destino": "Emoción", "relacion": "incluyen"}, {"origen": "Dimensiones de la experiencia humana", "destino": "Contexto cultural", "relacion": "son influenciadas por"}, {"origen": "Reflexión personal", "destino": "Dimensiones de la experiencia humana", "relacion": "integra"}, {"origen": "Reflexión personal", "destino": "Obras literarias", "relacion": "se basa en"}], "requiere_revision_humana": true}', 5, 0, true, '{mapa_conceptual,experiencia_humana,literatura,reflexion}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (121, 30, 'open_ended', 'all', '{"pregunta": "Elige una obra literaria o texto estudiado en el curso y crea una reflexión escrita en la que integres al menos tres dimensiones de la experiencia humana (emocional, social, cultural, histórica, o personal). Explica cómo estas dimensiones se manifiestan en la obra y qué aprendizajes o cuestionamientos te genera en relación con tu propia vida o la sociedad actual.", "explicacion": "El docente debe evaluar si el estudiante no solo identifica las dimensiones de la experiencia humana en la obra, sino que también logra relacionarlas de manera crítica y creativa con su propia realidad o contexto social. Se valorará la originalidad y profundidad del análisis, además de la calidad comunicativa del texto.", "contexto_adicional": "Considera las obras y textos literarios abordados durante el curso, así como temas presentes en la experiencia humana que se hayan discutido en clase.", "criterios_evaluacion": ["Integración coherente y profunda de al menos tres dimensiones de la experiencia humana en la reflexión (peso: 40%)", "Capacidad de análisis crítico y personal sobre la obra y su relación con la propia experiencia o la sociedad (peso: 35%)", "Claridad, organización y creatividad en la expresión escrita (peso: 25%)"]}', '{"puntos_clave": ["Identificación y explicación de al menos tres dimensiones de la experiencia humana en la obra", "Relación crítica y personal con la obra y su contexto", "Uso claro, organizado y creativo del lenguaje en la reflexión"], "respuesta_modelo": "En la obra \"La Casa de los Espíritus\" de Isabel Allende, se manifiestan diversas dimensiones de la experiencia humana que me hicieron reflexionar profundamente. Desde la dimensión emocional, la historia muestra cómo el amor y el dolor atraviesan a varias generaciones, revelando la complejidad de las relaciones familiares. En cuanto a la dimensión social, la novela retrata las desigualdades y conflictos políticos de Chile, lo que me llevó a cuestionar cómo estas realidades afectan a las personas comunes y su capacidad de resistencia. Finalmente, en la dimensión cultural e histórica, se evidencia la mezcla de tradiciones y cambios sociales que configuran la identidad de los personajes. Esta lectura me hizo pensar en cómo la historia de mi país influye en mi vida cotidiana y en la importancia de reconocer la memoria colectiva para entender nuestro presente.", "requiere_revision_humana": true}', 5, 0, true, '{escritura_creativa,experiencia_humana,reflexion_critica,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (122, 31, 'multiple_choice', 'all', '{"opciones": {"A": "El lugar donde se desarrolla la historia", "B": "El problema o desafío que enfrentan los personajes", "C": "La descripción física de los personajes", "D": "La opinión del narrador sobre la historia"}, "pregunta": "¿Cuál de los siguientes elementos corresponde al conflicto principal en una narración?", "explicacion": "La respuesta correcta es B, ya que el conflicto principal es el problema o desafío que enfrentan los personajes y que impulsa la acción de la narración. La opción A es incorrecta porque el lugar es el escenario, no el conflicto. La opción C describe a los personajes, pero no el conflicto. La opción D se refiere al tipo de narrador, no al conflicto."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{conflicto,narración,"elementos básicos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (123, 31, 'multiple_choice', 'all', '{"opciones": {"A": "La descripción del lugar donde ocurre la historia", "B": "La oposición o problema que enfrentan los personajes", "C": "El tipo de narrador que cuenta la historia", "D": "La cantidad de personajes que aparecen en la narración"}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para identificar el conflicto principal en una narración?", "explicacion": "La respuesta correcta es B, porque el conflicto principal se refiere al problema o desafío que enfrentan los personajes y que impulsa la trama de la narración. La opción A es un elemento importante para ambientar la historia, pero no define el conflicto. La opción C se relaciona con la perspectiva narrativa, no con el conflicto principal. La opción D está relacionada con los personajes, pero el número de personajes no determina el conflicto."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{narración,conflicto,personajes,"tipo de narrador"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (126, 31, 'fill_blanks', 'all', '{"texto": "En una narración, el problema principal que impulsa la historia se llama [BLANK_1], mientras que los seres que participan y realizan acciones se conocen como [BLANK_2]. Además, la voz que cuenta la historia es el [BLANK_3].", "pistas": ["Es el conflicto central que mueve la trama", "Son las personas o seres que interactúan en la historia", "Es quien relata los hechos en la narración"], "explicacion": "Los elementos básicos de una narración incluyen el conflicto o problema principal (conflicto), los personajes que intervienen en la historia, y el narrador, que es la voz que cuenta los acontecimientos."}', '{"respuestas_correctas": {"BLANK_1": ["conflicto", "problema"], "BLANK_2": ["personajes", "protagonistas", "actores"], "BLANK_3": ["narrador", "voz narrativa"]}}', 2, 0, true, '{narración,"elementos básicos",conflicto,personajes,narrador}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (127, 32, 'multiple_choice', 'all', '{"opciones": {"A": "La relación entre personajes es irrelevante, y el narrador solo aporta detalles descriptivos sin influir en la historia.", "B": "La relación entre personajes muestra sus motivaciones y conflictos, mientras que el narrador guía al lector para interpretar esos elementos y comprender el relato.", "C": "El narrador siempre es un personaje dentro de la historia, por lo que su influencia es la misma que la de cualquier otro personaje.", "D": "Las motivaciones de los personajes no afectan la comprensión, ya que el narrador presenta los hechos de forma objetiva y sin sesgos."}, "pregunta": "En una narración, ¿cómo influye la relación entre personajes y el punto de vista del narrador en la comprensión del relato?", "explicacion": "La opción B es correcta porque la relación entre personajes revela sus motivaciones y conflictos, elementos esenciales para entender la trama. Además, el narrador, según su perspectiva y estilo, orienta al lector en la interpretación del relato, influyendo en la comprensión. La opción A es incorrecta porque minimiza la importancia de las relaciones y el papel del narrador. La C confunde el rol del narrador, que no siempre es un personaje dentro de la historia. La D asume que el narrador es completamente objetivo y desconoce que su punto de vista puede teñir la presentación de los hechos."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{relacion_de_personajes,narrador,comprension_de_textos}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (128, 32, 'multiple_choice', 'all', '{"opciones": {"A": "Analizar únicamente las acciones de los personajes, sin considerar sus motivaciones ni la narración.", "B": "Identificar cómo las motivaciones de los personajes afectan su relación y cómo el narrador presenta esta interacción para guiar la interpretación del relato.", "C": "Centrarse en la descripción física de los personajes y asumir que el narrador es neutral y sin influencia en la historia.", "D": "Explicar que la relación entre personajes no es importante si el narrador es en primera persona."}, "pregunta": "¿Cuál es la mejor manera de explicar cómo la relación entre dos personajes influye en la comprensión de una narración, considerando además la perspectiva del narrador?", "explicacion": "La opción B es correcta porque implica analizar las motivaciones internas de los personajes y cómo estas afectan su relación, además de considerar la influencia del narrador en la interpretación del relato, lo cual enriquece la comprensión de la narración. La opción A es un error común pues ignora las motivaciones y el papel del narrador. La opción C falla al asumir que el narrador es neutral y se centra solo en aspectos externos de los personajes. La opción D es incorrecta porque la relación entre personajes sigue siendo relevante independientemente de la perspectiva narrativa."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"relación de personajes",motivaciones,narrador,"comprensión de texto"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (129, 32, 'true_false', 'all', '{"afirmacion": "En una narración, el narrador puede influir en cómo comprendemos las motivaciones y relaciones entre los personajes al presentar la historia desde un punto de vista particular.", "explicacion": "Esta afirmación es verdadera porque el narrador, al elegir qué detalles destacar y cómo presentar a los personajes, afecta la percepción del lector sobre las motivaciones y relaciones entre ellos. Por ejemplo, un narrador en primera persona puede mostrar un sesgo, mientras que un narrador omnisciente ofrece una visión más completa."}', '{"es_verdadero": true}', 2, 0, true, '{narrador,"relaciones entre personajes",motivaciones,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (130, 32, 'drag_drop_matching', 'all', '{"explicacion": "El conflicto principal es el problema central que mueve la trama. La motivación del personaje es la razón que impulsa sus acciones. La relación entre personajes puede ser, por ejemplo, una rivalidad que influye en la historia. El narrador omnisciente conoce todos los pensamientos y sentimientos de los personajes, mientras que el narrador en primera persona cuenta la historia desde su propia perspectiva. ''Ambiente'' es un distractor que no tiene pareja en esta actividad.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente.", "columna_derecha": ["Razón que impulsa las acciones de un personaje", "Historia de rivalidad entre dos personajes", "Narrador que conoce todos los pensamientos y sentimientos", "Problema central que mueve la trama", "Narrador que cuenta la historia desde su propia perspectiva"], "columna_izquierda": ["Conflicto principal", "Motivación del personaje", "Relación entre personajes", "Narrador omnisciente", "Narrador en primera persona", "Distractor: Ambiente"]}', '{"emparejamientos_correctos": {"Conflicto principal": "Problema central que mueve la trama", "Narrador omnisciente": "Narrador que conoce todos los pensamientos y sentimientos", "Motivación del personaje": "Razón que impulsa las acciones de un personaje", "Relación entre personajes": "Historia de rivalidad entre dos personajes", "Narrador en primera persona": "Narrador que cuenta la historia desde su propia perspectiva"}}', 2, 0, true, '{"comprensión lectora","análisis de textos",narrador,personajes}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (151, 37, 'multiple_choice', 'all', '{"opciones": {"A": "Un objeto que representa algo más allá de su significado literal", "B": "Una palabra que se repite varias veces sin cambiar su significado", "C": "Una estructura gramatical compleja usada para embellecer el texto", "D": "Un verso que siempre termina con rima consonante"}, "pregunta": "¿Cuál de los siguientes elementos es un símbolo comúnmente utilizado en la poesía para representar ideas o sentimientos?", "explicacion": "La respuesta correcta es A, porque un símbolo en poesía es un objeto, imagen o elemento que representa ideas o sentimientos más profundos más allá de su significado literal. La opción B describe una repetición, que es otro recurso pero no un símbolo. La opción C habla de una estructura gramatical, que no necesariamente es un símbolo. La opción D describe un aspecto formal (rima consonante) pero no es un símbolo."}', '{"respuesta_correcta": "A"}', 1, 0, true, '{símbolos,poesía,"lenguaje figurado"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (131, 32, 'sequencing', 'all', '{"explicacion": "El orden comienza con la presentación del conflicto por parte del narrador, seguido por la manifestación de motivaciones en los personajes a través del diálogo. Luego, se muestra la influencia del narrador en la percepción de los personajes, lo que lleva a sus interacciones y cambios en la trama. Finalmente, el narrador concluye enfatizando la relación entre ellos, completando así la comprensión del relato.", "instruccion": "Ordena los siguientes eventos para entender la relación entre los personajes y la influencia del narrador en el relato.", "elementos_desordenados": ["El narrador presenta el conflicto principal entre los personajes.", "Los personajes muestran sus motivaciones a través del diálogo.", "Se revela cómo la perspectiva del narrador afecta la percepción de los personajes.", "Los personajes interactúan generando cambios en la trama.", "El narrador concluye el relato enfatizando la relación entre los personajes."]}', '{"orden_correcto": ["El narrador presenta el conflicto principal entre los personajes.", "Los personajes muestran sus motivaciones a través del diálogo.", "Se revela cómo la perspectiva del narrador afecta la percepción de los personajes.", "Los personajes interactúan generando cambios en la trama.", "El narrador concluye el relato enfatizando la relación entre los personajes."]}', 3, 0, true, '{comprensión,"relación de personajes",narrador}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (132, 33, 'multiple_choice', 'all', '{"opciones": {"A": "El dilema moral solo sirve para mostrar la personalidad del protagonista, sin afectar el tema principal.", "B": "El dilema moral representa el conflicto central de la historia y refleja la lucha interna que impulsa el desarrollo de la trama.", "C": "El dilema moral es un detalle menor que no influye en la resolución del conflicto principal.", "D": "El dilema moral se presenta para distraer al lector de la verdadera acción de la historia."}, "pregunta": "En un fragmento donde el protagonista enfrenta un dilema moral, ¿cómo se relaciona esta situación con el sentido global de la narración?", "explicacion": "La opción B es correcta porque el dilema moral del protagonista está directamente relacionado con el conflicto principal y contribuye al sentido global de la narración, mostrando cómo sus decisiones afectan el desarrollo y desenlace de la historia. La opción A es incorrecta porque reduce el dilema a una simple caracterización, ignorando su impacto en la trama. La opción C minimiza la importancia del dilema, lo cual no corresponde cuando este es central para la historia. La opción D interpreta erróneamente el propósito del dilema, atribuyéndole una función de distracción que no tiene."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"análisis de texto","conflicto narrativo","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (133, 33, 'multiple_choice', 'all', '{"opciones": {"A": "El dilema solo sirve para extender la trama, sin influir en el mensaje principal.", "B": "El dilema refleja el conflicto central y permite entender la evolución del personaje y el mensaje de la historia.", "C": "El dilema es una distracción que desvía la atención del lector del tema principal.", "D": "El dilema muestra un evento aislado que no tiene conexión con otros personajes ni con el desarrollo del relato."}, "pregunta": "En un fragmento donde el protagonista enfrenta un dilema moral, ¿cómo se relaciona esta situación con el sentido global de la narración?", "explicacion": "La opción B es correcta porque el dilema moral del protagonista representa el conflicto central y es clave para comprender cómo evoluciona el personaje, lo que a su vez ilumina el sentido global de la obra. La opción A es incorrecta porque minimiza la importancia del dilema en la trama y el mensaje. La opción C es un error común que subestima la función de los conflictos en la narrativa. La opción D desconecta el dilema del contexto y las relaciones entre personajes, lo cual dificulta una interpretación global adecuada."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"análisis narrativo",conflicto,personajes}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (134, 33, 'drag_drop_matching', 'all', '{"explicacion": "Cada emparejamiento relaciona un concepto narrativo con su definición o ejemplo: El conflicto interno es la lucha dentro del protagonista; el conflicto externo es la pelea entre personajes; el personaje principal es el protagonista central; el personaje secundario apoya la trama sin protagonismo; el narrador en primera persona relata desde su propia experiencia; el narrador omnisciente conoce todo sobre los personajes.", "instruccion": "Empareja cada concepto narrativo con su ejemplo o definición correspondiente", "columna_derecha": ["El protagonista lucha consigo mismo por tomar una decisión difícil.", "Un personaje que aparece en pocas escenas y apoya al protagonista.", "El narrador cuenta la historia desde su propia perspectiva usando ''yo''.", "El enfrentamiento entre dos personajes rivales en la historia.", "El narrador conoce todos los pensamientos y sentimientos de los personajes.", "El personaje que tiene el papel central en la historia."], "columna_izquierda": ["Conflicto interno", "Conflicto externo", "Personaje principal", "Personaje secundario", "Narrador en primera persona", "Narrador omnisciente"]}', '{"emparejamientos_correctos": {"Conflicto externo": "El enfrentamiento entre dos personajes rivales en la historia.", "Conflicto interno": "El protagonista lucha consigo mismo por tomar una decisión difícil.", "Personaje principal": "El personaje que tiene el papel central en la historia.", "Narrador omnisciente": "El narrador conoce todos los pensamientos y sentimientos de los personajes.", "Personaje secundario": "Un personaje que aparece en pocas escenas y apoya al protagonista.", "Narrador en primera persona": "El narrador cuenta la historia desde su propia perspectiva usando ''yo''."}}', 3, 0, true, '{narrativa,"análisis de personajes",conflictos,"primeros medios","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (135, 33, 'sequencing', 'all', '{"explicacion": "Primero se describe el ambiente donde ocurre la narración para situar al lector. Luego, se presenta el conflicto principal que impulsa la trama. Después, el protagonista descubre un secreto importante relacionado con ese conflicto. A continuación, los personajes discuten sobre las consecuencias de ese secreto, lo que genera tensión. Finalmente, el protagonista toma una decisión que cambia el curso de la historia, resolviendo o transformando el conflicto.", "instruccion": "Ordena los siguientes eventos según la secuencia correcta en el fragmento de la narración", "elementos_desordenados": ["El protagonista descubre un secreto importante", "El conflicto principal se presenta en la historia", "Los personajes discuten sobre las consecuencias del secreto", "El protagonista toma una decisión que cambia el curso de la historia", "Se describe el ambiente donde ocurre la narración"]}', '{"orden_correcto": ["Se describe el ambiente donde ocurre la narración", "El conflicto principal se presenta en la historia", "El protagonista descubre un secreto importante", "Los personajes discuten sobre las consecuencias del secreto", "El protagonista toma una decisión que cambia el curso de la historia"]}', 3, 0, true, '{secuencia,"análisis de narración",conflicto,personajes}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (136, 33, 'open_ended', 'all', '{"pregunta": "Lee el siguiente fragmento de la obra asignada y explica cómo se relaciona este con el sentido global del texto. En tu respuesta, analiza el conflicto que se presenta en el fragmento y cómo influye en el desarrollo de los personajes.", "explicacion": "El docente debe evaluar que el estudiante no solo identifique elementos del fragmento, sino que también los relacione con la obra completa, demostrando comprensión profunda y capacidad analítica acorde al nivel de Bloom ''Aplicar''.", "contexto_adicional": "Se recomienda que el fragmento seleccionado sea representativo y permita identificar claramente un conflicto y la interacción entre personajes.", "criterios_evaluacion": ["Identificación y explicación clara del conflicto presente en el fragmento (peso: 30%)", "Análisis de la relación entre los personajes involucrados en el fragmento y cómo esta afecta la historia global (peso: 40%)", "Capacidad para conectar el fragmento con el sentido global del texto, mostrando comprensión del texto completo (peso: 30%)"]}', '{"puntos_clave": ["Identificación del conflicto presente en el fragmento", "Relación entre personajes y su impacto en la trama", "Conexión del fragmento con el sentido global del texto"], "respuesta_modelo": "En el fragmento, se muestra un conflicto central entre los personajes principales, donde surge una tensión que refleja un problema mayor en la trama. Por ejemplo, la disputa entre el protagonista y el antagonista revela sus motivaciones y fortalezas, lo que permite entender mejor sus acciones posteriores. Este conflicto impulsa el desarrollo de la historia y muestra cómo las relaciones entre personajes influyen en la resolución del problema global del texto, reforzando así el tema principal de la obra.", "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Análisis de texto",Narración,"Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (137, 34, 'compare_contrast', 'all', '{"conceptos": ["Personajes tipo", "Símbolos literarios"], "criterios": ["Función en la narración", "Relación con otros elementos del texto", "Representación de ideas o temas"], "explicacion": "Los personajes tipo y los símbolos literarios cumplen funciones importantes dentro de la narración, pero difieren en su naturaleza y en cómo representan ideas. Ambos pueden estar relacionados con otros elementos del texto, pero mientras los personajes tipo son figuras humanas o antropomórficas que representan características universales, los símbolos son objetos, elementos o acciones que representan conceptos abstractos.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Personajes tipo": {"Función en la narración": "Representar arquetipos que ayudan a avanzar la trama y reflejar características humanas universales", "Representación de ideas o temas": "Encarnan temas o valores específicos a través de su comportamiento y rol", "Relación con otros elementos del texto": "Interactúan con otros personajes y el conflicto para desarrollar la historia"}, "Símbolos literarios": {"Función en la narración": "Representar ideas, emociones o temas abstractos mediante objetos, acciones o elementos concretos", "Representación de ideas o temas": "Materializan conceptos abstractos presentes en la narración", "Relación con otros elementos del texto": "Se vinculan con el ambiente, personajes o eventos para reforzar el significado"}}}', 3, 0, true, '{"análisis textual",comparación,literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (138, 34, 'compare_contrast', 'all', '{"conceptos": ["Personajes tipo", "Símbolos literarios", "Tópicos literarios"], "criterios": ["Función en la narración", "Relación con el lector", "Manifestación en el texto"], "explicacion": "Los personajes tipo, símbolos y tópicos literarios cumplen funciones distintas en la narración. Los personajes tipo representan arquetipos humanos y son esenciales para el desarrollo de la trama, mientras que los símbolos transmiten significados profundos a través de objetos o elementos recurrentes, y los tópicos literarios son temas o motivos recurrentes que reflejan ideas universales. En cuanto a la relación con el lector, los personajes tipo generan identificación y empatía, los símbolos invitan a la interpretación y reflexión, y los tópicos establecen conexiones culturales o emocionales. Finalmente, en la manifestación en el texto, los personajes tipo se presentan a través de la caracterización directa o indirecta, los símbolos aparecen como elementos concretos con significados simbólicos, y los tópicos se evidencian en el tema general o en situaciones recurrentes dentro de la narración.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Personajes tipo": {"Relación con el lector": "Generan identificación y empatía", "Función en la narración": "Representan arquetipos que impulsan la trama", "Manifestación en el texto": "Caracterización directa o indirecta de personajes"}, "Tópicos literarios": {"Relación con el lector": "Establecen conexiones culturales o emocionales", "Función en la narración": "Reflejan temas o motivos universales recurrentes", "Manifestación en el texto": "Temas generales o situaciones recurrentes en la historia"}, "Símbolos literarios": {"Relación con el lector": "Invitan a la reflexión y análisis", "Función en la narración": "Transmiten significados profundos y múltiples interpretaciones", "Manifestación en el texto": "Elementos concretos con significado simbólico"}}}', 4, 0, true, '{"análisis textual","comparar y contrastar",narrativa}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (139, 34, 'open_ended', 'all', '{"pregunta": "Analiza dos personajes principales de la narración leída e identifica cómo representan personajes tipo, símbolos o tópicos literarios. Explica cómo estas características influyen en el desarrollo del conflicto y en la construcción del mensaje del texto.", "explicacion": "El docente debe evaluar que el estudiante no solo identifique características literarias, sino que también establezca relaciones entre personajes, conflictos y temas, demostrando pensamiento crítico y comprensión textual.", "contexto_adicional": "Se recomienda que el estudiante utilice ejemplos concretos del texto para sustentar su análisis.", "criterios_evaluacion": ["Identificación clara y precisa de los personajes tipo, símbolos o tópicos literarios presentes en los personajes seleccionados (peso: 30%)", "Análisis profundo de la relación entre estos personajes y el conflicto central de la narración (peso: 40%)", "Capacidad para explicar cómo estas características contribuyen a la construcción del mensaje o tema del texto (peso: 30%)"]}', '{"puntos_clave": ["Identificación de Juan como personaje tipo (héroe solitario) y María como símbolo de esperanza", "Relación entre los personajes y cómo su interacción sostiene el conflicto central", "Explicación de cómo estas características literarias contribuyen al mensaje del texto (resiliencia y solidaridad)"], "respuesta_modelo": "En la narración, el personaje de Juan representa el arquetipo del héroe solitario, simbolizando la lucha individual contra la adversidad. Por otro lado, María encarna el símbolo de la esperanza y el sacrificio, reflejando el tópico literario del amor redentor. La interacción entre Juan y María es fundamental para el desarrollo del conflicto, ya que sus acciones y decisiones impulsan la tensión narrativa y evidencian la dualidad entre lucha personal y apoyo emocional. Esta dinámica permite que el texto explore temas como la resiliencia y la solidaridad, reforzando el mensaje de que el crecimiento personal se alcanza tanto a través del esfuerzo individual como del vínculo con otros.", "requiere_revision_humana": true}', 4, 0, true, '{análisis_textual,personajes,símbolos,"tópicos literarios",conflicto}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (140, 34, 'concept_map', 'all', '{"conceptos": ["Personajes tipo", "Símbolos literarios", "Tópicos literarios", "Relación entre personajes", "Conflicto narrativo"], "explicacion": "Se espera que el estudiante organice y conecte cómo los personajes tipo pueden representar o interactuar con símbolos y tópicos literarios, cómo estos elementos contribuyen al desarrollo del conflicto narrativo, y cómo la relación entre personajes influye en la trama y en la manifestación de símbolos y tópicos.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de una narración literaria?"}', '{"conceptos_centrales": ["Personajes tipo", "Conflicto narrativo"], "relaciones_esperadas": [{"origen": "Personajes tipo", "destino": "Tópicos literarios", "relacion": "representan"}, {"origen": "Símbolos literarios", "destino": "Conflicto narrativo", "relacion": "refuerzan"}, {"origen": "Relación entre personajes", "destino": "Conflicto narrativo", "relacion": "genera"}, {"origen": "Personajes tipo", "destino": "Relación entre personajes", "relacion": "se vinculan con"}, {"origen": "Tópicos literarios", "destino": "Símbolos literarios", "relacion": "se expresan mediante"}], "requiere_revision_humana": true}', 3, 0, true, '{"análisis textual","mapa conceptual",literatura,narración,"primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (141, 34, 'concept_map', 'all', '{"conceptos": ["Personajes tipo", "Símbolos", "Tópicos literarios", "Conflicto", "Relación entre personajes"], "explicacion": "Se espera que el estudiante muestre cómo los personajes tipo representan o se vinculan con ciertos símbolos y tópicos literarios, cómo estos elementos influyen o reflejan el conflicto central de la narración, y cómo las relaciones entre personajes enriquecen la comprensión del texto.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis de una narración literaria?"}', '{"conceptos_centrales": ["Personajes tipo", "Tópicos literarios"], "relaciones_esperadas": [{"origen": "Personajes tipo", "destino": "Tópicos literarios", "relacion": "representan"}, {"origen": "Símbolos", "destino": "Tópicos literarios", "relacion": "refuerzan"}, {"origen": "Personajes tipo", "destino": "Símbolos", "relacion": "se vinculan con"}, {"origen": "Conflicto", "destino": "Relación entre personajes", "relacion": "es influenciado por"}, {"origen": "Relación entre personajes", "destino": "Conflicto", "relacion": "define"}], "requiere_revision_humana": true}', 4, 0, true, '{"análisis textual","mapa conceptual",literatura,narración,"primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (142, 35, 'criteria_evaluation', 'all', '{"rubrica": {"Justificación y argumentación": {"Excelente": "Presenta una justificación sólida y coherente para la evaluación, utilizando ejemplos específicos del texto y razonamientos bien estructurados.", "Satisfactorio": "Ofrece una justificación clara pero con ejemplos o argumentos limitados o poco desarrollados.", "Necesita mejorar": "La justificación es vaga, poco clara o carece de ejemplos concretos."}, "Análisis de la influencia de creencias y prejuicios": {"Excelente": "Identifica con precisión y profundidad las creencias, prejuicios y estereotipos presentes en la narración, explicando detalladamente cómo afectan el desarrollo de la historia y los personajes.", "Satisfactorio": "Reconoce algunas creencias y prejuicios en la narración, y explica de manera clara su impacto en la historia y personajes, aunque con menor profundidad.", "Necesita mejorar": "Muestra dificultad para identificar creencias o prejuicios en la narración o su influencia en la historia y personajes es vaga o incorrecta."}, "Conexión con el contexto histórico y relevancia actual": {"Excelente": "Establece vínculos claros y fundamentados entre el contexto histórico de la narración y su relevancia en la sociedad contemporánea, demostrando pensamiento crítico.", "Satisfactorio": "Hace conexiones básicas entre el contexto histórico y la actualidad, aunque con argumentos limitados o poco desarrollados.", "Necesita mejorar": "No logra relacionar adecuadamente el contexto histórico con la relevancia actual o lo hace de forma superficial."}}, "explicacion": "Se espera que el estudiante muestre una comprensión crítica del texto, identificando cómo las creencias y prejuicios afectan la trama y los personajes, conectando estas ideas con el contexto histórico y su impacto en la actualidad, y justificando su evaluación con argumentos sólidos.", "instruccion": "Evalúa la influencia de creencias, prejuicios y estereotipos en la narración considerando el contexto histórico y su relevancia actual, utilizando la rúbrica proporcionada. Justifica tu calificación con ejemplos del texto y argumentos claros.", "caso_a_evaluar": "Lee el fragmento de la novela histórica ''La casa de los espíritus'' de Isabel Allende, enfocándote en cómo se presentan los personajes principales y los conflictos que enfrentan en relación con las creencias y prejuicios sociales de la época. Luego, evalúa cómo estas creencias y estereotipos influyen en la narración y discute su relevancia en la sociedad actual."}', '{"evaluacion_modelo": {"Justificación y argumentación": "Excelente", "Análisis de la influencia de creencias y prejuicios": "Excelente", "Conexión con el contexto histórico y relevancia actual": "Satisfactorio"}, "justificacion_modelo": "El estudiante identifica claramente los prejuicios y estereotipos en la narración, explicando su impacto en la historia y personajes con profundidad (Excelente). Sin embargo, las conexiones con el contexto histórico y la relevancia actual son básicas y podrían estar mejor desarrolladas (Satisfactorio). La justificación está bien fundamentada con ejemplos específicos y argumentos claros (Excelente).", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Narración,"Análisis crítico","Prejuicios y estereotipos","Contexto histórico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (143, 35, 'criteria_evaluation', 'all', '{"rubrica": {"Relevancia actual": {"Excelente": "Argumenta con claridad y ejemplos cómo las creencias y prejuicios reflejados en la narración siguen siendo relevantes o se manifiestan en la sociedad actual.", "Satisfactorio": "Menciona la relevancia actual de las creencias y prejuicios, pero con argumentos poco elaborados o ejemplos limitados.", "Necesita mejorar": "No logra establecer una conexión entre la narración y su relevancia en el contexto contemporáneo."}, "Relación con el contexto histórico": {"Excelente": "Explica con precisión el contexto histórico que da origen a las creencias y prejuicios presentes, mostrando comprensión profunda de su influencia en la narración.", "Satisfactorio": "Describe el contexto histórico de forma general, relacionándolo de manera superficial con las creencias y prejuicios del texto.", "Necesita mejorar": "No establece una relación clara entre el contexto histórico y los prejuicios presentes en la narración."}, "Análisis de la influencia de creencias y prejuicios": {"Excelente": "Identifica de manera clara y profunda cómo las creencias y prejuicios afectan directamente la trama y el desarrollo de los personajes, con ejemplos precisos del texto.", "Satisfactorio": "Reconoce la presencia de creencias y prejuicios en la narración y su impacto general en la historia, pero con ejemplos o explicaciones poco detalladas.", "Necesita mejorar": "No logra identificar o explicar adecuadamente la influencia de creencias y prejuicios en la narración."}}, "explicacion": "Se espera que el estudiante identifique y explique cómo los prejuicios y creencias condicionan la trama y los personajes, contextualice históricamente estos elementos y valore su relevancia en la actualidad, fundamentando su juicio con evidencias del texto y razonamientos claros.", "instruccion": "Evalúa el fragmento de la narración según los criterios de la rúbrica y justifica tu calificación con argumentos que apoyen tu evaluación.", "caso_a_evaluar": "Lee el fragmento de la narración \"La sombra del prejuicio\", donde se presentan personajes que actúan bajo creencias y estereotipos propios de una época histórica determinada. Evalúa cómo estas creencias influyen en el desarrollo del conflicto y en las acciones de los personajes, y reflexiona sobre su relevancia en la sociedad actual."}', '{"evaluacion_modelo": {"Relevancia actual": "Excelente", "Relación con el contexto histórico": "Satisfactorio", "Análisis de la influencia de creencias y prejuicios": "Excelente"}, "justificacion_modelo": "El estudiante identifica claramente cómo los prejuicios moldean el conflicto y los personajes, aporta ejemplos textuales precisos. Además, describe adecuadamente el contexto histórico aunque con menor profundidad. Finalmente, establece con solidez la importancia actual de estos prejuicios, relacionándolos con ejemplos contemporáneos relevantes.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Evaluación crítica","Prejuicios y estereotipos","Contexto histórico","Primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (144, 35, 'open_ended', 'all', '{"pregunta": "Analiza cómo las creencias, prejuicios y estereotipos presentes en la narración influyen en el desarrollo de los personajes y en el conflicto principal. ¿De qué manera estos elementos reflejan el contexto histórico de la obra y qué relevancia tienen para nuestra sociedad actual?", "explicacion": "El docente debe evaluar que el estudiante no sólo identifique los elementos mencionados sino que también los analice en relación con los personajes y el conflicto. Además, debe valorar la capacidad del alumno para establecer conexiones entre el contexto histórico de la obra y su vigencia en el presente, promoviendo una reflexión crítica y contextualizada.", "contexto_adicional": "Se recomienda que los estudiantes basen su análisis en una narración estudiada en clase que incluya elementos evidentes de creencias, prejuicios o estereotipos.", "criterios_evaluacion": ["Identificación clara y precisa de las creencias, prejuicios y estereotipos presentes en la narración (peso: 30%)", "Análisis profundo del impacto de estos elementos en los personajes y en el conflicto principal de la historia (peso: 40%)", "Reflexión crítica sobre la relación entre el contexto histórico de la obra y su relevancia en la sociedad actual (peso: 30%)"]}', '{"puntos_clave": ["Identificación de creencias, prejuicios o estereotipos en la narración", "Relación de estos elementos con el desarrollo de personajes y conflicto", "Conexión entre el contexto histórico de la obra y su relevancia actual"], "respuesta_modelo": "En la narración, las creencias y prejuicios se manifiestan principalmente a través del personaje principal, quien es marginado debido a estereotipos sociales propios del contexto histórico en que se sitúa la historia, como la discriminación por clase o género. Estos prejuicios generan un conflicto central, ya que limitan las acciones del protagonista y condicionan sus relaciones con otros personajes. Además, estas representaciones reflejan una época marcada por normas sociales rígidas y exclusión, lo que invita a reflexionar sobre cómo aún hoy persisten ciertos estereotipos que influyen en nuestras actitudes y relaciones sociales. De esta forma, la obra no solo describe un momento histórico sino que también desafía al lector a cuestionar prejuicios actuales y a promover una sociedad más inclusiva.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Análisis crítico","Contexto histórico","Prejuicios y estereotipos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (145, 35, 'open_ended', 'all', '{"pregunta": "Analiza cómo las creencias, prejuicios y estereotipos presentes en la narración afectan el desarrollo del conflicto y las relaciones entre personajes. Considera el contexto histórico en el que se sitúa la historia y reflexiona sobre su relevancia en la sociedad actual.", "explicacion": "El docente debe evaluar que el estudiante no solo identifique los elementos de creencias, prejuicios y estereotipos, sino que también analice su impacto en la trama y los personajes, además de ofrecer una reflexión crítica sobre cómo estas temáticas siguen siendo relevantes hoy en día.", "contexto_adicional": "La pregunta se basa en una narración leída en clase que presenta conflictos sociales y personales en un contexto histórico específico. Se espera que el estudiante relacione la obra con su entorno y el presente.", "criterios_evaluacion": ["Identificación clara y precisa de creencias, prejuicios y estereotipos en la narración (peso: 30%)", "Análisis profundo de cómo estos elementos influyen en el conflicto y en las relaciones entre personajes, considerando el contexto histórico (peso: 40%)", "Reflexión crítica sobre la relevancia y vigencia de estas influencias en la sociedad actual (peso: 30%)"]}', '{"puntos_clave": ["Identificación de creencias, prejuicios y estereotipos en la narración", "Relación entre estos elementos y el conflicto/relaciones entre personajes", "Contextualización histórica y reflexión sobre su vigencia actual"], "respuesta_modelo": "En la narración, las creencias y prejuicios relacionados con la clase social y el género influyen directamente en el conflicto principal, ya que los personajes principales enfrentan discriminación y expectativas sociales que dificultan su relación. Por ejemplo, el personaje A es marginado debido a estereotipos que lo etiquetan como inferior, lo que genera tensiones con otros personajes. El contexto histórico, marcado por una sociedad rígida y jerárquica, refuerza estas creencias, limitando las opciones de los personajes y profundizando el conflicto. Actualmente, estas temáticas mantienen su relevancia, ya que en nuestra sociedad aún persisten prejuicios y estereotipos que afectan las relaciones sociales y generan conflictos similares, lo que invita a reflexionar sobre la importancia de cuestionar y superar estas barreras culturales.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Análisis narrativo","Pensamiento crítico","Contexto histórico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (152, 37, 'multiple_choice', 'all', '{"opciones": {"A": "La rima que aparece al final de los versos", "B": "Una rosa que representa el amor", "C": "La repetición de una palabra para dar ritmo", "D": "El número de sílabas en cada verso"}, "pregunta": "En un poema, ¿cuál de los siguientes elementos corresponde a un símbolo?", "explicacion": "La opción B es correcta porque un símbolo es un elemento que representa o sugiere una idea o concepto más allá de su significado literal, como una rosa que simboliza el amor. La opción A se refiere a un aspecto formal (la rima), no a un símbolo. La opción C describe una técnica de repetición, que no es un símbolo sino un recurso estilístico. La opción D habla de la métrica, que es un aspecto formal del poema, pero no un símbolo."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"Lengua y Literatura",poesía,símbolos,"primeros medios","elementos poéticos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (602, 128, 'criteria_evaluation', 'all', '{"rubrica": {"Efectividad persuasiva": {"Excelente": "Evalúa de manera clara cómo los argumentos impactan en la audiencia y si logran persuadir, con ejemplos concretos del discurso.", "Satisfactorio": "Realiza una evaluación general sobre la persuasión de los argumentos, aunque con poca profundidad o ejemplos limitados.", "Necesita mejorar": "No logra evaluar la efectividad persuasiva o su evaluación es confusa y sin ejemplos."}, "Validez de los argumentos": {"Excelente": "Identifica correctamente si los argumentos están basados en evidencias sólidas y relevantes, y explica por qué son válidos o no.", "Satisfactorio": "Reconoce algunos argumentos válidos pero no todos, y brinda una explicación básica sobre su validez.", "Necesita mejorar": "No logra identificar adecuadamente la validez de los argumentos o presenta explicaciones poco claras o incorrectas."}, "Justificación de la evaluación": {"Excelente": "Ofrece una justificación bien argumentada, con referencias específicas al discurso y a los criterios de validez y efectividad.", "Satisfactorio": "Da una justificación clara pero general, con pocas referencias específicas al discurso o criterios.", "Necesita mejorar": "No justifica adecuadamente su evaluación o la justificación es vaga y sin sustento."}}, "explicacion": "Se espera que el estudiante identifique si los argumentos presentados están fundamentados en evidencia confiable, evalúe cómo estos argumentos influyen en la audiencia y entregue una justificación coherente y basada en el contenido del discurso para apoyar su evaluación.", "instruccion": "Evalúa el discurso argumentativo según los criterios de validez de los argumentos, efectividad persuasiva y justificación de tu evaluación, utilizando la rúbrica proporcionada. Justifica tu calificación con ejemplos específicos del discurso.", "caso_a_evaluar": "Has escuchado un discurso argumentativo sobre la importancia de implementar tecnologías digitales en la educación secundaria. El emisor presenta varios argumentos para convencer a la audiencia, incluyendo datos estadísticos, ejemplos concretos y opiniones de expertos."}', '{"evaluacion_modelo": {"Efectividad persuasiva": "Excelente", "Validez de los argumentos": "Satisfactorio", "Justificación de la evaluación": "Satisfactorio"}, "justificacion_modelo": "El estudiante reconoce que algunos argumentos están basados en evidencias válidas, pero no todos, por lo que la validez es satisfactoria. Sin embargo, evalúa claramente cómo los argumentos lograron persuadir a la audiencia, con ejemplos precisos, por lo que la efectividad persuasiva es excelente. La justificación es clara pero podría incluir más referencias específicas, por lo que es satisfactoria.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Argumentación,Evaluación,Discurso,"Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (146, 36, 'open_ended', 'all', '{"pregunta": "Después de leer la narración, diseña una interpretación personal que integre un análisis profundo de los personajes principales, los conflictos que enfrentan, los símbolos presentes en la historia y el contexto histórico en el que se desarrolla. ¿Cómo crees que estos elementos se relacionan y contribuyen al mensaje general de la obra?", "explicacion": "El docente debe evaluar que el estudiante no solo describa los elementos sino que realice conexiones críticas entre ellos, demostrando comprensión profunda y capacidad creativa para integrar todos los aspectos en una interpretación original y fundamentada.", "contexto_adicional": "Se recomienda que el estudiante apoye su interpretación con ejemplos específicos de la narración y considere el periodo histórico en el que se sitúa la historia.", "criterios_evaluacion": ["Análisis de personajes: profundidad, relación entre ellos y coherencia con la narración (peso: 30%)", "Identificación y explicación de los conflictos y símbolos, y su vínculo con el contexto histórico (peso: 40%)", "Coherencia y creatividad en la interpretación personal, con argumentos fundamentados (peso: 30%)"]}', '{"puntos_clave": ["Relación entre personajes y sus conflictos", "Identificación de símbolos relevantes y su significado", "Contextualización histórica y su influencia en la interpretación"], "respuesta_modelo": "En la narración, los personajes principales muestran relaciones complejas que reflejan tensiones sociales propias del contexto histórico de la obra, como la lucha de clases en el Chile de mediados del siglo XX. El conflicto central surge de las diferencias ideológicas entre ellos, que se manifiestan en diálogos y acciones cargadas de simbolismo, como el uso recurrente de la casa en ruinas que representa la decadencia de un antiguo sistema social. Mi interpretación personal es que la obra critica las injusticias sociales y propone una reflexión sobre la necesidad de cambio y entendimiento entre generaciones, lo que se refuerza con el contexto histórico de transformaciones políticas y sociales presentes en la narración.", "requiere_revision_humana": true}', 4, 0, true, '{lengua_y_literatura,narracion,analisis_critico,creacion}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (147, 36, 'open_ended', 'all', '{"pregunta": "Después de leer la narración, diseña una interpretación personal que integre un análisis profundo de los personajes principales, los conflictos que enfrentan, los símbolos presentes en la historia y cómo el contexto histórico influye en el desarrollo de la trama y en las decisiones de los personajes. Explica cómo estos elementos se relacionan entre sí para enriquecer el mensaje o la enseñanza de la narración.", "explicacion": "El docente debe evaluar que el estudiante demuestre comprensión profunda de los personajes y conflictos, identificando símbolos relevantes y relacionándolos con el contexto histórico. La interpretación debe ser original, justificando cómo estos elementos se integran para aportar un significado enriquecido a la narración.", "contexto_adicional": "Los estudiantes pueden basarse en cualquier narración leída en clase que incluya personajes complejos, conflictos significativos, símbolos y un contexto histórico definido.", "criterios_evaluacion": ["Análisis detallado y coherente de los personajes y sus relaciones, demostrando comprensión de sus motivaciones y evolución (peso: 30%)", "Identificación y explicación clara de los conflictos y símbolos en la narración, vinculándolos adecuadamente con el contexto histórico (peso: 40%)", "Capacidad para integrar los elementos analizados en una interpretación personal original y bien fundamentada, que aporte una visión crítica sobre la obra (peso: 30%)"]}', '{"puntos_clave": ["Análisis de la evolución y motivaciones de los personajes", "Identificación de conflictos internos y externos", "Explicación de los símbolos y su relación con el contexto histórico"], "respuesta_modelo": "En la narración, los personajes principales muestran una evolución marcada por sus enfrentamientos internos y externos, especialmente el protagonista, cuyo conflicto con la sociedad refleja tensiones históricas de la época. Los símbolos, como la casa abandonada y el reloj detenido, representan la ruptura temporal y la pérdida de identidad, vinculándose con el contexto de inestabilidad política descrito en el relato. Esta integración revela que la obra no solo narra un conflicto personal, sino que también critica las consecuencias del entorno histórico en la vida individual, lo que enriquece el mensaje sobre la resistencia y la memoria.", "requiere_revision_humana": true}', 5, 0, true, '{"lengua y literatura","análisis narrativo","pensamiento crítico","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (148, 36, 'concept_map', 'all', '{"conceptos": ["Personajes", "Conflictos", "Símbolos", "Contexto histórico", "Narración"], "explicacion": "El mapa debe mostrar cómo los personajes están involucrados en los conflictos, cómo los símbolos representan ideas o temas relacionados con el conflicto y el contexto histórico, y cómo todos estos elementos conforman la narración para construir una interpretación personal del texto.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el análisis e interpretación de una narración literaria?"}', '{"conceptos_centrales": ["Narración", "Conflictos"], "relaciones_esperadas": [{"origen": "Personajes", "destino": "Conflictos", "relacion": "participan en"}, {"origen": "Símbolos", "destino": "Conflictos", "relacion": "representan"}, {"origen": "Contexto histórico", "destino": "Conflictos", "relacion": "influye en"}, {"origen": "Contexto histórico", "destino": "Símbolos", "relacion": "da sentido a"}, {"origen": "Conflictos", "destino": "Narración", "relacion": "desarrollan la"}, {"origen": "Personajes", "destino": "Narración", "relacion": "construyen la"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",Narración,"Análisis literario","Mapa conceptual","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (149, 36, 'concept_map', 'all', '{"conceptos": ["Conflicto", "Personajes", "Símbolos", "Contexto histórico", "Relaciones entre personajes", "Narración", "Interpretación personal"], "explicacion": "El mapa conceptual debe mostrar cómo el conflicto impulsa la narración, cómo los personajes y sus relaciones influyen en el desarrollo del conflicto, cómo los símbolos aportan significado y cómo el contexto histórico enmarca la historia. Finalmente, se espera que el estudiante integre todos estos elementos para construir una interpretación personal coherente de la narración.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el análisis e interpretación de una narración literaria?"}', '{"conceptos_centrales": ["Conflicto", "Interpretación personal"], "relaciones_esperadas": [{"origen": "Conflicto", "destino": "Narración", "relacion": "impulsa"}, {"origen": "Personajes", "destino": "Conflicto", "relacion": "participan en"}, {"origen": "Relaciones entre personajes", "destino": "Personajes", "relacion": "definen"}, {"origen": "Símbolos", "destino": "Narración", "relacion": "aportan significado a"}, {"origen": "Contexto histórico", "destino": "Narración", "relacion": "condiciona"}, {"origen": "Interpretación personal", "destino": "Conflicto", "relacion": "integra"}, {"origen": "Interpretación personal", "destino": "Personajes", "relacion": "integra"}, {"origen": "Interpretación personal", "destino": "Símbolos", "relacion": "integra"}, {"origen": "Interpretación personal", "destino": "Contexto histórico", "relacion": "considera"}], "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Análisis literario",Narración,"Mapa conceptual","Primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (150, 36, 'open_ended', 'all', '{"pregunta": "Después de leer la narración asignada, diseña una interpretación personal que integre un análisis profundo de los personajes principales, los conflictos que enfrentan, los símbolos presentes en la historia y el contexto histórico en el que se desarrolla. ¿Cómo estos elementos se relacionan para construir el significado global de la narración?", "explicacion": "El docente debe verificar que el estudiante no solo repita información, sino que realice una síntesis que integre los elementos solicitados para construir una interpretación original y coherente. Se deben valorar argumentos fundamentados en el texto y en el contexto histórico correspondiente.", "contexto_adicional": "La narración puede ser del corpus literario asignado para Primero Medio, preferentemente una obra que contemple un contexto histórico relevante y personajes complejos.", "criterios_evaluacion": ["Análisis de personajes: Se evalúa la capacidad para identificar y explicar las características, motivaciones y relaciones entre personajes (peso: 30%)", "Interpretación de conflictos y símbolos: Se valora la comprensión y explicación de los conflictos principales y los símbolos relevantes, y cómo estos aportan al significado de la narración (peso: 40%)", "Integración del contexto histórico: Se considera la habilidad para relacionar la narración con su contexto histórico y cómo este influye en la trama y los personajes (peso: 30%)"]}', '{"puntos_clave": ["Descripción clara y profunda de los personajes y sus relaciones", "Explicación de los conflictos principales y los símbolos presentes", "Relación entre el contexto histórico y la trama/personajes"], "respuesta_modelo": "En la narración, los personajes principales muestran conflictos internos y externos que reflejan tensiones sociales propias del contexto histórico de la obra, como la lucha de clases y la búsqueda de identidad. Por ejemplo, el protagonista enfrenta un conflicto moral que se manifiesta en su relación con otros personajes, quienes simbolizan diferentes valores y posturas sociales. Los símbolos recurrentes, como el uso de la oscuridad y la luz, representan la dualidad entre esperanza y desesperación. El contexto histórico, situado en un periodo de cambio social, influye directamente en las decisiones y destinos de los personajes, enriqueciendo así el mensaje global de la narración sobre la resistencia y transformación social.", "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,analisis_narrativo,interpretacion,primer_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (153, 37, 'multiple_choice', 'all', '{"opciones": {"A": "La repetición de una palabra para enfatizar una idea", "B": "Una rosa que representa amor o belleza", "C": "El uso de rimas en los versos pares", "D": "La cantidad de sílabas en cada verso"}, "pregunta": "En un poema, ¿cuál de los siguientes elementos corresponde a un símbolo?", "explicacion": "La opción B es correcta porque un símbolo es un objeto, imagen o elemento que representa una idea o concepto más allá de su significado literal, como la rosa que simboliza amor o belleza. La opción A describe repetición, que es un recurso estilístico diferente. La opción C se refiere a la rima, un aspecto formal del poema, y la opción D corresponde a la métrica, también un aspecto formal, pero ninguno de estos últimos es un símbolo."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{poesía,símbolos,elementos_poéticos,lenguaje_figurado}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (154, 37, 'true_false', 'all', '{"afirmacion": "En un poema, los símbolos son elementos que representan ideas o conceptos más allá de su significado literal.", "explicacion": "Esta afirmación es verdadera porque los símbolos en la poesía funcionan como imágenes o elementos que sugieren significados más profundos, permitiendo al lector interpretar el texto de manera más rica y compleja."}', '{"es_verdadero": true}', 1, 0, true, '{símbolos,poesía,"lenguaje figurado"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (155, 37, 'fill_blanks', 'all', '{"texto": "En un poema, los [BLANK_1] pueden representar ideas o sentimientos más allá de su significado literal. Además, el uso de [BLANK_2], como metáforas o comparaciones, ayuda a crear imágenes y emociones en el lector.", "pistas": ["Son elementos que tienen un significado simbólico en el poema", "Tipo de lenguaje que no se interpreta literalmente, sino que busca expresar ideas de forma creativa"], "explicacion": "Los símbolos en un poema son objetos, personajes o elementos que representan ideas o conceptos mayores. El lenguaje figurado, como las metáforas y comparaciones, enriquece el texto poético al generar imágenes y emociones que van más allá del sentido literal de las palabras."}', '{"respuestas_correctas": {"BLANK_1": ["símbolos", "simbolos"], "BLANK_2": ["lenguaje figurado", "figuras literarias"]}}', 2, 0, true, '{poesía,"elementos literarios","lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (156, 38, 'multiple_choice', 'all', '{"opciones": {"A": "La repetición y el símbolo sólo sirven para decorar el poema sin aportar un significado especial.", "B": "La repetición y el símbolo refuerzan la sensación de tristeza y pérdida que el hablante quiere transmitir.", "C": "La repetición indica que el hablante está confundido, y la flor marchita simboliza la esperanza renovada.", "D": "El símbolo de la flor marchita representa alegría, y la repetición enfatiza la felicidad del hablante."}, "pregunta": "En el poema donde se repite la imagen de una \"flor marchita\" para hablar de la tristeza, ¿qué efecto produce esta repetición y el símbolo de la flor marchita en el texto?", "explicacion": "La opción B es correcta porque la repetición de la imagen de la \"flor marchita\" actúa como un símbolo que refuerza el tema central de tristeza y pérdida en el poema. La repetición enfatiza y profundiza el sentimiento expresado por el hablante. La opción A es incorrecta porque minimiza el valor simbólico y funcional de estos recursos literarios. La opción C está errada porque la flor marchita no simboliza esperanza renovada ni la repetición indica confusión en este contexto. La opción D es incorrecta porque asocia un símbolo negativo con una emoción positiva, lo que no corresponde al análisis del poema."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{símbolos,repetición,"lenguaje figurado","análisis poético"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (157, 38, 'multiple_choice', 'all', '{"opciones": {"A": "Representa el ruido fuerte y violento del mar, generando una sensación de caos.", "B": "Refuerza la idea de un ambiente tranquilo y misterioso, invitando a la reflexión.", "C": "Simboliza la rapidez del movimiento del mar, mostrando agitación constante.", "D": "Indica la indiferencia del hablante hacia el mar, restándole importancia al tema."}, "pregunta": "En el poema \"La luna y el mar\", el autor utiliza la repetición de la frase \"olas que susurran\" para crear un efecto específico. ¿Cuál es el significado y efecto principal de esta repetición?", "explicacion": "La opción B es correcta porque la repetición de \"olas que susurran\" enfatiza un ambiente calmo y misterioso, lo que invita a la reflexión del lector sobre el tema tratado. La repetición aquí no genera caos ni agitación (descartando A y C), sino un efecto de calma. Además, la repetición muestra atención y valoración del hablante hacia el mar, por lo que D es incorrecta."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{poesía,"lenguaje figurado",repetición,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (158, 38, 'true_false', 'all', '{"afirmacion": "En un poema, la repetición de palabras o frases puede intensificar el sentimiento que el hablante quiere transmitir.", "explicacion": "Esta afirmación es verdadera porque la repetición es un recurso literario que enfatiza ciertas ideas o emociones, ayudando a que el lector perciba con mayor intensidad el mensaje o la actitud del hablante hacia el tema tratado."}', '{"es_verdadero": true}', 2, 0, true, '{"lenguaje figurado",repetición,"comprensión poética"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (159, 38, 'drag_drop_matching', 'all', '{"explicacion": "El símbolo representa una idea abstracta a través de un objeto o imagen; la metáfora es una comparación implícita; la repetición enfatiza ideas o crea ritmo; la actitud del hablante refleja su postura o emociones hacia el tema; la onomatopeya imita sonidos; la hipérbole exagera para enfatizar.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Uso de palabras que imitan sonidos, como ''tic-tac'' o ''plop''", "Una exageración intencionada para destacar una idea o sentimiento", "Una imagen o elemento que representa algo abstracto o una idea mayor", "El sentimiento o postura del hablante respecto al tema del poema", "Repetir palabras o frases para enfatizar una idea o crear ritmo", "Comparación implícita entre dos cosas sin usar ''como'' o ''parece''"], "columna_izquierda": ["Símbolo", "Metáfora", "Repetición", "Actitud del hablante", "Onomatopeya", "Hipérbole"]}', '{"emparejamientos_correctos": {"Símbolo": "Una imagen o elemento que representa algo abstracto o una idea mayor", "Metáfora": "Comparación implícita entre dos cosas sin usar ''como'' o ''parece''", "Hipérbole": "Una exageración intencionada para destacar una idea o sentimiento", "Onomatopeya": "Uso de palabras que imitan sonidos, como ''tic-tac'' o ''plop''", "Repetición": "Repetir palabras o frases para enfatizar una idea o crear ritmo", "Actitud del hablante": "El sentimiento o postura del hablante respecto al tema del poema"}}', 2, 0, true, '{símbolos,"lenguaje figurado",poesía,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (160, 38, 'sequencing', 'all', '{"explicacion": "El análisis comienza con una lectura cuidadosa que permite comprender el contenido general del poema. Luego se identifican los símbolos y repeticiones presentes, para después reflexionar sobre el significado o efecto que generan en el texto. Posteriormente, se determina la actitud del hablante hacia el tema, lo que ayuda a comprender mejor el contexto emocional. Finalmente, se relacionan todos estos elementos para obtener una interpretación más profunda del poema.", "instruccion": "Ordena los siguientes pasos para analizar los símbolos y el lenguaje figurado en un poema de manera correcta", "elementos_desordenados": ["Identificar los símbolos y repeticiones presentes en el poema", "Reflexionar sobre el significado o efecto que producen esos símbolos y repeticiones", "Leer el poema detenidamente para comprender su contenido general", "Determinar la actitud del hablante hacia el tema abordado en el poema", "Relacionar los símbolos y el lenguaje figurado con la actitud del hablante y el tema general"]}', '{"orden_correcto": ["Leer el poema detenidamente para comprender su contenido general", "Identificar los símbolos y repeticiones presentes en el poema", "Reflexionar sobre el significado o efecto que producen esos símbolos y repeticiones", "Determinar la actitud del hablante hacia el tema abordado en el poema", "Relacionar los símbolos y el lenguaje figurado con la actitud del hablante y el tema general"]}', 3, 0, true, '{"análisis poético","lenguaje figurado",símbolos,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (161, 39, 'multiple_choice', 'all', '{"opciones": {"A": "El hablante muestra indiferencia hacia la soledad, y los símbolos de la luna y la sombra se usan simplemente para describir el paisaje nocturno.", "B": "El hablante expresa melancolía y tristeza, relacionando la luna y la sombra como símbolos que intensifican su sentimiento de aislamiento.", "C": "El hablante tiene una actitud optimista, usando la luna como símbolo de esperanza y la sombra como un contraste pasajero sin importancia.", "D": "El hablante critica la oscuridad de la noche, utilizando la luna y la sombra para representar amenazas externas que causan miedo."}, "pregunta": "En el poema \"La noche oscura\" de un autor chileno contemporáneo, el hablante utiliza símbolos como la luna y la sombra para expresar su sentimiento de soledad. ¿Cuál es la actitud del hablante hacia el tema que aborda y cómo se relaciona esta actitud con los símbolos presentes?", "explicacion": "La opción B es correcta porque el hablante expresa una actitud melancólica y triste hacia la soledad, y los símbolos de la luna y la sombra refuerzan ese sentimiento de aislamiento. La luna, a menudo relacionada con la noche y la introspección, y la sombra, que puede representar la ausencia o la oscuridad, juntos intensifican la experiencia emocional del hablante. La opción A es incorrecta porque los símbolos no son solo descriptivos, sino que tienen un sentido emocional. La opción C es errónea porque no refleja la actitud melancólica del hablante ni el significado simbólico profundo. La opción D es un error conceptual común, pues interpreta los símbolos como amenazas externas cuando en realidad son manifestaciones internas del sentimiento del hablante."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"Lengua y Literatura","Análisis de poemas",Símbolos,"Actitud del hablante","Comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (162, 39, 'multiple_choice', 'all', '{"opciones": {"A": "La actitud de libertad se refleja en el símbolo del mar, que representa un espacio de confinamiento y reglas sociales estrictas.", "B": "La actitud desafiante está vinculada al símbolo del mar, que representa un espacio de libertad y autonomía para el hablante.", "C": "El hablante muestra sumisión, lo cual se relaciona con el símbolo del barco como un lugar de obediencia absoluta.", "D": "La actitud de tristeza se conecta con el símbolo de la noche, que representa la oscuridad y la desesperanza."}, "pregunta": "En el poema \"La canción del pirata\" de José de Espronceda, el hablante expresa una actitud de libertad y desafío. ¿Cómo se relaciona esta actitud con los símbolos presentes en el poema?", "explicacion": "La opción B es correcta porque en \"La canción del pirata\" el mar simboliza la libertad absoluta y la autonomía del hablante, quien desafía las normas sociales. La actitud de desafío se refleja en esta relación simbólica. La opción A es incorrecta porque el mar no representa confinamiento sino libertad. La C es errónea ya que el hablante no muestra sumisión ni el barco es símbolo de obediencia, sino de independencia. La D es un distractor que confunde la actitud real del hablante, que no es de tristeza, con un símbolo que no predomina en el poema."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{poesía,símbolos,"actitud del hablante","análisis literario"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (163, 39, 'drag_drop_matching', 'all', '{"explicacion": "Los símbolos son elementos que representan ideas abstractas; la actitud del hablante es cómo se siente o posiciona ante el tema; el lenguaje figurado incluye recursos como la metáfora para dar profundidad; el tono melancólico es una emoción que puede percibirse en el poema; la metáfora es una figura específica del lenguaje figurado; la rima consonante es un recurso formal pero no se relaciona directamente con la actitud o símbolos en este contexto.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente relacionado con el análisis del poema.", "columna_derecha": ["Elemento que representa una idea o sentimiento más allá de su significado literal", "La postura o sentimiento que el hablante expresa hacia el tema del poema", "Uso de expresiones que no se entienden literalmente para producir un efecto especial", "Sensación de tristeza o nostalgia que transmite el poema", "Comparación implícita entre dos elementos diferentes para enriquecer el significado", "Repetición de sonidos consonánticos al final de los versos"], "columna_izquierda": ["Símbolo", "Actitud del hablante", "Lenguaje figurado", "Tono melancólico", "Metáfora", "Distractor: Rima consonante"]}', '{"emparejamientos_correctos": {"Símbolo": "Elemento que representa una idea o sentimiento más allá de su significado literal", "Metáfora": "Comparación implícita entre dos elementos diferentes para enriquecer el significado", "Lenguaje figurado": "Uso de expresiones que no se entienden literalmente para producir un efecto especial", "Tono melancólico": "Sensación de tristeza o nostalgia que transmite el poema", "Actitud del hablante": "La postura o sentimiento que el hablante expresa hacia el tema del poema"}}', 3, 0, true, '{"Lengua y Literatura","Análisis de poesía","Primer Medio",Aplicar}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (476, 102, 'fill_blanks', 'all', '{"texto": "En el estilo [BLANK_1], las palabras del hablante se reproducen tal como se dijeron, mientras que en el estilo [BLANK_2] se expresan con cambios en los tiempos verbales y pronombres.", "pistas": ["Este estilo mantiene las palabras originales sin modificaciones.", "Este estilo requiere adaptar los tiempos verbales para narrar lo dicho."], "explicacion": "El estilo directo reproduce exactamente las palabras del hablante, usando comillas y sin modificar los tiempos verbales ni pronombres. En cambio, el estilo indirecto parafrasea lo dicho, adaptando tiempos verbales y pronombres para integrarlo en la narración."}', '{"respuestas_correctas": {"BLANK_1": ["directo"], "BLANK_2": ["indirecto"]}}', 2, 0, true, '{"estilo directo","estilo indirecto","lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (164, 39, 'sequencing', 'all', '{"explicacion": "El análisis comienza con la identificación de símbolos para entender los elementos clave. Luego, se determina la actitud del hablante, lo que ayuda a comprender su perspectiva. Después, se observa cómo el uso del lenguaje influye en el tono y el efecto. A continuación, se relaciona la actitud con los símbolos y el lenguaje para profundizar la comprensión. Finalmente, se formula una interpretación global que integra todos estos aspectos.", "instruccion": "Ordena los siguientes pasos para analizar la actitud del hablante en un poema y relacionarla con los elementos formales y simbólicos.", "elementos_desordenados": ["Identificar los símbolos presentes en el poema", "Determinar la actitud del hablante hacia el tema", "Observar el uso del lenguaje y sus efectos", "Relacionar la actitud del hablante con los símbolos y el lenguaje", "Formular una interpretación global del poema"]}', '{"orden_correcto": ["Identificar los símbolos presentes en el poema", "Determinar la actitud del hablante hacia el tema", "Observar el uso del lenguaje y sus efectos", "Relacionar la actitud del hablante con los símbolos y el lenguaje", "Formular una interpretación global del poema"]}', 3, 0, true, '{"análisis poético","actitud del hablante",símbolos,lenguaje}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (165, 39, 'open_ended', 'all', '{"pregunta": "Lee el poema asignado y analiza la actitud del hablante hacia el tema central. ¿Cómo se relacionan esta actitud y los símbolos presentes en el poema con el efecto que produce el uso del lenguaje en la comprensión del texto? Explica tu respuesta con ejemplos concretos del poema.", "explicacion": "El docente debe valorar la capacidad del estudiante para aplicar conceptos literarios en el análisis, evidenciar comprensión del texto y conectar la actitud del hablante con los símbolos y el lenguaje para profundizar su interpretación.", "contexto_adicional": "Se sugiere que el poema sea uno previamente leído en clase para asegurar familiaridad con el texto.", "criterios_evaluacion": ["Identificación clara y precisa de la actitud del hablante hacia el tema (peso: 30%)", "Análisis detallado de los símbolos presentes y su relación con la actitud del hablante (peso: 40%)", "Explicación coherente del efecto producido por el uso del lenguaje, apoyada con ejemplos del poema (peso: 30%)"]}', '{"puntos_clave": ["Actitud melancólica o reflexiva del hablante", "Identificación de símbolos como el reloj y las hojas caídas", "Relación entre el lenguaje metafórico y el efecto emocional en el lector"], "respuesta_modelo": "En el poema, el hablante muestra una actitud melancólica hacia el paso del tiempo, lo que se refleja en símbolos como el reloj que avanza y las hojas que caen. Estos símbolos enfatizan la sensación de pérdida y nostalgia. Además, el uso de un lenguaje cargado de metáforas y adjetivos oscuros intensifica el tono reflexivo y triste del poema, permitiendo al lector comprender la profundidad emocional del hablante.", "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Análisis de poema","Actitud del hablante",Símbolos,"Comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (166, 40, 'compare_contrast', 'all', '{"conceptos": ["Símbolos", "Lenguaje figurado", "Repeticiones"], "criterios": ["Función en el poema", "Contribución al mensaje", "Impacto en la comprensión del lector"], "explicacion": "Los símbolos representan ideas o conceptos abstractos, mientras que el lenguaje figurado utiliza imágenes y figuras retóricas para embellecer o intensificar el mensaje. Las repeticiones, en cambio, enfatizan ciertas ideas o ritmos. Aunque los tres elementos contribuyen al mensaje global, difieren en su función específica y en cómo afectan la interpretación del lector.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Símbolos": {"Función en el poema": "Representar ideas o conceptos abstractos a través de imágenes concretas", "Contribución al mensaje": "Profundizan el significado y permiten múltiples interpretaciones", "Impacto en la comprensión del lector": "Generan reflexión y enriquecen la interpretación"}, "Repeticiones": {"Función en el poema": "Reiterar palabras, frases o estructuras para enfatizar ideas o crear ritmo", "Contribución al mensaje": "Destacan aspectos importantes y crean musicalidad", "Impacto en la comprensión del lector": "Refuerzan el mensaje y ayudan a memorizar"}, "Lenguaje figurado": {"Función en el poema": "Emplear metáforas, símiles, personificaciones, etc., para embellecer o intensificar el mensaje", "Contribución al mensaje": "Hace el lenguaje más expresivo y emotivo", "Impacto en la comprensión del lector": "Facilita la visualización y conexión emocional con el texto"}}}', 3, 0, true, '{análisis,poesía,comparación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (167, 40, 'compare_contrast', 'all', '{"conceptos": ["Símbolos", "Lenguaje figurado"], "criterios": ["Función en el poema", "Forma de expresión", "Impacto en la comprensión del mensaje"], "explicacion": "Los símbolos y el lenguaje figurado son recursos que enriquecen el poema, pero mientras los símbolos representan ideas o conceptos de manera concreta, el lenguaje figurado utiliza figuras retóricas para expresar significados de manera creativa. Ambos contribuyen a la interpretación, aunque su forma y función dentro del texto difieren.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Símbolos": {"Forma de expresión": "Imágenes o elementos con significado asociado", "Función en el poema": "Representar ideas o conceptos abstractos a través de elementos concretos", "Impacto en la comprensión del mensaje": "Permiten una interpretación más profunda y múltiple del poema"}, "Lenguaje figurado": {"Forma de expresión": "Metáforas, símiles, personificaciones, entre otros", "Función en el poema": "Emplear figuras retóricas para expresar ideas de forma creativa y sugestiva", "Impacto en la comprensión del mensaje": "Genera imágenes mentales y emociones que enriquecen la experiencia lectora"}}}', 4, 0, true, '{poesía,"análisis literario","recursos literarios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (168, 40, 'open_ended', 'all', '{"pregunta": "Analiza un poema leído en clase identificando los símbolos presentes, la actitud del hablante hacia el tema y el uso del lenguaje figurado y repeticiones. ¿Cómo contribuyen estos elementos al mensaje global del poema? Compara estos aspectos con otro poema para profundizar en tu análisis.", "explicacion": "El docente debe evaluar que el alumno no solo identifique elementos literarios, sino que también explique cómo estos conforman el significado del poema y puedan establecer conexiones entre ambos textos. Se valorará la profundidad del análisis y la capacidad crítica.", "contexto_adicional": "El estudiante debe seleccionar dos poemas previamente leídos en clase, preferentemente con temáticas o estilos distintos, para realizar un análisis comparativo.", "criterios_evaluacion": ["Identificación precisa de símbolos, lenguaje figurado y repeticiones en ambos poemas (peso: 30%)", "Análisis claro y coherente de la actitud del hablante y cómo estos elementos contribuyen al mensaje global en cada poema (peso: 40%)", "Capacidad para comparar y contrastar los aspectos formales y simbólicos entre ambos poemas, demostrando comprensión profunda (peso: 30%)"]}', '{"puntos_clave": ["Identificación de símbolos significativos en ambos poemas", "Análisis de la actitud del hablante hacia el tema central", "Evaluación del impacto del lenguaje figurado y repeticiones en el mensaje global", "Comparación entre los dos poemas destacando similitudes y diferencias"], "respuesta_modelo": "En el poema ''La canción de otoño en primavera'' de Pablo Neruda, el símbolo recurrente es la estación del otoño, que representa la melancolía y el paso del tiempo. El hablante muestra una actitud nostálgica y reflexiva hacia el amor y la juventud perdida. El uso de lenguaje figurado, como metáforas relacionadas con la naturaleza, y la repetición de ciertas frases enfatizan el sentimiento de añoranza. Comparándolo con el poema ''Rima LIII'' de Gustavo Adolfo Bécquer, donde el símbolo del pájaro que no volverá representa el amor perdido, el hablante adopta una actitud más resignada y melancólica. En ambos poemas, las repeticiones y figuras literarias refuerzan el mensaje de la inevitabilidad del paso del tiempo y la pérdida, aunque con matices distintos en la expresión emocional. Así, los símbolos y el lenguaje figurado trabajan conjuntamente para profundizar la experiencia emotiva y el mensaje global en cada texto.", "requiere_revision_humana": true}', 4, 0, true, '{"análisis literario",poesía,comparación,"lenguaje figurado"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (169, 40, 'concept_map', 'all', '{"conceptos": ["Aspectos formales", "Símbolos", "Lenguaje figurado", "Repeticiones", "Mensaje global"], "explicacion": "Se espera que el estudiante identifique cómo los aspectos formales (como la estructura y métrica) influyen en el uso de símbolos y lenguaje figurado, cómo las repeticiones refuerzan ideas o emociones, y cómo todos estos elementos se integran para construir y fortalecer el mensaje global del poema.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis de un poema para comprender su mensaje global?"}', '{"conceptos_centrales": ["Aspectos formales", "Mensaje global"], "relaciones_esperadas": [{"origen": "Aspectos formales", "destino": "Símbolos", "relacion": "estructuran"}, {"origen": "Aspectos formales", "destino": "Repeticiones", "relacion": "organizan"}, {"origen": "Símbolos", "destino": "Lenguaje figurado", "relacion": "se expresan mediante"}, {"origen": "Repeticiones", "destino": "Mensaje global", "relacion": "refuerzan"}, {"origen": "Lenguaje figurado", "destino": "Mensaje global", "relacion": "contribuye a"}], "requiere_revision_humana": true}', 3, 0, true, '{lengua_y_literatura,primer_medio,analisis_poetico,mapa_conceptual}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (170, 40, 'concept_map', 'all', '{"conceptos": ["Aspectos formales", "Símbolos", "Lenguaje figurado", "Repeticiones", "Mensaje global"], "explicacion": "Se espera que el estudiante identifique cómo los aspectos formales (como la estructura y métrica) influyen en el uso de símbolos y lenguaje figurado, cómo las repeticiones refuerzan ciertos significados o emociones, y en conjunto, cómo todos estos elementos contribuyen a construir y comunicar el mensaje global del poema.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis de un poema?"}', '{"conceptos_centrales": ["Aspectos formales", "Mensaje global"], "relaciones_esperadas": [{"origen": "Aspectos formales", "destino": "Repeticiones", "relacion": "organizan"}, {"origen": "Símbolos", "destino": "Lenguaje figurado", "relacion": "se expresan a través de"}, {"origen": "Repeticiones", "destino": "Mensaje global", "relacion": "refuerzan"}, {"origen": "Lenguaje figurado", "destino": "Mensaje global", "relacion": "enriquece"}, {"origen": "Aspectos formales", "destino": "Mensaje global", "relacion": "contribuyen a la estructura del"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Análisis de poemas","Mapa conceptual","Bloom: Analizar","Bloom: Crear"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (171, 41, 'criteria_evaluation', 'all', '{"rubrica": {"Justificación de las relaciones intertextuales": {"Excelente": "Reconoce y explica claramente las relaciones intertextuales presentes, demostrando cómo enriquecen la comprensión del poema.", "Satisfactorio": "Identifica algunas relaciones intertextuales y sugiere cómo aportan al texto, pero con explicaciones superficiales.", "Necesita mejorar": "No identifica o no justifica adecuadamente las relaciones intertextuales."}, "Identificación y análisis de símbolos literarios": {"Excelente": "Identifica con precisión múltiples símbolos clave en el poema y explica su significado de manera clara y profunda.", "Satisfactorio": "Identifica algunos símbolos relevantes y ofrece una explicación general de su significado.", "Necesita mejorar": "Reconoce pocos o ningún símbolo, o la explicación es confusa o incompleta."}, "Evaluación de la actitud del hablante y efecto del lenguaje": {"Excelente": "Analiza detalladamente la actitud del hablante y el efecto del lenguaje, relacionándolos con el tema y el mensaje del poema.", "Satisfactorio": "Describe la actitud del hablante y el uso del lenguaje, pero con análisis limitado o general.", "Necesita mejorar": "No logra identificar o explicar la actitud del hablante ni el efecto del lenguaje."}}, "explicacion": "Se espera que el estudiante demuestre capacidad crítica para valorar el análisis literario presentado, reconociendo la profundidad y precisión en la identificación de símbolos, la comprensión de las relaciones intertextuales y la interpretación de la actitud y uso del lenguaje en el poema.", "instruccion": "Evalúa el análisis del poema según la rúbrica propuesta y justifica la calificación asignada a cada criterio.", "caso_a_evaluar": "Se presenta un análisis escrito de un poema donde el estudiante identifica símbolos presentes en el texto, reconoce las relaciones intertextuales con otros poemas o textos literarios y explica cómo estos recursos contribuyen a la comprensión profunda del poema. Además, el estudiante evalúa la actitud del hablante y el efecto del uso del lenguaje en la obra."}', '{"evaluacion_modelo": {"Justificación de las relaciones intertextuales": "Excelente", "Identificación y análisis de símbolos literarios": "Satisfactorio", "Evaluación de la actitud del hablante y efecto del lenguaje": "Satisfactorio"}, "justificacion_modelo": "El análisis presenta una identificación adecuada de símbolos, aunque no exhaustiva, por lo que es satisfactorio. La justificación de las relaciones intertextuales es clara y completa, enriqueciendo la comprensión del poema, alcanzando el nivel excelente. La evaluación de la actitud del hablante y el efecto del lenguaje es general y poco profunda, por lo que es satisfactorio.", "requiere_revision_humana": true}', 4, 0, true, '{"evaluación literaria",poesía,"relaciones intertextuales","análisis crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (172, 41, 'criteria_evaluation', 'all', '{"rubrica": {"Análisis de recursos literarios": {"Excelente": "Identifica correctamente los recursos literarios y explica con claridad su efecto en el significado o la atmósfera del poema.", "Satisfactorio": "Reconoce algunos recursos literarios y ofrece una explicación general sobre su aporte al poema.", "Necesita mejorar": "No identifica o explica incorrectamente los recursos literarios y su impacto en el poema."}, "Justificación de relaciones intertextuales": {"Excelente": "Explica claramente y con ejemplos precisos cómo las relaciones con otros textos enriquecen la comprensión del poema.", "Satisfactorio": "Menciona relaciones intertextuales con alguna explicación, pero con ejemplos o argumentos poco desarrollados.", "Necesita mejorar": "No identifica o justifica adecuadamente las relaciones intertextuales o la explicación es confusa."}, "Coherencia y profundidad de la justificación": {"Excelente": "La evaluación es coherente, argumentada en profundidad y conecta todos los elementos analizados de forma integrada.", "Satisfactorio": "La justificación es clara pero presenta argumentos superficiales o poco integrados.", "Necesita mejorar": "La justificación es confusa, poco coherente o carece de argumentos claros."}}, "explicacion": "Se espera que el estudiante valore críticamente el análisis, identificando fortalezas y debilidades en la justificación de las relaciones intertextuales, en el análisis de recursos literarios y en la coherencia de la argumentación.", "instruccion": "Evalúa el análisis presentado del poema según la rúbrica y justifica la calificación que le asignas en cada criterio.", "caso_a_evaluar": "Se presenta un análisis de un poema de Pablo Neruda en el que se identifican símbolos como el mar y la noche, se describe la actitud melancólica del hablante y se relaciona el poema con otros textos que abordan la temática del amor y la pérdida. El análisis también explica cómo el uso de metáforas y lenguaje figurado contribuye a la atmósfera del poema."}', '{"evaluacion_modelo": {"Análisis de recursos literarios": "Excelente", "Justificación de relaciones intertextuales": "Satisfactorio", "Coherencia y profundidad de la justificación": "Satisfactorio"}, "justificacion_modelo": "El análisis menciona relaciones con otros textos pero no profundiza ni aporta ejemplos precisos, por lo que se considera satisfactorio. El reconocimiento y explicación de los recursos literarios es clara y adecuada, alcanzando un nivel excelente. La justificación general es coherente pero algo superficial, por lo que se califica como satisfactorio.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Evaluación,"Primer Año Medio",Poesía,Intertextualidad,"Recursos Literarios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (173, 41, 'criteria_evaluation', 'all', '{"rubrica": {"Análisis de las relaciones intertextuales": {"Excelente": "Presenta conexiones pertinentes y detalladas entre el poema y otros textos o contextos culturales, justificando cómo enriquecen la comprensión.", "Satisfactorio": "Identifica algunas relaciones intertextuales básicas y señala su contribución a la comprensión del poema de forma general.", "Necesita mejorar": "No logra establecer relaciones intertextuales relevantes o las menciona sin explicación que aporte a la comprensión."}, "Claridad y coherencia en la argumentación": {"Excelente": "La argumentación es clara, coherente y bien estructurada, facilitando la comprensión del análisis presentado.", "Satisfactorio": "La argumentación es comprensible aunque presenta algunas inconsistencias o falta de profundidad.", "Necesita mejorar": "La argumentación es confusa, inconexa o poco fundamentada, dificultando la comprensión del análisis."}, "Justificación de la importancia de los símbolos y recursos literarios": {"Excelente": "Identifica claramente los símbolos y recursos literarios y explica con profundidad su relevancia para la interpretación del poema.", "Satisfactorio": "Reconoce algunos símbolos y recursos literarios y ofrece una explicación general sobre su importancia para la comprensión del poema.", "Necesita mejorar": "Menciona símbolos o recursos literarios de manera superficial o incorrecta, sin justificar su relevancia para el poema."}}, "explicacion": "Se espera que el estudiante valore críticamente el análisis, argumentando por qué considera que cumple o no con cada nivel de desempeño según lo esperado, fundamentando sus juicios en ejemplos concretos del texto analizado.", "instruccion": "Evalúa el análisis presentado según la rúbrica y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Se presenta un análisis crítico de un poema clásico chileno, donde el estudiante identifica símbolos, menciona recursos literarios presentes y establece relaciones intertextuales con otros textos literarios o culturales. El análisis incluye una valoración sobre cómo estos elementos contribuyen a una comprensión más profunda del poema."}', '{"evaluacion_modelo": {"Análisis de las relaciones intertextuales": "Satisfactorio", "Claridad y coherencia en la argumentación": "Excelente", "Justificación de la importancia de los símbolos y recursos literarios": "Excelente"}, "justificacion_modelo": "El análisis identifica y explica de manera profunda los símbolos y recursos literarios, pero las relaciones intertextuales son mencionadas sólo de forma básica sin profundizar en su impacto. La argumentación es clara y bien estructurada, facilitando la comprensión.", "requiere_revision_humana": true}', 5, 0, true, '{evaluación,"análisis literario",intertextualidad,símbolos,"primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (174, 41, 'open_ended', 'all', '{"pregunta": "Elige un poema que hayas leído en clase y analiza cómo las relaciones intertextuales y los recursos literarios empleados contribuyen a una comprensión más profunda del texto. Justifica por qué estas herramientas son importantes para interpretar el poema más allá de su significado literal.", "explicacion": "El docente debe evaluar si el estudiante identifica correctamente las relaciones intertextuales y recursos literarios, analiza su función en el texto y argumenta con coherencia su relevancia para una interpretación profunda, más allá del sentido literal.", "contexto_adicional": "El estudiante puede basarse en cualquier poema trabajado en el curso, considerando elementos como símbolos, actitud del hablante y efectos lingüísticos.", "criterios_evaluacion": ["Claridad y profundidad del análisis sobre las relaciones intertextuales presentes en el poema (peso: 30%)", "Identificación y explicación del uso de recursos literarios y su efecto en la comprensión del poema (peso: 40%)", "Capacidad para justificar la importancia de estas relaciones y recursos en la interpretación global del poema (peso: 30%)"]}', '{"puntos_clave": ["Identificación de relaciones intertextuales pertinentes al poema", "Análisis de recursos literarios como metáforas, símbolos o tono", "Justificación clara de cómo estas herramientas enriquecen la interpretación"], "respuesta_modelo": "En el poema \"La canción de las madres\" de Pablo Neruda, se pueden identificar relaciones intertextuales con la tradición lírica que exalta la maternidad y con otros poemas de Neruda que abordan el amor y la vida. Además, el autor utiliza metáforas como \"la vida es un río que fluye\" para representar el paso del tiempo y la continuidad. Estos recursos literarios y las conexiones con otros textos amplían el significado del poema, permitiendo que el lector comprenda no solo el amor maternal sino también su dimensión universal y atemporal. Por lo tanto, las relaciones intertextuales y los recursos literarios son fundamentales para interpretar el poema en su totalidad, ya que enriquecen el mensaje y la experiencia estética del lector.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",poesía,evaluación,"primer medio",intertextualidad,"recursos literarios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (175, 41, 'open_ended', 'all', '{"pregunta": "Elige un poema que hayas leído en clase y analiza cómo las relaciones intertextuales y los recursos literarios presentes en el texto contribuyen a una comprensión más profunda del poema. Justifica por qué crees que estos elementos son importantes para interpretar el mensaje y la actitud del hablante.", "explicacion": "El docente debe valorar que el estudiante no solo identifique recursos y relaciones, sino que también argumente cómo estos enriquecen la interpretación del poema, demostrando pensamiento crítico y comprensión profunda.", "contexto_adicional": "Puedes considerar poemas trabajados en clase o seleccionados por ti que cumplan con los requisitos para identificar relaciones intertextuales y recursos literarios.", "criterios_evaluacion": ["Claridad y coherencia en la explicación del análisis de las relaciones intertextuales y los recursos literarios (peso: 40%).", "Capacidad para justificar la importancia de estos elementos en la comprensión del mensaje y la actitud del hablante (peso: 35%).", "Uso adecuado de ejemplos concretos del poema para apoyar el análisis y la justificación (peso: 25%)."]}', '{"puntos_clave": ["Identificación de relaciones intertextuales relevantes en el poema.", "Análisis de recursos literarios y su efecto en la interpretación.", "Justificación clara de cómo estos elementos profundizan la comprensión del mensaje y la actitud del hablante."], "respuesta_modelo": "En el poema ''La canción de otoño en primavera'' de Rubén Darío, se pueden identificar relaciones intertextuales con la tradición modernista y alusiones a la mitología clásica que enriquecen el texto. Por ejemplo, la referencia a la juventud perdida dialoga con otros poemas sobre el paso del tiempo y la nostalgia. Los recursos literarios, como la metáfora del otoño para representar la madurez y la melancolía, y el ritmo pausado que refleja la reflexión, contribuyen a revelar la actitud del hablante, que es melancólica pero también resignada. Estos elementos son importantes porque permiten al lector captar no solo el mensaje superficial, sino también la profundidad emocional y cultural que da sentido al poema.", "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,poesía,evaluación_nivel_5,primero_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (176, 42, 'open_ended', 'all', '{"pregunta": "Elige un poema leído en clase y crea una interpretación personal que integre un análisis detallado de los símbolos presentes, la actitud del hablante hacia el tema, el uso del lenguaje figurado y las repeticiones, así como aspectos formales y posibles relaciones intertextuales. Explica cómo estos elementos contribuyen al significado general del poema.", "explicacion": "El docente debe evaluar la capacidad del estudiante para crear una interpretación original y fundamentada, considerando múltiples elementos literarios y su interacción para construir el sentido del poema. Se valorará la capacidad crítica, el uso adecuado del vocabulario literario y la coherencia argumentativa.", "contexto_adicional": "El estudiante debe basar su respuesta en un poema previamente leído en clase, utilizando evidencia textual para sustentar su interpretación.", "criterios_evaluacion": ["Análisis profundo y coherente de los símbolos presentes en el poema (peso: 30%)", "Integración clara y fundamentada de la actitud del hablante y del uso del lenguaje figurado y repeticiones (peso: 40%)", "Identificación y explicación de aspectos formales y relaciones intertextuales, articulando su aporte al significado global (peso: 30%)"]}', '{"puntos_clave": ["Identificación y explicación de símbolos relevantes en el poema", "Descripción de la actitud del hablante y análisis del lenguaje figurado y repeticiones", "Reconocimiento de aspectos formales y relaciones intertextuales que enriquecen el significado"], "respuesta_modelo": "En el poema \"La noche oscura\" de San Juan de la Cruz, los símbolos como la noche y la oscuridad representan tanto el misterio espiritual como la búsqueda interior del hablante. La actitud del hablante es de anhelo y esperanza; a pesar de la oscuridad, se percibe una confianza en la transformación. El uso del lenguaje figurado, como las metáforas de la luz y el fuego, junto con la repetición de imágenes ligadas a la noche, intensifican el efecto emocional y refuerzan la temática mística. Formalmente, el poema utiliza versos cortos y rima consonante que aportan musicalidad y ritmo pausado, lo que sugiere meditación y serenidad. Además, puede establecerse una relación intertextual con otros textos místicos y religiosos que abordan el encuentro con lo divino. Estos elementos en conjunto crean una experiencia poética que invita al lector a explorar la dimensión espiritual del alma humana.", "requiere_revision_humana": true}', 4, 0, true, '{lengua_y_literatura,poesía,análisis_literario,creación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (177, 42, 'open_ended', 'all', '{"pregunta": "Elige un poema leído en clase y crea una interpretación personal que integre un análisis detallado de los símbolos presentes, la actitud del hablante hacia el tema, el uso del lenguaje figurado, las repeticiones, aspectos formales del poema y las posibles relaciones intertextuales que puedas identificar. Explica cómo estos elementos contribuyen a enriquecer el significado global del poema.", "explicacion": "El docente debe evaluar que el estudiante no solo identifique elementos literarios, sino que también los articule en una interpretación personal que muestre comprensión profunda del poema. Se valorará la capacidad de relacionar diferentes elementos y de expresar ideas originales y fundamentadas.", "contexto_adicional": "Se espera que el estudiante utilice ejemplos específicos del poema para sustentar su interpretación y que demuestre un pensamiento crítico y creativo en su análisis.", "criterios_evaluacion": ["Análisis detallado y coherente de los símbolos del poema (peso: 30%)", "Interpretación clara de la actitud del hablante y uso del lenguaje figurado, repeticiones y aspectos formales (peso: 40%)", "Integración coherente de relaciones intertextuales y creación de una interpretación personal original (peso: 30%)"]}', '{"puntos_clave": ["Identificación y análisis de símbolos clave en el poema", "Descripción de la actitud del hablante y ejemplos de lenguaje figurado y repeticiones", "Reconocimiento de aspectos formales y conexiones intertextuales con otras obras"], "respuesta_modelo": "En el poema \"La noche oscura\", se observa que la oscuridad simboliza tanto el miedo como la introspección profunda. El hablante muestra una actitud ambivalente hacia este tema, oscilando entre la incertidumbre y la búsqueda de conocimiento. El uso de metáforas como \"el manto de sombra que envuelve el alma\" y la repetición del verso \"silencio que grita\" enfatizan la tensión interna del hablante. Formalmente, el poema utiliza versos cortos y rimas consonantes que crean un ritmo pausado, reforzando la atmósfera reflexiva. Además, se pueden establecer relaciones con la tradición mística, evocando textos como los versos de San Juan de la Cruz, lo que intensifica el sentido de búsqueda espiritual. En conjunto, estos elementos permiten interpretar el poema como un viaje interior hacia la aceptación de la oscuridad como parte esencial del ser.", "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,poesía,análisis_literario,creación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (178, 42, 'concept_map', 'all', '{"conceptos": ["Símbolos", "Actitud del hablante", "Lenguaje figurado", "Repeticiones", "Aspectos formales", "Relaciones intertextuales", "Interpretación personal"], "explicacion": "El mapa conceptual debe mostrar cómo los símbolos, la actitud del hablante, el lenguaje figurado, las repeticiones, los aspectos formales y las relaciones intertextuales se integran y se conectan para construir una interpretación personal profunda y coherente del poema.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis e interpretación de un poema?"}', '{"conceptos_centrales": ["Interpretación personal", "Símbolos"], "relaciones_esperadas": [{"origen": "Símbolos", "destino": "Interpretación personal", "relacion": "aportan a"}, {"origen": "Actitud del hablante", "destino": "Interpretación personal", "relacion": "influye en"}, {"origen": "Lenguaje figurado", "destino": "Interpretación personal", "relacion": "enriquece"}, {"origen": "Repeticiones", "destino": "Aspectos formales", "relacion": "destacan"}, {"origen": "Aspectos formales", "destino": "Interpretación personal", "relacion": "estructuran"}, {"origen": "Relaciones intertextuales", "destino": "Interpretación personal", "relacion": "conectan con"}], "requiere_revision_humana": true}', 4, 0, true, '{mapa_conceptual,poesía,análisis_literario,creatividad}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (179, 42, 'concept_map', 'all', '{"conceptos": ["Símbolos", "Actitud del hablante", "Lenguaje figurado", "Repeticiones", "Aspectos formales", "Relaciones intertextuales"], "explicacion": "El mapa conceptual debe mostrar cómo los símbolos presentes en el poema reflejan o influyen en la actitud del hablante, cómo el lenguaje figurado y las repeticiones contribuyen a enfatizar temas o emociones, cómo los aspectos formales como la métrica y la estructura organizan el poema, y cómo las relaciones intertextuales enriquecen el significado global. Se espera que el estudiante conecte estos elementos para construir una interpretación integrada y coherente del poema.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el análisis e interpretación de un poema?"}', '{"conceptos_centrales": ["Símbolos", "Actitud del hablante"], "relaciones_esperadas": [{"origen": "Símbolos", "destino": "Actitud del hablante", "relacion": "reflejan"}, {"origen": "Lenguaje figurado", "destino": "Temas y emociones", "relacion": "enfatiza"}, {"origen": "Repeticiones", "destino": "Actitud del hablante", "relacion": "refuerzan"}, {"origen": "Aspectos formales", "destino": "Estructura del poema", "relacion": "organizan"}, {"origen": "Relaciones intertextuales", "destino": "Significado global", "relacion": "enriquecen"}], "requiere_revision_humana": true}', 5, 0, true, '{poesía,"análisis literario","mapa conceptual",interpretación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (180, 42, 'open_ended', 'all', '{"pregunta": "Después de leer el poema asignado, diseña una interpretación personal que integre el análisis de los símbolos presentes, la actitud del hablante hacia el tema, el uso del lenguaje figurado, las repeticiones, los aspectos formales del poema y las posibles relaciones intertextuales que puedas identificar. ¿Cómo contribuyen estos elementos a la comprensión profunda del mensaje del poema?", "explicacion": "El docente debe valorar cómo el estudiante articula una interpretación personal que muestre comprensión profunda del poema, apoyándose en análisis literarios claros y conexiones entre diferentes elementos del texto. Se debe considerar la coherencia y creatividad en la respuesta, así como la capacidad de integrar diversos aspectos literarios para enriquecer la interpretación.", "contexto_adicional": "El poema leído en clase es el texto base para esta actividad. Se espera que el estudiante utilice elementos del texto directamente para construir su interpretación.", "criterios_evaluacion": ["Análisis de símbolos y su relación con el tema del poema (peso: 30%)", "Identificación y explicación de la actitud del hablante y el uso del lenguaje figurado, incluyendo repeticiones (peso: 40%)", "Integración de aspectos formales y relaciones intertextuales en la interpretación personal (peso: 30%)"]}', '{"puntos_clave": ["Identificación clara de símbolos y su significado en el contexto del poema", "Descripción de la actitud del hablante y análisis del lenguaje figurado y repeticiones", "Integración de aspectos formales del poema y reconocimiento de relaciones intertextuales"], "respuesta_modelo": "En este poema, los símbolos como la luna y el río representan el paso del tiempo y la fluidez de las emociones, respectivamente, lo que refleja la temática central de la transformación personal. La actitud del hablante es melancólica pero esperanzada, evidenciada en el uso de metáforas que humanizan a la naturaleza y en las repeticiones del verso ''nunca se detiene'', que enfatizan la inevitabilidad del cambio. Formalmente, el poema utiliza versos cortos y una estructura libre que sugieren libertad y movimiento, lo que se relaciona con otros textos literarios que abordan el tema del crecimiento, estableciendo así relaciones intertextuales que enriquecen su significado. En conjunto, estos elementos contribuyen a una comprensión profunda del mensaje sobre la aceptación del cambio y la continuidad de la vida.", "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,poesía,análisis_literario,creación,primero_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (181, 43, 'multiple_choice', 'all', '{"opciones": {"A": "La descripción del escenario donde ocurre la obra", "B": "El problema o enfrentamiento que enfrentan los personajes principales", "C": "El tipo de narrador que cuenta la historia", "D": "La cantidad de actos que tiene la obra"}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para identificar el conflicto en un texto dramático?", "explicacion": "La respuesta correcta es B, porque el conflicto en un texto dramático se refiere principalmente al problema o enfrentamiento que viven los personajes principales, lo que impulsa la trama. La opción A es un elemento escenográfico, pero no define el conflicto. La opción C es más relevante en narrativas literarias, no en textos dramáticos que no suelen tener narrador. La opción D se refiere a la estructura formal, pero no al conflicto en sí."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"texto dramático",conflicto,"elementos básicos","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (182, 43, 'multiple_choice', 'all', '{"opciones": {"A": "El escenario donde se desarrolla la obra", "B": "El problema o tensión entre personajes", "C": "La descripción física de los personajes", "D": "El narrador que cuenta la historia"}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para identificar el conflicto en un texto dramático?", "explicacion": "La respuesta correcta es B porque el conflicto en un texto dramático se refiere a la tensión o problema central que enfrentan los personajes, lo que impulsa la acción de la obra. El escenario (A) es el lugar donde ocurre la acción, pero no identifica el conflicto. La descripción física (C) ayuda a caracterizar a los personajes, pero no define el conflicto. El narrador (D) no es un elemento básico en textos dramáticos, ya que suelen representarse mediante diálogos y acciones."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"texto dramático",conflicto,"elementos dramáticos","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (183, 43, 'multiple_choice', 'all', '{"opciones": {"A": "La descripción detallada del escenario donde se desarrolla la obra", "B": "El problema principal que enfrentan los personajes", "C": "La longitud de los diálogos entre personajes secundarios", "D": "La cantidad de actos que tiene la obra"}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para identificar el conflicto en un texto dramático?", "explicacion": "La respuesta correcta es B, porque el conflicto en un texto dramático se refiere al problema principal que enfrentan los personajes y que impulsa la acción. La opción A, aunque importante para la ambientación, no define el conflicto. La opción C se enfoca en diálogos secundarios que pueden no estar relacionados con el conflicto principal. La opción D se refiere a la estructura formal de la obra, pero no identifica el conflicto."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"texto dramático",conflicto,"elementos literarios","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (184, 43, 'true_false', 'all', '{"afirmacion": "El conflicto en un texto dramático es el problema principal que enfrentan los personajes y que impulsa la trama.", "explicacion": "Esta afirmación es verdadera porque el conflicto es el elemento central que genera tensión y movimiento en la historia, mostrando los desafíos o problemas que los personajes deben resolver."}', '{"es_verdadero": true}', 1, 0, true, '{"texto dramático",conflicto,"elementos básicos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (185, 43, 'fill_blanks', 'all', '{"texto": "En un texto dramático, el [BLANK_1] representa la lucha principal que impulsa la historia, mientras que los [BLANK_2] son los personajes más importantes que participan en ella.", "pistas": ["Es el problema o situación que crea tensión en la obra.", "Son aquellos que tienen un papel central en la trama."], "explicacion": "El conflicto es el elemento que genera la tensión y el desarrollo de la historia, mostrando el problema humano central. Los personajes principales son quienes intervienen activamente en ese conflicto y en la trama general del drama."}', '{"respuestas_correctas": {"BLANK_1": ["conflicto"], "BLANK_2": ["personajes principales", "protagonistas"]}}', 2, 0, true, '{"lengua y literatura","primer medio","texto dramático",conflicto,"personajes principales"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (186, 44, 'multiple_choice', 'all', '{"opciones": {"A": "Identificando solo las acciones del personaje sin relacionarlas con el conflicto.", "B": "Explicando el problema humano que genera el conflicto y cómo las motivaciones del personaje cambian a lo largo de la obra.", "C": "Describiendo únicamente el escenario donde ocurre la obra y no mencionando al personaje.", "D": "Resumiendo la historia sin hacer referencia a los personajes ni al conflicto."}, "pregunta": "En un texto dramático, ¿cuál es la mejor manera de explicar el conflicto principal y describir la evolución de un personaje principal?", "explicacion": "La opción B es correcta porque conecta directamente el conflicto con un problema humano y analiza cómo las motivaciones del personaje principal evolucionan durante el desarrollo del texto dramático, cumpliendo con el objetivo específico. La opción A es incorrecta porque se enfoca solo en acciones aisladas sin contexto del conflicto. La opción C es un error conceptual porque se centra en el escenario y omite el análisis del personaje y el conflicto. La opción D no cumple con el objetivo porque no considera ni el conflicto ni la evolución de los personajes."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"Lengua y Literatura","Texto Dramático",Conflicto,Personajes,Comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (187, 44, 'multiple_choice', 'all', '{"opciones": {"A": "Presentar una descripción detallada del escenario donde ocurre la obra.", "B": "Mostrar un problema humano que impulsa la acción y el desarrollo de los personajes.", "C": "Enumerar los personajes que aparecen en la obra sin relación con sus motivaciones.", "D": "Establecer el final de la obra desde el principio para evitar confusiones."}, "pregunta": "En un texto dramático, ¿cuál es la función principal del conflicto en la historia?", "explicacion": "La opción B es correcta porque el conflicto en un texto dramático representa el problema humano central que motiva y dirige la acción, permitiendo comprender la evolución y motivaciones de los personajes principales. La opción A es incorrecta porque la descripción del escenario no define el conflicto. La opción C es un error común que confunde la simple enumeración de personajes con su análisis. La opción D es incorrecta porque el conflicto se desarrolla a lo largo de la obra y no se establece el final desde el inicio."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{conflicto,personajes,"texto dramático","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (188, 44, 'true_false', 'all', '{"afirmacion": "En un texto dramático, el conflicto principal siempre surge exclusivamente de problemas externos y no involucra luchas internas de los personajes.", "explicacion": "Esta afirmación es falsa porque en los textos dramáticos el conflicto puede originarse tanto de problemas externos (como enfrentamientos entre personajes o situaciones sociales) como de conflictos internos dentro de los personajes, como dilemas emocionales o éticos que afectan su evolución y motivaciones."}', '{"es_verdadero": false}', 2, 0, true, '{conflicto,personajes,"texto dramático"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (189, 44, 'drag_drop_matching', 'all', '{"explicacion": "El conflicto es el problema central que mueve la trama; la motivación explica por qué el personaje actúa; la evolución del personaje muestra sus cambios durante la obra; el personaje principal es el foco de la historia; el escenario es un distractor sin relación directa en este emparejamiento.", "instruccion": "Empareja cada concepto relacionado con el conflicto y los personajes en un texto dramático con su definición o ejemplo correspondiente", "columna_derecha": ["Problema o tensión central que impulsa la acción de la obra", "Razones o deseos que impulsan las acciones de un personaje", "Personaje que experimenta cambios a lo largo de la obra", "Personaje central alrededor del cual gira la historia", "Lugar o ambiente donde se desarrolla la acción"], "columna_izquierda": ["Conflicto", "Evolución del personaje", "Motivación", "Personaje principal", "Distractor: Escenario"]}', '{"emparejamientos_correctos": {"Conflicto": "Problema o tensión central que impulsa la acción de la obra", "Motivación": "Razones o deseos que impulsan las acciones de un personaje", "Personaje principal": "Personaje central alrededor del cual gira la historia", "Evolución del personaje": "Personaje que experimenta cambios a lo largo de la obra"}}', 2, 0, true, '{conflicto,personajes,"análisis dramático","primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (190, 44, 'sequencing', 'all', '{"explicacion": "El orden comienza con la presentación de la situación inicial y los personajes, luego surge un problema que genera el conflicto. A continuación, los personajes enfrentan una crisis que intensifica el conflicto, seguido por las decisiones que reflejan sus motivaciones y generan cambios. Finalmente, el conflicto se resuelve y se muestra la evolución de los personajes.", "instruccion": "Ordena los siguientes pasos/eventos en la secuencia correcta para explicar el conflicto y la evolución de los personajes en un texto dramático.", "elementos_desordenados": ["Los personajes enfrentan una crisis que intensifica el conflicto principal.", "Se presenta la situación inicial y los personajes principales.", "Los personajes toman decisiones que reflejan sus motivaciones y generan cambios.", "Se resuelve el conflicto y se muestra cómo han cambiado los personajes.", "Surge un problema o conflicto que afecta a los personajes."]}', '{"orden_correcto": ["Se presenta la situación inicial y los personajes principales.", "Surge un problema o conflicto que afecta a los personajes.", "Los personajes enfrentan una crisis que intensifica el conflicto principal.", "Los personajes toman decisiones que reflejan sus motivaciones y generan cambios.", "Se resuelve el conflicto y se muestra cómo han cambiado los personajes."]}', 3, 0, true, '{conflicto,personajes,secuencia,"texto dramático"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (191, 45, 'multiple_choice', 'all', '{"opciones": {"A": "Representar un personaje secundario sin importancia en la trama.", "B": "Transmitir un significado o idea abstracta que enriquece la comprensión del conflicto.", "C": "Describir en detalle el escenario donde se desarrolla la obra.", "D": "Mostrar únicamente las acciones físicas de los personajes principales."}, "pregunta": "En un texto dramático, ¿cuál es la función principal de un símbolo literario dentro de la obra?", "explicacion": "La opción B es correcta porque un símbolo literario en un texto dramático sirve para representar ideas o conceptos abstractos que aportan profundidad al conflicto y a la interpretación de la obra. La opción A es incorrecta porque los símbolos no representan personajes sino conceptos; la opción C es un error común al confundir símbolos con descripciones del escenario; y la opción D limita la función literaria a acciones físicas, olvidando el significado simbólico."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"símbolos literarios","texto dramático","análisis de personajes"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (192, 45, 'multiple_choice', 'all', '{"opciones": {"A": "Describir detalladamente la apariencia física de los personajes.", "B": "Representar ideas o conceptos abstractos que enriquecen el significado de la obra.", "C": "Indicar el lugar exacto donde se desarrolla la acción.", "D": "Mostrar el diálogo literal sin interpretación adicional."}, "pregunta": "En una obra dramática, ¿cuál es el propósito principal de identificar un símbolo literario dentro del texto?", "explicacion": "La opción B es correcta porque un símbolo literario es un elemento que representa ideas o conceptos abstractos, lo que permite una comprensión más profunda del texto dramático. La opción A confunde símbolos con descripciones físicas; la C se refiere al escenario, no a símbolos; y la D es solo una reproducción del diálogo sin análisis, lo que no ayuda a interpretar símbolos ni temas."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"símbolos literarios","análisis de textos dramáticos","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (193, 45, 'drag_drop_matching', 'all', '{"explicacion": "Los personajes tipo son aquellos estereotipados que representan características generalizadas. Los símbolos literarios son objetos o figuras que significan algo más profundo que su apariencia. El conflicto es el problema central que mueve la trama. Los tópicos literarios son ideas o temas recurrentes en la literatura. Los personajes redondos son complejos y evolucionan en la historia. El narrador es un distractor, ya que no suele estar presente en textos dramáticos.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente en un texto dramático.", "columna_derecha": ["Elemento que representa una idea o concepto recurrente en la literatura", "Personaje que presenta una personalidad compleja y desarrollo a lo largo de la obra", "Problema o enfrentamiento central que impulsa la acción dramática", "Figura o cosa que representa algo más allá de su sentido literal", "Personaje que encarna características generalizadas y estereotipadas", "Voz que relata la historia, no presente en el drama"], "columna_izquierda": ["Personaje tipo", "Símbolo literario", "Conflicto", "Tópico literario", "Personaje redondo", "Distractor: Narrador"]}', '{"emparejamientos_correctos": {"Conflicto": "Problema o enfrentamiento central que impulsa la acción dramática", "Personaje tipo": "Personaje que encarna características generalizadas y estereotipadas", "Personaje redondo": "Personaje que presenta una personalidad compleja y desarrollo a lo largo de la obra", "Tópico literario": "Elemento que representa una idea o concepto recurrente en la literatura", "Símbolo literario": "Figura o cosa que representa algo más allá de su sentido literal"}}', 3, 0, true, '{"Lengua y Literatura","Análisis dramático",Personajes,Símbolos,Conflicto}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (603, 128, 'criteria_evaluation', 'all', '{"rubrica": {"Efectividad persuasiva": {"Excelente": "Los argumentos logran persuadir eficazmente al público apoyándose en un lenguaje claro y apelando tanto a la lógica como a las emociones.", "Satisfactorio": "Los argumentos son persuasivos en algunos aspectos, pero el lenguaje o la apelación emocional son limitados o poco convincentes.", "Necesita mejorar": "Los argumentos no logran persuadir, el lenguaje es confuso o las apelaciones emocionales son inapropiadas o ausentes."}, "Validez de los argumentos": {"Excelente": "Los argumentos presentados son sólidos, basados en evidencia confiable y pertinentes al tema, sin contradicciones ni falacias.", "Satisfactorio": "Los argumentos son mayormente válidos, aunque algunos carecen de evidencia suficiente o son poco claros.", "Necesita mejorar": "Los argumentos presentan falacias, carecen de evidencia o son irrelevantes para el tema tratado."}, "Claridad y coherencia en la exposición": {"Excelente": "El discurso se presenta de manera clara y coherente, facilitando la comprensión de los argumentos y su relación con el tema.", "Satisfactorio": "El discurso es en general claro, pero presenta algunos momentos de confusión o falta de coherencia en la exposición.", "Necesita mejorar": "El discurso es difícil de seguir, con ideas desconectadas o poco claras."}}, "explicacion": "Se espera que el estudiante identifique la validez de los argumentos basándose en la evidencia y la lógica presentada, analice la efectividad persuasiva considerando el lenguaje y la apelación emocional, y evalúe la claridad y coherencia del discurso para determinar su impacto general.", "instruccion": "Evalúa el discurso escuchado según los criterios de la rúbrica y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Has escuchado un discurso argumentativo sobre la importancia de implementar un programa de reciclaje obligatorio en tu colegio. El emisor presenta datos estadísticos, ejemplos concretos y apela a valores ecológicos para convencer a su audiencia."}', '{"evaluacion_modelo": {"Efectividad persuasiva": "Excelente", "Validez de los argumentos": "Satisfactorio", "Claridad y coherencia en la exposición": "Satisfactorio"}, "justificacion_modelo": "Los argumentos son mayormente válidos pero carecen de evidencia en algunos casos, por lo que se califican como satisfactorios. El emisor utiliza un lenguaje claro y apela eficazmente a la lógica y emociones, logrando una alta efectividad persuasiva. Sin embargo, la exposición tiene algunos momentos poco claros, lo que justifica la calificación satisfactoria en claridad y coherencia.", "requiere_revision_humana": true}', 5, 0, true, '{"discurso argumentativo","evaluación de argumentos",debate,"primeros medios","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (194, 45, 'sequencing', 'all', '{"explicacion": "El análisis de un personaje principal comienza por describir sus características básicas, luego se identifican sus acciones para comprender su papel en la trama. Después se determina el conflicto en el que participa, lo que permite relacionar al personaje con el problema humano que expresa la obra, y finalmente, se analizan los símbolos literarios asociados para enriquecer la comprensión.", "instruccion": "Ordena los siguientes pasos para analizar un personaje principal en un texto dramático.", "elementos_desordenados": ["Identificar las acciones y decisiones del personaje en la obra.", "Describir las características físicas y psicológicas del personaje.", "Determinar el conflicto principal en el que está involucrado el personaje.", "Relacionar el personaje con el problema humano que representa el conflicto.", "Extraer símbolos literarios asociados al personaje."]}', '{"orden_correcto": ["Describir las características físicas y psicológicas del personaje.", "Identificar las acciones y decisiones del personaje en la obra.", "Determinar el conflicto principal en el que está involucrado el personaje.", "Relacionar el personaje con el problema humano que representa el conflicto.", "Extraer símbolos literarios asociados al personaje."]}', 3, 0, true, '{"análisis de personajes","texto dramático","secuencia lógica"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (195, 45, 'open_ended', 'all', '{"pregunta": "Después de leer la obra dramática asignada, identifica un personaje principal y analiza cómo sus características y acciones reflejan un conflicto humano central en la obra. Además, señala un símbolo literario presente en el texto y explica qué significado aporta a la comprensión del conflicto o del personaje.", "explicacion": "Esta pregunta evalúa la capacidad del estudiante para aplicar criterios de análisis literario en un texto dramático. Se espera que el alumno relacione personajes y símbolos con el conflicto central, demostrando comprensión profunda y uso de evidencias textuales.", "contexto_adicional": "Se recomienda que el estudiante utilice ejemplos específicos del texto para fundamentar su análisis.", "criterios_evaluacion": ["Identificación clara y precisa del personaje principal y del conflicto humano que representa (peso: 30%)", "Análisis coherente de las características y acciones del personaje en relación con el conflicto (peso: 40%)", "Interpretación adecuada del símbolo literario y su relación con el conflicto o el personaje (peso: 30%)"]}', '{"puntos_clave": ["Identificación del personaje principal y el conflicto humano central", "Análisis de las características y acciones del personaje en relación con el conflicto", "Explicación del símbolo literario y su significado en la obra"], "respuesta_modelo": "En la obra, el personaje principal es Juan, un hombre atrapado entre su deseo de libertad y las expectativas sociales que lo limitan. Este conflicto humano refleja la lucha entre el individualismo y la conformidad social. Juan es terco y soñador, lo que lo lleva a confrontar a su familia y la sociedad, evidenciando la tensión interna del personaje. Un símbolo literario relevante es la jaula que aparece en varias escenas, representando la prisión simbólica de las normas sociales que restringen a Juan. Este símbolo ayuda a comprender mejor el conflicto, mostrando cómo las limitaciones externas afectan la libertad personal del personaje.", "requiere_revision_humana": true}', 3, 0, true, '{"análisis literario","texto dramático",personajes,"símbolos literarios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (196, 46, 'compare_contrast', 'all', '{"conceptos": ["Creencias", "Prejuicios", "Estereotipos"], "criterios": ["Origen", "Impacto en la percepción social", "Flexibilidad o cambio a lo largo del tiempo"], "explicacion": "Las creencias son ideas o convicciones generalmente aceptadas basadas en valores o experiencias, los prejuicios son juicios preconcebidos y negativos sobre un grupo sin conocimiento suficiente, mientras que los estereotipos son imágenes o ideas simplificadas y fijas sobre características de un grupo. Las creencias pueden ser más flexibles y cambiar con la experiencia, los prejuicios suelen ser resistentes, y los estereotipos se mantienen a través de la repetición cultural. En cuanto al impacto, los prejuicios y estereotipos suelen generar discriminación, mientras que las creencias pueden ser neutras o positivas.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Creencias": {"Origen": "Basadas en valores, experiencias o tradiciones culturales", "Impacto en la percepción social": "Pueden influir positivamente o negativamente, pero no siempre generan discriminación", "Flexibilidad o cambio a lo largo del tiempo": "Generalmente flexibles y susceptibles a cambio con nuevas experiencias"}, "Prejuicios": {"Origen": "Juicios preconcebidos sin base suficiente, influenciados por emociones y estereotipos", "Impacto en la percepción social": "Generan percepciones negativas y pueden conducir a discriminación", "Flexibilidad o cambio a lo largo del tiempo": "Difíciles de cambiar, suelen ser resistentes incluso con evidencia contraria"}, "Estereotipos": {"Origen": "Ideas simplificadas y generalizadas sobre un grupo, transmitidas culturalmente", "Impacto en la percepción social": "Refuerzan imágenes fijas que pueden limitar la comprensión y generar prejuicios", "Flexibilidad o cambio a lo largo del tiempo": "Persisten a través del tiempo aunque pueden modificarse lentamente con cambios sociales"}}}', 3, 0, true, '{comparar,análisis,"lengua y literatura","primer medio",drama}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (197, 46, 'compare_contrast', 'all', '{"conceptos": ["Prejuicios", "Estereotipos"], "criterios": ["Origen o fuente", "Grado de generalización", "Impacto en la percepción de los personajes"], "explicacion": "Los prejuicios son juicios preconcebidos que una persona tiene hacia otra sin un conocimiento profundo, mientras que los estereotipos son creencias generalizadas y simplificadas sobre un grupo. Ambos afectan la percepción de los personajes en la obra, pero difieren en su origen y nivel de generalización.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Prejuicios": {"Origen o fuente": "Experiencias personales o emociones", "Grado de generalización": "Menor, más individual", "Impacto en la percepción de los personajes": "Juicios personales que afectan la interacción y conflicto"}, "Estereotipos": {"Origen o fuente": "Creencias culturales o sociales generalizadas", "Grado de generalización": "Alto, afecta a grupos enteros", "Impacto en la percepción de los personajes": "Simplifica y fija roles o características de los personajes"}}}', 4, 0, true, '{"Lengua y Literatura","Análisis de textos","Prejuicios y estereotipos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (198, 46, 'open_ended', 'all', '{"pregunta": "Analiza cómo las creencias, prejuicios y estereotipos presentes en la obra dramática reflejan la visión del mundo de su época. ¿De qué manera estos elementos se conectan o contrastan con las actitudes y valores actuales? Justifica tu respuesta con ejemplos específicos del texto y del contexto histórico-social.", "explicacion": "Esta pregunta evalúa la habilidad del estudiante para analizar críticamente un texto dramático en su contexto histórico y social, y para hacer conexiones con el presente. Se debe valorar la profundidad del análisis, la capacidad argumentativa y la pertinencia de los ejemplos usados.", "contexto_adicional": "La obra dramática debe ser conocida por los estudiantes y haber sido analizada en clase, considerando su contexto histórico y social.", "criterios_evaluacion": ["Identificación y explicación clara de las creencias, prejuicios y estereotipos en la obra (peso: 30%)", "Análisis crítico de cómo estos elementos representan la visión de mundo de la época en que fue escrita la obra (peso: 40%)", "Capacidad para establecer conexiones o contrastes entre la obra y la realidad actual, con ejemplos pertinentes (peso: 30%)"]}', '{"puntos_clave": ["Descripción de prejuicios y estereotipos en la obra", "Relación de estos con la visión del mundo histórica", "Comparación con la realidad o valores actuales"], "respuesta_modelo": "En la obra, los personajes muestran creencias y prejuicios típicos de su época, como la discriminación hacia ciertos grupos sociales, lo cual refleja una visión del mundo marcada por jerarquías rígidas y roles definidos. Por ejemplo, el personaje principal es rechazado por su origen social, lo que evidencia un estereotipo prevalente en esa sociedad. Al comparar esto con la actualidad, aunque persisten algunos prejuicios, existe una mayor conciencia y rechazo social hacia la discriminación, evidenciado en leyes y movimientos sociales que promueven la igualdad. Sin embargo, algunos estereotipos aún persisten, lo que demuestra una continuidad y transformación en las actitudes sociales. De este modo, la obra no solo refleja su tiempo, sino que también invita a reflexionar sobre los avances y desafíos presentes.", "requiere_revision_humana": true}', 4, 0, true, '{lengua-y-literatura,análisis-textual,contexto-histórico,pensamiento-crítico}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (199, 46, 'concept_map', 'all', '{"conceptos": ["Creencias", "Prejuicios", "Estereotipos", "Visión de mundo de la época", "Conexión con el presente"], "explicacion": "Se espera que el estudiante conecte cómo las creencias, prejuicios y estereotipos presentes en la obra reflejan la visión de mundo de la época en que fue escrita y cómo estos elementos mantienen o cambian su relevancia y significado en el contexto actual.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la obra dramática y su análisis histórico y actual?"}', '{"conceptos_centrales": ["Creencias", "Visión de mundo de la época"], "relaciones_esperadas": [{"origen": "Creencias", "destino": "Prejuicios", "relacion": "influyen en"}, {"origen": "Prejuicios", "destino": "Estereotipos", "relacion": "se manifiestan a través de"}, {"origen": "Creencias", "destino": "Visión de mundo de la época", "relacion": "son parte de"}, {"origen": "Estereotipos", "destino": "Conexión con el presente", "relacion": "se comparan con"}, {"origen": "Visión de mundo de la época", "destino": "Conexión con el presente", "relacion": "contrasta con"}], "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Análisis de texto dramático","Mapa conceptual","Comparación histórica"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (200, 46, 'concept_map', 'all', '{"conceptos": ["Conflicto dramático", "Creencias", "Prejuicios", "Estereotipos", "Visión de mundo de la época", "Conexión con el presente", "Personajes principales"], "explicacion": "Se espera que el estudiante conecte cómo el conflicto dramático surge de las creencias, prejuicios y estereotipos presentes en la obra, relacionándolos con la visión de mundo de la época en que fue escrita y cómo estos elementos influyen en los personajes principales. Además, debe mostrar la conexión entre esos aspectos y su vigencia o transformación en el presente.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis de una obra dramática y su relevancia histórica y actual?"}', '{"conceptos_centrales": ["Conflicto dramático", "Visión de mundo de la época"], "relaciones_esperadas": [{"origen": "Creencias", "destino": "Prejuicios", "relacion": "generan"}, {"origen": "Prejuicios", "destino": "Estereotipos", "relacion": "se manifiestan como"}, {"origen": "Estereotipos", "destino": "Personajes principales", "relacion": "afectan"}, {"origen": "Visión de mundo de la época", "destino": "Creencias", "relacion": "influye en"}, {"origen": "Conflicto dramático", "destino": "Prejuicios", "relacion": "es reflejo de"}, {"origen": "Conexión con el presente", "destino": "Conflicto dramático", "relacion": "permite analizar"}, {"origen": "Personajes principales", "destino": "Conflicto dramático", "relacion": "representan"}], "requiere_revision_humana": true}', 4, 0, true, '{"análisis textual",drama,"historia y literatura","comparación temporal"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (201, 47, 'criteria_evaluation', 'all', '{"rubrica": {"Evaluación del uso de elementos escénicos": {"Excelente": "Analiza detalladamente cómo los elementos escénicos refuerzan la comprensión del conflicto y los personajes.", "Satisfactorio": "Menciona algunos elementos escénicos y su función, pero con un análisis limitado.", "Necesita mejorar": "No reconoce o evalúa adecuadamente los elementos escénicos presentes."}, "Identificación y análisis de relaciones intertextuales": {"Excelente": "Reconoce y explica claramente varias relaciones intertextuales relevantes que enriquecen la comprensión de la obra.", "Satisfactorio": "Identifica algunas relaciones intertextuales, pero el análisis es superficial o poco claro.", "Necesita mejorar": "No identifica o entiende las relaciones intertextuales o las confunde con otros elementos."}, "Justificación de la importancia de ambos aspectos en la comprensión profunda de la obra": {"Excelente": "Presenta una justificación coherente, argumentada y fundamentada que integra las relaciones intertextuales y elementos escénicos para explicar la comprensión profunda de la obra.", "Satisfactorio": "Justifica la importancia de manera general, con argumentos poco desarrollados o poco claros.", "Necesita mejorar": "No logra justificar o la justificación es inconsistente y sin fundamento."}}, "explicacion": "Se espera que el estudiante demuestre capacidad para identificar y analizar las relaciones intertextuales y elementos escénicos, y que argumente claramente su importancia para la comprensión del conflicto y personajes en la obra dramática.", "instruccion": "Evalúa el análisis presentado según los criterios de la rúbrica y justifica tu calificación para cada criterio, explicando por qué le asignas ese nivel de desempeño.", "caso_a_evaluar": "Analiza el siguiente fragmento de una obra dramática leída en clase junto con su adaptación escénica grabada. Identifica las relaciones intertextuales presentes y los elementos escénicos utilizados en la puesta en escena. Luego, evalúa cómo estos aspectos contribuyen a una comprensión más profunda del conflicto y de los personajes principales de la obra."}', '{"evaluacion_modelo": {"Evaluación del uso de elementos escénicos": "Excelente", "Identificación y análisis de relaciones intertextuales": "Satisfactorio", "Justificación de la importancia de ambos aspectos en la comprensión profunda de la obra": "Satisfactorio"}, "justificacion_modelo": "El análisis reconoce algunas relaciones intertextuales pero sin profundidad suficiente, mientras que el uso de elementos escénicos está bien detallado y claramente vinculado a la comprensión del conflicto y personajes. La justificación integra ambos aspectos, aunque con argumentos que podrían profundizarse más.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Primero Medio","Evaluación crítica",Dramaturgia,Intertextualidad}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (202, 47, 'criteria_evaluation', 'all', '{"rubrica": {"Análisis de elementos escénicos": {"Excelente": "Analiza detalladamente cómo los elementos escénicos influyen en la comprensión del conflicto y problemas humanos, con ejemplos precisos y argumentación sólida.", "Satisfactorio": "Describe algunos elementos escénicos y su función, pero el análisis es general o con pocos ejemplos.", "Necesita mejorar": "No se realiza un análisis claro o se omiten los elementos escénicos en la comprensión de la obra."}, "Capacidad crítica y argumentativa": {"Excelente": "Presenta una evaluación crítica coherente, con argumentos bien fundamentados y conectados al objetivo de aprendizaje.", "Satisfactorio": "Ofrece una evaluación con argumentos presentes pero poco desarrollados o con conexiones débiles al objetivo.", "Necesita mejorar": "La evaluación carece de argumentación clara o no se relaciona con el objetivo de aprendizaje."}, "Justificación de relaciones intertextuales": {"Excelente": "El estudiante identifica y explica con profundidad y claridad las relaciones intertextuales, mostrando comprensión crítica y relevancia para el análisis de la obra.", "Satisfactorio": "El estudiante menciona algunas relaciones intertextuales, pero la explicación es superficial o poco clara en cuanto a su relevancia para la obra.", "Necesita mejorar": "El estudiante apenas o no identifica relaciones intertextuales o las menciona sin explicar su importancia para la comprensión del texto."}}, "explicacion": "Se espera que el estudiante valore críticamente la capacidad del ensayo para justificar las relaciones intertextuales y el análisis de los elementos escénicos, fundamentando su evaluación en ejemplos claros y en la coherencia argumentativa con el objetivo de aprendizaje propuesto.", "instruccion": "Evalúa el ensayo crítico entregado según los criterios de la rúbrica y justifica la calificación otorgada a cada criterio, argumentando tu evaluación.", "caso_a_evaluar": "Se presenta un ensayo crítico realizado por un estudiante que analiza la obra dramática ''La casa de Bernarda Alba'' de Federico García Lorca. En su análisis, el estudiante aborda las relaciones intertextuales con otras obras de Lorca y del teatro español, además de describir cómo los elementos escénicos (como el uso del espacio, la iluminación y el vestuario) contribuyen a la comprensión del conflicto central y la expresión de problemas humanos en la obra."}', '{"evaluacion_modelo": {"Análisis de elementos escénicos": "Excelente", "Capacidad crítica y argumentativa": "Excelente", "Justificación de relaciones intertextuales": "Satisfactorio"}, "justificacion_modelo": "El ensayo presenta un análisis profundo y bien fundamentado de los elementos escénicos, con ejemplos claros que enriquecen la comprensión del conflicto de la obra. Sin embargo, la identificación y explicación de las relaciones intertextuales son más superficiales y poco desarrolladas. La argumentación general es coherente y sólida, mostrando capacidad crítica adecuada.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Evaluación crítica","Obras dramáticas",Intertextualidad,"Elementos escénicos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (203, 47, 'criteria_evaluation', 'all', '{"rubrica": {"Coherencia y profundidad argumentativa en el ensayo crítico": {"Excelente": "Presenta argumentos claros, coherentes y bien estructurados que integran las relaciones intertextuales y elementos escénicos de forma crítica.", "Satisfactorio": "Argumenta de manera clara pero con poca profundidad o cohesión en la integración de los elementos.", "Necesita mejorar": "Los argumentos son poco claros, inconexos o superficiales, dificultando la comprensión del análisis."}, "Justificación de la importancia de las relaciones intertextuales": {"Excelente": "Identifica y explica claramente múltiples relaciones intertextuales relevantes, demostrando comprensión profunda de su aporte a la obra.", "Satisfactorio": "Reconoce algunas relaciones intertextuales relevantes y explica su aporte de manera general.", "Necesita mejorar": "Muestra dificultad para identificar relaciones intertextuales o explica su aporte de forma superficial o confusa."}, "Análisis de los elementos escénicos en la comprensión de la obra": {"Excelente": "Describe detalladamente los elementos escénicos y justifica con ejemplos precisos cómo enriquecen la comprensión del conflicto y los personajes.", "Satisfactorio": "Describe algunos elementos escénicos y explica de forma general su contribución a la comprensión de la obra.", "Necesita mejorar": "Presenta un análisis limitado o poco claro de los elementos escénicos y su relación con la obra."}}, "explicacion": "Se espera que el estudiante demuestre capacidad evaluativa al valorar con argumentos la integración de relaciones intertextuales y elementos escénicos en la comprensión de la obra, justificando con ejemplos concretos y manteniendo coherencia en su análisis crítico.", "instruccion": "Evalúa el ensayo crítico basado en el análisis de la obra dramática siguiendo la rúbrica y justifica tu calificación en cada criterio.", "caso_a_evaluar": "Lee la obra dramática \"La Casa de Bernarda Alba\" de Federico García Lorca y analiza un ensayo crítico que relaciona sus elementos escénicos y las relaciones intertextuales con otras obras del realismo y el simbolismo teatral. Evalúa cómo estos elementos contribuyen a una comprensión profunda del conflicto central y de los personajes principales."}', '{"evaluacion_modelo": {"Coherencia y profundidad argumentativa en el ensayo crítico": "Excelente", "Justificación de la importancia de las relaciones intertextuales": "Excelente", "Análisis de los elementos escénicos en la comprensión de la obra": "Satisfactorio"}, "justificacion_modelo": "La evaluación reconoce que el ensayo identifica claramente y explica en profundidad las relaciones intertextuales, y presenta argumentos coherentes y bien estructurados. Sin embargo, el análisis de los elementos escénicos es general y podría profundizarse más, por lo que se califica como satisfactorio.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Evaluación Crítica","Obras Dramáticas",Intertextualidad,"Elementos Escénicos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (204, 47, 'open_ended', 'all', '{"pregunta": "Analiza una obra dramática que hayas leído o visto y justifica cómo las relaciones intertextuales y los elementos escénicos contribuyen a una comprensión más profunda del conflicto y los personajes principales. ¿Por qué consideras que estos elementos son fundamentales para interpretar el problema humano que se expresa en la obra?", "explicacion": "El docente debe evaluar que el estudiante no solo identifique las relaciones intertextuales y elementos escénicos, sino que también explique cómo estos enriquecen la comprensión del conflicto y los personajes, vinculándolo con un problema humano subyacente. Se valorará la profundidad del análisis y la capacidad crítica.", "contexto_adicional": "Se recomienda que el estudiante utilice ejemplos concretos de la obra escogida para sustentar sus argumentos.", "criterios_evaluacion": ["Claridad y coherencia en la argumentación sobre la importancia de las relaciones intertextuales (peso: 30%)", "Análisis detallado de los elementos escénicos y su aporte a la comprensión del conflicto y personajes (peso: 40%)", "Capacidad para relacionar el conflicto dramático con un problema humano y justificar la relevancia de los elementos estudiados (peso: 30%)"]}', '{"puntos_clave": ["Identificación clara de relaciones intertextuales relevantes para la obra.", "Descripción y análisis de elementos escénicos concretos y su función.", "Vinculación del conflicto dramático con un problema humano y justificación de la importancia de los elementos mencionados."], "respuesta_modelo": "En la obra \"La Casa de Bernarda Alba\" de Federico García Lorca, las relaciones intertextuales con el contexto social y otras obras de Lorca permiten entender la opresión y el encierro de las mujeres como un problema humano fundamental. Además, los elementos escénicos como la disposición del espacio, el uso del color negro en el vestuario, y el silencio prolongado intensifican la atmósfera de represión y tensión entre personajes. Estos elementos juntos permiten una comprensión más profunda del conflicto entre la libertad individual y la autoridad familiar, mostrando cómo el drama trasciende la historia para reflejar problemáticas sociales universales.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Evaluación crítica","Obra dramática",Intertextualidad,"Elementos escénicos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (205, 47, 'open_ended', 'all', '{"pregunta": "Considerando una obra dramática que hayas leído o visto recientemente, analiza cómo las relaciones intertextuales y los elementos escénicos contribuyen a una comprensión más profunda del conflicto y los personajes principales. Justifica por qué estos aspectos son fundamentales para interpretar el mensaje humano que la obra transmite.", "explicacion": "El docente debe evaluar que el estudiante sea capaz de identificar y analizar las relaciones intertextuales (referencias a otras obras, géneros, contextos) y elementos escénicos (uso del espacio, vestuario, iluminación, sonido) y que pueda argumentar cómo estos contribuyen a la comprensión del conflicto y los personajes. Se valorará la profundidad del análisis, la coherencia en la argumentación y la capacidad crítica.", "contexto_adicional": "El estudiante puede basarse en cualquier obra dramática abordada durante el curso, considerando tanto la lectura como la puesta en escena.", "criterios_evaluacion": ["Claridad y profundidad del análisis sobre las relaciones intertextuales y su impacto en la comprensión de la obra (peso: 30%)", "Evaluación crítica del uso de elementos escénicos y cómo estos enriquecen la interpretación del conflicto y los personajes (peso: 40%)", "Capacidad para justificar con argumentos coherentes la importancia de ambos aspectos en la interpretación global de la obra (peso: 30%)"]}', '{"puntos_clave": ["Identificación de relaciones intertextuales relevantes y su impacto en la comprensión del conflicto", "Análisis crítico de elementos escénicos y su función en la caracterización y atmósfera", "Justificación argumentada sobre la importancia de estos aspectos para la interpretación global de la obra"], "respuesta_modelo": "En la obra ''La casa de Bernarda Alba'' de Federico García Lorca, las relaciones intertextuales con la tragedia clásica y el contexto social de la España rural profundizan el conflicto central sobre la opresión y la autoridad femenina. Estas referencias permiten entender el problema humano de la represión y la lucha por la libertad. Además, los elementos escénicos, como el uso del espacio cerrado y oscuro, el vestuario negro de luto y la iluminación tenue, intensifican la atmósfera de claustrofobia y tensión entre los personajes, especialmente entre Bernarda y sus hijas. Estos aspectos escénicos no solo refuerzan el conflicto sino que también revelan la psicología de los personajes, mostrando sus deseos reprimidos y conflictos internos. Por lo tanto, tanto las relaciones intertextuales como los elementos escénicos son fundamentales para interpretar el mensaje humano de la obra: la crítica a la opresión social y la expresión del sufrimiento humano.", "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,obra_dramática,análisis_crítico,relaciones_intertextuales,elementos_escénicos}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (206, 48, 'open_ended', 'all', '{"pregunta": "Diseña una propuesta creativa para representar una escena clave de una obra dramática que hayas leído o visto, incorporando un análisis detallado de los personajes principales, los símbolos presentes, los elementos escénicos y las posibles relaciones intertextuales con otras obras o contextos. Explica cómo cada uno de estos componentes contribuye a la interpretación del conflicto y al problema humano que se expresa en la obra.", "explicacion": "La evaluación se centrará en la capacidad del estudiante para combinar análisis literario con creatividad en la propuesta, demostrando comprensión profunda de la obra y habilidad para comunicar cómo los diferentes elementos dramáticos contribuyen al significado global.", "contexto_adicional": "El estudiante puede elegir cualquier obra dramática estudiada en el curso y debe justificar sus elecciones creativas y analíticas en la propuesta.", "criterios_evaluacion": ["Análisis profundo y coherente de los personajes principales y su relación con el conflicto (peso: 30%)", "Integración creativa y pertinente de símbolos, elementos escénicos y relaciones intertextuales en la propuesta (peso: 40%)", "Claridad y coherencia en la explicación de cómo estos elementos enriquecen la interpretación del problema humano en la obra (peso: 30%)"]}', '{"puntos_clave": ["Análisis claro y detallado de los personajes principales y su conflicto", "Uso creativo y simbólico de elementos escénicos y objetos simbólicos", "Identificación y explicación de relaciones intertextuales relevantes", "Conexión explícita entre los elementos analizados y la interpretación del problema humano"], "respuesta_modelo": "Para representar la escena del enfrentamiento final en la obra ''La casa de Bernarda Alba'' de Federico García Lorca, propongo una puesta en escena que enfatice el conflicto entre Bernarda y sus hijas a través del uso de colores fríos y cálidos para simbolizar la opresión y la rebeldía respectivamente. Los personajes principales, Bernarda y Adela, serán representados con contrastes marcados en vestuario y gestualidad para reflejar sus posiciones en el conflicto. Incorporaré un abanico como símbolo recurrente que representa el control y la represión. Los elementos escénicos incluirán una casa cerrada y rígida que sugiere el encierro físico y emocional. Las relaciones intertextuales se establecerán con la tragedia griega, especialmente con ''Antígona'', reflejando el tema del choque generacional y la lucha por la libertad. Esta propuesta busca destacar cómo el conflicto refleja la lucha por la autonomía y la expresión individual frente a una autoridad opresora, un problema humano universal y vigente.", "requiere_revision_humana": true}', 4, 0, true, '{drama,"análisis literario",creatividad,"primero medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (207, 48, 'open_ended', 'all', '{"pregunta": "Diseña una propuesta creativa para representar una obra dramática que hayas leído o visto, integrando un análisis profundo de los personajes principales, los símbolos presentes, los elementos escénicos y las relaciones intertextuales. Explica cómo cada uno de estos componentes contribuye a la interpretación del conflicto y al mensaje humano que la obra transmite.", "explicacion": "Se evaluará la capacidad del estudiante para analizar en profundidad los personajes y cómo sus características afectan el conflicto. Además, se valorará la habilidad para identificar y usar símbolos, elementos escénicos y referencias intertextuales que aporten a la interpretación. Finalmente, se considerará la creatividad y coherencia general de la propuesta para comunicar el problema humano central en la obra.", "contexto_adicional": "El estudiante debe seleccionar una obra dramática estudiada en el curso y basar su propuesta en evidencias textuales y visuales, proponiendo una representación innovadora que refleje su comprensión crítica.", "criterios_evaluacion": ["Análisis de personajes: claridad y profundidad en la caracterización y motivaciones (peso: 30%)", "Integración creativa de símbolos, elementos escénicos y relaciones intertextuales para enriquecer la interpretación (peso: 40%)", "Coherencia y originalidad en la propuesta, vinculando todos los elementos para interpretar el conflicto y el mensaje humano de la obra (peso: 30%)"]}', '{"puntos_clave": ["Análisis detallado de los personajes y sus motivaciones", "Uso de símbolos y elementos escénicos para representar el conflicto", "Incorporación de relaciones intertextuales para profundizar en la interpretación", "Coherencia y creatividad en la propuesta final"], "respuesta_modelo": "Propongo una representación teatral de \"La casa de Bernarda Alba\" que enfatiza el simbolismo del color negro y el espacio cerrado como elementos que reflejan la opresión y el conflicto entre libertad y control. Analizo a Bernarda como personaje principal, cuya rigidez y autoritarismo generan el conflicto familiar y social. Incorporo elementos escénicos como paredes móviles y luces que cambian para representar el encierro y las emociones reprimidas. Además, relaciono la obra con otras tragedias clásicas que abordan temas de poder y represión, para enriquecer la interpretación del mensaje sobre la lucha individual contra las imposiciones sociales.", "requiere_revision_humana": true}', 5, 0, true, '{creación,"análisis dramático","propuesta creativa",primeromedia}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (208, 48, 'concept_map', 'all', '{"conceptos": ["Conflicto", "Personajes principales", "Símbolos", "Elementos escénicos", "Relaciones intertextuales"], "explicacion": "El estudiante debe mostrar cómo el conflicto genera el problema humano expresado en la obra, cómo los personajes principales están involucrados en este conflicto, y cómo los símbolos y elementos escénicos refuerzan la interpretación. Además, debe integrar cómo las relaciones intertextuales enriquecen la comprensión y ofrecen nuevas perspectivas sobre la obra.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis e interpretación de una obra dramática?"}', '{"conceptos_centrales": ["Conflicto", "Personajes principales"], "relaciones_esperadas": [{"origen": "Conflicto", "destino": "Personajes principales", "relacion": "afecta a"}, {"origen": "Símbolos", "destino": "Conflicto", "relacion": "representan"}, {"origen": "Elementos escénicos", "destino": "Símbolos", "relacion": "refuerzan"}, {"origen": "Relaciones intertextuales", "destino": "Conflicto", "relacion": "enriquecen la comprensión de"}, {"origen": "Personajes principales", "destino": "Relaciones intertextuales", "relacion": "se vinculan con"}], "requiere_revision_humana": true}', 4, 0, true, '{lengua_y_literatura,obra_dramatica,mapa_conceptual,analisis,crear}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (209, 48, 'concept_map', 'all', '{"conceptos": ["Conflicto dramático", "Personajes principales", "Símbolos", "Elementos escénicos", "Relaciones intertextuales"], "explicacion": "El mapa conceptual debe mostrar cómo el conflicto dramático actúa como motor de la trama y refleja problemas humanos, cómo los personajes principales están involucrados y desarrollan ese conflicto, cómo los símbolos aportan significado adicional y se conectan con las emociones y temáticas, cómo los elementos escénicos refuerzan la atmósfera y la interpretación, y cómo las relaciones intertextuales enriquecen la comprensión al vincular la obra con otras referencias literarias o culturales.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis e interpretación de una obra dramática?"}', '{"conceptos_centrales": ["Conflicto dramático", "Personajes principales"], "relaciones_esperadas": [{"origen": "Conflicto dramático", "destino": "Personajes principales", "relacion": "es vivido por"}, {"origen": "Símbolos", "destino": "Conflicto dramático", "relacion": "representan aspectos de"}, {"origen": "Elementos escénicos", "destino": "Símbolos", "relacion": "refuerzan"}, {"origen": "Relaciones intertextuales", "destino": "Conflicto dramático", "relacion": "enriquecen la comprensión de"}, {"origen": "Personajes principales", "destino": "Elementos escénicos", "relacion": "se expresan a través de"}], "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,análisis_dramático,mapa_conceptual,primero_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (210, 48, 'open_ended', 'all', '{"pregunta": "Diseña una propuesta creativa para representar una obra dramática que hayas leído o visto, integrando un análisis profundo de los personajes principales, los símbolos presentes en la obra, los elementos escénicos que utilizarías y las relaciones intertextuales que consideres relevantes para enriquecer la interpretación. Describe cómo cada uno de estos aspectos contribuye a expresar el conflicto y el problema humano central de la obra.", "explicacion": "La evaluación se centrará en la capacidad del estudiante para crear una propuesta integral que combine análisis literario y creatividad escénica, demostrando comprensión profunda del texto y su significado humano y simbólico.", "contexto_adicional": "El estudiante puede escoger cualquier obra dramática vista o leída en el curso y debe justificar sus elecciones con base en el análisis crítico del texto.", "criterios_evaluacion": ["Análisis detallado y coherente de los personajes principales y su desarrollo en la obra (peso: 30%)", "Integración creativa y pertinente de símbolos y elementos escénicos para apoyar la interpretación del conflicto (peso: 40%)", "Incorporación y explicación clara de las relaciones intertextuales que enriquecen la propuesta (peso: 30%)"]}', '{"puntos_clave": ["Análisis de personajes con enfoque en sus conflictos internos y roles", "Símbolos específicos vinculados al tema central", "Elementos escénicos que reflejan el conflicto y el ambiente", "Relaciones intertextuales que amplían la comprensión de la obra", "Conexión clara entre elementos y el problema humano expresado"], "respuesta_modelo": "Para representar la obra \"La Casa de Bernarda Alba\" de Federico García Lorca, diseñaría una propuesta que enfatice el conflicto entre la represión social y los deseos individuales. Los personajes principales, especialmente Bernarda y sus hijas, serían analizados en función de sus roles en perpetuar y desafiar las normas sociales. Utilizaría el símbolo del color blanco para representar la opresión y el luto, y elementos escénicos como puertas cerradas y espacios reducidos para reflejar el encierro emocional y físico. Además, incorporaría relaciones intertextuales con otras obras de Lorca y con tragedias clásicas como \"Antígona\" para profundizar en el tema del conflicto entre autoridad y libertad. Cada elemento refuerza la tensión central y el problema humano de la lucha por la autonomía frente a la opresión.", "requiere_revision_humana": true}', 5, 0, true, '{"análisis dramático",creatividad,"interpretación teatral","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (211, 49, 'multiple_choice', 'all', '{"opciones": {"A": "Presentan un final feliz donde todos los personajes logran sus objetivos.", "B": "Muestran el conflicto entre el destino y la voluntad humana, con un desenlace fatal.", "C": "Se centran en la comedia de enredos y situaciones humorísticas.", "D": "Son relatos cortos que no incluyen personajes complejos ni conflictos profundos."}, "pregunta": "¿Cuál de las siguientes características es propia de las tragedias clásicas que se suelen leer en Primero Medio, considerando su contexto histórico?", "explicacion": "La respuesta correcta es la opción B, ya que las tragedias clásicas suelen presentar un conflicto entre el destino inevitable y las acciones de los personajes, llevando a un desenlace fatal. Esta característica refleja la visión del mundo de la época en que fueron escritas, donde el destino y la fatalidad eran temas centrales. La opción A es incorrecta porque las tragedias no tienen finales felices, sino trágicos. La opción C describe características propias de la comedia, no de la tragedia. La opción D es errónea porque las tragedias suelen incluir personajes complejos y conflictos profundos."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{tragedia,características,"contexto histórico","primer año medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (212, 49, 'multiple_choice', 'all', '{"opciones": {"A": "Un final feliz donde los personajes principales solucionan todos sus problemas.", "B": "La presencia de un héroe trágico que enfrenta un destino inevitable marcado por sus propias decisiones.", "C": "La representación de situaciones cómicas para entretener al público sin consecuencias graves.", "D": "La ausencia de conflictos internos en los personajes principales."}, "pregunta": "¿Cuál de las siguientes características es propia de una tragedia clásica y refleja su contexto histórico?", "explicacion": "La respuesta correcta es la opción B, ya que una característica esencial de la tragedia clásica es la figura del héroe trágico, quien se enfrenta a un destino inevitable producto tanto de sus acciones como del contexto histórico, reflejando la visión de mundo de la época. La opción A es incorrecta porque las tragedias clásicas no terminan con un final feliz; justamente, el desenlace trágico es central. La opción C describe características de la comedia, no de la tragedia. La opción D es incorrecta porque en las tragedias los conflictos internos son fundamentales para el desarrollo dramático."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{tragedia,características,"contexto histórico","primer medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (213, 49, 'multiple_choice', 'all', '{"opciones": {"A": "La presencia de un héroe con un defecto fatal que conduce a su caída", "B": "El uso de finales felices donde todos los personajes solucionan sus problemas", "C": "La ausencia de conflicto entre personajes, promoviendo la armonía social", "D": "El enfoque exclusivo en la comedia y el humor para entretener al público"}, "pregunta": "¿Cuál de las siguientes características es fundamental en las tragedias clásicas y refleja la visión de mundo de su contexto histórico?", "explicacion": "La opción A es correcta porque las tragedias clásicas se centran en un héroe trágico que posee un defecto fatal (hamartia) que lo lleva a su caída, reflejando la visión de mundo sobre el destino y la condición humana en su contexto histórico. La opción B es incorrecta, ya que las tragedias no presentan finales felices sino trágicos. La opción C es errónea porque los conflictos son esenciales en las tragedias para desarrollar la trama y las tensiones dramáticas. La opción D es un error conceptual, pues se confunde la tragedia con la comedia, que sí se enfoca en el humor y la diversión."}', '{"respuesta_correcta": "A"}', 3, 0, true, '{tragedia,características,"contexto histórico","primer medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (214, 49, 'true_false', 'all', '{"afirmacion": "Las tragedias clásicas suelen representar conflictos entre fuerzas opuestas y suelen estar ambientadas en contextos históricos que reflejan valores y creencias de la sociedad de su época.", "explicacion": "Esta afirmación es verdadera porque las tragedias clásicas, como las griegas, están diseñadas para mostrar conflictos profundos entre personajes o fuerzas contrapuestas, y su ambientación histórica refleja las creencias, valores y tensiones sociales del momento en que fueron escritas."}', '{"es_verdadero": true}', 1, 0, true, '{tragedia,"contexto histórico",características}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (215, 49, 'fill_blanks', 'all', '{"texto": "Las tragedias clásicas suelen presentar un protagonista con una falla trágica llamada [BLANK_1], y su desarrollo está influenciado por el contexto histórico de la [BLANK_2], periodo en el que se buscaba explicar las acciones humanas mediante el destino y la moralidad.", "pistas": ["Es un defecto o error en el carácter del protagonista que conduce a su caída.", "Época histórica de la antigua Grecia donde se originaron muchas tragedias clásicas."], "explicacion": "En las tragedias clásicas, el protagonista posee una falla trágica o ''hamartia'' que desencadena su destino fatal. Estas obras se enmarcan en la antigua Grecia, un contexto histórico donde se exploraban temas como el destino, la moralidad y la condición humana."}', '{"respuestas_correctas": {"BLANK_1": ["hamartia", "falla trágica"], "BLANK_2": ["Grecia", "antigua Grecia"]}}', 2, 0, true, '{tragedia,"contexto histórico","primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (216, 50, 'multiple_choice', 'all', '{"opciones": {"A": "Una visión optimista que muestra cómo la justicia siempre prevalece y los personajes logran la felicidad.", "B": "Una visión que enfatiza la inevitabilidad del destino y las consecuencias trágicas de las acciones humanas.", "C": "Una visión que promueve la indiferencia ante el sufrimiento humano y la falta de responsabilidad personal.", "D": "Una visión que se centra exclusivamente en la crítica social sin considerar el destino o la moralidad."}, "pregunta": "¿Cuál es la visión de mundo que generalmente transmiten las tragedias clásicas, considerando sus características y contexto?", "explicacion": "La respuesta correcta es la opción B, porque las tragedias clásicas suelen presentar una visión de mundo en la que el destino es ineludible y las acciones de los personajes conducen a consecuencias trágicas inevitables, reflejando la fragilidad humana y los conflictos morales. La opción A es incorrecta porque las tragedias no suelen terminar en felicidad ni justicia completa. La opción C es un error conceptual al pensar que las tragedias promueven la indiferencia, cuando en realidad buscan generar reflexión sobre el sufrimiento y la responsabilidad. La opción D confunde las tragedias con otros géneros literarios que podrían centrarse en crítica social, pero las tragedias clásicas integran destino y moralidad en su estructura."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{tragedia,"visión de mundo","literatura clásica","Primer Medio",comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (217, 50, 'multiple_choice', 'all', '{"opciones": {"A": "Una visión optimista donde los personajes siempre superan sus conflictos gracias a la ayuda divina.", "B": "Una visión pesimista que muestra la inevitabilidad del destino y las consecuencias trágicas de las acciones humanas.", "C": "Una visión neutral que evita mostrar juicios morales sobre los personajes y sus decisiones.", "D": "Una visión centrada en la comedia y el humor para criticar la sociedad sin dramatismo."}, "pregunta": "¿Cuál es la visión de mundo que suele reflejar una tragedia clásica, considerando sus características y contexto?", "explicacion": "La respuesta correcta es B porque las tragedias clásicas suelen explorar la inevitabilidad del destino y las consecuencias trágicas que resultan de las decisiones humanas, reflejando una visión pesimista sobre la condición humana y el orden del mundo. La opción A es incorrecta porque, aunque puede haber intervención divina, no siempre conduce a un resultado positivo. La opción C es errónea porque las tragedias generalmente implican juicios morales y muestran consecuencias graves. La opción D confunde la tragedia con la comedia, que son géneros distintos con objetivos diferentes."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{tragedia,"visión de mundo","contexto histórico","Lengua y Literatura","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (218, 50, 'true_false', 'all', '{"afirmacion": "Las tragedias clásicas reflejan una visión de mundo donde el destino y la fatalidad juegan un papel central en el desarrollo de la historia.", "explicacion": "Esta afirmación es verdadera porque en las tragedias clásicas, como las de Sófocles o Eurípides, el destino y la fatalidad son fuerzas inevitables que condicionan las acciones y el desenlace de los personajes, reflejando una visión del mundo donde el ser humano está sujeto a fuerzas superiores."}', '{"es_verdadero": true}', 2, 0, true, '{tragedia,"visión de mundo",contexto}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (219, 50, 'drag_drop_matching', 'all', '{"explicacion": "Hamartia es el error trágico del protagonista que causa su caída; Catarsis es la purificación emocional del público; Destino inevitable es la fuerza que marca el desenlace; Contexto histórico es el marco donde se sitúa la tragedia; Tragedia griega es el género con conflictos fatales; Comedia es un distractor con final feliz.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente relacionado con las tragedias clásicas.", "columna_derecha": ["Fallo trágico o error del protagonista que conduce a su caída", "Proceso de purificación emocional que experimenta el espectador", "Fuerza que determina el desenlace independientemente de la voluntad humana", "Época y circunstancias sociales y políticas en que se desarrolla la obra", "Género teatral que presenta conflictos humanos con desenlaces fatales", "Género teatral con final feliz y tono humorístico"], "columna_izquierda": ["Hamartia", "Catarsis", "Destino inevitable", "Contexto histórico", "Tragedia griega", "Distractor: Comedia"]}', '{"emparejamientos_correctos": {"Catarsis": "Proceso de purificación emocional que experimenta el espectador", "Hamartia": "Fallo trágico o error del protagonista que conduce a su caída", "Tragedia griega": "Género teatral que presenta conflictos humanos con desenlaces fatales", "Destino inevitable": "Fuerza que determina el desenlace independientemente de la voluntad humana", "Contexto histórico": "Época y circunstancias sociales y políticas en que se desarrolla la obra"}}', 2, 0, true, '{"Lengua y Literatura",Tragedia,Comprensión,"Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (220, 50, 'sequencing', 'all', '{"explicacion": "El orden comienza con la lectura y comprensión de la obra para tener una base clara. Luego, se identifican las características principales de la tragedia, seguidas de su contextualización histórica y social. A partir de estas dos etapas, se puede explicar cómo estas características reflejan la visión de mundo que transmite la obra. Finalmente, se discuten las interpretaciones en grupo para enriquecer la comprensión.", "instruccion": "Ordena los siguientes pasos para analizar la visión de mundo en una tragedia", "elementos_desordenados": ["Identificar las características principales de la tragedia", "Contextualizar la obra en su época histórica y social", "Explicar cómo estas características reflejan la visión de mundo", "Leer y comprender el texto de la tragedia", "Discutir en grupo las interpretaciones posibles"]}', '{"orden_correcto": ["Leer y comprender el texto de la tragedia", "Identificar las características principales de la tragedia", "Contextualizar la obra en su época histórica y social", "Explicar cómo estas características reflejan la visión de mundo", "Discutir en grupo las interpretaciones posibles"]}', 3, 0, true, '{comprensión,tragedia,"visión de mundo",secuencia}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (221, 51, 'compare_contrast', 'all', '{"conceptos": ["Tragedia Griega", "Tragedia Moderna"], "criterios": ["Visión de mundo", "Contexto histórico", "Estructura dramática", "Temas principales"], "explicacion": "Las tragedias griegas presentan una visión de mundo marcada por el destino y la intervención divina, en un contexto de sociedades antiguas con valores religiosos fuertes. Su estructura dramática sigue un esquema rígido con coro y actos limitados, y abordan temas como el orgullo (hybris) y la justicia divina. Por otro lado, las tragedias modernas reflejan una visión más humanista y existencial, en contextos históricos variados y complejos, con estructuras más flexibles y temas como el conflicto interno, la alienación y la crítica social.", "instruccion": "Compara y contrasta las tragedias griegas y las tragedias modernas según los criterios dados"}', '{"tabla_correcta": {"Tragedia Griega": {"Visión de mundo": "Determinismo y destino marcado por dioses", "Temas principales": "Orgullo (hybris), justicia divina, destino inevitable", "Contexto histórico": "Sociedad antigua, fuerte influencia religiosa", "Estructura dramática": "Esquema rígido con coro y actos limitados"}, "Tragedia Moderna": {"Visión de mundo": "Humanismo y existencialismo, foco en el individuo", "Temas principales": "Conflicto interno, alienación, crítica social", "Contexto histórico": "Sociedades diversas con cambios sociales y políticos", "Estructura dramática": "Estructura flexible, sin coro, actos variables"}}}', 3, 0, true, '{tragedia,comparación,"visión de mundo","contexto histórico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (316, 70, 'open_ended', 'all', '{"pregunta": "Analiza un artículo de opinión publicado en un diario digital. Identifica y evalúa las estrategias de persuasión utilizadas por el autor y el papel que juegan los recursos no lingüísticos (como imágenes o diseño) en la construcción del mensaje. ¿Consideras que estos elementos contribuyen efectivamente al propósito del texto? Justifica tu respuesta.", "explicacion": "El docente debe valorar la capacidad del estudiante para distinguir entre diferentes estrategias persuasivas, reconocer y analizar el aporte de recursos no lingüísticos y emitir un juicio fundamentado que considere la intención comunicativa del texto.", "contexto_adicional": "Se sugiere que el estudiante seleccione un artículo de opinión actual de un medio digital chileno o internacional, que incluya imágenes o diseño gráfico relevante.", "criterios_evaluacion": ["Identificación clara y precisa de las estrategias de persuasión presentes en el texto (peso: 30%)", "Análisis crítico del uso y efecto de los recursos no lingüísticos en el mensaje (peso: 40%)", "Justificación argumentada sobre la efectividad global de los elementos persuasivos en relación con el propósito del texto (peso: 30%)"]}', '{"puntos_clave": ["Identificación de estrategias persuasivas (apelar a la emoción, uso de datos, etc.)", "Reconocimiento y análisis del impacto de imágenes y diseño en el mensaje", "Evaluación fundamentada sobre la efectividad de esos elementos en relación con el propósito del texto"], "respuesta_modelo": "En el artículo de opinión seleccionado, el autor utiliza principalmente la apelación emocional y el uso de datos estadísticos para persuadir al lector. Por ejemplo, emplea un lenguaje cargado de adjetivos valorativos para generar empatía y preocupación. Los recursos no lingüísticos, como una imagen impactante que acompaña el texto, refuerzan esta apelación emocional al mostrar el problema tratado de manera visual. El diseño claro y el uso de colores llamativos dirigen la atención hacia los puntos clave del texto. Considero que estos elementos contribuyen efectivamente al propósito del artículo, que es sensibilizar y convencer al público sobre la urgencia del tema. La combinación de estrategias lingüísticas y visuales crea un mensaje coherente y persuasivo que cumple con la intención comunicativa.", "requiere_revision_humana": true}', 4, 0, true, '{"evaluación crítica","estrategias persuasivas","recursos no lingüísticos","artículo de opinión"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (222, 51, 'compare_contrast', 'all', '{"conceptos": ["Tragedia griega", "Tragedia moderna"], "criterios": ["Visión de mundo", "Contexto histórico", "Características dramáticas"], "explicacion": "Las tragedias griegas presentan una visión de mundo centrada en el destino y la intervención divina, en un contexto histórico de la antigua Grecia, con características como el coro y la estructura formal rígida. Las tragedias modernas, en cambio, reflejan una visión más humanista y existencial, ubicándose en contextos históricos variados y contemporáneos, con características dramáticas que pueden incluir mayor complejidad psicológica y libertad estructural.", "instruccion": "Compara y contrasta las tragedias griegas y las tragedias modernas según los criterios dados"}', '{"tabla_correcta": {"Tragedia griega": {"Visión de mundo": "Destino y voluntad de los dioses", "Contexto histórico": "Antigua Grecia, siglo V a.C.", "Características dramáticas": "Uso del coro, estructura formal, tragedia como enseñanza moral"}, "Tragedia moderna": {"Visión de mundo": "Humanismo, existencialismo y conflicto interno", "Contexto histórico": "Siglo XIX y XX, sociedades contemporáneas", "Características dramáticas": "Mayor complejidad psicológica, libertad estructural, ausencia de coro"}}}', 4, 0, true, '{tragedia,comparación,"análisis literario"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (223, 51, 'open_ended', 'all', '{"pregunta": "Analiza dos tragedias leídas en clase y compara sus visiones del mundo. ¿Cuáles son las similitudes y diferencias en cómo cada obra refleja el contexto histórico y las características propias del género trágico? Justifica tu respuesta con ejemplos específicos de cada texto.", "explicacion": "El docente debe evaluar que el alumno sea capaz de identificar y analizar la visión del mundo en cada tragedia, comparándolas críticamente y sustentando su respuesta con evidencias textuales claras. Se valorará la profundidad del análisis y la capacidad para relacionar contexto y contenido.", "contexto_adicional": "Se espera que el estudiante relacione elementos históricos y culturales que influyen en la construcción de la tragedia, así como las características propias del género como el destino, el sufrimiento y la catarsis.", "criterios_evaluacion": ["Comprensión y análisis de las visiones del mundo presentes en cada tragedia (peso: 40%)", "Identificación clara y precisa de similitudes y diferencias entre las dos obras (peso: 35%)", "Uso de ejemplos textuales relevantes para sustentar el análisis (peso: 25%)"]}', '{"puntos_clave": ["Visión del mundo centrada en el destino inevitable versus la responsabilidad personal", "Contexto histórico que influye en la construcción de la tragedia (Grecia antigua vs Renacimiento europeo)", "Características del género trágico presentes en ambas obras (sufrimiento, caída del héroe)"], "respuesta_modelo": "En la tragedia ''Edipo Rey'' de Sófocles, la visión del mundo se centra en la inevitabilidad del destino y la limitación del conocimiento humano, reflejando un contexto griego donde los dioses y el destino son fuerzas supremas. Por otro lado, en ''Macbeth'' de Shakespeare, la tragedia refleja una visión más centrada en la ambición humana y las consecuencias del poder mal utilizado, en un contexto renacentista europeo con fuerte influencia de la moral cristiana. Ambas obras comparten la característica trágica del sufrimiento inevitable y la caída del protagonista, pero difieren en su enfoque: el destino inmutable versus la responsabilidad moral personal. Por ejemplo, Edipo intenta evitar su destino pero termina cumpliéndolo, mientras que Macbeth elige sus acciones que lo conducen a su destrucción. Estas diferencias reflejan cómo cada tragedia expresa su visión del mundo y su contexto histórico.", "requiere_revision_humana": true}', 4, 0, true, '{tragedia,comparación,"análisis literario"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (224, 51, 'concept_map', 'all', '{"conceptos": ["Tragedia", "Visión de mundo", "Contexto histórico", "Personajes", "Conflicto"], "explicacion": "Se espera que el estudiante conecte la ''Tragedia'' con su ''Visión de mundo'', la cual está influenciada por el ''Contexto histórico''. Los ''Personajes'' son agentes que representan y viven el ''Conflicto'' central, el cual refleja la visión y el contexto. Además, deben identificar cómo estos elementos interactúan para construir el significado de la tragedia.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de las tragedias literarias?"}', '{"conceptos_centrales": ["Tragedia", "Visión de mundo"], "relaciones_esperadas": [{"origen": "Tragedia", "destino": "Visión de mundo", "relacion": "expresa"}, {"origen": "Visión de mundo", "destino": "Contexto histórico", "relacion": "está influenciada por"}, {"origen": "Personajes", "destino": "Conflicto", "relacion": "viven"}, {"origen": "Conflicto", "destino": "Visión de mundo", "relacion": "refleja"}, {"origen": "Tragedia", "destino": "Personajes", "relacion": "incluye"}], "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura",Tragedia,"Mapa Conceptual",Análisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (225, 51, 'concept_map', 'all', '{"conceptos": ["Tragedia", "Visión de mundo", "Contexto histórico", "Personajes trágicos", "Conflicto", "Destino", "Catarsis"], "explicacion": "Se espera que el estudiante conecte la ''Tragedia'' con su ''Visión de mundo'' y el ''Contexto histórico'' en que se desarrolla. Debe mostrar cómo los ''Personajes trágicos'' enfrentan un ''Conflicto'' influenciado por su ''Destino'', lo que genera una ''Catarsis'' en el espectador o lector. Estas relaciones permiten analizar las similitudes y diferencias en las tragedias leídas.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de las tragedias literarias?"}', '{"conceptos_centrales": ["Tragedia", "Visión de mundo"], "relaciones_esperadas": [{"origen": "Tragedia", "destino": "Visión de mundo", "relacion": "expresa"}, {"origen": "Tragedia", "destino": "Contexto histórico", "relacion": "se desarrolla en"}, {"origen": "Personajes trágicos", "destino": "Conflicto", "relacion": "enfrentan"}, {"origen": "Conflicto", "destino": "Destino", "relacion": "está influenciado por"}, {"origen": "Conflicto", "destino": "Catarsis", "relacion": "provoca"}, {"origen": "Tragedia", "destino": "Personajes trágicos", "relacion": "incluye"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",Tragedia,"Mapa conceptual",Análisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (226, 52, 'criteria_evaluation', 'all', '{"rubrica": {"Uso de ejemplos y referencias al texto": {"Excelente": "Incorpora múltiples ejemplos concretos y citas relevantes del texto para apoyar la evaluación.", "Satisfactorio": "Incluye algunos ejemplos del texto, aunque pueden ser limitados o no del todo pertinentes.", "Necesita mejorar": "No utiliza ejemplos del texto o los usados son irrelevantes o incorrectos."}, "Justificación del impacto en la sociedad": {"Excelente": "Explica con detalle y coherencia cómo la visión de mundo de la tragedia genera un impacto significativo en la sociedad de la época, considerando personajes y situaciones.", "Satisfactorio": "Explica de forma general el impacto en la sociedad, pero con poca profundidad o ejemplos limitados.", "Necesita mejorar": "No logra justificar el impacto o la explicación es confusa y sin sustento."}, "Argumentación sobre la relevancia de la visión de mundo": {"Excelente": "Presenta una argumentación clara, profunda y bien fundamentada que conecta la visión de mundo con el contexto social de la tragedia, usando ejemplos precisos.", "Satisfactorio": "Presenta una argumentación adecuada que relaciona la visión de mundo con el contexto social, aunque con algunos aspectos poco desarrollados o generales.", "Necesita mejorar": "La argumentación es vaga, superficial o no logra relacionar la visión de mundo con el contexto social."}}, "explicacion": "Se espera que el estudiante argumente con fundamentos claros la conexión entre la visión de mundo de la tragedia y su contexto social, justifique el impacto de esa visión en la sociedad mostrada y utilice ejemplos concretos del texto para sustentar su evaluación.", "instruccion": "Evalúa la relevancia y el impacto de la visión de mundo expresada en la tragedia ''Antígona'' según los criterios de la rúbrica y justifica tu calificación.", "caso_a_evaluar": "Has leído la tragedia ''Antígona'' de Sófocles, la cual presenta una visión de mundo centrada en el conflicto entre la ley divina y la ley humana, y sus consecuencias en la sociedad de la época. Evalúa la relevancia y el impacto que tiene esta visión de mundo en el contexto social en que fue escrita, considerando sus características y cómo se refleja en la trama y en los personajes."}', '{"evaluacion_modelo": {"Uso de ejemplos y referencias al texto": "Excelente", "Justificación del impacto en la sociedad": "Satisfactorio", "Argumentación sobre la relevancia de la visión de mundo": "Excelente"}, "justificacion_modelo": "La argumentación sobre la relevancia es clara y está bien fundamentada, mostrando una comprensión profunda del contexto social y la visión de mundo. La justificación del impacto en la sociedad es adecuada pero podría profundizarse más en algunos aspectos. El uso de ejemplos es preciso y pertinente, apoyando eficazmente la evaluación.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",Tragedia,Evaluación,"Primero Medio","Visión de mundo"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (227, 52, 'criteria_evaluation', 'all', '{"rubrica": {"Coherencia y claridad en la expresión escrita": {"Excelente": "La redacción es coherente, clara y estructurada, sin errores significativos que dificulten la comprensión.", "Satisfactorio": "La redacción es generalmente clara, pero presenta algunos errores o falta de estructura que no impiden entender el argumento.", "Necesita mejorar": "La redacción es confusa, con errores frecuentes que afectan la comprensión del texto."}, "Justificación del impacto en la sociedad de la época": {"Excelente": "Justifica con claridad cómo la tragedia influía o reflejaba las normas y valores sociales, considerando múltiples perspectivas y evidencias textuales o contextuales.", "Satisfactorio": "Ofrece una justificación básica sobre el impacto social, pero con análisis limitado o poco detallado.", "Necesita mejorar": "La justificación es vaga, insuficiente o no se relaciona con el impacto social de la tragedia."}, "Argumentación sobre la relevancia social de la tragedia": {"Excelente": "Presenta una argumentación clara, profunda y bien fundamentada que conecta la visión de mundo de la tragedia con su contexto social, mostrando comprensión crítica y ejemplos precisos.", "Satisfactorio": "La argumentación es adecuada y muestra comprensión general de la relación entre la visión de mundo y el contexto social, aunque puede carecer de profundidad o ejemplos específicos.", "Necesita mejorar": "La argumentación es superficial, confusa o desconectada del contexto social y la visión de mundo expresada en la tragedia."}}, "explicacion": "Se espera que el estudiante analice críticamente la obra y su contexto, argumente con ejemplos, y exprese su evaluación con claridad y coherencia escrita. La justificación debe demostrar comprensión profunda y reflexión crítica.", "instruccion": "Evalúa la visión de mundo expresada en la tragedia ''Antígona'' utilizando la rúbrica proporcionada. Asigna un nivel para cada criterio y justifica detalladamente tu calificación.", "caso_a_evaluar": "Has leído la tragedia clásica ''Antígona'' de Sófocles, la cual aborda temas de ley, justicia y deber familiar en el contexto de la antigua Grecia. Considerando el contexto histórico y social de la obra, evalúa la relevancia e impacto de la visión de mundo que se expresa en la tragedia y cómo esta refleja o cuestiona las normas de su época."}', '{"evaluacion_modelo": {"Coherencia y claridad en la expresión escrita": "Excelente", "Justificación del impacto en la sociedad de la época": "Satisfactorio", "Argumentación sobre la relevancia social de la tragedia": "Excelente"}, "justificacion_modelo": "La argumentación muestra un análisis profundo y bien fundamentado, conectando la obra con su contexto histórico. La justificación del impacto social es adecuada pero podría incluir más perspectivas o evidencias. La expresión escrita es clara y coherente, facilitando la comprensión de los argumentos.", "requiere_revision_humana": true}', 5, 0, true, '{tragedia,"evaluación crítica","contexto social","Lengua y Literatura","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (228, 52, 'criteria_evaluation', 'all', '{"rubrica": {"Uso de evidencias del texto": {"Excelente": "Incorpora citas o referencias precisas de la tragedia que respaldan los argumentos presentados.", "Satisfactorio": "Incluye algunas referencias al texto, aunque de forma general o poco precisa.", "Necesita mejorar": "No utiliza evidencias del texto o las menciones son irrelevantes o incorrectas."}, "Relevancia e impacto en la sociedad": {"Excelente": "Explica con claridad y ejemplos cómo la visión de mundo expresada en la tragedia tiene relevancia e impacto en la sociedad tanto en su época como en la actualidad.", "Satisfactorio": "Menciona la relevancia e impacto de la visión de mundo, pero con explicaciones limitadas o poco fundamentadas.", "Necesita mejorar": "No logra explicar adecuadamente la relevancia o impacto de la visión de mundo en la sociedad."}, "Argumentación sobre la visión de mundo": {"Excelente": "Presenta una argumentación clara, profunda y coherente que conecta la visión de mundo expresada en la tragedia con su contexto social, mostrando comprensión crítica.", "Satisfactorio": "Ofrece una argumentación adecuada que relaciona la visión de mundo con el contexto social, aunque con algunas generalizaciones o falta de profundidad.", "Necesita mejorar": "La argumentación es vaga, superficial o no logra conectar la visión de mundo con el contexto social de manera coherente."}}, "explicacion": "Se espera que el estudiante demuestre capacidad crítica para evaluar la visión de mundo en la tragedia, su relación con el contexto social y su impacto, usando evidencias textuales para sustentar sus argumentos.", "instruccion": "Evalúa la calidad de la argumentación en el análisis de la tragedia según la rúbrica proporcionada y justifica tu calificación en cada criterio.", "caso_a_evaluar": "Una tragedia clásica representa la historia de un héroe que enfrenta su destino inevitable, mostrando valores y conflictos característicos de su época. Analiza cómo la visión de mundo expresada en esta tragedia refleja el contexto social de su tiempo y argumenta su relevancia e impacto en la sociedad contemporánea."}', '{"evaluacion_modelo": {"Uso de evidencias del texto": "Satisfactorio", "Relevancia e impacto en la sociedad": "Excelente", "Argumentación sobre la visión de mundo": "Satisfactorio"}, "justificacion_modelo": "La argumentación presenta una relación clara con el contexto social pero con poca profundidad crítica, mientras que la explicación sobre la relevancia e impacto es clara y bien fundamentada con ejemplos. El uso de evidencias es adecuado pero podría ser más preciso para alcanzar el nivel excelente.", "requiere_revision_humana": true}', 5, 0, true, '{evaluación,tragedia,"visión de mundo",argumentación,"enseñanza media"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (229, 52, 'open_ended', 'all', '{"pregunta": "Después de analizar las tragedias leídas en clase, ¿cómo argumentarías sobre la relevancia y el impacto de la visión de mundo que expresan en su contexto social? Justifica tu respuesta considerando las características propias de la tragedia y el momento histórico en que fue escrita.", "explicacion": "El docente debe evaluar que el estudiante no solo reconozca la visión de mundo, sino que la analice en relación con su contexto y características de la tragedia, valorando la profundidad y coherencia de su argumentación y su capacidad para expresar ideas de manera clara.", "contexto_adicional": "Considera el contexto social, político y cultural de la época en que se escribió la tragedia, así como sus elementos formales y temáticos.", "criterios_evaluacion": ["Argumentación coherente y fundamentada sobre la visión de mundo expresada en la tragedia (peso: 40%)", "Relación clara entre las características de la tragedia y el contexto social e histórico (peso: 35%)", "Estructura y claridad en la expresión escrita, con uso adecuado del lenguaje (peso: 25%)"]}', '{"puntos_clave": ["Reconocimiento de la visión de mundo expresada en la tragedia", "Conexión entre elementos formales de la tragedia y su contexto histórico-social", "Argumentación clara y justificada sobre la relevancia e impacto de la obra"], "respuesta_modelo": "Las tragedias leídas reflejan una visión de mundo marcada por conflictos humanos universales, como el destino, la fatalidad y la lucha interna. Esta visión es relevante porque permite comprender las tensiones sociales y morales del momento histórico en el que fueron escritas, como la crisis de valores y el cuestionamiento del poder. Por ejemplo, en la tragedia X, la estructura dramática y el desarrollo del personaje principal evidencian cómo la sociedad enfrentaba dilemas éticos y políticos. Así, la obra impacta al mostrar la fragilidad humana y la inevitabilidad del sufrimiento, temas que resonaban con las experiencias de su público original y que siguen siendo significativos hoy, evidenciando la perdurabilidad de estas problemáticas sociales.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",Tragedia,"Análisis crítico","Contexto histórico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (230, 53, 'open_ended', 'all', '{"pregunta": "Diseña una tragedia breve que refleje una visión de mundo influenciada por un contexto histórico específico. Describe el contexto elegido, los personajes principales y cómo los eventos trágicos expresan las características y dilemas de esa época.", "explicacion": "Se evaluará que el estudiante logre integrar el contexto histórico con la construcción literaria, demostrando comprensión de la visión de mundo que se expresa en tragedias clásicas y su capacidad para crear un texto original que refleje dicha visión.", "contexto_adicional": "Puedes elegir cualquier contexto histórico que haya sido tratado en clases o investigado previamente, como la Grecia clásica, el Renacimiento, la Revolución Industrial, entre otros.", "criterios_evaluacion": ["Contextualización histórica clara y coherente que fundamenta la visión de mundo (peso: 30%)", "Desarrollo de personajes y trama que reflejen los conflictos y valores del contexto seleccionado (peso: 40%)", "Creatividad y originalidad en la construcción de la tragedia, incluyendo el uso adecuado de elementos trágicos (peso: 30%)"]}', '{"puntos_clave": ["Elección explícita y justificada del contexto histórico", "Personajes y trama vinculados directamente a problemáticas del contexto", "Uso de elementos trágicos para expresar la visión de mundo (conflicto, fatalidad, dilemas morales)"], "respuesta_modelo": "Contexto: Revolución Industrial en el siglo XIX. Personajes: Un obrero llamado Juan y su familia. Trama: Juan lucha por mantener a su familia mientras enfrenta la explotación laboral y la pérdida de valores humanos. La tragedia se desarrolla cuando Juan, agotado y enfermo, muere en un accidente en la fábrica, dejando a su familia en la pobreza y desesperanza. Este desenlace refleja la visión pesimista sobre el progreso industrial y sus consecuencias sociales, mostrando la tragedia humana detrás del avance tecnológico.", "requiere_revision_humana": true}', 4, 0, true, '{lengua_y_literatura,tragedia,creacion_literaria,contexto_historico}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (231, 53, 'open_ended', 'all', '{"pregunta": "Diseña una tragedia breve que refleje una visión de mundo influenciada por un contexto histórico específico. Explica cómo los elementos trágicos (personajes, conflicto, destino) y el contexto elegido se relacionan para comunicar esa visión.", "explicacion": "Evalúa si el estudiante logra crear una tragedia breve que contenga elementos esenciales del género, demostrando comprensión del contexto histórico elegido. Se debe valorar la capacidad creativa y el análisis crítico en la explicación de la visión de mundo reflejada.", "contexto_adicional": "Puedes elegir cualquier contexto histórico relevante para la literatura universal o chilena, como la Grecia clásica, la Edad Media, el Renacimiento, la dictadura en Chile, entre otros.", "criterios_evaluacion": ["Originalidad y coherencia en la creación de la tragedia breve (peso: 30%)", "Capacidad para integrar elementos trágicos clásicos con la visión de mundo del contexto histórico seleccionado (peso: 40%)", "Claridad y profundidad en la explicación de cómo el contexto histórico influye en la visión de mundo expresada en la tragedia (peso: 30%)"]}', '{"puntos_clave": ["Selección clara y pertinente del contexto histórico", "Integración de elementos trágicos (personajes, conflicto, destino) coherentes con el contexto", "Explicación detallada de cómo la visión de mundo se expresa a través de la tragedia"], "respuesta_modelo": "Título: \"El último suspiro de la libertad\"\nContexto histórico: Dictadura militar en Chile (1973-1990)\nTragedia breve: La obra narra la historia de un joven activista que lucha por la justicia y libertad en un régimen opresor. A medida que enfrenta la persecución y la traición, su destino trágico culmina en la pérdida de la vida, simbolizando el sacrificio por ideales mayores.\nExplicación: La tragedia refleja la visión de un mundo marcado por la represión y la lucha por los derechos humanos. Los personajes representan la resistencia y el sacrificio, mientras que el conflicto surge del enfrentamiento entre la libertad y el autoritarismo. El destino fatal del protagonista refuerza la idea de que, en contextos opresivos, la búsqueda de justicia puede implicar grandes costos personales.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Tragedia,"Creación Literaria","Contexto Histórico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (232, 53, 'concept_map', 'all', '{"conceptos": ["Tragedia", "Visión de mundo", "Contexto histórico", "Características de la tragedia", "Destino", "Conflicto", "Catarsis"], "explicacion": "Se espera que el estudiante conecte cómo el contexto histórico influye en la visión de mundo que se expresa en la tragedia, relacionando las características propias del género (como el conflicto, destino y catarsis) con la construcción de esa visión.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la tragedia y su expresión de una visión de mundo influenciada por un contexto histórico?"}', '{"conceptos_centrales": ["Tragedia", "Visión de mundo"], "relaciones_esperadas": [{"origen": "Contexto histórico", "destino": "Visión de mundo", "relacion": "influye en"}, {"origen": "Visión de mundo", "destino": "Tragedia", "relacion": "se expresa a través de"}, {"origen": "Tragedia", "destino": "Conflicto", "relacion": "presenta"}, {"origen": "Tragedia", "destino": "Destino", "relacion": "incluye"}, {"origen": "Tragedia", "destino": "Catarsis", "relacion": "provoca"}, {"origen": "Características de la tragedia", "destino": "Tragedia", "relacion": "definen"}], "requiere_revision_humana": true}', 4, 0, true, '{mapa_conceptual,tragedia,visión_de_mundo,contexto_histórico,crear}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (245, 56, 'multiple_choice', 'all', '{"opciones": {"A": "\"Don Quijote de la Mancha\" de Miguel de Cervantes", "B": "\"Los Miserables\" de Victor Hugo", "C": "\"Cien Años de Soledad\" de Gabriel García Márquez", "D": "\"La Metamorfosis\" de Franz Kafka"}, "pregunta": "¿Cuál de las siguientes obras literarias representa mejor las características del Romanticismo en la literatura?", "explicacion": "La respuesta correcta es B: \"Los Miserables\" de Victor Hugo, ya que es una obra emblemática del Romanticismo que refleja temas como la exaltación del sentimiento, la crítica social y la libertad individual. La opción A, \"Don Quijote\", pertenece al Siglo de Oro y es una novela del Renacimiento-Barroco, no del Romanticismo. La opción C, \"Cien Años de Soledad\", es una obra del realismo mágico del siglo XX, fuera del periodo romántico. La opción D, \"La Metamorfosis\", es una obra del modernismo y el existencialismo, también posterior al Romanticismo. Por lo tanto, solo la opción B refleja correctamente las características y contexto del Romanticismo."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{Romanticismo,Literatura,Comprensión,"Primero Medio","Lengua y Literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (233, 53, 'concept_map', 'all', '{"conceptos": ["Tragedia", "Visión de mundo", "Contexto histórico", "Características de la tragedia", "Personajes trágicos"], "explicacion": "Se espera que el estudiante conecte cómo el contexto histórico influye en la visión de mundo expresada en la tragedia, cómo las características de la tragedia definen su estructura y mensaje, y el papel de los personajes trágicos en manifestar esa visión.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la construcción de una tragedia que refleje una visión de mundo influenciada por un contexto histórico?"}', '{"conceptos_centrales": ["Tragedia", "Visión de mundo"], "relaciones_esperadas": [{"origen": "Contexto histórico", "destino": "Visión de mundo", "relacion": "influye en"}, {"origen": "Visión de mundo", "destino": "Tragedia", "relacion": "se expresa en"}, {"origen": "Tragedia", "destino": "Características de la tragedia", "relacion": "tiene"}, {"origen": "Tragedia", "destino": "Personajes trágicos", "relacion": "incluye"}, {"origen": "Personajes trágicos", "destino": "Visión de mundo", "relacion": "representan"}], "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Tragedia,"Mapa conceptual",Creación,"Visión de mundo"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (234, 53, 'open_ended', 'all', '{"pregunta": "Diseña una tragedia breve que refleje una visión de mundo influenciada por un contexto histórico específico. Explica cómo las características de la tragedia y el contexto elegido se manifiestan en tu obra, y qué mensaje o reflexión esperas transmitir a través de ella.", "explicacion": "El docente debe evaluar que el estudiante no solo haya creado un texto original que respete las características básicas de la tragedia (como el conflicto trágico, el destino inevitable o la catarsis), sino que también haya integrado de manera efectiva un contexto histórico que influya en la construcción de la obra. Además, debe valorar la profundidad con que el estudiante explique el mensaje o reflexión que su tragedia busca comunicar, evidenciando comprensión del género y del contexto.", "contexto_adicional": "Puedes elegir cualquier periodo histórico que consideres relevante, como la antigüedad clásica, la Edad Media, la época de la independencia de Chile, o cualquier otro que te permita expresar una visión particular del mundo a través de una tragedia.", "criterios_evaluacion": ["Creatividad y originalidad en la construcción de la tragedia y la visión de mundo expresada (peso: 30%)", "Coherencia y relevancia del contexto histórico elegido y su influencia en la trama y personajes (peso: 40%)", "Claridad en la explicación del mensaje o reflexión que la tragedia busca transmitir, vinculándolo con las características del género (peso: 30%)"]}', '{"puntos_clave": ["Elección y descripción clara de un contexto histórico específico", "Integración coherente del contexto en la trama y personajes de la tragedia", "Explicación del mensaje o reflexión vinculada a las características del género trágico"], "respuesta_modelo": "Título: \"El último adiós de la corona\"\n\nContexto histórico: La caída del Imperio Romano de Occidente.\n\nTragedia breve: La obra narra la historia de un joven noble romano, Marco, que enfrenta la inevitable pérdida de su familia y su patria ante la invasión bárbara. A través de su lucha interna entre aceptar el destino o resistir contra lo inevitable, se refleja la decadencia y el fin de una era.\n\nExplicación: Esta tragedia utiliza el contexto histórico de la caída del Imperio Romano para mostrar una visión del mundo marcada por la fatalidad y la transformación inevitable. Las características trágicas, como el conflicto interno y la catarsis, están presentes en el protagonista. El mensaje busca provocar una reflexión sobre cómo las grandes civilizaciones, al igual que las personas, enfrentan su fin y cómo el destino puede ser ineludible, pero también da espacio a la dignidad en la resistencia.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Tragedia,"Contexto histórico","Creación literaria"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (235, 54, 'multiple_choice', 'all', '{"opciones": {"A": "Enfoque en la razón y el orden clásico", "B": "Exaltación de los sentimientos y la naturaleza", "C": "Predominio de la literatura científica y técnica", "D": "Uso exclusivo de la narrativa histórica objetiva"}, "pregunta": "¿Cuál de las siguientes características es propia del Romanticismo en la literatura?", "explicacion": "La característica correcta del Romanticismo es la exaltación de los sentimientos y la naturaleza, ya que este movimiento literario privilegia la expresión emocional, la libertad creativa y el amor por lo natural y lo sublime. La opción A corresponde al Neoclasicismo, que valora la razón y el orden. La opción C se relaciona más con textos científicos y no literarios, y la opción D es incorrecta porque el Romanticismo no se limita a la narrativa histórica objetiva, sino que busca la subjetividad y la imaginación."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{Romanticismo,Literatura,"Lengua y Literatura","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (236, 54, 'multiple_choice', 'all', '{"opciones": {"A": "Enfoque en la razón y la objetividad científica.", "B": "Exaltación de los sentimientos y la libertad individual.", "C": "Predominio de la literatura clásica y reglas estrictas.", "D": "Uso exclusivo del verso clásico y temas mitológicos."}, "pregunta": "¿Cuál de las siguientes características es propia del Romanticismo en la literatura?", "explicacion": "La respuesta correcta es la opción B, porque el Romanticismo se caracteriza principalmente por la exaltación de los sentimientos, la libertad individual y la subjetividad. La opción A es incorrecta porque la razón y la objetividad son características del Neoclasicismo, no del Romanticismo. La opción C es un error común, ya que el Romanticismo se aleja de las reglas estrictas y la literatura clásica. La opción D es incorrecta porque aunque pueden aparecer temas mitológicos, no es exclusivo ni predominante; además, el Romanticismo incluye diversas formas y temáticas."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{Romanticismo,Características,Literatura,"Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (237, 54, 'multiple_choice', 'all', '{"opciones": {"A": "Enfoque en la razón y la objetividad científica.", "B": "Exaltación de los sentimientos y la libertad individual.", "C": "Predominio de la narrativa realista y detallada.", "D": "Uso exclusivo del verso clásico y la métrica rígida."}, "pregunta": "¿Cuál de las siguientes características es propia del Romanticismo en la literatura?", "explicacion": "La respuesta correcta es B, porque el Romanticismo se caracteriza por la exaltación de los sentimientos, la libertad individual y la expresión del yo interior. La opción A corresponde más al Neoclasicismo, que valoraba la razón y la objetividad. La opción C es propia del Realismo, que busca representar la realidad de manera detallada y objetiva. La opción D no es correcta, ya que el Romanticismo experimentó con distintas formas y rompió con las estructuras clásicas rígidas."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{Romanticismo,Literatura,Características}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (238, 54, 'true_false', 'all', '{"afirmacion": "El Romanticismo se caracteriza por la exaltación de los sentimientos y la libertad individual.", "explicacion": "Esta afirmación es verdadera porque una de las características principales del Romanticismo es la valoración de las emociones y la expresión subjetiva, enfatizando la libertad individual frente a las normas sociales y racionales del Neoclasicismo."}', '{"es_verdadero": true}', 1, 0, true, '{Romanticismo,Características,"Lengua y Literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (239, 54, 'fill_blanks', 'all', '{"texto": "El Romanticismo se caracteriza por valorar la [BLANK_1] y la expresión de los sentimientos personales. Una obra relevante de este movimiento es \"[BLANK_2]\", escrita por un autor destacado de la época.", "pistas": ["Esta característica está relacionada con la importancia de las emociones y la imaginación.", "Es una obra literaria emblemática del Romanticismo en lengua española."], "explicacion": "El Romanticismo es un movimiento literario que enfatiza la libertad creativa, la expresión de los sentimientos y la exaltación de la naturaleza y el individuo. Obras como \"Don Juan Tenorio\" ejemplifican estas características."}', '{"respuestas_correctas": {"BLANK_1": ["imaginación", "sentimientos", "emociones"], "BLANK_2": ["Don Juan Tenorio", "don juan tenorio"]}}', 2, 0, true, '{Romanticismo,Literatura,"Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (240, 55, 'multiple_choice', 'all', '{"opciones": {"A": "El Romanticismo es un movimiento literario que se centra en la razón y la objetividad, desarrollado principalmente durante la Revolución Industrial en el siglo XX.", "B": "El Romanticismo es un movimiento cultural del siglo XIX que valora las emociones, la libertad individual y la conexión con la naturaleza, surgido en un contexto de cambios sociales y políticos tras la Revolución Francesa.", "C": "El Romanticismo es una corriente artística que promueve el retorno a las reglas clásicas y la imitación de la antigüedad grecorromana, predominante en el Renacimiento.", "D": "El Romanticismo es un movimiento literario que busca la exactitud científica y la descripción objetiva de la realidad, desarrollado en el siglo XVIII."}, "pregunta": "¿Cuál de las siguientes afirmaciones describe correctamente el Romanticismo y su contexto histórico?", "explicacion": "La opción B es correcta porque describe con precisión el Romanticismo como un movimiento del siglo XIX que enfatiza las emociones, la libertad individual y la naturaleza, en un contexto histórico marcado por las revoluciones y cambios sociales, especialmente tras la Revolución Francesa. La opción A es incorrecta porque asocia el Romanticismo con la razón y la objetividad y un período posterior (siglo XX), lo cual corresponde más al positivismo o realismo. La opción C confunde el Romanticismo con el clasicismo del Renacimiento, que se caracteriza por la imitación de la antigüedad y el equilibrio. La opción D mezcla características del Iluminismo y el siglo XVIII, pero no del Romanticismo."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{Romanticismo,"Historia literaria","Primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (241, 55, 'multiple_choice', 'all', '{"opciones": {"A": "El Romanticismo es un movimiento artístico que busca la perfección técnica y se desarrolló durante el Renacimiento, enfocándose en valores racionales y clásicos.", "B": "El Romanticismo es un movimiento cultural que destaca la expresión de emociones y la libertad creativa, surgiendo a finales del siglo XVIII y en el siglo XIX, en un contexto de cambios sociales y políticos.", "C": "El Romanticismo es una corriente literaria que privilegia la objetividad y el análisis científico, surgida en el siglo XX como respuesta a la modernidad.", "D": "El Romanticismo es un movimiento que se centra exclusivamente en la descripción detallada de la naturaleza sin involucrar sentimientos ni ideas políticas."}, "pregunta": "¿Cuál de las siguientes afirmaciones describe correctamente el Romanticismo y su contexto histórico?", "explicacion": "La opción B es correcta porque el Romanticismo enfatiza la expresión emocional, la imaginación y la libertad creativa, y se desarrolló durante finales del siglo XVIII y XIX, en un contexto marcado por revoluciones y transformaciones sociales. La opción A confunde el Romanticismo con el Renacimiento y su énfasis en el racionalismo y clasicismo. La opción C asocia erróneamente el Romanticismo con el siglo XX y la objetividad científica, características más propias del Positivismo o Modernismo. La opción D reduce el Romanticismo solo a la naturaleza, ignorando su énfasis en sentimientos y contextos sociales."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{Romanticismo,"contexto histórico","Lengua y Literatura","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (242, 55, 'true_false', 'all', '{"afirmacion": "El Romanticismo fue un movimiento cultural y literario que surgió como una reacción contra las ideas racionalistas y clásicas de la Ilustración.", "explicacion": "Esta afirmación es verdadera porque el Romanticismo se desarrolló a finales del siglo XVIII y principios del XIX, precisamente como una reacción a los valores de la Ilustración, enfatizando la emoción, la individualidad y la naturaleza, en contraste con la razón y el orden clásico."}', '{"es_verdadero": true}', 2, 0, true, '{Romanticismo,"contexto histórico","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (243, 55, 'drag_drop_matching', 'all', '{"explicacion": "El Romanticismo se caracteriza por valorar la emoción y la libertad creativa; se desarrolló en un contexto influenciado por la Revolución Francesa; da gran importancia al sentimiento y a la expresión individual (individualismo); y la naturaleza es un elemento recurrente y simbolizado en sus obras. La industrialización es un distractor, pues si bien es parte del siglo XIX, no define directamente el Romanticismo.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente relacionado con el Romanticismo", "columna_derecha": ["Movimiento cultural que valoraba la emoción y la libertad creativa", "Proceso histórico que influyó en el contexto social del Romanticismo", "Valoración de las emociones y la intuición sobre la razón", "Importancia del yo y la expresión personal", "Elemento natural idealizado y presente en la poesía romántica", "Distractor: desarrollo tecnológico y económico del siglo XIX"], "columna_izquierda": ["Romanticismo", "Individualismo", "Revolución Francesa", "Sentimiento", "Naturaleza", "Industrialización"]}', '{"emparejamientos_correctos": {"Naturaleza": "Elemento natural idealizado y presente en la poesía romántica", "Sentimiento": "Valoración de las emociones y la intuición sobre la razón", "Romanticismo": "Movimiento cultural que valoraba la emoción y la libertad creativa", "Individualismo": "Importancia del yo y la expresión personal", "Revolución Francesa": "Proceso histórico que influyó en el contexto social del Romanticismo"}}', 2, 0, true, '{Romanticismo,"Lengua y Literatura","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (244, 55, 'sequencing', 'all', '{"explicacion": "El orden correcto comienza con la reacción contra la Ilustración y la Revolución Industrial, que provoca un cambio cultural. Luego, el Romanticismo surge como movimiento cultural. A continuación, los artistas y escritores adoptan nuevos valores emocionales y subjetivos. Después, se crean obras que reflejan estos ideales. Finalmente, todo esto ocurre dentro del contexto histórico de cambios tras la Revolución Francesa.", "instruccion": "Ordena los siguientes eventos en la secuencia correcta que explica el surgimiento del Romanticismo.", "elementos_desordenados": ["Reacción contra el racionalismo de la Ilustración y la Revolución Industrial.", "El Romanticismo emerge como un movimiento cultural y artístico en Europa.", "Los escritores y artistas comienzan a valorar la emoción, la subjetividad y la naturaleza.", "Se desarrollan obras literarias y artísticas que reflejan ideales románticos.", "El contexto histórico incluye cambios sociales y políticos tras la Revolución Francesa."]}', '{"orden_correcto": ["Reacción contra el racionalismo de la Ilustración y la Revolución Industrial.", "El Romanticismo emerge como un movimiento cultural y artístico en Europa.", "Los escritores y artistas comienzan a valorar la emoción, la subjetividad y la naturaleza.", "Se desarrollan obras literarias y artísticas que reflejan ideales románticos.", "El contexto histórico incluye cambios sociales y políticos tras la Revolución Francesa."]}', 3, 0, true, '{Romanticismo,secuencia,"contexto histórico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (246, 56, 'multiple_choice', 'all', '{"opciones": {"A": "“Fausto” de Johann Wolfgang von Goethe, por su énfasis en la búsqueda del conocimiento racional.", "B": "“Las flores del mal” de Charles Baudelaire, por su exploración de la melancolía y el subjetivismo.", "C": "“Canción de otoño en primavera” de Rubén Darío, por su celebración de la juventud y la naturaleza.", "D": "“Cien años de soledad” de Gabriel García Márquez, por su realismo mágico y estructura familiar."}, "pregunta": "¿Cuál de las siguientes obras literarias refleja de manera más clara las características del Romanticismo, considerando su enfoque en la exaltación de los sentimientos y la naturaleza?", "explicacion": "La opción C es correcta porque “Canción de otoño en primavera” es un poema que refleja la exaltación de los sentimientos y la naturaleza, características propias del Romanticismo. La opción A, aunque es una obra importante, está más vinculada al clasicismo y al racionalismo. La opción B pertenece al simbolismo, que es posterior y tiene un enfoque más oscuro y estético diferente al Romanticismo. La opción D es un ejemplo del realismo mágico del siglo XX, que no pertenece al Romanticismo ni comparte sus características principales."}', '{"respuesta_correcta": "C"}', 4, 0, true, '{Romanticismo,Literatura,"Comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (247, 56, 'drag_drop_matching', 'all', '{"explicacion": "Cada característica del Romanticismo se refleja en ejemplos claros: la exaltación de los sentimientos se manifiesta en el poema ''Romance Sonámbulo'', la naturaleza es vista como refugio para expresar emociones, el héroe solitario representa la búsqueda individual, y la rebeldía se expresa en la oposición a las normas sociales. Los distractores hacen referencia a la Ilustración, que es un movimiento anterior y contrario en enfoque.", "instruccion": "Empareja cada característica del Romanticismo con su ejemplo o definición correspondiente", "columna_derecha": ["Búsqueda de la libertad individual y rechazo a lo establecido", "Uso de la naturaleza para expresar emociones profundas", "Personaje que actúa solo, enfrentando su destino", "Poema que refleja la pasión y el misterio propios del Romanticismo", "Movimiento que antecede al Romanticismo, centrado en la razón", "Predominio de la razón sobre la emoción"], "columna_izquierda": ["Exaltación de los sentimientos", "Naturaleza como refugio", "El héroe solitario", "Rebeldía contra las normas", "Obra: \"Romance Sonámbulo\" de Federico García Lorca", "Distractor: Ilustración"]}', '{"emparejamientos_correctos": {"El héroe solitario": "Personaje que actúa solo, enfrentando su destino", "Naturaleza como refugio": "Uso de la naturaleza para expresar emociones profundas", "Rebeldía contra las normas": "Búsqueda de la libertad individual y rechazo a lo establecido", "Exaltación de los sentimientos": "Poema que refleja la pasión y el misterio propios del Romanticismo", "Obra: \"Romance Sonámbulo\" de Federico García Lorca": "Poema que refleja la pasión y el misterio propios del Romanticismo"}}', 3, 0, true, '{Romanticismo,Literatura,"Lengua y Literatura","Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (248, 56, 'sequencing', 'all', '{"explicacion": "El orden correcto comienza con el surgimiento del Romanticismo como movimiento cultural, seguido por la reacción contra el Neoclasicismo. Luego, los escritores valoran la naturaleza y las emociones, lo que se refleja en las temáticas de sus obras. Finalmente, se publican las obras emblemáticas que consolidan el movimiento.", "instruccion": "Ordena los siguientes pasos/eventos en la secuencia correcta para entender el desarrollo del Romanticismo en la literatura", "elementos_desordenados": ["Los escritores comienzan a expresar una fuerte valoración de la naturaleza y las emociones individuales", "Se produce una reacción contra las normas racionalistas y clásicas del Neoclasicismo", "Se publican obras emblemáticas que reflejan los ideales románticos, como poemas y novelas", "El Romanticismo surge como un movimiento cultural y artístico en Europa a finales del siglo XVIII", "Las obras románticas se centran en temas como la libertad, el nacionalismo y el subjetivismo"]}', '{"orden_correcto": ["El Romanticismo surge como un movimiento cultural y artístico en Europa a finales del siglo XVIII", "Se produce una reacción contra las normas racionalistas y clásicas del Neoclasicismo", "Los escritores comienzan a expresar una fuerte valoración de la naturaleza y las emociones individuales", "Las obras románticas se centran en temas como la libertad, el nacionalismo y el subjetivismo", "Se publican obras emblemáticas que reflejan los ideales románticos, como poemas y novelas"]}', 3, 0, true, '{Romanticismo,secuencia,literatura,primeromedia}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (249, 56, 'open_ended', 'all', '{"pregunta": "Elige una obra literaria o artística del Romanticismo y explica cómo sus características reflejan el contexto histórico y cultural de la época. Argumenta tu respuesta relacionando al menos tres características del Romanticismo con elementos concretos de la obra seleccionada.", "explicacion": "El docente debe evaluar si el estudiante comprende el Romanticismo y su contexto, si puede aplicar ese conocimiento a una obra concreta y si estructura su respuesta de manera coherente y argumentativa.", "contexto_adicional": "Puedes escoger una obra literaria o una obra artística del Romanticismo chileno o internacional para responder a esta pregunta.", "criterios_evaluacion": ["Identificación clara de la obra y su contexto histórico-cultural (peso: 30%)", "Capacidad para relacionar correctamente al menos tres características del Romanticismo con elementos específicos de la obra (peso: 40%)", "Claridad y coherencia en la argumentación presentada (peso: 30%)"]}', '{"puntos_clave": ["Selección clara de una obra romántica reconocida", "Contextualización histórica y cultural del Romanticismo", "Relación explícita de al menos tres características románticas con elementos concretos de la obra"], "respuesta_modelo": "He elegido la obra literaria \"Rimas\" de Gustavo Adolfo Bécquer, un importante exponente del Romanticismo español. Esta obra refleja el contexto histórico de un siglo XIX marcado por la reacción a la Ilustración y la Revolución Industrial, donde se valoraba la expresión de los sentimientos y la libertad individual. Tres características románticas presentes en \"Rimas\" son: la exaltación del sentimiento y la subjetividad, evidenciada en la introspección y melancolía del autor; la idealización de la naturaleza, que aparece como un refugio y espejo del alma humana; y el interés por lo misterioso y lo fantástico, visible en la atmósfera y temas que exploran el amor imposible y la muerte. Estas características conectan profundamente con el contexto de incertidumbre y búsqueda de identidad del Romanticismo, mostrando cómo la obra es un reflejo de esa época.", "requiere_revision_humana": true}', 3, 0, true, '{Romanticismo,"Análisis literario","Aplicación de conceptos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (250, 57, 'compare_contrast', 'all', '{"conceptos": ["Romanticismo", "Realismo"], "criterios": ["Temática principal", "Estilo y lenguaje", "Contexto histórico", "Visión del individuo"], "explicacion": "El Romanticismo se caracteriza por su enfoque en la emoción, la naturaleza y la libertad individual, con un lenguaje emotivo y subjetivo, en un contexto histórico de búsqueda de identidad y libertad. En contraste, el Realismo se centra en la representación objetiva de la realidad social, con un estilo detallado y lenguaje cotidiano, en un contexto de cambios sociales y económicos, mostrando al individuo como parte de un entorno determinado.", "instruccion": "Compara y contrasta las siguientes corrientes literarias según los criterios dados"}', '{"tabla_correcta": {"Realismo": {"Estilo y lenguaje": "Objetivo, detallado, lenguaje común", "Contexto histórico": "Revolución Industrial avanzada, cambios sociales y económicos", "Temática principal": "Vida cotidiana, sociedad, problemas sociales", "Visión del individuo": "Producto y reflejo de su entorno social"}, "Romanticismo": {"Estilo y lenguaje": "Expresivo, subjetivo, poético", "Contexto histórico": "Post Revolución Industrial, búsqueda de identidad nacional", "Temática principal": "Emociones, naturaleza, libertad individual", "Visión del individuo": "Protagonista emocional y libre"}}}', 3, 0, true, '{Romanticismo,Realismo,"Comparar y contrastar",Literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (251, 57, 'compare_contrast', 'all', '{"conceptos": ["Romanticismo", "Realismo"], "criterios": ["Temática principal", "Estilo narrativo", "Contexto histórico"], "explicacion": "El Romanticismo se caracteriza por enfatizar la subjetividad, la imaginación y la exaltación del individuo, mientras que el Realismo busca representar la realidad de manera objetiva y detallada. Ambos movimientos responden a contextos históricos diferentes: el Romanticismo surge como reacción a las normas neoclásicas y a la Revolución Industrial, y el Realismo aparece en un contexto de transformación social y científica, buscando reflejar la vida cotidiana con precisión.", "instruccion": "Compara y contrasta los siguientes movimientos literarios según los criterios dados"}', '{"tabla_correcta": {"Realismo": {"Estilo narrativo": "Objetivo, detallado y descriptivo", "Contexto histórico": "Segunda mitad del siglo XIX, en un contexto de cambios sociales y científicos", "Temática principal": "Vida cotidiana, problemas sociales y personajes comunes"}, "Romanticismo": {"Estilo narrativo": "Subjetivo, emotivo y a veces idealista", "Contexto histórico": "Finales del siglo XVIII y primera mitad del XIX, reacción contra la Ilustración y la Revolución Industrial", "Temática principal": "Sentimientos, naturaleza, individualismo y libertad"}}}', 4, 0, true, '{Romanticismo,Realismo,"Comparar y Contrastar","Literatura 1º Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (252, 57, 'open_ended', 'all', '{"pregunta": "Compara una obra representativa del Romanticismo con una obra de otro movimiento literario (por ejemplo, el Realismo o el Modernismo). Analiza las similitudes y diferencias en sus características literarias y el contexto histórico que influyó en cada una. ¿Cómo estos elementos reflejan los valores y preocupaciones de sus respectivas épocas?", "explicacion": "El docente debe evaluar si el estudiante comprende y distingue las características clave de los movimientos literarios y sus contextos históricos, así como su capacidad para realizar un análisis comparativo profundo y fundamentado, demostrando pensamiento crítico.", "contexto_adicional": "Se sugiere que el estudiante elija obras conocidas dentro del Romanticismo y otro movimiento literario estudiado previamente.", "criterios_evaluacion": ["Identificación precisa de características literarias de ambas obras y movimientos (peso: 30%)", "Análisis comparativo claro y coherente de similitudes y diferencias en características y contextos (peso: 40%)", "Capacidad para relacionar los elementos literarios y contextuales con los valores y preocupaciones históricas de cada época (peso: 30%)"]}', '{"puntos_clave": ["Características literarias específicas del Romanticismo y del otro movimiento elegido", "Contexto histórico que influyó en cada obra y movimiento", "Relación entre características literarias, contexto histórico y valores/temáticas de la época"], "respuesta_modelo": "Por ejemplo, al comparar ''Don Juan Tenorio'' de José Zorrilla (Romanticismo) con ''La novela de un literato'' de Benito Pérez Galdós (Realismo), se observa que el Romanticismo enfatiza la exaltación del individuo, la pasión y la libertad creativa, mientras que el Realismo busca representar la realidad social de manera objetiva y detallada. El contexto histórico del Romanticismo, marcado por revoluciones y cambios sociales, se refleja en la rebeldía y la idealización presentes en la obra de Zorrilla. Por otro lado, el Realismo surge en un período de consolidación social y preocupación por las condiciones materiales y sociales, lo que se refleja en la obra de Galdós con su enfoque en la vida cotidiana y crítica social. Así, las diferencias en características literarias y contexto evidencian los valores y preocupaciones de sus respectivas épocas.", "requiere_revision_humana": true}', 4, 0, true, '{Romanticismo,"Comparación literaria","Análisis crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (253, 57, 'concept_map', 'all', '{"conceptos": ["Romanticismo", "Realismo", "Características del Romanticismo", "Características del Realismo", "Contexto histórico del Romanticismo", "Contexto histórico del Realismo", "Temas literarios", "Estilo narrativo", "Emoción y subjetividad", "Objetividad y análisis social"], "explicacion": "El mapa conceptual debe mostrar cómo el Romanticismo y el Realismo se diferencian y se relacionan a partir de sus características, contexto histórico, temas literarios y estilos narrativos. Por ejemplo, cómo el Romanticismo enfatiza la emoción y la subjetividad, mientras que el Realismo privilegia la objetividad y el análisis social. Además, debe reflejar las influencias del contexto histórico en cada movimiento.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de los movimientos literarios del Romanticismo y el Realismo?"}', '{"conceptos_centrales": ["Romanticismo", "Realismo"], "relaciones_esperadas": [{"origen": "Romanticismo", "destino": "Características del Romanticismo", "relacion": "tiene"}, {"origen": "Realismo", "destino": "Características del Realismo", "relacion": "tiene"}, {"origen": "Características del Romanticismo", "destino": "Emoción y subjetividad", "relacion": "incluyen"}, {"origen": "Características del Realismo", "destino": "Objetividad y análisis social", "relacion": "incluyen"}, {"origen": "Romanticismo", "destino": "Contexto histórico del Romanticismo", "relacion": "se enmarca en"}, {"origen": "Realismo", "destino": "Contexto histórico del Realismo", "relacion": "se enmarca en"}, {"origen": "Temas literarios", "destino": "Romanticismo", "relacion": "se expresan en"}, {"origen": "Temas literarios", "destino": "Realismo", "relacion": "se expresan en"}, {"origen": "Estilo narrativo", "destino": "Romanticismo", "relacion": "difiere en"}, {"origen": "Estilo narrativo", "destino": "Realismo", "relacion": "difiere en"}], "requiere_revision_humana": true}', 4, 0, true, '{Romanticismo,Realismo,"Mapa conceptual","Análisis literario"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (254, 58, 'criteria_evaluation', 'all', '{"rubrica": {"Capacidad crítica y valoración personal": {"Excelente": "Incluye una valoración personal fundamentada sobre la relevancia del Romanticismo en la actualidad.", "Satisfactorio": "Presenta una valoración personal, aunque poco argumentada o superficial.", "Necesita mejorar": "No incluye valoración personal o esta carece de fundamento."}, "Claridad y coherencia en la argumentación": {"Excelente": "El ensayo presenta ideas claras, bien organizadas y coherentes que permiten comprender fácilmente la importancia del Romanticismo.", "Satisfactorio": "El ensayo es comprensible, aunque algunas ideas pueden estar poco desarrolladas o ligeramente desorganizadas.", "Necesita mejorar": "El ensayo presenta ideas confusas o desorganizadas que dificultan la comprensión del argumento central."}, "Dominio de las características del Romanticismo": {"Excelente": "Describe adecuadamente las características principales del Romanticismo, relacionándolas con el contexto histórico.", "Satisfactorio": "Identifica algunas características del Romanticismo, pero con explicaciones superficiales o incompletas.", "Necesita mejorar": "No identifica o confunde las características del Romanticismo o no las relaciona con el contexto."}, "Justificación del impacto histórico y cultural": {"Excelente": "Se fundamenta con ejemplos precisos y relevantes cómo el Romanticismo influyó en la sociedad de su época y su legado actual.", "Satisfactorio": "Se mencionan algunos ejemplos sobre el impacto histórico y cultural, aunque con menor profundidad o relevancia.", "Necesita mejorar": "No se presentan justificaciones claras o los ejemplos son poco pertinentes respecto al impacto del Romanticismo."}}, "explicacion": "Se espera que el estudiante analice la calidad argumentativa del ensayo, la pertinencia y profundidad de las justificaciones sobre el impacto histórico y cultural del Romanticismo, su conocimiento de las características del movimiento y su capacidad para hacer una valoración crítica fundamentada.", "instruccion": "Evalúa el ensayo presentado según los criterios de la rúbrica y justifica con argumentos tu calificación en cada criterio.", "caso_a_evaluar": "Un ensayo presentado por un estudiante que argumenta la importancia del Romanticismo en la historia literaria y cultural, destacando sus características principales, el contexto histórico en que surge y su impacto en la sociedad pasada y actual."}', '{"evaluacion_modelo": {"Capacidad crítica y valoración personal": "Satisfactorio", "Claridad y coherencia en la argumentación": "Satisfactorio", "Dominio de las características del Romanticismo": "Excelente", "Justificación del impacto histórico y cultural": "Excelente"}, "justificacion_modelo": "El ensayo presenta argumentos claros pero con algunos puntos poco desarrollados, mostrando una buena organización general. La justificación sobre el impacto histórico y cultural es precisa y con ejemplos relevantes, demostrando un buen dominio del tema. Las características del Romanticismo están bien descritas y relacionadas con el contexto. La valoración personal está presente pero podría profundizarse más en su fundamentación.", "requiere_revision_humana": true}', 4, 0, true, '{Romanticismo,evaluación,"debate guiado","lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (255, 58, 'criteria_evaluation', 'all', '{"rubrica": {"Claridad y coherencia en la argumentación": {"Excelente": "El ensayo presenta ideas claras, bien organizadas y coherentes que facilitan la comprensión y apoyan la evaluación realizada.", "Satisfactorio": "Las ideas son en general claras pero la organización es básica, con algunas inconsistencias en la coherencia argumentativa.", "Necesita mejorar": "El texto es confuso o desorganizado, dificultando la comprensión y afectando la fuerza de la argumentación."}, "Justificación de la relevancia histórica y cultural": {"Excelente": "Justifica claramente la importancia del Romanticismo con argumentos sólidos y ejemplos precisos que muestran comprensión profunda del contexto histórico y cultural.", "Satisfactorio": "Ofrece una justificación adecuada con algunos ejemplos y argumentos que reflejan un entendimiento general del Romanticismo y su contexto.", "Necesita mejorar": "Presenta una justificación superficial o vaga, con pocos ejemplos o argumentos poco claros sobre la importancia del Romanticismo."}, "Valoración del impacto en la sociedad de su época y en la actualidad": {"Excelente": "Evalúa críticamente el impacto del Romanticismo en ambas épocas, mostrando reflexión y conexión con fenómenos culturales y sociales relevantes.", "Satisfactorio": "Muestra una valoración general del impacto en la sociedad, aunque con análisis limitado o poco detallado.", "Necesita mejorar": "No logra valorar adecuadamente el impacto del Romanticismo, con poco o ningún análisis sobre su influencia social."}}, "explicacion": "Se espera que el estudiante analice críticamente el texto, identificando la fuerza de los argumentos presentados sobre la importancia del Romanticismo en su contexto histórico y su impacto social, valorando además la claridad y coherencia del ensayo.", "instruccion": "Evalúa el ensayo según la rúbrica proporcionada y justifica tu calificación en cada criterio.", "caso_a_evaluar": "Se te presenta un ensayo escrito por un estudiante que analiza la importancia del Romanticismo en la historia literaria y cultural. El ensayo argumenta cómo este movimiento influyó en la sociedad de su época y en la actualidad, destacando sus características principales y su legado."}', '{"evaluacion_modelo": {"Claridad y coherencia en la argumentación": "Excelente", "Justificación de la relevancia histórica y cultural": "Excelente", "Valoración del impacto en la sociedad de su época y en la actualidad": "Satisfactorio"}, "justificacion_modelo": "El ensayo presenta argumentos sólidos y bien fundamentados sobre la relevancia histórica y cultural del Romanticismo, demostrando comprensión profunda del contexto. Sin embargo, la valoración del impacto social es general y podría profundizarse más. La argumentación es clara y coherente, facilitando la comprensión del análisis realizado.", "requiere_revision_humana": true}', 5, 0, true, '{Romanticismo,"Historia literaria","Evaluación crítica","Lengua y Literatura","Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (314, 70, 'criteria_evaluation', 'all', '{"rubrica": {"Justificación crítica de la valoración": {"Excelente": "Presenta una valoración crítica coherente y fundamentada que integra las estrategias persuasivas y recursos no lingüísticos, con argumentos sólidos.", "Satisfactorio": "Ofrece una valoración con argumentos básicos que relacionan algunas estrategias y recursos, aunque con poca profundidad.", "Necesita mejorar": "Realiza una valoración vaga o sin fundamento claro, con escasa o nula relación entre estrategias y recursos."}, "Identificación de estrategias de persuasión": {"Excelente": "Identifica claramente todas las estrategias persuasivas utilizadas, tanto explícitas como implícitas, y explica cómo influyen en el lector.", "Satisfactorio": "Identifica algunas estrategias persuasivas relevantes y explica su función de manera general.", "Necesita mejorar": "Reconoce pocas o ninguna estrategia persuasiva y ofrece explicaciones poco claras o incorrectas."}, "Análisis del uso de recursos no lingüísticos": {"Excelente": "Analiza detalladamente cómo las imágenes, diseño y video complementan y refuerzan el mensaje persuasivo del texto.", "Satisfactorio": "Describe el uso de algunos recursos no lingüísticos y su relación con el mensaje principal de forma básica.", "Necesita mejorar": "No identifica o describe incorrectamente los recursos no lingüísticos o su función en el texto."}}, "explicacion": "Se espera que el estudiante reconozca las estrategias persuasivas explícitas e implícitas, analice cómo las imágenes y el video potencian el mensaje, y formule una valoración crítica sustentada que demuestre comprensión y reflexión sobre el texto analizado.", "instruccion": "Evalúa la noticia digital según la rúbrica presentada y justifica tu calificación para cada criterio con ejemplos o argumentos claros.", "caso_a_evaluar": "Analiza una noticia publicada en un periódico digital sobre un tema ambiental actual. La noticia incluye texto, imágenes y un video breve. Evalúa las estrategias de persuasión y el uso de recursos no lingüísticos presentes en el texto."}', '{"evaluacion_modelo": {"Justificación crítica de la valoración": "Excelente", "Identificación de estrategias de persuasión": "Excelente", "Análisis del uso de recursos no lingüísticos": "Satisfactorio"}, "justificacion_modelo": "El estudiante identifica todas las estrategias persuasivas aplicadas, como apelaciones emocionales y datos contrastados, explicando su impacto. Reconoce el uso de imágenes y video, aunque con un análisis general y no profundo. La valoración crítica está bien fundamentada, integrando ambos aspectos con argumentos coherentes y claros.", "requiere_revision_humana": true}', 5, 0, true, '{evaluación,"estrategias persuasivas","recursos no lingüísticos","debate guiado","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (256, 58, 'open_ended', 'all', '{"pregunta": "Considerando las características del Romanticismo y el contexto histórico en que surgió, ¿por qué crees que esta corriente literaria tuvo un impacto significativo en la sociedad de su época y cómo se manifiestan sus influencias en la cultura actual? Justifica tu respuesta con ejemplos concretos.", "explicacion": "El docente debe valorar si el estudiante logra conectar las características del Romanticismo con su contexto histórico y social, y si puede evaluar su relevancia actual, apoyándose en ejemplos claros que evidencien su comprensión y capacidad crítica.", "contexto_adicional": "Se espera que el estudiante reflexione sobre el Romanticismo desde una perspectiva histórica y cultural, evaluando su importancia y vigencia.", "criterios_evaluacion": ["Claridad y profundidad en la justificación del impacto del Romanticismo en su contexto histórico (peso: 40%)", "Capacidad para relacionar características del Romanticismo con manifestaciones culturales actuales (peso: 35%)", "Uso de ejemplos concretos y pertinentes para apoyar el argumento (peso: 25%)"]}', '{"puntos_clave": ["Reacción contra el racionalismo y el Neoclasicismo", "Exaltación de la emoción, libertad individual y naturaleza", "Influencia en movimientos sociales y culturales de la época", "Manifestaciones actuales en la cultura y el arte contemporáneo"], "respuesta_modelo": "El Romanticismo tuvo un impacto significativo en la sociedad de su época porque surgió como una reacción contra el racionalismo y el orden impuesto por la Ilustración y el Neoclasicismo. Al priorizar la emoción, la libertad individual y la naturaleza, esta corriente cuestionó las estructuras sociales y políticas, promoviendo ideales de libertad y expresión personal. Por ejemplo, la exaltación del individuo y la rebeldía se reflejaron en la literatura y el arte, influyendo en movimientos sociales y culturales posteriores. En la actualidad, su influencia se manifiesta en la valorización de la subjetividad y la creatividad en diversas expresiones culturales, como la música, la literatura y el cine, donde aún se exploran temas como la pasión, la lucha interna y la conexión con la naturaleza. Esto muestra la vigencia del Romanticismo como una fuente de inspiración que desafía las convenciones y promueve la diversidad de perspectivas.", "requiere_revision_humana": true}', 4, 0, true, '{Romanticismo,"Evaluación crítica","Historia literaria"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (257, 58, 'open_ended', 'all', '{"pregunta": "Analiza cómo las características del Romanticismo reflejaron las tensiones sociales y culturales de su época y evalúa por qué esta corriente literaria sigue siendo relevante en la sociedad actual. Justifica tu respuesta con ejemplos concretos de obras o autores y su impacto tanto en el pasado como en el presente.", "explicacion": "El docente debe valorar que el estudiante no solo describa características del Romanticismo, sino que demuestre comprensión profunda vinculado con su contexto histórico y social. Además, debe evaluar la capacidad para argumentar sobre la vigencia de esta corriente, usando ejemplos literarios y culturales, y la calidad comunicativa de la respuesta.", "contexto_adicional": "Considera el contexto histórico del siglo XIX, las ideas predominantes de libertad, individualismo y nacionalismo, así como ejemplos literarios representativos del Romanticismo.", "criterios_evaluacion": ["Análisis de las características del Romanticismo y su relación con el contexto histórico y social (peso: 40%)", "Capacidad para evaluar la relevancia actual del Romanticismo con argumentos sólidos y ejemplos (peso: 40%)", "Claridad, coherencia y estructura en la argumentación presentada (peso: 20%)"]}', '{"puntos_clave": ["Relación entre las características románticas y el contexto social e histórico del siglo XIX", "Ejemplos concretos de obras o autores representativos y su impacto", "Justificación clara de la relevancia actual del Romanticismo"], "respuesta_modelo": "El Romanticismo surgió en el siglo XIX como una reacción a las normas rígidas del Neoclasicismo y a los cambios sociales derivados de la Revolución Industrial. Esta corriente literaria enfatizaba la expresión de emociones, el individualismo, la libertad creativa y el nacionalismo, reflejando las tensiones de una sociedad en transformación y la búsqueda de identidad frente a la modernidad. Por ejemplo, en Chile, obras como las de José Joaquín Vallejo muestran la valoración del paisaje y las raíces nacionales. Actualmente, el Romanticismo sigue siendo relevante porque sus valores de libertad y expresión individual inspiran movimientos culturales y artísticos que buscan desafiar estructuras establecidas, además de mantener viva la reflexión sobre la identidad y la emoción humana. Por lo tanto, el Romanticismo no sólo impactó su época sino que continúa influyendo en la manera en que entendemos el arte y la sociedad.", "requiere_revision_humana": true}', 5, 0, true, '{Romanticismo,"Análisis literario","Evaluación crítica","Historia literaria"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (258, 59, 'open_ended', 'all', '{"pregunta": "Diseña una presentación creativa (puede ser un video, una presentación digital o un cartel) en la que integres las características principales del Romanticismo, el contexto histórico y social en que surgió, y expliques por qué consideras que esta corriente literaria sigue siendo relevante en la actualidad. Justifica tu interpretación personal con ejemplos concretos de obras o autores románticos.", "explicacion": "Se evalúa la capacidad del estudiante para sintetizar información sobre el Romanticismo, contextualizarla históricamente y expresar una opinión personal fundamentada, fomentando habilidades de análisis, evaluación y creación.", "contexto_adicional": "Los estudiantes pueden usar recursos digitales o manuales para crear su presentación, fomentando la creatividad y la reflexión crítica acerca del Romanticismo y su vigencia.", "criterios_evaluacion": ["Integración clara y precisa de las características del Romanticismo (peso: 30%)", "Análisis del contexto histórico y social que permitió el surgimiento del Romanticismo (peso: 40%)", "Interpretación personal bien argumentada sobre la relevancia actual, con ejemplos concretos (peso: 30%)"]}', '{"puntos_clave": ["Características esenciales del Romanticismo: exaltación del sentimiento, libertad creativa, naturaleza, individualismo", "Contexto histórico: cambios políticos y sociales, reacción al Neoclasicismo", "Relevancia actual: interpretación personal con ejemplos concretos"], "respuesta_modelo": "Mi presentación se centra en destacar que el Romanticismo es una corriente literaria marcada por la exaltación de los sentimientos, la libertad creativa y el interés por la naturaleza y lo sublime. Surgió en Europa a fines del siglo XVIII y principios del XIX, en un contexto de cambios sociales y políticos como la Revolución Francesa y la industrialización, que generaron un rechazo a la rigidez del Neoclasicismo y un interés por la individualidad y la emoción. Considero que el Romanticismo sigue siendo relevante hoy porque invita a valorar la expresión personal y la conexión con la naturaleza en un mundo cada vez más tecnificado y globalizado. Por ejemplo, obras como \"Los Miserables\" de Victor Hugo o poemas de Pablo Neruda reflejan ese ideal romántico de libertad y pasión que sigue inspirando a lectores contemporáneos.", "requiere_revision_humana": true}', 4, 0, true, '{Romanticismo,creación,"pensamiento crítico","Lengua y Literatura","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (259, 59, 'open_ended', 'all', '{"pregunta": "Diseña una presentación creativa en la que expliques las características principales del Romanticismo, el contexto histórico y social en que surgió, y argumenta por qué consideras que esta corriente literaria sigue siendo relevante en la actualidad. Incluye además una interpretación personal que relacione una obra romántica con un tema contemporáneo.", "explicacion": "Se evaluará la profundidad del contenido, la coherencia de la argumentación sobre la relevancia del Romanticismo, y la creatividad en la forma de presentación, valorando también la capacidad de relacionar la obra estudiada con temas actuales desde una visión personal.", "contexto_adicional": "El proyecto puede presentarse en formato digital o físico, utilizando recursos visuales, textos, y cualquier otro medio creativo que facilite la comprensión y el interés del público.", "criterios_evaluacion": ["Claridad y precisión en la explicación de las características y contexto del Romanticismo (peso: 30%)", "Capacidad crítica para argumentar la relevancia actual del Romanticismo (peso: 40%)", "Creatividad y originalidad en la presentación y en la interpretación personal sobre la obra romántica (peso: 30%)"]}', '{"puntos_clave": ["Características destacadas del Romanticismo: sentimientos, individualismo, naturaleza, libertad creativa", "Contexto histórico-social: reacción a la Ilustración, influencia de la Revolución Francesa y cambios sociales", "Relevancia actual: valoración de la subjetividad y expresión emocional en la sociedad contemporánea", "Interpretación personal conectando una obra romántica con un tema actual"], "respuesta_modelo": "Mi presentación aborda primero las características del Romanticismo, como la exaltación del sentimiento, la libertad creativa, y el interés por la naturaleza y lo sublime. Luego, explico que esta corriente surgió en un contexto de cambio social y político, marcado por la Revolución Francesa y la reacción contra el racionalismo de la Ilustración. Argumento que el Romanticismo sigue siendo relevante porque nos invita a valorar la subjetividad y la diversidad de experiencias, algo esencial en nuestra sociedad actual. Finalmente, relaciono la obra \"Rimas\" de Gustavo Adolfo Bécquer con el tema contemporáneo de la búsqueda de identidad personal y emocional, mostrando cómo sus poemas expresan emociones universales que aún nos conmueven hoy.", "requiere_revision_humana": true}', 5, 0, true, '{Romanticismo,"Proyecto creativo","Pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (260, 59, 'concept_map', 'all', '{"conceptos": ["Romanticismo", "Características", "Contexto histórico", "Obras representativas", "Relevancia"], "explicacion": "El mapa debe mostrar cómo el Romanticismo surge en un contexto histórico específico, cuáles son sus características definitorias, cómo estas características se reflejan en las obras representativas y cuál es la relevancia de este movimiento en la literatura y cultura.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del Romanticismo literario?"}', '{"conceptos_centrales": ["Romanticismo", "Características"], "relaciones_esperadas": [{"origen": "Contexto histórico", "destino": "Romanticismo", "relacion": "da origen a"}, {"origen": "Romanticismo", "destino": "Características", "relacion": "se manifiesta en"}, {"origen": "Características", "destino": "Obras representativas", "relacion": "se reflejan en"}, {"origen": "Romanticismo", "destino": "Relevancia", "relacion": "posee"}, {"origen": "Obras representativas", "destino": "Relevancia", "relacion": "demuestran"}], "requiere_revision_humana": true}', 4, 0, true, '{Romanticismo,MapaConceptual,LenguaYLiteratura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (261, 59, 'concept_map', 'all', '{"conceptos": ["Romanticismo", "Sentimiento", "Naturaleza", "Individualismo", "Revolución Industrial", "Imaginación", "Contraste con Neoclasicismo", "Libertad creativa"], "explicacion": "Se espera que el estudiante relacione el Romanticismo con su contexto histórico, señalando la influencia de la Revolución Industrial y el contraste con el Neoclasicismo. Deben conectar conceptos como el énfasis en el sentimiento, la naturaleza y el individualismo como características centrales, y mostrar cómo la imaginación y la libertad creativa son expresiones clave dentro del movimiento.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del Romanticismo y su relevancia histórica y literaria?"}', '{"conceptos_centrales": ["Romanticismo", "Sentimiento"], "relaciones_esperadas": [{"origen": "Romanticismo", "destino": "Revolución Industrial", "relacion": "surge en contexto de"}, {"origen": "Romanticismo", "destino": "Sentimiento", "relacion": "se caracteriza por"}, {"origen": "Romanticismo", "destino": "Naturaleza", "relacion": "valora"}, {"origen": "Romanticismo", "destino": "Individualismo", "relacion": "promueve"}, {"origen": "Romanticismo", "destino": "Contraste con Neoclasicismo", "relacion": "contrasta con"}, {"origen": "Individualismo", "destino": "Libertad creativa", "relacion": "impulsa"}, {"origen": "Sentimiento", "destino": "Imaginación", "relacion": "se expresa a través de"}], "requiere_revision_humana": true}', 5, 0, true, '{Romanticismo,"Mapa Conceptual",Creatividad}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (262, 59, 'open_ended', 'all', '{"pregunta": "Diseña una presentación creativa en la que expliques cómo las características del Romanticismo reflejan el contexto histórico y social de su época, y argumenta por qué consideras que esta corriente sigue siendo relevante en la actualidad. Incluye ejemplos de obras o autores representativos y una interpretación personal sobre su impacto.", "explicacion": "Se debe evaluar que el estudiante no solo reproduzca información, sino que analice la relación entre las características del Romanticismo y su contexto, además de expresar una opinión personal bien argumentada sobre su vigencia, apoyándose en ejemplos concretos.", "contexto_adicional": "Los estudiantes pueden utilizar recursos multimedia o cualquier formato creativo para su presentación, considerando el primer año de enseñanza media y los conocimientos previos adquiridos sobre el Romanticismo.", "criterios_evaluacion": ["Comprensión y explicación clara de las características del Romanticismo y su relación con el contexto histórico y social (peso: 40%)", "Integración creativa y coherente de ejemplos de obras o autores representativos (peso: 30%)", "Capacidad de argumentar la relevancia actual del Romanticismo con una interpretación personal fundamentada (peso: 30%)"]}', '{"puntos_clave": ["Contexto histórico y social del Romanticismo", "Características principales del Romanticismo (individualismo, emoción, naturaleza, etc.)", "Ejemplos claros de obras o autores representativos", "Interpretación personal sobre la vigencia y relevancia actual"], "respuesta_modelo": "El Romanticismo surge en un contexto de grandes cambios sociales y políticos, como las revoluciones de finales del siglo XVIII y principios del XIX, que influyeron en su énfasis en la libertad, la expresión de emociones y la crítica a la razón excesiva de la Ilustración. Características como el individualismo, la exaltación de la naturaleza y el interés por lo sublime reflejan este contexto. Por ejemplo, en la poesía de Gustavo Adolfo Bécquer se observa la expresión intensa del yo y el misterio. Actualmente, el Romanticismo sigue siendo relevante porque nos invita a valorar la subjetividad y la conexión con la naturaleza en un mundo cada vez más tecnológico y racionalizado. Personalmente, creo que esta corriente nos recuerda la importancia de las emociones y la libertad creativa en la vida moderna.", "requiere_revision_humana": true}', 5, 0, true, '{Romanticismo,"Análisis crítico",Creatividad,"Lengua y Literatura","Primer Año Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (263, 60, 'multiple_choice', 'all', '{"opciones": {"A": "Una opinión personal que no necesita ser sostenida con argumentos.", "B": "La idea principal que el autor quiere defender y que se sostiene con argumentos.", "C": "Un hecho comprobable que no requiere explicación ni apoyo.", "D": "Un dato irrelevante que se incluye para decorar el texto."}, "pregunta": "¿Cuál de las siguientes opciones describe correctamente qué es una tesis en un texto argumentativo?", "explicacion": "La tesis es la idea principal que el autor busca defender en un texto argumentativo, y para ello presenta argumentos que la respaldan. La opción A es incorrecta porque la tesis debe estar sustentada, no es solo una opinión sin apoyo. La opción C describe un hecho, que es diferente de la tesis. La opción D se refiere a información irrelevante, que no forma parte de la tesis."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"texto argumentativo",tesis,argumentos,"lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (264, 60, 'multiple_choice', 'all', '{"opciones": {"A": "Es un dato o información comprobable que apoya el texto.", "B": "Es la idea principal que el autor quiere defender o demostrar.", "C": "Es una opinión personal sin necesidad de argumentos.", "D": "Es un detalle secundario que no influye en el argumento principal."}, "pregunta": "¿Cuál de las siguientes opciones describe correctamente la tesis en un texto argumentativo?", "explicacion": "La tesis en un texto argumentativo es la idea principal que el autor busca defender o demostrar, por eso la opción B es correcta. La opción A corresponde a un hecho, que es un dato comprobable pero no la tesis. La opción C describe una opinión, que puede apoyar o no la tesis, pero no es la tesis misma. La opción D es incorrecta porque todos los detalles relevantes deben contribuir al argumento principal, y los detalles secundarios no definen la tesis."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"texto argumentativo",tesis,hecho,opinión,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (265, 60, 'multiple_choice', 'all', '{"opciones": {"A": "Una afirmación principal que el autor quiere defender", "B": "Una opinión sin fundamento que no necesita ser demostrada", "C": "Un dato o hecho que solo ilustra la historia", "D": "Un resumen de todos los argumentos presentados"}, "pregunta": "¿Cuál de los siguientes elementos corresponde a la tesis en un texto argumentativo?", "explicacion": "La tesis es la afirmación principal que el autor quiere defender en un texto argumentativo, por eso la opción A es correcta. La opción B es incorrecta porque una opinión sin fundamento no es una tesis ni un elemento sólido en un argumento. La opción C describe un hecho, que sirve para apoyar la tesis, pero no es la tesis en sí. La opción D es incorrecta porque un resumen de argumentos no es la tesis, sino una recopilación de evidencia para sostenerla."}', '{"respuesta_correcta": "A"}', 3, 0, true, '{"texto argumentativo",tesis,"elementos básicos","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (266, 60, 'true_false', 'all', '{"afirmacion": "En un texto argumentativo, la tesis es la idea principal que el autor quiere demostrar o defender.", "explicacion": "Esta afirmación es verdadera porque la tesis es el punto central del texto argumentativo, la cual el autor busca apoyar mediante argumentos y evidencias."}', '{"es_verdadero": true}', 1, 0, true, '{"texto argumentativo",tesis,"elementos básicos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (267, 60, 'fill_blanks', 'all', '{"texto": "En un texto argumentativo, la [BLANK_1] es la idea principal que se defiende, mientras que los [BLANK_2] son las razones o evidencias que la apoyan. Además, es importante diferenciar entre [BLANK_3], que son datos verificables, y opiniones, que reflejan creencias personales.", "pistas": ["La idea central que el autor quiere probar o convencer", "Las bases o justificaciones que sostienen la idea central", "Información comprobable frente a interpretaciones personales"], "explicacion": "Un texto argumentativo se estructura en torno a una tesis, que es la afirmación principal; los argumentos que sustentan esa tesis; y la distinción entre hechos, que son datos objetivos, y opiniones, que son juicios subjetivos."}', '{"respuestas_correctas": {"BLANK_1": ["tesis", "idea principal"], "BLANK_2": ["argumentos", "razones"], "BLANK_3": ["hechos", "datos"]}}', 2, 0, true, '{"texto argumentativo",tesis,argumentos,hechos,opiniones}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (268, 61, 'multiple_choice', 'all', '{"opciones": {"A": "Presentar información sin relación con la tesis para enriquecer el texto.", "B": "Sustentar y apoyar la tesis para convencer al lector.", "C": "Describir detalladamente hechos sin expresar una opinión.", "D": "Organizar los párrafos sin conexión directa con la idea central."}, "pregunta": "¿Cuál es la función principal de los argumentos en un texto con finalidad argumentativa?", "explicacion": "La respuesta correcta es B, porque los argumentos en un texto argumentativo sirven para sustentar y fortalecer la tesis, persuadiendo al lector sobre la validez de la postura presentada. La opción A es incorrecta porque los argumentos deben estar relacionados directamente con la tesis, no ser información irrelevante. La opción C confunde la descripción de hechos con la función argumentativa, ya que describir hechos no implica sustentar una tesis. La opción D se refiere a la organización del texto, pero no explica la función de los argumentos en relación con la tesis."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{argumentación,tesis,"organización textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (269, 61, 'multiple_choice', 'all', '{"opciones": {"A": "Presentar datos de manera aleatoria para informar al lector.", "B": "Sustentar y apoyar la tesis propuesta para convencer al lector.", "C": "Describir hechos sin relacionarlos con una idea central.", "D": "Incluir opiniones personales sin necesidad de justificarlas."}, "pregunta": "¿Cuál es la función principal de los argumentos en un texto con finalidad argumentativa?", "explicacion": "La respuesta correcta es B, porque los argumentos sirven para sustentar y apoyar la tesis, con el fin de convencer al lector sobre un punto de vista. La opción A es incorrecta porque los datos deben estar organizados y relacionados con la tesis, no presentados aleatoriamente. La opción C es errónea ya que describir hechos sin vincularlos a una tesis no cumple la función argumentativa. La opción D es un error conceptual porque las opiniones deben estar justificadas para fortalecer la argumentación."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{argumentación,tesis,"estructura textual","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (270, 61, 'true_false', 'all', '{"afirmacion": "Los argumentos en un texto argumentativo sirven para apoyar y justificar la tesis presentada.", "explicacion": "Esta afirmación es verdadera porque la función principal de los argumentos es sustentar la tesis del texto, proporcionando razones y evidencia que la refuercen y hagan convincente para el lector."}', '{"es_verdadero": true}', 2, 0, true, '{argumentos,tesis,"texto argumentativo"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (271, 61, 'drag_drop_matching', 'all', '{"explicacion": "La tesis es la idea central que se defiende; los argumentos son los datos o razones que la sustentan; un hecho es un dato comprobable; la opinión es un juicio personal; y la organización del texto es la forma en que se disponen las ideas para apoyar la tesis.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Idea principal que se defiende en un texto argumentativo", "Información que sustenta o justifica la tesis", "Dato verificable y objetivo", "Juicio personal o punto de vista subjetivo", "Estructura que ordena las ideas para apoyar la tesis"], "columna_izquierda": ["Tesis", "Argumento", "Hecho", "Opinión", "Organización del texto"]}', '{"emparejamientos_correctos": {"Hecho": "Dato verificable y objetivo", "Tesis": "Idea principal que se defiende en un texto argumentativo", "Opinión": "Juicio personal o punto de vista subjetivo", "Argumento": "Información que sustenta o justifica la tesis", "Organización del texto": "Estructura que ordena las ideas para apoyar la tesis"}}', 2, 0, true, '{argumentación,"lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (272, 61, 'sequencing', 'all', '{"explicacion": "El análisis comienza con identificar la tesis principal. Luego, se examinan los argumentos que la sostienen, distinguiendo hechos de opiniones. A continuación, se observa la organización del texto para entender cómo se presentan estos argumentos. Finalmente, se explica cómo la estructura y los argumentos trabajan juntos para apoyar la tesis.", "instruccion": "Ordena los siguientes pasos en la secuencia correcta para analizar un texto argumentativo y explicar cómo los argumentos sustentan la tesis.", "elementos_desordenados": ["Identificar la tesis del texto, que puede ser explícita o implícita", "Examinar los argumentos que apoyan la tesis y diferenciar entre hechos y opiniones", "Determinar la organización del texto y cómo se presentan los argumentos", "Explicar cómo la estructura y los argumentos contribuyen a sustentar la tesis"]}', '{"orden_correcto": ["Identificar la tesis del texto, que puede ser explícita o implícita", "Examinar los argumentos que apoyan la tesis y diferenciar entre hechos y opiniones", "Determinar la organización del texto y cómo se presentan los argumentos", "Explicar cómo la estructura y los argumentos contribuyen a sustentar la tesis"]}', 3, 0, true, '{"análisis de texto",argumentación,"estructura textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (273, 62, 'multiple_choice', 'all', '{"opciones": {"A": "Presentar hechos comprobables para apoyar la tesis", "B": "Invitar al lector a reflexionar sin esperar una respuesta directa", "C": "Expresar una duda real sobre la validez de un argumento", "D": "Mostrar una opinión contraria para generar debate"}, "pregunta": "En un texto argumentativo, ¿cuál es la función principal de las preguntas retóricas utilizadas por el autor?", "explicacion": "La opción B es correcta porque las preguntas retóricas se utilizan para involucrar al lector y hacerle reflexionar sobre un punto, sin esperar una respuesta explícita. La opción A es incorrecta porque las preguntas retóricas no presentan hechos comprobables, sino que generan reflexión. La opción C confunde preguntas retóricas con preguntas dubitativas, que sí expresan duda real. La opción D corresponde más a la estrategia de contrapunto o refutación, no a la función principal de las preguntas retóricas."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"texto argumentativo","preguntas retóricas","intención comunicativa","Lengua y Literatura","1° Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (274, 62, 'multiple_choice', 'all', '{"opciones": {"A": "Presentar una duda real que necesita ser resuelta por el lector.", "B": "Reforzar la tesis al implicar una respuesta que el autor da por sentada.", "C": "Introducir un nuevo argumento basado en hechos verificables.", "D": "Expresar un deseo o esperanza sobre el tema tratado."}, "pregunta": "En un texto argumentativo, ¿cuál es la función principal de las preguntas retóricas utilizadas por el autor?", "explicacion": "La respuesta correcta es la opción B, ya que las preguntas retóricas se utilizan para enfatizar o reforzar una idea, asumiendo que la respuesta es obvia y apoyando la tesis del autor. La opción A es incorrecta porque las preguntas retóricas no buscan una respuesta real, sino provocar reflexión o énfasis. La opción C confunde las preguntas retóricas con la presentación de argumentos basados en hechos; no es su función principal. La opción D corresponde a oraciones desiderativas, no a preguntas retóricas."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"análisis de textos","preguntas retóricas",argumentación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (275, 62, 'drag_drop_matching', 'all', '{"explicacion": "La tesis es la idea central que sostiene el texto; la pregunta retórica enfatiza sin esperar respuesta; las oraciones desiderativas expresan deseos; las dubitativas, incertidumbre; y el hecho es información comprobable. El distractor no corresponde a ningún concepto argumentativo relevante.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Afirmación principal que el autor defiende en un texto argumentativo", "Interrogación que no espera respuesta, usada para enfatizar una idea", "Oración que expresa un deseo o esperanza", "Oración que expresa duda o incertidumbre", "Información comprobable y objetiva", "Oración que expresa una opinión personal sin intención argumentativa"], "columna_izquierda": ["Tesis", "Pregunta retórica", "Oración desiderativa", "Oración dubitativa", "Hecho", "Distractor"]}', '{"emparejamientos_correctos": {"Hecho": "Información comprobable y objetiva", "Tesis": "Afirmación principal que el autor defiende en un texto argumentativo", "Pregunta retórica": "Interrogación que no espera respuesta, usada para enfatizar una idea", "Oración dubitativa": "Oración que expresa duda o incertidumbre", "Oración desiderativa": "Oración que expresa un deseo o esperanza"}}', 3, 0, true, '{"análisis argumentativo","recursos retóricos","primero medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (276, 62, 'sequencing', 'all', '{"explicacion": "Primero se debe leer el texto para comprenderlo en general, luego identificar la tesis para saber qué se está defendiendo. Después, es importante distinguir entre hechos y opiniones para entender la validez de los argumentos. Finalmente, se analizan los recursos usados por el autor para reforzar su intención comunicativa.", "instruccion": "Ordena los siguientes pasos para analizar un texto argumentativo de forma correcta", "elementos_desordenados": ["Identificar si la tesis es explícita o implícita", "Reconocer los recursos argumentativos como preguntas retóricas o oraciones desiderativas", "Leer atentamente el texto para comprender su contenido general", "Distinguir entre hechos y opiniones dentro del texto"]}', '{"orden_correcto": ["Leer atentamente el texto para comprender su contenido general", "Identificar si la tesis es explícita o implícita", "Distinguir entre hechos y opiniones dentro del texto", "Reconocer los recursos argumentativos como preguntas retóricas o oraciones desiderativas"]}', 3, 0, true, '{"análisis de texto",argumentación,"recursos argumentativos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (277, 62, 'open_ended', 'all', '{"pregunta": "Lee el siguiente texto argumentativo y responde: ¿Cuál es la intención comunicativa del autor y cómo utiliza preguntas retóricas y oraciones desiderativas o dubitativas para reforzar su tesis? Explica con ejemplos extraídos del texto.", "explicacion": "El docente debe evaluar si el estudiante identifica correctamente la intención del autor (por ejemplo, persuadir, criticar, informar), reconoce y explica cómo las preguntas retóricas y oraciones desiderativas/dubitativas contribuyen a fortalecer los argumentos y si establece la relación clara entre estos recursos y la tesis presentada.", "contexto_adicional": "Se proporcionará un texto argumentativo breve con una tesis explícita o implícita, que contenga preguntas retóricas y oraciones desiderativas o dubitativas para que el estudiante pueda analizarlos.", "criterios_evaluacion": ["Identificación clara de la intención comunicativa del autor (peso: 30%)", "Análisis del uso de preguntas retóricas y oraciones desiderativas o dubitativas con ejemplos concretos (peso: 40%)", "Capacidad para relacionar estos recursos con la sustentación de la tesis del texto (peso: 30%)"]}', '{"puntos_clave": ["Identificación de la intención persuasiva del autor", "Ejemplos de preguntas retóricas y explicación de su función", "Ejemplos de oraciones desiderativas o dubitativas y su relación con la tesis"], "respuesta_modelo": "La intención comunicativa del autor es persuadir al lector sobre la importancia de cuidar el medio ambiente. Para ello, utiliza preguntas retóricas como ''¿Quién no desea un futuro limpio para nuestros hijos?'' que invitan a la reflexión sin esperar respuesta, reforzando su argumento de responsabilidad compartida. Además, emplea oraciones desiderativas como ''Ojalá todos tomáramos conciencia del daño que causamos'' para expresar un deseo que apoya la urgencia de su tesis. También incluye oraciones dubitativas, por ejemplo, ''Quizás aún estamos a tiempo de cambiar'', que introducen una posibilidad y mantienen la atención del lector sobre la necesidad de actuar. Estos recursos contribuyen a hacer más persuasiva y emotiva la tesis del texto.", "requiere_revision_humana": true}', 3, 0, true, '{"análisis argumentativo","recursos lingüísticos","pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (278, 63, 'compare_contrast', 'all', '{"conceptos": ["Tesis explícita", "Tesis implícita"], "criterios": ["Claridad", "Ubicación en el texto", "Grado de evidencia necesaria"], "explicacion": "La tesis explícita es una afirmación clara y directa que se presenta en el texto, generalmente al inicio, facilitando su identificación y requiriendo evidencia clara para sustentarla. En cambio, la tesis implícita está insinuada o subentendida, no aparece de forma directa, y suele ubicarse de manera dispersa, lo que exige una interpretación más profunda y una evaluación cuidadosa de la evidencia para identificarla.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Tesis explícita": {"Claridad": "Alta, se expresa claramente", "Ubicación en el texto": "Generalmente al inicio o en un párrafo introductorio", "Grado de evidencia necesaria": "Evidencia directa y clara para sostenerla"}, "Tesis implícita": {"Claridad": "Baja, está sugerida o inferida", "Ubicación en el texto": "Distribuida o implícita en diferentes partes", "Grado de evidencia necesaria": "Evidencia interpretativa y análisis profundo para identificarla"}}}', 3, 0, true, '{argumentación,tesis,"análisis de texto"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (279, 63, 'compare_contrast', 'all', '{"conceptos": ["Tesis Explícita", "Tesis Implícita"], "criterios": ["Claridad en la presentación", "Ubicación en el texto", "Dependencia de la interpretación del lector"], "explicacion": "La tesis explícita se presenta de manera clara y directa, generalmente al inicio o cierre del texto, facilitando su identificación. En cambio, la tesis implícita no se expresa directamente y requiere que el lector interprete el conjunto de argumentos para identificarla. Ambas sostienen la argumentación, pero varían en su visibilidad y en el grado de inferencia que demanda el lector.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Tesis Explícita": {"Ubicación en el texto": "Generalmente al inicio o al final", "Claridad en la presentación": "Alta, se expresa claramente", "Dependencia de la interpretación del lector": "Baja, no requiere mucha inferencia"}, "Tesis Implícita": {"Ubicación en el texto": "Distribuida o implícita en el desarrollo", "Claridad en la presentación": "Baja, no se expresa directamente", "Dependencia de la interpretación del lector": "Alta, requiere inferencia y análisis"}}}', 4, 0, true, '{tesis,argumentación,análisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (280, 63, 'open_ended', 'all', '{"pregunta": "Lee dos textos argumentativos sobre el mismo tema (por ejemplo, el uso de redes sociales en adolescentes). Compara la suficiencia y pertinencia de la información que cada autor utiliza para sustentar su tesis. ¿Cuál de los dos textos presenta un argumento más sólido y por qué?", "explicacion": "El docente debe valorar que el estudiante logre identificar correctamente la tesis y los argumentos en cada texto y que analice de forma crítica la calidad y relevancia de la información presentada. La respuesta debe incluir ejemplos específicos que apoyen su juicio sobre cuál texto es más convincente y por qué.", "contexto_adicional": "Se proporcionarán dos textos argumentativos breves sobre un tema actual y relevante para adolescentes, con tesis y argumentos explícitos e implícitos.", "criterios_evaluacion": ["Identificación clara de la tesis y los argumentos en ambos textos (peso: 30%)", "Análisis crítico de la suficiencia y pertinencia de la información presentada en cada texto (peso: 40%)", "Capacidad para justificar con ejemplos concretos cuál texto tiene un argumento más sólido (peso: 30%)"]}', '{"puntos_clave": ["Identificación de la tesis en ambos textos", "Evaluación de la cantidad y calidad de la información en cada texto", "Justificación con ejemplos que evidencien la solidez de uno de los argumentos"], "respuesta_modelo": "En el primer texto, la tesis es que el uso de redes sociales en adolescentes tiene más efectos negativos que positivos, sustentada con datos estadísticos y testimonios de expertos. En el segundo texto, la tesis sostiene que las redes sociales son herramientas valiosas para el aprendizaje y la socialización, pero la información presentada es mayormente anecdótica y carece de respaldo científico. Por lo tanto, el primer texto presenta un argumento más sólido porque utiliza información suficiente y pertinente que apoya claramente su tesis, mientras que el segundo texto no logra sustentar adecuadamente sus afirmaciones.", "requiere_revision_humana": true}', 4, 0, true, '{"análisis de textos",argumentación,"pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (281, 63, 'concept_map', 'all', '{"conceptos": ["Tesis explícita", "Tesis implícita", "Argumentos", "Hechos", "Opiniones", "Pertinencia de la información", "Suficiencia de la información", "Texto argumentativo"], "explicacion": "Se espera que el estudiante identifique cómo la tesis, ya sea explícita o implícita, es sustentada por argumentos que pueden estar basados en hechos u opiniones. Además, debe diferenciar y conectar la suficiencia y pertinencia de la información para evaluar la calidad del texto argumentativo.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis y evaluación de textos argumentativos?"}', '{"conceptos_centrales": ["Tesis explícita", "Argumentos"], "relaciones_esperadas": [{"origen": "Texto argumentativo", "destino": "Tesis explícita", "relacion": "contiene"}, {"origen": "Texto argumentativo", "destino": "Tesis implícita", "relacion": "contiene"}, {"origen": "Tesis explícita", "destino": "Argumentos", "relacion": "es sustentada por"}, {"origen": "Tesis implícita", "destino": "Argumentos", "relacion": "es sustentada por"}, {"origen": "Argumentos", "destino": "Hechos", "relacion": "pueden basarse en"}, {"origen": "Argumentos", "destino": "Opiniones", "relacion": "pueden basarse en"}, {"origen": "Suficiencia de la información", "destino": "Argumentos", "relacion": "evalúa"}, {"origen": "Pertinencia de la información", "destino": "Argumentos", "relacion": "evalúa"}], "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Análisis de textos",Argumentación,"Primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (282, 63, 'concept_map', 'all', '{"conceptos": ["Tesis explícita", "Tesis implícita", "Argumentos", "Hechos", "Opiniones", "Pertinencia de la información", "Suficiencia de la información"], "explicacion": "Se espera que el estudiante identifique que la tesis puede ser explícita o implícita y que para sostenerla se emplean argumentos. Estos argumentos pueden basarse en hechos o en opiniones, por lo que es importante diferenciar entre ambos. Además, el estudiante debe analizar cómo la pertinencia y suficiencia de la información presentada influyen en la solidez de la argumentación y, por ende, en la validez de la tesis.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la evaluación de textos argumentativos?"}', '{"conceptos_centrales": ["Tesis explícita", "Argumentos"], "relaciones_esperadas": [{"origen": "Tesis explícita", "destino": "Tesis", "relacion": "es una forma de"}, {"origen": "Tesis implícita", "destino": "Tesis", "relacion": "es una forma de"}, {"origen": "Argumentos", "destino": "Tesis explícita", "relacion": "sostienen"}, {"origen": "Argumentos", "destino": "Tesis implícita", "relacion": "sostienen"}, {"origen": "Argumentos", "destino": "Hechos", "relacion": "se basan en"}, {"origen": "Argumentos", "destino": "Opiniones", "relacion": "se basan en"}, {"origen": "Pertinencia de la información", "destino": "Argumentos", "relacion": "evalúa"}, {"origen": "Suficiencia de la información", "destino": "Argumentos", "relacion": "evalúa"}], "requiere_revision_humana": true}', 4, 0, true, '{lengua,literatura,argumentacion,"primero medio",analisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (283, 64, 'criteria_evaluation', 'all', '{"rubrica": {"Claridad y precisión de la tesis": {"Excelente": "Identifica claramente la tesis, ya sea explícita o implícita, y explica con precisión su significado en el contexto del texto.", "Satisfactorio": "Reconoce la tesis, aunque la explicación puede ser general o incompleta.", "Necesita mejorar": "No logra identificar o confunde la tesis del texto."}, "Distinción entre hechos y opiniones": {"Excelente": "Distingue claramente entre hechos verificables y opiniones del autor, justificando con ejemplos del texto.", "Satisfactorio": "Identifica algunos hechos y opiniones, aunque la distinción puede ser imprecisa.", "Necesita mejorar": "No diferencia adecuadamente entre hechos y opiniones en el texto."}, "Validez y relevancia de los argumentos": {"Excelente": "Analiza críticamente los argumentos presentados, evaluando su fuerza y relevancia para sostener la tesis.", "Satisfactorio": "Reconoce algunos argumentos y su relación con la tesis, pero el análisis es superficial.", "Necesita mejorar": "No identifica o evalúa correctamente los argumentos del texto."}, "Fundamentación de la opinión personal": {"Excelente": "Emite una opinión propia bien fundamentada, apoyándose en el análisis crítico del texto.", "Satisfactorio": "Presenta una opinión personal, pero con fundamentos limitados o poco claros.", "Necesita mejorar": "No emite una opinión o la fundamentación es insuficiente o irrelevante."}}, "explicacion": "Se espera que el estudiante identifique la tesis de forma clara, analice críticamente los argumentos presentados, distinga correctamente entre hechos y opiniones, y fundamente su propia opinión con base en el análisis realizado.", "instruccion": "Evalúa la columna de opinión utilizando los criterios de la rúbrica y justifica tu calificación para cada uno de ellos. Finalmente, emite tu opinión personal fundamentada sobre la postura del autor.", "caso_a_evaluar": "Lee la columna de opinión titulada ''El impacto de las redes sociales en la juventud'' donde el autor presenta su postura sobre cómo las redes sociales afectan el bienestar emocional de los jóvenes. Luego, evalúa críticamente la postura del autor considerando la claridad de la tesis, la validez de los argumentos y la distinción entre hechos y opiniones en el texto."}', '{"evaluacion_modelo": {"Claridad y precisión de la tesis": "Excelente", "Distinción entre hechos y opiniones": "Excelente", "Validez y relevancia de los argumentos": "Satisfactorio", "Fundamentación de la opinión personal": "Satisfactorio"}, "justificacion_modelo": "El estudiante identificó con claridad la tesis explícita del autor y explicó su significado adecuadamente. Analizó algunos argumentos pero sin profundizar en todos, por lo que la validez fue valorada como satisfactoria. Distinguió correctamente entre hechos y opiniones con ejemplos claros. Su opinión personal fue pertinente pero con fundamentos limitados, justificando la calificación asignada.", "requiere_revision_humana": true}', 4, 0, true, '{"evaluación crítica","textos argumentativos","opinión fundamentada","primer medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (284, 64, 'criteria_evaluation', 'all', '{"rubrica": {"Claridad y explicitud de la tesis": {"Excelente": "La tesis está claramente formulada y es explícita desde el inicio del texto.", "Satisfactorio": "La tesis se puede identificar, pero está implícita o poco clara en algunos pasajes.", "Necesita mejorar": "La tesis es confusa o inexistente, dificultando la comprensión de la postura del autor."}, "Diferenciación entre hecho y opinión": {"Excelente": "El autor distingue claramente entre hechos verificables y opiniones personales, apoyando su postura con información objetiva.", "Satisfactorio": "Hay una diferenciación parcial entre hechos y opiniones, aunque en algunos casos se confunden.", "Necesita mejorar": "No se distingue entre hechos y opiniones, lo que afecta la credibilidad del texto."}, "Solidez y pertinencia de los argumentos": {"Excelente": "Los argumentos son sólidos, pertinentes y están bien fundamentados con ejemplos o datos claros.", "Satisfactorio": "Los argumentos son adecuados pero algunos carecen de fundamentación o ejemplos concretos.", "Necesita mejorar": "Los argumentos son débiles, poco pertinentes o carecen de respaldo adecuado."}, "Fundamentación de la valoración personal": {"Excelente": "La valoración personal está claramente fundamentada con referencias al texto y argumentos propios bien desarrollados.", "Satisfactorio": "La valoración está presente pero se basa en afirmaciones generales sin suficiente fundamentación.", "Necesita mejorar": "La valoración personal es vaga, poco clara o no está fundamentada en el texto leído."}}, "explicacion": "Se espera que el estudiante identifique con precisión la tesis del texto, analice la calidad y pertinencia de los argumentos, distinga entre hechos y opiniones, y fundamente su propia valoración crítica con argumentos claros y coherentes.", "instruccion": "Evalúa la columna de opinión según los criterios de la rúbrica y justifica tu calificación para cada uno.", "caso_a_evaluar": "Lee la columna de opinión titulada ''El impacto de las redes sociales en la juventud'', donde el autor presenta su postura sobre cómo estas plataformas afectan el desarrollo social y emocional de los jóvenes. Evalúa críticamente la postura del autor considerando la claridad de la tesis, la solidez de los argumentos y el uso correcto de hechos e información."}', '{"evaluacion_modelo": {"Claridad y explicitud de la tesis": "Excelente", "Diferenciación entre hecho y opinión": "Excelente", "Solidez y pertinencia de los argumentos": "Satisfactorio", "Fundamentación de la valoración personal": "Satisfactorio"}, "justificacion_modelo": "La tesis está claramente expresada desde el principio, y el autor distingue bien entre hechos y opiniones. Sin embargo, algunos argumentos carecen de ejemplos concretos o evidencia sólida. La valoración personal está presente pero podría estar más desarrollada y fundamentada con referencias precisas al texto.", "requiere_revision_humana": true}', 5, 0, true, '{"evaluación crítica",argumentación,"columna de opinión","primer medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (285, 64, 'criteria_evaluation', 'all', '{"rubrica": {"Diferenciación entre hecho y opinión": {"Excelente": "Distingue correctamente entre hechos y opiniones dentro del texto, explicando cómo cada uno apoya la tesis.", "Satisfactorio": "Reconoce algunos hechos y opiniones, pero no siempre logra diferenciarlos correctamente.", "Necesita mejorar": "Confunde hechos con opiniones o no identifica ninguno claramente."}, "Identificación de la tesis y argumentos": {"Excelente": "Identifica claramente la tesis, tanto si es explícita o implícita, y reconoce todos los principales argumentos y evidencias que la sostienen.", "Satisfactorio": "Identifica la tesis y algunos argumentos, pero omite detalles importantes o confunde algún aspecto.", "Necesita mejorar": "No logra identificar la tesis o los argumentos principales del texto."}, "Valoración crítica de la postura del autor": {"Excelente": "Emite una opinión fundamentada, señalando fortalezas y debilidades en la argumentación del autor, con un análisis profundo.", "Satisfactorio": "Da una opinión sobre la postura del autor, pero con justificaciones limitadas o poco detalladas.", "Necesita mejorar": "No logra valorar críticamente la postura o su opinión carece de fundamentación."}}, "explicacion": "Se espera que el estudiante identifique la tesis y argumentos, distinga correctamente entre hechos y opiniones, y que valore críticamente la postura del autor con argumentos propios bien fundamentados.", "instruccion": "Evalúa la columna de opinión según la rúbrica presentada y justifica tu calificación para cada criterio con argumentos claros y ejemplos del texto.", "caso_a_evaluar": "Lee el siguiente texto argumentativo: una columna de opinión sobre la importancia de la educación ambiental en las escuelas, en la que el autor presenta su tesis de forma implícita y utiliza datos estadísticos y ejemplos para apoyar su postura."}', '{"evaluacion_modelo": {"Diferenciación entre hecho y opinión": "Satisfactorio", "Identificación de la tesis y argumentos": "Excelente", "Valoración crítica de la postura del autor": "Excelente"}, "justificacion_modelo": "El estudiante identificó claramente la tesis implícita y los argumentos usados, aunque confundió algún hecho con opinión. Además, realizó un análisis crítico profundo, señalando tanto aspectos positivos como limitaciones en la argumentación del autor.", "requiere_revision_humana": true}', 5, 0, true, '{"evaluación argumentativa","primero medio","lengua y literatura","análisis crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (286, 64, 'open_ended', 'all', '{"pregunta": "Después de leer la columna de opinión proporcionada, ¿cómo valorarías la postura del autor respecto al tema tratado? Analiza los argumentos que presenta, distingue entre hechos y opiniones, y emite tu propia opinión fundamentada sobre la validez y coherencia de su posición.", "explicacion": "El docente debe evaluar que el estudiante sea capaz de identificar claramente la tesis y los argumentos del autor, reconociendo qué elementos son hechos y cuáles opiniones. Además, debe valorar que el estudiante emita una opinión propia que esté bien sustentada y que demuestre una reflexión crítica sobre la postura presentada en la columna.", "contexto_adicional": "Se entrega una columna de opinión sobre un tema de actualidad social o cultural relevante para el curso.", "criterios_evaluacion": ["Análisis crítico de la postura del autor, identificando tesis y argumentos principales (peso: 30%)", "Capacidad para distinguir entre hechos y opiniones en el texto argumentativo (peso: 40%)", "Emisión de una opinión personal fundamentada, coherente y bien argumentada sobre la postura del autor (peso: 30%)"]}', '{"puntos_clave": ["Identificación clara de la tesis y argumentos principales del autor", "Diferenciación precisa entre hechos y opiniones en el texto", "Opinión personal fundamentada y coherente respecto a la postura del autor"], "respuesta_modelo": "La columna de opinión aborda el tema de la educación inclusiva. El autor sostiene que es fundamental implementarla para garantizar igualdad de oportunidades, apoyando su postura con datos estadísticos sobre el acceso a la educación y testimonios de estudiantes con necesidades especiales. Identifico que los hechos presentados, como las cifras oficiales, respaldan su tesis, mientras que las valoraciones sobre los beneficios sociales son opiniones. Personalmente, coincido con la importancia de la educación inclusiva, pero considero que también se deben mejorar los recursos y la formación docente para que esta sea efectiva, lo que el autor no aborda en profundidad. En general, la postura del autor es válida y coherente, aunque podría enriquecerse con un análisis de los desafíos prácticos.", "requiere_revision_humana": true}', 4, 0, true, '{argumentacion,evaluacion_critica,escritura_creativa}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (287, 64, 'open_ended', 'all', '{"pregunta": "Después de leer la columna de opinión proporcionada, evalúa críticamente la postura del autor. ¿Estás de acuerdo o en desacuerdo con su tesis? Fundamenta tu respuesta, identificando al menos dos argumentos que sostienen la tesis y analiza si estos son convincentes o no. Además, menciona si el autor diferencia adecuadamente entre hechos y opiniones y cómo esto afecta la solidez del texto.", "explicacion": "El docente debe evaluar la capacidad del estudiante para reconocer la tesis y argumentos en un texto argumentativo, distinguir entre hechos y opiniones, y emitir un juicio crítico personal fundamentado. Se valorará la profundidad del análisis y la claridad en la expresión escrita.", "contexto_adicional": "Se proporcionará una columna de opinión breve relacionada con un tema de actualidad o interés social para Primero Medio.", "criterios_evaluacion": ["Identificación clara y precisa de la tesis y los argumentos principales del autor (peso: 30%)", "Análisis crítico de la validez y pertinencia de los argumentos, incluyendo la distinción entre hechos y opiniones (peso: 40%)", "Emisión de una opinión fundamentada y coherente respecto a la postura del autor, con un lenguaje claro y estructurado (peso: 30%)"]}', '{"puntos_clave": ["Tesis explícita o implícita correctamente identificada", "Al menos dos argumentos principales señalados y analizados", "Evaluación de la distinción entre hechos y opiniones en el texto", "Opinión personal fundamentada y coherente"], "respuesta_modelo": "La columna de opinión sostiene que el uso excesivo de dispositivos móviles afecta negativamente el rendimiento académico de los jóvenes. La tesis es clara y está explícita en el texto. Dos argumentos que la sustentan son: primero, que el tiempo dedicado a los dispositivos reduce el tiempo de estudio; segundo, que la distracción constante disminuye la concentración. Ambos argumentos son convincentes porque están respaldados por datos estadísticos y ejemplos concretos. El autor distingue adecuadamente entre hechos, como las estadísticas de uso, y opiniones, como la valoración del impacto en la concentración, lo que fortalece la credibilidad del texto. En general, estoy de acuerdo con la postura porque los argumentos son sólidos y reflejan una realidad observable, aunque creo que también debería considerar el uso positivo de la tecnología en el aprendizaje.", "requiere_revision_humana": true}', 5, 0, true, '{evaluación,"pensamiento crítico",argumentación,"lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (288, 65, 'open_ended', 'all', '{"pregunta": "Elabora un texto argumentativo sobre un tema de actualidad que te interese, en el que presentes una tesis clara, al menos tres argumentos pertinentes y utilices recursos discursivos que refuercen tu postura. ¿Cómo justificas tu postura frente a posibles opiniones contrarias?", "explicacion": "El docente evaluará si el estudiante logra construir una tesis explícita y clara, si los argumentos son pertinentes y están bien desarrollados para sostener la tesis, y si utiliza adecuadamente recursos discursivos (como ejemplos, preguntas retóricas o anticipación a contraargumentos) que fortalezcan su postura y la argumentación en general.", "contexto_adicional": "El estudiante debe seleccionar un tema de actualidad que le interese y redactar un texto argumentativo original, enfocándose en la estructura y en la calidad del razonamiento.", "criterios_evaluacion": ["Claridad y precisión de la tesis presentada (peso: 30%)", "Pertinencia y desarrollo coherente de los argumentos que sustentan la tesis (peso: 40%)", "Uso adecuado de recursos discursivos para persuadir y anticipar contraargumentos (peso: 30%)"]}', '{"puntos_clave": ["Presentación clara y explícita de la tesis", "Desarrollo de al menos tres argumentos pertinentes y coherentes", "Incorporación de recursos discursivos que refuercen la argumentación y anticipen contraargumentos"], "respuesta_modelo": "Tema: La importancia de implementar la educación ambiental en los colegios.\n\nTesis: Creo firmemente que la educación ambiental debe ser parte fundamental del currículo escolar porque fomenta el respeto por la naturaleza, promueve hábitos sostenibles y prepara a las nuevas generaciones para enfrentar desafíos ecológicos.\n\nArgumentos:\n1. La educación ambiental genera conciencia sobre los problemas ecológicos actuales, ayudando a formar ciudadanos responsables.\n2. Promueve acciones concretas, como el reciclaje y el ahorro de recursos, que pueden ser aplicadas en la vida diaria.\n3. Prepara a los estudiantes para tomar decisiones informadas frente a desafíos futuros como el cambio climático.\n\nRecursos discursivos: Empleo preguntas retóricas para involucrar al lector («¿Queremos seguir dañando nuestro planeta?»), uso de ejemplos concretos de iniciativas escolares exitosas y anticipación a contraargumentos, señalando que algunos creen que esto distrae de otras materias, pero que en realidad complementa el aprendizaje integral.\n\nJustificación ante opiniones contrarias: Reconozco que hay quienes consideran que el tiempo dedicado a la educación ambiental podría restar a otras asignaturas, sin embargo, sostengo que la formación en valores y conocimiento ambiental es esencial para el desarrollo integral y la responsabilidad social.", "requiere_revision_humana": true}', 4, 0, true, '{argumentación,creación,"texto argumentativo","primero medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (289, 65, 'open_ended', 'all', '{"pregunta": "Elabora un texto argumentativo en el que definas tu postura sobre el impacto de las redes sociales en la comunicación entre jóvenes. Asegúrate de incluir una tesis clara, al menos tres argumentos sólidos que la respalden y el uso adecuado de recursos discursivos para persuadir a tu lector.", "explicacion": "El docente debe evaluar si el estudiante logra exponer una tesis explícita y clara, si los argumentos que utiliza son pertinentes y están bien desarrollados para apoyar su postura, y si emplea recursos discursivos que mejoren la persuasión y coherencia del texto. Es importante también valorar la organización y cohesión del texto argumentativo.", "contexto_adicional": "Considera que tu texto será leído por compañeros de curso con distintos puntos de vista sobre el uso de redes sociales.", "criterios_evaluacion": ["Claridad y relevancia de la tesis presentada (peso: 30%)", "Pertinencia, coherencia y profundidad de los argumentos usados para sustentar la tesis (peso: 40%)", "Uso adecuado y efectivo de recursos discursivos (como conectores, ejemplos, preguntas retóricas) para fortalecer el texto (peso: 30%)"]}', '{"puntos_clave": ["Tesis clara y explícita sobre el impacto de las redes sociales", "Tres argumentos pertinentes y bien desarrollados que apoyan la tesis", "Empleo de recursos discursivos como conectores, ejemplos y preguntas retóricas"], "respuesta_modelo": "Las redes sociales tienen un impacto significativo en la comunicación entre jóvenes, y considero que, en general, este impacto es negativo. Primero, las redes sociales pueden reducir la calidad de las interacciones personales, pues privilegian la comunicación virtual sobre el contacto cara a cara, lo que afecta habilidades sociales básicas. Segundo, fomentan la superficialidad, ya que la comunicación se centra en mensajes breves y muchas veces poco profundos, generando malentendidos. Tercero, las redes sociales pueden aumentar la ansiedad y la presión social al estar constantemente expuestos a la opinión de otros, lo que afecta la autoestima. Por estas razones, creo que el uso excesivo de redes sociales limita el desarrollo de una comunicación auténtica y saludable entre los jóvenes. Es fundamental reflexionar sobre nuestro uso para buscar un equilibrio que permita aprovechar sus beneficios sin perder la calidad en nuestras relaciones personales.", "requiere_revision_humana": true}', 5, 0, true, '{"texto argumentativo","redes sociales","pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (290, 65, 'concept_map', 'all', '{"conceptos": ["Tesis", "Argumentos", "Hecho", "Opinión", "Recursos discursivos", "Texto argumentativo", "Persuasión"], "explicacion": "El mapa conceptual debe mostrar que la tesis es la idea central de un texto argumentativo, sustentada por argumentos que pueden basarse en hechos o en opiniones. Los recursos discursivos se utilizan para fortalecer la persuasión del texto y conectar los argumentos con la tesis. Además, debe evidenciar la diferencia entre hecho y opinión como base para la credibilidad del texto.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la elaboración de un texto argumentativo?"}', '{"conceptos_centrales": ["Tesis", "Argumentos"], "relaciones_esperadas": [{"origen": "Texto argumentativo", "destino": "Tesis", "relacion": "incluye"}, {"origen": "Tesis", "destino": "Argumentos", "relacion": "es sustentada por"}, {"origen": "Argumentos", "destino": "Hecho", "relacion": "se basan en"}, {"origen": "Argumentos", "destino": "Opinión", "relacion": "se basan en"}, {"origen": "Recursos discursivos", "destino": "Persuasión", "relacion": "fortalecen"}, {"origen": "Persuasión", "destino": "Tesis", "relacion": "apoya"}, {"origen": "Hecho", "destino": "Opinión", "relacion": "se diferencia de"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Texto Argumentativo","Mapa Conceptual","Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (291, 65, 'concept_map', 'all', '{"conceptos": ["Tesis", "Argumentos", "Hechos", "Opiniones", "Recursos discursivos", "Texto argumentativo"], "explicacion": "El mapa debe mostrar que la tesis es la idea central del texto argumentativo, sustentada por argumentos que pueden incluir hechos y opiniones. Además, los recursos discursivos se utilizan para fortalecer la presentación y persuasión del texto.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la elaboración de un texto argumentativo?"}', '{"conceptos_centrales": ["Texto argumentativo", "Tesis"], "relaciones_esperadas": [{"origen": "Texto argumentativo", "destino": "Tesis", "relacion": "contiene"}, {"origen": "Texto argumentativo", "destino": "Argumentos", "relacion": "incluye"}, {"origen": "Argumentos", "destino": "Hechos", "relacion": "se sustentan en"}, {"origen": "Argumentos", "destino": "Opiniones", "relacion": "pueden contener"}, {"origen": "Texto argumentativo", "destino": "Recursos discursivos", "relacion": "utiliza"}, {"origen": "Recursos discursivos", "destino": "Argumentos", "relacion": "fortalecen"}, {"origen": "Tesis", "destino": "Argumentos", "relacion": "es defendida por"}], "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Argumentación,"Mapa conceptual","Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (292, 65, 'open_ended', 'all', '{"pregunta": "Elabora un texto argumentativo sobre un tema de actualidad que te interese, en el que presentes una tesis clara, al menos tres argumentos sólidos que la respalden, y utilices recursos discursivos adecuados para persuadir a tu audiencia. Explica también por qué elegiste ese tema y cómo tus argumentos contribuyen a fortalecer tu postura.", "explicacion": "Se evaluará si el estudiante es capaz de formular una tesis explícita y clara, seleccionar y desarrollar argumentos coherentes y relevantes, y emplear recursos discursivos (como ejemplos, comparaciones, preguntas retóricas) que refuercen su postura. También se considerará la organización y cohesión del texto para asegurar una comunicación persuasiva efectiva.", "contexto_adicional": "El estudiante debe elegir un tema actual que le interese personalmente y desarrollar un texto argumentativo completo, demostrando su capacidad para crear y estructurar una argumentación sólida.", "criterios_evaluacion": ["Claridad y pertinencia de la tesis presentada (peso: 30%)", "Coherencia, relevancia y desarrollo de los argumentos que respaldan la tesis (peso: 40%)", "Uso adecuado de recursos discursivos y organización del texto para persuadir al lector (peso: 30%)"]}', '{"puntos_clave": ["Tesis explícita y clara relacionada con un tema actual", "Tres argumentos desarrollados y relevantes que apoyan la tesis", "Uso efectivo de recursos discursivos y estructura coherente del texto"], "respuesta_modelo": "Tema elegido: La importancia de implementar educación ambiental en los colegios. Tesis: Es fundamental incorporar educación ambiental en el currículo escolar para formar ciudadanos conscientes y responsables con el medio ambiente. Argumentos: 1) La educación ambiental promueve hábitos sostenibles desde la infancia, lo que contribuye a la protección del planeta a largo plazo. 2) Al entender los problemas ambientales, los estudiantes pueden tomar decisiones informadas que impactan positivamente en su comunidad. 3) Fomenta el pensamiento crítico y la responsabilidad social, competencias esenciales para el desarrollo personal y colectivo. Recursos discursivos: uso de ejemplos concretos de programas exitosos, preguntas retóricas para involucrar al lector, y una estructura clara con introducción, desarrollo y conclusión. Explicación: Elegí este tema porque el cambio climático y la contaminación son problemas urgentes que afectan a todos. Mis argumentos demuestran cómo la educación puede ser una herramienta clave para enfrentar estos desafíos.", "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,primer_medio,texto_argumentativo,creacion}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (293, 66, 'multiple_choice', 'all', '{"opciones": {"A": "Un artículo que describe los acontecimientos de un partido de fútbol reciente.", "B": "Un mensaje escrito por un lector que expresa su opinión sobre un tema de actualidad y se envía al periódico para ser publicado.", "C": "Un texto publicitario que intenta convencer al lector de comprar un producto.", "D": "Un informe detallado que investiga las causas y consecuencias de una protesta social."}, "pregunta": "¿Cuál de los siguientes textos corresponde a una carta al director en un medio de comunicación?", "explicacion": "La opción B es correcta porque una carta al director es un texto enviado por un lector al medio de comunicación para expresar su opinión sobre un tema. La opción A describe una crónica o noticia deportiva, la opción C corresponde a propaganda o publicidad, y la opción D es un reportaje o artículo de investigación. Estas confusiones son comunes al identificar los tipos de textos de medios."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"Lengua y Literatura","Textos de medios","Primero Medio","Identificación de textos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (294, 66, 'multiple_choice', 'all', '{"opciones": {"A": "Una noticia", "B": "Una carta al director", "C": "Una propaganda", "D": "Una crónica"}, "pregunta": "¿Cuál de los siguientes textos de medios de comunicación tiene como propósito principal informar de manera objetiva sobre un hecho reciente?", "explicacion": "La respuesta correcta es A: Una noticia. Las noticias tienen como propósito principal informar de manera objetiva y rápida sobre hechos recientes. La carta al director (B) es un texto de opinión dirigido a un medio, la propaganda (C) busca persuadir, y la crónica (D), aunque también informa, suele incluir la opinión y descripción personal del autor, por lo que no se enfoca en la objetividad pura como la noticia."}', '{"respuesta_correcta": "A"}', 2, 0, true, '{"textos de medios",noticias,"objetivo del texto"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (295, 66, 'multiple_choice', 'all', '{"opciones": {"A": "Un texto que informa sobre un evento reciente, presentando hechos y declaraciones de testigos.", "B": "Un texto donde un lector expresa su opinión o preocupación sobre un tema de interés público dirigido al editor del medio.", "C": "Un texto diseñado para promocionar un producto o servicio mediante mensajes persuasivos y atractivos.", "D": "Un texto que narra de forma detallada un acontecimiento desde el punto de vista del periodista, incluyendo contexto y análisis."}, "pregunta": "¿Cuál de los siguientes textos corresponde a una carta al director de un medio de comunicación?", "explicacion": "La opción B es correcta porque una carta al director es un texto donde un lector comunica sus opiniones o preocupaciones directamente al editor o director del medio. La opción A describe una noticia, la C corresponde a propaganda y la D a un reportaje o crónica, que son otros tipos de textos de medios de comunicación."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"Lengua y Literatura","Medios de Comunicación","Tipos de Textos","Primer Año Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (296, 66, 'true_false', 'all', '{"afirmacion": "Una noticia es un tipo de texto de medios de comunicación que informa hechos recientes de manera objetiva.", "explicacion": "Esta afirmación es verdadera porque las noticias tienen como propósito principal informar al público sobre hechos actuales, presentándolos de manera clara y objetiva, sin incluir opiniones personales."}', '{"es_verdadero": true}', 1, 0, true, '{noticias,"medios de comunicación","tipos de texto"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (297, 66, 'fill_blanks', 'all', '{"texto": "En los medios de comunicación, un texto que informa sobre hechos actuales y relevantes se llama [BLANK_1], mientras que un texto que expresa opiniones de los lectores al medio es una [BLANK_2].", "pistas": ["Es un tipo de texto periodístico que busca informar hechos recientes.", "Este texto es una forma de comunicación del público hacia el medio, generalmente con opiniones o quejas."], "explicacion": "Los textos de medios de comunicación incluyen diversos tipos, donde la noticia presenta información objetiva y actual, y la carta al director es un espacio donde los lectores pueden expresar sus opiniones o comentarios."}', '{"respuestas_correctas": {"BLANK_1": ["noticia", "noticias"], "BLANK_2": ["carta al director", "cartas al director"]}}', 2, 0, true, '{"textos de medios","tipos de textos","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (298, 67, 'multiple_choice', 'all', '{"opciones": {"A": "Expresar satisfacción por las medidas tomadas por las autoridades y felicitar su trabajo.", "B": "Informar sobre un problema comunitario y motivar a las autoridades a actuar para solucionarlo.", "C": "Describir un evento histórico relacionado con el desarrollo urbano de la ciudad.", "D": "Promover el uso exclusivo de automóviles particulares para mejorar la movilidad."}, "pregunta": "Lee el siguiente extracto de una carta al director publicada en un diario local:\n\n\"Es preocupante cómo el aumento del tráfico ha afectado la calidad de vida en nuestra comunidad. Las calles están más congestionadas y el aire más contaminado. Es hora de que las autoridades tomen medidas urgentes para mejorar el transporte público y fomentar el uso de bicicletas.\"\n\n¿Cuál es el propósito explícito e implícito de este texto?\n", "explicacion": "La respuesta correcta es B, porque el texto explícitamente describe un problema (aumento del tráfico y contaminación) y de forma implícita busca que las autoridades tomen acciones para resolverlo, motivando un cambio. La opción A es incorrecta porque no expresa satisfacción ni felicita a nadie. La opción C no corresponde ya que no se habla de un evento histórico sino de una situación actual. La opción D es errónea porque el texto propone fomentar el uso de transporte público y bicicletas, no automóviles particulares."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"análisis textual","propósito implícito","propósito explícito","medios de comunicación"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (299, 67, 'multiple_choice', 'all', '{"opciones": {"A": "Informar objetivamente sobre los datos científicos del cambio climático sin emitir juicios.", "B": "Expresar la preocupación por el impacto del cambio climático y persuadir a los lectores para que apoyen acciones que ayuden a proteger la agricultura.", "C": "Criticar a los agricultores por no adaptarse a las nuevas condiciones climáticas.", "D": "Relatar una historia sobre cómo el cambio climático afecta solo a una región específica sin intención de generar conciencia."}, "pregunta": "En un artículo de opinión sobre los efectos del cambio climático en la agricultura, el autor señala que \"es urgente tomar medidas para proteger los cultivos y garantizar la seguridad alimentaria\". ¿Cuál es el propósito explícito e implícito de este texto?", "explicacion": "La opción B es correcta porque el texto explícitamente expresa preocupación por el impacto del cambio climático y busca persuadir a los lectores para que apoyen medidas de protección. La opción A es incorrecta porque el texto no es solo informativo, sino que tiene un propósito persuasivo. La opción C representa un error conceptual común al interpretar críticas no presentes en el texto. La opción D confunde un relato descriptivo sin intención con un texto que busca generar conciencia, lo que no corresponde al caso."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"análisis textual","propósito del texto","medios de comunicación"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (300, 67, 'true_false', 'all', '{"afirmacion": "Los propósitos explícitos de un texto en los medios de comunicación son aquellos que se expresan de manera clara y directa en el contenido.", "explicacion": "Esta afirmación es verdadera porque los propósitos explícitos se refieren a las intenciones que el autor comunica de forma evidente, sin necesidad de interpretación adicional, como informar, persuadir o entretener."}', '{"es_verdadero": true}', 2, 0, true, '{propósitos,texto,"medios de comunicación",comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (301, 67, 'drag_drop_matching', 'all', '{"explicacion": "El propósito explícito es el mensaje claro y directo, mientras que el implícito es lo que se sugiere. La noticia informa objetivamente, la carta al director expresa opinión personal, y la propaganda busca persuadir. El reportaje es un distractor que no corresponde con las definiciones principales.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Mensaje claro y directo que el autor quiere comunicar", "Intención o mensaje que no se dice abiertamente, pero se sugiere", "Texto informativo que presenta hechos actuales de manera objetiva", "Texto donde un lector expresa su opinión o reclamo hacia un medio", "Texto que busca persuadir o influir en la opinión o comportamiento", "Texto narrativo que detalla un suceso con profundidad y contexto"], "columna_izquierda": ["Propósito explícito", "Propósito implícito", "Noticia", "Carta al director", "Propaganda", "Distractor: Reportaje"]}', '{"emparejamientos_correctos": {"Noticia": "Texto informativo que presenta hechos actuales de manera objetiva", "Propaganda": "Texto que busca persuadir o influir en la opinión o comportamiento", "Carta al director": "Texto donde un lector expresa su opinión o reclamo hacia un medio", "Propósito explícito": "Mensaje claro y directo que el autor quiere comunicar", "Propósito implícito": "Intención o mensaje que no se dice abiertamente, pero se sugiere"}}', 2, 0, true, '{analisis_textual,medios_de_comunicacion,propósitos_textuales}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (302, 67, 'sequencing', 'all', '{"explicacion": "El orden comienza con una lectura completa para entender el texto, luego se identifica el propósito explícito que es lo que el texto dice abiertamente. Después, se analizan las señales que indican un propósito implícito, que no está expresado directamente. A partir de esto, se determina el propósito implícito y finalmente se concluye cómo ambos propósitos influyen en la intención del mensaje.", "instruccion": "Ordena los siguientes pasos para identificar los propósitos explícitos e implícitos en un texto de los medios de comunicación.", "elementos_desordenados": ["Leer detenidamente el texto completo.", "Identificar el propósito explícito del texto.", "Analizar las palabras o frases que sugieren intenciones ocultas.", "Determinar el propósito implícito basado en el análisis.", "Concluir cómo los propósitos explícito e implícito afectan el mensaje."]}', '{"orden_correcto": ["Leer detenidamente el texto completo.", "Identificar el propósito explícito del texto.", "Analizar las palabras o frases que sugieren intenciones ocultas.", "Determinar el propósito implícito basado en el análisis.", "Concluir cómo los propósitos explícito e implícito afectan el mensaje."]}', 3, 0, true, '{"análisis textual","propósitos del texto","ordenar secuencia"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (303, 68, 'multiple_choice', 'all', '{"opciones": {"A": "Uso de estereotipos", "B": "Apelación a los sentimientos", "C": "Uso del humor", "D": "Presentación de datos estadísticos"}, "pregunta": "En un anuncio publicitario que muestra a una familia feliz usando un producto para el hogar, ¿qué estrategia de persuasión está siendo utilizada principalmente?", "explicacion": "La opción correcta es B, apelación a los sentimientos, porque el anuncio busca generar una conexión emocional positiva mostrando una familia feliz para motivar la compra del producto. La opción A, uso de estereotipos, sería correcta si se enfatizara un grupo social específico de forma simplificada o prejuiciosa. La opción C, uso del humor, implicaría que el anuncio busca hacer reír o usar bromas, lo cual no ocurre aquí. La opción D, presentación de datos estadísticos, no aplica porque no se muestran datos o cifras en el anuncio."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"estrategias de persuasión","medios de comunicación","apelación a los sentimientos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (304, 68, 'multiple_choice', 'all', '{"opciones": {"A": "Uso del humor para captar la atención del público.", "B": "Apelación a los sentimientos mostrando felicidad y bienestar.", "C": "Presentación de estadísticas para demostrar eficacia.", "D": "Uso de estereotipos negativos para crear rechazo hacia la competencia."}, "pregunta": "En un anuncio publicitario que muestra a un grupo de personas muy felices usando un producto para el cuidado de la piel, ¿qué estrategia de persuasión está principalmente siendo utilizada?", "explicacion": "La respuesta correcta es la B, porque mostrar a personas felices usando un producto apela a los sentimientos del público, sugiriendo que el producto genera bienestar y felicidad. La opción A es incorrecta porque no se menciona ningún elemento humorístico. La opción C es errónea ya que no se presentan datos o estadísticas. La opción D es incorrecta porque no se evidencia una crítica ni estereotipos negativos hacia otras marcas."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"estrategias de persuasión","medios de comunicación","lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (305, 68, 'drag_drop_matching', 'all', '{"explicacion": "El humor utiliza bromas para atraer; los estereotipos generalizan grupos; la apelación a sentimientos busca provocar emociones; los datos estadísticos respaldan argumentos; los testimonios son relatos personales que apoyan; las promesas exageradas intentan convencer con garantías poco realistas.", "instruccion": "Empareja cada estrategia de persuasión con su ejemplo correspondiente en textos de medios de comunicación.", "columna_derecha": ["Uso de chistes o bromas para hacer que el mensaje sea más atractivo", "Generalizar características sobre un grupo de personas para influir en la opinión", "Generar emociones como tristeza o alegría para persuadir al lector", "Presentar cifras para dar respaldo a un argumento", "Relato personal de alguien que apoya el producto o idea", "Garantizar resultados imposibles para convencer al público"], "columna_izquierda": ["Humor", "Estereotipos", "Apelación a los sentimientos", "Datos estadísticos", "Testimonios", "Promesas exageradas"]}', '{"emparejamientos_correctos": {"Humor": "Uso de chistes o bromas para hacer que el mensaje sea más atractivo", "Testimonios": "Relato personal de alguien que apoya el producto o idea", "Estereotipos": "Generalizar características sobre un grupo de personas para influir en la opinión", "Datos estadísticos": "Presentar cifras para dar respaldo a un argumento", "Promesas exageradas": "Garantizar resultados imposibles para convencer al público", "Apelación a los sentimientos": "Generar emociones como tristeza o alegría para persuadir al lector"}}', 3, 0, true, '{"estrategias de persuasión","medios de comunicación","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (306, 68, 'sequencing', 'all', '{"explicacion": "Primero se debe leer detenidamente el texto para comprenderlo. Luego, se determina el propósito explícito o implícito, para entender la intención del autor. A continuación, se identifican las estrategias de persuasión: primero las palabras o imágenes que apelan a los sentimientos, luego si se utilizan estereotipos y finalmente si hay uso de humor, ya que estas son técnicas comunes que se deben revisar en orden para un análisis completo.", "instruccion": "Ordena los siguientes pasos para identificar estrategias de persuasión en un texto de medios de comunicación", "elementos_desordenados": ["Identificar las palabras o imágenes que apelan a los sentimientos del lector", "Leer detenidamente el texto seleccionado", "Reconocer si se usan estereotipos para influir en la opinión", "Determinar el propósito explícito o implícito del texto", "Observar el uso del humor como estrategia persuasiva"]}', '{"orden_correcto": ["Leer detenidamente el texto seleccionado", "Determinar el propósito explícito o implícito del texto", "Identificar las palabras o imágenes que apelan a los sentimientos del lector", "Reconocer si se usan estereotipos para influir en la opinión", "Observar el uso del humor como estrategia persuasiva"]}', 3, 0, true, '{"estrategias de persuasión","análisis de textos","medios de comunicación"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (307, 68, 'open_ended', 'all', '{"pregunta": "Lee el siguiente texto publicitario y analiza las estrategias de persuasión que utiliza. ¿Cómo emplea el humor, los estereotipos o la apelación a los sentimientos para convencer al público? Da ejemplos específicos y explica el efecto que estas estrategias podrían tener en los lectores.", "explicacion": "El docente debe evaluar si el estudiante reconoce correctamente las estrategias persuasivas y si puede explicar cómo estas buscan influir en el receptor. Se valorará la capacidad de aplicar el conocimiento a un texto real y respaldar el análisis con ejemplos claros.", "contexto_adicional": "Se proporcionará un texto publicitario breve para que los estudiantes lo analicen en clase.", "criterios_evaluacion": ["Identificación precisa de las estrategias de persuasión presentes en el texto (peso: 30%)", "Análisis claro y fundamentado del efecto de dichas estrategias sobre el público objetivo (peso: 40%)", "Uso de ejemplos concretos extraídos del texto para sustentar el análisis (peso: 30%)"]}', '{"puntos_clave": ["Identificación del humor en el texto y su función persuasiva", "Reconocimiento de estereotipos y su rol en generar confianza", "Análisis de la apelación a los sentimientos y su impacto emocional en el lector"], "respuesta_modelo": "En el texto publicitario, se utiliza el humor mediante un juego de palabras que genera simpatía y hace que el mensaje sea más memorable. Además, se emplean estereotipos positivos relacionados con la familia, lo que busca generar confianza y cercanía con el público. También hay una clara apelación a los sentimientos al mostrar escenas emotivas que evocan felicidad y bienestar. Estas estrategias persuaden al lector al conectar emocionalmente y facilitar la identificación con el producto o servicio ofrecido.", "requiere_revision_humana": true}', 3, 0, true, '{persuasión,"análisis de texto","estrategias comunicativas"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (308, 69, 'compare_contrast', 'all', '{"conceptos": ["Noticia", "Reportaje"], "criterios": ["Propósito principal", "Nivel de profundidad en la información", "Uso de fuentes", "Tono y estilo"], "explicacion": "La noticia y el reportaje son textos periodísticos que informan sobre hechos, pero difieren en su propósito y forma de presentar la información. La noticia busca entregar información rápida y objetiva sobre un hecho reciente, mientras que el reportaje profundiza en el contexto y detalles, usando múltiples fuentes y un tono más elaborado.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Noticia": {"Tono y estilo": "Formal, claro y conciso", "Uso de fuentes": "Limitado, generalmente una o pocas fuentes directas", "Propósito principal": "Informar de manera inmediata y objetiva sobre un hecho reciente", "Nivel de profundidad en la información": "Superficial, centrado en los aspectos básicos del hecho"}, "Reportaje": {"Tono y estilo": "Narrativo, descriptivo y más elaborado", "Uso de fuentes": "Diversificado, incluye múltiples fuentes y testimonios", "Propósito principal": "Analizar y profundizar en un tema o hecho con contexto y detalles", "Nivel de profundidad en la información": "Extenso, incluye antecedentes y diferentes perspectivas"}}}', 3, 0, true, '{comparacion_textual,analisis_textual,medios_de_comunicacion}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (315, 70, 'criteria_evaluation', 'all', '{"rubrica": {"Justificación crítica de la valoración": {"Excelente": "Presenta argumentos claros, coherentes y fundamentados que respaldan su evaluación de manera crítica y reflexiva.", "Satisfactorio": "Ofrece una justificación básica con argumentos poco profundos o poco desarrollados.", "Necesita mejorar": "No justifica o presenta argumentos poco claros o incoherentes para su valoración."}, "Valoración de las estrategias de persuasión": {"Excelente": "Identifica y explica claramente las estrategias persuasivas utilizadas, reconociendo su efectividad y propósito en el texto.", "Satisfactorio": "Reconoce algunas estrategias persuasivas y explica su uso de manera general, con ejemplos limitados.", "Necesita mejorar": "No identifica ni explica adecuadamente las estrategias persuasivas presentes en el texto."}, "Análisis del uso de recursos no lingüísticos (imágenes, diseño)": {"Excelente": "Describe detalladamente cómo las imágenes y el diseño contribuyen al mensaje y propósito del texto, valorando su impacto persuasivo.", "Satisfactorio": "Menciona el uso de imágenes y diseño y su función general, pero con una explicación superficial.", "Necesita mejorar": "No considera o valora adecuadamente los recursos no lingüísticos empleados en el texto."}}, "explicacion": "Se espera que el estudiante identifique y valore críticamente las estrategias persuasivas y recursos no lingüísticos presentes en el texto, fundamentando su evaluación con argumentos coherentes y bien elaborados.", "instruccion": "Evalúa el artículo de opinión según los criterios de la rúbrica y justifica tu calificación con argumentos claros.", "caso_a_evaluar": "Analiza un artículo de opinión publicado en un diario digital que aborda el impacto de las redes sociales en la juventud. El artículo utiliza imágenes llamativas y un diseño atractivo para captar la atención, y emplea recursos persuasivos como ejemplos emotivos y estadísticas para convencer al lector."}', '{"evaluacion_modelo": {"Justificación crítica de la valoración": "Excelente", "Valoración de las estrategias de persuasión": "Excelente", "Análisis del uso de recursos no lingüísticos (imágenes, diseño)": "Satisfactorio"}, "justificacion_modelo": "El estudiante identifica claramente las estrategias persuasivas como ejemplos emotivos y estadísticas, explicando su función y efectividad, mientras que reconoce la presencia de imágenes y diseño pero con una explicación más general. La justificación es coherente y bien fundamentada, reflejando una valoración crítica adecuada.", "requiere_revision_humana": true}', 5, 0, true, '{"evaluación crítica","estrategias persuasivas","recursos no lingüísticos","artículo de opinión","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (309, 69, 'compare_contrast', 'all', '{"conceptos": ["Propósito explícito", "Propósito implícito"], "criterios": ["Definición", "Ejemplo en textos periodísticos", "Función en la persuasión"], "explicacion": "El propósito explícito es el objetivo claramente expresado en el texto, mientras que el implícito es el mensaje o intención subyacente no directamente declarada. Ambos cumplen funciones persuasivas, pero el explícito se comunica abiertamente, y el implícito influye de manera más sutil en el lector.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Propósito explícito": {"Definición": "Objetivo claramente declarado y visible en el texto.", "Función en la persuasión": "Busca convencer al lector mediante un mensaje directo y claro.", "Ejemplo en textos periodísticos": "Informar sobre un evento específico o denunciar una situación."}, "Propósito implícito": {"Definición": "Intención o mensaje subyacente que no se expresa de forma directa.", "Función en la persuasión": "Influye en el lector de manera sutil, apelando a emociones o prejuicios.", "Ejemplo en textos periodísticos": "Sugerir una opinión o actitud hacia el tema sin declararla abiertamente."}}}', 4, 0, true, '{comparacion_textual,analisis,persuasion}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (310, 69, 'open_ended', 'all', '{"pregunta": "Lee dos textos periodísticos que informan sobre el mismo evento, pero que presentan diferencias en los detalles y en el enfoque. Analiza y compara la veracidad y consistencia de la información de ambos textos, identificando posibles sesgos o estrategias persuasivas que puedan afectar la objetividad de la noticia. ¿Cuál de los dos textos consideras más confiable y por qué?", "explicacion": "El docente debe evaluar si el estudiante es capaz de comparar críticamente la información de ambos textos, identificando no solo las diferencias factuales sino también las estrategias discursivas que afectan la objetividad. Se debe valorar la capacidad para argumentar de manera clara y fundamentada cuál texto considera más confiable.", "contexto_adicional": "Se proporcionarán dos textos periodísticos breves sobre un mismo hecho, que pueden diferir en el enfoque, tono o detalles presentados.", "criterios_evaluacion": ["Identificación clara de diferencias y similitudes en la información presentada en ambos textos (peso: 30%)", "Análisis crítico de la veracidad y consistencia de los datos, incluyendo la detección de posibles sesgos o estrategias persuasivas (peso: 40%)", "Justificación argumentada sobre cuál texto es más confiable, apoyada en evidencias extraídas de los textos (peso: 30%)"]}', '{"puntos_clave": ["Comparación de detalles y datos presentados en ambos textos", "Análisis de estrategias persuasivas y posibles sesgos en los textos", "Argumentación fundamentada sobre cuál texto es más confiable"], "respuesta_modelo": "Los dos textos informan sobre el mismo evento, pero presentan diferencias en detalles como las cifras y el contexto descrito. El primer texto ofrece datos más precisos y cita fuentes oficiales, mientras que el segundo utiliza un lenguaje más emotivo y generaliza algunos hechos. Se identifican estrategias persuasivas en el segundo texto, como apelaciones emocionales y uso de adjetivos valorativos que pueden influir en la percepción del lector. Por lo tanto, considero que el primer texto es más confiable debido a su consistencia en la información, respaldo en fuentes y menor presencia de sesgos evidentes.", "requiere_revision_humana": true}', 4, 0, true, '{"análisis textual","pensamiento crítico","medios de comunicación"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (311, 69, 'concept_map', 'all', '{"conceptos": ["Veracidad", "Consistencia", "Fuente de información", "Estrategias de persuasión", "Propósito del texto", "Medios de comunicación"], "explicacion": "Se espera que el estudiante vincule cómo la veracidad y consistencia dependen de la fuente de información y el propósito del texto, además de identificar cómo las estrategias de persuasión influyen en la manera en que los medios de comunicación presentan la información.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis y comparación de textos sobre un mismo hecho?"}', '{"conceptos_centrales": ["Veracidad", "Consistencia"], "relaciones_esperadas": [{"origen": "Medios de comunicación", "destino": "Estrategias de persuasión", "relacion": "utilizan"}, {"origen": "Estrategias de persuasión", "destino": "Propósito del texto", "relacion": "impactan en"}, {"origen": "Fuente de información", "destino": "Veracidad", "relacion": "determina"}, {"origen": "Veracidad", "destino": "Consistencia", "relacion": "afecta"}, {"origen": "Propósito del texto", "destino": "Consistencia", "relacion": "influye en"}], "requiere_revision_humana": true}', 3, 0, true, '{comparacion_textual,analisis_de_textos,medios_de_comunicacion}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (312, 69, 'concept_map', 'all', '{"conceptos": ["Veracidad", "Consistencia", "Texto informativo", "Estrategias de persuasión", "Propósito del texto", "Comparación de fuentes"], "explicacion": "Se espera que el estudiante conecte cómo la veracidad y consistencia son criterios clave para evaluar la información en textos informativos, cómo las estrategias de persuasión y el propósito del texto influyen en la presentación de la información, y cómo la comparación de fuentes permite identificar diferencias o coincidencias en la información presentada.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis y evaluación de textos periodísticos sobre un mismo hecho?"}', '{"conceptos_centrales": ["Veracidad", "Consistencia"], "relaciones_esperadas": [{"origen": "Texto informativo", "destino": "Información veraz", "relacion": "presenta"}, {"origen": "Estrategias de persuasión", "destino": "Propósito del texto", "relacion": "modifican"}, {"origen": "Veracidad", "destino": "Consistencia", "relacion": "afecta"}, {"origen": "Comparación de fuentes", "destino": "Veracidad", "relacion": "permite evaluar"}, {"origen": "Comparación de fuentes", "destino": "Consistencia", "relacion": "permite evaluar"}, {"origen": "Propósito del texto", "destino": "Estrategias de persuasión", "relacion": "influye en"}], "requiere_revision_humana": true}', 4, 0, true, '{"lengua y literatura","análisis textual","medios de comunicación","evaluación crítica"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (313, 70, 'criteria_evaluation', 'all', '{"rubrica": {"Justificación crítica de la valoración": {"Excelente": "Presenta una valoración crítica fundamentada, argumentando con ejemplos concretos y mostrando una reflexión profunda sobre la efectividad persuasiva del texto.", "Satisfactorio": "Ofrece una valoración con argumentos básicos o generales, con poca profundidad en el análisis crítico.", "Necesita mejorar": "Realiza una valoración sin fundamentación o con argumentos poco claros y poco relacionados con el texto."}, "Identificación de estrategias persuasivas": {"Excelente": "Reconoce y explica de manera clara y precisa varias estrategias persuasivas utilizadas en el texto, tanto explícitas como implícitas.", "Satisfactorio": "Identifica algunas estrategias persuasivas, aunque con explicaciones generales o incompletas.", "Necesita mejorar": "Tiene dificultades para reconocer las estrategias persuasivas o las interpreta incorrectamente."}, "Análisis del uso de recursos no lingüísticos": {"Excelente": "Analiza detalladamente cómo las imágenes, diseño o elementos audiovisuales refuerzan el mensaje y la persuasión del texto.", "Satisfactorio": "Describe de forma superficial el uso de recursos no lingüísticos sin profundizar en su función persuasiva.", "Necesita mejorar": "No identifica o no analiza adecuadamente los recursos no lingüísticos presentes en el texto."}}, "explicacion": "Se espera que el estudiante identifique y explique las estrategias persuasivas usadas, analice el papel de los recursos no lingüísticos y entregue una valoración crítica bien argumentada, demostrando comprensión profunda del texto y sus técnicas de persuasión.", "instruccion": "Evalúa el artículo de opinión según la rúbrica presentada y justifica tu calificación en cada criterio con ejemplos concretos del texto.", "caso_a_evaluar": "Analiza un artículo de opinión publicado en un diario digital que trata sobre el impacto de las redes sociales en la salud mental de los jóvenes. El artículo incluye imágenes sugestivas, estadísticas y testimonios personales para persuadir a los lectores."}', '{"evaluacion_modelo": {"Justificación crítica de la valoración": "Excelente", "Identificación de estrategias persuasivas": "Excelente", "Análisis del uso de recursos no lingüísticos": "Satisfactorio"}, "justificacion_modelo": "El estudiante reconoce claramente las estrategias persuasivas presentes, pero su análisis de las imágenes y otros recursos no lingüísticos es superficial. Sin embargo, su valoración crítica está bien fundamentada con ejemplos precisos, mostrando comprensión profunda.", "requiere_revision_humana": true}', 4, 0, true, '{"evaluación crítica","estrategias persuasivas","recursos no lingüísticos","lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (317, 70, 'open_ended', 'all', '{"pregunta": "Analiza un texto de medios de comunicación (por ejemplo, un reportaje o una propaganda) que hayas leído recientemente. Justifica críticamente cómo las estrategias de persuasión y los recursos no lingüísticos (como imágenes, diseño o audio) contribuyen a reforzar el mensaje y el propósito del texto. ¿Crees que estas estrategias son efectivas o manipuladoras? Explica tu posición con argumentos claros y ejemplos específicos del texto.", "explicacion": "El docente debe evaluar si el estudiante logra identificar correctamente las estrategias persuasivas y los recursos no lingüísticos, analizar su función dentro del texto, y emitir una valoración crítica fundamentada, evidenciando pensamiento reflexivo y argumentación clara.", "contexto_adicional": "El estudiante puede elegir cualquier texto reciente de medios de comunicación que haya leído y debe apoyar su análisis con ejemplos específicos.", "criterios_evaluacion": ["Identificación y explicación de las estrategias de persuasión presentes en el texto (peso: 30%)", "Análisis crítico del uso de recursos no lingüísticos y su impacto en el mensaje (peso: 40%)", "Justificación coherente y fundamentada de la valoración personal sobre la efectividad o manipulación de las estrategias (peso: 30%)"]}', '{"puntos_clave": ["Identificación de estrategias persuasivas (testimonios, datos, apelación emocional, etc.)", "Descripción del impacto de imágenes, diseño o audio en el mensaje", "Valoración personal fundamentada sobre la efectividad o manipulación de dichas estrategias"], "respuesta_modelo": "En el reportaje sobre el cambio climático publicado en un diario nacional, se utilizan estrategias de persuasión como el uso de testimonios de expertos y datos estadísticos para generar credibilidad. Además, las imágenes impactantes de glaciares derritiéndose refuerzan el sentido de urgencia. El diseño del texto, con colores fríos y tipografía clara, ayuda a resaltar la seriedad del tema. Considero que estas estrategias son efectivas porque apelan tanto a la razón como a las emociones del lector, logrando un equilibrio que motiva a la reflexión. No las percibo como manipuladoras porque están sustentadas en evidencia y no buscan engañar, sino informar y concientizar.", "requiere_revision_humana": true}', 5, 0, true, '{evaluación,"análisis crítico","estrategias persuasivas","recursos no lingüísticos","medios de comunicación"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (318, 71, 'open_ended', 'all', '{"pregunta": "Imagina que eres el editor de una revista escolar y debes crear un artículo de opinión sobre un tema relevante para los estudiantes de tu colegio. Diseña un texto que utilice estrategias persuasivas y recursos no lingüísticos (como imágenes, gráficos o tipografía) para influir en la opinión de tus lectores. Explica cómo cada estrategia y recurso contribuye a persuadir al público.", "explicacion": "El docente debe evaluar que el estudiante no solo cree un texto con estrategias persuasivas, sino que también justifique su elección y explique cómo los recursos no lingüísticos fortalecen el mensaje. Se valorará la creatividad, la pertinencia y la capacidad de argumentación.", "contexto_adicional": "El tema del artículo debe ser relevante y de interés para los estudiantes del colegio. Se espera que el texto tenga una estructura clara y que las estrategias persuasivas sean explícitas o implícitas, con un uso adecuado de recursos visuales para potenciar el impacto del mensaje.", "criterios_evaluacion": ["Uso intencionado y coherente de estrategias persuasivas adecuadas al público objetivo (peso: 40%)", "Incorporación efectiva de recursos no lingüísticos que complementen y refuercen el mensaje del texto (peso: 30%)", "Claridad y coherencia en la explicación sobre cómo las estrategias y recursos seleccionados influyen en la opinión del lector (peso: 30%)"]}', '{"puntos_clave": ["Identificación y uso de estrategias persuasivas adecuadas (ej. logos, pathos, ethos)", "Incorporación y explicación del uso de recursos no lingüísticos (imágenes, gráficos, tipografía)", "Justificación clara de cómo las estrategias y recursos influyen en la opinión del lector"], "respuesta_modelo": "Título: \"Por qué debemos fomentar el reciclaje en nuestro colegio\"\n\nArtículo: En nuestro colegio, el reciclaje es una práctica esencial para cuidar el medio ambiente y mejorar la calidad de vida. Según estudios recientes, cada persona genera más de 1 kg de residuos diarios, y gran parte de estos podrían reciclarse para reducir la contaminación. Al implementar un programa de reciclaje, no solo protegemos la naturaleza, sino que también fomentamos una cultura de responsabilidad y conciencia ambiental entre los estudiantes.\n\nPara persuadir a los lectores, utilizo datos estadísticos que generan credibilidad y apelan a la lógica (logos). Además, el texto está acompañado por una infografía que muestra el proceso de reciclaje y sus beneficios, lo que facilita la comprensión visual del mensaje. La tipografía es clara y los colores verdes refuerzan la idea de ecología y naturaleza, generando una conexión emocional (pathos) con el público.\n\nEsta combinación de argumentos, imágenes y diseño ayuda a que los estudiantes se sientan motivados a participar activamente en el programa de reciclaje, influyendo positivamente en su opinión y comportamiento.", "requiere_revision_humana": true}', 4, 0, true, '{creación,persuasión,"medios de comunicación","texto argumentativo"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (319, 71, 'open_ended', 'all', '{"pregunta": "Diseña un texto para un medio de comunicación (puede ser una noticia, una carta al director, un reportaje o una propaganda) que busque influir en la opinión del lector sobre un tema de actualidad. Explica cuáles son tus propósitos explícitos e implícitos, qué estrategias de persuasión utilizaste y cómo incorporaste recursos no lingüísticos para reforzar tu mensaje.", "explicacion": "El docente debe valorar que el estudiante no solo cree un texto funcional, sino que también identifique y aplique con intención estrategias persuasivas y recursos no lingüísticos que amplifiquen el impacto del mensaje. Se debe considerar la originalidad, la adecuación al formato y la profundidad del análisis en la explicación de las decisiones tomadas.", "contexto_adicional": "El estudiante puede elegir cualquier tema de actualidad relevante para su entorno y seleccionar el formato textual que prefiera para expresar su postura y persuadir al lector.", "criterios_evaluacion": ["Claridad y coherencia en el diseño del texto comunicativo, adecuándose al formato elegido (peso: 30%)", "Uso intencionado y efectivo de estrategias de persuasión explícitas e implícitas (peso: 40%)", "Incorporación y adecuada utilización de recursos no lingüísticos que complementen y refuercen el mensaje (peso: 30%)"]}', '{"puntos_clave": ["Elección clara y adecuada del formato textual y tema de actualidad", "Identificación y aplicación de estrategias persuasivas explícitas e implícitas", "Uso efectivo de recursos no lingüísticos para complementar el mensaje"], "respuesta_modelo": "He creado una carta al director sobre la importancia de implementar más espacios verdes en la ciudad. El propósito explícito es concienciar a la comunidad sobre los beneficios ambientales y de salud que esto trae. Implícitamente, busco generar presión social para que las autoridades tomen medidas. Utilicé estrategias persuasivas como apelar a las emociones mencionando el bienestar de las familias y la necesidad de un ambiente limpio. Además, incorporé datos estadísticos para dar credibilidad. En cuanto a recursos no lingüísticos, añadí una imagen impactante de un parque deteriorado y gráficos que muestran la relación entre espacios verdes y calidad de vida, los cuales refuerzan visualmente el argumento y llaman la atención del lector.", "requiere_revision_humana": true}', 5, 0, true, '{lengua-y-literatura,primer-medio,estrategias-de-persuasión,creación-textual}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (320, 71, 'concept_map', 'all', '{"conceptos": ["Texto de medios de comunicación", "Estrategias de persuasión", "Recursos no lingüísticos", "Propósito explícito", "Propósito implícito", "Opinión del lector"], "explicacion": "Se espera que el estudiante muestre cómo un texto de medios de comunicación utiliza estrategias de persuasión y recursos no lingüísticos para cumplir con propósitos explícitos e implícitos, con el fin de influir en la opinión del lector.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la creación de textos persuasivos en medios de comunicación?"}', '{"conceptos_centrales": ["Texto de medios de comunicación", "Estrategias de persuasión"], "relaciones_esperadas": [{"origen": "Texto de medios de comunicación", "destino": "Estrategias de persuasión", "relacion": "utiliza"}, {"origen": "Texto de medios de comunicación", "destino": "Recursos no lingüísticos", "relacion": "incorpora"}, {"origen": "Estrategias de persuasión", "destino": "Propósito explícito", "relacion": "buscan cumplir"}, {"origen": "Estrategias de persuasión", "destino": "Propósito implícito", "relacion": "pueden sugerir"}, {"origen": "Recursos no lingüísticos", "destino": "Propósito explícito", "relacion": "refuerzan"}, {"origen": "Propósito explícito", "destino": "Opinión del lector", "relacion": "influye en"}, {"origen": "Propósito implícito", "destino": "Opinión del lector", "relacion": "también influye en"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Primero Medio","Textos persuasivos","Mapa conceptual","Estrategias de persuasión"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (321, 71, 'concept_map', 'all', '{"conceptos": ["Estrategias de persuasión", "Propósitos explícitos", "Propósitos implícitos", "Recursos no lingüísticos", "Opinión del lector"], "explicacion": "El mapa conceptual debe reflejar cómo las estrategias de persuasión y los recursos no lingüísticos se utilizan para alcanzar tanto propósitos explícitos como implícitos en un texto, con el objetivo final de influir en la opinión del lector.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la construcción e interpretación de textos de medios de comunicación persuasivos?"}', '{"conceptos_centrales": ["Estrategias de persuasión", "Opinión del lector"], "relaciones_esperadas": [{"origen": "Estrategias de persuasión", "destino": "Propósitos explícitos", "relacion": "se utilizan para alcanzar"}, {"origen": "Estrategias de persuasión", "destino": "Propósitos implícitos", "relacion": "se utilizan para alcanzar"}, {"origen": "Recursos no lingüísticos", "destino": "Estrategias de persuasión", "relacion": "refuerzan"}, {"origen": "Propósitos explícitos", "destino": "Opinión del lector", "relacion": "influyen en"}, {"origen": "Propósitos implícitos", "destino": "Opinión del lector", "relacion": "influyen en"}], "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Medios de Comunicación",Persuasión,"Mapa Conceptual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (345, 76, 'criteria_evaluation', 'all', '{"rubrica": {"Coherencia y estructura de la argumentación": {"Excelente": "Presenta una argumentación lógica, bien organizada y coherente, con ideas conectadas que apoyan claramente la evaluación.", "Satisfactorio": "La argumentación es en general clara, aunque puede tener pequeños errores de coherencia o estructura, pero se entiende el punto central.", "Necesita mejorar": "La argumentación es desordenada, confusa o carece de conexión entre las ideas, dificultando la comprensión del juicio emitido."}, "Justificación de la relevancia del texto no literario": {"Excelente": "Explica claramente con ejemplos específicos cómo el texto no literario enriquece la interpretación del texto literario, mostrando una comprensión profunda del contexto.", "Satisfactorio": "Explica de manera general cómo el texto no literario ayuda a entender el texto literario, con algunos ejemplos pero sin mucha profundidad.", "Necesita mejorar": "Muestra dificultad para relacionar el texto no literario con la interpretación del texto literario o la explicación es vaga y sin ejemplos claros."}, "Uso de lenguaje académico y precisión terminológica": {"Excelente": "Utiliza un lenguaje formal, preciso y adecuado al nivel académico, con términos propios de lengua y literatura correctamente empleados.", "Satisfactorio": "El lenguaje es adecuado pero con algunas imprecisiones o uso limitado de terminología específica.", "Necesita mejorar": "El lenguaje es informal, impreciso o incorrecto para el contexto académico, con poco o mal uso de términos relevantes."}}, "explicacion": "Se espera que el estudiante identifique y explique la relación entre ambos textos, argumentando con ejemplos la importancia del contexto histórico-social que ofrece el texto no literario. Además, debe organizar sus ideas de manera coherente y utilizar un lenguaje apropiado a la materia.", "instruccion": "Evalúa cómo el texto no literario aporta a la interpretación del fragmento literario usando la rúbrica proporcionada. Justifica tu calificación en cada criterio con argumentos claros y ejemplos.", "caso_a_evaluar": "Se presenta un fragmento de una novela histórica y un artículo no literario que describe el contexto histórico y social de la época en que se desarrolla la novela. Evalúa cómo el artículo no literario contribuye a una mejor comprensión e interpretación del fragmento literario."}', '{"evaluacion_modelo": {"Coherencia y estructura de la argumentación": "Satisfactorio", "Justificación de la relevancia del texto no literario": "Excelente", "Uso de lenguaje académico y precisión terminológica": "Satisfactorio"}, "justificacion_modelo": "La evaluación reconoce que el estudiante explicó claramente cómo el texto no literario enriquece la interpretación literaria (Excelente). Sin embargo, la argumentación presenta una estructura general clara pero con algunas pequeñas incoherencias (Satisfactorio). El lenguaje es adecuado aunque no siempre preciso en terminología especializada (Satisfactorio).", "requiere_revision_humana": true}', 5, 0, true, '{evaluación,"texto no literario","interpretación literaria","debate guiado"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (322, 71, 'open_ended', 'all', '{"pregunta": "Imagina que debes diseñar un texto para un medio de comunicación (por ejemplo, una noticia, un reportaje o una carta al director) cuyo objetivo sea influir en la opinión de los lectores sobre un tema social actual. Describe el tema elegido, el propósito explícito e implícito de tu texto, las estrategias de persuasión que utilizarás y los recursos no lingüísticos que incorporarás para reforzar tu mensaje. Justifica cómo cada elemento contribuye a persuadir a tus lectores.", "explicacion": "El docente debe evaluar si el estudiante puede seleccionar un tema social relevante, definir claramente los propósitos del texto (tanto explícitos como implícitos), aplicar estrategias persuasivas reconocidas (como apelaciones emocionales, datos, testimonios, etc.) y complementar el texto con recursos visuales o gráficos que refuercen el mensaje. Se valorará la coherencia entre todos los elementos y la capacidad de justificar las elecciones realizadas.", "contexto_adicional": "El texto debe estar dirigido a un público general y debe reflejar un enfoque crítico y creativo acorde con los objetivos del currículo de Lengua y Literatura para Primero Medio.", "criterios_evaluacion": ["Claridad y coherencia en la definición del tema y los propósitos explícito e implícito del texto (peso: 30%)", "Uso intencionado y adecuado de estrategias de persuasión en el contenido escrito (peso: 40%)", "Incorporación efectiva de recursos no lingüísticos y justificación de su aporte persuasivo (peso: 30%)"]}', '{"puntos_clave": ["Definición clara del tema y propósitos explícito e implícito", "Selección y aplicación adecuada de estrategias persuasivas textuales", "Incorporación y justificación de recursos no lingüísticos relevantes"], "respuesta_modelo": "He elegido el tema del cambio climático, enfocándome en la necesidad de adoptar medidas urgentes para reducir la contaminación. El propósito explícito de mi texto es informar a los lectores sobre los efectos actuales del cambio climático y motivarlos a apoyar políticas ambientales. Implícitamente, busco generar un sentido de urgencia y responsabilidad personal. Para persuadir, utilizo estadísticas impactantes sobre el aumento de temperaturas y testimonios de expertos, apelando tanto a la lógica como a la emoción. Además, incorporo imágenes de eventos climáticos extremos y gráficos que muestran el aumento de CO2, que refuerzan visualmente el mensaje y captan la atención. Cada estrategia y recurso está pensado para influir en la opinión del lector y fomentar una actitud proactiva frente al problema.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Persuasión,"Medios de Comunicación","Creación de textos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (323, 72, 'multiple_choice', 'all', '{"opciones": {"A": "Utiliza un lenguaje figurado y metáforas para crear imágenes.", "B": "Presenta información clara y objetiva sobre un tema específico.", "C": "Se enfoca en contar una historia con personajes y trama.", "D": "Busca provocar emociones a través del ritmo y la rima."}, "pregunta": "¿Cuál de las siguientes opciones es una característica típica de un texto no literario?", "explicacion": "La opción B es correcta porque los textos no literarios se caracterizan por presentar información de manera clara y objetiva, con el fin de informar o explicar un tema. La opción A es propia de textos literarios que usan lenguaje figurado. La opción C describe textos narrativos literarios, y la opción D hace referencia a características de la poesía, que también es literaria."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"textos no literarios",características,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (324, 72, 'multiple_choice', 'all', '{"opciones": {"A": "Utiliza un lenguaje figurado y metafórico para expresar emociones.", "B": "Presenta información objetiva y organizada para informar o explicar.", "C": "Está escrito en verso y utiliza rimas para crear ritmo.", "D": "Busca provocar una respuesta emocional a través de relatos imaginarios."}, "pregunta": "¿Cuál de las siguientes características es propia de un texto no literario?", "explicacion": "La respuesta correcta es la opción B, porque los textos no literarios se caracterizan por presentar información objetiva y organizada con el fin de informar, explicar o argumentar. La opción A es propia de textos literarios, ya que usan lenguaje figurado para expresar emociones. La opción C se refiere a características de textos literarios en verso, como la poesía. La opción D también describe un propósito típico de textos literarios que buscan impactar emocionalmente mediante relatos ficticios."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"textos no literarios",características,"lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (325, 72, 'multiple_choice', 'all', '{"opciones": {"A": "Busca provocar emociones y sensaciones en el lector.", "B": "Presenta información clara y objetiva sobre un tema específico.", "C": "Utiliza un lenguaje figurado y metáforas para embellecer el texto.", "D": "Se centra en contar una historia ficticia con personajes imaginarios."}, "pregunta": "¿Cuál de las siguientes características es propia de un texto no literario?", "explicacion": "La opción B es correcta porque los textos no literarios tienen como finalidad informar o explicar de manera clara y objetiva. La opción A corresponde a textos literarios, que buscan evocar emociones. La opción C también pertenece a textos literarios, que suelen utilizar lenguaje figurado. La opción D describe características de textos literarios como cuentos o novelas, no de textos no literarios."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"textos no literarios",características,"objetivo de aprendizaje"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (326, 72, 'true_false', 'all', '{"afirmacion": "Los textos no literarios tienen como objetivo principal informar o explicar hechos reales y no buscar una respuesta emocional del lector.", "explicacion": "Esta afirmación es verdadera porque los textos no literarios, a diferencia de los textos literarios, se centran en transmitir información clara y objetiva sobre temas concretos, como noticias, informes o ensayos, sin buscar provocar emociones o crear una experiencia estética."}', '{"es_verdadero": true}', 1, 0, true, '{"textos no literarios","comprensión lectora","Lengua y Literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (327, 72, 'fill_blanks', 'all', '{"texto": "Los textos no literarios se caracterizan por presentar información [BLANK_1] y organizada, con el objetivo de [BLANK_2] hechos o ideas de manera clara y precisa.", "pistas": ["BLANK_1 se refiere a cómo se presenta la información, sin adornos ni ficción.", "BLANK_2 indica la función principal de estos textos al comunicar información."], "explicacion": "Los textos no literarios buscan entregar información objetiva y estructurada para informar o explicar hechos o ideas reales, a diferencia de los textos literarios que se centran en la ficción y la creación artística."}', '{"respuestas_correctas": {"BLANK_1": ["objetiva", "clara"], "BLANK_2": ["informar", "comunicar"]}}', 2, 0, true, '{textos_no_literarios,características,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (328, 73, 'multiple_choice', 'all', '{"opciones": {"A": "Entretener al lector con historias imaginarias.", "B": "Informar o explicar hechos y datos de manera clara y objetiva.", "C": "Desarrollar personajes y escenarios ficticios.", "D": "Crear emociones a través del lenguaje poético."}, "pregunta": "¿Cuál es el propósito principal de un texto no literario, como un artículo informativo o un informe?", "explicacion": "La respuesta correcta es la opción B, ya que los textos no literarios tienen como propósito principal informar o explicar hechos, conceptos o datos de forma clara y objetiva. La opción A y C corresponden a características de textos literarios, que buscan entretener y desarrollar ficciones. La opción D también se relaciona con textos literarios, particularmente poéticos, que buscan crear emociones mediante el lenguaje."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"Lengua y Literatura","Textos no literarios","Comprensión lectora","Objetivo Específico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (329, 73, 'multiple_choice', 'all', '{"opciones": {"A": "Entretener al lector a través de historias imaginarias", "B": "Informar o explicar hechos, ideas o eventos de manera clara y objetiva", "C": "Describir emociones y sentimientos personales del autor", "D": "Confundir al lector para generar debate y reflexión"}, "pregunta": "¿Cuál es el propósito principal de un texto no literario, como un artículo informativo o un reportaje?", "explicacion": "La opción B es correcta porque los textos no literarios tienen como propósito principal informar, explicar o presentar hechos de manera clara y objetiva, facilitando la comprensión del lector. La opción A es incorrecta porque la entretención mediante historias imaginarias corresponde a textos literarios. La opción C se relaciona más con textos literarios o subjetivos, no con textos no literarios. La opción D es un error conceptual, ya que los textos no literarios buscan claridad, no confusión."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"texto no literario","propósito del texto","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (330, 73, 'true_false', 'all', '{"afirmacion": "Los textos no literarios tienen como propósito principal informar, explicar o persuadir, y su estructura suele incluir una introducción, desarrollo y conclusión.", "explicacion": "Esta afirmación es verdadera porque los textos no literarios generalmente buscan transmitir información clara y objetiva al lector. Además, presentan una estructura organizada que facilita la comprensión del contenido, comúnmente dividida en introducción, desarrollo y conclusión para exponer ideas de manera ordenada."}', '{"es_verdadero": true}', 2, 0, true, '{"textos no literarios","estructura textual","propósito del texto","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (331, 73, 'drag_drop_matching', 'all', '{"explicacion": "Cada texto no literario tiene un propósito específico y una estructura que lo caracteriza. Por ejemplo, una noticia presenta hechos actuales, mientras que un artículo de opinión refleja el punto de vista del autor. La estructura típica incluye introducción, desarrollo y conclusión, y el propósito general es comunicar información de forma clara y objetiva. Un informe presenta resultados y análisis detallados.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Presenta hechos actuales y verificados", "Expresa la postura o juicio personal del autor", "Organización clara con introducción, desarrollo y conclusión", "Comunicar información clara y objetiva", "Documento que presenta resultados y análisis detallados"], "columna_izquierda": ["Artículo de opinión", "Noticia", "Informe", "Propósito de un texto no literario", "Estructura de un texto no literario"]}', '{"emparejamientos_correctos": {"Informe": "Documento que presenta resultados y análisis detallados", "Noticia": "Presenta hechos actuales y verificados", "Artículo de opinión": "Expresa la postura o juicio personal del autor", "Estructura de un texto no literario": "Organización clara con introducción, desarrollo y conclusión", "Propósito de un texto no literario": "Comunicar información clara y objetiva"}}', 2, 0, true, '{"texto no literario","comprensión lectora","estructura textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (332, 73, 'sequencing', 'all', '{"explicacion": "El orden correcto comienza con leer atentamente el texto para comprenderlo. Luego, se identifican las ideas principales que serán la base del resumen. Después, se redacta el resumen usando palabras propias para demostrar comprensión. Finalmente, se revisa el resumen para corregir errores y garantizar que sea claro y coherente.", "instruccion": "Ordena los siguientes pasos para escribir un resumen de un texto no literario en la secuencia correcta", "elementos_desordenados": ["Identificar las ideas principales del texto", "Leer atentamente el texto completo", "Redactar el resumen usando tus propias palabras", "Revisar y corregir el resumen para asegurar claridad y coherencia"]}', '{"orden_correcto": ["Leer atentamente el texto completo", "Identificar las ideas principales del texto", "Redactar el resumen usando tus propias palabras", "Revisar y corregir el resumen para asegurar claridad y coherencia"]}', 3, 0, true, '{comprensión,resumen,"texto no literario"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (333, 74, 'multiple_choice', 'all', '{"opciones": {"A": "Ignorar la información no literaria y centrarse solo en la trama del cuento.", "B": "Relacionar los detalles históricos y culturales del artículo con las situaciones y personajes del cuento para entender mejor sus acciones y motivaciones.", "C": "Usar la información del artículo para cambiar el final del cuento según lo que aprendiste.", "D": "Comparar el artículo con otro cuento de un periodo diferente para ver cuál es mejor."}, "pregunta": "Después de leer un artículo no literario sobre la época victoriana, ¿cómo puedes usar esa información para comprender mejor el cuento \"La señal\" que estudiaste en clase y que está ambientado en ese periodo?", "explicacion": "La respuesta correcta es B porque aplicar información de un texto no literario sobre la época victoriana permite contextualizar el cuento \"La señal\", ayudando a comprender las motivaciones y situaciones de los personajes dentro de ese marco histórico. La opción A es incorrecta porque descarta información útil; la C es un error conceptual pues la información no se usa para modificar el texto literario, sino para entenderlo mejor; la D no se relaciona con el objetivo de complementar la comprensión del texto literario estudiado."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"comprensión lectora","textos no literarios",contextualización,"lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (334, 74, 'multiple_choice', 'all', '{"opciones": {"A": "Ofrecer una interpretación emocional y subjetiva del poema.", "B": "Brindar información contextual o factual que ayude a entender mejor el contenido del poema.", "C": "Presentar una versión resumida y simplificada del poema para facilitar su lectura.", "D": "Reemplazar la lectura del poema por un texto más fácil y breve."}, "pregunta": "¿Cuál es la función principal de un texto no literario cuando se utiliza para complementar la lectura de un poema estudiado en clase?", "explicacion": "La opción B es correcta porque los textos no literarios, como artículos o ensayos informativos, aportan datos contextuales o históricos que enriquecen la comprensión del poema. La opción A es incorrecta porque la interpretación emocional es propia de lecturas literarias, no no literarias. La opción C es un error común, pues los textos no literarios no buscan simplificar sino complementar con información. La opción D confunde la finalidad del texto no literario, que no es sustituir la lectura literaria sino apoyarla."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"comprensión lectora","texto no literario",contextualización,"Lengua y Literatura","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (335, 74, 'drag_drop_matching', 'all', '{"explicacion": "Cada emparejamiento conecta conceptos clave para comprender cómo los textos no literarios complementan las lecturas literarias: el texto literario es una narración artística, mientras que el no literario entrega datos reales; la biografía es un ejemplo de texto no literario que aporta información factual; contextualizar es situar un texto en su época; y complementar es añadir información para una mejor comprensión.", "instruccion": "Empareja cada concepto relacionado con textos literarios y no literarios con su definición o ejemplo correspondiente", "columna_derecha": ["Información factual que explica o amplía la historia de un autor", "Narración que utiliza lenguaje artístico para expresar emociones o contar historias", "Texto informativo que presenta datos y hechos reales", "Acción de añadir información para entender mejor un texto", "Situar un texto dentro de su tiempo histórico o cultural"], "columna_izquierda": ["Texto literario", "Texto no literario", "Contextualización", "Complementar", "Biografía"]}', '{"emparejamientos_correctos": {"Biografía": "Información factual que explica o amplía la historia de un autor", "Complementar": "Acción de añadir información para entender mejor un texto", "Texto literario": "Narración que utiliza lenguaje artístico para expresar emociones o contar historias", "Contextualización": "Situar un texto dentro de su tiempo histórico o cultural", "Texto no literario": "Texto informativo que presenta datos y hechos reales"}}', 3, 0, true, '{"Comprensión lectora","Textos literarios","Textos no literarios",Contextualización}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (336, 74, 'sequencing', 'all', '{"explicacion": "El orden correcto inicia con la lectura del texto literario para entender su contenido. Luego, se identifican datos relevantes en el texto no literario que aportan contexto adicional. Después, se comparan ambas informaciones para establecer conexiones. Finalmente, se reflexiona sobre cómo esta información complementaria enriquece la comprensión del texto literario.", "instruccion": "Ordena los siguientes pasos para analizar un texto literario usando información de un texto no literario complementario", "elementos_desordenados": ["Comparar la información del texto no literario con la del texto literario", "Leer cuidadosamente el texto literario asignado en clase", "Identificar datos relevantes en el texto no literario que complementen la lectura", "Reflexionar sobre cómo la información complementaria enriquece la comprensión del texto literario"]}', '{"orden_correcto": ["Leer cuidadosamente el texto literario asignado en clase", "Identificar datos relevantes en el texto no literario que complementen la lectura", "Comparar la información del texto no literario con la del texto literario", "Reflexionar sobre cómo la información complementaria enriquece la comprensión del texto literario"]}', 3, 0, true, '{"comprensión lectora","análisis de textos","texto literario","texto no literario"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (337, 74, 'open_ended', 'all', '{"pregunta": "Lee el texto no literario proporcionado sobre el contexto histórico y social de la obra literaria que están estudiando en clase. Luego, explica cómo esta información complementa la comprensión de los personajes y los eventos del texto literario. ¿De qué manera el contexto influye en las decisiones de los personajes y en el desarrollo de la trama?", "explicacion": "Para evaluar esta pregunta, el docente debe revisar si el estudiante utiliza correctamente la información del texto no literario para enriquecer su comprensión del texto literario. Se debe valorar si el alumno identifica cómo el contexto influye en los personajes y en la trama, y si su respuesta está bien estructurada y es coherente. Se recomienda considerar la profundidad del análisis y la claridad de la redacción.", "contexto_adicional": "Se proporcionará un texto no literario que describa el contexto histórico y social de la obra literaria estudiada en clase, por ejemplo, un fragmento sobre la época y las condiciones de vida presentes en la obra.", "criterios_evaluacion": ["Comprensión y aplicación correcta de la información del texto no literario para explicar aspectos del texto literario (peso: 40%)", "Capacidad de análisis crítico para relacionar el contexto histórico-social con las decisiones y acciones de los personajes (peso: 35%)", "Claridad y coherencia en la expresión escrita para organizar y presentar las ideas (peso: 25%)"]}', '{"puntos_clave": ["Uso adecuado de información del texto no literario para explicar elementos del texto literario", "Relación clara entre el contexto histórico-social y las acciones o decisiones de los personajes", "Expresión clara y coherente de las ideas"], "respuesta_modelo": "El texto no literario describe el contexto histórico de la obra, mostrando las condiciones sociales y políticas de la época. Esto ayuda a entender por qué los personajes actúan de determinada manera, por ejemplo, la presión social o la falta de recursos que enfrentan. Por ejemplo, el protagonista toma decisiones arriesgadas porque la situación económica es difícil y esto influye en su comportamiento. Además, el contexto explica algunos conflictos presentes en la trama, como las tensiones sociales o culturales, lo que enriquece la comprensión de la obra y permite interpretar mejor las motivaciones de los personajes.", "requiere_revision_humana": true}', 3, 0, true, '{"lengua y literatura","lectura crítica","contexto histórico","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (338, 75, 'compare_contrast', 'all', '{"conceptos": ["Texto literario", "Texto no literario"], "criterios": ["Propósito", "Estructura", "Lenguaje utilizado", "Función en la comprensión del tema"], "explicacion": "Los textos literarios buscan principalmente evocar emociones y explorar la creatividad mediante narrativas o poesía, mientras que los textos no literarios tienen como finalidad informar o explicar hechos y datos concretos. En cuanto a la estructura, los textos literarios suelen tener formatos más flexibles y artísticos, mientras que los no literarios presentan una organización más clara y lógica. El lenguaje en los textos literarios es figurado y simbólico, mientras que en los no literarios es directo y preciso. Ambos tipos de textos complementan la comprensión de un tema, pero desde perspectivas distintas: uno desde la experiencia estética y el otro desde la información objetiva.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Texto literario": {"Estructura": "Flexible y artística", "Propósito": "Evocar emociones y explorar creatividad", "Lenguaje utilizado": "Figurado y simbólico", "Función en la comprensión del tema": "Complementar desde la experiencia estética"}, "Texto no literario": {"Estructura": "Organizada y lógica", "Propósito": "Informar o explicar datos concretos", "Lenguaje utilizado": "Directo y preciso", "Función en la comprensión del tema": "Complementar desde la información objetiva"}}}', 3, 0, true, '{comparación,"texto literario","texto no literario",análisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (339, 75, 'compare_contrast', 'all', '{"conceptos": ["Texto literario", "Texto no literario"], "criterios": ["Función principal", "Estructura", "Lenguaje utilizado"], "explicacion": "Los textos literarios tienen como función principal la expresión artística y emocional, suelen presentar estructuras narrativas o poéticas y usan un lenguaje figurado y estético. En cambio, los textos no literarios buscan informar, explicar o persuadir, presentan estructuras más organizadas y formales, y utilizan un lenguaje claro, directo y objetivo.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Texto literario": {"Estructura": "Narrativa, poética o dramática, con libertad creativa", "Función principal": "Expresar emociones, contar historias o transmitir experiencias estéticas", "Lenguaje utilizado": "Figurado, simbólico y estético"}, "Texto no literario": {"Estructura": "Organizada, con introducción, desarrollo y conclusión", "Función principal": "Informar, explicar o persuadir sobre hechos o ideas", "Lenguaje utilizado": "Claro, directo y objetivo"}}}', 4, 0, true, '{comparación,"texto literario","texto no literario"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (340, 75, 'open_ended', 'all', '{"pregunta": "Lee un texto literario y un texto no literario que aborden un mismo tema, luego elabora una tabla comparativa donde analices y contrastes cómo cada tipo de texto presenta y desarrolla la información. ¿De qué manera la función de cada texto contribuye a una comprensión más completa del tema?", "explicacion": "El docente debe evaluar que el estudiante identifique diferencias y similitudes claras entre los textos, reconociendo la función específica de cada uno para ampliar o profundizar en el tema tratado. También debe valorar que la tabla sea clara y que la argumentación escrita sea coherente y fundamentada.", "contexto_adicional": "Se proveerán dos textos sobre el mismo tema, uno literario y otro no literario, para que el estudiante realice la comparación.", "criterios_evaluacion": ["Claridad y precisión en la comparación de características entre textos literarios y no literarios (peso: 30%)", "Capacidad para analizar y explicar la función que cumple cada tipo de texto en la comprensión del tema (peso: 40%)", "Coherencia y estructura en la presentación de la tabla comparativa y en la argumentación escrita (peso: 30%)"]}', '{"puntos_clave": ["Diferencias en el enfoque y lenguaje entre texto literario y no literario", "Función del texto literario en generar reflexión o emoción", "Función del texto no literario en informar y explicar objetivamente"], "respuesta_modelo": "En la tabla comparativa, el texto literario presenta el tema desde una perspectiva subjetiva y emocional, utilizando recursos narrativos que buscan generar empatía en el lector, mientras que el texto no literario expone datos objetivos y estructurados para informar y explicar el tema de manera clara. La función del texto literario es provocar una conexión emocional y reflexiva, contribuyendo a que el lector valore el tema desde una experiencia personal; en cambio, el texto no literario busca entregar información y contexto que fundamentan y amplían la comprensión del tema. En conjunto, ambos textos complementan la comprensión: uno desde la emoción y la experiencia, el otro desde el conocimiento y la información concreta.", "requiere_revision_humana": true}', 4, 0, true, '{comparación,"análisis de textos","pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (341, 75, 'concept_map', 'all', '{"conceptos": ["Texto literario", "Texto no literario", "Función informativa", "Función estética", "Contextualización", "Comprensión del tema"], "explicacion": "Se espera que el estudiante muestre cómo los textos literarios y no literarios se diferencian principalmente en sus funciones (estética e informativa respectivamente), y cómo ambos contribuyen a la contextualización y a la comprensión del tema central estudiado.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la lectura y comprensión de textos en Lengua y Literatura?"}', '{"conceptos_centrales": ["Texto literario", "Texto no literario"], "relaciones_esperadas": [{"origen": "Texto literario", "destino": "Función estética", "relacion": "tiene"}, {"origen": "Texto no literario", "destino": "Función informativa", "relacion": "tiene"}, {"origen": "Función informativa", "destino": "Contextualización", "relacion": "apoya"}, {"origen": "Función estética", "destino": "Comprensión del tema", "relacion": "complementa"}, {"origen": "Contextualización", "destino": "Comprensión del tema", "relacion": "mejora"}], "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Comprensión lectora","Mapa conceptual",Análisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (342, 75, 'concept_map', 'all', '{"conceptos": ["Texto literario", "Texto no literario", "Función informativa", "Función estética", "Comprensión del tema", "Contextualización", "Análisis comparativo"], "explicacion": "El mapa conceptual debe mostrar cómo los textos literarios y no literarios se diferencian principalmente en sus funciones (estética e informativa respectivamente), y cómo ambos contribuyen a la comprensión del tema a través de la contextualización y el análisis comparativo.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la lectura y comprensión de textos literarios y no literarios?"}', '{"conceptos_centrales": ["Texto literario", "Texto no literario"], "relaciones_esperadas": [{"origen": "Texto literario", "destino": "Función estética", "relacion": "tiene"}, {"origen": "Texto no literario", "destino": "Función informativa", "relacion": "tiene"}, {"origen": "Función informativa", "destino": "Contextualización", "relacion": "contribuye a"}, {"origen": "Función estética", "destino": "Comprensión del tema", "relacion": "enriquece"}, {"origen": "Contextualización", "destino": "Análisis comparativo", "relacion": "facilita"}, {"origen": "Análisis comparativo", "destino": "Comprensión del tema", "relacion": "mejora"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Comprensión lectora","Mapa conceptual","Análisis textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (343, 76, 'criteria_evaluation', 'all', '{"rubrica": {"Capacidad para relacionar ambos textos": {"Excelente": "Establece conexiones explícitas y pertinentes entre el texto literario y el no literario, mostrando comprensión crítica y contextual.", "Satisfactorio": "Identifica algunas relaciones entre los textos, aunque de manera superficial o parcial.", "Necesita mejorar": "No logra relacionar ambos textos o las relaciones establecidas son incorrectas o muy vagas."}, "Claridad y coherencia en la argumentación": {"Excelente": "Presenta una argumentación clara, coherente y bien estructurada que facilita la comprensión del punto de vista.", "Satisfactorio": "La argumentación es entendible pero puede presentar ciertas inconsistencias o falta de estructura.", "Necesita mejorar": "La argumentación es confusa, desorganizada o difícil de seguir."}, "Justificación de la relevancia del texto no literario": {"Excelente": "Explica claramente y con argumentos sólidos cómo el texto no literario enriquece la interpretación del texto literario, mostrando comprensión profunda del contexto.", "Satisfactorio": "Reconoce la importancia del texto no literario para complementar la lectura literaria, pero con argumentos poco desarrollados o generales.", "Necesita mejorar": "No logra justificar adecuadamente la importancia del texto no literario o presenta argumentos poco claros y superficiales."}}, "explicacion": "Se espera que el estudiante analice críticamente cómo el texto no literario aporta al entendimiento del texto literario, estableciendo relaciones claras y argumentando con coherencia y profundidad.", "instruccion": "Evalúa el caso según la rúbrica y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Se presenta un texto literario clásico leído en clase y un artículo periodístico relacionado con el contexto histórico y social en que se escribió dicho texto. Evalúa cómo el artículo no literario contribuye a una mejor comprensión e interpretación del texto literario."}', '{"evaluacion_modelo": {"Capacidad para relacionar ambos textos": "Excelente", "Claridad y coherencia en la argumentación": "Satisfactorio", "Justificación de la relevancia del texto no literario": "Excelente"}, "justificacion_modelo": "El estudiante demuestra una comprensión profunda del aporte del texto no literario, estableciendo conexiones claras con el texto literario, aunque la argumentación puede mejorar en claridad y estructura.", "requiere_revision_humana": true}', 4, 0, true, '{evaluación,"texto no literario",interpretación,"debate guiado"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (344, 76, 'criteria_evaluation', 'all', '{"rubrica": {"Claridad y coherencia en la argumentación": {"Excelente": "Presenta una argumentación lógica, organizada y fácil de entender, con un lenguaje adecuado y correcto.", "Satisfactorio": "Argumenta de forma comprensible pero con algunas imprecisiones o falta de organización.", "Necesita mejorar": "La argumentación es confusa, desorganizada o presenta errores que dificultan la comprensión."}, "Uso de evidencia textual para apoyar la evaluación": {"Excelente": "Utiliza citas o ejemplos concretos de ambos textos para sustentar su evaluación de forma pertinente y coherente.", "Satisfactorio": "Incluye alguna evidencia textual, pero la relación con la evaluación es general o poco detallada.", "Necesita mejorar": "No utiliza evidencia textual o la que presenta es irrelevante o insuficiente para apoyar su evaluación."}, "Justificación de la relación entre texto literario y no literario": {"Excelente": "Explica claramente y con profundidad cómo el texto no literario complementa y enriquece la interpretación del texto literario, estableciendo conexiones precisas y relevantes.", "Satisfactorio": "Identifica la relación entre ambos textos y ofrece una justificación adecuada, aunque con menor profundidad o detalle.", "Necesita mejorar": "Presenta una justificación vaga o confusa, sin establecer vínculos claros entre los textos o sin comprender la importancia del texto no literario."}}, "explicacion": "Se espera que el estudiante reconozca la función del texto no literario en aportar contexto histórico y político que facilita una lectura más profunda de la novela. Debe fundamentar sus apreciaciones con ejemplos y mantener una argumentación coherente y clara.", "instruccion": "Evalúa cómo el texto no literario (artículo periodístico) enriquece la interpretación del texto literario (novela) usando la rúbrica proporcionada. Justifica tu calificación para cada criterio con argumentos claros.", "caso_a_evaluar": "Se entrega un ensayo breve que analiza la novela ''La casa de los espíritus'' de Isabel Allende, complementado con un artículo periodístico sobre la historia política de Chile en el siglo XX. El estudiante debe evaluar cómo el artículo no literario enriquece la comprensión de la novela y justificar su importancia para interpretar el texto literario."}', '{"evaluacion_modelo": {"Claridad y coherencia en la argumentación": "Excelente", "Uso de evidencia textual para apoyar la evaluación": "Satisfactorio", "Justificación de la relación entre texto literario y no literario": "Excelente"}, "justificacion_modelo": "El estudiante demuestra una comprensión profunda de cómo el artículo histórico complementa la novela, explicando claramente esta relación y usando algunos ejemplos textuales, aunque no todos muy detallados. Además, la argumentación es clara y bien estructurada, facilitando la comprensión de su evaluación.", "requiere_revision_humana": true}', 5, 0, true, '{evaluación,"debate guiado","comprensión de textos",intertextualidad}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (346, 76, 'open_ended', 'all', '{"pregunta": "Analiza un texto literario que hayas leído en clase y un texto no literario relacionado con el mismo tema o contexto. ¿De qué manera el texto no literario enriquece o modifica tu comprensión e interpretación del texto literario? Justifica tu respuesta con ejemplos específicos de ambos textos.", "explicacion": "El docente debe evaluar si el estudiante logra vincular ambos tipos de textos, demostrando pensamiento crítico y capacidad argumentativa. Se debe valorar tanto la precisión en el uso de ejemplos como la profundidad de la reflexión sobre la importancia del texto no literario para la interpretación literaria.", "contexto_adicional": "Se recomienda que el estudiante utilice textos trabajados durante el curso para sustentar su análisis, y que destaque la función y aporte del texto no literario en la comprensión del literario.", "criterios_evaluacion": ["Claridad y profundidad en la justificación de cómo el texto no literario enriquece la interpretación del texto literario (peso: 40%)", "Uso de ejemplos específicos y pertinentes extraídos de ambos textos para apoyar la argumentación (peso: 35%)", "Coherencia y estructura en la respuesta, con un análisis crítico y reflexivo (peso: 25%)"]}', '{"puntos_clave": ["Identificación de un texto literario y un texto no literario relacionados", "Justificación clara del aporte del texto no literario a la comprensión del literario", "Uso de ejemplos específicos para sustentar la argumentación"], "respuesta_modelo": "En la novela ''La casa de los espíritus'' de Isabel Allende, un texto literario que aborda temas de historia y memoria, la lectura de un artículo periodístico sobre la dictadura chilena (texto no literario) aporta un contexto histórico real que enriquece mi comprensión de la novela. El artículo me permitió entender mejor las referencias y el impacto social que se reflejan en la trama literaria, como la represión y las consecuencias políticas. Esto modifica mi interpretación al dar un marco real a los eventos ficticios, haciendo la historia más tangible y relevante. Por ejemplo, la descripción del artículo sobre las desapariciones forzadas me ayudó a comprender la profundidad del sufrimiento de los personajes y la crítica social presente en la novela. Así, el texto no literario funciona como un complemento esencial para interpretar con mayor profundidad el texto literario.", "requiere_revision_humana": true}', 4, 0, true, '{evaluar,"texto no literario","interpretación literaria","pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (347, 76, 'open_ended', 'all', '{"pregunta": "Analiza cómo la incorporación de un texto no literario, como un artículo periodístico o un ensayo histórico, puede enriquecer la interpretación de una obra literaria estudiada en clase. Justifica tu respuesta con ejemplos específicos y argumenta por qué es importante considerar estos textos en el análisis literario.", "explicacion": "El docente debe evaluar la capacidad del estudiante para integrar conocimientos y justificar con argumentos sólidos cómo los textos no literarios complementan y enriquecen la comprensión de textos literarios. Se valorará la profundidad del análisis, relevancia de los ejemplos y la coherencia en la exposición.", "contexto_adicional": "Se considera que los estudiantes han trabajado previamente con una obra literaria y al menos un texto no literario relacionado que aporte contexto histórico, social o cultural.", "criterios_evaluacion": ["Claridad y coherencia en la argumentación sobre la relación entre textos literarios y no literarios (peso: 40%)", "Uso de ejemplos específicos y pertinentes que evidencien la comprensión y aplicación del concepto (peso: 35%)", "Capacidad para justificar la importancia de los textos no literarios en la interpretación literaria, demostrando pensamiento crítico (peso: 25%)"]}', '{"puntos_clave": ["Relación entre el texto no literario y la obra literaria", "Ejemplos específicos que evidencien esta relación", "Justificación clara de la importancia del texto no literario para la interpretación literaria"], "respuesta_modelo": "La incorporación de un texto no literario, como un artículo periodístico que aborda el contexto histórico de la obra ''La casa de los espíritus'' de Isabel Allende, permite profundizar en el entendimiento de los conflictos sociales y políticos que influyen en la trama y los personajes. Por ejemplo, conocer las dictaduras en Chile y sus efectos sociales ayuda a interpretar las motivaciones de los personajes y el ambiente de tensión en la novela. Esto enriquece la lectura al ofrecer una perspectiva más amplia y fundamentada, evitando una interpretación superficial. Considerar estos textos es importante porque permite al lector situar la obra literaria en un marco real y comprensible, fomentando un análisis crítico y más completo.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Evaluación,"Interpretación literaria","Textos no literarios","Pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (348, 77, 'open_ended', 'all', '{"pregunta": "Diseña un folleto informativo que combine información de un texto literario y un texto no literario para explicar cómo un contexto histórico específico influye en la obra literaria seleccionada. Justifica la inclusión de cada fuente y cómo contribuyen a la comprensión del tema.", "explicacion": "El docente debe evaluar que el estudiante seleccione fuentes pertinentes, que integre correctamente la información para construir un recurso coherente y que explique claramente sus decisiones, demostrando un pensamiento crítico y creativo en la elaboración del folleto.", "contexto_adicional": "Puedes elegir cualquier obra literaria trabajada en clase junto con un texto histórico, ensayo o artículo que aporte información sobre el contexto en que se sitúa la obra.", "criterios_evaluacion": ["Relevancia y selección adecuada de los textos literario y no literario para abordar el contexto histórico (peso: 30%)", "Capacidad para integrar y relacionar la información de ambas fuentes de manera coherente y creativa en el folleto (peso: 40%)", "Claridad en la justificación de la elección de fuentes y en la explicación de cómo estas contribuyen a la comprensión del contexto y la obra (peso: 30%)"]}', '{"puntos_clave": ["Selección adecuada y pertinente de textos", "Integración coherente y creativa de información", "Justificación clara y fundamentada de la elección de fuentes"], "respuesta_modelo": "He seleccionado el texto literario ''La casa de los espíritus'' de Isabel Allende y un artículo histórico sobre la dictadura militar en Chile. En el folleto, explico cómo el contexto político y social de Chile durante ese período influye en los temas y personajes de la novela. El texto literario proporciona la narrativa y los elementos simbólicos, mientras que el texto no literario aporta hechos históricos que enriquecen la comprensión del trasfondo donde se desarrolla la historia. La combinación de ambos permite a los lectores entender la complejidad del contexto y su impacto en la obra.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Comprensión de textos","Contexto histórico","Creación de recursos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (349, 77, 'open_ended', 'all', '{"pregunta": "Diseña un texto explicativo que integre información de un texto literario y un texto no literario para abordar un tema o contexto específico relacionado con la obra literaria estudiada en clases. Explica cómo cada tipo de texto contribuye a la comprensión del tema y justifica la selección de las fuentes utilizadas.", "explicacion": "El docente debe evaluar si el estudiante logra combinar de manera coherente y clara la información de ambos textos, demostrando comprensión profunda del tema. Se debe valorar la capacidad crítica para explicar la función de cada texto en el recurso creado y la justificación en la elección de los textos, promoviendo la creatividad y el pensamiento analítico.", "contexto_adicional": "El estudiante debe basarse en una obra literaria que haya sido trabajada en clase y seleccionar un texto no literario relacionado que aporte información contextual, histórica o temática que complemente la lectura.", "criterios_evaluacion": ["Claridad y coherencia en la integración de la información de ambos textos (peso: 30%)", "Capacidad para explicar cómo cada texto complementa y enriquece la comprensión del tema o contexto (peso: 40%)", "Originalidad y justificación adecuada en la selección de los textos y en la presentación del recurso escrito (peso: 30%)"]}', '{"puntos_clave": ["Integración clara y coherente de información literaria y no literaria", "Explicación de la función y aporte de cada texto al tema abordado", "Justificación de la selección de fuentes y presentación original del recurso"], "respuesta_modelo": "El estudiante presenta un texto explicativo donde analiza la novela ''La casa de los espíritus'' de Isabel Allende y complementa la información con un artículo histórico sobre la dictadura militar en Chile. Explica cómo la novela utiliza elementos ficticios para narrar experiencias personales y familiares durante ese período, mientras que el texto no literario aporta datos históricos y contexto social que permiten entender mejor las circunstancias y la relevancia del relato. Justifica la selección del artículo por su enfoque detallado y confiable que ayuda a contextualizar la obra literaria, logrando un recurso escrito que facilita la comprensión del tema de la represión política y sus efectos en la sociedad chilena.", "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,primer_medio,evaluacion_crear,texto_explicativo,integracion_textos}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (350, 77, 'concept_map', 'all', '{"conceptos": ["Texto literario", "Texto no literario", "Contexto histórico", "Tema central", "Complementariedad", "Interpretación", "Información factual"], "explicacion": "Se espera que el estudiante conecte cómo los textos literarios y no literarios aportan información desde diferentes perspectivas (interpretativa y factual) para enriquecer la comprensión del tema central. El contexto histórico debe vincularse como un marco que influye en ambos tipos de textos. La complementariedad indica que ambos tipos de textos se utilizan para lograr una interpretación más completa del tema.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la comprensión y explicación de un tema o contexto específico a partir de textos literarios y no literarios?"}', '{"conceptos_centrales": ["Texto literario", "Texto no literario"], "relaciones_esperadas": [{"origen": "Texto literario", "destino": "Interpretación", "relacion": "ofrece"}, {"origen": "Texto no literario", "destino": "Información factual", "relacion": "proporciona"}, {"origen": "Contexto histórico", "destino": "Texto literario", "relacion": "influye en"}, {"origen": "Contexto histórico", "destino": "Texto no literario", "relacion": "influye en"}, {"origen": "Interpretación", "destino": "Información factual", "relacion": "complementa"}, {"origen": "Texto literario", "destino": "Texto no literario", "relacion": "se complementa con"}, {"origen": "Interpretación", "destino": "Tema central", "relacion": "ayuda a entender"}, {"origen": "Información factual", "destino": "Tema central", "relacion": "ayuda a explicar"}, {"origen": "Complementariedad", "destino": "Texto literario", "relacion": "describe la relación entre"}, {"origen": "Complementariedad", "destino": "Texto no literario", "relacion": "describe la relación entre"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Comprensión lectora","Mapa conceptual","OA Crear"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (351, 77, 'concept_map', 'all', '{"conceptos": ["Texto literario", "Texto no literario", "Contexto histórico", "Tema central", "Complementariedad", "Interpretación", "Información factual"], "explicacion": "Se espera que el estudiante conecte cómo los textos literarios y no literarios se complementan para enriquecer la interpretación del tema central, considerando el contexto histórico y la información factual que aportan los textos no literarios para una comprensión más profunda.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la comprensión y análisis de textos para explicar un tema específico?"}', '{"conceptos_centrales": ["Texto literario", "Texto no literario"], "relaciones_esperadas": [{"origen": "Texto no literario", "destino": "Información factual", "relacion": "proporciona"}, {"origen": "Texto literario", "destino": "Tema central", "relacion": "explora"}, {"origen": "Información factual", "destino": "Interpretación", "relacion": "enriquece"}, {"origen": "Contexto histórico", "destino": "Texto literario", "relacion": "influye en"}, {"origen": "Complementariedad", "destino": "Texto literario", "relacion": "une"}, {"origen": "Complementariedad", "destino": "Texto no literario", "relacion": "une"}, {"origen": "Interpretación", "destino": "Complementariedad", "relacion": "se basa en"}], "requiere_revision_humana": true}', 5, 0, true, '{mapa_conceptual,comprension_lectora,texto_literario,texto_no_literario,nivel_crear}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (352, 77, 'open_ended', 'all', '{"pregunta": "Diseña un recurso escrito (por ejemplo, un artículo, infografía o folleto) que combine información de un texto literario y un texto no literario para explicar un tema o contexto histórico, social o cultural relacionado con la obra literaria seleccionada. Justifica cómo integraste ambos tipos de textos y explica la relevancia de esta combinación para una mejor comprensión del tema.", "explicacion": "El docente debe evaluar la capacidad del estudiante para sintetizar información de diferentes fuentes, la creatividad y claridad en la presentación del recurso, y la calidad argumentativa en la justificación de la integración de los textos. Se recomienda revisar que el recurso permita al lector comprender mejor el tema o contexto elegido, y que la justificación evidencie pensamiento crítico y reflexión.", "contexto_adicional": "El estudiante puede seleccionar una obra literaria vista en clases y un texto no literario que aporte información contextual, histórica, social o cultural, para crear un recurso que facilite la comprensión más profunda del tema.", "criterios_evaluacion": ["Integración coherente y pertinente de información de textos literarios y no literarios para abordar el tema seleccionado (peso: 40%)", "Claridad y creatividad en la presentación del recurso escrito, considerando estructura, lenguaje y formato adecuado al público objetivo (peso: 30%)", "Justificación fundamentada sobre la elección de los textos y la importancia de combinarlos para enriquecer la comprensión del contexto o tema tratado (peso: 30%)"]}', '{"puntos_clave": ["Selección pertinente de un texto literario y un texto no literario relacionados", "Integración clara y coherente de ambos textos en el recurso escrito", "Justificación que explica la relevancia de combinar ambos tipos de textos", "Presentación creativa y adecuada al público", "Evidencia de reflexión crítica sobre la relación entre literatura y contexto"], "respuesta_modelo": "Para este proyecto, seleccioné la novela ''La casa de los espíritus'' de Isabel Allende y un artículo histórico sobre la dictadura chilena. Creé un folleto informativo que combina extractos literarios que reflejan la vida y las emociones de los personajes con datos históricos sobre el contexto político y social de Chile en esa época. Utilicé citas literarias para mostrar la experiencia personal y emocional, y el texto histórico para aportar hechos y contexto real. Esta combinación permite al lector entender no solo la historia oficial, sino también cómo afectó a las personas en su vida cotidiana, enriqueciendo así la comprensión del tema. La estructura del folleto incluye secciones claras, imágenes y un lenguaje accesible para estudiantes de enseñanza media.", "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,creacion_de_textos,integracion_de_informacion,pensamiento_critico}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (353, 78, 'multiple_choice', 'all', '{"opciones": {"A": "Presenta instrucciones detalladas para realizar una tarea.", "B": "Cuenta una historia con personajes, tiempo y lugar definidos.", "C": "Expone argumentos para convencer al lector sobre un tema.", "D": "Describe objetos o lugares usando adjetivos y sensaciones."}, "pregunta": "¿Cuál de las siguientes características corresponde al género narrativo?", "explicacion": "La opción B es correcta porque el género narrativo se caracteriza por contar una historia que incluye personajes, un tiempo y un lugar específicos. La opción A corresponde al género instructivo, la opción C al género argumentativo y la opción D al género descriptivo, por lo que son distractores que representan confusiones comunes entre géneros textuales."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{géneros_textuales,nivel_recordar,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (354, 78, 'multiple_choice', 'all', '{"opciones": {"A": "Presenta argumentos para defender una idea", "B": "Cuenta hechos o eventos que ocurren en un tiempo y lugar determinados", "C": "Explica paso a paso cómo realizar una actividad", "D": "Describe detalladamente las propiedades de un objeto o persona"}, "pregunta": "¿Cuál de las siguientes características es típica de una narración?", "explicacion": "La opción B es correcta porque una narración se caracteriza por contar hechos o eventos que suceden en un tiempo y lugar específicos, lo que la diferencia de otros géneros. La opción A corresponde a un texto argumentativo, la C a un instructivo o procedimiento, y la D a una descripción, que son géneros distintos con características propias."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"géneros textuales",narración,"lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (355, 78, 'multiple_choice', 'all', '{"opciones": {"A": "Presenta argumentos para convencer al lector sobre un punto de vista.", "B": "Relata hechos reales o ficticios organizados en una secuencia temporal.", "C": "Explica un procedimiento paso a paso para realizar una acción.", "D": "Describe detalladamente las características de una persona, lugar o cosa."}, "pregunta": "¿Cuál de las siguientes características corresponde al género textual narrativo?", "explicacion": "La opción B es correcta porque el género narrativo se caracteriza por contar una historia o hechos, reales o imaginarios, organizados en un orden temporal (inicio, desarrollo y desenlace). La opción A describe el género argumentativo, la C corresponde al instructivo o texto procedural, y la D se relaciona con el texto descriptivo. Estos distractores son errores comunes al confundir las funciones y características de los géneros textuales."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{géneros_textuales,narrativo,primero_medio,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (356, 78, 'true_false', 'all', '{"afirmacion": "El género narrativo se caracteriza por contar una historia con personajes, un espacio y un tiempo definidos.", "explicacion": "Esta afirmación es verdadera porque el género narrativo efectivamente presenta una historia que incluye personajes que actúan en un espacio y tiempo específicos, lo que permite al lector comprender el desarrollo de los hechos."}', '{"es_verdadero": true}', 1, 0, true, '{géneros_textuales,narrativo,características}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (357, 78, 'fill_blanks', 'all', '{"texto": "Los géneros textuales se diferencian por sus [BLANK_1], que son características propias que definen su estructura y propósito. Antes de escribir un nuevo género, es fundamental [BLANK_2] sus características para adecuar el texto correctamente.", "pistas": ["Se refiere a los elementos que distinguen un tipo de texto de otro", "Es una acción que implica buscar información o investigar"], "explicacion": "Los géneros textuales tienen características particulares que los identifican, como la estructura, el estilo y el propósito comunicativo. Investigar estas características permite al escritor adaptar su texto al género que va a trabajar."}', '{"respuestas_correctas": {"BLANK_1": ["características", "rasgos", "propiedades"], "BLANK_2": ["investigar", "explorar", "analizar"]}}', 2, 0, true, '{"géneros textuales",escritura,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (358, 79, 'multiple_choice', 'all', '{"opciones": {"A": "Presenta información ordenada y objetiva para informar sobre un tema específico.", "B": "Cuenta una historia con personajes, un ambiente y una secuencia de eventos.", "C": "Expresa opiniones o argumentos para persuadir al lector sobre un punto de vista.", "D": "Enumera instrucciones precisas para realizar una tarea o actividad."}, "pregunta": "¿Cuál de las siguientes opciones describe mejor la característica principal de un texto narrativo?", "explicacion": "La opción B es correcta porque un texto narrativo se caracteriza por contar una historia que incluye personajes, un ambiente y una secuencia de eventos. La opción A corresponde a textos expositivos, la C a textos argumentativos y la D a textos instructivos. Estos distractores representan errores comunes al confundir tipos de textos con características de otros géneros."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"géneros textuales",narrativo,comprensión,escritura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (359, 79, 'multiple_choice', 'all', '{"opciones": {"A": "Presentar argumentos para convencer al lector sobre un tema.", "B": "Contar una historia que puede ser real o ficticia para entretener o informar.", "C": "Explicar paso a paso cómo realizar una actividad o procedimiento.", "D": "Describir detalladamente las características de una persona, objeto o lugar."}, "pregunta": "¿Cuál de las siguientes opciones describe mejor el propósito principal de un texto narrativo?", "explicacion": "La opción B es correcta porque el texto narrativo se centra en contar una historia con un inicio, desarrollo y desenlace, buscando entretener o informar mediante hechos reales o imaginarios. La opción A corresponde a un texto argumentativo, la C a un texto instructivo o procedural, y la D a un texto descriptivo. Estos distractores reflejan confusiones comunes sobre los propósitos y características de los géneros textuales."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"géneros textuales","texto narrativo",comprensión,"primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (360, 79, 'true_false', 'all', '{"afirmacion": "Antes de escribir un texto, es importante investigar las características del género para adecuar el contenido y el estilo.", "explicacion": "Esta afirmación es verdadera porque conocer las características específicas de un género textual permite organizar las ideas, utilizar el lenguaje adecuado y cumplir con el propósito comunicativo, lo que mejora la efectividad de la escritura."}', '{"es_verdadero": true}', 2, 0, true, '{"géneros textuales",comprensión,escritura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (361, 79, 'drag_drop_matching', 'all', '{"explicacion": "Cada género textual tiene características específicas: la narración ordena eventos; la descripción detalla características; el diálogo es la conversación entre personajes; la instrucción explica procedimientos; y el ensayo presenta argumentos y opiniones.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Relato de hechos o eventos en orden cronológico", "Texto que explica cómo realizar una acción paso a paso", "Intercambio de palabras entre personajes", "Texto que presenta opiniones y argumentos sobre un tema", "Representación detallada de personas, lugares o cosas"], "columna_izquierda": ["Narración", "Descripción", "Diálogo", "Instrucción", "Ensayo"]}', '{"emparejamientos_correctos": {"Ensayo": "Texto que presenta opiniones y argumentos sobre un tema", "Diálogo": "Intercambio de palabras entre personajes", "Narración": "Relato de hechos o eventos en orden cronológico", "Descripción": "Representación detallada de personas, lugares o cosas", "Instrucción": "Texto que explica cómo realizar una acción paso a paso"}}', 2, 0, true, '{géneros_textuales,comprensión_lectora,escritura_creativa}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (362, 79, 'sequencing', 'all', '{"explicacion": "El orden correcto comienza con seleccionar el género a investigar, luego se lee ejemplos para observar sus características, se revisa material adicional para profundizar, se resumen las características y propósitos, y finalmente se aplican estos conocimientos al planificar la escritura.", "instruccion": "Ordena los siguientes pasos para investigar las características de un género textual antes de escribir.", "elementos_desordenados": ["Leer varios ejemplos del género para identificar sus características.", "Seleccionar el género textual que se desea investigar.", "Resumir las características y propósitos principales del género.", "Revisar fuentes confiables o material didáctico sobre el género.", "Aplicar lo aprendido en la planificación del propio texto."]}', '{"orden_correcto": ["Seleccionar el género textual que se desea investigar.", "Leer varios ejemplos del género para identificar sus características.", "Revisar fuentes confiables o material didáctico sobre el género.", "Resumir las características y propósitos principales del género.", "Aplicar lo aprendido en la planificación del propio texto."]}', 3, 0, true, '{escritura,géneros_textuales,investigación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (363, 80, 'multiple_choice', 'all', '{"opciones": {"A": "Usar un lenguaje informal, incluir emoticones y despedirse con un \"nos vemos\".", "B": "Incluir un saludo formal, presentar claramente el motivo de la carta y usar un cierre adecuado como \"Atentamente\".", "C": "Escribir en primera persona sin preocuparse por la estructura y usar frases coloquiales para mayor cercanía.", "D": "Omitir el saludo y comenzar directamente con la queja para captar rápido la atención."}, "pregunta": "¿Cuál de las siguientes opciones refleja mejor el uso adecuado de las características investigadas para redactar un texto del género carta formal dirigido a una empresa?", "explicacion": "La opción B es correcta porque una carta formal requiere un saludo respetuoso, claridad en el motivo y un cierre adecuado, respetando las características propias del género. La opción A es incorrecta porque el lenguaje informal y emoticones no corresponden a la formalidad del género. La opción C falla al usar frases coloquiales y no cuidar la estructura, lo que no es apropiado para una carta formal. La opción D omite elementos esenciales del género, como el saludo, afectando la adecuación del texto."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{escritura,"géneros textuales","carta formal",aplicar}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (383, 84, 'multiple_choice', 'all', '{"opciones": {"A": "Presenta una narrativa con personajes y una trama", "B": "Expone claramente el tema y organiza la información para facilitar la comprensión", "C": "Incluye opiniones personales sobre un tema", "D": "Utiliza recursos poéticos y lenguaje figurado para crear imágenes"}, "pregunta": "¿Cuál de las siguientes características es esencial en un texto explicativo?", "explicacion": "La respuesta correcta es B porque un texto explicativo debe presentar claramente el tema y organizar la información de manera lógica para facilitar la comprensión del lector. La opción A describe características de un texto narrativo; la C corresponde más a un texto argumentativo o de opinión; y la D es propia de textos literarios o poéticos, no explicativos."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"textos explicativos","características de texto","lengua y literatura","enseñanza media"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (364, 80, 'multiple_choice', 'all', '{"opciones": {"A": "Ignorar las características del género y escribir un texto libre que refleje solo las ideas personales.", "B": "Incorporar las características propias del género investigado y adaptar el contenido para cumplir con el propósito comunicativo.", "C": "Copiar un texto del género sin hacer modificaciones, para asegurar que se ajusta a las características.", "D": "Usar un estilo de otro género que prefieras porque te resulta más fácil, aunque no corresponda al propósito."}, "pregunta": "¿Cuál de las siguientes opciones representa mejor la manera de utilizar las características investigadas de un género literario para redactar un texto adecuado a ese género y propósito específico?", "explicacion": "La respuesta correcta es la opción B, porque implica aplicar de forma flexible y creativa las características propias del género para redactar un texto que cumple con un propósito específico, tal como se requiere en el objetivo de aprendizaje. La opción A es incorrecta porque no considera las características del género investigado. La opción C es errónea porque copiar sin adaptar no demuestra aplicación ni comprensión creativa. La opción D representa un error conceptual común, ya que usar otro género por preferencia sin considerar el propósito limita la adecuación del texto."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{escritura,"géneros literarios",aplicación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (365, 80, 'drag_drop_matching', 'all', '{"explicacion": "Cada emparejamiento conecta el género literario con su característica o función principal: la Narrativa cuenta una historia; la Descripción crea imágenes detalladas; el Diálogo representa conversaciones; el Texto argumentativo defiende una postura; la Epístola es una carta con intención comunicativa; y la Poesía transmite emociones mediante la forma estética.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Carta dirigida a un destinatario con intención personal o formal", "Relato de hechos que cuentan una historia con personajes y acciones", "Intercambio hablado entre dos o más personajes en un texto", "Expresión estética que busca transmitir emociones y sensaciones con ritmo y métrica", "Texto que presenta una postura y busca convencer al lector con razones", "Retrato detallado de personas, lugares o situaciones para crear una imagen"], "columna_izquierda": ["Narrativa", "Descripción", "Diálogo", "Texto argumentativo", "Epístola", "Poesía"]}', '{"emparejamientos_correctos": {"Poesía": "Expresión estética que busca transmitir emociones y sensaciones con ritmo y métrica", "Diálogo": "Intercambio hablado entre dos o más personajes en un texto", "Epístola": "Carta dirigida a un destinatario con intención personal o formal", "Narrativa": "Relato de hechos que cuentan una historia con personajes y acciones", "Descripción": "Retrato detallado de personas, lugares o situaciones para crear una imagen", "Texto argumentativo": "Texto que presenta una postura y busca convencer al lector con razones"}}', 3, 0, true, '{"géneros literarios",escritura,"lengua y literatura","primer medio",aplicar}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (366, 80, 'sequencing', 'all', '{"explicacion": "Primero se investiga para conocer las características del género, luego se planifican las ideas y estructura del texto. Después se escribe un borrador, que posteriormente se revisa y corrige para adecuarlo al propósito. Finalmente, se presenta el texto final.", "instruccion": "Ordena los siguientes pasos para redactar un texto adecuado a un género específico", "elementos_desordenados": ["Escribir el borrador del texto", "Investigar las características del género", "Revisar y corregir el texto para adecuarlo al propósito", "Planificar las ideas principales y la estructura", "Presentar el texto final"]}', '{"orden_correcto": ["Investigar las características del género", "Planificar las ideas principales y la estructura", "Escribir el borrador del texto", "Revisar y corregir el texto para adecuarlo al propósito", "Presentar el texto final"]}', 3, 0, true, '{escritura,géneros_textuales,secuencia,primeromedia}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (367, 80, 'open_ended', 'all', '{"pregunta": "Elige un género textual que no hayas trabajado anteriormente en clase (por ejemplo, una carta formal, un artículo de opinión o una crónica). Investiga sus características principales y redacta un texto original siguiendo esas características, asegurándote de adecuar el lenguaje y la estructura al propósito comunicativo del género seleccionado. Explica brevemente cómo aplicaste las características investigadas en tu texto.", "explicacion": "El docente debe evaluar si el estudiante aplica correctamente las características del género textual seleccionado, analizando la adecuación formal y funcional del texto, además de valorar la creatividad y la coherencia en la expresión escrita. La explicación del estudiante sobre su proceso permitirá verificar la comprensión y aplicación consciente del género.", "contexto_adicional": "El estudiante debe investigar previamente las características del género textual elegido antes de redactar el texto.", "criterios_evaluacion": ["Adecuación al género seleccionado: El texto debe reflejar las características esenciales del género, como estructura, lenguaje y propósito (peso: 40%).", "Creatividad y claridad en la expresión: Uso efectivo y original del lenguaje para comunicar ideas de manera coherente y atractiva (peso: 30%).", "Explicación del proceso: Claridad y precisión en la descripción de cómo se aplicaron las características del género en la redacción (peso: 30%)."]}', '{"puntos_clave": ["Selección clara y adecuada del género textual.", "Incorporación de características específicas del género (estructura, lenguaje, propósito).", "Explicación coherente y detallada del proceso de aplicación de las características."], "respuesta_modelo": "He elegido redactar una carta formal para solicitar información sobre un curso. Primero, investigué que una carta formal debe incluir encabezado con fecha y destinatario, saludo formal, cuerpo claro y conciso, y despedida respetuosa. En mi texto, comencé con la fecha y el nombre de la institución, usé un saludo como ''Estimados señores'', formulé mi solicitud de manera directa y educada, y terminé con un cierre cordial y mi nombre completo. Utilicé un lenguaje formal y respetuoso para adecuarme al propósito de la carta.", "requiere_revision_humana": true}', 3, 0, true, '{escritura,géneros_textuales,aplicación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (368, 81, 'compare_contrast', 'all', '{"conceptos": ["Ensayo", "Narrativa"], "criterios": ["Propósito", "Estructura", "Lenguaje", "Público objetivo"], "explicacion": "El ensayo y la narrativa son géneros textuales con propósitos diferentes: el ensayo busca argumentar o reflexionar, mientras que la narrativa busca contar una historia. En cuanto a la estructura, el ensayo suele ser más libre y organizado en párrafos con introducción, desarrollo y conclusión; la narrativa sigue una secuencia cronológica con introducción, nudo y desenlace. El lenguaje del ensayo es formal y analítico, mientras que la narrativa utiliza un lenguaje más descriptivo y emocional. Finalmente, el público objetivo del ensayo suele ser más académico o crítico, mientras que la narrativa se dirige a un público general interesado en historias.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Ensayo": {"Lenguaje": "Formal, analítico y objetivo", "Estructura": "Introducción, desarrollo y conclusión, estructura libre", "Propósito": "Argumentar o reflexionar sobre un tema", "Público objetivo": "Académico o crítico"}, "Narrativa": {"Lenguaje": "Descriptivo, emotivo y creativo", "Estructura": "Secuencia temporal: introducción, nudo y desenlace", "Propósito": "Contar una historia o relato", "Público objetivo": "General, interesado en historias"}}}', 3, 0, true, '{comparar,"géneros literarios","primer medio","análisis textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (369, 81, 'compare_contrast', 'all', '{"conceptos": ["Texto narrativo", "Texto descriptivo"], "criterios": ["Propósito", "Estructura", "Lenguaje utilizado"], "explicacion": "El texto narrativo tiene como propósito contar una historia o un evento, presenta una estructura con introducción, desarrollo y desenlace, y utiliza un lenguaje que busca generar interés y secuencialidad. En cambio, el texto descriptivo busca representar detalladamente personas, lugares o cosas, su estructura es más libre y se centra en la presentación de detalles, y su lenguaje es más sensorial y objetivo.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Texto narrativo": {"Estructura": "Introducción, desarrollo y desenlace", "Propósito": "Contar una historia o evento", "Lenguaje utilizado": "Secuencial y atractivo, con verbos de acción"}, "Texto descriptivo": {"Estructura": "Estructura libre, centrada en detalles", "Propósito": "Representar detalladamente personas, lugares o cosas", "Lenguaje utilizado": "Sensorial, objetivo y detallado"}}}', 4, 0, true, '{comparar,"géneros literarios","texto narrativo","texto descriptivo"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (370, 81, 'open_ended', 'all', '{"pregunta": "Analiza dos textos de géneros diferentes que hayas leído en clase (por ejemplo, un poema y un artículo de opinión). Compara sus características principales y propósitos, y explica cuál de los dos géneros sería más adecuado para expresar una idea personal sobre un tema social actual. Justifica tu elección.", "explicacion": "El docente debe evaluar que el estudiante sea capaz de distinguir las características formales y funcionales de los géneros, realizar una comparación analítica y fundamentar su elección con argumentos claros relacionados con el propósito expresivo en contextos sociales.", "contexto_adicional": "Se sugiere que el estudiante utilice textos trabajados previamente en clase para facilitar la comparación y reflexión.", "criterios_evaluacion": ["Identificación clara y precisa de las características de ambos géneros (peso: 30%)", "Comparación crítica y coherente entre los géneros en relación a sus propósitos comunicativos (peso: 40%)", "Argumentación fundamentada para seleccionar el género más adecuado para la expresión personal en un tema social (peso: 30%)"]}', '{"puntos_clave": ["Diferenciación entre características formales y lenguaje de cada género", "Reconocimiento de los propósitos comunicativos (emocional versus persuasivo)", "Justificación basada en la adecuación del género para expresar ideas personales sobre temas sociales"], "respuesta_modelo": "El poema y el artículo de opinión, aunque ambos pueden tratar temas sociales, presentan características muy diferentes. El poema utiliza un lenguaje figurado, ritmo y metáforas para evocar emociones y ofrecer una interpretación subjetiva, mientras que el artículo de opinión presenta un lenguaje claro y argumentativo con el objetivo de persuadir al lector. Para expresar una idea personal sobre un tema social actual, el artículo de opinión sería más adecuado porque permite desarrollar argumentos explícitos, presentar evidencias y convencer a otros sobre un punto de vista específico. Sin embargo, el poema puede ser útil para expresar sentimientos profundos y experiencias personales de manera creativa. Por lo tanto, la elección depende del objetivo comunicativo, pero para una expresión clara y persuasiva, el artículo es preferible.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Primer Año Medio","Análisis de Textos","Géneros Literarios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (371, 81, 'concept_map', 'all', '{"conceptos": ["Género literario", "Características del texto", "Propósito comunicativo", "Estructura", "Estilo", "Audiencia"], "explicacion": "Se espera que el estudiante analice cómo las características del texto, la estructura y el estilo se relacionan con el género literario y su propósito comunicativo, considerando también la audiencia a la que se dirigen para poder clasificar y seleccionar el género más adecuado.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la clasificación y selección de géneros literarios?"}', '{"conceptos_centrales": ["Género literario", "Características del texto"], "relaciones_esperadas": [{"origen": "Género literario", "destino": "Características del texto", "relacion": "determina"}, {"origen": "Características del texto", "destino": "Estructura", "relacion": "influyen en"}, {"origen": "Características del texto", "destino": "Estilo", "relacion": "influyen en"}, {"origen": "Género literario", "destino": "Propósito comunicativo", "relacion": "tiene como propósito"}, {"origen": "Propósito comunicativo", "destino": "Audiencia", "relacion": "considera"}, {"origen": "Audiencia", "destino": "Estilo", "relacion": "modifica"}], "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Mapa conceptual","Géneros literarios","Análisis textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (372, 81, 'concept_map', 'all', '{"conceptos": ["Género literario", "Características del texto", "Propósito comunicativo", "Estructura del texto", "Registro y estilo", "Contexto de producción"], "explicacion": "Se espera que el estudiante conecte cómo las características del texto y su estructura están determinadas por el género literario, que a su vez responde a un propósito comunicativo particular. Además, debe vincular cómo el registro y estilo varían según el contexto de producción y el género seleccionado.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la selección y clasificación de textos según su género?"}', '{"conceptos_centrales": ["Género literario", "Propósito comunicativo"], "relaciones_esperadas": [{"origen": "Género literario", "destino": "Características del texto", "relacion": "determina"}, {"origen": "Género literario", "destino": "Estructura del texto", "relacion": "define"}, {"origen": "Propósito comunicativo", "destino": "Registro y estilo", "relacion": "influye en"}, {"origen": "Contexto de producción", "destino": "Registro y estilo", "relacion": "modifica"}, {"origen": "Características del texto", "destino": "Propósito comunicativo", "relacion": "reflejan"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Mapa Conceptual","Género Literario","Primeros Medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (373, 82, 'criteria_evaluation', 'all', '{"rubrica": {"Adecuación al género": {"Excelente": "El texto refleja con claridad y coherencia las características esenciales del género fantástico, incluyendo el uso adecuado de elementos sobrenaturales y la creación de atmósfera.", "Satisfactorio": "El texto presenta varias características del género fantástico, aunque con cierta falta de profundidad o coherencia en algunos elementos.", "Necesita mejorar": "El texto no logra incorporar claramente los elementos del género fantástico o los presenta de manera confusa o inapropiada."}, "Uso del lenguaje y estilo": {"Excelente": "El lenguaje es preciso, variado y acorde al propósito del texto, contribuyendo eficazmente a la atmósfera y el impacto buscado.", "Satisfactorio": "El lenguaje es adecuado, aunque en ocasiones repetitivo o poco elaborado, sin afectar gravemente el propósito del texto.", "Necesita mejorar": "El lenguaje es pobre, repetitivo o inapropiado, lo que dificulta la expresión clara y efectiva de las ideas."}, "Coherencia y estructura narrativa": {"Excelente": "La historia tiene una estructura clara y lógica que facilita la comprensión, con una secuencia de eventos bien organizada.", "Satisfactorio": "La estructura es generalmente clara, pero existen algunos saltos o incoherencias que dificultan parcialmente la comprensión.", "Necesita mejorar": "La estructura es desordenada o incoherente, lo que impide entender la historia correctamente."}}, "explicacion": "Se espera que el estudiante identifique y valore críticamente cómo el texto cumple o no con las características del género fantástico, analice la lógica y organización de la historia y evalúe el uso del lenguaje en relación con el propósito comunicativo del texto.", "instruccion": "Evalúa el cuento breve según la rúbrica presentada para cada criterio y justifica tu calificación en cada uno, considerando la adecuación al género, la coherencia estructural y el uso del lenguaje.", "caso_a_evaluar": "Se presenta un cuento breve escrito por un estudiante de Primero Medio que pertenece al género fantástico. El texto incluye elementos propios del género, como lo sobrenatural y lo inesperado, y busca generar asombro en el lector. Sin embargo, presenta algunas incoherencias en la estructura narrativa y el uso del lenguaje."}', '{"evaluacion_modelo": {"Adecuación al género": "Satisfactorio", "Uso del lenguaje y estilo": "Necesita mejorar", "Coherencia y estructura narrativa": "Satisfactorio"}, "justificacion_modelo": "El texto incluye elementos del género fantástico, aunque algunos están poco desarrollados o son inconsistentes. La estructura narrativa presenta algunos saltos que afectan la comprensión, y el lenguaje es repetitivo y poco preciso, dificultando la expresión efectiva de la historia.", "requiere_revision_humana": true}', 4, 0, true, '{evaluacion_critica,genero_fantastico,escritura_creativa,primer_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (374, 82, 'criteria_evaluation', 'all', '{"rubrica": {"Adecuación al género narrativo": {"Excelente": "El texto presenta una estructura narrativa clara (introducción, desarrollo y desenlace), con personajes y ambiente bien definidos, respetando las características propias del género.", "Satisfactorio": "El texto presenta una estructura básica del género narrativo, aunque con algunos elementos poco desarrollados o confusos.", "Necesita mejorar": "El texto carece de una estructura narrativa clara y/o no presenta elementos propios del género."}, "Adecuación a la situación comunicativa": {"Excelente": "El registro y lenguaje utilizado son apropiados para el contexto y el público al que se dirige el texto.", "Satisfactorio": "El registro y lenguaje son generalmente apropiados, aunque presentan algunas inconsistencias con la situación comunicativa.", "Necesita mejorar": "El registro y lenguaje no son adecuados para el contexto ni para el público objetivo."}, "Coherencia con el propósito comunicativo": {"Excelente": "El texto logra expresar de manera clara y creativa la experiencia personal, conectando con el lector y manteniendo la intención comunicativa.", "Satisfactorio": "El texto expresa la experiencia personal, pero la conexión con el lector o la claridad en la intención comunicativa es limitada.", "Necesita mejorar": "El texto no logra expresar claramente la experiencia personal ni mantener una intención comunicativa definida."}}, "explicacion": "Se espera que el estudiante reconozca y valore la estructura y características del género narrativo, la efectividad del texto para cumplir con su propósito de comunicar una experiencia personal y la pertinencia del lenguaje usado según el contexto comunicativo.", "instruccion": "Evalúa el texto narrativo presentado según la rúbrica proporcionada y justifica tu calificación en cada criterio.", "caso_a_evaluar": "Se presenta un texto narrativo escrito por un estudiante de Primero Medio, que busca relatar una experiencia personal utilizando elementos propios del género narrativo, como la estructura clara, personajes definidos, y un ambiente coherente. El texto será evaluado en función de su adecuación al género, propósito comunicativo y situación comunicativa."}', '{"evaluacion_modelo": {"Adecuación al género narrativo": "Satisfactorio", "Adecuación a la situación comunicativa": "Satisfactorio", "Coherencia con el propósito comunicativo": "Excelente"}, "justificacion_modelo": "El texto presenta una estructura narrativa básica, aunque algunos elementos como el desarrollo de personajes son poco profundos, por lo que se califica como satisfactorio en adecuación al género. Sin embargo, logra expresar claramente la experiencia personal con un lenguaje creativo, por lo que la coherencia con el propósito comunicativo es excelente. En cuanto a la adecuación al contexto, el registro es mayormente apropiado pero con algunas inconsistencias, por lo que se califica como satisfactorio.", "requiere_revision_humana": true}', 5, 0, true, '{escritura,"evaluación crítica","género narrativo","primer medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (375, 82, 'criteria_evaluation', 'all', '{"rubrica": {"Adecuación al género narrativo": {"Excelente": "El texto presenta claramente todos los elementos característicos del género narrativo, tales como personajes definidos, ambiente bien descrito, secuencia temporal coherente y resolución del conflicto.", "Satisfactorio": "El texto presenta la mayoría de los elementos del género narrativo, aunque algunos están poco desarrollados o presentan imprecisiones.", "Necesita mejorar": "El texto carece de varios elementos propios del género narrativo o estos no están claramente identificables."}, "Coherencia y organización del texto": {"Excelente": "El texto está organizado de manera lógica y coherente, facilitando la comprensión del relato sin confusiones.", "Satisfactorio": "El texto presenta una organización adecuada, aunque algunas partes pueden generar dudas o confusión momentánea.", "Necesita mejorar": "El texto presenta desorden o incoherencias que dificultan la comprensión del relato."}, "Adecuación del lenguaje y estilo expresivo": {"Excelente": "El lenguaje es claro, variado y adecuado para expresar emociones y describir situaciones, logrando un impacto efectivo en el lector.", "Satisfactorio": "El lenguaje es generalmente adecuado, aunque presenta repeticiones o falta de variedad que afectan levemente la expresión.", "Necesita mejorar": "El lenguaje es pobre, repetitivo o inadecuado para el propósito expresivo, dificultando la conexión con el lector."}}, "explicacion": "Se espera que el estudiante identifique y valore críticamente la presencia y calidad de los elementos del género narrativo, la coherencia en la organización del texto y la adecuación del lenguaje y estilo expresivo. Debe argumentar claramente por qué asigna cada nivel de desempeño, fundamentando su juicio en evidencias específicas del texto.", "instruccion": "Evalúa el texto narrativo presentado según los criterios de la rúbrica y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Se presenta un texto narrativo escrito por un estudiante de Primero Medio, que relata una experiencia personal durante un viaje familiar. El texto incluye elementos propios del género narrativo, como personajes, ambiente, secuencia temporal y un conflicto. Sin embargo, algunas partes del texto presentan incoherencias en la organización y el uso del lenguaje no siempre es adecuado para el propósito expresivo."}', '{"evaluacion_modelo": {"Adecuación al género narrativo": "Satisfactorio", "Coherencia y organización del texto": "Satisfactorio", "Adecuación del lenguaje y estilo expresivo": "Necesita mejorar"}, "justificacion_modelo": "El texto presenta los elementos básicos del género narrativo, pero algunos están poco desarrollados, por lo que no alcanza el nivel excelente. La organización general es adecuada, aunque se detectan algunas incoherencias menores. En cuanto al lenguaje, hay repetición y falta de variedad que afecta la expresividad, por lo que se ubica en nivel necesita mejorar.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Evaluación crítica",Narrativa,"Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (376, 82, 'open_ended', 'all', '{"pregunta": "Analiza un texto narrativo breve que hayas escrito o leído recientemente. Evalúa cómo se adecuan sus características al género, propósito y situación comunicativa, y argumenta qué aspectos podrían mejorarse para aumentar su efectividad comunicativa.", "explicacion": "El docente debe valorar que el estudiante identifique correctamente características del género narrativo (como estructura, personajes, tiempo, espacio), que analice cómo estas características responden al propósito y contexto comunicativo, y que proponga mejoras fundamentadas que evidencien creatividad y comprensión del texto.", "contexto_adicional": "El estudiante puede elegir un texto narrativo propio o ajeno que haya trabajado en clase o leído recientemente. Se espera que realice un análisis crítico y reflexivo sobre la adecuación del texto a su género y función comunicativa.", "criterios_evaluacion": ["Identificación clara y precisa de las características del género narrativo presentes en el texto (peso: 30%)", "Capacidad para evaluar la adecuación del texto en relación con su propósito y situación comunicativa, fundamentando los argumentos (peso: 40%)", "Propuesta creativa y coherente de mejoras específicas para aumentar la efectividad del texto (peso: 30%)"]}', '{"puntos_clave": ["Reconocimiento de la estructura narrativa y sus elementos", "Análisis del propósito y contexto comunicativo del texto", "Propuestas específicas y fundamentadas para mejorar la efectividad del texto"], "respuesta_modelo": "El texto narrativo analizado presenta las características típicas del género, como una estructura clara con introducción, desarrollo y desenlace, además de personajes bien definidos y un espacio y tiempo coherentes. Su propósito es entretener e involucrar al lector en una experiencia vivencial, lo cual logra mediante un lenguaje descriptivo y un ritmo adecuado. Sin embargo, la situación comunicativa podría mejorarse si se profundiza en la motivación de los personajes para generar mayor empatía. También sugiero enriquecer el diálogo para hacerlo más natural y aportar dinamismo. Estas mejoras aumentarían la efectividad comunicativa del texto al conectar mejor con el lector y clarificar el mensaje central.", "requiere_revision_humana": true}', 4, 0, true, '{"evaluación crítica","género narrativo","escritura creativa"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (377, 82, 'open_ended', 'all', '{"pregunta": "Elige un texto que hayas escrito en clases recientemente y otro texto (puede ser un artículo, cuento o ensayo) de un autor reconocido. Analiza y evalúa la adecuación y efectividad de ambos textos en relación con su género, propósito y situación comunicativa. ¿En qué aspectos consideras que cada texto logra o no su objetivo comunicativo? Justifica tu respuesta con ejemplos concretos.", "explicacion": "El docente debe valorar que el estudiante pueda evaluar críticamente los textos vinculándolos con las características del género, así como con el propósito y situación comunicativa. Se enfatiza la argumentación sustentada en ejemplos y la capacidad de reflexión personal sobre la efectividad comunicativa.", "contexto_adicional": "Los estudiantes pueden usar textos trabajados en clases o buscar textos breves de autores reconocidos que correspondan a géneros estudiados. Se sugiere que el texto propio sea reciente para facilitar la reflexión.", "criterios_evaluacion": ["Análisis de la adecuación al género de cada texto y su impacto en la efectividad comunicativa (peso: 40%)", "Capacidad para identificar y argumentar la relación entre propósito y situación comunicativa en ambos textos (peso: 35%)", "Claridad, coherencia y profundidad en la justificación y uso de ejemplos concretos (peso: 25%)"]}', '{"puntos_clave": ["Identificación de características propias del género de cada texto", "Relación entre propósito comunicativo y recursos utilizados en los textos", "Justificación con ejemplos concretos que evidencien la efectividad o limitaciones"], "respuesta_modelo": "He elegido un cuento que escribí en clase y un artículo de opinión publicado en un periódico. Mi cuento, siendo del género narrativo, cumple con las características de estructura clara: introducción, desarrollo y desenlace. Utiliza lenguaje descriptivo y recursos literarios, lo que logra generar interés y emoción en el lector, adecuado para su propósito de entretener y transmitir una experiencia personal. En cambio, el artículo de opinión tiene un lenguaje formal, argumentativo y directo, propio del género, y está orientado a persuadir al lector sobre un tema social. Su estructura clara con tesis y argumentos fortalece su efectividad comunicativa. En comparación, mi cuento es efectivo para expresar emociones y contar una historia, pero su alcance es más limitado en cuanto a persuadir o informar. El artículo, en cambio, está diseñado para influir en la opinión pública, lo que logra mediante ejemplos y datos concretos. Por lo tanto, ambos textos son adecuados y efectivos dentro de sus géneros y propósitos, aunque con enfoques comunicativos distintos.", "requiere_revision_humana": true}', 5, 0, true, '{"evaluación crítica","escritura creativa","géneros literarios","pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (378, 83, 'open_ended', 'all', '{"pregunta": "Elige un género literario que hayas estudiado en clases (por ejemplo: cuento, poesía, crónica, o ensayo). Investiga sus características principales y luego crea un texto original dentro de ese género que exprese una experiencia personal o una reflexión significativa. Explica cómo aplicaste las características del género en tu texto y qué recursos de escritura usaste para lograr tu propósito comunicativo.", "explicacion": "El docente debe evaluar si el estudiante comprende y aplica correctamente las características del género literario elegido, observa la creatividad en el contenido y la expresión personal, y revisa si puede justificar la selección de recursos y cómo estos contribuyen al propósito del texto. Es importante valorar la coherencia entre la explicación y el producto final.", "contexto_adicional": "Se sugiere que el estudiante revise ejemplos y definiciones del género escogido antes de escribir, para asegurar que su texto cumpla con las convenciones y particularidades del género.", "criterios_evaluacion": ["Aplicación adecuada de las características del género seleccionado en el texto (peso: 40%)", "Creatividad y originalidad en la elaboración del contenido y expresión personal (peso: 30%)", "Claridad en la explicación sobre la elección de recursos y adaptación al propósito comunicativo (peso: 30%)"]}', '{"puntos_clave": ["Identificación y aplicación de características del género (estructura, tipo de narrador, elementos propios)", "Incorporación de recursos literarios o estilísticos adecuados (descripciones, metáforas, tono)", "Explicación clara y coherente sobre cómo se usaron las características y recursos para cumplir el propósito comunicativo"], "respuesta_modelo": "He elegido el género del cuento para narrar una experiencia personal sobre un viaje que me marcó. En mi texto, aplico las características del cuento, como la estructura clara con introducción, desarrollo y desenlace, y la presencia de un conflicto central que genera tensión. Utilicé un narrador en primera persona para acercar al lector a mis emociones y pensamientos. Además, incorporé descripciones sensoriales para crear atmósfera y metáforas para enriquecer el lenguaje. Mi propósito era transmitir la sensación de descubrimiento y cambio personal, por lo que elegí un tono reflexivo y emotivo. Así, combiné las características del género con recursos expresivos propios para comunicar mi experiencia de forma efectiva.", "requiere_revision_humana": true}', 4, 0, true, '{"escritura creativa","géneros literarios","pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (384, 84, 'multiple_choice', 'all', '{"opciones": {"A": "Usar un lenguaje muy emotivo para convencer al lector.", "B": "Presentar el tema claramente y organizar la información de manera lógica.", "C": "Incluir principalmente opiniones personales del autor.", "D": "Contar una historia con personajes y un conflicto."}, "pregunta": "¿Cuál de las siguientes características es fundamental en un texto explicativo?", "explicacion": "La opción B es correcta porque un texto explicativo debe presentar el tema de forma clara y organizar la información de manera lógica para facilitar la comprensión. La opción A es incorrecta porque el lenguaje emotivo es típico de textos persuasivos, no explicativos. La opción C es errónea ya que los textos explicativos se basan en información objetiva, no en opiniones personales. La opción D corresponde más a un texto narrativo, no a un texto explicativo."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"texto explicativo",características,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (379, 83, 'open_ended', 'all', '{"pregunta": "Imagina que debes escribir un cuento corto que integre elementos fantásticos y reflexione sobre un problema social actual. ¿Cómo utilizarías las características del género fantástico para expresar tu mensaje de manera creativa y efectiva? Diseña y redacta el inicio de tu cuento, asegurándote de incorporar estos elementos de forma original y adecuada al género.", "explicacion": "Se evaluará la capacidad del estudiante para aplicar creativamente los rasgos del género fantástico con un propósito comunicativo claro, la pertinencia de los elementos fantásticos en relación con el tema social escogido, y la calidad de la escritura en cuanto a coherencia, cohesión y corrección.", "contexto_adicional": "El estudiante debe investigar previamente las características del cuento fantástico y reflexionar sobre un problema social relevante antes de redactar su texto.", "criterios_evaluacion": ["Creatividad y originalidad en la aplicación de elementos fantásticos para reflejar un problema social (peso: 40%)", "Adecuación al género fantástico, considerando sus características principales (ambientación, atmósfera, personajes, etc.) (peso: 35%)", "Claridad y coherencia en la estructura y expresión escrita del texto (peso: 25%)"]}', '{"puntos_clave": ["Incorporación de elementos fantásticos como criaturas o situaciones irreales.", "Relación clara y creativa entre el elemento fantástico y un problema social contemporáneo.", "Inicio coherente, con atmósfera adecuada y personajes que introducen la trama."], "respuesta_modelo": "En un mundo donde los relojes dejaron de funcionar, las personas comenzaron a olvidar sus promesas más importantes. Clara, una joven con la habilidad de escuchar los susurros del tiempo, descubre que debe salvar a su comunidad antes de que el olvido se vuelva eterno. A través de extrañas criaturas que habitan en los rincones olvidados de la ciudad, Clara emprende un viaje que cuestiona la memoria y la responsabilidad social, reflejando el creciente desapego en la sociedad actual hacia los compromisos comunitarios.", "requiere_revision_humana": true}', 5, 0, true, '{"escritura creativa","cuento fantástico","pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (380, 83, 'concept_map', 'all', '{"conceptos": ["Género literario", "Características del género", "Propósito comunicativo", "Estructura del texto", "Recursos lingüísticos"], "explicacion": "Se espera que el estudiante identifique cómo el género literario determina las características específicas, el propósito comunicativo, la estructura y los recursos lingüísticos utilizados en la producción de textos, estableciendo conexiones claras entre estos elementos para comprender y aplicar la escritura creativa.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la producción de textos escritos en distintos géneros literarios?"}', '{"conceptos_centrales": ["Género literario", "Características del género"], "relaciones_esperadas": [{"origen": "Género literario", "destino": "Características del género", "relacion": "define"}, {"origen": "Características del género", "destino": "Estructura del texto", "relacion": "condicionan"}, {"origen": "Características del género", "destino": "Recursos lingüísticos", "relacion": "orientan"}, {"origen": "Propósito comunicativo", "destino": "Estructura del texto", "relacion": "influye en"}, {"origen": "Propósito comunicativo", "destino": "Recursos lingüísticos", "relacion": "determina"}, {"origen": "Género literario", "destino": "Propósito comunicativo", "relacion": "se adapta a"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Escritura Creativa","Mapa Conceptual","Géneros Literarios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (381, 83, 'concept_map', 'all', '{"conceptos": ["Género literario", "Características del género", "Propósito comunicativo", "Habilidades de escritura", "Adaptación del texto"], "explicacion": "El mapa conceptual debe mostrar cómo el conocimiento del género literario y sus características influye en el propósito comunicativo del texto. Además, debe conectar cómo las habilidades de escritura se aplican y adaptan para cumplir con ese propósito y género específico.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la producción de textos creativos en Lengua y Literatura?"}', '{"conceptos_centrales": ["Género literario", "Habilidades de escritura"], "relaciones_esperadas": [{"origen": "Género literario", "destino": "Características del género", "relacion": "define"}, {"origen": "Características del género", "destino": "Adaptación del texto", "relacion": "orientan"}, {"origen": "Propósito comunicativo", "destino": "Habilidades de escritura", "relacion": "determina"}, {"origen": "Habilidades de escritura", "destino": "Adaptación del texto", "relacion": "se aplican en"}, {"origen": "Adaptación del texto", "destino": "Cumplir con el propósito comunicativo", "relacion": "permite"}], "requiere_revision_humana": true}', 5, 0, true, '{"mapa conceptual",creación,"habilidades de escritura","géneros literarios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (382, 83, 'open_ended', 'all', '{"pregunta": "Elige un género literario estudiado en clase (por ejemplo, cuento, poesía, ensayo o teatro). Investiga sus características principales y diseña un texto original que las incorpore creativamente. Explica cómo adaptaste tu escritura a las características del género y al propósito comunicativo que querías lograr.", "explicacion": "El docente debe evaluar que el estudiante demuestre comprensión clara del género literario, que aplique esas características de forma creativa y original, y que el texto sea coherente con el propósito comunicativo que se propuso. Se debe valorar también la claridad y argumentación en la explicación de sus elecciones.", "contexto_adicional": "El estudiante debe investigar previamente las características del género elegido y luego diseñar un texto original que refleje ese aprendizaje, justificando sus decisiones de escritura.", "criterios_evaluacion": ["Comprensión y aplicación de las características del género seleccionado (peso: 40%)", "Creatividad y originalidad en la producción del texto (peso: 30%)", "Coherencia y adecuación del texto al propósito comunicativo planteado (peso: 30%)"]}', '{"puntos_clave": ["Identificación clara del género literario seleccionado", "Aplicación concreta de las características del género en el texto creado", "Justificación coherente sobre la adaptación del texto al propósito comunicativo"], "respuesta_modelo": "He elegido el género cuento porque permite desarrollar una narrativa breve con un conflicto central y un desenlace claro. En mi cuento original, titulé ''La sombra en el árbol'', utilicé un narrador en tercera persona para mantener distancia y suspenso, e incluí un giro sorpresivo al final, característica común del género. Además, cuidé la estructura clásica: introducción, desarrollo y desenlace, y empleé descripciones detalladas para crear atmósfera. Mi propósito era entretener y generar reflexión sobre el miedo a lo desconocido, por lo que adapté el lenguaje para ser accesible pero evocador. En la explicación final, justifico mis elecciones creativas basándome en las características del género cuento.", "requiere_revision_humana": true}', 5, 0, true, '{escritura_creativa,géneros_literarios,primeromedia}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (385, 84, 'multiple_choice', 'all', '{"opciones": {"A": "Incluir opiniones personales para hacer el texto más interesante", "B": "Presentar el tema de manera clara y organizar la información en secciones o apartados", "C": "Usar un lenguaje muy informal y coloquial para acercarse al lector", "D": "Evitar la introducción del tema para ir directamente al contenido"}, "pregunta": "¿Cuál de las siguientes características es fundamental en un texto explicativo para que cumpla su propósito de informar claramente sobre un tema?", "explicacion": "La opción B es correcta porque un texto explicativo debe presentar el tema con claridad y organizar la información para que el lector entienda fácilmente el contenido. Los textos explicativos no buscan opiniones personales (descartando A), ni usan lenguaje informal (descartando C), y siempre incluyen una introducción para contextualizar el tema (descartando D)."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"texto explicativo","organización textual","Lengua y Literatura","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (386, 84, 'true_false', 'all', '{"afirmacion": "Los textos explicativos presentan claramente el tema y organizan la información para facilitar la comprensión del lector.", "explicacion": "Esta afirmación es verdadera porque una característica fundamental de los textos explicativos es que introducen el tema de forma clara y estructuran la información de manera lógica para que el lector pueda entender fácilmente el contenido."}', '{"es_verdadero": true}', 1, 0, true, '{"textos explicativos",características,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (387, 84, 'fill_blanks', 'all', '{"texto": "Los textos explicativos presentan una [BLANK_1] clara del tema y una organización que permite entender los diferentes [BLANK_2] que se abordarán.", "pistas": ["Se refiere a cómo se introduce el contenido principal del texto.", "Son los distintos puntos o ideas que se desarrollan en el texto."], "explicacion": "Los textos explicativos se caracterizan por presentar una introducción o presentación clara del tema y una organización lógica que facilita la comprensión de los aspectos o puntos que se tratarán."}', '{"respuestas_correctas": {"BLANK_1": ["presentación", "introducción"], "BLANK_2": ["aspectos", "puntos", "temas"]}}', 2, 0, true, '{"texto explicativo",características,"estructura textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (388, 85, 'multiple_choice', 'all', '{"opciones": {"A": "Presentar el tema y anticipar los aspectos que se abordarán en el texto.", "B": "Incluir información detallada y ejemplos para respaldar el tema.", "C": "Resumir las ideas principales y dar una opinión personal sobre el tema.", "D": "Presentar gráficos y recursos visuales sin necesidad de explicación."}, "pregunta": "¿Cuál es la función principal de la introducción en un texto explicativo?", "explicacion": "La introducción en un texto explicativo tiene como función principal presentar el tema y anticipar los aspectos que se desarrollarán, preparando al lector para comprender lo que sigue. La opción A es correcta porque cumple con esta función. La opción B corresponde más bien al desarrollo del texto, donde se entregan detalles y ejemplos. La opción C describe la conclusión, pero incluye un error al mencionar opinión personal, ya que un texto explicativo debe ser objetivo. La opción D menciona recursos gráficos, que son complementarios y deben ir acompañados de explicación, pero no son la función principal de la introducción."}', '{"respuesta_correcta": "A"}', 2, 0, true, '{"texto explicativo",introducción,"estructura textual","recursos lingüísticos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (389, 85, 'multiple_choice', 'all', '{"opciones": {"A": "Presentar el tema y esbozar los aspectos que se abordarán en el texto.", "B": "Incluir datos estadísticos para apoyar el argumento principal.", "C": "Describir detalladamente cada uno de los aspectos tratados en el texto.", "D": "Finalizar el texto con una conclusión personal del autor."}, "pregunta": "¿Cuál es la función principal de la introducción en un texto explicativo?", "explicacion": "La opción A es correcta porque la introducción en un texto explicativo debe presentar claramente el tema y anticipar los aspectos que serán desarrollados. La opción B es un recurso que puede aparecer en el desarrollo, no en la introducción. La opción C corresponde a la función del cuerpo del texto, no de la introducción. La opción D se refiere a la conclusión, no a la introducción, y en textos explicativos se espera objetividad más que una opinión personal."}', '{"respuesta_correcta": "A"}', 3, 0, true, '{"texto explicativo","partes del texto",introducción,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (390, 85, 'true_false', 'all', '{"afirmacion": "En un texto explicativo, la introducción presenta claramente el tema y los aspectos que se abordarán en el desarrollo.", "explicacion": "Esta afirmación es verdadera porque la introducción en un texto explicativo cumple la función de presentar con claridad el tema principal y esbozar los puntos que se tratarán en las siguientes secciones, facilitando la comprensión del lector."}', '{"es_verdadero": true}', 2, 0, true, '{"texto explicativo","estructura textual",introducción}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (391, 85, 'drag_drop_matching', 'all', '{"explicacion": "La introducción presenta el tema general; el desarrollo detalla la información; la conclusión resume y cierra el texto; los recursos lingüísticos conectan y clarifican ideas; los recursos gráficos apoyan visualmente la explicación; y el ejemplo ilustra concretamente el tema tratado.", "instruccion": "Empareja cada concepto con su definición o función correspondiente en un texto explicativo", "columna_derecha": ["Parte que presenta el tema y los aspectos que se abordarán", "Parte donde se explica en detalle el tema", "Parte que cierra y resume las ideas principales", "Palabras o frases que ayudan a clarificar o conectar ideas", "Imágenes, gráficos o tablas que apoyan la explicación", "Una imagen que muestra cómo se hace un proceso"], "columna_izquierda": ["Introducción", "Desarrollo", "Conclusión", "Recursos lingüísticos", "Recursos gráficos", "Ejemplo"]}', '{"emparejamientos_correctos": {"Ejemplo": "Una imagen que muestra cómo se hace un proceso", "Desarrollo": "Parte donde se explica en detalle el tema", "Conclusión": "Parte que cierra y resume las ideas principales", "Introducción": "Parte que presenta el tema y los aspectos que se abordarán", "Recursos gráficos": "Imágenes, gráficos o tablas que apoyan la explicación", "Recursos lingüísticos": "Palabras o frases que ayudan a clarificar o conectar ideas"}}', 2, 0, true, '{"texto explicativo","partes del texto","recursos lingüísticos","recursos gráficos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (392, 85, 'sequencing', 'all', '{"explicacion": "El proceso para escribir un texto explicativo comienza con elegir el tema y propósito (Paso 1), luego organizar las ideas para darle una estructura clara (Paso 2). Después, se redacta el texto con los recursos lingüísticos adecuados (Paso 3), seguido de la inclusión de recursos gráficos que apoyen la explicación (Paso 4). Finalmente, se revisa y corrige el texto para asegurar su claridad y coherencia (Paso 5).", "instruccion": "Ordena los siguientes pasos en la secuencia correcta para escribir un texto explicativo.", "elementos_desordenados": ["Redactar el texto utilizando recursos lingüísticos adecuados.", "Elegir un tema y definir el propósito del texto.", "Incluir gráficos o imágenes que apoyen la explicación.", "Organizar las ideas en una estructura clara con introducción, desarrollo y conclusión.", "Revisar y corregir el texto para mejorar la claridad y coherencia."]}', '{"orden_correcto": ["Elegir un tema y definir el propósito del texto.", "Organizar las ideas en una estructura clara con introducción, desarrollo y conclusión.", "Redactar el texto utilizando recursos lingüísticos adecuados.", "Incluir gráficos o imágenes que apoyen la explicación.", "Revisar y corregir el texto para mejorar la claridad y coherencia."]}', 3, 0, true, '{escritura,"texto explicativo",organización,"recursos lingüísticos","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (393, 86, 'multiple_choice', 'all', '{"opciones": {"A": "Introducción con presentación del tema, desarrollo con explicación clara y conclusión que resume los puntos tratados.", "B": "Inicio con una opinión personal, desarrollo con anécdotas y conclusión sin relación con el tema.", "C": "Lista de ideas sin orden, sin introducción ni conclusión, solo con información dispersa.", "D": "Narración de hechos en orden cronológico sin explicar causas ni efectos."}, "pregunta": "¿Cuál de las siguientes opciones es la estructura correcta para redactar un texto explicativo sencillo?", "explicacion": "La opción A es correcta porque un texto explicativo debe comenzar presentando claramente el tema, seguido de un desarrollo que explique los aspectos relevantes de manera ordenada y clara, y finalizar con una conclusión que sintetice lo abordado. La opción B es incorrecta porque un texto explicativo no debe basarse en opiniones ni anécdotas personales, sino en información objetiva. La opción C presenta información desorganizada, lo que dificulta la comprensión del tema. La opción D corresponde más bien a un texto narrativo, no a un texto explicativo, ya que no se centra en explicar causas, características o procesos."}', '{"respuesta_correcta": "A"}', 3, 0, true, '{"texto explicativo","estructura del texto",redacción,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (394, 86, 'multiple_choice', 'all', '{"opciones": {"A": "Introducción con presentación del tema, desarrollo con explicación detallada y conclusión que resume el contenido.", "B": "Introducción con una anécdota personal, lista de opiniones sobre el tema y conclusión abierta sin resumen.", "C": "Desarrollo con datos sin introducción ni conclusión, solo explicaciones mezcladas sin orden.", "D": "Conclusión al inicio, luego desarrollo con ejemplos y sin presentación clara del tema."}, "pregunta": "¿Cuál de las siguientes opciones presenta la estructura correcta para redactar un texto explicativo sencillo?", "explicacion": "La opción A es correcta porque un texto explicativo debe comenzar con una introducción que presente claramente el tema y los aspectos que se abordarán, seguido de un desarrollo donde se explica el tema en detalle, y finaliza con una conclusión que resume y cierra el contenido. La opción B es incorrecta porque usar una anécdota personal y opiniones no corresponde a un texto explicativo, que debe ser objetivo. La opción C falla al no tener una estructura clara ni introducción ni conclusión, lo que dificulta la comprensión. La opción D es incorrecta porque la conclusión no debe ir al inicio, y la presentación clara del tema es fundamental para un texto explicativo."}', '{"respuesta_correcta": "A"}', 4, 0, true, '{"texto explicativo","estructura de texto",redacción,"Lengua y Literatura","1º medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (395, 86, 'drag_drop_matching', 'all', '{"explicacion": "Introducción corresponde a la parte donde se presenta el tema, Desarrollo es la sección que explica y detalla los aspectos, Conclusión resume y cierra el tema, Informe es un texto objetivo y organizado, Reportaje profundiza con testimonios y datos, y Artículo expone un tema desde diferentes enfoques.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente relacionado con textos explicativos.", "columna_derecha": ["Parte donde se presenta el tema y los aspectos a tratar", "Texto periodístico que profundiza en un tema con testimonios y datos", "Sección que resume y cierra el tema explicado", "Texto que presenta información organizada y objetiva sobre un tema específico", "Ejemplo de texto que expone un tema desde diferentes enfoques y opiniones", "Parte donde se explican y detallan los aspectos del tema"], "columna_izquierda": ["Introducción", "Desarrollo", "Conclusión", "Informe", "Reportaje", "Artículo"]}', '{"emparejamientos_correctos": {"Informe": "Texto que presenta información organizada y objetiva sobre un tema específico", "Artículo": "Ejemplo de texto que expone un tema desde diferentes enfoques y opiniones", "Reportaje": "Texto periodístico que profundiza en un tema con testimonios y datos", "Desarrollo": "Parte donde se explican y detallan los aspectos del tema", "Conclusión": "Sección que resume y cierra el tema explicado", "Introducción": "Parte donde se presenta el tema y los aspectos a tratar"}}', 3, 0, true, '{"textos explicativos","estructura del texto","lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (396, 86, 'sequencing', 'all', '{"explicacion": "El orden correcto comienza con la presentación del tema para contextualizar al lector, seguido de la organización de las ideas para estructurar el texto, luego la redacción del desarrollo con explicaciones claras, y finalmente la conclusión que resume y cierra el texto.", "instruccion": "Ordena los siguientes pasos para redactar un texto explicativo sencillo siguiendo la estructura adecuada.", "elementos_desordenados": ["Escribe el desarrollo donde se explica el tema con detalles claros.", "Presenta el tema y los aspectos que se abordarán en la introducción.", "Redacta una conclusión que resuma la explicación y cierre el texto.", "Organiza las ideas para asegurar una secuencia lógica y coherente."]}', '{"orden_correcto": ["Presenta el tema y los aspectos que se abordarán en la introducción.", "Organiza las ideas para asegurar una secuencia lógica y coherente.", "Escribe el desarrollo donde se explica el tema con detalles claros.", "Redacta una conclusión que resuma la explicación y cierre el texto."]}', 3, 0, true, '{escritura,"texto explicativo","estructura textual","primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (397, 86, 'open_ended', 'all', '{"pregunta": "Redacta un texto explicativo sobre la importancia del reciclaje en la conservación del medio ambiente. Asegúrate de presentar claramente el tema, organizar las ideas en párrafos coherentes y utilizar un lenguaje apropiado para informar a un público general.", "explicacion": "Evalúa que el estudiante aplique la estructura de un texto explicativo: introducción clara, desarrollo organizado y conclusión o cierre. Se debe valorar la coherencia y cohesión del texto, así como la adecuación del lenguaje para explicar el tema de manera sencilla y precisa.", "contexto_adicional": "El texto debe estar dirigido a un público general que desea comprender por qué es importante reciclar para cuidar el medio ambiente.", "criterios_evaluacion": ["Claridad y precisión en la presentación del tema y los aspectos que se abordarán (peso: 30%)", "Organización lógica y coherente de las ideas en párrafos bien estructurados (peso: 40%)", "Uso adecuado del lenguaje y características propias del texto explicativo, incluyendo términos específicos y conectores (peso: 30%)"]}', '{"puntos_clave": ["Definición clara del reciclaje y su relación con el medio ambiente", "Explicación organizada de los beneficios del reciclaje (reducción de basura, conservación de recursos, disminución de contaminación)", "Uso de ejemplos concretos que ilustren la importancia del reciclaje"], "respuesta_modelo": "El reciclaje es una práctica fundamental para la conservación del medio ambiente. Consiste en reutilizar materiales que de otra forma serían desechados, reduciendo la cantidad de basura y la contaminación. Al reciclar, se disminuye la extracción de recursos naturales, lo que contribuye a preservar los ecosistemas y a disminuir la emisión de gases contaminantes. Por ejemplo, reciclar papel ayuda a salvar árboles y reciclar plásticos evita la contaminación de océanos. Por estas razones, el reciclaje no solo ayuda a reducir los residuos, sino que también protege nuestro planeta para las futuras generaciones.", "requiere_revision_humana": true}', 3, 0, true, '{"texto explicativo",escritura,"medio ambiente",reciclaje,"primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (398, 87, 'compare_contrast', 'all', '{"conceptos": ["Artículo", "Informe"], "criterios": ["Organización del contenido", "Uso de recursos explicativos", "Tipo de cierre"], "explicacion": "Aunque ambos géneros buscan explicar un tema, el artículo suele tener una organización más flexible y un cierre que invita a la reflexión, utilizando recursos como ejemplos y opiniones para conectar con el lector. En cambio, el informe presenta una estructura más formal y ordenada, empleando datos y gráficos como recursos y concluye con un resumen objetivo de los hallazgos.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Informe": {"Tipo de cierre": "Resumen objetivo que sintetiza los hallazgos o resultados", "Organización del contenido": "Estructurada y formal, con secciones claras como introducción, desarrollo y conclusión", "Uso de recursos explicativos": "Datos, estadísticas, gráficos y citas para respaldar la información"}, "Artículo": {"Tipo de cierre": "Cierre abierto que invita a la reflexión o acción", "Organización del contenido": "Flexible, con introducción, desarrollo y conclusión que invita a la reflexión", "Uso de recursos explicativos": "Ejemplos, opiniones, comparaciones para facilitar la comprensión"}}}', 3, 0, true, '{comparar,"textos explicativos","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (399, 87, 'compare_contrast', 'all', '{"conceptos": ["Artículo", "Informe"], "criterios": ["Organización del texto", "Uso de recursos explicativos", "Cierre del texto"], "explicacion": "Ambos textos explicativos presentan información clara sobre un tema, pero difieren en su estructura y propósito. El artículo suele tener una organización más flexible y utiliza recursos como ejemplos y opiniones para atraer al lector, cerrando con una conclusión o reflexión. El informe, en cambio, sigue una estructura más rígida y formal, emplea datos y evidencia objetiva, y termina con un resumen o recomendaciones.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Informe": {"Cierre del texto": "Resumen de hallazgos y recomendaciones basadas en la información presentada", "Organización del texto": "Estructura formal y secuencial: introducción, metodología, resultados y conclusión", "Uso de recursos explicativos": "Uso de datos, gráficos y evidencia objetiva para fundamentar la información"}, "Artículo": {"Cierre del texto": "Conclusión o reflexión personal que sintetiza el tema", "Organización del texto": "Estructura flexible con introducción, desarrollo y conclusión", "Uso de recursos explicativos": "Incluye ejemplos, anécdotas y opiniones para clarificar el tema"}}}', 4, 0, true, '{comparar,"textos explicativos","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (400, 87, 'open_ended', 'all', '{"pregunta": "Analiza dos textos explicativos diferentes sobre un mismo tema. Compara cómo se organizan, qué recursos utilizan para clarificar la información y cómo concluyen. ¿Qué diferencias y similitudes encuentras en estos elementos y cómo afectan la comprensión del lector?", "explicacion": "El docente debe evaluar si el estudiante distingue adecuadamente la estructura de los textos, analiza los recursos utilizados (como ejemplos, definiciones, gráficos, conectores) y evalúa cómo cada texto concluye, observando el efecto que esto tiene en el lector. Se valorará el pensamiento crítico y la capacidad de argumentar la comparación.", "contexto_adicional": "Se proporcionarán dos textos explicativos sobre un mismo tema para que el estudiante pueda realizar el análisis comparativo.", "criterios_evaluacion": ["Identificación clara y precisa de elementos organizativos en ambos textos (peso: 30%)", "Análisis crítico del uso de recursos explicativos y su efectividad para clarificar información (peso: 40%)", "Comparación detallada de los cierres de ambos textos y reflexión sobre su impacto en la comprensión del lector (peso: 30%)"]}', '{"puntos_clave": ["Diferencias en la organización (secuencial vs. por subtítulos)", "Uso de recursos (gráficos y ejemplos vs. definiciones y conectores)", "Tipos de cierre (resumen vs. pregunta reflexiva) y su impacto en la comprensión"], "respuesta_modelo": "En el primer texto, la organización es secuencial, presentando el tema, desarrollando los aspectos en orden lógico y cerrando con un resumen. El segundo texto, en cambio, utiliza subtítulos para dividir los aspectos, facilitando la navegación. En cuanto a recursos, el primer texto utiliza gráficos y ejemplos prácticos que ayudan a ilustrar las ideas, mientras que el segundo se apoya más en definiciones claras y conectores para explicar. Respecto al cierre, el primer texto finaliza con un resumen conciso que refuerza lo explicado, mientras que el segundo invita a reflexionar con una pregunta abierta. Estas diferencias afectan la comprensión: el primer texto es más directo y estructurado, facilitando la memorización, mientras que el segundo fomenta la reflexión y comprensión profunda.", "requiere_revision_humana": true}', 4, 0, true, '{análisis,comparación,"textos explicativos","lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (417, 90, 'fill_blanks', 'all', '{"texto": "Un texto persuasivo generalmente comienza con una [BLANK_1], que es la idea principal que se quiere defender. Para apoyar esta afirmación, se utilizan [BLANK_2], que son datos o argumentos que dan fuerza a la hipótesis. Finalmente, el texto debe tener coherencia y una [BLANK_3] que reafirme la posición del autor.", "pistas": ["Es la afirmación central del texto persuasivo.", "Son los argumentos o pruebas que respaldan la hipótesis.", "Es la parte final que resume o cierra el argumento."], "explicacion": "Un texto persuasivo se estructura presentando primero una hipótesis o afirmación que se desea defender, seguida de evidencias que la sustenten, manteniendo coherencia en el desarrollo y concluyendo con una conclusión que refuerza la postura del autor."}', '{"respuestas_correctas": {"BLANK_1": ["hipótesis", "afirmación"], "BLANK_2": ["evidencias", "argumentos", "pruebas"], "BLANK_3": ["conclusión", "cierre", "final"]}}', 2, 0, true, '{"texto persuasivo","elementos del texto",ensayo,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (401, 87, 'concept_map', 'all', '{"conceptos": ["Texto explicativo", "Organización del contenido", "Recursos lingüísticos", "Introducción", "Cierre"], "explicacion": "Se espera que el estudiante identifique cómo la organización del contenido estructura el texto explicativo desde la introducción hasta el cierre, y cómo los recursos lingüísticos se utilizan para clarificar y conectar las ideas dentro de esta organización.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la estructura y características de textos explicativos?"}', '{"conceptos_centrales": ["Texto explicativo", "Organización del contenido"], "relaciones_esperadas": [{"origen": "Texto explicativo", "destino": "Introducción", "relacion": "incluye"}, {"origen": "Texto explicativo", "destino": "Organización del contenido", "relacion": "incluye"}, {"origen": "Texto explicativo", "destino": "Cierre", "relacion": "incluye"}, {"origen": "Organización del contenido", "destino": "Recursos lingüísticos", "relacion": "utiliza"}, {"origen": "Introducción", "destino": "Tema", "relacion": "presenta"}, {"origen": "Cierre", "destino": "Información principal", "relacion": "resume"}], "requiere_revision_humana": true}', 3, 0, true, '{lengua_y_literatura,texto_explicativo,mapa_conceptual,analizar,crear}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (402, 87, 'concept_map', 'all', '{"conceptos": ["Texto explicativo", "Organización del contenido", "Recursos lingüísticos", "Introducción", "Desarrollo", "Cierre"], "explicacion": "Se espera que el estudiante identifique que el ''Texto explicativo'' está compuesto por una ''Organización del contenido'' que incluye ''Introducción'', ''Desarrollo'' y ''Cierre''. Además, debe relacionar que los ''Recursos lingüísticos'' son herramientas que se utilizan en cada una de estas partes para clarificar y explicar el tema.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la estructura y características de los textos explicativos?"}', '{"conceptos_centrales": ["Texto explicativo", "Organización del contenido"], "relaciones_esperadas": [{"origen": "Texto explicativo", "destino": "Organización del contenido", "relacion": "se estructura en"}, {"origen": "Organización del contenido", "destino": "Introducción", "relacion": "incluye"}, {"origen": "Organización del contenido", "destino": "Desarrollo", "relacion": "incluye"}, {"origen": "Organización del contenido", "destino": "Cierre", "relacion": "incluye"}, {"origen": "Recursos lingüísticos", "destino": "Introducción", "relacion": "se utilizan en"}, {"origen": "Recursos lingüísticos", "destino": "Desarrollo", "relacion": "se utilizan en"}, {"origen": "Recursos lingüísticos", "destino": "Cierre", "relacion": "se utilizan en"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Texto explicativo","Análisis de textos","Mapa conceptual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (403, 88, 'criteria_evaluation', 'all', '{"rubrica": {"Organización del contenido": {"Excelente": "El texto está organizado de forma lógica y coherente, con párrafos bien estructurados que facilitan la comprensión.", "Satisfactorio": "El texto tiene una organización adecuada, aunque algunos párrafos podrían estar mejor conectados o estructurados.", "Necesita mejorar": "El texto carece de organización clara, dificultando la comprensión del contenido."}, "Uso de recursos explicativos": {"Excelente": "El texto utiliza recursos como ejemplos, gráficos y definiciones que enriquecen y clarifican la explicación.", "Satisfactorio": "El texto usa algunos recursos explicativos, pero de forma limitada o poco integrada.", "Necesita mejorar": "El texto no utiliza recursos explicativos o estos no aportan a la claridad del texto."}, "Claridad en la presentación del tema": {"Excelente": "El texto presenta el tema de manera clara y precisa, con una introducción que enmarca claramente los aspectos a abordar.", "Satisfactorio": "El texto presenta el tema de manera comprensible, pero la introducción podría ser más clara o explícita en los aspectos a tratar.", "Necesita mejorar": "El texto presenta confusión en el tema o no se logra comprender bien qué aspectos se abordarán."}}, "explicacion": "La evaluación debe considerar cómo el texto presenta el tema claramente, si está bien organizado para facilitar la comprensión y si utiliza recursos explicativos que apoyan la explicación. La justificación debe incluir ejemplos del texto que sustentan la calificación dada.", "instruccion": "Evalúa el texto explicativo según los criterios de claridad, organización y uso de recursos explicativos. Luego, justifica tu calificación para cada criterio con argumentos concretos.", "caso_a_evaluar": "Lee el siguiente texto explicativo sobre el cambio climático, que presenta una introducción, desarrollo y conclusión, e incluye gráficos y ejemplos para apoyar la información."}', '{"evaluacion_modelo": {"Organización del contenido": "Satisfactorio", "Uso de recursos explicativos": "Excelente", "Claridad en la presentación del tema": "Excelente"}, "justificacion_modelo": "El texto introduce claramente el tema del cambio climático y los aspectos que se abordarán, lo que justifica la calificación Excelente en claridad. La organización es adecuada pero algunos párrafos carecen de conexión fluida, por eso se asigna Satisfactorio. El uso de gráficos y ejemplos bien integrados fortalece la explicación, justificando el nivel Excelente en recursos.", "requiere_revision_humana": true}', 4, 0, true, '{evaluación,"texto explicativo","lengua y literatura","primer año medio",evaluar}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (404, 88, 'criteria_evaluation', 'all', '{"rubrica": {"Claridad": {"Excelente": "El texto presenta ideas claras, lenguaje preciso y facilita la comprensión del tema sin ambigüedades.", "Satisfactorio": "El texto es generalmente claro, aunque presenta algunas ideas poco precisas o formulaciones algo confusas.", "Necesita mejorar": "El texto es confuso, con lenguaje impreciso que dificulta la comprensión del tema."}, "Organización": {"Excelente": "El texto está bien estructurado, con una introducción que presenta el tema, desarrollo ordenado y una conclusión coherente.", "Satisfactorio": "El texto presenta una estructura básica, pero podría mejorar el orden o la conexión entre las partes.", "Necesita mejorar": "El texto carece de una organización clara, con ideas desordenadas o sin conexión lógica."}, "Uso de recursos explicativos": {"Excelente": "Utiliza adecuadamente ejemplos, definiciones y datos que enriquecen la explicación y apoyan el entendimiento.", "Satisfactorio": "Incluye algunos recursos explicativos, aunque de forma limitada o poco relevante.", "Necesita mejorar": "No utiliza recursos explicativos o los usa de manera inapropiada, lo que dificulta la comprensión."}}, "explicacion": "Se espera que el estudiante analice críticamente el texto, identificando fortalezas y debilidades en relación a cada criterio, y argumente su evaluación basándose en evidencias concretas del texto.", "instruccion": "Evalúa el texto según los criterios de claridad, organización y uso de recursos explicativos, asigna un nivel de desempeño para cada criterio y justifica tu calificación.", "caso_a_evaluar": "Se presenta un texto explicativo titulado ''El impacto del cambio climático en la biodiversidad'', que busca informar sobre las causas y consecuencias del cambio climático en diferentes ecosistemas."}', '{"evaluacion_modelo": {"Claridad": "Satisfactorio", "Organización": "Excelente", "Uso de recursos explicativos": "Satisfactorio"}, "justificacion_modelo": "El texto presenta una estructura clara y ordenada, lo que justifica la calificación excelente en organización. Sin embargo, algunas ideas son expresadas de manera poco precisa, justificando la valoración satisfactoria en claridad. Además, aunque incluye ejemplos y datos, su uso es limitado y no siempre relevante, por lo que el uso de recursos explicativos es considerado satisfactorio.", "requiere_revision_humana": true}', 5, 0, true, '{evaluación,"texto explicativo",claridad,organización,"recursos explicativos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (405, 88, 'criteria_evaluation', 'all', '{"rubrica": {"Organización del contenido": {"Excelente": "El contenido está organizado lógicamente, con una estructura coherente y uso efectivo de subtítulos y párrafos.", "Satisfactorio": "El contenido tiene una organización adecuada, aunque en algunos momentos la estructura no es totalmente coherente.", "Necesita mejorar": "El contenido carece de organización clara, con saltos temáticos o falta de coherencia en la estructura."}, "Uso de recursos explicativos": {"Excelente": "Utiliza recursos como ejemplos, gráficos o datos que enriquecen y facilitan la comprensión del tema.", "Satisfactorio": "Utiliza algunos recursos explicativos, pero estos podrían ser más variados o mejor integrados.", "Necesita mejorar": "No utiliza recursos explicativos o los que usa no aportan a la comprensión del tema."}, "Claridad en la presentación del tema": {"Excelente": "El texto presenta el tema de manera clara y precisa, con un lenguaje accesible y sin ambigüedades.", "Satisfactorio": "El texto presenta el tema de forma clara, aunque en algunos pasajes puede resultar confuso o poco preciso.", "Necesita mejorar": "El texto presenta el tema de manera poco clara, con lenguaje confuso o falta de precisión."}}, "explicacion": "Se espera que el estudiante identifique la claridad en la presentación del tema, la organización lógica del contenido y el uso adecuado de recursos para apoyar la explicación, fundamentando cada evaluación con ejemplos del texto.", "instruccion": "Evalúa el informe explicativo sobre el cambio climático según la rúbrica presentada y justifica la calificación que otorgas en cada criterio.", "caso_a_evaluar": "Se presenta un informe explicativo sobre el cambio climático que incluye una introducción al tema, desarrollo organizado con subtítulos y un uso moderado de gráficos y ejemplos para apoyar la información."}', '{"evaluacion_modelo": {"Organización del contenido": "Satisfactorio", "Uso de recursos explicativos": "Satisfactorio", "Claridad en la presentación del tema": "Excelente"}, "justificacion_modelo": "El informe presenta un tema claro y bien definido, con un lenguaje accesible, pero la organización presenta pequeñas incoherencias en la transición entre algunos subtítulos. Los recursos explicativos están presentes pero son limitados y podrían integrarse mejor para enriquecer la explicación.", "requiere_revision_humana": true}', 5, 0, true, '{evaluación,"texto explicativo",claridad,organización,recursos}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (406, 88, 'open_ended', 'all', '{"pregunta": "Lee un texto explicativo sobre un tema de actualidad y evalúa su eficacia. Analiza cómo la claridad del lenguaje, la organización de las ideas y el uso de recursos contribuyen a que el texto cumpla su propósito. Justifica tu valoración con ejemplos concretos del texto.", "explicacion": "El docente debe revisar que el estudiante identifique y justifique, con ejemplos del texto, los elementos que aportan a la claridad, organización y uso de recursos explicativos. Se espera que el estudiante argumente su evaluación de manera crítica y fundamentada.", "contexto_adicional": "Se proporcionará un texto explicativo breve sobre un tema de actualidad para que el estudiante lo analice y evalúe.", "criterios_evaluacion": ["Claridad del lenguaje: Evalúa si el texto utiliza un lenguaje comprensible y adecuado para el público objetivo. (peso: 30%)", "Organización de las ideas: Valora la estructura del texto, la secuencia lógica y la coherencia entre las partes. (peso: 40%)", "Uso de recursos explicativos: Considera el empleo de elementos como definiciones, ejemplos, comparaciones o gráficos para facilitar la comprensión. (peso: 30%)"]}', '{"puntos_clave": ["Identificación de lenguaje claro y accesible", "Reconocimiento de la estructura lógica y coherente del texto", "Análisis del uso de recursos explicativos (ejemplos, gráficos, definiciones)"], "respuesta_modelo": "El texto explicativo sobre el cambio climático es eficaz en términos de claridad, organización y uso de recursos. Primero, el lenguaje es sencillo y preciso, evitando tecnicismos que podrían confundir al lector, como cuando define ''gases de efecto invernadero'' con palabras accesibles. En cuanto a la organización, el texto sigue una estructura lógica: comienza con una introducción clara del tema, luego explica las causas, y finalmente presenta las consecuencias y posibles soluciones, lo que facilita la comprensión progresiva del lector. Además, utiliza recursos explicativos como ejemplos concretos, por ejemplo, al mencionar el derretimiento de glaciares, y una tabla comparativa que ilustra el aumento de temperaturas en las últimas décadas, lo que ayuda a visualizar la información. Por estas razones, considero que el texto cumple eficazmente su propósito explicativo.", "requiere_revision_humana": true}', 4, 0, true, '{evaluación,"texto explicativo","pensamiento crítico","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (407, 88, 'open_ended', 'all', '{"pregunta": "Lee un texto explicativo de tu elección (por ejemplo, un artículo o un informe) y evalúa su eficacia. Justifica tu respuesta analizando la claridad con que presenta el tema, la organización del contenido y el uso de recursos explicativos (como ejemplos, definiciones o gráficos). ¿En qué aspectos consideras que el texto cumple o no cumple con su propósito explicativo y por qué?", "explicacion": "El docente debe evaluar que el estudiante pueda identificar claramente cómo el texto presenta el tema, si la información está organizada de forma lógica y secuencial, y si los recursos explicativos utilizados son adecuados y contribuyen a la comprensión. La justificación debe ser argumentada con ejemplos concretos del texto.", "contexto_adicional": "Se recomienda que el estudiante seleccione un texto explicativo que haya leído previamente o que le sea proporcionado, para realizar un análisis crítico fundamentado.", "criterios_evaluacion": ["Claridad en la identificación y explicación de la presentación del tema (peso: 30%)", "Análisis de la organización del contenido y su coherencia interna (peso: 40%)", "Evaluación del uso y pertinencia de recursos explicativos para apoyar la comprensión (peso: 30%)"]}', '{"puntos_clave": ["Identificación clara de la presentación del tema en el texto", "Análisis de la estructura y organización lógica del contenido", "Evaluación crítica del uso de recursos explicativos y su aporte a la comprensión"], "respuesta_modelo": "El texto seleccionado presenta claramente el tema desde el inicio, indicando los aspectos que se abordarán, lo que facilita la comprensión inicial. La organización es lógica, con párrafos que desarrollan cada aspecto de manera secuencial y coherente, lo que permite seguir el hilo argumental sin dificultad. Además, el uso de recursos como ejemplos claros y definiciones precisas ayuda a explicar conceptos complejos, haciendo el texto accesible para el lector. Sin embargo, en algunos momentos la información es demasiado técnica sin suficiente explicación adicional, lo que podría dificultar la comprensión para algunos lectores. En general, el texto cumple eficazmente su propósito explicativo, aunque podría mejorar en la adaptación del lenguaje para el público objetivo.", "requiere_revision_humana": true}', 5, 0, true, '{"evaluación textual","pensamiento crítico","texto explicativo"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (408, 89, 'open_ended', 'all', '{"pregunta": "Diseña y escribe un texto explicativo original sobre un tema de tu interés relacionado con la ciencia o la tecnología. Asegúrate de presentar claramente el tema, organizar las ideas de manera coherente y utilizar recursos adecuados para facilitar la comprensión del lector.", "explicacion": "El docente debe evaluar si el estudiante presenta claramente el tema seleccionando aspectos relevantes, si organiza sus ideas de forma coherente y lógica, y si utiliza recursos propios del texto explicativo para facilitar la comprensión del público. Se recomienda revisar la claridad del lenguaje, la estructura del texto y la adecuación al género.", "contexto_adicional": "El estudiante debe elegir un tema relacionado con ciencia o tecnología que le interese, y desarrollar un texto explicativo que cumpla con las características del género. Puede ser un artículo breve, un informe o un reportaje, según prefiera.", "criterios_evaluacion": ["Claridad y precisión en la presentación del tema, incluyendo una introducción que esboce los aspectos que se abordarán (peso: 30%)", "Organización lógica y coherente de las ideas, con uso adecuado de conectores y párrafos que faciliten la comprensión (peso: 40%)", "Integración de recursos propios del género explicativo, como definiciones, ejemplos, y lenguaje formal acorde al propósito (peso: 30%)"]}', '{"puntos_clave": ["Presentación clara del tema y los aspectos a abordar", "Organización coherente y uso correcto de conectores", "Uso de elementos propios del género explicativo como definiciones y ejemplos"], "respuesta_modelo": "Tema: La importancia de la energía solar. La energía solar es una fuente renovable que se obtiene a partir de la radiación del sol. En este texto se explicará qué es la energía solar, cómo se aprovecha y cuáles son sus beneficios para el medio ambiente y la sociedad. Primero, la energía solar se captura mediante paneles fotovoltaicos que transforman la luz en electricidad. Además, es una energía limpia que no emite gases contaminantes, lo que ayuda a reducir el calentamiento global. Por último, el uso de la energía solar contribuye a la independencia energética y puede disminuir costos a largo plazo. En conclusión, la energía solar es una alternativa sostenible que ofrece múltiples ventajas para el futuro.", "requiere_revision_humana": true}', 4, 0, true, '{escritura,"texto explicativo",creación,"lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (409, 89, 'open_ended', 'all', '{"pregunta": "Imagina que eres un experto en un tema que te apasione y debes explicar ese tema a estudiantes de tu curso mediante un texto explicativo. ¿Cómo organizarías la información para que sea clara y completa? Diseña y escribe un texto explicativo original que incluya una introducción donde presentes el tema y los aspectos que abordarás, un desarrollo ordenado que explique cada aspecto, y una conclusión que resuma lo expuesto. Además, justifica las decisiones que tomaste en la organización y el uso del lenguaje para facilitar la comprensión.", "explicacion": "El docente debe evaluar si el estudiante presenta el tema de manera clara en la introducción, si desarrolla los contenidos de forma ordenada y completa, y si la conclusión resume adecuadamente el tema. Además, debe valorar la reflexión del estudiante sobre por qué eligió esa organización y el tipo de lenguaje empleado para facilitar la comprensión del texto.", "contexto_adicional": "El texto debe ser adecuado para estudiantes de primero medio y utilizar un lenguaje claro y accesible. Puede tratarse de cualquier tema que el estudiante elija y conozca bien.", "criterios_evaluacion": ["Claridad y coherencia en la presentación del tema y los aspectos a abordar en la introducción (peso: 30%)", "Organización lógica y desarrollo completo de los aspectos explicativos con un lenguaje adecuado al público (peso: 40%)", "Conclusión efectiva que sintetiza el contenido y justificación clara de las decisiones organizativas y lingüísticas (peso: 30%)"]}', '{"puntos_clave": ["Presentación clara del tema y aspectos a abordar en la introducción", "Organización lógica y coherente del desarrollo con explicaciones completas", "Conclusión que sintetiza el contenido y justificación de las decisiones de organización y lenguaje"], "respuesta_modelo": "Tema: La importancia del reciclaje en el medio ambiente.\n\nIntroducción: En este texto explicaré qué es el reciclaje, por qué es fundamental para cuidar el medio ambiente y cómo podemos contribuir desde nuestra comunidad escolar.\n\nDesarrollo: Primero, definiré el concepto de reciclaje y los materiales que se pueden reciclar. Luego, describiré los beneficios ambientales, como la reducción de residuos y la conservación de recursos naturales. Finalmente, presentaré acciones prácticas que los estudiantes pueden implementar para fomentar el reciclaje en el colegio.\n\nConclusión: En resumen, el reciclaje es una herramienta clave para proteger el planeta y cada persona puede aportar con acciones sencillas. Elegí esta organización porque facilita la comprensión al avanzar de lo general a lo específico, y empleé un lenguaje sencillo para que todos los compañeros puedan entender la importancia del tema.", "requiere_revision_humana": true}', 5, 0, true, '{escritura,"texto explicativo",creación,"pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (410, 89, 'concept_map', 'all', '{"conceptos": ["Texto explicativo", "Presentación clara del tema", "Organización del contenido", "Lenguaje formal y preciso", "Introducción, desarrollo y conclusión"], "explicacion": "El mapa conceptual debe mostrar cómo la presentación clara del tema es fundamental para un texto explicativo, cómo la organización del contenido se estructura en introducción, desarrollo y conclusión, y cómo el uso de un lenguaje formal y preciso contribuye a la claridad y efectividad del texto.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la elaboración de un texto explicativo?"}', '{"conceptos_centrales": ["Texto explicativo", "Presentación clara del tema"], "relaciones_esperadas": [{"origen": "Texto explicativo", "destino": "Presentación clara del tema", "relacion": "requiere"}, {"origen": "Texto explicativo", "destino": "Introducción, desarrollo y conclusión", "relacion": "se estructura en"}, {"origen": "Texto explicativo", "destino": "Lenguaje formal y preciso", "relacion": "utiliza"}, {"origen": "Introducción, desarrollo y conclusión", "destino": "Organización del contenido", "relacion": "conforman"}, {"origen": "Presentación clara del tema", "destino": "Organización del contenido", "relacion": "apoya"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Texto explicativo","Mapa conceptual","Primeros medios",Crear}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (411, 89, 'concept_map', 'all', '{"conceptos": ["Texto explicativo", "Presentación clara", "Organización de ideas", "Géneros textuales", "Propósito del texto", "Ejemplos de textos explicativos", "Elementos del género"], "explicacion": "Se espera que el estudiante muestre cómo la presentación clara y la organización de ideas son fundamentales para construir un texto explicativo efectivo, que debe ajustarse a los elementos propios del género y al propósito comunicativo. Además, debe conectar los ejemplos específicos de géneros textuales con las características que los definen dentro de los textos explicativos.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la escritura de textos explicativos?"}', '{"conceptos_centrales": ["Texto explicativo", "Presentación clara", "Organización de ideas"], "relaciones_esperadas": [{"origen": "Texto explicativo", "destino": "Presentación clara", "relacion": "requiere"}, {"origen": "Texto explicativo", "destino": "Organización de ideas", "relacion": "implica"}, {"origen": "Texto explicativo", "destino": "Géneros textuales", "relacion": "se manifiesta en"}, {"origen": "Géneros textuales", "destino": "Ejemplos de textos explicativos", "relacion": "incluyen"}, {"origen": "Texto explicativo", "destino": "Elementos del género", "relacion": "debe cumplir con"}, {"origen": "Texto explicativo", "destino": "Propósito del texto", "relacion": "tiene como"}], "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Primero Medio","Texto explicativo","Mapa conceptual",Crear}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (412, 89, 'open_ended', 'all', '{"pregunta": "Diseña y escribe un texto explicativo original sobre un tema de tu elección relacionado con la cultura chilena. Asegúrate de presentar claramente el tema, organizar la información de manera coherente y utilizar un lenguaje adecuado para explicar los aspectos principales del tema seleccionado.", "explicacion": "Se evaluará que el estudiante sea capaz de crear un texto que cumpla con las características de un texto explicativo: introducción clara del tema, desarrollo organizado y coherente, y lenguaje adecuado. El docente debe revisar que el contenido sea original, pertinente y que el texto mantenga un hilo conductor que facilite la comprensión.", "contexto_adicional": "El estudiante puede elegir cualquier tema relacionado con la cultura chilena, como tradiciones, festividades, gastronomía, música, o historia, siempre que pueda explicarlo claramente en un texto explicativo.", "criterios_evaluacion": ["Claridad y precisión en la presentación del tema y los aspectos a abordar (peso: 30%)", "Organización lógica y coherente del contenido con uso adecuado de conectores (peso: 40%)", "Uso correcto y adecuado del lenguaje propio del texto explicativo, con vocabulario pertinente y sin errores ortográficos graves (peso: 30%)"]}', '{"puntos_clave": ["Presentación clara del tema (la cueca como danza nacional chilena)", "Organización coherente con introducción, desarrollo y cierre", "Uso adecuado del lenguaje explicativo y vocabulario pertinente"], "respuesta_modelo": "La cueca es la danza nacional de Chile y representa una tradición muy importante en la cultura del país. Esta danza simboliza el cortejo entre un hombre y una mujer, quienes utilizan pañuelos y ejecutan movimientos que imitan el vuelo de un gallo y una gallina. Se baila principalmente durante las festividades patrias en septiembre, especialmente el 18 de septiembre. La cueca no solo es un baile, sino también una expresión cultural que une a las personas y refleja la identidad chilena. Sus orígenes se remontan a la época colonial y ha sido reconocida oficialmente como danza nacional desde 1979.", "requiere_revision_humana": true}', 5, 0, true, '{escritura,texto_explicativo,cultura_chilena,primer_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (413, 90, 'multiple_choice', 'all', '{"opciones": {"A": "Una narración detallada de hechos sin opinión", "B": "Una afirmación clara que presenta el punto de vista del autor sobre un tema", "C": "Una lista de ejemplos sin relación entre sí", "D": "Un resumen final sin conexión con la introducción"}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para identificar la hipótesis en un texto persuasivo?", "explicacion": "La hipótesis en un texto persuasivo es la afirmación clara que expresa el punto de vista o la idea principal que el autor quiere defender. La opción B corresponde a esta definición. La opción A describe una narración, que no implica necesariamente una hipótesis; la C muestra evidencias o ejemplos, pero sin conexión clara no cumple la función de hipótesis; y la D describe una conclusión que debería estar conectada con la hipótesis, pero no es la hipótesis misma."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"texto persuasivo",hipótesis,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (414, 90, 'multiple_choice', 'all', '{"opciones": {"A": "Presentación de una hipótesis o afirmación", "B": "Lista de personajes y sus características", "C": "Evidencias que apoyan la hipótesis", "D": "Conclusión que reafirma el punto de vista"}, "pregunta": "¿Cuál de los siguientes elementos NO forma parte de un texto persuasivo?", "explicacion": "La opción B es correcta porque una lista de personajes y sus características es propia de textos narrativos, no persuasivos. Los textos persuasivos se basan en presentar una hipótesis (A), respaldarla con evidencias (C) y cerrar con una conclusión que reafirma la postura (D). Los distractores A, C y D son elementos fundamentales para convencer al lector, mientras que B es un error común confundir características narrativas con elementos persuasivos."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"texto persuasivo","elementos del texto","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (415, 90, 'multiple_choice', 'all', '{"opciones": {"A": "El uso de un lenguaje muy coloquial para conectar con el lector.", "B": "La presentación de evidencias que apoyen la afirmación principal.", "C": "Una introducción larga que explique todos los detalles del tema.", "D": "Describir personajes y lugares para ambientar el texto."}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para sostener la hipótesis en un texto persuasivo?", "explicacion": "La respuesta correcta es B, porque en un texto persuasivo, las evidencias son esenciales para respaldar la hipótesis o afirmación central, permitiendo que el argumento sea convincente. La opción A es un error común ya que el lenguaje coloquial puede ser útil, pero no es fundamental para sostener la hipótesis. La opción C confunde la extensión de la introducción con su función; no es necesario que sea larga sino clara. La opción D corresponde a características de textos narrativos, no persuasivos."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"texto persuasivo","elementos básicos",hipótesis,evidencias}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (416, 90, 'true_false', 'all', '{"afirmacion": "En un texto persuasivo, la hipótesis es la afirmación principal que el autor quiere defender.", "explicacion": "Esta afirmación es verdadera porque la hipótesis en un texto persuasivo representa la idea o postura central que el autor intenta convencer al lector que acepte, siendo fundamental para guiar la presentación de evidencias y la estructura del texto."}', '{"es_verdadero": true}', 1, 0, true, '{"texto persuasivo",hipótesis,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (418, 91, 'multiple_choice', 'all', '{"opciones": {"A": "Presentar datos y estadísticas para respaldar un argumento.", "B": "Establecer una afirmación o idea principal que se defenderá a lo largo del texto.", "C": "Describir en detalle el contexto histórico del tema tratado.", "D": "Concluir el texto resumiendo las ideas principales."}, "pregunta": "¿Cuál es la función principal de la hipótesis en un texto persuasivo?", "explicacion": "La hipótesis en un texto persuasivo tiene como función principal establecer una afirmación o idea central que se defenderá y argumentará a lo largo del texto, orientando la argumentación. La opción A corresponde a la función de la evidencia o apoyo, no de la hipótesis. La opción C describe una función más típica de una introducción o contextualización, pero no de la hipótesis. La opción D corresponde a la conclusión, que sintetiza argumentos, no a la hipótesis."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"texto persuasivo",hipótesis,argumentación,"OA Lengua y Literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (419, 91, 'multiple_choice', 'all', '{"opciones": {"A": "Presentar información objetiva sin expresar opiniones.", "B": "Plantear una afirmación o idea que se defenderá a lo largo del texto.", "C": "Contar una experiencia personal para captar la atención del lector.", "D": "Resumir las ideas principales al final del texto."}, "pregunta": "¿Cuál es la función principal de la hipótesis en un texto persuasivo?", "explicacion": "La hipótesis en un texto persuasivo es esencial porque plantea una afirmación o idea que el autor buscará defender mediante argumentos a lo largo del texto. La opción B es correcta porque refleja esta función. La opción A es incorrecta ya que los textos persuasivos no solo presentan información objetiva sino que buscan convencer. La opción C es un recurso que puede usarse, pero no define la función de la hipótesis. La opción D corresponde a la conclusión, no a la hipótesis."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"texto persuasivo",hipótesis,argumentación,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (420, 91, 'true_false', 'all', '{"afirmacion": "En un texto persuasivo, la presentación de una hipótesis o afirmación inicial es fundamental porque orienta la argumentación y da coherencia al mensaje.", "explicacion": "Esta afirmación es verdadera porque la hipótesis o afirmación inicial actúa como el eje central del texto persuasivo, guiando la selección y organización de los argumentos para convencer al lector sobre un punto de vista específico."}', '{"es_verdadero": true}', 2, 0, true, '{"texto persuasivo","estructura argumentativa",comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (421, 91, 'drag_drop_matching', 'all', '{"explicacion": "La tesis es la afirmación principal que se defiende; los argumentos son las razones que la apoyan; el contraargumento presenta la opinión contraria para refutarla; la conclusión cierra y refuerza la tesis; el ejemplo ilustra un argumento concreto; el distractor es información irrelevante que no corresponde a ningún elemento persuasivo.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente en un texto persuasivo", "columna_derecha": ["Presentación de la idea principal que se quiere defender", "Razones o evidencias que apoyan la tesis", "Opinión opuesta que se anticipa para refutarla", "Cierre que resume y refuerza la tesis", "Caso o situación específica que ilustra un argumento", "Información irrelevante que no aporta a la argumentación"], "columna_izquierda": ["Tesis", "Argumento", "Contraargumento", "Conclusión", "Ejemplo"]}', '{"emparejamientos_correctos": {"Tesis": "Presentación de la idea principal que se quiere defender", "Ejemplo": "Caso o situación específica que ilustra un argumento", "Argumento": "Razones o evidencias que apoyan la tesis", "Conclusión": "Cierre que resume y refuerza la tesis", "Contraargumento": "Opinión opuesta que se anticipa para refutarla"}}', 2, 0, true, '{"texto persuasivo","elementos argumentativos","Lengua y Literatura","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (422, 91, 'sequencing', 'all', '{"explicacion": "Primero se introduce la hipótesis o afirmación para establecer el tema. Luego, se presenta evidencia que la respalde. A continuación, se explica cómo esa evidencia apoya la afirmación para fortalecer la argumentación. Finalmente, se concluye reafirmando la postura presentada.", "instruccion": "Ordena los siguientes pasos en la secuencia correcta para construir un texto persuasivo.", "elementos_desordenados": ["Presentar evidencia que apoye la afirmación", "Introducir la hipótesis o afirmación principal", "Conclusión que reafirme la postura", "Explicar cómo la evidencia respalda la afirmación"]}', '{"orden_correcto": ["Introducir la hipótesis o afirmación principal", "Presentar evidencia que apoye la afirmación", "Explicar cómo la evidencia respalda la afirmación", "Conclusión que reafirme la postura"]}', 3, 0, true, '{"texto persuasivo",argumentación,"estructura de texto"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (423, 92, 'multiple_choice', 'all', '{"opciones": {"A": "Iniciar con una conclusión clara y luego agregar evidencias sin presentar una hipótesis.", "B": "Presentar una hipótesis o afirmación clara al inicio, seguida de evidencias que la respalden y concluir con una síntesis coherente.", "C": "Comenzar con una lista de datos y hechos sin relacionarlos con una idea central o hipótesis.", "D": "Escribir una introducción extensa sin plantear una afirmación clara, para luego exponer opiniones personales sin evidencias."}, "pregunta": "¿Cuál de las siguientes opciones representa la estructura correcta para comenzar a redactar un ensayo persuasivo sobre un tema propuesto?", "explicacion": "La opción B es correcta porque un ensayo persuasivo debe comenzar con una hipótesis o afirmación clara sobre el tema, que luego será respaldada por evidencias y argumentos coherentes, finalizando con una conclusión que sintetice lo expuesto. La opción A es incorrecta porque iniciar con la conclusión sin presentar la hipótesis y evidencias afecta la claridad y la persuasión. La opción C es errónea ya que presentar datos sin conectarlos a una hipótesis carece de coherencia argumentativa. La opción D falla porque no plantea una afirmación clara y se basa en opiniones sin respaldo, lo que debilita la persuasión del texto."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"ensayo persuasivo","estructura del texto",argumentación,"lengua y literatura","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (435, 94, 'criteria_evaluation', 'all', '{"rubrica": {"Coherencia y cohesión argumentativa": {"Excelente": "Los argumentos están organizados de manera lógica, con transiciones claras y conexión fluida entre ideas.", "Satisfactorio": "Los argumentos son comprensibles pero la organización o las conexiones pueden mejorar para mayor fluidez.", "Necesita mejorar": "Los argumentos son confusos, poco organizados o carecen de conexiones claras entre ideas."}, "Claridad y relevancia de la hipótesis": {"Excelente": "La hipótesis está claramente formulada, es precisa y directamente relacionada con el tema propuesto.", "Satisfactorio": "La hipótesis es clara pero podría ser más precisa o estar mejor enfocada en el tema.", "Necesita mejorar": "La hipótesis es vaga, confusa o no está claramente relacionada con el tema."}, "Calidad y pertinencia de las evidencias": {"Excelente": "Las evidencias son variadas, relevantes, actuales y respaldan de manera contundente la hipótesis.", "Satisfactorio": "Las evidencias son adecuadas pero podrían ser más variadas o mejor seleccionadas para apoyar la hipótesis.", "Necesita mejorar": "Las evidencias son escasas, poco relevantes o no respaldan adecuadamente la hipótesis."}, "Capacidad crítica en la evaluación del tema": {"Excelente": "El ensayo muestra un análisis profundo, considera diferentes perspectivas y anticipa posibles contraargumentos.", "Satisfactorio": "El ensayo presenta análisis adecuado pero no profundiza en todas las perspectivas o contraargumentos.", "Necesita mejorar": "El ensayo carece de análisis crítico y no considera otras perspectivas o contraargumentos."}}, "explicacion": "Se espera que el estudiante identifique la claridad de la hipótesis, valore la pertinencia y calidad de las evidencias presentadas, analice la coherencia interna de los argumentos y reconozca el nivel de pensamiento crítico aplicado en el ensayo. La justificación debe incluir referencias específicas al texto que apoyen su evaluación.", "instruccion": "Evalúa el ensayo según los criterios de la rúbrica y justifica tu calificación para cada uno, argumentando con ejemplos específicos del texto.", "caso_a_evaluar": "Analiza el siguiente ensayo persuasivo que defiende la implementación de jornadas escolares más cortas para mejorar el bienestar estudiantil. El ensayo presenta una hipótesis clara, argumentos respaldados por datos estadísticos y citas de expertos, pero en algunos párrafos la conexión entre ideas no es fluida."}', '{"evaluacion_modelo": {"Coherencia y cohesión argumentativa": "Satisfactorio", "Claridad y relevancia de la hipótesis": "Excelente", "Calidad y pertinencia de las evidencias": "Satisfactorio", "Capacidad crítica en la evaluación del tema": "Necesita mejorar"}, "justificacion_modelo": "La hipótesis está claramente expresada y bien relacionada con el tema, por lo que merece ''Excelente''. Las evidencias son relevantes y respaldan la hipótesis, pero podrían ser más variadas, por lo que se califican como ''Satisfactorio''. La organización del ensayo es comprensible, aunque algunas transiciones no son fluidas, justificando un ''Satisfactorio''. Finalmente, el ensayo no profundiza en análisis crítico ni considera contraargumentos, por lo que obtiene ''Necesita mejorar''.", "requiere_revision_humana": true}', 5, 0, true, '{"ensayo persuasivo","evaluación crítica",argumentación,"Lengua y Literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (424, 92, 'multiple_choice', 'all', '{"opciones": {"A": "Presentar una serie de ideas sin relación clara, finalizar con una conclusión sin relación con el contenido.", "B": "Plantear una hipótesis clara, apoyar con evidencias relevantes, mantener coherencia entre párrafos y concluir reafirmando la postura.", "C": "Escribir una introducción extensa con muchos detalles, sin incluir hipótesis; luego, solo ejemplos sin conclusión.", "D": "Iniciar con una opinión personal sin fundamentarla, presentar argumentos contradictorios y concluir con una pregunta abierta."}, "pregunta": "¿Cuál de las siguientes opciones describe mejor la estructura adecuada para redactar un ensayo persuasivo sobre un tema propuesto?", "explicacion": "La opción B es correcta porque describe la estructura esencial de un ensayo persuasivo: se debe presentar una hipótesis o afirmación clara, respaldarla con evidencias relevantes, mantener coherencia lógica entre las ideas y finalizar con una conclusión que reafirme la postura. La opción A falla porque las ideas no están relacionadas y la conclusión no conecta con el contenido. La opción C no incluye una hipótesis ni una conclusión, elementos clave para un ensayo persuasivo. La opción D presenta argumentos contradictorios y no fundamenta la opinión, lo que afecta la coherencia y fuerza del texto."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"ensayo persuasivo","estructura del texto",hipótesis,coherencia,"Lengua y Literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (425, 92, 'drag_drop_matching', 'all', '{"explicacion": "La hipótesis es la afirmación que se defiende, la evidencia son los datos que la apoyan, la conclusión sintetiza y reafirma la postura, la coherencia asegura la lógica entre ideas, y el ensayo persuasivo es el texto con el propósito de convencer. La narración es un distractor que no pertenece a esta estructura.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente en el contexto de un ensayo persuasivo", "columna_derecha": ["Afirmación o idea principal que se defiende en el texto", "Datos, ejemplos o argumentos que apoyan la hipótesis", "Cierre donde se reafirma la postura y se sintetizan los argumentos", "Relación lógica y ordenada entre las ideas del texto", "Texto que busca convencer al lector sobre un punto de vista", "Relato de hechos en orden cronológico sin intención persuasiva"], "columna_izquierda": ["Hipótesis", "Evidencia", "Conclusión", "Coherencia", "Ensayo Persuasivo", "Distractor: Narración"]}', '{"emparejamientos_correctos": {"Evidencia": "Datos, ejemplos o argumentos que apoyan la hipótesis", "Coherencia": "Relación lógica y ordenada entre las ideas del texto", "Hipótesis": "Afirmación o idea principal que se defiende en el texto", "Conclusión": "Cierre donde se reafirma la postura y se sintetizan los argumentos", "Ensayo Persuasivo": "Texto que busca convencer al lector sobre un punto de vista"}}', 3, 0, true, '{"ensayo persuasivo","estructura textual","primer medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (426, 92, 'sequencing', 'all', '{"explicacion": "El orden correcto comienza con una introducción que capte la atención, seguida de la presentación clara de la hipótesis o afirmación principal. Luego, se deben incluir evidencias y argumentos que apoyen esa hipótesis para persuadir al lector. Después, se concluye reafirmando la hipótesis con una conclusión sólida. Finalmente, se revisa la coherencia y cohesión para asegurar que el ensayo sea claro y efectivo.", "instruccion": "Ordena los siguientes pasos para redactar un ensayo persuasivo correctamente", "elementos_desordenados": ["Presentar una conclusión que reafirme la hipótesis", "Introducir la hipótesis o afirmación principal", "Incluir evidencias y argumentos que apoyen la hipótesis", "Escribir una introducción que capte la atención", "Revisar la coherencia y cohesión del texto"]}', '{"orden_correcto": ["Escribir una introducción que capte la atención", "Introducir la hipótesis o afirmación principal", "Incluir evidencias y argumentos que apoyen la hipótesis", "Presentar una conclusión que reafirme la hipótesis", "Revisar la coherencia y cohesión del texto"]}', 3, 0, true, '{"ensayo persuasivo","ordenar pasos",redacción,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (427, 92, 'open_ended', 'all', '{"pregunta": "Redacta un ensayo persuasivo en el que presentes una hipótesis sobre el impacto de las redes sociales en el desempeño académico de los estudiantes de enseñanza media. Asegúrate de estructurar tu texto con una introducción que incluya la hipótesis, al menos dos evidencias que la apoyen, un análisis coherente de dichas evidencias y una conclusión que refuerce tu postura.", "explicacion": "Se evalúa la capacidad del estudiante para aplicar la estructura del ensayo persuasivo, formulando una hipótesis clara, seleccionando evidencias relevantes para respaldarla y organizando sus ideas de manera coherente y cohesionada, terminando con una conclusión que reafirme su postura.", "contexto_adicional": "El ensayo debe centrarse en temas actuales relacionados con el uso de redes sociales y su posible influencia en el rendimiento escolar, considerando ejemplos o datos que el estudiante conozca o haya investigado.", "criterios_evaluacion": ["Presentación clara y precisa de la hipótesis en la introducción (peso: 30%)", "Uso adecuado de evidencias relevantes y análisis coherente que apoye la hipótesis (peso: 40%)", "Estructura general y coherencia del ensayo, incluyendo introducción, desarrollo y conclusión (peso: 30%)"]}', '{"puntos_clave": ["Hipótesis clara sobre el impacto de las redes sociales en el desempeño académico", "Evidencias concretas que apoyan la hipótesis (ejemplos o datos)", "Estructura del ensayo: introducción, desarrollo con análisis y conclusión coherente"], "respuesta_modelo": "En la actualidad, las redes sociales tienen un impacto significativo en la vida de los estudiantes de enseñanza media, afectando su desempeño académico de manera negativa. Mi hipótesis es que el uso excesivo de las redes sociales disminuye la concentración y el tiempo dedicado al estudio, lo que repercute en los resultados escolares. Por ejemplo, diversos estudios muestran que los estudiantes que pasan más de tres horas diarias en redes sociales tienden a obtener menores calificaciones. Además, el constante uso de estas plataformas puede generar distracciones frecuentes durante el estudio, interrumpiendo el proceso de aprendizaje. Estas evidencias evidencian que, aunque las redes sociales pueden ser una herramienta útil para la comunicación, su uso desmedido puede perjudicar el rendimiento académico. En conclusión, es fundamental que los estudiantes aprendan a gestionar su tiempo en las redes sociales para evitar que interfieran negativamente en sus estudios.", "requiere_revision_humana": true}', 3, 0, true, '{"ensayo persuasivo","redes sociales","desempeño académico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (428, 93, 'compare_contrast', 'all', '{"conceptos": ["Presentación de hipótesis", "Uso de evidencias"], "criterios": ["Claridad", "Relevancia", "Coherencia"], "explicacion": "La presentación de hipótesis se enfoca en cómo se formula y comunica la idea principal, buscando claridad y coherencia en el planteamiento. El uso de evidencias se refiere a la selección y vinculación de datos o ejemplos que apoyan la hipótesis, evaluándose su relevancia y coherencia con el argumento central.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Uso de evidencias": {"Claridad": "Las evidencias deben estar claramente explicadas y vinculadas a la hipótesis.", "Coherencia": "Las evidencias deben integrarse de manera lógica dentro del argumento general.", "Relevancia": "Las evidencias deben apoyar directamente la hipótesis presentada."}, "Presentación de hipótesis": {"Claridad": "La hipótesis debe ser explícita y comprensible para el lector.", "Coherencia": "Debe mantenerse consistente a lo largo del texto, sin contradicciones.", "Relevancia": "Debe estar directamente relacionada con el tema central del ensayo."}}}', 3, 0, true, '{"ensayo persuasivo","análisis de texto",comparación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (516, 110, 'open_ended', 'all', '{"pregunta": "Escribe un texto breve (150-200 palabras) en el que describas una experiencia personal significativa utilizando correferencia léxica compleja. En tu texto, incorpora al menos una metáfora y una metonimia para enriquecer la expresión y conectar las ideas. Explica al final cómo utilizaste cada recurso y qué efecto buscaste al emplearlos.", "explicacion": "Se evalúa que el alumno pueda aplicar la correferencia léxica compleja para evitar repeticiones y asegurar la fluidez del texto. Además, debe demostrar comprensión y creatividad en el uso de metáforas y metonimias, explicando cómo estos recursos aportan sentido y estética a su texto.", "contexto_adicional": "El texto debe ser una narración personal o una descripción de una experiencia significativa para el estudiante, donde pueda aplicar los recursos literarios solicitados de manera natural.", "criterios_evaluacion": ["Uso adecuado y coherente de la correferencia léxica compleja para mantener la cohesión textual (peso: 40%)", "Incorporación correcta y creativa de una metáfora y una metonimia en el texto (peso: 35%)", "Claridad y profundidad en la explicación del uso de los recursos literarios y su efecto en el texto (peso: 25%)"]}', '{"puntos_clave": ["Uso consistente de pronombres, sinónimos o expresiones referenciales para mantener la correferencia", "Empleo claro y pertinente de una metáfora y una metonimia en el contexto del texto", "Explicación reflexiva sobre el uso de los recursos y el efecto buscado en la comunicación del texto"], "respuesta_modelo": "Durante el invierno pasado, mi alma navegaba en un mar de incertidumbre; las dudas eran olas que golpeaban mi barco sin cesar. La escuela, ese faro silencioso, me guiaba en la tormenta. En ese tiempo, enfrenté uno de mis mayores retos: un examen difícil que parecía un gigante imbatible. Sin embargo, al recordar las palabras de mi profesora, comprendí que las montañas más altas se escalan paso a paso. La metáfora del ''alma navegando en un mar de incertidumbre'' representa mis sentimientos de inseguridad, mientras que la metonimia del ''faro'' simboliza la escuela como fuente de guía y apoyo. Estas imágenes me ayudaron a expresar cómo, a pesar de los obstáculos, encontré luz y esperanza para seguir adelante.", "requiere_revision_humana": true}', 3, 0, true, '{lengua_y_literatura,correferencia_lexica,metafora,metonimia,escritura_creativa}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (429, 93, 'compare_contrast', 'all', '{"conceptos": ["Presentación de hipótesis", "Uso de evidencias"], "criterios": ["Claridad", "Relevancia", "Coherencia"], "explicacion": "La presentación de hipótesis se centra en la claridad y coherencia con la que se propone una afirmación, mientras que el uso de evidencias se enfoca en la relevancia y efectividad de los datos o argumentos que respaldan dicha hipótesis. Ambas son fundamentales para la persuasión, pero difieren en su función dentro del ensayo.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Uso de evidencias": {"Claridad": "Debe estar claramente vinculada a la hipótesis", "Coherencia": "Debe integrarse lógicamente con la hipótesis y argumentos", "Relevancia": "Datos, ejemplos o citas que apoyan directamente la afirmación"}, "Presentación de hipótesis": {"Claridad": "Debe ser explícita y comprensible para el lector", "Coherencia": "Consistente con el desarrollo y conclusión del texto", "Relevancia": "Relacionada directamente con el tema central del ensayo"}}}', 4, 0, true, '{"ensayo persuasivo",comparación,análisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (430, 93, 'open_ended', 'all', '{"pregunta": "Analiza dos ensayos persuasivos que hayan leído en clase y compara cómo presentan la hipótesis, el uso de evidencias y la coherencia argumentativa. ¿Cuál de los dos consideras que es más efectivo para persuadir y por qué?", "explicacion": "El docente debe verificar que el estudiante logre identificar correctamente la hipótesis en ambos textos, evaluar críticamente las evidencias utilizadas y valorar la coherencia interna de los argumentos, fomentando un juicio fundamentado sobre la efectividad persuasiva.", "contexto_adicional": "Los estudiantes deben basar su análisis en los ensayos discutidos en clase, enfocándose en temas contingentes o literarios según el nivel.", "criterios_evaluacion": ["Identificación clara y precisa de la hipótesis en ambos ensayos (peso: 30%)", "Análisis comparativo del uso de evidencias en términos de relevancia y suficiencia (peso: 40%)", "Evaluación de la coherencia y cohesión en la argumentación de cada ensayo (peso: 30%)"]}', '{"puntos_clave": ["Claridad en la presentación de la hipótesis en ambos ensayos", "Uso de evidencias relevantes y suficientes para apoyar la hipótesis", "Coherencia y cohesión en la argumentación que facilita la persuasión"], "respuesta_modelo": "En el primer ensayo, la hipótesis se presenta de forma explícita y clara, afirmando que el cambio climático es la principal amenaza global actual. El segundo ensayo plantea que la educación es la herramienta más efectiva para combatir la desigualdad social. En cuanto a evidencias, el primer ensayo utiliza datos científicos y estadísticas recientes, lo que fortalece su argumento, mientras que el segundo se basa mayormente en ejemplos anecdóticos y citas de expertos, lo que resulta menos contundente. Respecto a la coherencia, el primer ensayo mantiene una secuencia lógica y clara que conecta la hipótesis con las evidencias y conclusiones; en cambio, el segundo presenta saltos argumentativos que dificultan la comprensión. Por lo tanto, considero que el primer ensayo es más efectivo para persuadir porque presenta una hipótesis clara, usa evidencias relevantes y mantiene una argumentación coherente.", "requiere_revision_humana": true}', 4, 0, true, '{"ensayo persuasivo","análisis crítico","blooms nivel 4"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (431, 93, 'concept_map', 'all', '{"conceptos": ["Hipótesis", "Evidencia", "Coherencia", "Argumento", "Contraargumento"], "explicacion": "Se espera que el estudiante demuestre que la hipótesis es la afirmación central, que los argumentos la apoyan mediante evidencia sólida, que la coherencia asegura la claridad y conexión lógica entre ideas, y que el contraargumento se presenta para fortalecer la defensa de la hipótesis.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la estructura y efectividad de un ensayo persuasivo?"}', '{"conceptos_centrales": ["Hipótesis", "Argumento"], "relaciones_esperadas": [{"origen": "Hipótesis", "destino": "Argumento", "relacion": "es apoyada por"}, {"origen": "Argumento", "destino": "Evidencia", "relacion": "se sustenta en"}, {"origen": "Coherencia", "destino": "claridad en el Argumento", "relacion": "garantiza"}, {"origen": "Contraargumento", "destino": "Hipótesis", "relacion": "refuerza"}, {"origen": "Contraargumento", "destino": "Argumento", "relacion": "se enfrenta a"}], "requiere_revision_humana": true}', 3, 0, true, '{"ensayo persuasivo","análisis crítico","estructura textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (432, 93, 'concept_map', 'all', '{"conceptos": ["Hipótesis", "Evidencia", "Coherencia", "Persuasión", "Estructura del ensayo", "Contraargumento", "Tesis", "Conclusión"], "explicacion": "Se espera que el estudiante conecte la ''Hipótesis'' o ''Tesis'' con la ''Evidencia'' que la sustenta, mostrando cómo la ''Coherencia'' asegura una presentación lógica y fluida. La ''Estructura del ensayo'' debe organizar estos elementos, incluyendo el uso de ''Contraargumentos'' para fortalecer la ''Persuasión'', finalizando con una ''Conclusión'' que refuerce la hipótesis.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la construcción de ensayos persuasivos?"}', '{"conceptos_centrales": ["Hipótesis", "Persuasión"], "relaciones_esperadas": [{"origen": "Tesis", "destino": "Evidencia", "relacion": "es sustentada por"}, {"origen": "Hipótesis", "destino": "Estructura del ensayo", "relacion": "es parte de"}, {"origen": "Evidencia", "destino": "Persuasión", "relacion": "refuerza"}, {"origen": "Coherencia", "destino": "claridad en la estructura", "relacion": "asegura"}, {"origen": "Contraargumento", "destino": "Persuasión", "relacion": "fortalece"}, {"origen": "Conclusión", "destino": "Hipótesis", "relacion": "refuerza"}], "requiere_revision_humana": true}', 4, 0, true, '{"ensayo persuasivo","mapa conceptual",análisis,creación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (433, 94, 'criteria_evaluation', 'all', '{"rubrica": {"Coherencia argumentativa": {"Excelente": "Los argumentos están estructurados de manera lógica y fluida, con conexiones claras que refuerzan la hipótesis central.", "Satisfactorio": "Los argumentos son generalmente coherentes, aunque existen algunas inconsistencias o falta de fluidez en la exposición.", "Necesita mejorar": "Los argumentos presentan incoherencias, están desordenados o no apoyan efectivamente la hipótesis."}, "Calidad de las evidencias": {"Excelente": "Las evidencias son variadas, pertinentes, actuales y confiables, respaldando claramente la hipótesis del ensayo.", "Satisfactorio": "Las evidencias son adecuadas y relevantes, aunque pueden ser limitadas o poco variadas.", "Necesita mejorar": "Las evidencias son insuficientes, poco claras o no respaldan adecuadamente la hipótesis."}, "Claridad en la presentación de la hipótesis": {"Excelente": "La hipótesis está claramente expresada desde el inicio y es fácilmente identificable a lo largo del ensayo.", "Satisfactorio": "La hipótesis está presente pero su expresión es ambigua o poco destacada.", "Necesita mejorar": "La hipótesis no está clara o no se identifica en el ensayo."}}, "explicacion": "Se espera que el estudiante identifique la pertinencia y confiabilidad de las evidencias utilizadas, analice la estructura lógica de los argumentos y reconozca la claridad con que se presenta la hipótesis. La justificación debe mostrar un juicio crítico fundamentado.", "instruccion": "Evalúa el ensayo según los criterios de la rúbrica y justifica tu calificación para cada uno de ellos, argumentando por qué consideras que el ensayo cumple o no con los niveles propuestos.", "caso_a_evaluar": "Lee el siguiente ensayo persuasivo que defiende la importancia de implementar jornadas escolares más cortas para mejorar el bienestar de los estudiantes. Evalúa la validez del ensayo, considerando la calidad de las evidencias presentadas y la coherencia de los argumentos expuestos."}', '{"evaluacion_modelo": {"Coherencia argumentativa": "Excelente", "Calidad de las evidencias": "Satisfactorio", "Claridad en la presentación de la hipótesis": "Excelente"}, "justificacion_modelo": "El ensayo presenta evidencias adecuadas pero limitadas en variedad, por lo que se califica como satisfactorio en calidad de evidencias. Sin embargo, los argumentos están bien organizados y conectados, demostrando excelente coherencia argumentativa. La hipótesis se expresa claramente desde el inicio y se mantiene visible, lo que justifica la calificación excelente en ese criterio.", "requiere_revision_humana": true}', 4, 0, true, '{evaluacion_critica,ensayo_persuasivo,argumentacion,primeromedia}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (434, 94, 'criteria_evaluation', 'all', '{"rubrica": {"Coherencia argumentativa": {"Excelente": "Los argumentos están organizados lógicamente, con conexiones claras y refuerzan eficazmente la hipótesis.", "Satisfactorio": "Los argumentos son generalmente coherentes, aunque algunas conexiones podrían ser más claras o mejor desarrolladas.", "Necesita mejorar": "Los argumentos carecen de organización lógica o presentan contradicciones que debilitan la hipótesis."}, "Calidad de las evidencias": {"Excelente": "Las evidencias son variadas, pertinentes, actuales y están bien integradas para apoyar la hipótesis.", "Satisfactorio": "Las evidencias son pertinentes y en su mayoría actuales, aunque podrían ser más variadas o mejor integradas.", "Necesita mejorar": "Las evidencias son escasas, poco relevantes o desactualizadas y no apoyan adecuadamente la hipótesis."}, "Claridad en la presentación de la hipótesis": {"Excelente": "La hipótesis está explícita y claramente formulada desde el inicio del ensayo.", "Satisfactorio": "La hipótesis está presente pero su formulación es poco clara o está implícita en el texto.", "Necesita mejorar": "La hipótesis no está claramente identificada o es confusa."}}, "explicacion": "Se espera que el estudiante valore críticamente la calidad, pertinencia y actualidad de las evidencias presentadas, analice la estructura y coherencia de los argumentos, y determine la claridad con la que se plantea la hipótesis central del ensayo.", "instruccion": "Evalúa el ensayo ''La importancia de implementar energías renovables en Chile'' según los criterios de la rúbrica y justifica tu calificación para cada uno de ellos.", "caso_a_evaluar": "Se presenta el ensayo persuasivo titulado ''La importancia de implementar energías renovables en Chile'', en el cual el autor sostiene que la adopción masiva de energías limpias es fundamental para el desarrollo sostenible del país. El ensayo contiene una hipótesis clara, varios argumentos basados en datos estadísticos recientes, testimonios de expertos y ejemplos de países que han avanzado en esta materia."}', '{"evaluacion_modelo": {"Coherencia argumentativa": "Satisfactorio", "Calidad de las evidencias": "Excelente", "Claridad en la presentación de la hipótesis": "Excelente"}, "justificacion_modelo": "El ensayo utiliza datos estadísticos actuales y testimonios relevantes que apoyan claramente la hipótesis, demostrando evidencias variadas y pertinentes, por lo que se evalúa como excelente. La organización argumentativa es buena, aunque algunos argumentos podrían estar mejor conectados, lo que justifica un nivel satisfactorio. La hipótesis está claramente expuesta al inicio del texto, lo que corresponde a un nivel excelente.", "requiere_revision_humana": true}', 5, 0, true, '{"ensayo persuasivo","evaluación crítica",argumentación,"primero medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (436, 94, 'open_ended', 'all', '{"pregunta": "Lee el siguiente ensayo persuasivo sobre el uso de la tecnología en la educación. Evalúa la validez de la hipótesis presentada considerando la calidad de las evidencias y la coherencia argumentativa. ¿En qué medida los argumentos apoyan efectivamente la afirmación principal? Justifica tu respuesta con ejemplos específicos del texto.", "explicacion": "El docente debe valorar que el estudiante identifique la hipótesis principal, analice la calidad y pertinencia de las evidencias, y evalúe la coherencia interna del argumento. Además, se espera que el estudiante justifique su evaluación usando ejemplos específicos extraídos del texto.", "contexto_adicional": "Se proporciona un ensayo corto que defiende la integración de la tecnología digital en las aulas como medio para mejorar el aprendizaje.", "criterios_evaluacion": ["Evaluación de la calidad y relevancia de las evidencias presentadas para apoyar la hipótesis (peso: 40%)", "Análisis de la coherencia y estructura lógica de los argumentos en el ensayo (peso: 35%)", "Capacidad para justificar con ejemplos concretos y argumentar críticamente la validez de la hipótesis (peso: 25%)"]}', '{"puntos_clave": ["Identificación clara de la hipótesis o afirmación principal", "Valoración crítica de la calidad y relevancia de las evidencias presentadas", "Análisis de la coherencia y lógica interna de los argumentos", "Justificación con ejemplos específicos extraídos del texto"], "respuesta_modelo": "La hipótesis principal del ensayo sostiene que la integración de la tecnología en la educación mejora significativamente el aprendizaje de los estudiantes. Las evidencias que se presentan, como estudios recientes que muestran aumentos en la motivación y rendimiento académico, son relevantes y confiables, lo que fortalece la afirmación. Sin embargo, el ensayo podría mejorar al incluir contrargumentos o limitaciones para equilibrar la perspectiva. La estructura argumentativa es clara y lógica, con cada párrafo apoyando la idea central, lo que facilita la comprensión y refuerza la coherencia. Por ejemplo, el autor vincula el uso de plataformas digitales con el desarrollo de habilidades colaborativas, lo que es una conexión directa y pertinente. En conclusión, los argumentos apoyan eficazmente la hipótesis, aunque una mayor profundidad en el análisis crítico enriquecería el ensayo.", "requiere_revision_humana": true}', 4, 0, true, '{evaluacion_critica,ensayo_persuasivo,nivel_evaluar,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (437, 94, 'open_ended', 'all', '{"pregunta": "Lee el siguiente ensayo persuasivo sobre el impacto de las redes sociales en la educación. Evalúa la validez de su hipótesis central considerando la calidad de las evidencias presentadas y la coherencia de los argumentos. ¿Crees que el autor logra persuadir eficazmente? Justifica tu respuesta con ejemplos concretos del texto.", "explicacion": "El docente debe guiar la evaluación considerando si el estudiante identifica correctamente la hipótesis central, analiza críticamente la evidencia (fuentes, datos, ejemplos) y examina la estructura argumentativa. Además, debe valorar si el estudiante puede expresar una opinión fundamentada sobre el nivel de persuasión logrado, soportando sus juicios con referencias específicas al texto.", "contexto_adicional": "Se proporcionará al estudiante un ensayo persuasivo breve sobre redes sociales y educación para su análisis.", "criterios_evaluacion": ["Evaluación de la calidad y relevancia de las evidencias utilizadas para sustentar la hipótesis (peso: 40%)", "Análisis de la coherencia y lógica interna de los argumentos presentados en el ensayo (peso: 35%)", "Capacidad para justificar la valoración personal sobre la efectividad persuasiva del texto, usando ejemplos concretos (peso: 25%)"]}', '{"puntos_clave": ["Identificación clara de la hipótesis central del ensayo", "Análisis crítico de la calidad y tipo de evidencias presentadas", "Evaluación de la coherencia lógica y estructura argumentativa", "Justificación explícita sobre la efectividad persuasiva, con ejemplos"], "respuesta_modelo": "El ensayo plantea que las redes sociales tienen un impacto negativo en la educación al distraer a los estudiantes y disminuir su rendimiento académico. La hipótesis es clara y relevante. Sin embargo, algunas evidencias presentadas son anecdóticas y carecen de datos estadísticos robustos, lo que debilita la fuerza del argumento. Por ejemplo, el autor menciona casos individuales pero no estudios amplios que respalden su afirmación. En cuanto a la coherencia, el ensayo mantiene un hilo lógico, aunque en algunos párrafos se repiten ideas sin profundizar. En conclusión, aunque el ensayo logra presentar un punto de vista plausible, la falta de evidencias sólidas afecta su capacidad persuasiva. Por lo tanto, no considero que el autor logre persuadir de manera eficaz en su totalidad.", "requiere_revision_humana": true}', 5, 0, true, '{evaluacion_critica,ensayo_persuasivo,pensamiento_critico,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (438, 95, 'open_ended', 'all', '{"pregunta": "Elige un tema de actualidad que te interese y redacta un ensayo persuasivo en el que presentes una hipótesis clara. Sustenta tu postura con evidencias pertinentes, organiza tus ideas de manera coherente y concluye reafirmando tu argumento principal. Asegúrate de incluir citas y referencias correctamente formateadas.", "explicacion": "Se evaluará que el estudiante sea capaz de formular una hipótesis clara y original, que seleccione y utilice evidencias relevantes para apoyar su postura, que organice sus ideas de manera lógica y coherente, y que concluya reafirmando su argumento. También se valorará el correcto uso de citas y referencias para respaldar sus fuentes, lo que demuestra rigor académico y respeto por la propiedad intelectual.", "contexto_adicional": "El ensayo debe abordar un tema actual, que pueda generar debate o reflexión en la sociedad chilena o mundial, y debe estar escrito en un formato formal adecuado para un trabajo académico de primero medio.", "criterios_evaluacion": ["Claridad y pertinencia de la hipótesis presentada (peso: 30%)", "Uso adecuado de evidencias y fuentes para sustentar el argumento (peso: 40%)", "Coherencia en la estructura del ensayo, incluyendo introducción, desarrollo y conclusión (peso: 30%)"]}', '{"puntos_clave": ["Presentación clara de una hipótesis relacionada con el tema escogido", "Incorporación de evidencias y datos relevantes que apoyen la postura", "Estructura coherente con introducción, desarrollo argumentativo y conclusión", "Uso correcto de citas y referencias bibliográficas"], "respuesta_modelo": "Tema elegido: El impacto de las redes sociales en la salud mental de los jóvenes.\n\nHipótesis: Las redes sociales tienen un efecto negativo significativo en la salud mental de los jóvenes debido a la exposición constante a contenidos que generan ansiedad y baja autoestima.\n\nDesarrollo: Numerosos estudios indican que el uso excesivo de redes sociales está asociado con mayores niveles de depresión y ansiedad en adolescentes. Por ejemplo, según un informe de la Organización Mundial de la Salud (2022), el 30% de los jóvenes que usan redes sociales más de tres horas diarias presentan síntomas de estrés elevado. Además, la constante comparación con imágenes idealizadas afecta la percepción personal.\n\nConclusión: En consecuencia, es fundamental promover un uso consciente y regulado de las redes sociales para proteger la salud mental de los jóvenes.\n\nReferencias:\n- Organización Mundial de la Salud. (2022). Informe sobre salud mental y redes sociales.\n- Pérez, J. (2021). \"Efectos psicológicos del uso digital en adolescentes.\" Revista Chilena de Psicología.", "requiere_revision_humana": true}', 4, 0, true, '{"ensayo persuasivo","primeros medios","pensamiento crítico","redacción académica"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (439, 95, 'open_ended', 'all', '{"pregunta": "Elige un tema de actualidad que te interese y redacta un ensayo persuasivo en el que presentes una hipótesis clara. Sustenta tu postura con evidencias relevantes y bien argumentadas, organiza coherentemente las ideas a lo largo del texto y concluye reafirmando tu afirmación. Además, incorpora citas y referencias correctamente formateadas según las normas académicas.", "explicacion": "El docente debe evaluar si el estudiante formula una hipótesis clara y específica, si utiliza evidencias pertinentes y argumentos lógicos para defender su postura, y si el ensayo está organizado de manera coherente, con una conclusión que sintetice la idea principal. También es importante verificar el uso correcto de citas y referencias según normas académicas.", "contexto_adicional": "El estudiante debe seleccionar un tema relevante y actual que le permita desarrollar una postura argumentativa clara y fundamentada, utilizando fuentes confiables para sustentar su hipótesis.", "criterios_evaluacion": ["Claridad y pertinencia de la hipótesis presentada en el ensayo (peso: 30%)", "Uso adecuado y coherente de evidencias y argumentos para sustentar la hipótesis (peso: 40%)", "Estructura coherente del texto, conclusión sólida y correcto uso de citas y referencias (peso: 30%)"]}', '{"puntos_clave": ["Presentación clara y específica de una hipótesis relacionada con un tema de actualidad", "Sustentación de la hipótesis con evidencias y argumentos relevantes y coherentes", "Estructura lógica del ensayo con introducción, desarrollo, conclusión y uso correcto de citas y referencias"], "respuesta_modelo": "Tema elegido: El impacto de las redes sociales en la salud mental de los adolescentes.\n\nHipótesis: Las redes sociales tienen un efecto negativo significativo en la salud mental de los adolescentes, aumentando los niveles de ansiedad y depresión.\n\nDesarrollo: Diversos estudios científicos muestran que el uso excesivo de redes sociales correlaciona con síntomas de ansiedad y depresión en jóvenes. Por ejemplo, según un informe de la Organización Mundial de la Salud (2022), el tiempo prolongado en plataformas digitales afecta la autoestima y la calidad del sueño. Además, testimonios y estadísticas locales indican un aumento en consultas psicológicas relacionadas con estas problemáticas.\n\nConclusión: En conclusión, los datos presentados respaldan la hipótesis de que las redes sociales pueden perjudicar la salud mental de los adolescentes, por lo que es imprescindible promover un uso responsable y estrategias de apoyo desde la comunidad educativa y familiar.\n\nReferencias:\n- Organización Mundial de la Salud. (2022). Informe sobre salud mental y uso de redes sociales.\n- Pérez, L. (2023). \"Redes sociales y bienestar adolescente\". Revista Psicología Contemporánea.", "requiere_revision_humana": true}', 5, 0, true, '{"ensayo persuasivo","pensamiento crítico",argumentación,primeromedia}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (440, 95, 'concept_map', 'all', '{"conceptos": ["Hipótesis", "Evidencia", "Coherencia temática", "Conclusión", "Citas y referencias"], "explicacion": "El mapa conceptual debe mostrar que la hipótesis es la afirmación central que se sostiene mediante evidencias. La coherencia temática asegura que las evidencias y argumentos se conecten lógicamente, mientras que la conclusión sintetiza y reafirma la hipótesis. Las citas y referencias sustentan las evidencias y aportan credibilidad al texto.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la elaboración de un ensayo persuasivo?"}', '{"conceptos_centrales": ["Hipótesis", "Evidencia"], "relaciones_esperadas": [{"origen": "Hipótesis", "destino": "Evidencia", "relacion": "se sostiene con"}, {"origen": "Evidencia", "destino": "Citas y referencias", "relacion": "requiere"}, {"origen": "Coherencia temática", "destino": "relación lógica entre Hipótesis y Evidencia", "relacion": "asegura"}, {"origen": "Conclusión", "destino": "Hipótesis", "relacion": "resume y reafirma"}, {"origen": "Citas y referencias", "destino": "Evidencia", "relacion": "aportan credibilidad a"}], "requiere_revision_humana": true}', 4, 0, true, '{"ensayo persuasivo","mapa conceptual","lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (482, 104, 'multiple_choice', 'all', '{"opciones": {"A": "María dijo que estaba estudiando para el examen.", "B": "María dijo que está estudiando para el examen.", "C": "María dijo que estudiaba para el examen.", "D": "María dijo que estudiar para el examen."}, "pregunta": "¿Cuál es la forma correcta de transformar la oración en estilo directo al estilo indirecto?\n\nDirecto: María dijo: \"Estoy estudiando para el examen\".", "explicacion": "La respuesta correcta es A porque al transformar un enunciado en estilo directo al estilo indirecto en pasado, el presente progresivo \"estoy estudiando\" cambia a imperfecto progresivo \"estaba estudiando\" para mantener la concordancia temporal. La opción B mantiene el presente, lo cual es incorrecto porque el verbo principal está en pasado. La opción C usa el imperfecto simple \"estudiaba\" que cambia el aspecto progresivo original y puede alterar el sentido. La opción D no es una construcción gramatical correcta."}', '{"respuesta_correcta": "A"}', 3, 0, true, '{"estilo indirecto","tiempos verbales","transformación de oraciones"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (441, 95, 'concept_map', 'all', '{"conceptos": ["Hipótesis", "Evidencias", "Coherencia temática", "Conclusión", "Citas y referencias"], "explicacion": "Se espera que el estudiante organice los conceptos mostrando que la hipótesis es la afirmación central que debe ser sustentada por evidencias pertinentes. La coherencia temática asegura que todas las partes del ensayo estén conectadas y apoyen la hipótesis. Las citas y referencias se utilizan para respaldar las evidencias y dar credibilidad, mientras que la conclusión sintetiza y reafirma la hipótesis basándose en las evidencias presentadas.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la elaboración de un ensayo persuasivo?"}', '{"conceptos_centrales": ["Hipótesis", "Evidencias"], "relaciones_esperadas": [{"origen": "Hipótesis", "destino": "Evidencias", "relacion": "es sustentada por"}, {"origen": "Evidencias", "destino": "Citas y referencias", "relacion": "son respaldadas por"}, {"origen": "Coherencia temática", "destino": "Hipótesis", "relacion": "asegura conexión entre"}, {"origen": "Coherencia temática", "destino": "Evidencias", "relacion": "asegura conexión entre"}, {"origen": "Conclusión", "destino": "Hipótesis", "relacion": "reafirma"}, {"origen": "Conclusión", "destino": "Evidencias", "relacion": "resume"}], "requiere_revision_humana": true}', 5, 0, true, '{"ensayo persuasivo","mapa conceptual",creación,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (442, 95, 'open_ended', 'all', '{"pregunta": "Elige un tema de actualidad que te resulte relevante y redacta un ensayo persuasivo en el que presentes una hipótesis clara relacionada con ese tema. Debes sustentar tu postura con evidencias pertinentes, mantener la coherencia temática a lo largo del texto, incluir una conclusión sólida y utilizar correctamente citas y referencias bibliográficas. ¿Cómo construyes tu argumento para persuadir efectivamente a tu lector?", "explicacion": "El docente debe evaluar si la hipótesis es clara y relevante, si las evidencias apoyan efectivamente la postura, y si el ensayo está bien organizado, con una conclusión que sintetice el argumento y un uso correcto de citas según normas básicas. Se recomienda valorar también la originalidad y la capacidad crítica del estudiante.", "contexto_adicional": "El ensayo debe abordar un tema actual que sea significativo para el estudiante y su entorno, empleando un lenguaje formal y recursos persuasivos propios del género.", "criterios_evaluacion": ["Claridad y pertinencia de la hipótesis presentada (peso: 30%)", "Uso adecuado y coherente de evidencias y referencias para sustentar el argumento (peso: 40%)", "Estructura del ensayo: coherencia temática, conclusión sólida y correcto uso de citas y referencias (peso: 30%)"]}', '{"puntos_clave": ["Presentación clara y específica de la hipótesis", "Incorporación de evidencias relevantes y bien fundamentadas", "Estructura coherente del ensayo con conclusión que refuerza la tesis y uso adecuado de citas"], "respuesta_modelo": "Tema: El impacto de las redes sociales en la salud mental de los adolescentes.\n\nHipótesis: El uso excesivo de redes sociales afecta negativamente la salud mental de los adolescentes.\n\nDesarrollo: Se presentan estudios recientes que muestran correlación entre tiempo en redes y aumento de ansiedad y depresión. Se argumenta cómo la comparación social y el ciberacoso contribuyen a este impacto. Se incluyen citas de expertos en psicología y datos estadísticos.\n\nConclusión: Se reafirma la hipótesis y se sugiere la importancia de promover un uso responsable y regulado de redes para proteger la salud mental juvenil.\n\nReferencias: Se citan artículos académicos y fuentes confiables siguiendo normas básicas de citación.", "requiere_revision_humana": true}', 5, 0, true, '{"ensayo persuasivo","pensamiento crítico","primer año medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (443, 96, 'multiple_choice', 'all', '{"opciones": {"A": "Elegir un tema sin considerar a quién va dirigido el texto", "B": "Organizar las ideas y seleccionar un vocabulario adecuado al destinatario", "C": "Copiar un texto ya escrito para ahorrar tiempo", "D": "Escribir primero y pensar en el propósito después"}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para planificar y escribir un texto considerando el contexto, el destinatario y el propósito?", "explicacion": "La respuesta correcta es B porque planificar un texto implica organizar las ideas y adecuar el vocabulario según el destinatario y el propósito. La opción A es incorrecta porque no considera al destinatario, fundamental para la planificación. La opción C es un error conceptual relacionado con la falta de originalidad y no es parte del proceso de planificación. La opción D representa un enfoque desordenado y no planificado, contrario a lo que se busca en la planificación."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{planificación,vocabulario,destinatario,contexto,escritura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (444, 96, 'multiple_choice', 'all', '{"opciones": {"A": "Usar siempre un lenguaje formal sin importar el público.", "B": "Recopilar información e ideas y organizarlas antes de escribir.", "C": "Escribir el texto directamente sin planificar para ser más creativo.", "D": "Copiar textos de otros autores para asegurarse de que sea correcto."}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para planificar y escribir un texto adecuadamente según el contexto, destinatario y propósito?", "explicacion": "La opción B es correcta porque planificar un texto implica recopilar información e ideas y organizarlas antes de escribir, considerando el contexto, destinatario y propósito. La opción A es incorrecta porque el registro debe adecuarse al público, no siempre ser formal. La opción C confunde la creatividad con la falta de planificación, lo cual puede afectar la coherencia del texto. La opción D es errónea y va en contra de la originalidad y el respeto por la autoría."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{planificación,escritura,contexto,registro,lenguaje}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (445, 96, 'true_false', 'all', '{"afirmacion": "Antes de escribir un texto, es importante recopilar información y organizarla para adecuar el contenido al contexto, destinatario y propósito.", "explicacion": "Esta afirmación es verdadera porque planificar un texto implica reunir ideas e información relevante y ordenarlas, lo que permite que el texto final sea coherente y adecuado según el contexto, el destinatario y el propósito comunicativo."}', '{"es_verdadero": true}', 1, 0, true, '{planificación,escritura,contexto,destinatario}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (446, 96, 'fill_blanks', 'all', '{"texto": "Antes de escribir un texto, es fundamental recopilar [BLANK_1] e ideas, y luego organizar esta información considerando el [BLANK_2], el destinatario y el propósito del mensaje.", "pistas": ["Información y pensamientos que ayudarán a construir el texto", "El lugar o situación donde se desarrolla la comunicación"], "explicacion": "Para planificar y escribir un texto de manera efectiva, es necesario primero recopilar información e ideas, para luego organizarlas. Además, el contexto en que se escribe influye en cómo se debe desarrollar el texto, junto con la consideración del destinatario y el propósito."}', '{"respuestas_correctas": {"BLANK_1": ["información", "datos", "ideas"], "BLANK_2": ["contexto", "situación"]}}', 2, 0, true, '{planificación,escritura,contexto,"recopilación de información"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (471, 101, 'open_ended', 'all', '{"pregunta": "Imagina que eres el encargado de crear un boletín informativo digital para tu comunidad escolar sobre un tema de interés actual. Describe cómo planificarías, organizarías y redactarías este texto considerando el contexto, el destinatario y el propósito. Además, explica qué herramientas digitales utilizarías para revisar y editar tu trabajo antes de entregarlo.", "explicacion": "El docente debe evaluar cómo el estudiante articula su proceso de planificación, si demuestra comprensión del público y finalidad del texto, y si aplica un registro adecuado. Además, se debe valorar la capacidad de usar herramientas digitales para mejorar el texto, evidenciando un proceso de revisión y edición reflexivo.", "contexto_adicional": "El estudiante debe considerar que el boletín será leído por miembros de la comunidad escolar, incluyendo alumnos, profesores y apoderados, y que el tema debe ser de interés general y actualidad.", "criterios_evaluacion": ["Planificación y organización de ideas coherentes y relevantes según el contexto, destinatario y propósito (peso: 30%)", "Adecuación del registro lingüístico y uso apropiado del vocabulario para el público y objetivo del texto (peso: 40%)", "Uso efectivo de herramientas digitales para la revisión, reescritura y edición del texto final (peso: 30%)"]}', '{"puntos_clave": ["Identificación y selección de un tema pertinente para la comunidad escolar", "Organización clara y lógica de la información con secciones definidas", "Adecuación del registro y vocabulario según el público y propósito", "Uso de herramientas digitales para revisión ortográfica y gramatical", "Incorporación de retroalimentación y edición final del texto"], "respuesta_modelo": "Para crear el boletín informativo digital, primero identificaría un tema actual relevante para la comunidad escolar, como la importancia del cuidado ambiental. Luego, recopilaría información confiable y organizaría las ideas en secciones claras (introducción, desarrollo, conclusión). Considerando que el público incluye estudiantes, profesores y apoderados, usaría un registro formal pero accesible, evitando jergas y utilizando un vocabulario claro y preciso. Para la redacción, emplearía un procesador de texto como Google Docs, que permite escribir, revisar y compartir fácilmente. Usaría sus herramientas de revisión ortográfica y gramática para corregir errores, y solicitaría retroalimentación de un compañero a través de comentarios. Finalmente, ajustaría el texto según las sugerencias recibidas para entregar un boletín claro, atractivo y bien editado que cumpla con su propósito informativo.", "requiere_revision_humana": true}', 5, 0, true, '{planificación,"producción textual","herramientas digitales","registro lingüístico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (447, 97, 'multiple_choice', 'all', '{"opciones": {"A": "Usar un vocabulario coloquial y expresiones informales para que el texto sea más cercano.", "B": "Emplear un vocabulario formal, respetuoso y un registro adecuado al contexto y destinatario.", "C": "Utilizar un lenguaje técnico y muy especializado sin importar el conocimiento del destinatario.", "D": "Incluir modismos y jerga juvenil para hacer el texto más atractivo y moderno."}, "pregunta": "¿Cuál es la mejor manera de adecuar el vocabulario y el registro al escribir una carta formal dirigida a una autoridad escolar?", "explicacion": "La opción B es correcta porque al escribir una carta formal a una autoridad escolar, es fundamental usar un vocabulario formal y respetuoso, ajustando el registro al contexto y al destinatario. La opción A es incorrecta porque el lenguaje coloquial no es apropiado en cartas formales. La opción C es errónea porque el uso excesivo de lenguaje técnico puede dificultar la comprensión si el destinatario no está familiarizado con ese vocabulario. La opción D es incorrecta porque los modismos y jerga juvenil no son adecuados en contextos formales."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{vocabulario,registro,"género discursivo",contexto,destinatario}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (448, 97, 'multiple_choice', 'all', '{"opciones": {"A": "Usar un lenguaje muy formal y palabras técnicas para demostrar conocimiento.", "B": "Emplear un vocabulario cotidiano, cercano y un registro informal para conectar con el público.", "C": "Utilizar un vocabulario muy simple y frases cortas, sin importar el tema o el contexto.", "D": "Incluir muchos modismos y jerga local, aunque el destinatario sea un público de otra región."}, "pregunta": "¿Cuál de las siguientes opciones es la mejor manera de adecuar el vocabulario y el registro al escribir un texto dirigido a un público juvenil en redes sociales?", "explicacion": "La opción B es correcta porque para conectar con un público juvenil en redes sociales es importante usar un vocabulario cotidiano y un registro informal, adecuado al contexto y destinatario. La opción A es incorrecta porque un lenguaje muy formal puede crear distancia con el público joven. La opción C es errónea porque un vocabulario simple no siempre es adecuado si el tema requiere precisión o profundidad. La opción D falla porque el uso excesivo de modismos locales puede dificultar la comprensión si el público es diverso regionalmente."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{vocabulario,registro,"género discursivo",contexto,destinatario}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (449, 97, 'true_false', 'all', '{"afirmacion": "Para adecuar correctamente un texto al destinatario, es necesario modificar el vocabulario y el registro según el género discursivo y el contexto en que se produce.", "explicacion": "Esta afirmación es verdadera porque el vocabulario y el registro deben adaptarse al tipo de texto (género discursivo), al contexto en que se comunica y al destinatario para que el mensaje sea claro, pertinente y efectivo."}', '{"es_verdadero": true}', 2, 0, true, '{vocabulario,registro,"género discursivo",contexto,destinatario}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (450, 97, 'drag_drop_matching', 'all', '{"explicacion": "Cada emparejamiento relaciona un concepto con su definición o un ejemplo claro que permite entender cómo adecuar el lenguaje según el género discursivo, el contexto y el destinatario.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente relacionado con la adecuación del vocabulario, estructura y registro al género discursivo, contexto y destinatario.", "columna_derecha": ["Uso de palabras y expresiones propias de la conversación cotidiana, con tono relajado.", "Texto que se organiza con saludo, cuerpo y despedida usando lenguaje respetuoso.", "Texto breve y espontáneo con expresiones informales y abreviaturas.", "Uso de un lenguaje serio, respetuoso y sin modismos.", "Organización lógica y coherente de ideas para facilitar la comprensión.", "Ejemplo de texto escrito para un contexto formal o profesional."], "columna_izquierda": ["Registro formal", "Vocabulario coloquial", "Carta formal", "Mensaje de texto a un amigo", "Estructura clara y ordenada", "Correo electrónico informal"]}', '{"emparejamientos_correctos": {"Carta formal": "Texto que se organiza con saludo, cuerpo y despedida usando lenguaje respetuoso.", "Registro formal": "Uso de un lenguaje serio, respetuoso y sin modismos.", "Vocabulario coloquial": "Uso de palabras y expresiones propias de la conversación cotidiana, con tono relajado.", "Estructura clara y ordenada": "Organización lógica y coherente de ideas para facilitar la comprensión.", "Mensaje de texto a un amigo": "Texto breve y espontáneo con expresiones informales y abreviaturas.", "Correo electrónico informal": "Ejemplo de texto escrito para un contexto formal o profesional."}}', 2, 0, true, '{vocabulario,registro,"género discursivo",contexto,destinatario}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (451, 97, 'sequencing', 'all', '{"explicacion": "Primero se debe identificar el género discursivo para entender las características del texto. Luego, se organiza la información y las ideas para tener una base clara. Después, se considera el propósito para determinar el registro apropiado. Luego, se selecciona el vocabulario adecuado para el destinatario y el contexto. Finalmente, se revisa y ajusta la estructura para que el texto sea coherente y efectivo.", "instruccion": "Ordena los siguientes pasos para adecuar el vocabulario y el registro al escribir un texto según el género discursivo, contexto y destinatario.", "elementos_desordenados": ["Seleccionar el vocabulario adecuado según el destinatario y el contexto.", "Identificar el género discursivo del texto que se va a escribir.", "Revisar y ajustar la estructura para que sea coherente con el género.", "Considerar el propósito del texto para elegir el registro adecuado.", "Organizar las ideas antes de comenzar a escribir."]}', '{"orden_correcto": ["Identificar el género discursivo del texto que se va a escribir.", "Organizar las ideas antes de comenzar a escribir.", "Considerar el propósito del texto para elegir el registro adecuado.", "Seleccionar el vocabulario adecuado según el destinatario y el contexto.", "Revisar y ajustar la estructura para que sea coherente con el género."]}', 3, 0, true, '{"Lengua y Literatura","Primer Medio",Escritura,Vocabulario,Registro}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (473, 102, 'multiple_choice', 'all', '{"opciones": {"A": "María dijo que estaba cansada.", "B": "María dijo: \"Estoy cansada\".", "C": "María había dicho que se sentía cansada.", "D": "María comentó que se sentía cansada."}, "pregunta": "¿Cuál de las siguientes oraciones es un ejemplo de estilo directo?", "explicacion": "La opción B es correcta porque presenta las palabras exactas que dijo María, indicadas entre comillas, lo que caracteriza al estilo directo. Las opciones A, C y D están en estilo indirecto, ya que parafrasean o relatan lo que dijo María sin usar sus palabras textuales."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"estilo directo","estilo indirecto","lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (452, 98, 'multiple_choice', 'all', '{"opciones": {"A": "Primero, el cambio climático afecta a todos. Además, muchas personas no lo notan. Finalmente, es importante actuar.", "B": "En primer lugar, el cambio climático afecta a todos; por lo tanto, es urgente tomar medidas. Sin embargo, algunas personas no lo reconocen, por eso debemos informar más.", "C": "El cambio climático es un problema. Pero muchas personas no creen en él. También, hay soluciones.", "D": "Cambio climático afecta. Por eso, muchas personas no lo ven. Entonces, debemos actuar."}, "pregunta": "¿Cuál de las siguientes opciones muestra el mejor uso de conectores para organizar ideas y lograr coherencia en un texto argumentativo?", "explicacion": "La opción B es correcta porque utiliza conectores apropiados (''en primer lugar'', ''por lo tanto'', ''sin embargo'', ''por eso'') que organizan las ideas claramente y establecen relaciones lógicas entre ellas, lo que facilita la coherencia y cohesión del texto. La opción A presenta conectores adecuados pero la idea ''muchas personas no lo notan'' no se conecta claramente con las demás; la opción C usa conectores muy simples y poco precisos, generando falta de fluidez; la opción D presenta oraciones fragmentadas y conectores mal empleados que dificultan la comprensión y la fluidez del texto."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"organización de ideas",conectores,coherencia,cohesión,"texto argumentativo"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (453, 98, 'multiple_choice', 'all', '{"opciones": {"A": "Primero, fui al supermercado. Luego, terminé mi tarea. Además, me gusta el helado.", "B": "Primero, fui al supermercado. Luego, compré frutas y verduras. Finalmente, preparé una ensalada.", "C": "Fui al supermercado, entonces terminé la tarea, y por eso me gusta el helado.", "D": "Me gusta el helado, también fui al supermercado, y terminé la tarea."}, "pregunta": "¿Cuál de las siguientes opciones muestra un uso adecuado de conectores para organizar ideas de manera coherente en un texto?", "explicacion": "La opción B es correcta porque utiliza conectores temporales (''Primero'', ''Luego'', ''Finalmente'') que organizan las ideas en una secuencia lógica y coherente, facilitando la comprensión del texto. La opción A mezcla ideas no relacionadas (gustos personales) sin conexión clara. La opción C usa conectores inapropiados que no mantienen la coherencia entre las ideas presentadas. La opción D presenta las ideas sin un orden lógico ni conectores que permitan una buena cohesión."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"organización de ideas",conectores,"coherencia textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (454, 98, 'drag_drop_matching', 'all', '{"explicacion": "Los conectores causales indican causa y efecto (Porque, ya que). La organización cronológica presenta los eventos en orden temporal. Los conectores de contraste muestran oposición o diferencia (Sin embargo, pero). Los mapas mentales son esquemas gráficos para organizar ideas. Los conectores de adición añaden información (Además, también). La lista desordenada es un distractor que no corresponde con técnicas específicas de organización o conectores.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Porque, ya que", "Secuencia de eventos en orden de tiempo", "Sin embargo, pero", "Representación gráfica de ideas relacionadas", "Además, también", "Elemento que no tiene relación directa"], "columna_izquierda": ["Conector causal", "Organización cronológica", "Conector de contraste", "Mapa mental", "Conector de adición", "Lista desordenada"]}', '{"emparejamientos_correctos": {"Mapa mental": "Representación gráfica de ideas relacionadas", "Conector causal": "Porque, ya que", "Conector de adición": "Además, también", "Conector de contraste": "Sin embargo, pero", "Organización cronológica": "Secuencia de eventos en orden de tiempo"}}', 3, 0, true, '{"organización de ideas","conectores textuales","escritura coherente"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (455, 98, 'sequencing', 'all', '{"explicacion": "El orden correcto comienza con recopilar información para tener contenido, luego organizar esas ideas en un esquema para estructurar el texto. Después, se escribe el borrador aplicando conectores adecuados para cohesión, y finalmente se revisa y edita para mejorar la coherencia y adecuar el vocabulario.", "instruccion": "Ordena los siguientes pasos para organizar y redactar un texto coherente y cohesionado.", "elementos_desordenados": ["Escribir el borrador utilizando conectores adecuados", "Recopilar información e ideas sobre el tema", "Revisar y editar el texto para mejorar la coherencia y el vocabulario", "Organizar las ideas en un esquema o mapa conceptual"]}', '{"orden_correcto": ["Recopilar información e ideas sobre el tema", "Organizar las ideas en un esquema o mapa conceptual", "Escribir el borrador utilizando conectores adecuados", "Revisar y editar el texto para mejorar la coherencia y el vocabulario"]}', 3, 0, true, '{"organización de ideas",redacción,coherencia,cohesión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (456, 98, 'open_ended', 'all', '{"pregunta": "Imagina que debes escribir una carta formal para solicitar la realización de una actividad extracurricular en tu colegio. Organiza tus ideas y redacta un texto coherente y cohesionado, utilizando conectores adecuados para justificar la importancia de esta actividad y convencer al destinatario.", "explicacion": "El docente debe valorar cómo el estudiante ordena sus ideas de forma lógica y clara, cómo emplea conectores para unir sus argumentos y la adecuación del tono formal y vocabulario para una carta dirigida a una autoridad escolar.", "contexto_adicional": "El destinatario es el director del colegio y el propósito es solicitar permiso para realizar una actividad extracurricular.", "criterios_evaluacion": ["Organización de ideas: claridad en la secuencia y estructura del texto (peso: 30%)", "Uso adecuado de conectores para lograr coherencia y cohesión en la redacción (peso: 40%)", "Adecuación del registro formal y vocabulario pertinente al contexto de la carta (peso: 30%)"]}', '{"puntos_clave": ["Introducción clara del propósito de la carta", "Uso de conectores como ''en primer lugar'', ''además'', ''por lo tanto'' para conectar ideas", "Registro formal y vocabulario adecuado para una carta dirigida a una autoridad"], "respuesta_modelo": "Estimado Sr. Director:\n\nMe dirijo a usted para solicitar la autorización para realizar una actividad extracurricular que consiste en un taller de teatro para los estudiantes de primero medio. En primer lugar, esta actividad fomentaría el desarrollo de habilidades comunicativas y la creatividad entre los alumnos. Además, el taller contribuiría a fortalecer el trabajo en equipo y la autoestima, aspectos fundamentales en la formación integral de los jóvenes. Por lo tanto, considero que esta iniciativa aportaría significativamente al ambiente escolar y al crecimiento personal de los estudiantes.\n\nAgradezco mucho su atención y quedo a la espera de su respuesta.\n\nAtentamente,\n[Nombre del estudiante]", "requiere_revision_humana": true}', 3, 0, true, '{redacción,coherencia,conectores,"escritura formal"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (505, 108, 'true_false', 'all', '{"afirmacion": "La metonimia es un recurso literario que consiste en nombrar una cosa con el nombre de otra con la que tiene una relación de contigüidad o proximidad.", "explicacion": "Esta afirmación es verdadera porque la metonimia se basa en sustituir el término propio por otro con el que mantiene una relación cercana, como el autor por su obra o el continente por el contenido."}', '{"es_verdadero": true}', 1, 0, true, '{metonimia,"recursos literarios","correferencia léxica"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (457, 99, 'compare_contrast', 'all', '{"conceptos": ["Uso correcto de pronombres", "Uso incorrecto de pronombres"], "criterios": ["Concordancia con el sujeto", "Posición en la oración", "Adecuación al registro"], "explicacion": "El uso correcto de pronombres implica que estos concuerdan en género y número con el sujeto al que se refieren, se colocan en la posición adecuada dentro de la oración y son apropiados para el registro del texto. En cambio, el uso incorrecto de pronombres se caracteriza por falta de concordancia, posiciones erróneas que afectan la claridad y un vocabulario inapropiado para el contexto comunicativo.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Uso correcto de pronombres": {"Adecuación al registro": "Apropiado para el contexto y destinatario", "Posición en la oración": "Colocado en posición gramatical adecuada", "Concordancia con el sujeto": "Coincide en género y número"}, "Uso incorrecto de pronombres": {"Adecuación al registro": "Inapropiado para el contexto o destinatario", "Posición en la oración": "Colocado en posición incorrecta que genera confusión", "Concordancia con el sujeto": "No coincide en género o número"}}}', 3, 0, true, '{pronombres,concordancia,registro,análisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (458, 99, 'compare_contrast', 'all', '{"conceptos": ["Conjugación verbal correcta", "Conjugación verbal incorrecta"], "criterios": ["Uso del tiempo verbal", "Concordancia con el sujeto", "Adecuación al contexto"], "explicacion": "La conjugación verbal correcta utiliza adecuadamente el tiempo verbal, mantiene la concordancia con el sujeto y es coherente con el contexto comunicativo. En cambio, la conjugación verbal incorrecta presenta errores en alguno o todos estos aspectos, afectando la claridad y corrección del texto.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Conjugación verbal correcta": {"Uso del tiempo verbal": "El tiempo verbal corresponde a la acción que se quiere expresar (pasado, presente, futuro).", "Adecuación al contexto": "La conjugación es apropiada para el tipo de texto y situación comunicativa.", "Concordancia con el sujeto": "El verbo concuerda en número y persona con el sujeto (yo hablo, ellos hablan)."}, "Conjugación verbal incorrecta": {"Uso del tiempo verbal": "El tiempo verbal no corresponde a la acción o es inconsistente (ejemplo: usar presente para una acción pasada).", "Adecuación al contexto": "La conjugación no es coherente con el contexto o registro del texto.", "Concordancia con el sujeto": "El verbo no concuerda en número o persona con el sujeto (yo hablan, ella cantamos)."}}}', 4, 0, true, '{conjugación,gramática,comparación,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (459, 99, 'open_ended', 'all', '{"pregunta": "Analiza el siguiente texto y detecta los errores relacionados con el uso de pronombres, conjugaciones y concordancia. Luego, explica por qué son incorrectos y propone una versión corregida del fragmento.", "explicacion": "El docente debe revisar que el estudiante identifique correctamente los errores, los explique con fundamentos gramaticales adecuados y proponga una corrección que mantenga la coherencia del texto, adecuando el registro y respetando el contexto.", "contexto_adicional": "Se entrega un fragmento de texto con errores intencionales en pronombres, conjugaciones y concordancia para que el estudiante realice el análisis solicitado.", "criterios_evaluacion": ["Identificación precisa y completa de errores en pronombres, conjugaciones y concordancia (peso: 40%)", "Explicación clara y fundamentada de por qué cada error es incorrecto según las reglas gramaticales (peso: 35%)", "Propuesta coherente y correcta de la versión corregida del texto, que respete contexto y registro (peso: 25%)"]}', '{"puntos_clave": ["Detección correcta de errores en pronombres, conjugaciones y concordancia", "Explicación gramatical fundamentada de cada error", "Corrección apropiada que respeta el contexto y el registro"], "respuesta_modelo": "En el texto, se identifican los siguientes errores: uso incorrecto del pronombre ''le'' en lugar de ''lo'' para objeto directo masculino, conjugación inadecuada del verbo en pretérito imperfecto, y falta de concordancia de número entre sujeto y verbo. Por ejemplo, ''le vi ayer'' debería ser ''lo vi ayer'' porque ''lo'' es el pronombre correcto para objeto directo masculino. Además, el verbo ''hablaba'' fue conjugado en singular cuando el sujeto es plural, por lo que debe ser ''hablaban''. La versión corregida del fragmento es: ''Los estudiantes lo escuchaban atentamente mientras el profesor explicaba la lección.''", "requiere_revision_humana": true}', 4, 0, true, '{pronombres,conjugaciones,concordancia,"análisis gramatical","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (460, 99, 'concept_map', 'all', '{"conceptos": ["Pronombres correctos", "Pronombres incorrectos", "Conjugaciones verbales", "Concordancia gramatical", "Errores comunes"], "explicacion": "El mapa debe mostrar cómo los pronombres correctos e incorrectos afectan la concordancia gramatical y el uso adecuado de conjugaciones verbales, además de identificar errores comunes que surgen al no aplicar estas reglas correctamente.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la corrección gramatical en textos escritos?"}', '{"conceptos_centrales": ["Pronombres correctos", "Concordancia gramatical"], "relaciones_esperadas": [{"origen": "Pronombres incorrectos", "destino": "Errores comunes", "relacion": "provocan"}, {"origen": "Conjugaciones verbales", "destino": "Concordancia gramatical", "relacion": "deben respetar"}, {"origen": "Pronombres correctos", "destino": "Concordancia gramatical", "relacion": "favorecen"}, {"origen": "Errores comunes", "destino": "Pronombres incorrectos", "relacion": "se originan en"}, {"origen": "Errores comunes", "destino": "Conjugaciones verbales", "relacion": "se originan en"}], "requiere_revision_humana": true}', 3, 0, true, '{gramática,pronombres,conjugación,concordancia,"mapa conceptual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (461, 99, 'concept_map', 'all', '{"conceptos": ["Pronombres correctos", "Pronombres incorrectos", "Conjugaciones verbales", "Concordancia gramatical", "Errores comunes en textos escritos"], "explicacion": "Se espera que el estudiante identifique cómo los pronombres correctos y las conjugaciones verbales adecuadas contribuyen a una correcta concordancia gramatical, mientras que los pronombres incorrectos y errores en conjugaciones generan errores comunes en textos escritos. El mapa debe mostrar las conexiones entre el correcto uso de estos elementos y los errores que se producen cuando no se aplican adecuadamente.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del uso correcto del lenguaje en textos escritos?"}', '{"conceptos_centrales": ["Concordancia gramatical", "Errores comunes en textos escritos"], "relaciones_esperadas": [{"origen": "Pronombres correctos", "destino": "Concordancia gramatical", "relacion": "favorecen"}, {"origen": "Conjugaciones verbales", "destino": "Concordancia gramatical", "relacion": "contribuyen a"}, {"origen": "Pronombres incorrectos", "destino": "Errores comunes en textos escritos", "relacion": "provocan"}, {"origen": "Errores comunes en textos escritos", "destino": "Pronombres incorrectos", "relacion": "incluyen"}, {"origen": "Errores comunes en textos escritos", "destino": "Conjugaciones verbales", "relacion": "incluyen"}], "requiere_revision_humana": true}', 4, 0, true, '{"lengua y literatura",gramática,pronombres,conjugación,"mapa conceptual","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (462, 100, 'criteria_evaluation', 'all', '{"rubrica": {"Evaluación de la presentación y formato": {"Excelente": "Analiza críticamente la presentación y formato del texto, justificando las modificaciones y su impacto en la claridad y legibilidad.", "Satisfactorio": "Reconoce algunas mejoras en la presentación y formato, pero con justificaciones limitadas.", "Necesita mejorar": "No reconoce ni justifica cambios en la presentación ni el formato del texto."}, "Justificación de correcciones ortográficas": {"Excelente": "Justifica claramente cada corrección ortográfica realizada, explicando por qué es necesaria y cómo mejora el texto.", "Satisfactorio": "Justifica la mayoría de las correcciones ortográficas, aunque algunas explicaciones son superficiales o incompletas.", "Necesita mejorar": "Justifica pocas correcciones ortográficas o las justificaciones no son claras ni adecuadas."}, "Argumentación sobre correcciones gramaticales": {"Excelente": "Explica con precisión las correcciones gramaticales, señalando errores específicos y el efecto de las correcciones en la coherencia del texto.", "Satisfactorio": "Ofrece argumentos generales sobre las correcciones gramaticales, pero sin detalle o ejemplos claros.", "Necesita mejorar": "No justifica adecuadamente las correcciones gramaticales o las explicaciones son confusas."}}, "explicacion": "Se espera que el estudiante analice detalladamente las correcciones, explicando por qué son necesarias y cómo contribuyen a mejorar el texto en ortografía, gramática y presentación, demostrando capacidad crítica y argumentativa.", "instruccion": "Evalúa las correcciones realizadas en el texto según los criterios de la rúbrica y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Se presenta un texto argumentativo de un estudiante de Primero Medio que ha escrito sobre la importancia del cuidado ambiental. El texto contiene varias correcciones realizadas en términos de ortografía, gramática y presentación, tanto en el propio texto original como en la versión corregida del mismo."}', '{"evaluacion_modelo": {"Evaluación de la presentación y formato": "Satisfactorio", "Justificación de correcciones ortográficas": "Excelente", "Argumentación sobre correcciones gramaticales": "Satisfactorio"}, "justificacion_modelo": "La justificación de las correcciones ortográficas es clara y detallada, mostrando comprensión de las reglas implicadas. La argumentación sobre correcciones gramaticales es adecuada pero carece de ejemplos específicos y profundidad. La evaluación de presentación es reconocida pero con un análisis limitado, sin detallar plenamente el impacto en la legibilidad.", "requiere_revision_humana": true}', 4, 0, true, '{argumentación,"corrección de textos","evaluación crítica","Lengua y Literatura","Primer Año Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (463, 100, 'criteria_evaluation', 'all', '{"rubrica": {"Corrección gramatical": {"Excelente": "Analiza y argumenta con claridad todas las correcciones gramaticales, mostrando comprensión del impacto en la coherencia y cohesión del texto.", "Satisfactorio": "Identifica las correcciones gramaticales principales y justifica su relevancia, aunque con explicaciones poco profundas o parciales.", "Necesita mejorar": "No logra identificar ni justificar las correcciones gramaticales de manera adecuada."}, "Corrección ortográfica": {"Excelente": "Identifica y justifica con precisión todas las correcciones ortográficas realizadas, explicando por qué cada cambio mejora el texto.", "Satisfactorio": "Reconoce la mayoría de las correcciones ortográficas y ofrece justificaciones adecuadas, aunque algunas pueden ser superficiales o incompletas.", "Necesita mejorar": "No identifica ni justifica adecuadamente las correcciones ortográficas, o las explicaciones son confusas o incorrectas."}, "Presentación y estructura del texto": {"Excelente": "Evalúa con detalle las mejoras en la presentación y estructura, justificando cómo estas contribuyen a la claridad y al propósito comunicativo.", "Satisfactorio": "Reconoce mejoras en la presentación y estructura, pero las justificaciones son generales o poco desarrolladas.", "Necesita mejorar": "No reconoce ni justifica adecuadamente las mejoras en presentación y estructura del texto."}}, "explicacion": "Se espera que el estudiante analice detalladamente las correcciones realizadas, explicando con argumentos claros y fundamentados por qué cada corrección ortográfica, gramatical y de presentación mejora el texto original, demostrando comprensión de las normas lingüísticas y del propósito comunicativo.", "instruccion": "Evalúa la versión corregida del texto según los criterios de la rúbrica y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Se presenta un texto narrativo escrito por un estudiante de Primero Medio que contiene varios errores ortográficos, problemas gramaticales y falta de coherencia en la presentación. Además, se incluye una versión corregida del mismo texto, en la que se han realizado ajustes para mejorar estos aspectos."}', '{"evaluacion_modelo": {"Corrección gramatical": "Satisfactorio", "Corrección ortográfica": "Excelente", "Presentación y estructura del texto": "Satisfactorio"}, "justificacion_modelo": "El estudiante identifica correctamente todas las correcciones ortográficas y explica con claridad por qué son necesarias, demostrando dominio de la ortografía. En cuanto a la gramática, reconoce la mayoría de las correcciones pero sus justificaciones son menos detalladas. Respecto a la presentación, señala las mejoras principales, aunque sus argumentos son generales y podrían profundizarse más.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Evaluación de textos",Ortografía,Gramática,Presentación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (464, 100, 'criteria_evaluation', 'all', '{"rubrica": {"Adecuación del registro y vocabulario": {"Excelente": "Reconoce y argumenta cómo las correcciones mejoran el registro y vocabulario acorde al propósito y destinatario del texto.", "Satisfactorio": "Identifica algunos cambios en el registro o vocabulario, pero la justificación es poco precisa o limitada.", "Necesita mejorar": "No logra identificar ni justificar adecuadamente las correcciones relacionadas con registro y vocabulario."}, "Mejora en la presentación y organización del texto": {"Excelente": "Describe detalladamente cómo las correcciones mejoran la presentación y organización, facilitando la comprensión del texto.", "Satisfactorio": "Menciona algunas mejoras en presentación y organización, pero con justificaciones poco desarrolladas.", "Necesita mejorar": "No evidencia comprensión ni justificación sobre la mejora en presentación y organización."}, "Justificación de correcciones ortográficas y gramaticales": {"Excelente": "Explica claramente cada corrección realizada, mostrando comprensión profunda de las reglas ortográficas y gramaticales implicadas.", "Satisfactorio": "Justifica la mayoría de las correcciones, aunque algunas explicaciones son superficiales o incompletas.", "Necesita mejorar": "Ofrece pocas o ninguna justificación clara sobre las correcciones realizadas."}}, "explicacion": "Se espera que el estudiante analice críticamente las correcciones realizadas en el texto, justificando con fundamentos claros y precisos sus evaluaciones en ortografía, gramática, registro, vocabulario, presentación y organización.", "instruccion": "Evalúa la corrección realizada en el texto según los criterios de la rúbrica y justifica detalladamente tu calificación para cada criterio.", "caso_a_evaluar": "Se presenta un texto narrativo breve elaborado por un estudiante de Primero Medio, que contiene varios errores ortográficos, gramaticales y problemas en la presentación visual (uso irregular de mayúsculas, falta de puntuación adecuada y párrafos mal organizados). El texto ha sido corregido por otro estudiante, quien realizó modificaciones para mejorar estos aspectos."}', '{"evaluacion_modelo": {"Adecuación del registro y vocabulario": "Excelente", "Mejora en la presentación y organización del texto": "Satisfactorio", "Justificación de correcciones ortográficas y gramaticales": "Satisfactorio"}, "justificacion_modelo": "La justificación de las correcciones ortográficas y gramaticales cubre la mayoría de los cambios, pero carece de profundidad en algunos casos; en cuanto al registro y vocabulario, se argumenta claramente cómo las correcciones adecuan el texto al destinatario y propósito; en la presentación y organización, se reconocen mejoras pero con explicaciones más superficiales.", "requiere_revision_humana": true}', 5, 0, true, '{evaluar,"corrección de textos","ortografía y gramática","registro y vocabulario","presentación de textos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (465, 100, 'open_ended', 'all', '{"pregunta": "Analiza un texto propio o ajeno que hayas redactado y justifica las correcciones que realizarías en cuanto a ortografía, gramática y presentación. ¿Por qué consideras que estas correcciones mejoran la calidad del texto en función del contexto, el destinatario y el propósito comunicativo?", "explicacion": "El docente debe evaluar que el estudiante no solo identifique errores, sino que también argumente de manera fundamentada cómo y por qué las correcciones mejoran el texto considerando el contexto comunicativo, el público objetivo y el propósito del mensaje.", "contexto_adicional": "Se puede utilizar un texto propio o un texto proporcionado por el docente para el análisis y justificación de correcciones.", "criterios_evaluacion": ["Justificación clara y pertinente de las correcciones ortográficas realizadas, mostrando comprensión de las normas (peso: 30%)", "Análisis detallado de las correcciones gramaticales y su impacto en la coherencia y claridad del texto (peso: 40%)", "Reflexión sobre cómo las correcciones en la presentación y adecuación del registro mejoran la comunicación según el contexto y destinatario (peso: 30%)"]}', '{"puntos_clave": ["Identificación y corrección de errores ortográficos con justificación clara", "Explicación del impacto de correcciones gramaticales en la claridad y coherencia", "Reflexión sobre la adecuación del registro y presentación respecto al contexto y destinatario"], "respuesta_modelo": "En el texto analizado, corregí la palabra ''haver'' por ''haber'' porque es un error ortográfico común que afecta la formalidad y comprensión. Además, modifiqué la estructura de la oración que contenía un error de concordancia verbal para que el sujeto y el verbo concuerden en número, lo que mejora la claridad y coherencia del mensaje. Finalmente, ajusté el registro eliminando expresiones informales para adecuar el texto a un destinatario académico, lo que mejora la presentación y efectividad del texto según su propósito de informar de manera formal.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",Evaluación,Ortografía,Gramática,Presentación,"Primeros Medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (466, 100, 'open_ended', 'all', '{"pregunta": "Analiza un texto propio o ajeno que hayas escrito o leído recientemente. Identifica y justifica al menos tres correcciones que mejorarían la ortografía, gramática y presentación del texto, explicando cómo estas modificaciones contribuyen a adecuar el texto al contexto, destinatario y propósito.", "explicacion": "El docente debe evaluar si el estudiante no solo identifica errores, sino que también argumenta por qué y cómo corregirlos mejora la calidad del texto, tomando en cuenta el contexto comunicativo. Se debe valorar la profundidad del análisis y la adecuación del lenguaje empleado para explicar las correcciones.", "contexto_adicional": "Se puede utilizar un texto propio o ajeno de cualquier tipo (narrativo, descriptivo, argumentativo) que se haya trabajado en clase o en actividades personales recientes.", "criterios_evaluacion": ["Justificación clara y fundamentada de las correcciones propuestas, demostrando comprensión de las reglas ortográficas y gramaticales (peso: 40%)", "Capacidad para relacionar las correcciones con la adecuación del texto al contexto, destinatario y propósito comunicativo (peso: 35%)", "Organización y coherencia en la exposición de las ideas, con un lenguaje apropiado y presentación ordenada (peso: 25%)"]}', '{"puntos_clave": ["Identificación precisa de errores ortográficos, gramaticales o de presentación", "Justificación fundamentada que conecta la corrección con la mejora en la comunicación", "Relación entre las correcciones y la adecuación al contexto, destinatario y propósito"], "respuesta_modelo": "En el texto analizado, identifiqué tres correcciones: primero, corregí la falta de tildes en palabras como ''técnico'' y ''próximo'', ya que la ausencia afecta la comprensión y la corrección ortográfica. Segundo, reemplacé frases con construcciones gramaticales incorrectas, como cambiar ''ellos fue'' por ''ellos fueron'' para respetar la concordancia verbal. Por último, mejoré la presentación al organizar los párrafos y usar conectores adecuados para que el texto sea más coherente y fácil de seguir. Estas correcciones permiten que el texto sea más claro y adecuado para un público escolar, facilitando la comunicación efectiva del mensaje según el propósito de informar.", "requiere_revision_humana": true}', 5, 0, true, '{evaluacion,ortografia,gramatica,presentacion,argumentacion}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (467, 101, 'open_ended', 'all', '{"pregunta": "Imagina que debes escribir una carta formal a la municipalidad de tu comuna para solicitar la creación de un espacio cultural para jóvenes. Describe cómo planificarías, escribirías, revisarías y editarías esta carta considerando el contexto, el destinatario y el propósito. Además, explica qué herramientas digitales utilizarías para mejorar tu texto final.", "explicacion": "Se evalúa la capacidad del estudiante para aplicar estrategias de planificación y organización antes de escribir, adaptar el lenguaje formal apropiado para el destinatario y propósito, y utilizar herramientas digitales para revisar y editar su texto, demostrando reflexión sobre el proceso de escritura.", "contexto_adicional": "La carta debe ser formal, dirigida a una autoridad municipal, y su propósito es solicitar la creación de un espacio cultural para jóvenes en la comuna.", "criterios_evaluacion": ["Planificación y organización de ideas en función del contexto, destinatario y propósito (peso: 30%)", "Adecuación del registro y vocabulario en la carta formal (peso: 40%)", "Uso y justificación de herramientas digitales para revisar y editar el texto (peso: 30%)"]}', '{"puntos_clave": ["Organización clara y lógica de ideas antes de escribir", "Uso de un registro formal y vocabulario adecuado para la autoridad municipal", "Empleo de herramientas digitales (correctores, procesadores de texto) para revisar y editar el texto"], "respuesta_modelo": "Para planificar la carta, primero recopilaría información sobre la necesidad de un espacio cultural para jóvenes en la comuna y organizaría mis ideas en un esquema que incluya introducción, motivos y solicitud. Escribiría la carta con un registro formal, usando vocabulario respetuoso y claro, adecuado para dirigirme a la municipalidad. Luego, usaría herramientas digitales como correctores ortográficos y de gramática, y la función de revisión de texto para mejorar la coherencia y claridad. Finalmente, editaría la carta incorporando las correcciones y asegurando que el contenido sea persuasivo y adecuado al propósito.", "requiere_revision_humana": true}', 4, 0, true, '{escritura,planificación,"herramientas digitales","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (468, 101, 'open_ended', 'all', '{"pregunta": "Diseña y escribe un texto informativo dirigido a estudiantes de Primero Medio, explicando la importancia de planificar y revisar un escrito antes de entregarlo. Utiliza un vocabulario adecuado para tu público y emplea herramientas digitales para organizar y editar tu texto. Justifica las decisiones que tomaste en cuanto a la organización, el vocabulario y la edición durante tu proceso de escritura.", "explicacion": "El docente debe evaluar cómo el estudiante organiza sus ideas y si el texto presenta una estructura clara que facilita la comprensión. Además, se debe valorar la adecuación del lenguaje al público objetivo y el propósito comunicativo. Finalmente, el docente debe revisar el uso de herramientas digitales, observando si el estudiante ha utilizado funciones de procesamiento de texto para mejorar la redacción y si puede justificar las modificaciones realizadas.", "contexto_adicional": "El texto debe estar dirigido a compañeros de Primero Medio, con el propósito de informar y persuadir sobre la relevancia de planificar y revisar antes de escribir.", "criterios_evaluacion": ["Organización y coherencia del texto, demostrando planificación clara y lógica (peso: 30%)", "Adecuación del registro y vocabulario para el destinatario y propósito del texto (peso: 40%)", "Uso efectivo de herramientas digitales para la edición y mejora del texto final, incluyendo justificación de las decisiones tomadas (peso: 30%)"]}', '{"puntos_clave": ["Explicación clara de la importancia de planificar y revisar el texto", "Adecuación del vocabulario y registro al público de Primero Medio", "Justificación del uso de herramientas digitales para editar y mejorar el texto"], "respuesta_modelo": "Planificar y revisar un escrito antes de entregarlo es fundamental para mejorar la calidad del texto y asegurar que el mensaje llegue de manera clara a los lectores. En primer lugar, planificar permite organizar las ideas principales y secundarias, lo que ayuda a evitar repeticiones y confusiones. Por ejemplo, al hacer un esquema o un borrador, podemos visualizar mejor la estructura del texto. Además, revisar el texto es clave para corregir errores ortográficos, mejorar la coherencia y adecuar el vocabulario al público, en este caso, estudiantes de Primero Medio. Para facilitar este proceso, utilicé herramientas digitales como el corrector ortográfico y la función de revisión de texto, que me ayudaron a identificar y corregir errores rápidamente. También utilicé la función de comentarios para justificar algunos cambios, como la sustitución de palabras técnicas por términos más accesibles para mis compañeros. En conclusión, planificar y revisar no solo mejora la claridad y la calidad del texto, sino que también facilita la comunicación efectiva con el destinatario.", "requiere_revision_humana": true}', 5, 0, true, '{creación,escritura,"procesamiento digital","planificación textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (469, 101, 'concept_map', 'all', '{"conceptos": ["Contexto", "Destinatario", "Propósito", "Registro", "Vocabulario", "Planificación", "Revisión", "Edición", "Herramientas digitales", "Texto final"], "explicacion": "El mapa debe mostrar cómo los elementos Contexto, Destinatario y Propósito influyen en la elección del Registro y el Vocabulario. Además, debe conectar las etapas de Planificación, Revisión y Edición con el uso de Herramientas digitales para llegar al Texto final. Se espera que el estudiante evidencie la secuencia y las interdependencias entre estos conceptos en la creación de un texto adecuado.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el proceso de diseño y producción de un texto escrito?"}', '{"conceptos_centrales": ["Contexto", "Texto final"], "relaciones_esperadas": [{"origen": "Contexto", "destino": "Registro", "relacion": "influye en"}, {"origen": "Destinatario", "destino": "Vocabulario", "relacion": "determina"}, {"origen": "Propósito", "destino": "Registro", "relacion": "condiciona"}, {"origen": "Planificación", "destino": "Revisión", "relacion": "precede a"}, {"origen": "Revisión", "destino": "Edición", "relacion": "lleva a"}, {"origen": "Edición", "destino": "Herramientas digitales", "relacion": "utiliza"}, {"origen": "Edición", "destino": "Texto final", "relacion": "produce"}, {"origen": "Registro", "destino": "Texto final", "relacion": "se refleja en"}, {"origen": "Vocabulario", "destino": "Texto final", "relacion": "se ajusta a"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Producción de textos","Herramientas digitales","Mapa conceptual","Primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (470, 101, 'concept_map', 'all', '{"conceptos": ["Contexto", "Destinatario", "Propósito", "Planificación", "Revisión", "Edición", "Registro", "Vocabulario", "Herramientas digitales"], "explicacion": "Se espera que el estudiante conecte cómo la planificación inicial incluye la recopilación de información sobre el contexto, destinatario y propósito, cómo esto influye en la elección del registro y vocabulario, y cómo la revisión y edición, apoyadas por herramientas digitales, permiten adecuar y mejorar el texto final para que cumpla con las exigencias comunicativas.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el proceso de diseñar y producir un texto que cumple con las características de contexto, destinatario y propósito?"}', '{"conceptos_centrales": ["Planificación", "Edición"], "relaciones_esperadas": [{"origen": "Planificación", "destino": "Contexto", "relacion": "considera"}, {"origen": "Planificación", "destino": "Destinatario", "relacion": "considera"}, {"origen": "Planificación", "destino": "Propósito", "relacion": "considera"}, {"origen": "Contexto", "destino": "Registro", "relacion": "influye en"}, {"origen": "Destinatario", "destino": "Vocabulario", "relacion": "determina"}, {"origen": "Propósito", "destino": "Registro", "relacion": "define"}, {"origen": "Revisión", "destino": "Texto", "relacion": "mejora"}, {"origen": "Edición", "destino": "Herramientas digitales", "relacion": "utiliza"}, {"origen": "Edición", "destino": "Registro", "relacion": "ajusta"}, {"origen": "Edición", "destino": "Vocabulario", "relacion": "corrige"}], "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Producción textual","Mapa conceptual","Primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (472, 102, 'multiple_choice', 'all', '{"opciones": {"A": "María dijo: \"Estoy feliz por el resultado\".", "B": "María dijo que estaba feliz por el resultado.", "C": "¿María está feliz por el resultado?", "D": "María, feliz por el resultado."}, "pregunta": "¿Cuál de las siguientes oraciones es un ejemplo de estilo indirecto?", "explicacion": "La opción B es un ejemplo de estilo indirecto porque presenta el contenido de lo que María dijo sin usar comillas ni reproducir sus palabras exactas, y además emplea un cambio en el tiempo verbal (''estaba'' en lugar de ''estoy''). La opción A es estilo directo, ya que cita textualmente las palabras de María entre comillas. La opción C es una pregunta y no representa ningún estilo de discurso citado. La opción D es una frase descriptiva, tampoco es estilo directo ni indirecto."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"estilo directo","estilo indirecto","lengua y literatura","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (477, 103, 'multiple_choice', 'all', '{"opciones": {"A": "En el estilo directo, se cambian los tiempos verbales para reflejar el pasado, mientras que en el estilo indirecto se mantienen iguales.", "B": "En el estilo directo se citan las palabras exactas de la persona, usando comillas, mientras que en el estilo indirecto se parafrasea y se ajustan los tiempos verbales según el contexto.", "C": "El estilo indirecto siempre usa el tiempo presente, mientras que el estilo directo usa el tiempo pasado.", "D": "En el estilo directo no se utilizan signos de puntuación, mientras que en el estilo indirecto sí."}, "pregunta": "¿Cuál de las siguientes afirmaciones explica correctamente una diferencia entre el estilo directo y el estilo indirecto en la narración de un hecho?", "explicacion": "La opción B es correcta porque el estilo directo reproduce las palabras exactas del hablante, generalmente entre comillas, y el estilo indirecto reformula lo dicho ajustando los tiempos verbales para concordar con el contexto temporal. La opción A es incorrecta porque es justamente el estilo indirecto el que requiere el cambio de tiempos verbales, no el directo. La opción C es incorrecta porque el estilo indirecto no usa exclusivamente el presente, sino que adapta los tiempos verbales según la situación. La opción D es falsa porque el estilo directo sí utiliza signos de puntuación, especialmente las comillas para indicar la cita textual."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"Estilo directo","Estilo indirecto","Tiempos verbales","Comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (478, 103, 'multiple_choice', 'all', '{"opciones": {"A": "El estilo directo reproduce exactamente las palabras del hablante, mientras que el estilo indirecto parafrasea el mensaje y ajusta los tiempos verbales según el contexto.", "B": "El estilo indirecto siempre usa el mismo tiempo verbal que el estilo directo, sin necesidad de cambios.", "C": "El estilo directo implica cambiar los tiempos verbales para adaptar el mensaje, mientras que el estilo indirecto los mantiene iguales para conservar el significado original.", "D": "En el estilo indirecto se usan siempre comillas para destacar las palabras del hablante, mientras que en el directo no se usan."}, "pregunta": "¿Cuál de las siguientes afirmaciones explica correctamente la diferencia entre el estilo directo y el estilo indirecto en un texto?", "explicacion": "La opción A es correcta porque el estilo directo reproduce textualmente las palabras del hablante, generalmente usando comillas, y el estilo indirecto parafrasea esas palabras, ajustando los tiempos verbales para mantener la coherencia temporal en el discurso. La opción B es incorrecta porque los tiempos verbales suelen modificarse en estilo indirecto. La opción C confunde los roles de los estilos; es el estilo indirecto el que cambia los tiempos, no el directo. La opción D es incorrecta porque las comillas se usan en el estilo directo, no en el indirecto."}', '{"respuesta_correcta": "A"}', 3, 0, true, '{"estilo directo","estilo indirecto","tiempos verbales"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (479, 103, 'true_false', 'all', '{"afirmacion": "En el estilo indirecto, los tiempos verbales suelen cambiar para reflejar el momento en que se comunica la información, mientras que en el estilo directo se mantienen los tiempos originales de la cita.", "explicacion": "Esta afirmación es verdadera porque en el estilo indirecto, al parafrasear lo dicho por alguien, los tiempos verbales se ajustan para concordar con el momento presente del hablante (por ejemplo, el presente puede cambiar a pasado). En cambio, en el estilo directo se reproducen literalmente las palabras y tiempos verbales originales de quien habló."}', '{"es_verdadero": true}', 2, 0, true, '{"estilo directo","estilo indirecto","tiempos verbales",comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (480, 103, 'drag_drop_matching', 'all', '{"explicacion": "El estilo directo reproduce textualmente las palabras, por eso usa comillas. El estilo indirecto lo parafrasea y requiere cambiar los tiempos verbales para concordancia temporal. Por ejemplo, en estilo directo: \"María dijo: ''Estoy feliz''\" y en indirecto: María dijo que estaba feliz. El distractor no se relaciona con los estilos ni sus características.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Repetir exactamente lo que alguien dijo, usando comillas", "Reformular lo que alguien dijo, sin usar comillas y con cambios en tiempos verbales", "El presente cambia a pasado, el pasado a pluscuamperfecto, entre otros ajustes", "\"María dijo: ''Estoy feliz''\"", "María dijo que estaba feliz", "Una oración que no contiene verbo"], "columna_izquierda": ["Estilo directo", "Estilo indirecto", "Cambio de tiempo verbal en estilo indirecto", "Ejemplo de estilo directo", "Ejemplo de estilo indirecto", "Distractor: Oración sin verbo"]}', '{"emparejamientos_correctos": {"Estilo directo": "Repetir exactamente lo que alguien dijo, usando comillas", "Estilo indirecto": "Reformular lo que alguien dijo, sin usar comillas y con cambios en tiempos verbales", "Ejemplo de estilo directo": "\"María dijo: ''Estoy feliz''\"", "Ejemplo de estilo indirecto": "María dijo que estaba feliz", "Cambio de tiempo verbal en estilo indirecto": "El presente cambia a pasado, el pasado a pluscuamperfecto, entre otros ajustes"}}', 2, 0, true, '{"estilo directo","estilo indirecto","tiempos verbales","lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (481, 103, 'sequencing', 'all', '{"explicacion": "Primero se debe identificar la oración en estilo directo para saber qué transformar. Luego se cambia el pronombre para que tenga sentido desde la perspectiva del narrador. Después se ajustan los tiempos verbales para mantener la coherencia temporal. A continuación, se eliminan las marcas gráficas propias del estilo directo. Finalmente, se añade el verbo de comunicación para introducir el estilo indirecto.", "instruccion": "Ordena los siguientes pasos para transformar una oración de estilo directo a estilo indirecto correctamente", "elementos_desordenados": ["Cambia los pronombres para que coincidan con el sujeto del narrador.", "Identifica la oración en estilo directo que quieres transformar.", "Ajusta los tiempos verbales según la concordancia de tiempos.", "Elimina las comillas y los signos de interrogación o exclamación.", "Introduce el verbo de comunicación adecuado (como ''dijo'' o ''comentó'')."]}', '{"orden_correcto": ["Identifica la oración en estilo directo que quieres transformar.", "Cambia los pronombres para que coincidan con el sujeto del narrador.", "Ajusta los tiempos verbales según la concordancia de tiempos.", "Elimina las comillas y los signos de interrogación o exclamación.", "Introduce el verbo de comunicación adecuado (como ''dijo'' o ''comentó'')."]}', 3, 0, true, '{"estilo directo","estilo indirecto","concordancia verbal","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (564, 120, 'true_false', 'all', '{"afirmacion": "Un documental es un texto audiovisual que presenta información real sobre un tema específico, generalmente con imágenes y testimonios.", "explicacion": "Esta afirmación es verdadera porque un documental combina imágenes, sonidos y testimonios para informar y educar sobre un tema real, diferenciándose de otros textos audiovisuales que pueden ser ficticios o con objetivos distintos."}', '{"es_verdadero": true}', 1, 0, true, '{documental,"texto audiovisual","identificación de elementos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (483, 104, 'multiple_choice', 'all', '{"opciones": {"A": "María dijo que estaba estudiando para el examen.", "B": "María dijo que estoy estudiando para el examen.", "C": "María dijo que estará estudiando para el examen.", "D": "María dijo que estudia para el examen."}, "pregunta": "¿Cuál es la forma correcta de transformar la oración en estilo directo a estilo indirecto, respetando los tiempos verbales? \n\nEstilo directo: María dijo: \"Estoy estudiando para el examen\".", "explicacion": "La respuesta correcta es A porque al transformar una oración de estilo directo a indirecto, el tiempo verbal presente progresivo ''estoy estudiando'' cambia a pasado progresivo ''estaba estudiando'' para mantener la concordancia temporal. La opción B es incorrecta porque mantiene el presente, lo que rompe la concordancia temporal. La opción C usa futuro, que no corresponde al tiempo original ni a la relación temporal adecuada. La opción D usa presente simple, que cambia el aspecto del verbo y no refleja correctamente la acción en progreso."}', '{"respuesta_correcta": "A"}', 4, 0, true, '{"estilo indirecto","tiempos verbales",transformación,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (484, 104, 'drag_drop_matching', 'all', '{"explicacion": "Cada oración en estilo directo se transforma en estilo indirecto cambiando los tiempos verbales y adaptando la estructura según el tipo de oración: afirmativa, interrogativa o exclamativa.", "instruccion": "Empareja cada oración en estilo directo con su transformación correcta en estilo indirecto.", "columna_derecha": ["María dijo que estaba cansada.", "Juan afirmó que llegaría al día siguiente.", "Ana preguntó si venías a la fiesta.", "Pedro comentó que no quería ir al cine.", "Luis exclamó lo hermosa que era la ciudad."], "columna_izquierda": ["\"María dijo: ''Estoy cansada''\"", "\"Juan afirmó: ''Llegaré mañana''\"", "\"Ana preguntó: ''¿Vienes a la fiesta?''\"", "\"Pedro comentó: ''No quiero ir al cine''\"", "\"Luis exclamó: ''¡Qué hermosa es la ciudad!''\"", "Distractor: \"Ella piensa: ''Mañana estudiare para el examen''\""]}', '{"emparejamientos_correctos": {"\"María dijo: ''Estoy cansada''\"": "María dijo que estaba cansada.", "\"Juan afirmó: ''Llegaré mañana''\"": "Juan afirmó que llegaría al día siguiente.", "\"Ana preguntó: ''¿Vienes a la fiesta?''\"": "Ana preguntó si venías a la fiesta.", "\"Pedro comentó: ''No quiero ir al cine''\"": "Pedro comentó que no quería ir al cine.", "\"Luis exclamó: ''¡Qué hermosa es la ciudad!''\"": "Luis exclamó lo hermosa que era la ciudad."}}', 3, 0, true, '{"estilo indirecto","transformación de oraciones","tiempos verbales","lengua y literatura","1º medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (485, 104, 'sequencing', 'all', '{"explicacion": "El orden es lógico y procedimental: primero se identifica el tiempo verbal original para saber cómo transformarlo; luego se ajustan pronombres y adverbios que cambian con el estilo indirecto; después se adapta el verbo principal al tiempo correcto; a continuación, se introduce la oración con un verbo que indica comunicación; finalmente, se escribe la oración completa en estilo indirecto.", "instruccion": "Ordena los siguientes pasos para transformar una oración de estilo directo a estilo indirecto utilizando los tiempos verbales correctos.", "elementos_desordenados": ["Identificar el tiempo verbal de la oración en estilo directo.", "Cambiar los pronombres y adverbios de tiempo si es necesario.", "Adaptar el verbo principal al tiempo verbal correspondiente en estilo indirecto.", "Introducir la oración con un verbo de comunicación como ''dijo que'', ''afirmó que'', etc.", "Escribir la oración completa en estilo indirecto con las modificaciones realizadas."]}', '{"orden_correcto": ["Identificar el tiempo verbal de la oración en estilo directo.", "Cambiar los pronombres y adverbios de tiempo si es necesario.", "Adaptar el verbo principal al tiempo verbal correspondiente en estilo indirecto.", "Introducir la oración con un verbo de comunicación como ''dijo que'', ''afirmó que'', etc.", "Escribir la oración completa en estilo indirecto con las modificaciones realizadas."]}', 3, 0, true, '{"estilo indirecto","tiempos verbales",transformación,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (486, 104, 'open_ended', 'all', '{"pregunta": "Lee el siguiente diálogo en estilo directo y reescríbelo en estilo indirecto, asegurándote de aplicar correctamente los cambios en los tiempos verbales. Luego, explica qué cambios realizaste y por qué son necesarios en la transformación.", "explicacion": "El docente debe valorar que el estudiante aplique correctamente las reglas gramaticales para la transformación de estilo directo a indirecto, especialmente en la concordancia de tiempos verbales. Además, debe observar si el texto resultante es coherente y claro, y si el estudiante puede explicar con precisión los cambios realizados, mostrando comprensión del contraste formal y de significado entre ambos estilos.", "contexto_adicional": "Diálogo en estilo directo: \n— María dijo: \"Estoy estudiando para el examen.\" \n— Juan respondió: \"Nos veremos mañana a las diez.\"", "criterios_evaluacion": ["Aplicación correcta de los tiempos verbales al transformar de estilo directo a indirecto (peso: 40%)", "Claridad y coherencia en la redacción del texto transformado (peso: 30%)", "Análisis y explicación precisa de los cambios realizados y su justificación (peso: 30%)"]}', '{"puntos_clave": ["Cambio correcto de tiempos verbales (presente a pasado, futuro a condicional o pretérito)", "Modificación adecuada de expresiones temporales (''mañana'' a ''al día siguiente'')", "Explicación clara del motivo de estos cambios basados en reglas del estilo indirecto"], "respuesta_modelo": "María dijo que estaba estudiando para el examen. Juan respondió que se verían al día siguiente a las diez. \n\nCambios realizados: Cambié el presente progresivo ''estoy estudiando'' por el pretérito imperfecto ''estaba estudiando'' porque el estilo indirecto requiere retroceder un tiempo verbal cuando el verbo principal está en pasado. Además, ''nos veremos mañana'' cambió a ''se verían al día siguiente'' para ajustar el tiempo verbal y la referencia temporal al contexto del discurso indirecto.", "requiere_revision_humana": true}', 3, 0, true, '{estilo_directo,estilo_indirecto,tiempos_verbales,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (487, 105, 'compare_contrast', 'all', '{"conceptos": ["Estilo directo", "Estilo indirecto"], "criterios": ["Forma de presentación", "Uso de tiempos verbales", "Relación con el emisor original", "Función comunicativa"], "explicacion": "El estilo directo reproduce fielmente las palabras exactas del emisor original, manteniendo los tiempos verbales originales y el tono, mientras que el estilo indirecto reformula el mensaje adaptando los tiempos verbales para integrarlo en la narración o discurso del emisor actual, alterando a veces matices de significado. Ambos estilos cumplen funciones comunicativas distintas: el directo enfatiza la vivacidad y autenticidad, y el indirecto facilita la integración y síntesis de información.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Estilo directo": {"Función comunicativa": "Transmitir con exactitud y autenticidad el mensaje", "Forma de presentación": "Reproducción literal de las palabras del emisor", "Uso de tiempos verbales": "Se mantienen los tiempos verbales originales", "Relación con el emisor original": "Preserva la voz y perspectiva del emisor original"}, "Estilo indirecto": {"Función comunicativa": "Sintetizar y adaptar la información para el receptor", "Forma de presentación": "Paráfrasis o reformulación del mensaje", "Uso de tiempos verbales": "Adaptación de tiempos verbales según el contexto del emisor actual", "Relación con el emisor original": "Se integra la información al discurso del narrador"}}}', 3, 0, true, '{"estilo directo","estilo indirecto",comparación,"lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (488, 105, 'compare_contrast', 'all', '{"conceptos": ["Estilo Directo", "Estilo Indirecto"], "criterios": ["Forma de presentación", "Uso de tiempos verbales", "Presencia de signos de puntuación"], "explicacion": "El estilo directo reproduce textualmente las palabras del hablante, usando comillas y signos de puntuación específicos, mientras que el estilo indirecto parafrasea esas palabras, ajustando los tiempos verbales y eliminando las comillas. Ambos estilos sirven para reportar información, pero varían en su estructura formal y en cómo se transmiten los significados.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Estilo Directo": {"Forma de presentación": "Reproducción textual exacta de las palabras del hablante", "Uso de tiempos verbales": "Mantiene los tiempos verbales originales", "Presencia de signos de puntuación": "Uso de comillas y signos de exclamación o interrogación"}, "Estilo Indirecto": {"Forma de presentación": "Paráfrasis o reexpresión de las palabras del hablante", "Uso de tiempos verbales": "Adaptación de los tiempos verbales según el contexto temporal", "Presencia de signos de puntuación": "No usa comillas; signos de exclamación o interrogación se adaptan al enunciado"}}}', 4, 0, true, '{"estilo directo","estilo indirecto",comparación,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (489, 105, 'open_ended', 'all', '{"pregunta": "Analiza un fragmento en estilo directo y su correspondiente versión en estilo indirecto de un texto académico. ¿Cuáles son los principales cambios formales y de significado que se producen al transformar el estilo directo en indirecto? Explica cómo estos cambios afectan la claridad y la intención comunicativa del texto.", "explicacion": "El docente debe valorar que el estudiante no solo identifique diferencias formales, sino que también pueda explicar el impacto que estos cambios generan en la comprensión y propósito del texto. Se espera un análisis crítico que demuestre dominio del tema y habilidades para contrastar estilos de expresión.", "contexto_adicional": "Se proporcionará un fragmento breve de un texto académico en estilo directo junto con su versión en estilo indirecto para que el estudiante pueda comparar.", "criterios_evaluacion": ["Identificación precisa de los cambios formales entre estilo directo e indirecto, como variaciones en los tiempos verbales y signos de puntuación (peso: 30%)", "Análisis claro y profundo sobre cómo los cambios afectan el significado y la intención comunicativa del texto (peso: 40%)", "Capacidad para organizar la respuesta de manera coherente y utilizar vocabulario adecuado al análisis literario (peso: 30%)"]}', '{"puntos_clave": ["Cambio en tiempos verbales al pasar de estilo directo a indirecto", "Eliminación o adaptación de signos de puntuación (comillas, signos de interrogación/exclamación)", "Impacto en la intención comunicativa: de expresión directa y emotiva a una exposición más formal y objetiva"], "respuesta_modelo": "En el fragmento en estilo directo, se observan oraciones entrecomilladas que reproducen literalmente el discurso original, con tiempos verbales presentes o pasados según el contexto, y signos de interrogación o exclamación que expresan emociones o énfasis. Al pasar al estilo indirecto, estos signos desaparecen y los tiempos verbales suelen retroceder (por ejemplo, presente a pretérito imperfecto), lo que formaliza el discurso y lo integra dentro de una narración. Esto puede hacer que la información sea menos inmediata o emotiva, pero a la vez más objetiva y fluida, facilitando la conexión con otras ideas en el texto académico. Por tanto, el cambio formal no solo modifica la estructura gramatical, sino que también suaviza la intención comunicativa, pasando de un relato directo y personal a una exposición más distante y analítica, propia de los textos académicos.", "requiere_revision_humana": true}', 4, 0, true, '{"estilo directo","estilo indirecto","análisis textual","lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (490, 105, 'concept_map', 'all', '{"conceptos": ["Estilo Directo", "Estilo Indirecto", "Tiempos Verbales", "Cambio de Perspectiva", "Significado", "Aspectos Formales", "Textos Académicos"], "explicacion": "El mapa conceptual debe mostrar cómo el Estilo Directo y el Estilo Indirecto se diferencian y relacionan a través de aspectos formales y de significado. Debe incluir cómo los Tiempos Verbales se modifican en el paso del estilo directo al indirecto, el rol del Cambio de Perspectiva en esta transformación, y cómo estos elementos afectan la construcción y comprensión de los Textos Académicos.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del uso del estilo directo e indirecto en textos académicos?"}', '{"conceptos_centrales": ["Estilo Directo", "Estilo Indirecto"], "relaciones_esperadas": [{"origen": "Estilo Directo", "destino": "Estilo Indirecto", "relacion": "contrasta con"}, {"origen": "Estilo Indirecto", "destino": "Cambio de Perspectiva", "relacion": "requiere"}, {"origen": "Estilo Indirecto", "destino": "Tiempos Verbales", "relacion": "implica modificación de"}, {"origen": "Estilo Directo", "destino": "Tiempos Verbales", "relacion": "mantiene"}, {"origen": "Estilo Directo", "destino": "Aspectos Formales", "relacion": "difiere en"}, {"origen": "Estilo Indirecto", "destino": "Aspectos Formales", "relacion": "difiere en"}, {"origen": "Aspectos Formales", "destino": "Significado", "relacion": "afectan"}, {"origen": "Estilo Directo", "destino": "Textos Académicos", "relacion": "se utiliza en"}, {"origen": "Estilo Indirecto", "destino": "Textos Académicos", "relacion": "se utiliza en"}], "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Estilo Directo e Indirecto","Textos Académicos","Mapa Conceptual",Análisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (491, 105, 'concept_map', 'all', '{"conceptos": ["Estilo directo", "Estilo indirecto", "Tiempos verbales", "Significado", "Formalidad", "Textos académicos"], "explicacion": "Se espera que el estudiante muestre cómo el estilo directo y el indirecto se diferencian en formalidad y uso de tiempos verbales, cómo esos aspectos afectan el significado del mensaje, y cómo estos estilos se aplican específicamente en textos académicos.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del uso del estilo directo e indirecto en textos académicos?"}', '{"conceptos_centrales": ["Estilo directo", "Estilo indirecto"], "relaciones_esperadas": [{"origen": "Estilo directo", "destino": "Estilo indirecto", "relacion": "contrasta con"}, {"origen": "Estilo indirecto", "destino": "Tiempos verbales", "relacion": "modifica"}, {"origen": "Tiempos verbales", "destino": "Significado", "relacion": "afectan"}, {"origen": "Estilo indirecto", "destino": "Formalidad", "relacion": "aumenta"}, {"origen": "Formalidad", "destino": "Textos académicos", "relacion": "es relevante en"}, {"origen": "Estilo directo", "destino": "Textos académicos", "relacion": "se usa en"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Estilo directo e indirecto","Mapas conceptuales","Textos académicos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (492, 106, 'criteria_evaluation', 'all', '{"rubrica": {"Adecuación del uso del estilo directo": {"Excelente": "El estudiante utiliza el estilo directo en momentos donde se requiere transmitir con precisión las palabras exactas del entrevistado, logrando claridad y fidelidad en el relato.", "Satisfactorio": "El estudiante emplea el estilo directo en la mayoría de los casos apropiados, aunque en algunos momentos podría mejorar la precisión en la selección del estilo.", "Necesita mejorar": "El estudiante confunde el uso del estilo directo, empleándolo en situaciones donde no es pertinente o genera confusión en la interpretación."}, "Adecuación del uso del estilo indirecto": {"Excelente": "El estudiante usa el estilo indirecto correctamente para parafrasear o resumir ideas, respetando los tiempos verbales y manteniendo coherencia con el contexto.", "Satisfactorio": "El estudiante utiliza el estilo indirecto en forma adecuada en la mayoría de las ocasiones, pero presenta pequeños errores en tiempos verbales o claridad.", "Necesita mejorar": "El estudiante aplica el estilo indirecto de manera incorrecta, con errores frecuentes en tiempos verbales o que afectan la comprensión del texto."}, "Justificación de la elección entre estilos": {"Excelente": "El estudiante explica claramente y con argumentos sólidos por qué eligió cada estilo en el fragmento, mostrando comprensión profunda del contraste formal y de significado.", "Satisfactorio": "El estudiante justifica adecuadamente la elección de estilos, aunque con explicaciones poco detalladas o con algunos vacíos en la argumentación.", "Necesita mejorar": "El estudiante presenta justificaciones vagas o incorrectas sobre la elección entre estilo directo e indirecto, sin evidenciar comprensión clara."}}, "explicacion": "Se espera que el estudiante reconozca el propósito comunicativo de cada estilo, aplique correctamente los tiempos verbales, y argumente con claridad las razones para elegir uno u otro en contextos específicos.", "instruccion": "Evalúa el fragmento presentado según la rúbrica proporcionada y justifica tu calificación en cada criterio, explicando por qué consideras que el uso de estilo directo e indirecto es apropiado o no.", "caso_a_evaluar": "Se presenta un fragmento de texto donde un estudiante utiliza tanto estilo directo como indirecto para narrar una entrevista realizada a un personaje histórico. El fragmento debe ser evaluado en cuanto a la adecuación del uso de cada estilo según el contexto y propósito comunicativo."}', '{"evaluacion_modelo": {"Adecuación del uso del estilo directo": "Satisfactorio", "Adecuación del uso del estilo indirecto": "Excelente", "Justificación de la elección entre estilos": "Excelente"}, "justificacion_modelo": "El fragmento muestra un uso mayormente correcto del estilo indirecto, con adecuados tiempos verbales y coherencia, mereciendo un nivel excelente. El estilo directo es empleado en momentos apropiados, aunque con leves imprecisiones que justifican la calificación satisfactorio. La justificación del estudiante es clara y fundamentada, demostrando comprensión, por lo que se evalúa como excelente.", "requiere_revision_humana": true}', 4, 0, true, '{"estilo directo","estilo indirecto",evaluación,"Lengua y Literatura","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (493, 106, 'criteria_evaluation', 'all', '{"rubrica": {"Claridad y coherencia en la justificación": {"Excelente": "La justificación es clara, coherente y bien estructurada, con argumentos sólidos y pertinentes.", "Satisfactorio": "La justificación es comprensible pero con argumentos poco desarrollados o alguna falta de coherencia.", "Necesita mejorar": "La justificación es confusa, incoherente o carece de argumentos relevantes."}, "Uso correcto de tiempos verbales en estilo indirecto": {"Excelente": "Reconoce y explica adecuadamente los cambios de tiempos verbales en estilo indirecto, mostrando comprensión profunda.", "Satisfactorio": "Identifica algunos cambios correctos en los tiempos verbales, aunque la explicación es superficial.", "Necesita mejorar": "No reconoce los cambios en los tiempos verbales o la explicación es errónea."}, "Adecuación en la elección del estilo (directo o indirecto)": {"Excelente": "Identifica correctamente el estilo usado en cada fragmento y explica con claridad y precisión por qué es el más apropiado para el contexto.", "Satisfactorio": "Reconoce el estilo en la mayoría de los fragmentos y ofrece una justificación básica sobre su adecuación al contexto.", "Necesita mejorar": "Tiene dificultades para identificar el estilo usado y la justificación es confusa o incorrecta."}}, "explicacion": "El estudiante debe demostrar capacidad para distinguir cuándo es más adecuado usar estilo directo o indirecto en un contexto académico, justificar su elección considerando aspectos formales y de significado, y reconocer correctamente los tiempos verbales en estilo indirecto. La argumentación debe ser clara y coherente para reflejar comprensión profunda del tema.", "instruccion": "Evalúa el uso del estilo directo e indirecto en el texto según los criterios de la rúbrica y justifica tu calificación para cada uno.", "caso_a_evaluar": "Se presenta un texto narrativo donde el autor utiliza tanto estilo directo como indirecto para relatar una conversación entre dos personajes en un contexto académico (una exposición en clase). El estudiante debe analizar el uso de ambos estilos, identificar cuál es más adecuado en cada fragmento y justificar por qué."}', '{"evaluacion_modelo": {"Claridad y coherencia en la justificación": "Excelente", "Uso correcto de tiempos verbales en estilo indirecto": "Satisfactorio", "Adecuación en la elección del estilo (directo o indirecto)": "Excelente"}, "justificacion_modelo": "El estudiante identifica con precisión el uso de ambos estilos y justifica adecuadamente su elección contextual. Reconoce cambios en los tiempos verbales indirectos, aunque con explicaciones básicas, y presenta una argumentación clara y coherente que demuestra comprensión del contraste entre estilos.", "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,estilo_directo_indirecto,evaluacion,primer_medio,debate_guiado}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (494, 106, 'criteria_evaluation', 'all', '{"rubrica": {"Justificación del uso del estilo": {"Excelente": "El estudiante ofrece argumentos claros y fundamentados que explican por qué se emplea cada estilo, relacionando tiempos verbales y función comunicativa.", "Satisfactorio": "El estudiante brinda una justificación general pero poco detallada o con algunos errores conceptuales.", "Necesita mejorar": "El estudiante no logra justificar adecuadamente la elección del estilo o presenta argumentos incorrectos."}, "Adecuación del estilo al contexto": {"Excelente": "El estudiante identifica correctamente cuándo se utiliza estilo directo o indirecto según la formalidad y claridad necesarias en un texto académico.", "Satisfactorio": "El estudiante reconoce mayormente la elección apropiada del estilo, aunque con algunas confusiones en ciertos casos.", "Necesita mejorar": "El estudiante no diferencia adecuadamente entre los usos del estilo directo e indirecto en el contexto académico."}, "Precisión en el uso de tiempos verbales": {"Excelente": "El estudiante reconoce y explica el uso correcto de los tiempos verbales en estilo indirecto, mostrando comprensión de su función.", "Satisfactorio": "El estudiante identifica en forma general los tiempos verbales adecuados, pero con imprecisiones o confusiones menores.", "Necesita mejorar": "El estudiante no reconoce ni explica adecuadamente los tiempos verbales usados en estilo indirecto."}}, "explicacion": "El estudiante debe demostrar comprensión para distinguir cuándo es más apropiado usar estilo directo o indirecto en un contexto académico, justificar esta elección considerando el efecto comunicativo y formalidad, y reconocer el correcto uso de tiempos verbales en estilo indirecto.", "instruccion": "Evalúa la elección del estilo directo e indirecto en el texto proporcionado utilizando la rúbrica. Justifica tu calificación en cada criterio con ejemplos concretos del texto.", "caso_a_evaluar": "Se presenta un fragmento de un texto académico en el que se cita una entrevista realizada a un experto. El texto utiliza tanto estilo directo como indirecto para reproducir las respuestas del entrevistado. Evalúa la elección del estilo directo o indirecto en cada caso, considerando la adecuación al contexto académico y la claridad del mensaje."}', '{"evaluacion_modelo": {"Justificación del uso del estilo": "Satisfactorio", "Adecuación del estilo al contexto": "Excelente", "Precisión en el uso de tiempos verbales": "Excelente"}, "justificacion_modelo": "El estudiante identifica claramente la función de cada estilo en el texto académico y explica con precisión los tiempos verbales del estilo indirecto. Sin embargo, la justificación del uso del estilo podría ser más profunda y vinculada a teorías o reglas específicas.", "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,estilo_directo_indirecto,evaluacion,primero_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (495, 106, 'open_ended', 'all', '{"pregunta": "En un contexto académico, ¿cuándo considerarías más apropiado utilizar el estilo directo en lugar del estilo indirecto, y viceversa? Justifica tu respuesta con ejemplos concretos y reflexiona sobre cómo la elección entre ambos estilos afecta la claridad y la intención comunicativa del texto.", "explicacion": "El docente debe evaluar si el estudiante puede identificar y justificar adecuadamente en qué situaciones es preferible usar estilo directo o indirecto, apoyándose en ejemplos claros y analizando cómo esta elección influye en la comprensión y el objetivo comunicativo del mensaje.", "contexto_adicional": "Considera diferentes situaciones dentro de textos académicos, como la presentación de citas, resúmenes o exposiciones orales.", "criterios_evaluacion": ["Justificación clara y coherente sobre la elección entre estilo directo e indirecto (peso: 40%)", "Uso adecuado de ejemplos concretos que ilustran la aplicación en contextos académicos (peso: 35%)", "Análisis del impacto de la elección del estilo en la claridad y la intención comunicativa (peso: 25%)"]}', '{"puntos_clave": ["Distinción clara entre uso de estilo directo e indirecto en contextos académicos", "Ejemplos específicos que aplican a citas textuales y parafraseo o resumen", "Reflexión sobre cómo la elección impacta la claridad y la intención comunicativa"], "respuesta_modelo": "En un contexto académico, el estilo directo es más apropiado cuando se desea presentar las palabras exactas de un autor o interlocutor, como al citar textualmente en una exposición o trabajo escrito, para preservar la fidelidad y autoridad de la fuente. Por ejemplo, al incluir una cita textual en un ensayo, usar el estilo directo mantiene la precisión del mensaje original. Por otro lado, el estilo indirecto es preferible cuando se busca resumir o parafrasear ideas, facilitando la integración de la información y evitando la repetición literal, como al explicar teorías o argumentos en una presentación oral. Esta elección afecta la claridad: el estilo directo puede aportar énfasis y credibilidad, mientras que el indirecto favorece la síntesis y la fluidez. Así, la elección entre ambos estilos debe contemplar el propósito comunicativo y el tipo de audiencia para asegurar una comunicación efectiva.", "requiere_revision_humana": true}', 4, 0, true, '{estilo_directo,estilo_indirecto,evaluacion_critica,primer_medio,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (496, 106, 'open_ended', 'all', '{"pregunta": "En un contexto académico, como la presentación de un informe o la redacción de un ensayo, ¿cuándo y por qué es más apropiado utilizar el estilo directo o el estilo indirecto? Justifica tu respuesta con ejemplos concretos y explica cómo la elección afecta la claridad y formalidad del texto.", "explicacion": "Se evaluará la capacidad del estudiante para evaluar críticamente y justificar cuándo es preferible usar estilo directo o indirecto, considerando aspectos formales y de significado. También se revisará la precisión en el uso de los tiempos verbales y la calidad de los ejemplos presentados para sustentar su postura.", "contexto_adicional": "Considere situaciones académicas como exposiciones orales, informes escritos, o ensayos donde la elección entre estilo directo e indirecto influye en la formalidad y precisión del mensaje.", "criterios_evaluacion": ["Justificación clara y fundamentada sobre el uso del estilo directo e indirecto en contextos académicos (peso: 40%)", "Uso adecuado y correcto de los tiempos verbales en estilo indirecto al explicar su elección (peso: 30%)", "Capacidad para ejemplificar con textos o situaciones concretas que evidencien la diferencia en la elección del estilo (peso: 30%)"]}', '{"puntos_clave": ["Diferenciación clara entre estilo directo e indirecto en función del contexto académico", "Justificación basada en la formalidad, claridad y propósito comunicativo", "Ejemplos concretos que demuestren la aplicación correcta de ambos estilos y los tiempos verbales en estilo indirecto"], "respuesta_modelo": "En contextos académicos, el estilo indirecto suele ser más apropiado en informes y ensayos porque permite integrar información de manera formal y fluida, evitando citas textuales largas que pueden interrumpir la lectura. Por ejemplo, en un informe científico es preferible parafrasear declaraciones de expertos usando tiempos verbales adecuados para mantener la coherencia temporal. En cambio, el estilo directo puede ser útil en presentaciones orales o cuando se quiere destacar la voz original del autor para enfatizar una idea o provocar un impacto emocional. La elección afecta la claridad porque el estilo indirecto ayuda a resumir y clarificar información, mientras que el directo ofrece precisión y autenticidad. Por ejemplo, en un ensayo podría escribirse: “El autor afirma que la tecnología mejora el aprendizaje” (estilo indirecto), en lugar de citar textualmente todo el párrafo. En resumen, la selección debe basarse en la intención comunicativa y el contexto para lograr efectividad y formalidad en el mensaje.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Estilo Directo e Indirecto","Evaluación Crítica","Primer Año Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (497, 107, 'open_ended', 'all', '{"pregunta": "Imagina que eres un periodista que debe presentar una entrevista escrita con un personaje histórico chileno. Diseña un texto que combine estilo directo e indirecto para contar la entrevista, asegurándote de que los tiempos verbales en estilo indirecto estén correctamente aplicados y que se refleje claramente el contraste entre ambos estilos en forma y significado. Explica brevemente por qué decidiste usar cada estilo en los fragmentos correspondientes.", "explicacion": "Se evaluará la habilidad del estudiante para aplicar correctamente los tiempos verbales en estilo indirecto, la cohesión y coherencia al alternar entre estilos, y la profundidad de su reflexión sobre las decisiones tomadas en la construcción del texto.", "contexto_adicional": "El texto debe tener una extensión aproximada de una página y representar una entrevista creíble, con preguntas y respuestas que permitan el uso de ambos estilos.", "criterios_evaluacion": ["Uso correcto y consistente de los tiempos verbales en estilo indirecto (peso: 40%)", "Integración clara y coherente del estilo directo e indirecto en el texto, reflejando el contraste en forma y significado (peso: 35%)", "Capacidad de reflexión y justificación sobre la elección y función de cada estilo en el texto (peso: 25%)"]}', '{"puntos_clave": ["Uso correcto del pretérito y otros tiempos en estilo indirecto", "Diferenciación clara entre citas textuales (estilo directo) y paráfrasis o resúmenes (estilo indirecto)", "Reflexión que muestra comprensión sobre la función comunicativa de cada estilo"], "respuesta_modelo": "Como periodista, entrevisté a Bernardo O''Higgins para conocer su visión sobre la independencia de Chile. Pregunté: \"¿Cuál fue el mayor desafío durante la lucha por la independencia?\" Él respondió que \"el mayor desafío fue unir a las diferentes regiones bajo una sola causa\". Más adelante, expliqué que O''Higgins destacó que la unidad fue fundamental para el éxito del movimiento, enfatizando que sin ella, la independencia habría sido imposible. En el texto, utilicé el estilo directo para presentar las palabras exactas del entrevistado y el estilo indirecto para resumir y explicar sus ideas, aplicando correctamente los tiempos verbales para mantener la coherencia temporal. Esta combinación permite que el lector se acerque a la voz original y, a la vez, comprenda el contexto general de sus declaraciones.", "requiere_revision_humana": true}', 4, 0, true, '{"estilo directo","estilo indirecto","tiempos verbales","creación de texto",entrevista}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (498, 107, 'open_ended', 'all', '{"pregunta": "Imagina que eres un periodista que debe presentar una entrevista tanto en un artículo escrito como en un programa radial. Crea un texto escrito y un guion para la presentación oral donde utilices de forma consistente el estilo directo e indirecto. Asegúrate de aplicar correctamente los tiempos verbales y reflexiona en una breve explicación final cómo el uso de cada estilo afecta el significado y la forma del mensaje que quieres transmitir.", "explicacion": "Se evaluará la capacidad del estudiante para distinguir y usar correctamente los estilos directo e indirecto, el manejo de tiempos verbales en el estilo indirecto, y la comprensión crítica sobre cómo cada estilo influye en la comunicación según el formato. La reflexión final es clave para evidenciar el nivel de análisis y creación.", "contexto_adicional": "El estudiante debe imaginar una entrevista a un personaje ficticio o real y adaptar la información para dos formatos distintos: un texto escrito y una presentación oral.", "criterios_evaluacion": ["Uso correcto y coherente del estilo directo e indirecto en ambos formatos (peso: 40%)", "Aplicación adecuada de los tiempos verbales correspondientes al estilo indirecto (peso: 30%)", "Reflexión clara y fundamentada sobre las diferencias de significado y forma entre los estilos directo e indirecto (peso: 30%)"]}', '{"puntos_clave": ["Diferenciación clara entre estilo directo e indirecto en ambas producciones", "Correcta conjugación de verbos en estilo indirecto", "Análisis reflexivo sobre cómo el cambio de estilo afecta el mensaje"], "respuesta_modelo": "Texto escrito: \"María afirmó: ''Estoy emocionada por el estreno de la obra''. Además, explicó que la preparación ha sido intensa durante meses.\"\n\nGuion para presentación oral: \"María dijo que está emocionada por el estreno de la obra y explicó que la preparación ha sido intensa durante meses.\"\n\nReflexión: En el texto escrito se utiliza el estilo directo para transmitir las palabras exactas de María, lo que genera un impacto más inmediato y vívido. En la presentación oral, el estilo indirecto permite una narración más fluida y resumida, adaptándose a la dinámica del medio. Además, los tiempos verbales cambian para mantener la coherencia temporal en el estilo indirecto, lo que modifica ligeramente el significado, enfocándose más en la interpretación que en la cita literal.\"", "requiere_revision_humana": true}', 5, 0, true, '{estilo_directo,estilo_indirecto,produccion_textual,presentacion_oral,primer_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (499, 107, 'concept_map', 'all', '{"conceptos": ["Estilo directo", "Estilo indirecto", "Tiempos verbales", "Cambio de pronombres", "Significado", "Forma", "Narración", "Cita textual"], "explicacion": "El mapa conceptual debe mostrar cómo el estilo directo y el indirecto se diferencian en forma y significado, cómo los tiempos verbales cambian al pasar del estilo directo al indirecto, el rol del cambio de pronombres en esta transformación, y cómo estos elementos se aplican en la narración y en la cita textual.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del uso del estilo directo e indirecto en textos escritos y orales?"}', '{"conceptos_centrales": ["Estilo directo", "Estilo indirecto"], "relaciones_esperadas": [{"origen": "Estilo directo", "destino": "Cita textual", "relacion": "usa"}, {"origen": "Estilo indirecto", "destino": "Cambio de pronombres", "relacion": "requiere"}, {"origen": "Estilo indirecto", "destino": "Tiempos verbales", "relacion": "modifica"}, {"origen": "Estilo directo", "destino": "Forma", "relacion": "se diferencia en"}, {"origen": "Estilo indirecto", "destino": "Forma", "relacion": "se diferencia en"}, {"origen": "Estilo directo", "destino": "Significado", "relacion": "se diferencia en"}, {"origen": "Estilo indirecto", "destino": "Significado", "relacion": "se diferencia en"}, {"origen": "Narración", "destino": "Estilo directo", "relacion": "utiliza"}, {"origen": "Narración", "destino": "Estilo indirecto", "relacion": "utiliza"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Estilo directo e indirecto","Mapa conceptual","Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (500, 107, 'concept_map', 'all', '{"conceptos": ["Estilo directo", "Estilo indirecto", "Tiempos verbales", "Forma del discurso", "Significado", "Cambio temporal", "Narrador", "Citas textuales"], "explicacion": "El mapa debe mostrar cómo el estilo directo e indirecto se diferencian en la forma del discurso y en el significado, especialmente en el uso y cambio de los tiempos verbales. También debe incluir la función del narrador y cómo se incorporan las citas textuales en el estilo directo, así como el efecto del cambio temporal en el estilo indirecto.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del uso del estilo directo e indirecto en textos escritos y orales?"}', '{"conceptos_centrales": ["Estilo directo", "Estilo indirecto"], "relaciones_esperadas": [{"origen": "Estilo directo", "destino": "Citas textuales", "relacion": "usa"}, {"origen": "Estilo indirecto", "destino": "Cambio temporal", "relacion": "implica"}, {"origen": "Cambio temporal", "destino": "Tiempos verbales", "relacion": "afecta"}, {"origen": "Estilo directo", "destino": "Forma del discurso", "relacion": "se diferencia en"}, {"origen": "Estilo indirecto", "destino": "Forma del discurso", "relacion": "se diferencia en"}, {"origen": "Estilo directo", "destino": "Significado original", "relacion": "preserva"}, {"origen": "Estilo indirecto", "destino": "Significado", "relacion": "puede modificar"}, {"origen": "Narrador", "destino": "Estilo indirecto", "relacion": "introduce"}], "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Estilo directo e indirecto","Tiempos verbales","Mapa conceptual","Primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (501, 107, 'open_ended', 'all', '{"pregunta": "Diseña un breve diálogo entre dos personajes que incluya al menos tres intervenciones en estilo directo y tres en estilo indirecto. Luego, explica cómo los cambios en los tiempos verbales afectan el significado y la forma entre ambos estilos, y reflexiona sobre por qué elegirías uno u otro en diferentes contextos comunicativos.", "explicacion": "El docente debe valorar cómo el estudiante aplica el conocimiento gramatical en la creación de un texto, su capacidad para analizar los efectos de los cambios verbales y su reflexión crítica sobre el uso comunicativo de los estilos, fomentando la creatividad y el pensamiento profundo.", "contexto_adicional": "El diálogo puede situarse en cualquier contexto de conversación cotidiana o literaria, pero debe ser original y mostrar dominio de los estilos directo e indirecto.", "criterios_evaluacion": ["Uso correcto y coherente de estilo directo e indirecto en el diálogo (peso: 40%)", "Análisis claro y preciso de los cambios en los tiempos verbales y su impacto en significado y forma (peso: 35%)", "Reflexión fundamentada sobre la elección del estilo en distintos contextos (peso: 25%)"]}', '{"puntos_clave": ["Incorporación de al menos tres intervenciones en estilo directo y tres en indirecto correctamente formuladas", "Explicación de los cambios en tiempos verbales (presente a pasado, pasado a pluscuamperfecto, etc.) y cómo afectan el sentido", "Reflexión sobre el uso contextual de cada estilo y su función comunicativa"], "respuesta_modelo": "Diálogo:\nMaría dijo: \"Estoy cansada después de la escuela.\" Juan respondió que él también estaba agotado y que había tenido un día difícil. Luego, María comentó: \"Quizás deberíamos descansar un poco.\" Juan sugirió que podrían tomar una siesta juntos. María afirmó que eso sería una buena idea.\n\nAnálisis:\nEn el estilo directo, los verbos se mantienen en presente o pasado conforme a la situación original, por ejemplo, \"estoy cansada\" mantiene el presente. Al pasar al estilo indirecto, los verbos cambian para reflejar el punto de vista temporal del narrador, como \"estaba cansada\" o \"había tenido\". Estos cambios afectan la percepción temporal y la relación entre las acciones narradas.\n\nReflexión:\nElegir el estilo directo suele ser más efectivo para transmitir emociones y dar vivacidad a la conversación, mientras que el estilo indirecto es útil para resumir o reportar información con distancia. Dependiendo del contexto, uno puede preferirse para enfatizar proximidad o neutralidad en la comunicación.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","estilo directo","estilo indirecto","primer medio","evaluación abierta","pensamiento crítico",creación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (502, 108, 'multiple_choice', 'all', '{"opciones": {"A": "\"El sol es un globo de fuego en el cielo.\"", "B": "\"El viento susurraba entre los árboles.\"", "C": "\"La corona del rey brilla con orgullo.\"", "D": "\"Compré pan, leche y queso en la tienda.\""}, "pregunta": "¿Cuál de las siguientes oraciones contiene un ejemplo de metáfora?", "explicacion": "La opción A es correcta porque compara el sol con un globo de fuego de manera implícita, lo que caracteriza a la metáfora. La opción B es un ejemplo de personificación, no metáfora. La opción C es metonimia, ya que ''corona'' se usa para referirse al rey. La opción D es una oración literal sin recursos de correferencia léxica, metáfora o metonimia."}', '{"respuesta_correcta": "A"}', 1, 0, true, '{metáfora,"correferencia léxica","Lengua y Literatura","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (503, 108, 'multiple_choice', 'all', '{"opciones": {"A": "El sol es un faro brillante en el cielo.", "B": "El perro persiguió su cola hasta cansarse.", "C": "Compró un auto nuevo el mes pasado.", "D": "El viento soplaba muy fuerte en la tarde."}, "pregunta": "¿Cuál de las siguientes oraciones contiene un ejemplo de metáfora?", "explicacion": "La opción A contiene una metáfora, ya que compara el sol con un faro brillante sin usar palabras comparativas como ''como'' o ''parece''. Las metáforas implican una relación directa entre dos elementos diferentes. La opción B describe una acción literal, sin figura retórica. La opción C es una oración factual sin metáfora ni metonimia. La opción D describe un fenómeno natural de manera literal. Así, la única metáfora presente está en la opción A."}', '{"respuesta_correcta": "A"}', 2, 0, true, '{metáfora,"correferencia léxica","Lengua y Literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (504, 108, 'multiple_choice', 'all', '{"opciones": {"A": "\"El sol sonreía en el cielo\"", "B": "\"la corona de luz iluminaba el valle\"", "C": "\"el valle\"", "D": "\"en el cielo\""}, "pregunta": "En el siguiente texto: \"El sol sonreía en el cielo y la corona de luz iluminaba el valle\", ¿cuál es un ejemplo de metáfora?", "explicacion": "La opción B es correcta porque \"la corona de luz\" es una metáfora que representa la luz que rodea al sol, atribuyéndole una cualidad humana o física distinta para crear una imagen poética. La opción A es un ejemplo de personificación, no de metáfora. Las opciones C y D son elementos literales sin uso de metáfora ni correferencia léxica."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"correferencia léxica",metáfora,"lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (506, 108, 'fill_blanks', 'all', '{"texto": "En un texto, la [BLANK_1] léxica permite que una palabra o expresión se refiera a otra ya mencionada, mientras que la [BLANK_2] es un recurso literario que reemplaza un término por otro relacionado para aportar significado, como ocurre en la metáfora y la metonimia.", "pistas": ["Es un recurso que conecta palabras en un texto para evitar repeticiones.", "Es un tipo de figura retórica que implica sustitución con base en una relación de contigüidad o semejanza."], "explicacion": "La correferencia léxica es un mecanismo que permite referirse a un elemento mencionado previamente en el texto mediante palabras relacionadas o equivalentes, evitando la repetición innecesaria. La metáfora y la metonimia son figuras retóricas que funcionan sustituyendo un término por otro, ya sea por semejanza (metáfora) o por una relación de contigüidad o causalidad (metonimia), enriqueciendo así el significado del texto."}', '{"respuestas_correctas": {"BLANK_1": ["correferencia", "correferencia léxica"], "BLANK_2": ["metáfora", "metonimia"]}}', 2, 0, true, '{"correferencia léxica",metáfora,metonimia,"figuras retóricas"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (507, 109, 'multiple_choice', 'all', '{"opciones": {"A": "La correferencia léxica compleja se usa para cambiar el tema principal, mientras que la metáfora y la metonimia solo decoran el texto sin aportar significado.", "B": "La correferencia léxica compleja conecta diferentes términos que se refieren a un mismo concepto, y la metáfora y la metonimia enriquecen el texto al establecer relaciones implícitas entre ideas o elementos.", "C": "La metáfora y la metonimia son tipos de correferencia léxica compleja que siempre sustituyen palabras por sinónimos exactos para evitar repeticiones.", "D": "La correferencia léxica compleja es un recurso que elimina la necesidad de usar metáforas y metonimias en un texto porque cumplen funciones opuestas."}, "pregunta": "¿Cuál de las siguientes afirmaciones explica correctamente la función de la correferencia léxica compleja, la metáfora y la metonimia en un texto?", "explicacion": "La opción B es correcta porque describe adecuadamente la función de la correferencia léxica compleja como la conexión entre términos que refieren al mismo concepto, y el papel de la metáfora y la metonimia en la creación de relaciones implícitas que enriquecen el significado del texto. La opción A es incorrecta porque la correferencia no cambia el tema principal ni la metáfora y metonimia son meramente decorativas. La opción C es errónea porque la metáfora y la metonimia no son tipos de correferencia ni sustituyen palabras por sinónimos exactos, sino que generan significados figurados. La opción D es falsa porque la correferencia y las figuras retóricas cumplen funciones complementarias y no opuestas."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"correferencia léxica",metáfora,metonimia,"comprensión textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (508, 109, 'multiple_choice', 'all', '{"opciones": {"A": "Repetir palabras para enfatizar el tema principal sin cambiar su significado.", "B": "Establecer relaciones entre términos diferentes para enriquecer el sentido y evitar repeticiones directas.", "C": "Simplificar el lenguaje para que el texto sea más fácil de entender, eliminando figuras retóricas.", "D": "Sustituir palabras por sinónimos exactos para mantener la coherencia léxica sin alterar el estilo."}, "pregunta": "¿Cuál es la función principal de la correferencia léxica compleja cuando se usa junto con metáforas y metonimias en un texto?", "explicacion": "La correferencia léxica compleja funciona para relacionar términos diferentes dentro de un texto, como el uso de metáforas y metonimias, que permiten enriquecer el significado y evitar la repetición directa de palabras. La opción A es incorrecta porque la correferencia no se basa en la repetición literal. La opción C es errónea ya que las metáforas y metonimias no simplifican el lenguaje, sino que lo hacen más complejo y sugerente. La opción D confunde correferencia con sinónimos exactos, pero la correferencia compleja involucra relaciones semánticas más amplias, no solo sustituciones directas."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"correferencia léxica",metáfora,metonimia,"lengua y literatura","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (509, 109, 'true_false', 'all', '{"afirmacion": "La metáfora y la metonimia son recursos que pueden usarse para establecer correferencia léxica compleja en un texto, ya que ambos permiten referirse a un elemento mediante otro relacionado.", "explicacion": "Esta afirmación es verdadera porque la correferencia léxica compleja implica el uso de expresiones que hacen referencia a un mismo referente a través de diferentes palabras o figuras retóricas. La metáfora y la metonimia son recursos que sustituyen un término por otro relacionado, facilitando así la correferencia y enriqueciendo el texto."}', '{"es_verdadero": true}', 2, 0, true, '{correferencia,metáfora,metonimia,comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (510, 109, 'drag_drop_matching', 'all', '{"explicacion": "La metáfora es un recurso que compara dos elementos distintos, como en el ejemplo \"Tus ojos son dos luceros\". La metonimia sustituye un término por otro relacionado, por ejemplo, \"Leí a Shakespeare\". La correferencia léxica compleja consiste en usar distintas expresiones para referirse al mismo referente, asegurando cohesión. La anáfora es un distractor que involucra repetición al inicio de frases, no correferencia.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Sustitución de un término por otro con el que guarda una relación de contigüidad", "Repetición de un término para mantener la cohesión en un texto", "Uso de diferentes expresiones para referirse al mismo referente en un texto", "\"Tus ojos son dos luceros\"", "\"Leí a Shakespeare\" (en lugar de ''leí las obras de Shakespeare'')", "Recurso retórico que repite una palabra al inicio de frases consecutivas"], "columna_izquierda": ["Metáfora", "Metonimia", "Correferencia léxica compleja", "Ejemplo de metáfora", "Ejemplo de metonimia", "Distractor: Anáfora"]}', '{"emparejamientos_correctos": {"Metonimia": "Sustitución de un término por otro con el que guarda una relación de contigüidad", "Metáfora": "\"Tus ojos son dos luceros\"", "Ejemplo de metonimia": "\"Leí a Shakespeare\" (en lugar de ''leí las obras de Shakespeare'')", "Ejemplo de metáfora": "\"Tus ojos son dos luceros\"", "Correferencia léxica compleja": "Uso de diferentes expresiones para referirse al mismo referente en un texto"}}', 2, 0, true, '{correferencia,metáfora,metonimia,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (511, 109, 'sequencing', 'all', '{"explicacion": "El orden inicia con la selección del fragmento, luego se identifican las correferencias léxicas, seguido del reconocimiento específico de metáforas y metonimias, para después analizar su aporte a la cohesión del texto, y finalmente explicar su función comunicativa, siguiendo un proceso lógico de comprensión y análisis.", "instruccion": "Ordena los siguientes pasos para identificar y explicar el uso de la correferencia léxica compleja, la metáfora y la metonimia en un texto.", "elementos_desordenados": ["Identificar las palabras o expresiones que se refieren a un mismo referente en el texto.", "Reconocer el uso de metáforas y metonimias que crean relaciones de significado entre los términos.", "Analizar cómo estas correferencias complejas contribuyen a la cohesión y el sentido del texto.", "Explicar con tus propias palabras la función comunicativa de estas figuras en el texto.", "Seleccionar un fragmento del texto que contenga metáforas, metonimias y correferencias léxicas complejas."]}', '{"orden_correcto": ["Seleccionar un fragmento del texto que contenga metáforas, metonimias y correferencias léxicas complejas.", "Identificar las palabras o expresiones que se refieren a un mismo referente en el texto.", "Reconocer el uso de metáforas y metonimias que crean relaciones de significado entre los términos.", "Analizar cómo estas correferencias complejas contribuyen a la cohesión y el sentido del texto.", "Explicar con tus propias palabras la función comunicativa de estas figuras en el texto."]}', 3, 0, true, '{correferencia_lexica,metafora,metonimia,comprension_textual}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (512, 110, 'multiple_choice', 'all', '{"opciones": {"A": "El sol lloraba en el cielo, mientras la ciudad despertaba con el ruido de sus calles.", "B": "La corona del rey brillaba al amanecer, y la corona de la montaña se teñía de luz dorada.", "C": "El río corría rápido, y el viento soplaba fuerte por la noche.", "D": "La luna salió y las estrellas brillaron, iluminando el campo oscuro."}, "pregunta": "¿Cuál de los siguientes fragmentos utiliza correctamente la correferencia léxica compleja mediante metáfora y metonimia en un texto narrativo?", "explicacion": "La opción B es correcta porque utiliza la correferencia léxica compleja: ''la corona del rey'' y ''la corona de la montaña'' emplean una metáfora y una metonimia para referirse a diferentes conceptos pero con la misma palabra, creando una relación figurada. La opción A presenta una metáfora (''el sol lloraba'') pero no establece una correferencia léxica compleja. La opción C es literal y no utiliza metáfora ni metonimia. La opción D es una descripción literal y no usa correferencia léxica compleja ni recursos figurados."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"correferencia léxica",metáfora,metonimia,"escritura creativa"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (513, 110, 'multiple_choice', 'all', '{"opciones": {"A": "El sol lloraba lágrimas doradas mientras la ciudad despertaba.", "B": "La corona brillaba en la cabeza del rey, símbolo del poder absoluto.", "C": "El río cantaba su canción mientras el viento soplaba con fuerza.", "D": "La luna es una lámpara que ilumina la noche oscura."}, "pregunta": "¿Cuál de los siguientes fragmentos utiliza correctamente la correferencia léxica compleja mediante metáfora y metonimia para enriquecer el texto?", "explicacion": "La opción B es correcta porque utiliza la metonimia (''la corona'' en lugar del rey) como correferencia léxica compleja que representa el poder del monarca, enlazando adecuadamente el significado figurado con el referente. La opción A usa una metáfora (''el sol lloraba lágrimas doradas'') pero no implica correferencia léxica compleja, sino una imagen poética aislada. La opción C personifica el río y el viento, pero no establece correferencia léxica compleja ni utiliza metáfora o metonimia que vincule términos. La opción D es una metáfora simple, pero no implica correferencia ni relaciona términos complejos dentro del texto."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"correferencia léxica",metáfora,metonimia,"lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (514, 110, 'drag_drop_matching', 'all', '{"explicacion": "La metáfora es una figura que establece una relación de semejanza entre dos elementos, como en el ejemplo \"Tus ojos son dos luceros\". La metonimia reemplaza un término por otro con relación de contigüidad, como \"Leí a Shakespeare\" que se refiere a sus obras. La correferencia léxica compleja implica usar diferentes términos que se refieren al mismo referente para mantener coherencia en el texto. El distractor hipérbole no corresponde con los demás conceptos.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Sustitución de un término por otro con el que mantiene una relación de contigüidad", "Expresión en la que una palabra o frase se usa para referirse a otra por semejanza", "Uso de distintos términos que hacen referencia a un mismo referente en un texto", "\"Tus ojos son dos luceros\"", "\"Leí a Shakespeare\" para referirse a sus obras", "Exageración intencional para enfatizar una idea"], "columna_izquierda": ["Metáfora", "Metonimia", "Correferencia léxica compleja", "Ejemplo de metáfora", "Ejemplo de metonimia", "Distractor: Hipérbole"]}', '{"emparejamientos_correctos": {"Metonimia": "Sustitución de un término por otro con el que mantiene una relación de contigüidad", "Metáfora": "Expresión en la que una palabra o frase se usa para referirse a otra por semejanza", "Ejemplo de metonimia": "\"Leí a Shakespeare\" para referirse a sus obras", "Ejemplo de metáfora": "\"Tus ojos son dos luceros\"", "Correferencia léxica compleja": "Uso de distintos términos que hacen referencia a un mismo referente en un texto"}}', 3, 0, true, '{"correferencia léxica",metáfora,metonimia,"lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (515, 110, 'sequencing', 'all', '{"explicacion": "El proceso comienza definiendo el tema y la idea central para tener un enfoque claro. Luego, se identifican los elementos que se referirán mediante correferencia léxica para estructurar adecuadamente el texto. A continuación, se escribe un borrador incluyendo estas referencias. Después, se incorporan metáforas y metonimias para enriquecer el texto y finalmente se revisa y ajusta para asegurar coherencia y uso correcto de los recursos.", "instruccion": "Ordena los siguientes pasos para crear un texto que use correferencia léxica compleja con metáfora y metonimia", "elementos_desordenados": ["Incorpora metáforas y metonimias para enriquecer el texto", "Identifica los elementos a los que se referirá mediante correferencia léxica", "Revisa y ajusta el texto para asegurar coherencia y uso adecuado de recursos", "Escribe un borrador que incluya referencias complejas entre palabras", "Define el tema y la idea central del texto"]}', '{"orden_correcto": ["Define el tema y la idea central del texto", "Identifica los elementos a los que se referirá mediante correferencia léxica", "Escribe un borrador que incluya referencias complejas entre palabras", "Incorpora metáforas y metonimias para enriquecer el texto", "Revisa y ajusta el texto para asegurar coherencia y uso adecuado de recursos"]}', 3, 0, true, '{correferencia,metáfora,metonimia,"producción de textos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (517, 111, 'compare_contrast', 'all', '{"conceptos": ["Metáfora", "Metonimia"], "criterios": ["Relación entre términos", "Función en el texto", "Ejemplo típico"], "explicacion": "La metáfora establece una relación de semejanza entre dos términos diferentes para enriquecer el texto, mientras que la metonimia se basa en una relación de contigüidad o asociación directa entre conceptos. Ambas funcionan como recursos de correferencia léxica que aportan significado, pero difieren en cómo conectan los elementos y en el tipo de imagen que generan.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Metonimia": {"Ejemplo típico": "‘Le hizo daño el sol’ (calor del sol)", "Función en el texto": "Sustituir un término por otro relacionado para simplificar o intensificar el mensaje", "Relación entre términos": "Contigüidad o asociación directa (parte-todo, causa-efecto, etc.)"}, "Metáfora": {"Ejemplo típico": "‘Las perlas de tu boca’ (dientes)", "Función en el texto": "Crear imágenes y enriquecer el significado mediante la comparación implícita", "Relación entre términos": "Semejanza o analogía entre dos elementos distintos"}}}', 3, 0, true, '{correferencia_lexica,metafora,metonimia,comparacion}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (518, 111, 'compare_contrast', 'all', '{"conceptos": ["Metáfora", "Metonimia"], "criterios": ["Relación entre los términos", "Tipo de sustitución", "Ejemplo común"], "explicacion": "La metáfora establece una relación de semejanza o analogía entre dos elementos diferentes, sustituyendo uno por otro para crear una imagen poética o conceptual. La metonimia, en cambio, utiliza una relación contigua o de proximidad entre los elementos, sustituyendo un término por otro que está relacionado espacial, causal o temporalmente. Ambas son figuras retóricas que enriquecen el texto, pero difieren en el tipo de vínculo que establecen entre los términos.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Metonimia": {"Ejemplo común": "\"Leí a Shakespeare\" (por sus obras)", "Tipo de sustitución": "Sustitución basada en la relación espacial, causal o temporal", "Relación entre los términos": "Contigüidad o proximidad"}, "Metáfora": {"Ejemplo común": "\"Tus ojos son estrellas\"", "Tipo de sustitución": "Sustitución basada en la semejanza", "Relación entre los términos": "Semejanza o analogía"}}}', 4, 0, true, '{"correferencia léxica","figuras retóricas",comparación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (519, 111, 'open_ended', 'all', '{"pregunta": "Analiza dos textos distintos (uno literario y otro no literario) que contengan ejemplos de correferencia léxica, metáfora y metonimia. Compara cómo se utilizan estos recursos en cada texto y explica cómo contribuyen al significado y efecto comunicativo en ambos casos.", "explicacion": "El docente debe evaluar que el estudiante no solo identifique correctamente los recursos, sino que también realice un análisis comparativo profundo que muestre comprensión de sus funciones comunicativas y estilísticas en diferentes tipos de texto.", "contexto_adicional": "Se proporcionarán dos textos breves para análisis: un fragmento literario (por ejemplo, un poema o cuento corto) y un texto no literario (como un artículo de opinión o noticia).", "criterios_evaluacion": ["Identificación precisa y correcta de ejemplos de correferencia léxica, metáfora y metonimia en ambos textos (peso: 30%)", "Análisis comparativo claro y fundamentado que explique las diferencias y similitudes en el uso de estos recursos en textos literarios y no literarios (peso: 40%)", "Explicación coherente sobre cómo cada recurso contribuye al significado y efecto comunicativo en los textos analizados (peso: 30%)"]}', '{"puntos_clave": ["Identificación correcta de ejemplos de correferencia léxica, metáfora y metonimia en ambos textos", "Comparación de la función y uso de estos recursos en textos literarios versus no literarios", "Explicación del aporte de estos recursos al significado y efecto comunicativo en cada texto"], "respuesta_modelo": "En el texto literario, se observa el uso de correferencia léxica a través de pronombres que retoman sustantivos previos, por ejemplo, ''él'' que refiere al protagonista. La metáfora aparece en la frase ''las alas de la libertad'', donde se asocia la libertad con la capacidad de volar, generando una imagen poética. La metonimia se encuentra en ''bebió la copa'', donde ''copa'' representa el contenido alcohólico. En contraste, en el texto no literario, la correferencia léxica se usa para evitar repeticiones y clarificar ideas, como en ''el presidente anunció... él también destacó...''. La metáfora es menos frecuente, pero aparece en la expresión ''el motor de la economía'', donde ''motor'' sustituye la idea de fuerza impulsora. La metonimia se usa en frases como ''la Casa Blanca respondió'', donde ''Casa Blanca'' representa a la administración estadounidense. En resumen, mientras en el texto literario estos recursos tienen un valor estético y emocional, en el no literario sirven para claridad y concisión comunicativa, aunque también pueden aportar énfasis o figuras expresivas para reforzar el mensaje.", "requiere_revision_humana": true}', 4, 0, true, '{"correferencia léxica",metáfora,metonimia,"análisis comparativo","lengua y literatura","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (520, 111, 'concept_map', 'all', '{"conceptos": ["Correferencia léxica", "Metáfora", "Metonimia", "Textos literarios", "Textos no literarios"], "explicacion": "Se espera que el estudiante muestre cómo la correferencia léxica funciona como un recurso que puede incluir o emplear la metáfora y la metonimia para establecer conexiones dentro del texto. Además, debe diferenciar cómo estos recursos se manifiestan y cumplen funciones distintas en textos literarios frente a textos no literarios.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del uso de recursos de correferencia léxica compleja en diferentes tipos de textos?"}', '{"conceptos_centrales": ["Correferencia léxica", "Metáfora"], "relaciones_esperadas": [{"origen": "Correferencia léxica", "destino": "Metáfora", "relacion": "incluye"}, {"origen": "Correferencia léxica", "destino": "Metonimia", "relacion": "incluye"}, {"origen": "Metáfora", "destino": "Textos literarios", "relacion": "se usa en"}, {"origen": "Metonimia", "destino": "Textos literarios", "relacion": "se usa en"}, {"origen": "Metáfora", "destino": "Textos no literarios", "relacion": "puede usarse en"}, {"origen": "Metonimia", "destino": "Textos no literarios", "relacion": "puede usarse en"}, {"origen": "Correferencia léxica", "destino": "Textos literarios", "relacion": "se manifiesta en"}, {"origen": "Correferencia léxica", "destino": "Textos no literarios", "relacion": "se manifiesta en"}], "requiere_revision_humana": true}', 3, 0, true, '{correferencia,metáfora,metonimia,"textos literarios","análisis textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (521, 111, 'concept_map', 'all', '{"conceptos": ["Correferencia léxica compleja", "Metáfora", "Metonimia", "Textos literarios", "Textos no literarios"], "explicacion": "El mapa conceptual debe mostrar cómo la correferencia léxica compleja se manifiesta a través de recursos como la metáfora y la metonimia, y cómo estos recursos se emplean de manera distinta en textos literarios y no literarios para lograr efectos comunicativos específicos.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del uso de recursos de correferencia léxica en diferentes tipos de textos?"}', '{"conceptos_centrales": ["Correferencia léxica compleja", "Textos literarios"], "relaciones_esperadas": [{"origen": "Correferencia léxica compleja", "destino": "Metáfora", "relacion": "se manifiesta mediante"}, {"origen": "Correferencia léxica compleja", "destino": "Metonimia", "relacion": "se manifiesta mediante"}, {"origen": "Metáfora", "destino": "Textos literarios", "relacion": "se utiliza en"}, {"origen": "Metonimia", "destino": "Textos literarios", "relacion": "se utiliza en"}, {"origen": "Metonimia", "destino": "Textos no literarios", "relacion": "se utiliza en"}, {"origen": "Metáfora", "destino": "Textos no literarios", "relacion": "uso menos frecuente en"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",Correferencia,Metáfora,Metonimia,"Mapa conceptual",Análisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (522, 112, 'criteria_evaluation', 'all', '{"rubrica": {"Uso de la metonimia": {"Excelente": "Identifica claramente la metonimia y justifica con argumentos sólidos su contribución al sentido y coherencia del texto.", "Satisfactorio": "Detecta la metonimia, aunque la justificación puede ser incompleta o superficial.", "Necesita mejorar": "No reconoce la metonimia ni su función en el texto."}, "Justificación argumentada": {"Excelente": "Presenta una justificación coherente, bien estructurada y fundamentada para las evaluaciones asignadas a cada criterio.", "Satisfactorio": "La justificación es clara pero puede carecer de profundidad o ejemplos concretos.", "Necesita mejorar": "La justificación es vaga, poco clara o ausente."}, "Aplicación de la metáfora": {"Excelente": "Analiza de forma profunda y precisa las metáforas presentes, explicando su función en el texto y su impacto en el significado.", "Satisfactorio": "Reconoce las metáforas y brinda una explicación general sobre su función, pero con limitaciones en profundidad o precisión.", "Necesita mejorar": "No identifica ni explica las metáforas o su relevancia en el texto."}, "Uso de correferencia léxica compleja": {"Excelente": "Identifica y explica claramente el empleo adecuado de correferencias léxicas complejas que contribuyen a la cohesión del texto.", "Satisfactorio": "Reconoce algunas correferencias léxicas complejas, aunque la explicación puede ser parcial o poco precisa.", "Necesita mejorar": "No logra identificar ni explicar el uso de correferencias léxicas complejas en el texto."}}, "explicacion": "Se espera que el estudiante identifique y explique cómo la correferencia léxica compleja, la metáfora y la metonimia contribuyen a la coherencia y profundidad del texto, además de argumentar con claridad sus evaluaciones basadas en la rúbrica.", "instruccion": "Evalúa el fragmento según cada criterio de la rúbrica y justifica detalladamente tu calificación para cada uno.", "caso_a_evaluar": "Lee el siguiente fragmento de un poema y evalúa el uso de la correferencia léxica compleja, así como la aplicación de la metáfora y la metonimia para construir coherencia y profundidad en el texto:\n\n\"El sol, viejo guardián de días perdidos,\nsusurra secretos en lenguas doradas;\nla corona de fuego se desvanece,\ny la sombra, su reina sin trono, danza.\""}', '{"evaluacion_modelo": {"Uso de la metonimia": "Satisfactorio", "Justificación argumentada": "Excelente", "Aplicación de la metáfora": "Excelente", "Uso de correferencia léxica compleja": "Excelente"}, "justificacion_modelo": "El fragmento utiliza correferencia léxica compleja mediante expresiones como \"el sol\" y \"la corona de fuego\", que se refieren a la misma entidad, asegurando coherencia. Las metáforas \"viejo guardián de días perdidos\" y \"la sombra, su reina sin trono\" están bien identificadas y explican la función poética y simbólica. La metonimia está presente en \"corona de fuego\" para referirse al sol, pero su explicación puede ser más detallada. La justificación es clara y fundamentada en ejemplos textuales.", "requiere_revision_humana": true}', 4, 0, true, '{"correferencia léxica",metáfora,metonimia,evaluación,"primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (523, 112, 'criteria_evaluation', 'all', '{"rubrica": {"Justificación de la efectividad": {"Excelente": "Argumenta claramente por qué el uso de estos recursos mejora el texto, apoyándose en ejemplos específicos del fragmento.", "Satisfactorio": "Da una justificación general del aporte de los recursos al texto, con ejemplos poco desarrollados.", "Necesita mejorar": "Su justificación es vaga, poco clara o no utiliza ejemplos del fragmento."}, "Análisis de metáfora y metonimia": {"Excelente": "Describe con precisión las metáforas y metonimias presentes, explicando su significado y relevancia para el mensaje del texto.", "Satisfactorio": "Reconoce algunas metáforas y metonimias y ofrece una interpretación básica de su función en el texto.", "Necesita mejorar": "No identifica las metáforas ni metonimias o su interpretación es incorrecta o superficial."}, "Uso de correferencia léxica compleja": {"Excelente": "Identifica correctamente todas las instancias de correferencia léxica compleja y explica cómo contribuyen a la cohesión del texto.", "Satisfactorio": "Reconoce algunas instancias de correferencia léxica compleja y menciona su aporte general a la cohesión del texto.", "Necesita mejorar": "No identifica o confunde las instancias de correferencia léxica compleja y no explica su función en el texto."}}, "explicacion": "Se espera que el estudiante identifique y analice los recursos literarios usados, explicando cómo contribuyen a la cohesión y significado del texto, y que argumente con claridad su valoración en base a ejemplos concretos.", "instruccion": "Evalúa el fragmento literario según la rúbrica presentada y justifica tu calificación en cada criterio con ejemplos del texto.", "caso_a_evaluar": "Lee el siguiente fragmento de un texto literario donde el autor utiliza correferencia léxica compleja, metáfora y metonimia para construir sentido:\n\n\"La ciudad despertaba con un murmullo constante; sus calles, venas de un cuerpo vivo, palpitan al ritmo de los pasos invisibles. El sol, un pintor incansable, colorea los muros con tonos dorados, mientras las sombras, testigos silenciosos, guardan secretos antiguos.\"\n\nEvalúa la efectividad del uso de correferencia léxica compleja, metáfora y metonimia en este fragmento."}', '{"evaluacion_modelo": {"Justificación de la efectividad": "Excelente", "Análisis de metáfora y metonimia": "Excelente", "Uso de correferencia léxica compleja": "Excelente"}, "justificacion_modelo": "El estudiante identifica correctamente expresiones como \"sus calles, venas de un cuerpo vivo\" que es una metáfora, y reconoce que ''venas'' se refiere a ''calles'' mediante correferencia léxica compleja, explicando cómo esto aporta cohesión y vivacidad al texto. También explica que el sol como \"pintor incansable\" es una metáfora que enriquece la imagen visual. Justifica que estos recursos hacen el texto más evocador y coherente, apoyándose en ejemplos específicos.", "requiere_revision_humana": true}', 5, 0, true, '{correferencia,metáfora,metonimia,evaluación,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (524, 112, 'criteria_evaluation', 'all', '{"rubrica": {"Capacidad para fundamentar la calificación": {"Excelente": "Presenta argumentos sólidos, coherentes y bien estructurados que sustentan la evaluación realizada.", "Satisfactorio": "Presenta argumentos que sustentan la evaluación, aunque pueden ser poco elaborados o poco claros.", "Necesita mejorar": "No presenta argumentos claros o coherentes que justifiquen la calificación dada."}, "Evaluación del uso de la metáfora y la metonimia": {"Excelente": "Evalúa con precisión cómo la metáfora y la metonimia enriquecen el texto, aportando significado y profundidad, con ejemplos claros.", "Satisfactorio": "Comprende que la metáfora y metonimia aportan al texto, pero la evaluación es básica y poco detallada.", "Necesita mejorar": "No logra reconocer o evaluar adecuadamente el uso de metáfora y metonimia en el texto."}, "Justificación del uso de correferencia léxica compleja": {"Excelente": "La justificación explica con claridad y profundidad cómo la correferencia léxica compleja contribuye a la cohesión del texto, mencionando ejemplos específicos.", "Satisfactorio": "La justificación reconoce la función de la correferencia léxica compleja en el texto, pero con explicaciones generales y pocos ejemplos concretos.", "Necesita mejorar": "La justificación es confusa o superficial, sin relacionar adecuadamente la correferencia léxica con la cohesión textual."}}, "explicacion": "Se espera que el estudiante identifique y valore cómo la correferencia léxica compleja, la metáfora y la metonimia contribuyen a la cohesión y profundidad del texto, justificando sus respuestas con ejemplos específicos y argumentos claros.", "instruccion": "Evalúa el texto según la rúbrica propuesta y justifica tu calificación en cada criterio, fundamentando tu opinión con ejemplos concretos del texto.", "caso_a_evaluar": "Se presenta un texto narrativo breve que utiliza correferencia léxica compleja a través de metáforas y metonimias para construir el relato. Analiza cómo estos recursos contribuyen a la cohesión y profundidad del texto."}', '{"evaluacion_modelo": {"Capacidad para fundamentar la calificación": "Excelente", "Evaluación del uso de la metáfora y la metonimia": "Excelente", "Justificación del uso de correferencia léxica compleja": "Satisfactorio"}, "justificacion_modelo": "La correferencia léxica compleja está identificada y su función reconocida, aunque con explicaciones generales. En cambio, el uso de metáfora y metonimia fue evaluado con precisión, describiendo cómo enriquecen el texto con ejemplos claros. La fundamentación de la calificación fue sólida y coherente, sustentando adecuadamente la evaluación.", "requiere_revision_humana": true}', 5, 0, true, '{correferencia,metáfora,metonimia,evaluación,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (525, 112, 'open_ended', 'all', '{"pregunta": "Analiza un texto literario que contenga ejemplos de correferencia léxica compleja mediante metáfora y metonimia. Justifica cómo estos recursos enriquecen la coherencia y el significado del texto, y evalúa su efectividad en la construcción del mensaje global.", "explicacion": "El docente debe valorar que el estudiante no solo reconozca los recursos literarios, sino que también analice su función y aporte semántico en el texto, demostrando un juicio crítico fundamentado. Se recomienda observar la coherencia argumentativa y el uso correcto de conceptos literarios.", "contexto_adicional": "Se puede utilizar un texto literario breve proporcionado en clase o un fragmento seleccionado por el estudiante que contenga ambos recursos estilísticos.", "criterios_evaluacion": ["Identificación precisa de ejemplos de correferencia léxica compleja, metáfora y metonimia en el texto (peso: 30%)", "Capacidad para justificar con argumentos claros cómo estos recursos contribuyen a la coherencia y profundidad del texto (peso: 40%)", "Evaluación crítica sobre la efectividad de los recursos en la construcción del mensaje global, con uso adecuado de terminología literaria (peso: 30%)"]}', '{"puntos_clave": ["Identificación clara de metáfora y metonimia como formas de correferencia léxica compleja", "Justificación del aporte de estos recursos a la coherencia y al significado del texto", "Evaluación crítica de la efectividad en la construcción del mensaje global usando terminología adecuada"], "respuesta_modelo": "En el texto seleccionado, se identifican metáforas como ''el río de la vida'' que representa el transcurso del tiempo, y metonimias como ''la corona'' para referirse al poder. Estos recursos funcionan como correferencias léxicas complejas que mantienen la cohesión referencial sin repetir palabras explícitas. La metáfora enriquece el significado al ofrecer una imagen vívida que conecta emocionalmente con el lector, mientras que la metonimia aporta concreción y familiaridad cultural. En conjunto, estos recursos refuerzan la coherencia del texto al enlazar ideas abstractas con elementos concretos, facilitando la comprensión y profundizando el mensaje global sobre la inevitabilidad del cambio y la autoridad. Por lo tanto, su uso es altamente efectivo para lograr un texto coherente y significativo.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Correferencia léxica",Metáfora,Metonimia,"Análisis literario","Evaluación crítica"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (526, 112, 'open_ended', 'all', '{"pregunta": "Analiza un texto literario que utilice correferencia léxica compleja con metáforas y metonimias. ¿De qué manera estos recursos contribuyen a la coherencia y profundidad del texto? Justifica tu respuesta con ejemplos específicos del texto.", "explicacion": "El docente debe evaluar que el estudiante no solo reconozca los recursos literarios, sino que también explique cómo éstos funcionan para articular el texto, aportando sentido y riqueza expresiva. Se debe valorar la argumentación y el uso de ejemplos concretos para sustentar sus ideas.", "contexto_adicional": "Se puede utilizar un fragmento literario previamente leído en clase o un texto seleccionado por el docente que contenga recursos de correferencia léxica compleja, metáfora y metonimia.", "criterios_evaluacion": ["Identificación y explicación precisa de la correferencia léxica compleja, metáforas y metonimias en el texto (peso: 40%)", "Análisis crítico de cómo estos recursos aportan a la coherencia y profundidad del texto (peso: 40%)", "Claridad, coherencia y organización en la justificación presentada (peso: 20%)"]}', '{"puntos_clave": ["Reconocimiento correcto de correferencia léxica compleja, metáfora y metonimia", "Análisis de la función de estos recursos en la coherencia textual", "Justificación clara con ejemplos concretos extraídos del texto"], "respuesta_modelo": "En el texto, la correferencia léxica compleja se observa en el uso de sinónimos y términos relacionados que mantienen la unidad temática, como ''la ciudad'', ''la metrópoli'' y ''este lugar''. La metáfora aparece en la frase ''la ciudad es un corazón palpitante'', que atribuye cualidades vitales a la ciudad y genera una imagen poderosa. La metonimia se evidencia en ''las ruedas rugieron'' para referirse a los automóviles. Estos recursos contribuyen a la coherencia al conectar elementos del texto y evitar repeticiones directas, mientras que profundizan el significado al añadir capas de interpretación y emoción, haciendo el texto más expresivo y envolvente.", "requiere_revision_humana": true}', 5, 0, true, '{correferencia,metáfora,metonimia,"análisis literario",evaluación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (527, 113, 'open_ended', 'all', '{"pregunta": "Diseña un texto original en el que utilices recursos de correferencia léxica compleja, incluyendo metáforas y metonimias, para construir un mensaje claro y creativo. Explica cómo cada recurso contribuye a la cohesión y al significado del texto.", "explicacion": "Se evaluará la capacidad del estudiante para aplicar recursos lingüísticos complejos en un texto propio, valorando tanto la variedad y precisión en el uso de la correferencia léxica como la creatividad y pertinencia en el uso de metáforas y metonimias. Además, se considera fundamental que el estudiante demuestre comprensión explicando cómo estos recursos aportan a la cohesión y el significado general del texto.", "contexto_adicional": "El texto puede abordar un tema libre, pero debe ser original y tener al menos 15 líneas para permitir la integración adecuada de los recursos solicitados.", "criterios_evaluacion": ["Uso adecuado y variado de recursos de correferencia léxica compleja para mantener la cohesión textual (peso: 30%)", "Incorporación creativa y pertinente de metáforas y metonimias que enriquezcan el sentido del texto (peso: 40%)", "Claridad en la explicación del aporte de cada recurso al mensaje y cohesión del texto (peso: 30%)"]}', '{"puntos_clave": ["Identificación y uso correcto de expresiones que mantienen la correferencia, evitando repeticiones innecesarias", "Empleo de metáforas como ''jardín de la vida'' y ''mente, un invernadero de sueños'' para dar profundidad y riqueza al texto", "Uso de metonimias como ''pluma'' para representar la escritura y ''reloj de arena'' para el tiempo, integradas coherentemente"], "respuesta_modelo": "En el vasto jardín de la vida, cada pensamiento es una semilla que germina bajo el sol de la experiencia. Mi mente, un invernadero de sueños, cultiva esperanzas que florecen en colores que no existen en el arcoíris. La pluma, mi fiel aliada, dibuja caminos invisibles donde el alma se encuentra con el horizonte. En este viaje, las palabras son barcos que navegan entre océanos de significado, y el tiempo, reloj de arena eterno, mide cada instante con paciencia infinita. Así, la memoria se convierte en un faro que guía mis pasos en la oscuridad, y la creatividad, el viento que impulsa mis velas hacia destinos desconocidos.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Correferencia Léxica",Metáfora,Metonimia,Creatividad,"Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (528, 113, 'open_ended', 'all', '{"pregunta": "Crea un texto narrativo breve (máximo 250 palabras) en el que utilices recursos de correferencia léxica compleja, incluyendo metáforas y metonimias, para construir una historia coherente y creativa. Explica brevemente al final cómo empleaste estos recursos para enriquecer tu texto.", "explicacion": "El docente debe evaluar que el estudiante aplique correctamente la correferencia léxica compleja para evitar repeticiones innecesarias, que identifique y utilice metáforas y metonimias de forma creativa para enriquecer el texto, y que el texto resultante sea claro y coherente, tanto en contenido como en la explicación que justifica el uso de dichos recursos.", "contexto_adicional": "Puedes elegir cualquier tema o escenario para tu texto, siempre que los recursos lingüísticos mencionados estén presentes y sean efectivos para la comprensión y estética del texto.", "criterios_evaluacion": ["Uso adecuado y coherente de correferencia léxica compleja para mantener la cohesión textual (peso: 30%)", "Integración creativa y precisa de metáforas y metonimias que aporten significado y profundidad al texto (peso: 40%)", "Claridad y coherencia en la estructura del texto y en la explicación del uso de los recursos (peso: 30%)"]}', '{"puntos_clave": ["Empleo correcto y variado de correferencia léxica compleja para evitar repeticiones directas", "Uso explícito y creativo de metáforas y metonimias que aportan significado simbólico o estético", "Explicación clara que conecta los recursos lingüísticos con el efecto logrado en el texto"], "respuesta_modelo": "En el crepúsculo del pueblo, la luna era un farol encendido que guiaba los pasos de los caminantes. Las calles, venas dormidas, aguardaban el murmullo del viento. Ella, la sombra que susurraba secretos, recorrió el sendero sin nombre. El reloj de la plaza, testigo mudo, marcaba el latido pausado de la noche. En este escenario, la soledad no era ausencia, sino un océano inmenso donde nadaba el alma. \n\nEn este texto, utilicé correferencia léxica compleja a través de sinónimos y expresiones como “crepúsculo del pueblo” para referirme al atardecer, y “las calles, venas dormidas” para aludir a las calles sin repetir el sustantivo. La metáfora está presente en “la luna era un farol encendido” y “las calles, venas dormidas”, que atribuyen cualidades humanas o de objetos distintos para enriquecer la imagen. La metonimia se observa en “el reloj de la plaza” como símbolo del paso del tiempo y “la sombra que susurraba secretos” como representación de una persona misteriosa. Estos recursos aportan profundidad y ritmo al texto, evitando repeticiones y generando imágenes vívidas.", "requiere_revision_humana": true}', 5, 0, true, '{"correferencia léxica",metáfora,metonimia,"creación textual","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (529, 113, 'concept_map', 'all', '{"conceptos": ["Correferencia léxica compleja", "Metáfora", "Metonimia", "Texto original", "Recursos literarios"], "explicacion": "Se espera que el estudiante organice el mapa conceptual mostrando que la correferencia léxica compleja es un recurso que puede emplearse en un texto original, y que tanto la metáfora como la metonimia son tipos específicos de recursos literarios que contribuyen a la correferencia. Además, el texto original integra estos elementos para lograr cohesión y creatividad.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la creación textual en Lengua y Literatura?"}', '{"conceptos_centrales": ["Correferencia léxica compleja", "Texto original"], "relaciones_esperadas": [{"origen": "Metáfora", "destino": "Recursos literarios", "relacion": "es un tipo de"}, {"origen": "Metonimia", "destino": "Recursos literarios", "relacion": "es un tipo de"}, {"origen": "Correferencia léxica compleja", "destino": "Metáfora", "relacion": "utiliza"}, {"origen": "Correferencia léxica compleja", "destino": "Metonimia", "relacion": "utiliza"}, {"origen": "Texto original", "destino": "Correferencia léxica compleja", "relacion": "integra"}, {"origen": "Texto original", "destino": "Recursos literarios", "relacion": "contiene"}], "requiere_revision_humana": true}', 4, 0, true, '{correferencia,metáfora,metonimia,"mapa conceptual","creación textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (530, 113, 'concept_map', 'all', '{"conceptos": ["Correferencia léxica compleja", "Metáfora", "Metonimia", "Texto original", "Recursos literarios", "Creatividad", "Relación semántica"], "explicacion": "El mapa conceptual debe mostrar cómo los recursos literarios como la metáfora y la metonimia se utilizan para construir correferencia léxica compleja dentro de un texto original. Además, debe reflejar la importancia de la creatividad en el uso de estos recursos y cómo las relaciones semánticas entre términos facilitan la cohesión textual.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la creación de textos que integran correferencia léxica compleja, metáfora y metonimia?"}', '{"conceptos_centrales": ["Correferencia léxica compleja", "Recursos literarios"], "relaciones_esperadas": [{"origen": "Metáfora", "destino": "Recursos literarios", "relacion": "es un tipo de"}, {"origen": "Metonimia", "destino": "Recursos literarios", "relacion": "es un tipo de"}, {"origen": "Recursos literarios", "destino": "Correferencia léxica compleja", "relacion": "contribuyen a"}, {"origen": "Correferencia léxica compleja", "destino": "Texto original", "relacion": "se utiliza en"}, {"origen": "Creatividad", "destino": "Texto original", "relacion": "es fundamental para"}, {"origen": "Relación semántica", "destino": "Correferencia léxica compleja", "relacion": "permite"}], "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura",Correferencia,Metáfora,Metonimia,"Creación de texto"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (531, 113, 'open_ended', 'all', '{"pregunta": "Crea un texto original (puede ser un poema, relato breve o ensayo) que utilice recursos de correferencia léxica compleja y que integre de manera creativa y adecuada metáforas y metonimias para enriquecer su contenido. Explica cómo estos recursos contribuyen al significado y la cohesión del texto.", "explicacion": "El docente debe evaluar que el estudiante no solo utilice los recursos lingüísticos solicitados, sino que también los emplee con intencionalidad artística y comunicativa, verificando la originalidad y la capacidad de análisis reflejada en la explicación.", "contexto_adicional": "El texto debe ser coherente y reflejar un manejo consciente de los recursos literarios para comunicar una idea o experiencia de forma atractiva y cohesionada.", "criterios_evaluacion": ["Originalidad y creatividad en la construcción del texto (peso: 30%)", "Uso correcto y variado de recursos de correferencia léxica compleja para mantener la cohesión textual (peso: 40%)", "Incorporación adecuada y efectiva de metáforas y metonimias, con explicación clara de su función en el texto (peso: 30%)"]}', '{"puntos_clave": ["Texto original con estructura coherente", "Empleo correcto de correferencia léxica compleja para cohesión", "Incorporación clara y creativa de metáforas y metonimias con explicación de su función"], "respuesta_modelo": "En el crepúsculo de la ciudad dormida, las luces eran ojos que vigilaban el silencio. La noche, un manto negro que abrazaba los secretos, susurraba historias antiguas. Caminé entre sus calles, donde el asfalto era una lengua que contaba memorias y los edificios, gigantes cansados, guardaban el aliento del tiempo. En este relato, el uso de pronombres y sinónimos mantiene la correferencia léxica, mientras que la metáfora ''las luces eran ojos'' y la metonimia ''el asfalto era una lengua'' enriquecen la imagen sensorial y el significado, haciendo que el texto tenga cohesión y profundidad.", "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,creacion_textual,recursos_literarios}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (532, 114, 'multiple_choice', 'all', '{"opciones": {"A": "El arbol tiene hojas verdes, y flores muy bonitas.", "B": "El árbol tiene hojas verdes y flores muy bonitas.", "C": "El arbol tiene hojas verdes y flores, muy bonitas.", "D": "El árbol tiene hojas verdes, y flores muy bonitas"}, "pregunta": "¿Cuál de las siguientes oraciones está correctamente escrita según las reglas básicas de ortografía literal, acentual y puntuación?", "explicacion": "La opción B es correcta porque ''árbol'' lleva tilde según las reglas de acentuación, y no se usa coma antes de la conjunción ''y'' cuando une dos elementos en una oración simple. La opción A no lleva tilde en ''arbol'', lo cual es incorrecto. La opción C omite la tilde y usa una coma innecesaria antes de ''muy bonitas''. La opción D aunque tilda correctamente ''árbol'', usa una coma innecesaria antes de ''y'' y no termina con punto."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{ortografía,acentuación,puntuación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (533, 114, 'multiple_choice', 'all', '{"opciones": {"A": "El árbol tiene muchas hojas; además, está muy alto.", "B": "El arbol tiene muchas hojas, ademas está muy alto.", "C": "El árbol tiene muchas hojas ademas está muy alto", "D": "El árbol tiene muchas hojas además está muy alto."}, "pregunta": "¿Cuál de las siguientes oraciones está correctamente escrita según las reglas básicas de ortografía literal, acentual y puntuación?", "explicacion": "La opción A es correcta porque utiliza correctamente la tilde en ''árbol'' y en ''además'', y emplea adecuadamente el punto y coma para separar ideas relacionadas pero independientes. La opción B presenta errores en la tilde de ''árbol'' y ''además'', además de que la coma no es suficiente para separar correctamente las ideas; la opción C carece de comas y tildes necesarias, lo que dificulta la comprensión; la opción D tiene buena ortografía pero debería usar un punto y coma o un punto para separar las oraciones, ya que unirlas solo con ''además'' sin puntuación correcta es incorrecto."}', '{"respuesta_correcta": "A"}', 2, 0, true, '{ortografía,acentuación,puntuación,"nivel 1"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (534, 114, 'multiple_choice', 'all', '{"opciones": {"A": "El árbol esta en el jardin, y tiene muchas flores.", "B": "El árbol está en el jardín, y tiene muchas flores.", "C": "El arbol está en el jardin y tiene muchas flores", "D": "El árbol esta en el jardín y tiene muchas flores."}, "pregunta": "¿Cuál de las siguientes oraciones cumple correctamente con las reglas básicas de ortografía literal, acentual y uso de signos de puntuación?", "explicacion": "La opción B es correcta porque aplica correctamente la ortografía literal (uso de la tilde en ''está'' y ''jardín''), y usa adecuadamente la coma para separar las oraciones. La opción A no lleva tildes en palabras que lo requieren (''está'', ''jardín''), la opción C omite tildes y signos de puntuación necesarios, y la opción D no tilda ''está'' y carece de coma después de ''jardín'', lo que dificulta la comprensión."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{ortografía,acentuación,puntuación,"selección múltiple","nivel recordar"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (535, 114, 'true_false', 'all', '{"afirmacion": "El punto se utiliza para indicar el final de una oración y separar ideas completas dentro de un texto.", "explicacion": "Esta afirmación es verdadera porque el punto es un signo de puntuación que señala el término de una oración o enunciado completo, ayudando a organizar y clarificar las ideas en un texto, facilitando así la comprensión del lector."}', '{"es_verdadero": true}', 1, 0, true, '{ortografía,puntuación,puntaje,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (536, 114, 'fill_blanks', 'all', '{"texto": "Para escribir correctamente, es fundamental aplicar las reglas de ortografía [BLANK_1] y [BLANK_2], además de usar adecuadamente los signos como el [BLANK_3].", "pistas": ["Tipo de ortografía relacionada con la escritura literal de las palabras", "Tipo de ortografía que involucra la colocación de tildes", "Signo de puntuación que indica una pausa fuerte al final de una oración"], "explicacion": "Las reglas de ortografía literal y acentual permiten que las palabras se escriban correctamente para facilitar la comprensión, y el uso adecuado de signos de puntuación como el punto ayuda a estructurar el texto y clarificar su significado."}', '{"respuestas_correctas": {"BLANK_1": ["literal"], "BLANK_2": ["acentual"], "BLANK_3": ["punto"]}}', 2, 0, true, '{ortografía,acentuación,puntuación,"Lengua y Literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (537, 115, 'multiple_choice', 'all', '{"opciones": {"A": "Indicar una pausa breve dentro de una oración para separar ideas relacionadas.", "B": "Señalar el final de una oración completa que expresa una idea acabada.", "C": "Unir dos oraciones independientes sin necesidad de una conjunción.", "D": "Mostrar que una palabra lleva tilde por ser aguda terminada en vocal."}, "pregunta": "¿Cuál es la función principal del punto en la escritura?", "explicacion": "La respuesta correcta es B porque el punto se utiliza para indicar el final de una oración que expresa una idea completa. La opción A corresponde a la coma, que indica una pausa breve. La opción C describe incorrectamente el uso del punto y coma o la coma con conjunción, pero el punto no une oraciones. La opción D es un error conceptual, pues la tilde marca la sílaba tónica, no el punto."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{ortografía,"signos de puntuación",punto,"reglas ortográficas"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (538, 115, 'multiple_choice', 'all', '{"opciones": {"A": "Se usa para separar elementos dentro de una enumeración simple.", "B": "Se emplea para separar oraciones independientes que están estrechamente relacionadas.", "C": "Sirve para indicar una pausa corta dentro de una oración, similar a la coma.", "D": "Se utiliza para finalizar una idea o párrafo, similar al punto final."}, "pregunta": "¿Cuál es la función principal del punto y coma (;) en la escritura?", "explicacion": "La respuesta correcta es la opción B porque el punto y coma se usa para separar oraciones independientes que están estrechamente relacionadas en sentido, facilitando la comprensión sin cortar abruptamente la idea. La opción A es incorrecta porque para separar elementos en una enumeración simple se usa la coma, mientras que el punto y coma se usa en enumeraciones complejas con elementos que contienen comas. La opción C es errónea porque la coma indica pausas cortas dentro de una oración, no el punto y coma. La opción D es incorrecta porque el punto final es el signo que se utiliza para finalizar ideas o párrafos, no el punto y coma."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{ortografía,signos_de_puntuación,comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (539, 115, 'true_false', 'all', '{"afirmacion": "El uso correcto del punto y coma es fundamental para separar elementos complejos dentro de una oración y evitar confusiones en la lectura.", "explicacion": "Esta afirmación es verdadera porque el punto y coma se utiliza justamente para separar elementos dentro de una oración cuando estos son complejos o contienen comas, facilitando así la comprensión y evitando ambigüedades."}', '{"es_verdadero": true}', 2, 0, true, '{ortografía,puntuación,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (565, 120, 'fill_blanks', 'all', '{"texto": "Los textos orales y audiovisuales, como [BLANK_1] y [BLANK_2], presentan información que se transmite mediante el habla o imágenes en movimiento. Entre estos, el [BLANK_3] suele explicar un tema específico, mientras que el [BLANK_4] informa sobre hechos actuales.", "pistas": ["Ejemplos comunes de textos audiovisuales y orales", "Tipo de texto que explica o desarrolla un tema", "Tipo de texto que comunica noticias recientes"], "explicacion": "Los textos orales y audiovisuales incluyen exposiciones, discursos, documentales, noticias y reportajes. Las exposiciones y discursos transmiten información oralmente, mientras que los documentales y noticias combinan imagen y sonido para informar o explicar temas o hechos actuales."}', '{"respuestas_correctas": {"BLANK_1": ["exposiciones", "discursos"], "BLANK_2": ["documentales", "reportajes"], "BLANK_3": ["documental"], "BLANK_4": ["noticia", "noticias"]}}', 2, 0, true, '{"Lengua y Literatura","textos orales","textos audiovisuales","primeros medio","OA comprensión"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (540, 115, 'drag_drop_matching', 'all', '{"explicacion": "El uso de la tilde es para marcar la sílaba tónica en palabras que lo requieren. El punto y seguido separa oraciones dentro de un mismo párrafo. Las palabras homófonas son aquellas que suenan igual pero tienen significado y escritura diferente. La coma se usa para separar elementos en una enumeración o indicar una pausa breve. Las palabras con ortografía irregular son aquellas que no siguen reglas claras y se deben aprender de memoria. El punto final indica el cierre de un texto o párrafo.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Separa oraciones dentro de un mismo párrafo", "Señala la diferencia de significado en palabras que suenan igual", "Finaliza un texto o un párrafo", "Palabras que llevan tilde para marcar la sílaba tónica", "Palabras que no siguen reglas ortográficas claras y se deben memorizar", "Separa elementos dentro de una enumeración o explica una pausa breve"], "columna_izquierda": ["Uso de la tilde", "Punto y seguido", "Palabras homófonas", "Coma", "Palabras con ortografía irregular", "Punto final"]}', '{"emparejamientos_correctos": {"Coma": "Separa elementos dentro de una enumeración o explica una pausa breve", "Punto final": "Finaliza un texto o un párrafo", "Punto y seguido": "Separa oraciones dentro de un mismo párrafo", "Uso de la tilde": "Palabras que llevan tilde para marcar la sílaba tónica", "Palabras homófonas": "Señala la diferencia de significado en palabras que suenan igual", "Palabras con ortografía irregular": "Palabras que no siguen reglas ortográficas claras y se deben memorizar"}}', 2, 0, true, '{ortografía,"signos de puntuación","lengua y literatura","1º medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (541, 115, 'sequencing', 'all', '{"explicacion": "El orden correcto inicia con una lectura general para identificar errores, luego se corrigen las faltas ortográficas basadas en reglas literales y acentuales. Posteriormente, se verifica la ortografía de palabras irregulares que no siguen reglas claras. Después, se aplican correctamente los signos de puntuación, especialmente punto y coma, para mejorar la comprensión. Finalmente, se realiza una última revisión para asegurar que el texto sea coherente y claro para el lector.", "instruccion": "Ordena los siguientes pasos para aplicar correctamente las reglas ortográficas y los signos de puntuación en un texto:", "elementos_desordenados": ["Revisar y corregir la ortografía literal y acentual de las palabras", "Leer el texto completo para identificar errores y puntos de mejora", "Aplicar las reglas de uso correcto de punto y coma", "Verificar la escritura de palabras que no siguen reglas ortográficas", "Hacer una última revisión para asegurar la coherencia y claridad del texto"]}', '{"orden_correcto": ["Leer el texto completo para identificar errores y puntos de mejora", "Revisar y corregir la ortografía literal y acentual de las palabras", "Verificar la escritura de palabras que no siguen reglas ortográficas", "Aplicar las reglas de uso correcto de punto y coma", "Hacer una última revisión para asegurar la coherencia y claridad del texto"]}', 3, 0, true, '{ortografía,puntuación,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (542, 116, 'multiple_choice', 'all', '{"opciones": {"A": "El niño dijo: \"mañana ire al parque\".", "B": "El niño dijo: \"mañana iré al parque.\"", "C": "El niño dijo, \"mañana ire al parque\".", "D": "El niño dijo: mañana iré al parque."}, "pregunta": "¿Cuál de las siguientes oraciones está correctamente escrita, aplicando correctamente las reglas ortográficas y de puntuación?", "explicacion": "La opción B es correcta porque utiliza correctamente las tildes en la palabra ''iré'' (verbo en futuro) y la puntuación adecuada, colocando el punto fuera de las comillas cuando el texto citado es una oración completa. La opción A no lleva tilde en ''iré'', lo cual es un error ortográfico. La opción C usa una coma en lugar de dos puntos para introducir la cita y no lleva tilde en ''iré''. La opción D omite las comillas para la cita directa, lo que representa un error en el uso de signos de puntuación."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{ortografía,puntuación,"aplicar reglas","Lengua y Literatura","1° Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (543, 116, 'multiple_choice', 'all', '{"opciones": {"A": "El perro, que estaba cansado salió a pasear por el parque.", "B": "El perro que estaba cansado, salió a pasear por el parque.", "C": "El perro que estaba cansado salió a pasear, por el parque.", "D": "El perro que estaba cansado salió a pasear por el parque."}, "pregunta": "¿Cuál de las siguientes oraciones está escrita correctamente, aplicando correctamente las reglas ortográficas y el uso de signos de puntuación?", "explicacion": "La opción D es correcta porque no lleva comas innecesarias y respeta las reglas ortográficas y de puntuación: no hay una oración subordinada explicativa que justifique el uso de comas. La opción A presenta una coma mal ubicada después de ''perro'', lo que interrumpe la oración sin motivo. La opción B coloca una coma después de ''cansado'', que separa incorrectamente el sujeto de la oración principal. La opción C tiene una coma innecesaria antes de ''por el parque'', fragmentando incorrectamente la frase."}', '{"respuesta_correcta": "D"}', 4, 0, true, '{ortografía,puntuación,redacción,"Lengua y Literatura","1° Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (544, 116, 'drag_drop_matching', 'all', '{"explicacion": "El uso correcto de la tilde en palabras agudas se aplica a palabras que terminan en vocal, n o s y llevan tilde en la última sílaba. El punto y seguido separa oraciones dentro del mismo párrafo. Las palabras homófonas son aquellas que suenan igual pero se escriben diferente, como ''haya'' y ''halla''. Las palabras con ortografía irregular son aquellas cuya escritura debe memorizarse, como ''excepción''. La coma se usa para separar elementos en una enumeración o indicar pausas breves.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Se utiliza para separar oraciones dentro de un mismo párrafo.", "Palabras que suenan igual pero se escriben diferente (ejemplo: ''haya'' y ''halla'').", "Palabras que terminan en vocal, n o s y llevan tilde en la última sílaba (ejemplo: ''café'').", "Tilde en palabras que no siguen reglas generales y deben memorizarse (ejemplo: ''excepción'').", "Separa elementos en una enumeración o indica una pausa breve en la oración."], "columna_izquierda": ["Uso correcto de la tilde en palabras agudas", "Punto y seguido", "Palabras homófonas", "Uso de la coma", "Palabras con ortografía irregular"]}', '{"emparejamientos_correctos": {"Uso de la coma": "Separa elementos en una enumeración o indica una pausa breve en la oración.", "Punto y seguido": "Se utiliza para separar oraciones dentro de un mismo párrafo.", "Palabras homófonas": "Palabras que suenan igual pero se escriben diferente (ejemplo: ''haya'' y ''halla'').", "Palabras con ortografía irregular": "Tilde en palabras que no siguen reglas generales y deben memorizarse (ejemplo: ''excepción'').", "Uso correcto de la tilde en palabras agudas": "Palabras que terminan en vocal, n o s y llevan tilde en la última sílaba (ejemplo: ''café'')."}}', 3, 0, true, '{ortografía,puntuación,"lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (545, 116, 'sequencing', 'all', '{"explicacion": "El orden correcto comienza con la lectura inicial para identificar errores, luego se aplican las reglas ortográficas y de acentuación, se corrigen los signos de puntuación y finalmente se verifica que las correcciones hayan mejorado la comprensión del texto.", "instruccion": "Ordena los siguientes pasos para corregir la ortografía y puntuación en un texto breve.", "elementos_desordenados": ["Revisar las palabras que no siguen reglas ortográficas claras.", "Leer el texto para identificar errores ortográficos y de puntuación.", "Aplicar las reglas de acentuación y ortografía literal en las palabras.", "Corregir el uso de los signos de puntuación para mejorar la comprensión.", "Releer el texto para verificar que las correcciones faciliten la comprensión."]}', '{"orden_correcto": ["Leer el texto para identificar errores ortográficos y de puntuación.", "Revisar las palabras que no siguen reglas ortográficas claras.", "Aplicar las reglas de acentuación y ortografía literal en las palabras.", "Corregir el uso de los signos de puntuación para mejorar la comprensión.", "Releer el texto para verificar que las correcciones faciliten la comprensión."]}', 3, 0, true, '{ortografía,puntuación,corrección,redacción}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (546, 116, 'open_ended', 'all', '{"pregunta": "Escribe un texto breve (de aproximadamente 8 a 10 líneas) sobre un tema de tu interés. Luego, revisa y corrige cuidadosamente la ortografía y la puntuación del texto, aplicando las reglas ortográficas y de signos de puntuación que has aprendido. Finalmente, explica en un párrafo cuáles fueron los errores que encontraste y cómo los corregiste.", "explicacion": "El docente debe evaluar la precisión en la aplicación de las reglas ortográficas y la correcta utilización de los signos de puntuación, además de valorar la capacidad del estudiante para reflexionar sobre sus errores y las correcciones realizadas. Es importante que el texto sea coherente y que las explicaciones sean claras y fundamentadas.", "contexto_adicional": "El estudiante debe producir un texto original y luego realizar un análisis crítico de su propia escritura para demostrar la aplicación práctica de las normas ortográficas y de puntuación.", "criterios_evaluacion": ["Aplicación correcta de las reglas de ortografía literal y acentual en el texto (peso: 40%)", "Uso adecuado y coherente de los signos de puntuación para facilitar la comprensión (peso: 30%)", "Capacidad para identificar y explicar los errores ortográficos y de puntuación corregidos (peso: 30%)"]}', '{"puntos_clave": ["Identificación y corrección de errores ortográficos, especialmente tildes", "Uso adecuado de puntos para separar oraciones y facilitar la lectura", "Explicación clara y fundamentada de las correcciones realizadas"], "respuesta_modelo": "Texto original:\n\"El verano pasado fui a la playa con mis amigos fue una experiencia inolvidable. Nosotros jugamos voleibol, nadamos en el mar y comimos helado. El sol brillaba mucho y la brisa del mar era refrescante. Por la noche, vimos las estrellas y contamos historias de miedo alrededor de la fogata. Sin duda, fue un viaje que siempre recordare.\"\n\nTexto corregido:\n\"El verano pasado fui a la playa con mis amigos. Fue una experiencia inolvidable. Nosotros jugamos voleibol, nadamos en el mar y comimos helado. El sol brillaba mucho y la brisa del mar era refrescante. Por la noche, vimos las estrellas y contamos historias de miedo alrededor de la fogata. Sin duda, fue un viaje que siempre recordaré.\"\n\nExplicación:\nEn el texto original, encontré que faltaban puntos para separar oraciones independientes, lo que dificultaba la comprensión. Agregué un punto después de \"amigos\" para dividir las ideas. También corregí la palabra \"recordare\" porque le faltaba la tilde en la \"e\" para indicar futuro en primera persona del singular. Estas correcciones mejoran la claridad y respetan las reglas ortográficas y de puntuación.", "requiere_revision_humana": true}', 3, 0, true, '{ortografía,puntuación,redacción,evaluación_aplicación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (547, 117, 'compare_contrast', 'all', '{"conceptos": ["Ortografía literal", "Ortografía acentual"], "criterios": ["Reglas que aplican", "Tipo de palabras afectadas", "Importancia en la comprensión"], "explicacion": "La ortografía literal se refiere a la correcta escritura de las letras según las reglas del idioma, mientras que la ortografía acentual se enfoca en la correcta colocación de tildes. Ambas son esenciales para la correcta escritura, pero aplican a aspectos diferentes de las palabras. La ortografía literal afecta a la elección de letras en general, y la acentual afecta a la correcta pronunciación y significado mediante el uso de tildes.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Ortografía literal": {"Reglas que aplican": "Normas para la correcta escritura de letras y combinación de letras", "Tipo de palabras afectadas": "Todas las palabras que tienen reglas ortográficas específicas para su escritura", "Importancia en la comprensión": "Evita confusiones por mala escritura de letras, facilitando la lectura y comprensión"}, "Ortografía acentual": {"Reglas que aplican": "Normas para la correcta colocación de tildes según la sílaba tónica y tipo de palabra", "Tipo de palabras afectadas": "Palabras que llevan tilde para marcar su pronunciación correcta", "Importancia en la comprensión": "Permite distinguir palabras con diferente significado y correcta pronunciación"}}}', 3, 0, true, '{ortografía,comparación,"lengua y literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (548, 117, 'compare_contrast', 'all', '{"conceptos": ["Ortografía literal", "Ortografía acentual", "Puntuación"], "criterios": ["Función en la escritura", "Reglas aplicadas", "Impacto en la comprensión del texto"], "explicacion": "La ortografía literal se refiere a la correcta escritura de las letras según las normas del idioma, la ortografía acentual se enfoca en la correcta colocación de los acentos gráficos para marcar la pronunciación y significado, y la puntuación regula la estructura y entonación del texto. Aunque las tres contribuyen a la correcta comunicación escrita, difieren en su función específica, las reglas que aplican y cómo afectan la comprensión del lector.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Puntuación": {"Reglas aplicadas": "Uso correcto de puntos, comas, signos de interrogación, exclamación, entre otros.", "Función en la escritura": "Organizar y clarificar la estructura del texto mediante signos.", "Impacto en la comprensión del texto": "Permite entender pausas, entonación y relaciones entre ideas."}, "Ortografía literal": {"Reglas aplicadas": "Normas sobre el uso correcto de letras y combinaciones (b/v, c/s/z, g/j, etc.).", "Función en la escritura": "Garantizar la correcta representación de los sonidos y palabras mediante letras.", "Impacto en la comprensión del texto": "Evita confusiones por palabras mal escritas que cambian el sentido."}, "Ortografía acentual": {"Reglas aplicadas": "Reglas de acentuación según tipo de palabra (aguda, grave, esdrújula) y uso de tildes.", "Función en la escritura": "Indicar la correcta pronunciación y distinguir palabras mediante tildes.", "Impacto en la comprensión del texto": "Facilita la correcta interpretación de palabras que se diferencian solo por el acento."}}}', 4, 0, true, '{ortografía,puntuación,"comparar y contrastar","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (566, 121, 'multiple_choice', 'all', '{"opciones": {"A": "Distracción para que el espectador no se aburra con el contenido hablado.", "B": "Complementar y reforzar la información presentada, ayudando a comprender mejor el tema.", "C": "Mostrar escenas aleatorias sin relación directa con el mensaje principal.", "D": "Sustituir completamente el texto hablado para que no haya necesidad de escuchar."}, "pregunta": "En un video explicativo sobre el cambio climático, ¿cuál es la función principal de las imágenes y los sonidos que acompañan al narrador?", "explicacion": "La opción B es correcta porque en textos audiovisuales, como videos explicativos, las imágenes y sonidos tienen la función de complementar y reforzar la información que se presenta verbalmente, facilitando la comprensión del contenido. La opción A es incorrecta porque la función no es distraer, sino apoyar el mensaje. La opción C es un error común de creer que las imágenes pueden ser irrelevantes, cuando en realidad deben estar relacionadas. La opción D es errónea porque las imágenes y sonidos no sustituyen completamente el texto hablado, sino que lo acompañan."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{audiovisual,comprensión,contexto,"Lengua y Literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (549, 117, 'open_ended', 'all', '{"pregunta": "A continuación, se presentan dos versiones de un mismo texto. Analiza ambos y compara la ortografía y puntuación en cada uno. Identifica los errores presentes y explica detalladamente las correcciones necesarias para mejorar la comprensión del texto.", "explicacion": "El docente debe evaluar que el estudiante logre identificar correctamente los errores, fundamente sus correcciones con base en las reglas ortográficas y de puntuación, y presente su análisis de manera clara y estructurada. Se valorará el uso de terminología adecuada y la capacidad de razonamiento crítico.", "contexto_adicional": "Se entregan dos textos breves con diferencias ortográficas y de puntuación para el análisis comparativo.", "criterios_evaluacion": ["Identificación precisa de errores ortográficos y de puntuación en ambos textos (peso: 40%)", "Explicación clara y fundamentada de las correcciones propuestas, mostrando comprensión de las reglas ortográficas y de puntuación (peso: 40%)", "Organización y coherencia en la presentación del análisis, usando lenguaje adecuado y respetando la estructura de respuesta (peso: 20%)"]}', '{"puntos_clave": ["Identificación de errores ortográficos, especialmente tildes ausentes o mal aplicadas", "Detección de errores en el uso de puntuación como comas y puntos", "Explicación de las reglas ortográficas y de puntuación aplicadas para justificar las correcciones"], "respuesta_modelo": "Al comparar ambos textos, se pueden observar que el Texto 1 presenta varias faltas ortográficas, como la ausencia de tildes en palabras como ''camion'' y ''arbol'', las cuales deben llevar tilde para respetar las reglas de acentuación de palabras graves terminadas en consonante distinta de ''n'' o ''s''. Además, en cuanto a puntuación, el Texto 1 carece de comas en enumeraciones y antes de conectores como ''pero'', lo que dificulta la comprensión. En cambio, el Texto 2 corrige estas faltas, aplicando correctamente las tildes y usando puntos y comas para separar ideas, lo que mejora la claridad. Por ejemplo, en la frase ''El camion rojo, que estaba estacionado, se movió'', el uso de comas delimita la oración subordinada explicativa. En resumen, las correcciones necesarias incluyen la aplicación correcta de tildes según las reglas de acentuación, el uso adecuado de comas para separar elementos y evitar ambigüedades, y la correcta segmentación de oraciones mediante puntos para facilitar la lectura y comprensión.", "requiere_revision_humana": true}', 4, 0, true, '{ortografía,puntuación,"análisis de texto","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (550, 117, 'concept_map', 'all', '{"conceptos": ["Ortografía literal", "Ortografía acentual", "Palabras de ortografía irregular", "Uso del punto", "Uso de la coma", "Facilitar la comprensión lectora"], "explicacion": "Se espera que el mapa conceptual muestre cómo la ortografía literal y acentual contribuyen a la correcta escritura, junto con el reconocimiento de palabras que tienen ortografía irregular. Además, el uso adecuado de signos de puntuación como el punto y la coma es fundamental para organizar el texto y facilitar la comprensión lectora. Estos elementos se interrelacionan para lograr una comunicación clara y efectiva.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la escritura correcta para facilitar la comprensión al lector?"}', '{"conceptos_centrales": ["Ortografía literal", "Ortografía acentual"], "relaciones_esperadas": [{"origen": "Ortografía literal", "destino": "Facilitar la comprensión lectora", "relacion": "garantiza"}, {"origen": "Ortografía acentual", "destino": "Facilitar la comprensión lectora", "relacion": "garantiza"}, {"origen": "Palabras de ortografía irregular", "destino": "Verificación especial", "relacion": "requieren"}, {"origen": "Uso del punto", "destino": "Texto", "relacion": "organiza"}, {"origen": "Uso de la coma", "destino": "Elementos dentro de la oración", "relacion": "separa"}, {"origen": "Uso del punto", "destino": "Comprensión lectora", "relacion": "facilita"}, {"origen": "Uso de la coma", "destino": "Comprensión lectora", "relacion": "facilita"}], "requiere_revision_humana": true}', 4, 0, true, '{ortografía,puntuación,"comprensión lectora","mapa conceptual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (551, 118, 'criteria_evaluation', 'all', '{"rubrica": {"Coherencia y claridad en la evaluación": {"Excelente": "La evaluación y justificación están expresadas de manera clara, coherente y ordenada, facilitando la comprensión del lector.", "Satisfactorio": "La evaluación es comprensible pero podría ser más clara u ordenada en algunos puntos.", "Necesita mejorar": "La evaluación es confusa, desordenada o difícil de seguir."}, "Justificación de la importancia de la correcta aplicación de reglas": {"Excelente": "Ofrece una justificación profunda y bien argumentada sobre por qué es fundamental aplicar correctamente las reglas ortográficas y de puntuación para facilitar la comprensión lectora.", "Satisfactorio": "Proporciona una justificación básica que reconoce la importancia, pero sin argumentos sólidos o elaborados.", "Necesita mejorar": "No justifica o la justificación es vaga y sin relación clara con la mejora de la comprensión."}, "Identificación del impacto de la ortografía y puntuación en la comprensión": {"Excelente": "Reconoce claramente cómo cada error ortográfico y de puntuación afecta la comprensión del texto, proporcionando ejemplos específicos.", "Satisfactorio": "Reconoce en general el impacto de los errores en la comprensión, pero con explicaciones poco detalladas.", "Necesita mejorar": "No identifica o identifica incorrectamente el impacto de los errores en la comprensión del texto."}}, "explicacion": "El estudiante debe demostrar capacidad crítica para identificar cómo los errores ortográficos y de puntuación afectan la comprensión del texto, fundamentar por qué es importante aplicar correctamente estas reglas y expresar su evaluación de forma clara y coherente.", "instruccion": "Evalúa el texto presentado según la rúbrica y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Se presenta un texto argumentativo escrito por un estudiante de Primero Medio que contiene varios errores ortográficos y de puntuación, además de otros aspectos correctamente aplicados. El estudiante debe evaluar en qué medida estos errores afectan la comprensión del texto y justificar la importancia de aplicar correctamente las reglas ortográficas y de puntuación."}', '{"evaluacion_modelo": {"Coherencia y claridad en la evaluación": "Satisfactorio", "Justificación de la importancia de la correcta aplicación de reglas": "Excelente", "Identificación del impacto de la ortografía y puntuación en la comprensión": "Excelente"}, "justificacion_modelo": "El estudiante identificó claramente que los errores dificultan la comprensión y ejemplificó cómo. Además, proporcionó una justificación sólida sobre la importancia de las reglas ortográficas y de puntuación para asegurar la claridad del mensaje. Sin embargo, la expresión de la evaluación tuvo algunos pasajes poco claros, por lo que se asignó nivel satisfactorio en coherencia.", "requiere_revision_humana": true}', 4, 0, true, '{ortografía,puntuación,"comprensión lectora","evaluación crítica","primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (552, 118, 'criteria_evaluation', 'all', '{"rubrica": {"Uso adecuado de puntuación para organizar ideas": {"Excelente": "La puntuación está correctamente utilizada para separar ideas y hacer el texto claro y fluido.", "Satisfactorio": "La puntuación es generalmente adecuada, aunque en algunos casos dificulta la comprensión.", "Necesita mejorar": "El uso de puntuación es incorrecto o insuficiente, generando confusión en la lectura."}, "Aplicación correcta de reglas ortográficas y acentuales": {"Excelente": "El texto no presenta errores ortográficos ni de acentuación, demostrando dominio total de las reglas.", "Satisfactorio": "El texto presenta algunos errores ortográficos o de acentuación que no afectan gravemente la comprensión.", "Necesita mejorar": "El texto tiene múltiples errores ortográficos y acentuales que dificultan la comprensión."}, "Justificación de la importancia de aplicar reglas ortográficas y de puntuación": {"Excelente": "La justificación es clara, bien argumentada y conecta directamente con la mejora de la comprensión lectora.", "Satisfactorio": "La justificación es adecuada pero superficial o poco desarrollada.", "Necesita mejorar": "La justificación es vaga, confusa o no se relaciona con la comprensión lectora."}}, "explicacion": "Se espera que el estudiante identifique la presencia o ausencia de errores ortográficos y de puntuación, analice cómo estos afectan la claridad del texto y argumente por qué es importante aplicar correctamente estas reglas para facilitar la comprensión del lector.", "instruccion": "Evalúa el texto presentado según los criterios de la rúbrica y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Se presenta un texto argumentativo elaborado por un estudiante de Primero Medio que contiene errores ortográficos y de puntuación, además de párrafos con estructura poco clara. El texto tiene como objetivo convencer al lector sobre la importancia de cuidar el medio ambiente."}', '{"evaluacion_modelo": {"Uso adecuado de puntuación para organizar ideas": "Excelente", "Aplicación correcta de reglas ortográficas y acentuales": "Satisfactorio", "Justificación de la importancia de aplicar reglas ortográficas y de puntuación": "Excelente"}, "justificacion_modelo": "El texto presenta algunos errores ortográficos menores que no afectan gravemente la comprensión, por lo que se califica como satisfactorio en ese criterio. Sin embargo, la puntuación está bien empleada, facilitando la organización clara de las ideas, lo que justifica la calificación excelente en ese aspecto. Además, la justificación sobre la importancia de aplicar correctamente las reglas es clara y bien fundamentada, conectando directamente con la comprensión lectora, por lo que también se califica como excelente.", "requiere_revision_humana": true}', 5, 0, true, '{ortografía,puntuación,"comprensión lectora",evaluación,"debate guiado"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (553, 118, 'criteria_evaluation', 'all', '{"rubrica": {"Coherencia y claridad en la justificación": {"Excelente": "Presenta una justificación coherente, bien estructurada y clara, que facilita la comprensión del lector.", "Satisfactorio": "La justificación es entendible pero presenta algunas ideas desordenadas o falta de claridad en ciertos puntos.", "Necesita mejorar": "La justificación es confusa, incoherente o difícil de entender."}, "Evaluación del impacto de errores en la comprensión": {"Excelente": "Identifica correctamente los errores específicos y evalúa con argumentos sólidos cómo afectan la comprensión del lector.", "Satisfactorio": "Reconoce algunos errores y menciona de forma general su impacto en la comprensión, pero con argumentos poco desarrollados.", "Necesita mejorar": "No identifica correctamente los errores ni logra evaluar su impacto en la comprensión."}, "Justificación de la importancia de la ortografía y puntuación": {"Excelente": "Explica claramente y con ejemplos precisos cómo los errores ortográficos y de puntuación afectan la comprensión del texto y la comunicación efectiva.", "Satisfactorio": "Explica la importancia de la ortografía y puntuación con ejemplos generales, pero con alguna falta de profundidad o precisión.", "Necesita mejorar": "No logra justificar adecuadamente la importancia de aplicar las reglas ortográficas y de puntuación o la explicación es confusa."}}, "explicacion": "Se espera que el estudiante identifique errores ortográficos y de puntuación, explique cómo estos afectan la comprensión del texto y justifique con claridad la importancia de aplicar correctamente las reglas ortográficas y de puntuación para facilitar la comunicación.", "instruccion": "Evalúa el texto presentado según la rúbrica y justifica tu calificación en cada criterio.", "caso_a_evaluar": "Se presenta un texto argumentativo de un estudiante de Primero Medio que contiene errores ortográficos y de puntuación. El texto es sobre la importancia de la lectura en la formación personal y social. El estudiante debe evaluar cómo estos errores afectan la comprensión del texto y justificar por qué es importante aplicar correctamente las reglas ortográficas y de puntuación."}', '{"evaluacion_modelo": {"Coherencia y claridad en la justificación": "Excelente", "Evaluación del impacto de errores en la comprensión": "Satisfactorio", "Justificación de la importancia de la ortografía y puntuación": "Excelente"}, "justificacion_modelo": "El estudiante explica claramente la importancia de la ortografía y puntuación con ejemplos pertinentes, identifica la mayoría de los errores pero sus argumentos sobre el impacto en la comprensión podrían ser más detallados. La justificación está bien organizada y facilita la comprensión de su evaluación.", "requiere_revision_humana": true}', 5, 0, true, '{ortografía,puntuación,evaluación,"comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (554, 118, 'open_ended', 'all', '{"pregunta": "En un debate, argumenta por qué es fundamental aplicar correctamente las reglas ortográficas y de puntuación para mejorar la comprensión lectora. ¿Qué consecuencias podrían tener en la comunicación escrita los errores en estas áreas? Justifica tu respuesta con ejemplos y explica cómo estos errores pueden afectar al lector.", "explicacion": "El docente debe evaluar que el estudiante no solo conozca la importancia de las reglas ortográficas y de puntuación, sino que también pueda argumentar críticamente, identificar consecuencias concretas de su mal uso y ejemplificar adecuadamente. Se valorará la coherencia, profundidad y fundamentación de la respuesta.", "contexto_adicional": "Los estudiantes deben prepararse para un debate guiado en el que defenderán la importancia de la ortografía y la puntuación en la escritura para facilitar la comprensión del lector.", "criterios_evaluacion": ["Claridad y coherencia en la argumentación sobre la importancia de la ortografía y puntuación (peso: 40%)", "Capacidad para identificar y explicar las consecuencias de los errores ortográficos y de puntuación en la comprensión lectora (peso: 35%)", "Uso de ejemplos pertinentes que apoyen la justificación (peso: 25%)"]}', '{"puntos_clave": ["Importancia de la claridad y precisión en la comunicación escrita", "Consecuencias negativas de errores ortográficos y de puntuación (malentendidos, confusión, dificultad en la lectura)", "Ejemplos claros que ilustran cómo un error puede cambiar el significado o afectar la comprensión"], "respuesta_modelo": "Aplicar correctamente las reglas ortográficas y de puntuación es fundamental porque permite que el texto sea claro y fácil de entender para el lector. Por ejemplo, la correcta colocación de comas puede cambiar el significado de una frase, evitando confusiones. Si alguien escribe sin respetar las reglas, puede generar malentendidos o dificultar la lectura, lo que afecta la comunicación efectiva. Por ejemplo, escribir \"Vamos a comer, niños\" versus \"Vamos a comer niños\" cambia totalmente el sentido. Además, los errores ortográficos pueden hacer que el texto se vea poco profesional o descuidado, lo que puede distraer o confundir al lector. Por estas razones, respetar estas reglas es esencial para comunicar ideas de manera precisa y eficaz.", "requiere_revision_humana": true}', 4, 0, true, '{ortografía,puntuación,"comprensión lectora","debate guiado","nivel evaluativo"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (555, 118, 'open_ended', 'all', '{"pregunta": "Analiza y argumenta por qué es fundamental aplicar correctamente las reglas ortográficas y de puntuación en un texto para mejorar la comprensión lectora. ¿Qué consecuencias puede tener no respetar estas reglas en la comunicación escrita?", "explicacion": "El docente debe evaluar si el estudiante presenta una argumentación bien estructurada que demuestre comprensión profunda del rol que juegan las normas ortográficas y de puntuación en la claridad y efectividad del mensaje escrito, así como su capacidad para identificar las consecuencias negativas de su incorrecta aplicación. También se valorará la inclusión de ejemplos concretos que respalden el análisis.", "contexto_adicional": "Los estudiantes pueden basar su análisis en textos cotidianos o escolares, considerando la función de la ortografía y la puntuación en facilitar la comprensión y evitar malentendidos.", "criterios_evaluacion": ["Claridad y coherencia en la argumentación sobre la importancia de las reglas ortográficas y de puntuación (peso: 40%)", "Capacidad para identificar y explicar las consecuencias de aplicar incorrectamente estas reglas en la comunicación escrita (peso: 40%)", "Uso de ejemplos o situaciones que apoyen la justificación presentada (peso: 20%)"]}', '{"puntos_clave": ["Importancia de la ortografía para evitar confusiones en el significado de las palabras", "Función de la puntuación para organizar ideas y facilitar la lectura", "Consecuencias negativas como malentendidos y dificultar la comunicación efectiva"], "respuesta_modelo": "Aplicar correctamente las reglas ortográficas y de puntuación es fundamental porque estas normas permiten que el texto sea claro y fácil de entender para cualquier lector. La ortografía asegura que las palabras se escriban de manera correcta, evitando confusiones entre términos que pueden tener significados diferentes si se escriben mal. La puntuación, por su parte, organiza las ideas y marca pausas necesarias para que el mensaje sea interpretado correctamente. Si no se respetan estas reglas, pueden surgir malentendidos o interpretaciones erróneas, lo que dificulta la comunicación. Por ejemplo, la ausencia de comas puede cambiar el sentido de una oración o hacerla confusa. En conclusión, respetar estas normas es esencial para que el escritor comunique sus ideas claramente y el lector pueda comprender el mensaje sin dificultades.", "requiere_revision_humana": true}', 5, 0, true, '{ortografía,puntuación,"comprensión lectora","evaluación crítica"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (556, 119, 'open_ended', 'all', '{"pregunta": "Escribe un relato breve original (mínimo 200 palabras) en el que apliques correctamente todas las reglas ortográficas y de puntuación para asegurar su clara comprensión. Luego, explica las decisiones ortográficas y de puntuación que tomaste y cómo contribuyen a la claridad del texto.", "explicacion": "El docente debe evaluar tanto la producción escrita como la reflexión metalingüística del estudiante, valorando la precisión ortográfica y el uso de la puntuación que favorezca la comprensión, además de la capacidad para justificar sus elecciones en la escritura.", "contexto_adicional": "El relato debe ser original y puede abordar cualquier tema de interés personal. Se debe considerar la revisión y corrección para evitar errores ortográficos y de puntuación.", "criterios_evaluacion": ["Aplicación correcta y coherente de las reglas ortográficas literales y acentuales en el texto (peso: 40%)", "Uso adecuado y correcto de signos de puntuación para facilitar la comprensión del relato (peso: 30%)", "Capacidad para explicar críticamente las decisiones ortográficas y de puntuación tomadas y su impacto en la claridad del texto (peso: 30%)"]}', '{"puntos_clave": ["Aplicación precisa de reglas ortográficas: acentuación, uso de mayúsculas, correcta escritura de palabras.", "Uso adecuado de signos de puntuación: comas, puntos, signos de interrogación y exclamación.", "Reflexión crítica sobre cómo estas elecciones facilitan la comprensión del texto."], "respuesta_modelo": "Era una noche fría y oscura; las estrellas brillaban en el cielo, iluminando tenuemente el sendero que llevaba al bosque. María, con el corazón acelerado, decidió aventurarse más allá de lo conocido. \"No puedo darme el lujo de tener miedo\", pensó mientras avanzaba. De repente, un susurro suave se escuchó entre los árboles: \"¿Quién anda ahí?\". Ella respondió con voz firme: \"Soy yo, solo busco respuestas.\" Gracias al uso correcto de comas para separar oraciones coordinadas, puntos para delimitar ideas y signos de interrogación para las preguntas, el relato se entiende con claridad. Además, la correcta acentuación en palabras como \"estrellas\", \"decidió\" y \"más allá\" evita confusiones en la lectura.", "requiere_revision_humana": true}', 4, 0, true, '{ortografía,puntuación,"escritura creativa",primeromedia,lenguayliteratura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (557, 119, 'open_ended', 'all', '{"pregunta": "Diseña un texto narrativo original de al menos 150 palabras en el que apliques correctamente las reglas ortográficas y de puntuación para asegurar la comprensión del lector. Luego, explica brevemente las decisiones ortográficas y de puntuación que tomaste y cómo contribuyen a la claridad y coherencia del texto.", "explicacion": "El docente debe evaluar si el estudiante aplica correctamente las reglas ortográficas y de puntuación en la creación de un texto narrativo, además de valorar la reflexión escrita sobre sus decisiones, lo que indica un nivel metacognitivo sobre la escritura. Se recomienda revisar la coherencia interna del texto y la precisión en el uso de los signos ortográficos.", "contexto_adicional": "El texto debe ser original y puede abordar cualquier tema que el estudiante elija, con un enfoque en la narración. Se espera que el estudiante demuestre dominio de la ortografía y puntuación para que el mensaje sea claro y comprensible.", "criterios_evaluacion": ["Aplicación correcta de las reglas ortográficas literales y acentuales en el texto (peso: 40%)", "Uso adecuado y pertinente de los signos de puntuación para organizar las ideas y facilitar la comprensión (peso: 35%)", "Capacidad para explicar de manera clara y coherente las decisiones ortográficas y de puntuación tomadas en el texto (peso: 25%)"]}', '{"puntos_clave": ["Uso correcto de tildes en palabras agudas, graves y esdrújulas", "Empleo adecuado de comas, puntos y otros signos de puntuación para organizar las ideas", "Explicación coherente y clara sobre las decisiones ortográficas y de puntuación"], "respuesta_modelo": "En una pequeña aldea, vivía un niño llamado Andrés. Cada tarde, después de la escuela, corría hacia el río para observar cómo el agua reflejaba el sol. Un día, mientras jugaba, encontró una piedra brillante que parecía esconder un secreto. Intrigado, decidió explorar los alrededores y descubrir qué misterio guardaba aquel objeto. La emoción crecía con cada paso, y Andrés comprendió que la aventura apenas comenzaba.\n\nEn este texto, decidí usar correctamente las tildes en palabras como \"día\" y \"decidió\", respetando las reglas de acentuación. También utilicé comas para separar ideas y evitar confusiones, como en la frase \"Cada tarde, después de la escuela, corría hacia el río\". Los puntos se emplearon para marcar el final de cada oración y así facilitar la lectura y comprensión del texto. Estas decisiones contribuyen a que el relato sea claro, fluido y fácil de seguir para el lector.", "requiere_revision_humana": true}', 5, 0, true, '{ortografía,puntuación,"creación textual","lengua y literatura","enseñanza media"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (558, 119, 'concept_map', 'all', '{"conceptos": ["Ortografía literal", "Ortografía acentual", "Palabras de ortografía irregular", "Puntuación", "Comprensión lectora", "Texto original"], "explicacion": "El mapa conceptual debe mostrar cómo la ortografía literal y acentual contribuyen a la correcta escritura, mientras que el conocimiento de palabras con ortografía irregular y el uso adecuado de la puntuación complementan esta corrección. Todo esto se articula en la elaboración de un texto original que garantice la comprensión lectora.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la correcta escritura para facilitar la comprensión del lector?"}', '{"conceptos_centrales": ["Texto original", "Comprensión lectora"], "relaciones_esperadas": [{"origen": "Ortografía literal", "destino": "Texto original", "relacion": "aplica en"}, {"origen": "Ortografía acentual", "destino": "Texto original", "relacion": "aplica en"}, {"origen": "Palabras de ortografía irregular", "destino": "verificación", "relacion": "requieren"}, {"origen": "Puntuación", "destino": "Comprensión lectora", "relacion": "facilita"}, {"origen": "Texto original", "destino": "Comprensión lectora", "relacion": "garantiza"}], "requiere_revision_humana": true}', 4, 0, true, '{ortografía,puntuación,"comprensión lectora","mapa conceptual",creación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (559, 119, 'concept_map', 'all', '{"conceptos": ["Ortografía literal", "Ortografía acentual", "Palabras de ortografía irregular", "Puntuación", "Comprensión lectora"], "explicacion": "Se espera que el estudiante relacione cómo la ortografía literal y acentual contribuyen a la correcta escritura, cómo se debe verificar la ortografía de palabras irregulares, y cómo el uso adecuado de la puntuación influye en la claridad y comprensión del texto.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la escritura correcta para facilitar la comprensión del lector?"}', '{"conceptos_centrales": ["Ortografía literal", "Ortografía acentual", "Puntuación"], "relaciones_esperadas": [{"origen": "Ortografía literal", "destino": "Escritura correcta", "relacion": "es parte de"}, {"origen": "Ortografía acentual", "destino": "Escritura correcta", "relacion": "es parte de"}, {"origen": "Palabras de ortografía irregular", "destino": "Verificación especial", "relacion": "requieren"}, {"origen": "Puntuación", "destino": "Comprensión lectora", "relacion": "facilita"}, {"origen": "Escritura correcta", "destino": "Comprensión lectora", "relacion": "asegura"}], "requiere_revision_humana": true}', 5, 0, true, '{ortografía,puntuación,"comprensión lectora",creación,"mapa conceptual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (560, 119, 'open_ended', 'all', '{"pregunta": "Imagina que debes redactar una carta dirigida a un amigo explicándole un evento importante que viviste recientemente. Escribe esta carta aplicando todas las reglas ortográficas y de puntuación estudiadas para asegurar que el mensaje sea claro y fácil de comprender. Justifica brevemente las decisiones ortográficas y de puntuación que consideres más relevantes en tu texto.", "explicacion": "El docente debe evaluar si el estudiante integra correctamente las reglas ortográficas y de puntuación para facilitar la comprensión del lector, así como la capacidad de reflexión que demuestra en la justificación escrita. Se recomienda revisar la coherencia general del texto y la precisión en el uso de los signos de puntuación.", "contexto_adicional": "La carta debe tener una extensión mínima de 150 palabras y reflejar un evento real o ficticio que sea significativo para el estudiante.", "criterios_evaluacion": ["Aplicación correcta de las reglas ortográficas y acentuales en el texto (peso: 40%)", "Uso adecuado y variado de los signos de puntuación para estructurar y clarificar el mensaje (peso: 35%)", "Justificación clara y coherente de las decisiones ortográficas y de puntuación tomadas en la redacción (peso: 25%)"]}', '{"puntos_clave": ["Uso correcto de tildes en palabras agudas, graves y esdrújulas", "Empleo adecuado de signos de puntuación como comas, puntos y signos de interrogación o exclamación si es necesario", "Justificación que refleje el conocimiento y la aplicación consciente de reglas ortográficas y de puntuación"], "respuesta_modelo": "Querido amigo,\n\nEspero que te encuentres muy bien. Quiero contarte que la semana pasada viví una experiencia increíble: participé en una competencia de atletismo en mi colegio. Aunque estaba nervioso al principio, logré correr los 100 metros planos en un tiempo que me sorprendió. Además, compartí momentos muy especiales con mis compañeros y aprendí la importancia de la perseverancia y el trabajo en equipo.\n\nTe escribo esta carta para compartir mi alegría y motivarte a que también persigas tus metas con entusiasmo. Espero que pronto podamos reunirnos y contarnos más experiencias.\n\nCon cariño,\n\n[Nombre]\n\nJustificación: En el texto he aplicado las reglas de acentuación en palabras como ''estás'' y ''rápido''. He utilizado correctamente los signos de puntuación para separar ideas y dar claridad, como las comas para enumerar y las mayúsculas al inicio de cada oración. También he respetado la ortografía de palabras de uso común y cuidado la coherencia para facilitar la comprensión.", "requiere_revision_humana": true}', 5, 0, true, '{ortografía,puntuación,redacción,creación,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (561, 120, 'multiple_choice', 'all', '{"opciones": {"A": "Uso exclusivo de lenguaje escrito y formal", "B": "Presencia de un emisor que transmite un mensaje a un receptor", "C": "Ausencia de imágenes y sonidos para apoyar el contenido", "D": "Solo se utiliza para narrar historias ficticias"}, "pregunta": "¿Cuál de los siguientes elementos es característico de un texto oral o audiovisual, como un discurso o un documental?", "explicacion": "La opción B es correcta porque un elemento básico de los textos orales y audiovisuales es la presencia de un emisor que transmite un mensaje a un receptor, ya sea en discursos, exposiciones, documentales, noticias o reportajes. La opción A es incorrecta porque estos textos no se limitan al lenguaje escrito ni siempre usan un lenguaje formal. La opción C es errónea porque los textos audiovisuales incluyen imágenes y sonidos como parte fundamental para apoyar el contenido. La opción D es falsa porque estos textos pueden abordar temas informativos, argumentativos o narrativos, no solo historias ficticias."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{lengua_y_literatura,texto_oral,audiovisual,primer_medio,OA_comprension}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (562, 120, 'multiple_choice', 'all', '{"opciones": {"A": "La presencia de un narrador que presenta información basada en hechos reales.", "B": "Un narrador que solo expresa opiniones personales sin sustento.", "C": "Un guion con diálogos ficticios y personajes inventados.", "D": "Un formato breve sin imágenes ni sonidos."}, "pregunta": "¿Cuál de los siguientes elementos es básico para identificar un texto audiovisual como un documental?", "explicacion": "La opción A es correcta porque un documental se caracteriza por tener un narrador que presenta información real sobre un tema, sustentada en hechos. La opción B es incorrecta porque un documental no se basa en opiniones sin sustento, sino en información verificable. La opción C describe características más propias de una obra de ficción, no de un documental. La opción D es errónea porque un documental es un texto audiovisual que incluye imágenes y sonidos para apoyar la información presentada."}', '{"respuesta_correcta": "A"}', 2, 0, true, '{"Lengua y Literatura","Textos Audiovisuales",Documental,"Identificación de elementos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (563, 120, 'multiple_choice', 'all', '{"opciones": {"A": "La presencia de entrevistas y material visual que apoyan información real.", "B": "El uso exclusivo de lenguaje poético y metáforas para contar una historia.", "C": "La presentación de un solo punto de vista sin evidencia que lo respalde.", "D": "La transmisión en vivo de un evento sin edición ni guion."}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para identificar un texto audiovisual como un documental?", "explicacion": "La opción A es correcta porque un documental se caracteriza por presentar información real apoyada con entrevistas y material visual, buscando informar o educar sobre un tema. La opción B es incorrecta porque el lenguaje poético y metafórico es más propio de textos literarios, no documentales. La opción C es errónea porque un documental debe basarse en evidencias y mostrar diversas perspectivas o datos. La opción D describe más bien una transmisión en vivo, no un documental, que suele estar editado y estructurado."}', '{"respuesta_correcta": "A"}', 3, 0, true, '{"Lengua y Literatura","Textos audiovisuales",Documentales,"Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (567, 121, 'multiple_choice', 'all', '{"opciones": {"A": "Solo decoran el video para hacerlo más atractivo sin aportar información relevante.", "B": "Complementan y refuerzan el mensaje oral, ayudando a comprender mejor el contexto y los puntos de vista presentados.", "C": "Distractan al espectador para que no se concentre en la información oral.", "D": "Reemplazan completamente la información oral, haciendo innecesario escuchar el contenido hablado."}, "pregunta": "¿Cuál es el papel principal que cumplen las imágenes y los sonidos en un documental audiovisual?", "explicacion": "La opción B es correcta porque en textos audiovisuales como documentales, las imágenes y sonidos complementan y refuerzan el mensaje oral, facilitando la comprensión del contexto y los puntos de vista. La opción A es incorrecta porque reduce las imágenes y sonidos a un elemento meramente decorativo, lo cual no es su función principal. La opción C es un error conceptual que asume que los elementos audiovisuales son distracciones, cuando en realidad son herramientas comunicativas. La opción D es falsa porque las imágenes y sonidos no sustituyen completamente el mensaje oral, sino que lo complementan."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{audiovisual,comprensión,contexto,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (568, 121, 'true_false', 'all', '{"afirmacion": "En un texto audiovisual, las imágenes y los sonidos contribuyen a reforzar y complementar el mensaje que se transmite oralmente.", "explicacion": "Esta afirmación es verdadera porque en los textos audiovisuales, como documentales o reportajes, las imágenes y los sonidos no solo acompañan la información oral, sino que también ayudan a contextualizar, enfatizar y clarificar el mensaje, facilitando una mejor comprensión por parte del espectador."}', '{"es_verdadero": true}', 2, 0, true, '{"textos audiovisuales",comprensión,"imágenes y sonidos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (569, 121, 'drag_drop_matching', 'all', '{"explicacion": "El contexto es el entorno en que se produce el texto; el punto de vista es la postura expresada; la contribución de imágenes son los elementos visuales que apoyan el mensaje; y la contribución de sonidos son los elementos auditivos que enfatizan o crean ambiente.", "instruccion": "Empareja cada concepto relacionado con textos orales y audiovisuales con su definición o ejemplo correspondiente", "columna_derecha": ["Ambiente o circunstancias en que se produce un texto", "Opinión o postura que se expresa en un discurso o video", "Elementos visuales que refuerzan o complementan el mensaje", "Elementos auditivos que generan ambiente o enfatizan ideas"], "columna_izquierda": ["Contexto", "Punto de vista", "Contribución de imágenes", "Contribución de sonidos", "Distractor sin pareja"]}', '{"emparejamientos_correctos": {"Contexto": "Ambiente o circunstancias en que se produce un texto", "Punto de vista": "Opinión o postura que se expresa en un discurso o video", "Contribución de sonidos": "Elementos auditivos que generan ambiente o enfatizan ideas", "Contribución de imágenes": "Elementos visuales que refuerzan o complementan el mensaje"}}', 2, 0, true, '{"Lengua y Literatura","Comprensión audiovisual","OA Comprender textos orales y audiovisuales"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (570, 121, 'sequencing', 'all', '{"explicacion": "El análisis de un video explicativo debe comenzar por reconocer el contexto para entender el marco general. Luego, se identifican los sonidos relevantes que aportan información adicional. Después, se describen los puntos de vista presentados para comprender las posturas del contenido. A continuación, se analiza cómo las imágenes complementan el mensaje oral, enriqueciendo la comprensión. Finalmente, se formula una opinión personal sustentada en la observación y análisis previos.", "instruccion": "Ordena los siguientes pasos/eventos en la secuencia correcta para analizar un video explicativo.", "elementos_desordenados": ["Identificar los sonidos que aportan información relevante", "Reconocer el contexto en el que se desarrolla el video", "Describir los puntos de vista presentados en el video", "Analizar cómo las imágenes complementan el mensaje oral", "Formular una opinión personal sustentada en lo observado"]}', '{"orden_correcto": ["Reconocer el contexto en el que se desarrolla el video", "Identificar los sonidos que aportan información relevante", "Describir los puntos de vista presentados en el video", "Analizar cómo las imágenes complementan el mensaje oral", "Formular una opinión personal sustentada en lo observado"]}', 3, 0, true, '{"análisis audiovisual","ordenar secuencia","video explicativo",comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (571, 122, 'multiple_choice', 'all', '{"opciones": {"A": "Primero las opiniones de los expertos, luego los hechos científicos y finalmente las experiencias personales de las personas afectadas.", "B": "Primero los hechos científicos presentados, luego las opiniones de los expertos y, por último, las experiencias personales mencionadas.", "C": "Primero las experiencias personales, luego las opiniones de los expertos y por último los hechos científicos, para darle mayor peso a lo emocional.", "D": "Ordenar toda la información sin distinguir entre hechos y opiniones, para mantener la neutralidad del análisis."}, "pregunta": "Después de ver un reportaje sobre el impacto del cambio climático, ¿cuál de las siguientes opciones representa la mejor manera de ordenar la información para evaluar su valor argumentativo?", "explicacion": "La opción B es correcta porque al analizar un texto audiovisual con valor argumentativo, es fundamental ordenar primero los hechos (información objetiva y verificable) para establecer una base sólida, luego considerar las opiniones de expertos que interpretan esos hechos y finalmente las experiencias personales que aportan contexto, pero que tienen menor peso argumentativo. La opción A confunde el orden de relevancia, colocando opiniones antes que hechos. La opción C prioriza las experiencias personales, que son subjetivas, y la D no distingue entre hechos y opiniones, lo que dificulta evaluar la fuerza argumentativa."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"análisis de textos audiovisuales","hechos vs opiniones","evaluación crítica"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (587, 125, 'open_ended', 'all', '{"pregunta": "Diseña un análisis audiovisual donde expliques cómo la combinación de imágenes, texto y sonido contribuye a transmitir un mensaje relacionado con uno de los temas o obras estudiadas en el curso. Justifica tu análisis destacando la relación entre estos elementos y la postura personal que adoptas frente al contenido presentado.", "explicacion": "El docente debe evaluar que el estudiante no solo identifique los elementos audiovisuales, sino que también los analice en conjunto para comprender cómo generan un significado; que relacione este análisis con los contenidos del curso y que presente una postura personal bien argumentada, evidenciando pensamiento crítico y capacidad de creación.", "contexto_adicional": "Puedes seleccionar un fragmento audiovisual (documental, reportaje, discurso, etc.) que hayas visto en clase o material complementario para realizar tu análisis.", "criterios_evaluacion": ["Claridad y profundidad en la identificación y explicación de la relación entre imágenes, texto y sonido (peso: 40%)", "Capacidad para vincular el análisis audiovisual con temas u obras estudiadas en el curso (peso: 30%)", "Argumentación coherente que refleje una postura personal fundamentada frente al contenido analizado (peso: 30%)"]}', '{"puntos_clave": ["Identificación clara de imágenes, texto y sonido y su función en el mensaje audiovisual", "Relación explícita con un tema u obra estudiada en el curso", "Argumentación personal fundamentada que refleje una postura crítica"], "respuesta_modelo": "En el fragmento audiovisual seleccionado, la combinación de imágenes impactantes, texto en pantalla y música dramática trabaja en conjunto para enfatizar el tema de la injusticia social, un tema recurrente en la obra \"Los de Abajo\" estudiada en clase. Las imágenes muestran escenas de protestas y pobreza, mientras que el texto aporta datos concretos que refuerzan la gravedad del problema. El sonido, con tonos graves y ritmo pausado, genera una atmósfera de tensión y reflexión. Esta integración contribuye a que el espectador comprenda no solo la realidad representada, sino también la urgencia de abordar esta problemática. Personalmente, considero que este audiovisual logra sensibilizar eficazmente sobre la desigualdad, invitando a la acción y reflexión crítica sobre la sociedad actual.", "requiere_revision_humana": true}', 5, 0, true, '{"análisis audiovisual","pensamiento crítico",creación,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (572, 122, 'multiple_choice', 'all', '{"opciones": {"A": "Ordenar las ideas según el orden en que aparecieron en el documental, sin distinguir hechos de opiniones.", "B": "Separar primero los hechos de las opiniones, y luego ordenar la información de acuerdo a su relevancia para el argumento principal.", "C": "Tomar solo las opiniones más impactantes y descartarlas porque no son hechos comprobables.", "D": "Ordenar la información según la cantidad de veces que se repite, sin considerar su naturaleza factual o subjetiva."}, "pregunta": "Después de ver un documental sobre el cambio climático, ¿cuál sería la mejor manera de ordenar la información para analizar su valor argumentativo?", "explicacion": "La opción B es correcta porque permite distinguir claramente entre hechos y opiniones, aspecto fundamental para evaluar la validez y fuerza de los argumentos presentados. Además, ordenar la información según su relevancia facilita un análisis crítico y estructurado. La opción A no diferencia entre hechos y opiniones, lo que dificulta evaluar la argumentación. La opción C descarta opiniones importantes que forman parte del argumento y no solo hechos, lo que limita el análisis. La opción D se enfoca en la frecuencia, que no siempre indica relevancia o veracidad, lo que puede llevar a conclusiones erróneas."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{"análisis de textos audiovisuales","hechos vs opiniones","evaluación crítica"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (573, 122, 'drag_drop_matching', 'all', '{"explicacion": "Los hechos son afirmaciones comprobables; las opiniones son juicios personales; los argumentos sustentan una postura; la información relevante aporta directamente al tema; la anécdota es un distractor porque no se relaciona directamente con el análisis del texto.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Una afirmación comprobable y objetiva", "Una creencia o juicio personal", "Razón que sustenta una postura o idea", "Dato que aporta directamente al tema principal", "Narración breve sin relación con el tema"], "columna_izquierda": ["Hecho", "Opinión", "Argumento", "Información relevante", "Distractor: Anécdota"]}', '{"emparejamientos_correctos": {"Hecho": "Una afirmación comprobable y objetiva", "Opinión": "Una creencia o juicio personal", "Argumento": "Razón que sustenta una postura o idea", "Información relevante": "Dato que aporta directamente al tema principal"}}', 3, 0, true, '{comprensión,"análisis de textos audiovisuales"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (574, 122, 'sequencing', 'all', '{"explicacion": "El análisis comienza con escuchar atentamente el texto para comprenderlo en su totalidad. Luego, se identifican las opiniones y los hechos para diferenciarlos claramente. Después, se organiza la información según su relevancia para entender mejor el mensaje. Finalmente, se evalúa el valor argumentativo de las opiniones basándose en los hechos presentados.", "instruccion": "Ordena los siguientes pasos para analizar la información de un texto audiovisual según su relevancia y distinguir hechos de opiniones.", "elementos_desordenados": ["Identificar las opiniones expresadas por el emisor", "Escuchar atentamente todo el texto audiovisual", "Determinar cuáles son los hechos presentados", "Ordenar la información de acuerdo a su importancia para el tema", "Evaluar el valor argumentativo de las opiniones frente a los hechos"]}', '{"orden_correcto": ["Escuchar atentamente todo el texto audiovisual", "Identificar las opiniones expresadas por el emisor", "Determinar cuáles son los hechos presentados", "Ordenar la información de acuerdo a su importancia para el tema", "Evaluar el valor argumentativo de las opiniones frente a los hechos"]}', 3, 0, true, '{"ordenar secuencia","análisis de texto audiovisual","hechos y opiniones"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (575, 122, 'open_ended', 'all', '{"pregunta": "Luego de ver un reportaje sobre el cambio climático, identifica y ordena tres ideas principales según su relevancia. Además, señala cuáles de esas ideas son hechos y cuáles son opiniones, explicando cómo cada una aporta al valor argumentativo del reportaje.", "explicacion": "El docente debe evaluar que el alumno no solo identifique las ideas principales y las clasifique, sino que también comprenda la diferencia entre hechos y opiniones y cómo estas construyen la argumentación del reportaje. Se espera una respuesta organizada y con sustento claro.", "contexto_adicional": "El reportaje presentado debe ser reciente y relacionado con el cambio climático. Se puede utilizar material audiovisual proporcionado por el docente o un enlace a un video seleccionado.", "criterios_evaluacion": ["Identificación correcta y ordenación lógica de las ideas principales según su relevancia (peso: 30%)", "Distinción clara entre hechos y opiniones con ejemplos extraídos del reportaje (peso: 40%)", "Análisis coherente sobre cómo los hechos y opiniones contribuyen al valor argumentativo del texto audiovisual (peso: 30%)"]}', '{"puntos_clave": ["Ordenación lógica de las ideas principales", "Diferenciación clara y correcta entre hechos y opiniones", "Análisis del aporte de cada tipo de información al argumento general"], "respuesta_modelo": "En el reportaje sobre el cambio climático, las tres ideas principales ordenadas por relevancia son: 1) El aumento global de la temperatura promedio está relacionado con la actividad humana (hecho), 2) Algunas políticas gubernamentales actuales son insuficientes para frenar el calentamiento global (opinión), y 3) La conciencia ciudadana es clave para lograr cambios significativos (opinión). Los hechos, como el aumento de temperatura, aportan credibilidad y base científica al reportaje, mientras que las opiniones generan reflexión y llaman a la acción, fortaleciendo el valor argumentativo del contenido.", "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Comprensión audiovisual","Pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (576, 123, 'compare_contrast', 'all', '{"conceptos": ["Estereotipo", "Cliché", "Generalización"], "criterios": ["Definición", "Función en textos audiovisuales", "Impacto en la percepción del mensaje"], "explicacion": "Los estereotipos son ideas preconcebidas y simplificadas sobre un grupo que pueden reforzar prejuicios, los clichés son expresiones o ideas muy repetidas que pierden originalidad, y las generalizaciones son afirmaciones amplias basadas en casos particulares. Mientras los estereotipos y generalizaciones pueden influir en la percepción del mensaje de manera más profunda y a veces negativa, los clichés afectan principalmente la originalidad y frescura del mensaje.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Cliché": {"Definición": "Expresión o idea repetida en exceso que ha perdido originalidad.", "Función en textos audiovisuales": "Facilita la comunicación pero puede hacer el mensaje predecible o poco atractivo.", "Impacto en la percepción del mensaje": "Disminuye la frescura y el interés del mensaje."}, "Estereotipo": {"Definición": "Idea simplificada y fija sobre un grupo social o cultural.", "Función en textos audiovisuales": "Refuerza imágenes preconcebidas, puede influenciar la interpretación del mensaje.", "Impacto en la percepción del mensaje": "Puede generar prejuicios o sesgos en la audiencia."}, "Generalización": {"Definición": "Afirmación amplia basada en casos particulares o limitados.", "Función en textos audiovisuales": "Simplifica la información para facilitar comprensión, pero puede ser imprecisa.", "Impacto en la percepción del mensaje": "Puede inducir a conclusiones erróneas o simplistas."}}}', 3, 0, true, '{comparar,analizar,estereotipos,clichés,generalizaciones}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (577, 123, 'compare_contrast', 'all', '{"conceptos": ["Estereotipo", "Cliché", "Generalización"], "criterios": ["Definición", "Función en el texto audiovisual", "Impacto en la percepción del receptor"], "explicacion": "Los estereotipos son imágenes o ideas simplificadas sobre grupos sociales que se presentan como verdades absolutas; los clichés son expresiones o ideas repetidas hasta perder originalidad; y las generalizaciones son afirmaciones amplias que pueden basarse en pocos casos. Mientras los estereotipos y generalizaciones pueden influir en la formación de prejuicios, los clichés afectan la frescura y autenticidad del mensaje. Los tres conceptos pueden reforzar ideas preconcebidas, pero su función y efecto varían según su uso en el texto audiovisual.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Cliché": {"Definición": "Expresión o idea muy repetida y poco original", "Función en el texto audiovisual": "Reduce la novedad y puede hacer el mensaje predecible", "Impacto en la percepción del receptor": "Disminuye el interés y la credibilidad del mensaje"}, "Estereotipo": {"Definición": "Idea simplificada y fija sobre un grupo social", "Función en el texto audiovisual": "Refuerza imágenes preconcebidas y puede perpetuar prejuicios", "Impacto en la percepción del receptor": "Puede limitar la comprensión y promover juicios sesgados"}, "Generalización": {"Definición": "Afirmación amplia basada en pocos casos", "Función en el texto audiovisual": "Simplifica información compleja para facilitar comprensión", "Impacto en la percepción del receptor": "Puede conducir a conclusiones erróneas o superficiales"}}}', 4, 0, true, '{análisis,comparación,estereotipos,clichés,generalizaciones}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (578, 123, 'open_ended', 'all', '{"pregunta": "Analiza dos textos audiovisuales diferentes (por ejemplo, un documental y un reportaje) que contengan estereotipos, clichés o generalizaciones. ¿Cómo afecta el uso de estos recursos al mensaje que transmiten? Compara sus efectos y argumenta cuál de los dos textos logra comunicar de manera más efectiva y por qué.", "explicacion": "El docente debe evaluar que el estudiante reconozca los estereotipos o generalizaciones en ambos textos y que pueda comparar sus efectos en la comunicación del mensaje. Además, debe valorar la capacidad del alumno para argumentar su postura personal con fundamentos claros y coherentes, demostrando un pensamiento crítico y análisis profundo.", "contexto_adicional": "Se sugiere que el estudiante seleccione dos textos audiovisuales previamente vistos en clase o proporcionados por el docente para realizar el análisis comparativo.", "criterios_evaluacion": ["Identificación y análisis claro de estereotipos, clichés o generalizaciones presentes en ambos textos (peso: 30%)", "Capacidad para comparar y contrastar los efectos de estos recursos en el mensaje de cada texto (peso: 40%)", "Argumentación coherente y fundamentada sobre cuál texto comunica de forma más efectiva, con postura personal sustentada (peso: 30%)"]}', '{"puntos_clave": ["Identificación precisa de estereotipos y generalizaciones en ambos textos", "Análisis comparativo de los efectos en el mensaje audiovisual", "Argumentación personal con fundamentación clara y coherente"], "respuesta_modelo": "En el documental X, se identifican estereotipos relacionados con la representación simplificada de una cultura, lo que genera un mensaje parcial y refuerza prejuicios. En cambio, el reportaje Y utiliza generalizaciones para presentar una problemática social, lo que puede inducir a la simplificación excesiva pero a la vez genera empatía en la audiencia. Comparando ambos, el reportaje comunica de manera más efectiva porque logra conectar emocionalmente con el público sin caer tan directamente en estereotipos dañinos, aunque ambos textos tienen limitaciones que afectan la objetividad. Por lo tanto, considero que el reportaje cumple mejor su función comunicativa debido a su equilibrio entre información y apelación emocional.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Análisis audiovisual",Estereotipos,"Pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (579, 123, 'concept_map', 'all', '{"conceptos": ["Estereotipos", "Clichés", "Generalizaciones", "Texto audiovisual", "Mensaje", "Efectos en la audiencia", "Postura personal"], "explicacion": "El mapa debe mostrar cómo los estereotipos, clichés y generalizaciones se utilizan en los textos audiovisuales para construir un mensaje que tiene ciertos efectos en la audiencia, y cómo esto influye en la postura personal frente a lo escuchado o visto.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis de textos audiovisuales?"}', '{"conceptos_centrales": ["Generalizaciones", "Texto audiovisual"], "relaciones_esperadas": [{"origen": "Estereotipos", "destino": "Generalizaciones", "relacion": "son un tipo de"}, {"origen": "Clichés", "destino": "Generalizaciones", "relacion": "son un tipo de"}, {"origen": "Generalizaciones", "destino": "Texto audiovisual", "relacion": "se utilizan en"}, {"origen": "Texto audiovisual", "destino": "Mensaje", "relacion": "transmite"}, {"origen": "Mensaje", "destino": "Efectos en la audiencia", "relacion": "produce"}, {"origen": "Efectos en la audiencia", "destino": "Postura personal", "relacion": "influyen en"}], "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Análisis de textos",Estereotipos,"Mapas conceptuales"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (580, 123, 'concept_map', 'all', '{"conceptos": ["Estereotipos", "Clichés", "Generalizaciones", "Textos audiovisuales", "Efectos en el mensaje"], "explicacion": "Se espera que los estudiantes muestren cómo los estereotipos, clichés y generalizaciones son elementos que aparecen en textos audiovisuales y cómo estos afectan la interpretación, percepción y eficacia del mensaje. Deben analizar las conexiones entre estos conceptos, mostrando que los estereotipos y clichés son formas específicas de generalizaciones que influyen en la construcción del mensaje audiovisual y sus efectos en la audiencia.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis de textos audiovisuales y su impacto en el mensaje transmitido?"}', '{"conceptos_centrales": ["Generalizaciones", "Efectos en el mensaje"], "relaciones_esperadas": [{"origen": "Estereotipos", "destino": "Generalizaciones", "relacion": "son un tipo de"}, {"origen": "Clichés", "destino": "Generalizaciones", "relacion": "son un tipo de"}, {"origen": "Generalizaciones", "destino": "Textos audiovisuales", "relacion": "aparecen en"}, {"origen": "Textos audiovisuales", "destino": "Efectos en el mensaje", "relacion": "generan"}, {"origen": "Estereotipos", "destino": "Efectos en el mensaje", "relacion": "influyen en"}, {"origen": "Clichés", "destino": "Efectos en el mensaje", "relacion": "influyen en"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Análisis de textos",Estereotipos,"Textos audiovisuales","Primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (581, 124, 'criteria_evaluation', 'all', '{"rubrica": {"Claridad y coherencia de la postura personal": {"Excelente": "La postura personal está claramente expresada, es coherente a lo largo del texto y se mantiene un enfoque consistente.", "Satisfactorio": "La postura personal es entendible pero presenta algunas inconsistencias o falta de claridad en partes del texto.", "Necesita mejorar": "La postura personal es confusa, poco clara o contradictoria en el texto."}, "Consideración del contexto y elementos audiovisuales": {"Excelente": "Se analizan adecuadamente elementos audiovisuales (imágenes, testimonios, estadísticas) y el contexto, integrándolos en la justificación.", "Satisfactorio": "Se mencionan algunos elementos audiovisuales o contexto, pero el análisis es superficial o incompleto.", "Necesita mejorar": "No se consideran o se mencionan muy poco los elementos audiovisuales y contexto en la justificación."}, "Sustentación con argumentos relacionados al contenido": {"Excelente": "Los argumentos están directamente relacionados con el contenido del documental, son precisos y bien explicados.", "Satisfactorio": "Los argumentos se relacionan con el contenido, aunque algunos pueden ser vagos o poco desarrollados.", "Necesita mejorar": "Los argumentos son insuficientes, poco claros o no están relacionados con el contenido del documental."}}, "explicacion": "Se espera que el estudiante exprese claramente su opinión sobre el documental, apoyándola con argumentos extraídos del contenido, contexto y elementos audiovisuales presentados. La justificación debe mostrar un análisis crítico y fundamentado.", "instruccion": "Evalúa tu postura personal frente al documental según la rúbrica propuesta y justifica cada calificación que otorgues.", "caso_a_evaluar": "Un documental de 10 minutos sobre el impacto del cambio climático en comunidades rurales, que presenta testimonios, imágenes de la naturaleza afectada y estadísticas oficiales."}', '{"evaluacion_modelo": {"Claridad y coherencia de la postura personal": "Satisfactorio", "Consideración del contexto y elementos audiovisuales": "Excelente", "Sustentación con argumentos relacionados al contenido": "Excelente"}, "justificacion_modelo": "La postura personal es clara en general pero presenta algunas ambigüedades menores, por lo que se califica como satisfactorio. Los argumentos están bien fundamentados con ejemplos específicos del documental, demostrando excelente relación con el contenido. Además, el análisis incluye una adecuada valoración de los elementos audiovisuales y el contexto, mostrando un nivel excelente en este criterio.", "requiere_revision_humana": true}', 4, 0, true, '{evaluacion_oral_audiovisual,argumentacion,pensamiento_critico}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (594, 126, 'true_false', 'all', '{"afirmacion": "Un discurso argumentativo siempre contiene una conclusión que refleja la postura del emisor.", "explicacion": "Esta afirmación es verdadera porque un discurso argumentativo se caracteriza por presentar una serie de argumentos que conducen a una conclusión clara donde el emisor expresa su posición o punto de vista sobre un tema específico."}', '{"es_verdadero": true}', 1, 0, true, '{"discurso argumentativo","elementos básicos","escucha activa"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (582, 124, 'criteria_evaluation', 'all', '{"rubrica": {"Justificación de la postura": {"Excelente": "La postura personal está claramente expresada y fundamentada con argumentos sólidos que integran contenido del documental, contexto chileno y elementos audiovisuales.", "Satisfactorio": "La postura está expresada y sustentada con algunos argumentos relevantes, aunque la integración de contenido, contexto y elementos audiovisuales es parcial.", "Necesita mejorar": "La postura es vaga o poco clara, con escasa o ninguna fundamentación relacionada al contenido, contexto o elementos audiovisuales."}, "Uso de argumentos relacionados al contenido": {"Excelente": "Presenta múltiples argumentos precisos que reflejan una comprensión profunda del contenido del documental.", "Satisfactorio": "Presenta algunos argumentos correctos, aunque podrían ser más específicos o profundos respecto al contenido.", "Necesita mejorar": "Los argumentos son superficiales, incorrectos o no están relacionados con el contenido del documental."}, "Consideración del contexto y elementos audiovisuales": {"Excelente": "Analiza de forma crítica y coherente cómo el contexto chileno y los elementos audiovisuales influyen en el mensaje del documental.", "Satisfactorio": "Reconoce el contexto y algunos elementos audiovisuales, pero con un análisis limitado o poco profundo.", "Necesita mejorar": "Ignora o no relaciona el contexto y los elementos audiovisuales con la postura personal."}}, "explicacion": "Se espera que el estudiante exprese claramente su postura personal frente al documental, utilizando argumentos bien fundamentados que demuestren comprensión del contenido, análisis del contexto chileno y valoración crítica de los elementos audiovisuales. La justificación debe evidenciar reflexión y capacidad de evaluación.", "instruccion": "Evalúa tu respuesta utilizando la rúbrica proporcionada y justifica por qué otorgas cada calificación en los criterios.", "caso_a_evaluar": "Visualiza el documental \"El cambio climático y sus efectos en Chile\", el cual presenta diferentes posturas de expertos y testimonios de personas afectadas. Luego, redacta una respuesta en la que justifiques tu postura personal frente al documental, sustentándola con argumentos relacionados al contenido, el contexto chileno y los elementos audiovisuales utilizados."}', '{"evaluacion_modelo": {"Justificación de la postura": "Excelente", "Uso de argumentos relacionados al contenido": "Satisfactorio", "Consideración del contexto y elementos audiovisuales": "Satisfactorio"}, "justificacion_modelo": "La postura está claramente expresada y justificada, aunque algunos argumentos podrían ser más específicos respecto al contenido. Además, el análisis del contexto y los elementos audiovisuales es adecuado pero no muy profundo.", "requiere_revision_humana": true}', 5, 0, true, '{"evaluación crítica","texto audiovisual","postura personal",argumentación,"primeros medios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (583, 124, 'criteria_evaluation', 'all', '{"rubrica": {"Consideración del contexto": {"Excelente": "Se incorpora de manera pertinente el contexto social, cultural o histórico del documental para reforzar la postura.", "Satisfactorio": "El contexto es mencionado pero con poca profundidad o relación directa con la postura.", "Necesita mejorar": "No se considera el contexto o se hace de manera errónea."}, "Claridad y coherencia de la postura": {"Excelente": "La postura personal está claramente expresada, con coherencia interna y fácil de entender.", "Satisfactorio": "La postura es clara, aunque presenta algunas inconsistencias o falta de profundidad.", "Necesita mejorar": "La postura es confusa, poco clara o difícil de seguir."}, "Análisis de elementos audiovisuales": {"Excelente": "Se identifican y analizan correctamente elementos audiovisuales relevantes que sustentan la postura.", "Satisfactorio": "Se mencionan algunos elementos audiovisuales, pero el análisis es superficial o incompleto.", "Necesita mejorar": "No se identifican o analizan elementos audiovisuales o el análisis es erróneo."}, "Sustentación con argumentos relacionados al contenido": {"Excelente": "Los argumentos están directamente relacionados con el contenido del documental y son pertinentes y bien desarrollados.", "Satisfactorio": "Los argumentos se relacionan con el contenido, pero son poco elaborados o generales.", "Necesita mejorar": "Los argumentos son escasos, irrelevantes o no se relacionan con el contenido."}}, "explicacion": "Se espera que el estudiante presente una postura personal clara y coherente, sustentada con argumentos que demuestren comprensión del contenido, análisis crítico de los elementos audiovisuales y consideración del contexto. La justificación debe mostrar reflexión y autoevaluación crítica.", "instruccion": "Evalúa tu texto según la rúbrica presentada, asignando un nivel de desempeño para cada criterio y justifica por qué te otorgas esa calificación.", "caso_a_evaluar": "Visualiza un documental breve sobre el impacto de las redes sociales en la comunicación juvenil. Luego, escribe un texto en el que expreses tu postura personal frente a la información presentada, justificándola con argumentos que consideren el contenido, el contexto del documental y los elementos audiovisuales utilizados (imágenes, música, tono, etc.)."}', '{"evaluacion_modelo": {"Consideración del contexto": "Satisfactorio", "Claridad y coherencia de la postura": "Excelente", "Análisis de elementos audiovisuales": "Excelente", "Sustentación con argumentos relacionados al contenido": "Satisfactorio"}, "justificacion_modelo": "La postura está bien expresada y coherente, por lo que se asigna Excelente en claridad. Los argumentos relacionados al contenido son pertinentes pero podrían profundizarse, por eso Satisfactorio. Se realiza un buen análisis de elementos audiovisuales, por lo que es Excelente. Finalmente, se menciona el contexto pero de forma superficial, por lo que es Satisfactorio.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Evaluación crítica",Audiovisual,"Primeros medios","Postura personal"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (584, 124, 'open_ended', 'all', '{"pregunta": "Después de ver el documental proporcionado, ¿cuál es tu postura frente al tema tratado? Justifica tu opinión utilizando argumentos relacionados con el contenido, el contexto histórico y social, y los recursos audiovisuales utilizados para comunicar el mensaje.", "explicacion": "El docente debe evaluar si el estudiante logra expresar claramente su postura personal y si esta está bien sustentada con argumentos específicos que demuestren comprensión del contenido, análisis del contexto y valoración de los recursos audiovisuales empleados. Se debe valorar también la profundidad del análisis crítico y la capacidad para conectar las distintas dimensiones del texto audiovisual.", "contexto_adicional": "El documental aborda un tema social relevante en Chile, presentado con imágenes, entrevistas y narración que buscan generar conciencia en el espectador.", "criterios_evaluacion": ["Claridad y coherencia en la exposición de la postura personal (peso: 30%)", "Uso pertinente y fundamentado de argumentos relacionados con el contenido, contexto y elementos audiovisuales (peso: 40%)", "Capacidad para relacionar y analizar críticamente el mensaje audiovisual y su impacto (peso: 30%)"]}', '{"puntos_clave": ["Postura clara y personal respecto al tema del documental", "Argumentos que evidencian comprensión del contenido y contexto", "Análisis de los recursos audiovisuales y su función en el mensaje"], "respuesta_modelo": "Mi postura frente al documental es favorable, ya que considero que aborda de manera efectiva la problemática social presentada. El contenido está bien estructurado, mostrando diversas perspectivas a través de entrevistas que aportan profundidad. El contexto histórico-social es fundamental para entender la gravedad del tema, y el documental lo sitúa en un marco actual que facilita la conexión con el público. Los recursos audiovisuales, como la música emotiva y las imágenes impactantes, refuerzan el mensaje y generan empatía. Por estas razones, creo que el documental cumple su objetivo de sensibilizar y promover la reflexión crítica.", "requiere_revision_humana": true}', 4, 0, true, '{evaluar,"texto audiovisual","justificación postural","pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (585, 124, 'open_ended', 'all', '{"pregunta": "Después de ver el documental proporcionado, ¿cuál es tu postura personal frente al tema tratado? Justifica tu respuesta analizando el contenido, el contexto en que se presenta el documental y los elementos audiovisuales utilizados para persuadir al espectador.", "explicacion": "El docente debe evaluar que el estudiante no solo exprese una opinión, sino que la justifique de forma argumentada, considerando el contenido del documental, su contexto de producción y los recursos audiovisuales como música, imágenes, edición y narración que afectan la recepción del mensaje.", "contexto_adicional": "Se entregará a los estudiantes un documental breve relacionado con un tema social contemporáneo para que lo analicen.", "criterios_evaluacion": ["Claridad y coherencia en la exposición de la postura personal (peso: 30%)", "Capacidad para sustentar la postura con argumentos relacionados al contenido y contexto del documental (peso: 40%)", "Análisis crítico de los elementos audiovisuales y su influencia en la persuasión del mensaje (peso: 30%)"]}', '{"puntos_clave": ["Expresión clara y coherente de una postura personal", "Argumentos relacionados con el contenido y contexto del documental", "Análisis de elementos audiovisuales y su efecto persuasivo"], "respuesta_modelo": "Mi postura frente al documental es crítica y reflexiva. Considero que el tema tratado, la contaminación ambiental en zonas urbanas, es presentado con datos actuales y casos concretos que evidencian la gravedad del problema (contenido). El contexto de producción, realizado por una organización ambientalista, aporta una perspectiva comprometida pero clara, lo que me genera confianza en la información presentada. Además, los elementos audiovisuales como la música inquietante, las imágenes de lugares afectados y testimonios de expertos refuerzan la urgencia del mensaje y logran una conexión emocional conmigo, persuadiéndome sobre la necesidad de actuar. Por estas razones, apoyo la postura del documental y creo que es fundamental tomar medidas frente a esta problemática.", "requiere_revision_humana": true}', 5, 0, true, '{evaluar,"texto audiovisual",argumentación,"pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (586, 125, 'open_ended', 'all', '{"pregunta": "Elige un documental o reportaje audiovisual que hayas visto recientemente y diseña un análisis que integre la relación entre las imágenes, el texto y el sonido. Explica cómo estos elementos contribuyen a transmitir el mensaje principal y relaciona tu análisis con algún tema o una obra literaria que hayan estudiado en el curso. Finalmente, presenta tu postura personal frente a la efectividad del audiovisual y argumenta tu opinión.", "explicacion": "Se evalúa la capacidad del estudiante para crear un análisis audiovisual que integre elementos formales y temáticos, además de expresar una postura reflexiva y argumentada. El docente debe valorar la profundidad del análisis y la coherencia en la relación con los contenidos del curso, así como la calidad de los argumentos presentados.", "contexto_adicional": "El estudiante debe seleccionar un audiovisual que haya visto y que pueda vincular con contenidos literarios trabajados en primero medio, como por ejemplo temas sociales, culturales o literarios abordados en clase.", "criterios_evaluacion": ["Análisis coherente y profundo de la relación entre imágenes, texto y sonido en el audiovisual seleccionado (peso: 40%)", "Capacidad para relacionar el análisis con temas u obras estudiadas en el curso, demostrando comprensión interdisciplinaria (peso: 30%)", "Presentación clara y argumentada de la postura personal frente al audiovisual, sustentada con argumentos sólidos (peso: 30%)"]}', '{"puntos_clave": ["Descripción clara de cómo imágenes, texto y sonido se complementan para transmitir el mensaje", "Relación explícita con un tema u obra literaria estudiada en el curso", "Postura personal frente al audiovisual fundamentada con argumentos convincentes"], "respuesta_modelo": "He seleccionado el documental ''La voz de los pueblos indígenas'' que aborda la lucha por los derechos y la identidad cultural. En este audiovisual, las imágenes muestran paisajes naturales y rostros de personas indígenas, lo que genera una conexión emocional y visual con el tema. El texto, a través de narraciones y testimonios, aporta información histórica y social que contextualiza la problemática. El sonido incluye música tradicional y voces en lenguas originarias, lo que refuerza la autenticidad y profundidad cultural del mensaje. Esta combinación potencia el impacto del mensaje sobre la importancia de respetar y preservar las culturas originarias. Relaciono este análisis con la obra ''Los perros hambrientos'' de Hernán Rivera Letelier, que también aborda temas de identidad y marginalidad social. Considero que el documental es efectivo porque logra sensibilizar al espectador mediante una integración coherente de sus elementos audiovisuales, y su mensaje se percibe claro y emotivo.", "requiere_revision_humana": true}', 4, 0, true, '{"análisis audiovisual","pensamiento crítico",creación,"Lengua y Literatura","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (588, 125, 'concept_map', 'all', '{"conceptos": ["Imágenes", "Texto", "Sonido", "Análisis audiovisual", "Temas del curso", "Obras literarias", "Relación entre elementos", "Postura personal"], "explicacion": "Se espera que el estudiante muestre cómo las imágenes, el texto y el sonido se integran para formar un análisis audiovisual coherente, relacionando estos elementos con los temas y obras literarias estudiadas. Además, debe incluir cómo su postura personal se sustenta a partir de esta integración, mostrando conexiones claras entre los conceptos.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto del análisis audiovisual de obras y temas estudiados en Lengua y Literatura?"}', '{"conceptos_centrales": ["Análisis audiovisual", "Postura personal"], "relaciones_esperadas": [{"origen": "Imágenes", "destino": "Texto", "relacion": "complementan"}, {"origen": "Sonido", "destino": "Imágenes", "relacion": "refuerza"}, {"origen": "Análisis audiovisual", "destino": "Imágenes", "relacion": "integra"}, {"origen": "Análisis audiovisual", "destino": "Texto", "relacion": "integra"}, {"origen": "Análisis audiovisual", "destino": "Sonido", "relacion": "integra"}, {"origen": "Análisis audiovisual", "destino": "Temas del curso", "relacion": "relaciona con"}, {"origen": "Análisis audiovisual", "destino": "Obras literarias", "relacion": "relaciona con"}, {"origen": "Postura personal", "destino": "Análisis audiovisual", "relacion": "se basa en"}, {"origen": "Relación entre elementos", "destino": "Análisis audiovisual", "relacion": "fundamenta"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Análisis audiovisual","Mapa conceptual","Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (589, 125, 'concept_map', 'all', '{"conceptos": ["Imagen", "Texto", "Sonido", "Mensaje audiovisual", "Emoción", "Contexto", "Tema", "Obra literaria"], "explicacion": "Se espera que el estudiante muestre cómo la imagen, el texto y el sonido se combinan para construir un mensaje audiovisual que transmite emociones y significado. Este mensaje se debe conectar con el contexto, el tema y las obras literarias estudiadas en el curso, evidenciando una comprensión integrada del análisis audiovisual.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el análisis de un mensaje audiovisual vinculado a temas y obras literarias estudiadas?"}', '{"conceptos_centrales": ["Mensaje audiovisual", "Tema"], "relaciones_esperadas": [{"origen": "Imagen", "destino": "Texto", "relacion": "complementa"}, {"origen": "Sonido", "destino": "Mensaje audiovisual", "relacion": "refuerza"}, {"origen": "Texto", "destino": "Mensaje audiovisual", "relacion": "construye"}, {"origen": "Mensaje audiovisual", "destino": "Emoción", "relacion": "evoca"}, {"origen": "Mensaje audiovisual", "destino": "Tema", "relacion": "se relaciona con"}, {"origen": "Tema", "destino": "Obra literaria", "relacion": "se basa en"}, {"origen": "Mensaje audiovisual", "destino": "Contexto", "relacion": "considera"}], "requiere_revision_humana": true}', 5, 0, true, '{"análisis audiovisual","mapa conceptual","lengua y literatura","primer medio",crear}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (590, 125, 'open_ended', 'all', '{"pregunta": "Elige un documental o noticia audiovisual que hayas visto recientemente y diseña un análisis en el que expliques cómo la relación entre las imágenes, el texto y el sonido contribuye a comunicar el mensaje principal. Además, relaciona este análisis con al menos un tema o una obra literaria estudiada en el curso, justificando tu postura personal sobre la efectividad del audiovisual.", "explicacion": "El docente debe evaluar si el alumno logra identificar y explicar cómo los diferentes elementos audiovisuales trabajan en conjunto para transmitir un mensaje, si puede establecer vínculos conceptuales con contenidos literarios y si sustenta adecuadamente su opinión personal con argumentos sólidos.", "contexto_adicional": "El estudiante debe seleccionar un audiovisual reciente que haya visto y vincularlo con contenidos literarios del curso para promover la integración de saberes.", "criterios_evaluacion": ["Claridad y profundidad en la explicación de la relación entre imágenes, texto y sonido (peso: 40%)", "Capacidad para relacionar el análisis audiovisual con temas u obras literarias del curso (peso: 35%)", "Argumentación justificando la postura personal sobre la efectividad del audiovisual, con fundamentos claros (peso: 25%)"]}', '{"puntos_clave": ["Identificación y explicación clara de la relación entre imágenes, texto y sonido", "Vinculación explícita con temas u obras literarias del curso", "Justificación argumentada de la postura personal sobre la efectividad del audiovisual"], "respuesta_modelo": "En el documental ''La lucha por el bosque'', las imágenes muestran la destrucción del entorno natural mientras el texto informativo explica las causas y consecuencias de la deforestación. El sonido ambiental de la naturaleza y la música dramática refuerzan la sensación de urgencia y pérdida. Esta combinación comunica eficazmente el mensaje de la importancia de proteger el medio ambiente. Relaciono este mensaje con el tema de la naturaleza y el hombre presente en el poema ''La tierra baldía'' estudiado en clase, donde también se refleja una preocupación por la degradación del entorno. Personalmente considero que el audiovisual es muy efectivo porque logra conmover y sensibilizar al espectador a través de la integración coherente de sus elementos, apoyando el mensaje que busca transmitir.", "requiere_revision_humana": true}', 5, 0, true, '{"análisis audiovisual","pensamiento crítico",creación,"primer medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (591, 126, 'multiple_choice', 'all', '{"opciones": {"A": "La descripción detallada de personajes y lugares", "B": "La presentación de una opinión y sus razones para defenderla", "C": "La narración cronológica de eventos sin opiniones", "D": "El uso exclusivo de datos numéricos sin explicación"}, "pregunta": "¿Cuál de los siguientes elementos es básico para identificar un discurso argumentativo escuchado?", "explicacion": "La respuesta correcta es B porque un discurso argumentativo se caracteriza por presentar una opinión o postura junto con razones o argumentos que la respaldan. La opción A corresponde a elementos de textos descriptivos, la C a narraciones o relatos, y la D, aunque puede incluir datos, no es suficiente ni exclusivo para un discurso argumentativo si no se explica o justifica la postura."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"discurso argumentativo","escucha activa","elementos básicos"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (592, 126, 'multiple_choice', 'all', '{"opciones": {"A": "La descripción detallada de un lugar o personaje", "B": "La presentación de una opinión con razones que la apoyan", "C": "La enumeración de hechos sin expresar una opinión", "D": "El uso de rimas y metáforas para embellecer el texto"}, "pregunta": "¿Cuál de los siguientes elementos es básico para identificar un discurso argumentativo escuchado?", "explicacion": "La respuesta correcta es B porque un discurso argumentativo se caracteriza por presentar una opinión o postura respaldada por razones o argumentos que buscan convencer al receptor. La opción A corresponde a una descripción, no a un argumento; la opción C describe la exposición de hechos sin juicio, propia de textos informativos; la opción D se relaciona con recursos literarios, característicos de textos poéticos o narrativos, no argumentativos."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"discurso argumentativo","elementos básicos","escucha activa"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (593, 126, 'multiple_choice', 'all', '{"opciones": {"A": "Las descripciones detalladas de un lugar o persona", "B": "Las razones o argumentos que el emisor usa para defender su punto de vista", "C": "Los sentimientos personales del emisor expresados sin justificación", "D": "Las instrucciones o pasos para realizar una tarea"}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para identificar un discurso argumentativo escuchado?", "explicacion": "La respuesta correcta es B porque un discurso argumentativo se caracteriza por presentar razones o argumentos para defender una idea o punto de vista. La opción A corresponde más a un texto descriptivo, la C refleja opiniones sin soporte argumentativo, y la D se relaciona con textos instructivos, no argumentativos."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"discurso argumentativo","escucha activa","elementos del discurso"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (596, 127, 'multiple_choice', 'all', '{"opciones": {"A": "El discurso solo da opiniones personales sin usar ejemplos ni razones.", "B": "El discurso presenta una idea principal, seguida de argumentos que la apoyan y una conclusión que refuerza el mensaje.", "C": "El discurso se enfoca en narrar una historia sin relacionarla con el tema principal.", "D": "El discurso consiste en una lista de datos sin conectar entre sí ni expresar una opinión."}, "pregunta": "Después de escuchar un discurso argumentativo sobre la importancia del reciclaje, ¿cuál es la mejor manera de explicar su estructura con tus propias palabras?", "explicacion": "La opción B es correcta porque describe claramente la estructura típica de un discurso argumentativo: una idea central, argumentos que la sustentan y una conclusión. La opción A es incorrecta porque un discurso argumentativo debe incluir argumentos, no solo opiniones. La opción C describe más bien una narración, que no es característica de un discurso argumentativo. La opción D menciona datos sin conexión ni opinión, lo que impide que el discurso sea argumentativo, ya que necesita relacionar ideas y persuadir."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"discurso argumentativo","estructura del discurso","resumen oral",comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (597, 127, 'multiple_choice', 'all', '{"opciones": {"A": "Se presenta una historia personal, se describen emociones y se concluye con una opinión sin argumentos.", "B": "Se expone una tesis, se entregan argumentos que la respaldan y se concluye reafirmando la idea principal.", "C": "Se enumeran hechos aleatorios sin relación y se termina con una pregunta abierta.", "D": "Se presentan múltiples opiniones sin tomar una postura clara y se finaliza con una anécdota."}, "pregunta": "Después de escuchar un discurso argumentativo sobre la importancia del reciclaje, ¿cuál de las siguientes opciones describe correctamente la estructura típica de este tipo de discurso?", "explicacion": "La respuesta correcta es la opción B porque un discurso argumentativo se caracteriza por presentar una tesis o idea principal, apoyar esa tesis con argumentos lógicos o evidencias, y concluir reforzando la posición tomada. La opción A corresponde más a un relato personal que no estructura argumentos; la C muestra falta de coherencia y estructura propia de un discurso argumentativo; y la D describe más un diálogo o debate sin una postura clara, lo que no cumple con la estructura típica de un discurso argumentativo."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"discurso argumentativo","estructura del discurso","comprensión oral","Lengua y Literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (598, 127, 'true_false', 'all', '{"afirmacion": "Un resumen oral de un discurso argumentativo debe incluir una explicación clara de los argumentos presentados por el emisor.", "explicacion": "Esta afirmación es verdadera porque para resumir correctamente un discurso argumentativo, es fundamental comprender y explicar los argumentos que el emisor utiliza para apoyar su postura. Sin esta explicación, el resumen no reflejaría la estructura ni el contenido clave del discurso."}', '{"es_verdadero": true}', 2, 0, true, '{"resumen oral","discurso argumentativo",comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (599, 127, 'drag_drop_matching', 'all', '{"explicacion": "La tesis es la idea central del discurso; el argumento respalda esa idea con razones; la conclusión sintetiza la postura final; el ejemplo ilustra un argumento con un caso concreto; el contraargumento rebatiendo opiniones opuestas; y la opinión personal es una expresión subjetiva sin respaldo.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente en un discurso argumentativo.", "columna_derecha": ["Afirmación principal que se defiende en el discurso", "Dato o razón que apoya la tesis", "Frase final que resume y reafirma la postura", "Caso específico que ilustra un argumento", "Respuesta que refuta una opinión contraria", "Expresión subjetiva sin evidencia"], "columna_izquierda": ["Tesis", "Argumento", "Conclusión", "Ejemplo", "Contraargumento", "Opinión personal"]}', '{"emparejamientos_correctos": {"Tesis": "Afirmación principal que se defiende en el discurso", "Ejemplo": "Caso específico que ilustra un argumento", "Argumento": "Dato o razón que apoya la tesis", "Conclusión": "Frase final que resume y reafirma la postura", "Contraargumento": "Respuesta que refuta una opinión contraria", "Opinión personal": "Expresión subjetiva sin evidencia"}}', 2, 0, true, '{"discurso argumentativo","estructura textual","comprensión oral"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (600, 127, 'sequencing', 'all', '{"explicacion": "El proceso para resumir un discurso argumentativo comienza con escuchar atentamente y detectar los argumentos para comprender el mensaje. Luego, se identifica la conclusión principal que el emisor quiere transmitir. Después, se explica la estructura del discurso con tus propias palabras para asegurar comprensión. A continuación, se evalúan los argumentos para entender su relevancia. Finalmente, se organizan todas estas ideas en un resumen coherente que refleje el contenido y la estructura del discurso.", "instruccion": "Ordena los siguientes pasos para resumir un discurso argumentativo de manera correcta", "elementos_desordenados": ["Identificar la conclusión principal del discurso", "Escuchar atentamente y detectar los argumentos presentados", "Expresar con tus propias palabras la estructura del discurso", "Evaluar la relevancia y fuerza de los argumentos", "Organizar las ideas principales en un resumen coherente"]}', '{"orden_correcto": ["Escuchar atentamente y detectar los argumentos presentados", "Identificar la conclusión principal del discurso", "Expresar con tus propias palabras la estructura del discurso", "Evaluar la relevancia y fuerza de los argumentos", "Organizar las ideas principales en un resumen coherente"]}', 3, 0, true, '{resumen_oral,discurso_argumentativo,comprension}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (601, 128, 'criteria_evaluation', 'all', '{"rubrica": {"Evidencia y soporte de los argumentos": {"Excelente": "Evalúa críticamente la calidad y relevancia de la evidencia presentada para apoyar los argumentos.", "Satisfactorio": "Reconoce la evidencia usada, pero la evaluación de su calidad o relevancia es superficial.", "Necesita mejorar": "No evalúa adecuadamente la evidencia o la ignora en su análisis."}, "Claridad y pertinencia de los argumentos": {"Excelente": "Identifica claramente cada argumento y explica por qué es pertinente y relevante para el tema tratado.", "Satisfactorio": "Reconoce la mayoría de los argumentos y explica su pertinencia de forma general.", "Necesita mejorar": "Tiene dificultades para identificar los argumentos o su pertinencia en el contexto del discurso."}, "Coherencia y lógica en la argumentación": {"Excelente": "Analiza la coherencia interna y la lógica entre los argumentos, señalando posibles contradicciones o falacias.", "Satisfactorio": "Reconoce la mayoría de las relaciones lógicas, pero no detecta contradicciones menores.", "Necesita mejorar": "No identifica problemas de coherencia ni lógica en el discurso."}, "Capacidad de juicio crítico y justificación": {"Excelente": "Justifica claramente su evaluación con argumentos propios y ejemplos concretos del discurso.", "Satisfactorio": "Presenta justificaciones generales, pero sin profundizar o ejemplificar suficientemente.", "Necesita mejorar": "Da evaluaciones sin justificación o con argumentos poco claros."}}, "explicacion": "Se espera que el estudiante identifique y analice críticamente los argumentos, valore la calidad de la evidencia, detecte la coherencia interna y fundamente su evaluación con razonamientos claros y ejemplos precisos extraídos del discurso.", "instruccion": "Evalúa la validez y efectividad de los argumentos presentados en el discurso utilizando la rúbrica proporcionada. Justifica cada calificación que otorgues con ejemplos o explicaciones claras.", "caso_a_evaluar": "Escucha un discurso argumentativo sobre la importancia de implementar energías renovables en las escuelas chilenas. El emisor presenta datos estadísticos, opiniones de expertos y ejemplos de otras instituciones. Evalúa la validez y efectividad de los argumentos usados para persuadir a la audiencia."}', '{"evaluacion_modelo": {"Evidencia y soporte de los argumentos": "Excelente", "Claridad y pertinencia de los argumentos": "Satisfactorio", "Coherencia y lógica en la argumentación": "Excelente", "Capacidad de juicio crítico y justificación": "Satisfactorio"}, "justificacion_modelo": "El discurso presenta argumentos claros y generalmente pertinentes, aunque algunos son superficiales. La evidencia usada es sólida y relevante, apoyando bien los puntos expuestos. La argumentación mantiene coherencia y lógica, sin contradicciones evidentes. La justificación del evaluador es adecuada, pero podría profundizar más en algunos ejemplos para mejorar su juicio crítico.", "requiere_revision_humana": true}', 4, 0, true, '{argumentación,"evaluación crítica","discurso oral","primeros medios","energías renovables"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (604, 128, 'open_ended', 'all', '{"pregunta": "Después de escuchar el discurso argumentativo presentado, evalúa la validez y efectividad de los argumentos utilizados por el emisor. ¿Cuáles consideras que son los argumentos más sólidos y por qué? ¿Hay algún argumento que creas que es débil o inválido? Justifica tu respuesta con ejemplos específicos del discurso.", "explicacion": "Para evaluar esta pregunta, el docente debe revisar si el estudiante identifica correctamente los argumentos principales del discurso, si realiza un análisis crítico sobre su validez y efectividad, y si fundamenta sus juicios con ejemplos concretos. Se valorará la profundidad y claridad en la argumentación, así como la capacidad para diferenciar entre argumentos fuertes y débiles.", "contexto_adicional": "El discurso argumentativo fue escuchado en clase y trata sobre un tema de actualidad relevante para los estudiantes.", "criterios_evaluacion": ["Identificación clara de los argumentos presentados en el discurso (peso: 30%)", "Análisis crítico que evalúa la validez y efectividad de los argumentos con justificaciones específicas (peso: 40%)", "Capacidad para argumentar con ejemplos y expresar una opinión fundamentada (peso: 30%)"]}', '{"puntos_clave": ["Identificación de los argumentos principales del discurso", "Evaluación de la validez basada en evidencias o datos", "Juicio crítico sobre la efectividad de los argumentos para persuadir"], "respuesta_modelo": "En el discurso escuchado, el emisor presenta varios argumentos para apoyar su postura sobre la importancia de la educación ambiental. El argumento más sólido es el que se basa en datos científicos actuales, lo que le da validez y credibilidad. Además, utiliza ejemplos concretos de impactos ambientales visibles, lo que aumenta la efectividad para el público. Sin embargo, un argumento débil es el que apela únicamente a las emociones sin respaldo de datos, ya que aunque puede generar empatía, no es suficiente para sostener la postura. Por lo tanto, considero que la combinación de evidencias científicas y ejemplos concretos es lo que hace que el discurso sea persuasivo, mientras que los argumentos basados solo en sentimientos son menos convincentes.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Evaluación de argumentos","Pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (605, 128, 'open_ended', 'all', '{"pregunta": "Después de escuchar el discurso argumentativo presentado, evalúa la validez y efectividad de los argumentos utilizados por el emisor. ¿Cuáles consideras que son los argumentos más fuertes y por qué? ¿Existen falacias o debilidades en el discurso que afectan su persuasión? Fundamenta tu respuesta con ejemplos concretos del discurso.", "explicacion": "El docente debe valorar que el estudiante no solo identifique los argumentos, sino que sea capaz de analizarlos críticamente, señalando cuáles son persuasivos y cuáles presentan fallas lógicas o evidenciales. Se debe considerar la capacidad para ejemplificar y justificar sus evaluaciones con referencias claras al discurso.", "contexto_adicional": "Discurso argumentativo escuchado previamente en clase, relacionado con un tema de actualidad o interés social.", "criterios_evaluacion": ["Identificación clara y precisa de los argumentos principales del discurso (peso: 30%)", "Análisis crítico de la validez y efectividad de los argumentos, incluyendo detección de posibles falacias o debilidades (peso: 40%)", "Capacidad para fundamentar la evaluación con ejemplos específicos y coherentes extraídos del discurso (peso: 30%)"]}', '{"puntos_clave": ["Identificación de argumentos principales (ejemplo: datos estadísticos, afirmaciones generales)", "Análisis de validez (ejemplo: credibilidad, objetividad) y efectividad (ejemplo: persuasión, impacto)", "Detección de falacias o debilidades (ejemplo: generalizaciones, ausencia de contraargumentos)"], "respuesta_modelo": "En el discurso escuchado, uno de los argumentos más fuertes fue el uso de datos estadísticos recientes para respaldar la importancia de cuidar el medio ambiente, ya que aportan credibilidad y objetividad al mensaje. Sin embargo, noté que el emisor utilizó una generalización apresurada al afirmar que ''todos los jóvenes no se preocupan por el cambio climático'', lo que debilita su posición porque no todos los jóvenes tienen la misma actitud. Además, el discurso carece de contraargumentos, lo que limita la efectividad persuasiva, pues no considera puntos de vista opuestos. En conclusión, aunque algunos argumentos son válidos y bien fundamentados, la presencia de falacias y la falta de equilibrio afectan la fuerza total del discurso.", "requiere_revision_humana": true}', 5, 0, true, '{evaluar,argumentación,"pensamiento crítico",discurso,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (606, 129, 'multiple_choice', 'all', '{"opciones": {"A": "Interrumpir al interlocutor para expresar rápidamente tu opinión", "B": "Escuchar atentamente y demostrar comprensión de lo que dice el otro", "C": "Cambiar constantemente de tema para mantener la conversación interesante", "D": "Hablar sin fundamentar tus ideas para ahorrar tiempo"}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para mantener un diálogo constructivo en una conversación grupal?", "explicacion": "La opción B es correcta porque escuchar atentamente y demostrar comprensión son elementos básicos para un diálogo constructivo, ya que permiten mantener el foco y respetar la opinión del interlocutor. La opción A es incorrecta porque interrumpir rompe la comunicación y dificulta el entendimiento. La opción C es errónea porque cambiar de tema constantemente dispersa el foco del diálogo. La opción D es incorrecta porque fundamentar las ideas es esencial para un debate pertinente y enriquecedor."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"Diálogo constructivo",Comprensión,"Lengua y Literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (607, 129, 'multiple_choice', 'all', '{"opciones": {"A": "Interrumpir al interlocutor para expresar rápidamente tu opinión", "B": "Escuchar atentamente y demostrar comprensión de lo que dicen los demás", "C": "Hablar solo sobre temas que te interesan, sin considerar a los demás", "D": "Cambiar constantemente de tema para evitar conflictos"}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para mantener un diálogo constructivo en una conversación grupal?", "explicacion": "La opción B es correcta porque un diálogo constructivo implica escuchar atentamente y demostrar comprensión hacia los demás interlocutores, lo que permite mantener el foco y fundamentar posturas de manera pertinente. La opción A es incorrecta porque interrumpir impide la comunicación fluida y la comprensión mutua. La opción C es errónea porque no considera las ideas del grupo, lo que dificulta el diálogo. La opción D es un error porque cambiar de tema evita el desarrollo profundo de ideas, impidiendo una conversación constructiva."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{dialogo_constructivo,comprension_oral,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (608, 129, 'multiple_choice', 'all', '{"opciones": {"A": "Interrumpir al interlocutor para aclarar rápidamente el punto", "B": "Escuchar atentamente y demostrar comprensión de lo que dice el otro", "C": "Cambiar de tema cuando no se está de acuerdo", "D": "Hablar sólo para expresar opiniones sin considerar las de los demás"}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para mantener un diálogo constructivo en una conversación grupal?", "explicacion": "La opción B es correcta porque escuchar atentamente y demostrar comprensión es esencial para un diálogo constructivo, ya que permite mantener el foco y fundamentar posturas de manera pertinente. La opción A es incorrecta porque interrumpir rompe el respeto y la fluidez del diálogo. La opción C es un error porque cambiar de tema evita el debate constructivo sobre el punto en discusión. La opción D es incorrecta porque no considerar las opiniones de los demás impide un diálogo colaborativo y enriquecedor."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"diálogo constructivo",comprensión,debate,"primero medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (609, 129, 'true_false', 'all', '{"afirmacion": "Mantener el foco en el tema durante un diálogo constructivo es fundamental para que la conversación sea clara y productiva.", "explicacion": "Esta afirmación es verdadera porque mantener el foco ayuda a que los participantes no se desvíen del tema principal, facilitando una comprensión mutua y una discusión efectiva y ordenada."}', '{"es_verdadero": true}', 1, 0, true, '{"diálogo constructivo",comprensión,foco}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (610, 129, 'fill_blanks', 'all', '{"texto": "En un diálogo constructivo, es fundamental mantener el [BLANK_1] para que la conversación no se desvíe del tema principal. Además, se debe demostrar [BLANK_2] hacia las ideas expresadas por los demás participantes, escuchando atentamente y mostrando respeto.", "pistas": ["Se refiere a no perder el tema principal de la conversación", "Significa entender y valorar lo que otros dicen"], "explicacion": "Un diálogo constructivo requiere que los participantes mantengan el foco en el tema tratado y demuestren comprensión hacia las ideas de sus interlocutores, lo que permite un intercambio respetuoso y efectivo."}', '{"respuestas_correctas": {"BLANK_1": ["foco", "atención"], "BLANK_2": ["comprensión", "entendimiento"]}}', 2, 0, true, '{"diálogo constructivo",comprensión,foco,"Lengua y Literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (611, 130, 'multiple_choice', 'all', '{"opciones": {"A": "Para poder interrumpir al interlocutor cuando no esté de acuerdo.", "B": "Para comprender las ideas del interlocutor y responder de manera fundamentada.", "C": "Para preparar rápidamente una respuesta sin prestar mucha atención.", "D": "Para evitar que el interlocutor tenga tiempo de explicar sus argumentos."}, "pregunta": "¿Por qué es importante escuchar activamente durante un debate?", "explicacion": "La respuesta correcta es B porque escuchar activamente permite entender correctamente las ideas del interlocutor, lo que facilita responder con argumentos pertinentes y fundamentados. La opción A es un error conceptual, ya que interrumpir impide la comprensión y el diálogo constructivo. La opción C refleja una actitud pasiva y superficial, que no garantiza comprensión. La opción D es incorrecta porque evitar que el interlocutor explique sus ideas va en contra del diálogo y la comprensión mutua."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"escucha activa",debate,comprensión,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (612, 130, 'multiple_choice', 'all', '{"opciones": {"A": "Porque permite preparar una réplica sin prestar atención a lo que dice el otro.", "B": "Porque ayuda a mantener el foco en el tema y a responder de manera pertinente a las ideas expuestas.", "C": "Porque facilita interrumpir al interlocutor para corregirlo rápidamente.", "D": "Porque demuestra que uno tiene más conocimientos que los demás participantes."}, "pregunta": "¿Por qué es importante escuchar activamente y demostrar comprensión de las ideas del interlocutor durante un debate?", "explicacion": "La respuesta correcta es la opción B, ya que escuchar activamente y demostrar comprensión permite mantener el enfoque en el tema del debate y fundamentar respuestas pertinentes y relevantes. La opción A es incorrecta porque preparar una réplica sin escuchar realmente no contribuye a un diálogo constructivo. La opción C es errónea porque interrumpir suele obstaculizar la comunicación y genera un ambiente conflictivo. La opción D es un error conceptual porque el debate no se trata de demostrar superioridad, sino de intercambiar ideas respetuosamente."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{debate,"escucha activa",comprensión,"Lengua y Literatura","Primero Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (613, 130, 'true_false', 'all', '{"afirmacion": "Escuchar activamente en un debate significa simplemente esperar a que termine el interlocutor para responder.", "explicacion": "Esta afirmación es falsa porque escuchar activamente implica prestar atención completa, comprender y procesar las ideas del interlocutor, no solo esperar a que termine para hablar. La escucha activa permite fundamentar mejor la propia postura y mantener un diálogo constructivo."}', '{"es_verdadero": false}', 2, 0, true, '{"escucha activa",debate,comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (614, 130, 'drag_drop_matching', 'all', '{"explicacion": "Escuchar activamente implica prestar atención completa para entender lo que dice el otro. Mantener el foco significa no desviarse del tema principal durante el debate. Fundamentar la postura es usar argumentos y evidencia para apoyar una opinión. Demostrar comprensión se logra reformulando o parafraseando lo que dijo el interlocutor.", "instruccion": "Empareja cada concepto con su definición/ejemplo correspondiente", "columna_derecha": ["Prestar atención completa para entender lo que dice el otro", "No desviarse del tema principal durante el debate", "Usar argumentos y evidencia para apoyar una opinión", "Reformular o parafrasear lo que dijo el interlocutor", "Hablar sin dejar hablar a los demás"], "columna_izquierda": ["Escuchar activamente", "Mantener el foco", "Fundamentar la postura", "Demostrar comprensión", "Distractor: Interrumpir al interlocutor"]}', '{"emparejamientos_correctos": {"Mantener el foco": "No desviarse del tema principal durante el debate", "Escuchar activamente": "Prestar atención completa para entender lo que dice el otro", "Demostrar comprensión": "Reformular o parafrasear lo que dijo el interlocutor", "Fundamentar la postura": "Usar argumentos y evidencia para apoyar una opinión"}}', 2, 0, true, '{debate,"comprensión oral","habilidades comunicativas"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (615, 130, 'sequencing', 'all', '{"explicacion": "El orden correcto refleja el proceso de una escucha activa: primero se debe prestar atención completa sin interrumpir, luego hacer preguntas para clarificar, después resumir para confirmar comprensión y finalmente fundamentar la propia opinión. Responder inmediatamente sin analizar interrumpe la escucha activa y debe evitarse.", "instruccion": "Ordena los siguientes pasos para escuchar activamente durante un debate en la secuencia correcta:", "elementos_desordenados": ["Responder inmediatamente sin analizar lo que dijo el interlocutor", "Prestar atención completa a quien habla, sin interrumpir", "Hacer preguntas para clarificar las ideas del interlocutor", "Resumir lo que entendiste para demostrar que comprendes", "Fundamentar tu opinión usando información relevante"]}', '{"orden_correcto": ["Prestar atención completa a quien habla, sin interrumpir", "Hacer preguntas para clarificar las ideas del interlocutor", "Resumir lo que entendiste para demostrar que comprendes", "Fundamentar tu opinión usando información relevante", "Responder inmediatamente sin analizar lo que dijo el interlocutor"]}', 3, 0, true, '{"escucha activa",debate,"comprensión oral"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (628, 133, 'open_ended', 'all', '{"pregunta": "Durante un debate sobre un tema controversial, ¿cómo justificarías la importancia de negociar acuerdos y tomar turnos respetando al interlocutor para mantener un diálogo constructivo? Explica con ejemplos cómo estas prácticas influyen en la efectividad del debate y en el logro de sus objetivos.", "explicacion": "Evalúe si el estudiante puede identificar y argumentar la relevancia de negociar y respetar turnos en un debate, mostrando comprensión del rol del interlocutor. Además, revise si conecta estas prácticas con la efectividad del diálogo y si utiliza ejemplos concretos que demuestren análisis crítico.", "contexto_adicional": "Considera que el debate es un espacio para explorar ideas y llegar a acuerdos mediante la escucha activa y el respeto mutuo, sin perder el foco en el tema central.", "criterios_evaluacion": ["Justificación clara y coherente sobre la importancia de negociar acuerdos y tomar turnos (peso: 40%)", "Capacidad para relacionar la negociación y el respeto en los turnos con la efectividad y el logro de objetivos en el debate (peso: 35%)", "Uso de ejemplos pertinentes que demuestren comprensión del rol del interlocutor y del diálogo constructivo (peso: 25%)"]}', '{"puntos_clave": ["Importancia de negociar acuerdos para ordenar la interacción", "Respeto en la toma de turnos para evitar interrupciones y favorecer la escucha activa", "Relación entre estas prácticas y la efectividad del debate para cumplir objetivos", "Ejemplos que reflejen la aplicación concreta de estas ideas"], "respuesta_modelo": "Negociar acuerdos y tomar turnos respetando al interlocutor es fundamental para mantener un diálogo constructivo en un debate porque permite que todas las voces sean escuchadas y consideradas, evitando interrupciones y conflictos que desvíen el foco. Por ejemplo, si durante un debate sobre el uso de celulares en el aula, los participantes acuerdan turnarse para hablar, se facilita la comprensión de cada postura y se pueden construir ideas conjuntas. Esta práctica no solo mejora la comunicación sino que también ayuda a que el debate sea más efectivo, ya que permite fundamentar opiniones con información pertinente y mantener la atención en el tema central, lo que finalmente contribuye a alcanzar acuerdos o entender mejor las diferencias.", "requiere_revision_humana": true}', 5, 0, true, '{debate,"dialogo constructivo","toma de turnos",negociacion,"pensamiento critico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (616, 131, 'multiple_choice', 'all', '{"opciones": {"A": "Repetir varias veces tu opinión sin dar ejemplos para que quede clara.", "B": "Escuchar al interlocutor, hacer preguntas relacionadas y usar datos o ejemplos para apoyar tu argumento.", "C": "Ignorar los argumentos contrarios y enfocarte solo en tus ideas principales.", "D": "Interrumpir al otro para corregirlo rápidamente y así demostrar que tienes la razón."}, "pregunta": "Durante un debate, ¿cuál de las siguientes acciones es la mejor estrategia para fundamentar tu postura y enriquecer la discusión?", "explicacion": "La opción B es correcta porque implica escuchar activamente, hacer preguntas que profundicen en el tema y usar evidencia para respaldar la postura, cumpliendo con el objetivo de dialogar constructivamente. La opción A es incorrecta porque solo repetir la opinión no aporta fundamentos ni enriquece la discusión. La opción C es errónea ya que ignorar argumentos contrarios impide el diálogo efectivo. La opción D es inapropiada porque interrumpir puede generar conflicto y dificulta la comprensión mutua."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{debate,fundamentación,"estrategias de diálogo"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (617, 131, 'multiple_choice', 'all', '{"opciones": {"A": "Repetir varias veces tu opinión sin considerar los argumentos del otro.", "B": "Presentar datos o ejemplos relevantes y hacer preguntas que clarifiquen las ideas del interlocutor.", "C": "Interrumpir al otro para imponer tu punto de vista rápidamente.", "D": "Evitar responder preguntas para no mostrar debilidades en tu postura."}, "pregunta": "En un debate, ¿cuál es la mejor manera de fundamentar tu postura para enriquecer la discusión?", "explicacion": "La opción B es correcta porque fundamentar una postura con datos o ejemplos relevantes demuestra comprensión y aporta sustento sólido, además de formular preguntas que clarifican y profundizan la conversación, cumpliendo con el objetivo de dialogar constructivamente. La opción A es incorrecta porque repetir sin escuchar no aporta a la construcción del debate. La opción C es un error común que afecta el respeto y la comprensión mutua. La opción D muestra una actitud evasiva que limita el diálogo y la exploración de ideas."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{debate,fundamentación,"estrategias de diálogo","Lengua y Literatura","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (618, 131, 'drag_drop_matching', 'all', '{"explicacion": "Fundamentar una postura implica apoyar opiniones con razones claras y evidencias. Las preguntas abiertas fomentan respuestas elaboradas, mientras que las cerradas limitan la respuesta a sí o no. Parafrasear demuestra comprensión de lo expresado por el interlocutor. Usar ejemplos pertinentes ayuda a ilustrar y fortalecer un argumento.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente relacionado con estrategias para fundamentar posturas y formular preguntas en un debate.", "columna_derecha": ["Presentar razones claras y basadas en información para apoyar una opinión", "Consulta que invita a una respuesta amplia y reflexiva", "Repetir con otras palabras lo que dijo la otra persona para demostrar comprensión", "Caso específico que ilustra y fortalece una idea en la discusión", "Pregunta que solo permite respuestas concretas como sí o no"], "columna_izquierda": ["Fundamentar una postura", "Pregunta abierta", "Parafrasear al interlocutor", "Ejemplo pertinente", "Pregunta cerrada", "Distractor sin pareja"]}', '{"emparejamientos_correctos": {"Pregunta abierta": "Consulta que invita a una respuesta amplia y reflexiva", "Pregunta cerrada": "Pregunta que solo permite respuestas concretas como sí o no", "Ejemplo pertinente": "Caso específico que ilustra y fortalece una idea en la discusión", "Fundamentar una postura": "Presentar razones claras y basadas en información para apoyar una opinión", "Parafrasear al interlocutor": "Repetir con otras palabras lo que dijo la otra persona para demostrar comprensión"}}', 3, 0, true, '{debate,"estrategias comunicativas",fundamentación,preguntas,comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (619, 131, 'sequencing', 'all', '{"explicacion": "Primero, es fundamental escuchar atentamente para comprender lo que dice el interlocutor. Luego, se deben formular preguntas que permitan explorar o aclarar sus ideas. A continuación, se presentan argumentos fundamentados que apoyan la postura propia. Finalmente, durante todo el proceso, se debe mantener el foco en el tema central para que el debate sea constructivo.", "instruccion": "Ordena los siguientes pasos para fundamentar una postura y formular preguntas en un debate.", "elementos_desordenados": ["Escuchar atentamente la opinión del interlocutor", "Formular preguntas que profundicen o aclaren lo dicho", "Presentar argumentos claros y basados en información pertinente", "Mantener el foco en el tema central del debate"]}', '{"orden_correcto": ["Escuchar atentamente la opinión del interlocutor", "Formular preguntas que profundicen o aclaren lo dicho", "Presentar argumentos claros y basados en información pertinente", "Mantener el foco en el tema central del debate"]}', 3, 0, true, '{debate,fundamentación,"estrategias comunicativas"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (620, 131, 'open_ended', 'all', '{"pregunta": "Durante un debate sobre el impacto de las redes sociales en la comunicación juvenil, ¿cómo podrías fundamentar tu postura y qué preguntas formularías para enriquecer la discusión, asegurándote de mantener el foco y demostrar comprensión de las ideas de tus interlocutores?", "explicacion": "El docente debe evaluar si el estudiante aplica estrategias para fundamentar su postura con argumentos claros y relevantes, si formula preguntas que inviten a la reflexión y al diálogo, y si muestra comprensión activa de las ideas de los demás participantes, manteniendo la coherencia y el foco en el tema del debate.", "contexto_adicional": "El debate se desarrolla en el aula y los estudiantes deben interactuar mostrando respeto y atención a las opiniones ajenas.", "criterios_evaluacion": ["Fundamentación pertinente y clara de la postura, utilizando información relevante y coherente con el tema tratado (peso: 40%)", "Formulación de preguntas abiertas y reflexivas que promuevan el diálogo y profundicen en las ideas presentadas por los demás (peso: 35%)", "Demostración de comprensión activa de las ideas de los interlocutores, manteniendo el foco y respondiendo de manera constructiva (peso: 25%)"]}', '{"puntos_clave": ["Presentar argumentos claros y basados en información relevante sobre el impacto de las redes sociales", "Formular preguntas abiertas que inviten a reflexionar y dialogar", "Mostrar comprensión de las ideas ajenas y mantener el foco en el tema durante la argumentación"], "respuesta_modelo": "Considero que las redes sociales tienen un impacto significativo en la comunicación juvenil, ya que facilitan la conexión instantánea y el acceso a información diversa, pero también pueden generar problemas como la superficialidad en las relaciones y la dependencia digital. Por ejemplo, estudios indican que el uso excesivo puede afectar habilidades sociales presenciales. Para enriquecer la discusión, preguntaría: ¿De qué manera creen que las redes sociales han cambiado la forma en que expresamos nuestras emociones? ¿Qué ventajas y desventajas ven en la comunicación digital frente a la cara a cara? Esto permite explorar diferentes perspectivas y profundizar en el tema. Además, al escuchar otras opiniones, reconozco que, aunque algunos valoran la rapidez, otros destacan la pérdida de intimidad, por lo que mi postura considera ambos aspectos para mantener un enfoque equilibrado.", "requiere_revision_humana": true}', 3, 0, true, '{debate,fundamentacion,preguntas_abiertas,comprension_activa}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (621, 132, 'compare_contrast', 'all', '{"conceptos": ["Afirmaciones basadas en evidencias", "Afirmaciones sin evidencia"], "criterios": ["Fundamentación", "Impacto en la discusión", "Nivel de persuasión"], "explicacion": "Las afirmaciones basadas en evidencias se sustentan en datos o información verificable, lo que fortalece la discusión y aumenta su persuasión. Por otro lado, las afirmaciones sin evidencia carecen de respaldo concreto, lo que puede debilitar el argumento y disminuir su efecto en el diálogo.", "instruccion": "Compara y contrasta las afirmaciones basadas en evidencias y las afirmaciones sin evidencia según los criterios dados"}', '{"tabla_correcta": {"Afirmaciones sin evidencia": {"Fundamentación": "Carecen de respaldo concreto o datos verificables", "Nivel de persuasión": "Bajo, ya que no presentan argumentos sólidos", "Impacto en la discusión": "Pueden generar confusión o debilitar el diálogo"}, "Afirmaciones basadas en evidencias": {"Fundamentación": "Se apoyan en datos o información verificable", "Nivel de persuasión": "Alto, debido a su respaldo concreto", "Impacto en la discusión": "Fortalecen la discusión y promueven un diálogo constructivo"}}}', 3, 0, true, '{debate,argumentación,"pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (622, 132, 'compare_contrast', 'all', '{"conceptos": ["Afirmación basada en evidencias", "Afirmación sin evidencias"], "criterios": ["Fundamentación de la postura", "Impacto en la credibilidad del interlocutor", "Efecto en la calidad del diálogo"], "explicacion": "Las afirmaciones basadas en evidencias se sustentan en datos o información verificable, lo que fortalece la credibilidad y contribuye a un diálogo constructivo y enfocado. Por otro lado, las afirmaciones sin evidencias carecen de soporte factual, lo que puede disminuir la confianza y generar discusiones menos productivas o desviadas del tema.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Afirmación sin evidencias": {"Fundamentación de la postura": "Carece de soporte factual o datos verificables", "Efecto en la calidad del diálogo": "Puede derivar en discusiones poco productivas o desviadas", "Impacto en la credibilidad del interlocutor": "Disminuye la confianza y puede generar escepticismo"}, "Afirmación basada en evidencias": {"Fundamentación de la postura": "Se apoya en datos, hechos o ejemplos concretos", "Efecto en la calidad del diálogo": "Promueve una discusión constructiva y enfocada", "Impacto en la credibilidad del interlocutor": "Aumenta la confianza y respeto hacia quien habla"}}}', 4, 0, true, '{debate,argumentación,"pensamiento crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (623, 132, 'open_ended', 'all', '{"pregunta": "En un debate sobre la importancia de la tecnología en la educación, analiza dos afirmaciones: una basada en evidencias concretas y otra que no presenta fundamentos claros. ¿Cómo afecta cada tipo de afirmación al desarrollo y la calidad del diálogo? Explica tu respuesta apoyándote en ejemplos y argumentando el impacto en la comprensión y avance del debate.", "explicacion": "El docente debe evaluar si el estudiante puede distinguir entre afirmaciones fundamentadas y no fundamentadas, analizar cómo estas influyen en la calidad del diálogo y si justifica su respuesta con ejemplos claros. Se valorará la capacidad de mantener el foco y mostrar comprensión de las ideas del interlocutor.", "contexto_adicional": "Los estudiantes participarán en un debate guiado donde se presentan afirmaciones con diferentes grados de fundamentación sobre la tecnología en la educación.", "criterios_evaluacion": ["Identificación clara de las afirmaciones basadas en evidencias y las que no lo están, demostrando comprensión (peso: 30%)", "Análisis del efecto que cada tipo de afirmación tiene en el diálogo, fundamentando su respuesta con ejemplos pertinentes (peso: 40%)", "Claridad y coherencia en la explicación, manteniendo el foco en el objetivo del debate y mostrando comprensión de lo dicho (peso: 30%)"]}', '{"puntos_clave": ["Diferenciación entre afirmaciones fundamentadas y no fundamentadas", "Impacto positivo de las evidencias en la calidad del diálogo", "Efectos negativos de afirmaciones sin respaldo en la discusión"], "respuesta_modelo": "Una afirmación basada en evidencias, como citar estudios que demuestran mejoras en el aprendizaje gracias a la tecnología, fortalece el diálogo porque aporta información confiable que puede ser discutida y analizada. Esto permite que los participantes comprendan mejor el tema y avancen en sus argumentos. Por otro lado, una afirmación sin fundamentos claros, como decir que ''la tecnología es mala porque distrae'', sin explicar ni respaldar con datos, puede debilitar el diálogo al generar confusión o desacuerdos infundados. Esto puede desviar la conversación y dificultar llegar a conclusiones. Por lo tanto, las afirmaciones fundamentadas contribuyen a un debate más constructivo y enriquecedor, mientras que las no fundamentadas pueden obstaculizar el avance y la comprensión mutua.", "requiere_revision_humana": true}', 4, 0, true, '{debate,"pensamiento crítico",análisis,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (624, 132, 'concept_map', 'all', '{"conceptos": ["Afirmación basada en evidencia", "Afirmación sin evidencia", "Fundamentación pertinente", "Comprensión del interlocutor", "Foco en el tema", "Efectos en la discusión"], "explicacion": "Se espera que el estudiante identifique cómo las afirmaciones basadas en evidencia y aquellas sin evidencia afectan la fundamentación pertinente y la comprensión del interlocutor, manteniendo el foco en el tema, y cómo todo esto influye en los efectos en la discusión.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de un diálogo constructivo para debatir o explorar ideas?"}', '{"conceptos_centrales": ["Afirmación basada en evidencia", "Afirmación sin evidencia"], "relaciones_esperadas": [{"origen": "Afirmación basada en evidencia", "destino": "Fundamentación pertinente", "relacion": "permite"}, {"origen": "Afirmación sin evidencia", "destino": "Comprensión del interlocutor", "relacion": "dificulta"}, {"origen": "Fundamentación pertinente", "destino": "Mantener el foco en el tema", "relacion": "contribuye a"}, {"origen": "Comprensión del interlocutor", "destino": "Diálogo constructivo", "relacion": "favorece"}, {"origen": "Mantener el foco en el tema", "destino": "Efectos en la discusión", "relacion": "impacta en"}, {"origen": "Afirmación sin evidencia", "destino": "Efectos negativos en la discusión", "relacion": "provoca"}], "requiere_revision_humana": true}', 3, 0, true, '{debate,argumentación,"comprensión lectora","diálogo constructivo"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (625, 132, 'concept_map', 'all', '{"conceptos": ["Afirmación basada en evidencia", "Afirmación sin evidencia", "Fundamentación", "Comprensión del interlocutor", "Efectos en la discusión", "Mantener el foco"], "explicacion": "Se espera que el estudiante muestre cómo las afirmaciones basadas en evidencia se fundamentan y contribuyen a mantener el foco y la comprensión del interlocutor, mientras que las afirmaciones sin evidencia pueden afectar negativamente los efectos en la discusión. Además, debe identificar cómo la fundamentación y la comprensión mutua influyen en la calidad del diálogo.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de dialogar constructivamente para debatir o explorar ideas?"}', '{"conceptos_centrales": ["Afirmación basada en evidencia", "Afirmación sin evidencia"], "relaciones_esperadas": [{"origen": "Afirmación basada en evidencia", "destino": "Fundamentación", "relacion": "requiere"}, {"origen": "Fundamentación", "destino": "Mantener el foco", "relacion": "facilita"}, {"origen": "Mantener el foco", "destino": "Comprensión del interlocutor", "relacion": "contribuye a"}, {"origen": "Afirmación sin evidencia", "destino": "Efectos en la discusión", "relacion": "afecta negativamente"}, {"origen": "Comprensión del interlocutor", "destino": "Efectos en la discusión", "relacion": "mejora"}], "requiere_revision_humana": true}', 4, 0, true, '{debate_guiado,mapa_conceptual,dialogo_constructivo,analizar,crear}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (626, 133, 'criteria_evaluation', 'all', '{"rubrica": {"Mantener el foco en el tema durante el debate": {"Excelente": "El estudiante se mantiene claramente centrado en el tema sin desviarse, contribuyendo con ideas pertinentes y coherentes.", "Satisfactorio": "El estudiante en general mantiene el foco, pero ocasionalmente se desvía o hace comentarios poco relacionados.", "Necesita mejorar": "El estudiante frecuentemente se desvía del tema, dificultando el desarrollo del debate."}, "Negociación de acuerdos y respeto por la toma de turnos": {"Excelente": "El estudiante respeta los turnos, espera su momento para hablar y facilita la participación de otros, promoviendo un ambiente constructivo.", "Satisfactorio": "El estudiante generalmente respeta los turnos pero en ocasiones interrumpe o monopoliza la palabra sin intención clara de perjudicar.", "Necesita mejorar": "El estudiante interrumpe frecuentemente, no respeta los turnos y dificulta la participación de otros."}, "Demostrar comprensión de lo dicho por los interlocutores": {"Excelente": "El estudiante parafrasea o hace referencia clara a las ideas de otros, mostrando un entendimiento profundo.", "Satisfactorio": "El estudiante muestra comprensión básica, pero no siempre integra o responde claramente a lo expresado por otros.", "Necesita mejorar": "El estudiante ignora o malinterpreta las opiniones de los interlocutores."}, "Fundamentar la postura de manera pertinente y con información relevante": {"Excelente": "El estudiante utiliza información precisa y ejemplos concretos que apoyan fuertemente su postura.", "Satisfactorio": "El estudiante presenta argumentos relevantes, aunque con poca profundidad o ejemplos limitados.", "Necesita mejorar": "El estudiante presenta argumentos poco claros, vagos o irrelevantes."}}, "explicacion": "Se espera que el estudiante analice críticamente la conducta del participante en el debate, identificando fortalezas y debilidades en cada criterio, y argumentando su evaluación con base en evidencias del caso.", "instruccion": "Evalúa la participación del estudiante en el debate según los criterios de la rúbrica y justifica tu calificación para cada criterio.", "caso_a_evaluar": "En un debate grupal sobre la importancia de la lectura en la formación personal, observa la siguiente situación: Durante la discusión, un estudiante mantiene el foco en el tema, escucha atentamente las opiniones de sus compañeros y utiliza datos y ejemplos relevantes para fundamentar su postura. Sin embargo, en varias ocasiones interrumpe a sus interlocutores y no respeta los turnos de palabra, lo que genera confusión y dificulta el avance del debate."}', '{"evaluacion_modelo": {"Mantener el foco en el tema durante el debate": "Excelente", "Negociación de acuerdos y respeto por la toma de turnos": "Necesita mejorar", "Demostrar comprensión de lo dicho por los interlocutores": "Excelente", "Fundamentar la postura de manera pertinente y con información relevante": "Excelente"}, "justificacion_modelo": "El estudiante mantiene el foco en el tema, demuestra comprensión y fundamenta bien su postura con información relevante, pero presenta dificultades en la negociación de acuerdos y respeto por la toma de turnos, ya que interrumpe a los interlocutores y no espera su turno, afectando el desarrollo constructivo del debate.", "requiere_revision_humana": true}', 4, 0, true, '{debate,evaluacion,"dialogo constructivo","primer medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (627, 133, 'open_ended', 'all', '{"pregunta": "En un debate sobre un tema controversial, ¿cómo justificarías la importancia de negociar acuerdos y respetar la toma de turnos para mantener un diálogo constructivo? Explica cómo estas acciones pueden influir en la comprensión mutua y en el logro de conclusiones efectivas.", "explicacion": "Este criterio evalúa si el estudiante comprende y puede explicar la relevancia de negociar acuerdos y respetar turnos en un debate, mostrando cómo estas prácticas facilitan la escucha activa, el respeto y la construcción conjunta de ideas. Se debe valorar la profundidad del análisis y el respaldo con ejemplos o argumentos claros.", "contexto_adicional": "Imagina que participas en un debate guiado con tus compañeros sobre un tema de interés común, donde es fundamental mantener una comunicación respetuosa y efectiva.", "criterios_evaluacion": ["Claridad y coherencia en la justificación de la negociación de acuerdos y toma de turnos (peso: 30%)", "Capacidad para relacionar estas acciones con la mejora de la comprensión entre interlocutores y el avance del debate (peso: 40%)", "Uso de ejemplos o argumentos pertinentes que fundamenten la respuesta (peso: 30%)"]}', '{"puntos_clave": ["Justificación de la importancia de negociar acuerdos y respetar turnos", "Relación entre estas acciones y la mejora de la comprensión mutua", "Ejemplos o argumentos que fundamentan cómo estas prácticas contribuyen a un debate constructivo"], "respuesta_modelo": "En un debate, negociar acuerdos y respetar la toma de turnos es fundamental para mantener un diálogo constructivo porque permite que todas las voces sean escuchadas sin interrupciones, favoreciendo la comprensión mutua. Al acordar reglas de participación, se evita el caos y se promueve un ambiente de respeto, donde cada interlocutor puede expresar sus ideas claramente. Esto ayuda a que los participantes analicen mejor los puntos de vista contrarios y respondan de manera fundamentada, lo que facilita llegar a conclusiones más sólidas y consensuadas. Por ejemplo, si cada persona espera su turno para hablar, se reduce la tensión y se mejora la concentración en los argumentos presentados, fortaleciendo la calidad del debate.", "requiere_revision_humana": true}', 4, 0, true, '{debate,"habilidades comunicativas","pensamiento crítico","OA dialogar constructivamente"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (629, 134, 'open_ended', 'all', '{"pregunta": "Diseña y ejecuta una estrategia de diálogo constructivo para debatir sobre el impacto de las redes sociales en la comunicación entre jóvenes. Asegúrate de incluir reformulaciones de los comentarios de tus interlocutores y propone acuerdos o soluciones que reflejen una negociación respetuosa y fundamentada.", "explicacion": "El docente debe evaluar cómo el estudiante demuestra comprensión activa a través de la reformulación, la pertinencia y fuerza argumentativa de sus intervenciones, y su habilidad para llegar a acuerdos que evidencien una negociación constructiva. Se debe valorar tanto la forma como el contenido del diálogo.", "contexto_adicional": "La actividad se realiza en grupos pequeños o en parejas durante una sesión de debate en el aula. Los estudiantes deben tomar roles activos y respetuosos para promover un diálogo efectivo.", "criterios_evaluacion": ["Claridad y pertinencia en la reformulación de los comentarios de los interlocutores para demostrar comprensión (peso: 30%)", "Capacidad para fundamentar las posturas propias y ajenas con argumentos relevantes y bien estructurados (peso: 40%)", "Habilidad para negociar acuerdos o soluciones respetuosas que reflejen un diálogo constructivo y mantengan el foco en el tema (peso: 30%)"]}', '{"puntos_clave": ["Reformulación clara y precisa que demuestre comprensión del interlocutor", "Fundamentación con argumentos relacionados y evidencia o ejemplos pertinentes", "Propuesta de acuerdos o soluciones que reflejen negociación y respeto"], "respuesta_modelo": "En el debate sobre el impacto de las redes sociales en la comunicación entre jóvenes, primero reformulé el comentario de mi interlocutor diciendo: ''Entiendo que consideras que las redes sociales pueden aislar a los jóvenes porque reemplazan las interacciones cara a cara.'' Luego fundamente mi postura señalando que, aunque las redes sociales facilitan conexiones, también pueden generar superficialidad en las relaciones, apoyándome en estudios recientes que muestran un aumento en sentimientos de soledad. Finalmente, propuse que para mejorar la comunicación, los jóvenes podrían establecer tiempos específicos para encuentros presenciales y uso consciente de redes sociales, un acuerdo que fue aceptado tras discutir sus beneficios y desafíos, manteniendo siempre el respeto y el foco en el tema.", "requiere_revision_humana": true}', 4, 0, true, '{debate,"diálogo constructivo","redes sociales","primer medio","lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (630, 134, 'open_ended', 'all', '{"pregunta": "Imagina que eres parte de un debate escolar sobre el impacto de las redes sociales en la comunicación entre adolescentes. Diseña y ejecuta una estrategia de diálogo constructivo para defender tu postura, asegurándote de reformular los comentarios de tus interlocutores y negociar acuerdos que permitan enriquecer la discusión. Describe cómo llevarías a cabo esta estrategia y cuáles serían los pasos para mantener un diálogo respetuoso y productivo.", "explicacion": "El docente debe evaluar si el estudiante es capaz de crear una estrategia organizada para el diálogo, incluyendo técnicas como la reformulación para mostrar comprensión y la negociación para llegar a acuerdos. Además, debe observar si el estudiante mantiene el foco en el tema y fundamenta sus ideas con argumentos pertinentes y bien sustentados.", "contexto_adicional": "Situación hipotética de un debate escolar en el que los estudiantes deben dialogar sobre el impacto de las redes sociales en la comunicación entre adolescentes.", "criterios_evaluacion": ["Diseño y ejecución de la estrategia de diálogo: claridad, estructura y aplicación de técnicas de reformulación y negociación (peso: 40%)", "Capacidad para mantener el foco en el tema y demostrar comprensión activa de los puntos de vista de otros interlocutores (peso: 30%)", "Fundamentación pertinente y uso de información relevante para apoyar la postura y alcanzar acuerdos (peso: 30%)"]}', '{"puntos_clave": ["Uso explícito de la reformulación para mostrar comprensión", "Presentación de argumentos fundamentados y pertinentes", "Propuesta de negociación para alcanzar acuerdos y enriquecer el diálogo"], "respuesta_modelo": "Para llevar a cabo una estrategia de diálogo constructivo en el debate sobre las redes sociales, primero escucharía atentamente las opiniones de mis compañeros para entender sus puntos de vista. Luego, reformularía sus comentarios para demostrar comprensión, por ejemplo, diciendo: ''Si te entiendo bien, consideras que las redes sociales pueden aislar a los jóvenes, ¿cierto?''. A continuación, presentaría mi postura fundamentada en estudios que muestran tanto beneficios como riesgos de estas plataformas. Para negociar acuerdos, propondría identificar aspectos positivos y negativos en conjunto y buscar maneras de aprovechar lo bueno minimizando lo malo, fomentando así una comunicación más saludable. Durante toda la discusión, mantendría el foco evitando desviaciones y respetaría los turnos de palabra para asegurar un diálogo respetuoso y productivo.", "requiere_revision_humana": true}', 5, 0, true, '{debate,"diálogo constructivo","estrategia comunicativa",primeromedia,lenguayliteratura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (631, 134, 'concept_map', 'all', '{"conceptos": ["Diálogo constructivo", "Reformulación de comentarios", "Negociación de acuerdos", "Comprensión del interlocutor", "Fundamentación de posturas"], "explicacion": "Se espera que el mapa conceptual muestre cómo el diálogo constructivo se fundamenta en la comprensión del interlocutor y la reformulación de sus comentarios para mantener el foco. Además, la fundamentación de posturas debe estar vinculada a la negociación de acuerdos que surgen como resultado de un diálogo efectivo.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de un debate o exploración de ideas?"}', '{"conceptos_centrales": ["Diálogo constructivo", "Negociación de acuerdos"], "relaciones_esperadas": [{"origen": "Diálogo constructivo", "destino": "Comprensión del interlocutor", "relacion": "requiere"}, {"origen": "Comprensión del interlocutor", "destino": "Reformulación de comentarios", "relacion": "facilita"}, {"origen": "Reformulación de comentarios", "destino": "Negociación de acuerdos", "relacion": "contribuye a"}, {"origen": "Fundamentación de posturas", "destino": "Negociación de acuerdos", "relacion": "apoya"}, {"origen": "Diálogo constructivo", "destino": "Fundamentación de posturas", "relacion": "se basa en"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",Diálogo,Debate,"Primer Año Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (632, 134, 'concept_map', 'all', '{"conceptos": ["Diálogo constructivo", "Reformulación de comentarios", "Negociación de acuerdos", "Comprensión del interlocutor", "Fundamentación de posturas"], "explicacion": "Se espera que el mapa conceptual muestre que el diálogo constructivo requiere comprensión del interlocutor para poder reformular comentarios adecuadamente, lo cual facilita la negociación de acuerdos. Además, la fundamentación de posturas es necesaria para mantener el foco y aportar información pertinente durante el diálogo.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de un diálogo constructivo para debatir ideas?"}', '{"conceptos_centrales": ["Diálogo constructivo", "Reformulación de comentarios"], "relaciones_esperadas": [{"origen": "Diálogo constructivo", "destino": "Comprensión del interlocutor", "relacion": "requiere"}, {"origen": "Comprensión del interlocutor", "destino": "Reformulación de comentarios", "relacion": "permite"}, {"origen": "Reformulación de comentarios", "destino": "Negociación de acuerdos", "relacion": "facilita"}, {"origen": "Fundamentación de posturas", "destino": "Diálogo constructivo", "relacion": "apoya"}], "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Comunicación oral",Debate,"Habilidades sociales"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (633, 134, 'open_ended', 'all', '{"pregunta": "Diseña y ejecuta una estrategia de diálogo constructivo para debatir sobre el impacto de las redes sociales en la comunicación entre jóvenes. Tu estrategia debe incluir la reformulación de al menos dos comentarios realizados por tus interlocutores y la negociación de acuerdos o desacuerdos finales. Explica cómo cada elemento de tu estrategia contribuye a mantener un diálogo enfocado y respetuoso.", "explicacion": "El docente debe evaluar que el estudiante sea capaz de escuchar activamente y reformular comentarios, argumentar con fundamentos pertinentes y negociar acuerdos para un diálogo constructivo. Se valorará la creatividad en la estrategia y la capacidad de mantener el foco temático.", "contexto_adicional": "El debate debe centrarse en cómo las redes sociales afectan la comunicación entre jóvenes, considerando aspectos positivos y negativos.", "criterios_evaluacion": ["Claridad y pertinencia en la reformulación de comentarios para demostrar comprensión (peso: 30%)", "Capacidad para fundamentar posturas con información relevante y negociar acuerdos o desacuerdos (peso: 40%)", "Coherencia y creatividad en el diseño y ejecución de la estrategia de diálogo que mantenga el foco en el tema (peso: 30%)"]}', '{"puntos_clave": ["Reformulación clara y precisa de comentarios para demostrar comprensión", "Fundamentación de posturas con argumentos relevantes y equilibrados", "Negociación de acuerdos que refleje respeto y mantenga el foco en el tema"], "respuesta_modelo": "Para dialogar constructivamente sobre el impacto de las redes sociales, primero planteé una pregunta inicial para enfocar el debate. Luego, durante la conversación, reformulé dos comentarios: por ejemplo, cuando un interlocutor dijo que las redes sociales aislan a los jóvenes, respondí ‘Es decir, crees que las redes sociales generan aislamiento en lugar de conexión, ¿correcto?’, lo que confirmó su idea y demostró comprensión. En otro momento, reformulé la opinión de que las redes sociales facilitan el acceso a información diciendo ‘Entonces, consideras que estas plataformas son una herramienta educativa valiosa’. Finalmente, propuse que, aunque existen riesgos de aislamiento, también hay beneficios en la comunicación digital, y negociamos que el diálogo debe reconocer ambos aspectos. Esta estrategia permitió mantener el enfoque en el tema, respetar las opiniones y lograr un acuerdo parcial sobre la complejidad del impacto social.", "requiere_revision_humana": true}', 5, 0, true, '{dialogo_constructivo,debate,lenguaje_critico,primer_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (634, 135, 'multiple_choice', 'all', '{"opciones": {"A": "Hablar sin preparación para mantener la espontaneidad", "B": "Presentar información basada en una investigación previa y confiable", "C": "Usar un lenguaje informal sin importar la audiencia", "D": "Cambiar constantemente el tema para captar la atención"}, "pregunta": "¿Cuál de los siguientes elementos es clave para que una presentación sea clara y adecuada a la situación?", "explicacion": "La respuesta correcta es la opción B porque una presentación clara y adecuada requiere información fidedigna y que denota una investigación previa para generar confianza y coherencia. La opción A es incorrecta porque la falta de preparación puede causar confusión y falta de claridad. La opción C es un error común puesto que el lenguaje debe ajustarse a la audiencia y la situación para ser adecuado. La opción D genera desorden y dificulta la progresión temática clara, afectando la comprensión."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{presentación,claridad,investigación,"lengua y literatura"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (635, 135, 'multiple_choice', 'all', '{"opciones": {"A": "Hablar sin preparación para mantener la espontaneidad.", "B": "Presentar información que ha sido previamente investigada y organizada.", "C": "Usar palabras complicadas para impresionar a la audiencia.", "D": "Leer el texto completo sin hacer pausas ni contacto visual."}, "pregunta": "¿Cuál de los siguientes elementos es clave para que una presentación sea clara y adecuada a la situación?", "explicacion": "La opción B es correcta porque una presentación clara y adecuada requiere información fidedigna que se obtiene mediante una investigación previa y una organización adecuada. La opción A es incorrecta porque la falta de preparación puede causar confusión. La opción C es un error común, ya que usar palabras complicadas no garantiza claridad y puede dificultar la comprensión. La opción D es incorrecta porque leer sin pausas ni contacto visual hace que la presentación sea monótona y poco efectiva."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{presentación,comunicación,claridad}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (636, 135, 'multiple_choice', 'all', '{"opciones": {"A": "Utilizar un vocabulario muy técnico sin importar la audiencia", "B": "Presentar información organizada en una secuencia lógica", "C": "Hablar de varios temas sin conexión para mostrar variedad", "D": "Leer todo el texto sin interactuar con la audiencia"}, "pregunta": "¿Cuál de los siguientes elementos es clave para que una presentación sea clara y adecuada a la situación?", "explicacion": "La opción B es correcta porque una presentación clara y adecuada debe seguir una progresión temática lógica que ayude a la audiencia a comprender el mensaje. La opción A es incorrecta porque un vocabulario demasiado técnico puede dificultar la comprensión si la audiencia no está familiarizada con él. La opción C es un error común que confunde a la audiencia al no mantener un tema central. La opción D limita la interacción y puede hacer que la presentación sea monótona y menos efectiva."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"presentación oral","organización del discurso","comunicación clara"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (637, 135, 'true_false', 'all', '{"afirmacion": "Una presentación clara y adecuada debe incluir información que demuestre que se realizó una investigación previa.", "explicacion": "Esta afirmación es verdadera porque para que una presentación sea clara y adecuada, es fundamental que el contenido esté basado en información fidedigna y bien investigada, lo que garantiza la precisión y credibilidad del mensaje que se comunica."}', '{"es_verdadero": true}', 1, 0, true, '{presentación,investigación,claridad}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (638, 135, 'fill_blanks', 'all', '{"texto": "Para lograr una presentación clara, es fundamental seguir una [BLANK_1] temática que ordene las ideas y presentar información que sea [BLANK_2], basada en una investigación previa y confiable.", "pistas": ["Secuencia lógica que organiza las ideas en una presentación.", "Calidad de la información que asegura su veracidad y respaldo."], "explicacion": "Una presentación clara y adecuada requiere que las ideas se organicen siguiendo una progresión temática clara para facilitar la comprensión. Además, la información debe ser fidedigna, es decir, basada en datos confiables y una investigación previa, para asegurar la credibilidad ante la audiencia."}', '{"respuestas_correctas": {"BLANK_1": ["progresión", "secuencia", "orden"], "BLANK_2": ["fidedigna", "confiable", "verídica"]}}', 2, 0, true, '{"presentación oral","organización de ideas","información confiable"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (639, 136, 'multiple_choice', 'all', '{"opciones": {"A": "Porque ayuda a que la audiencia recuerde solo los datos más llamativos, sin importar su veracidad.", "B": "Porque asegura que la audiencia entienda el mensaje y confíe en lo que se comunica.", "C": "Porque permite improvisar y cambiar el tema según el interés del público durante la presentación.", "D": "Porque evita tener que investigar previamente y facilita hablar sin preparación."}, "pregunta": "¿Por qué es importante presentar información fidedigna y organizar una presentación con progresión temática clara?", "explicacion": "La opción B es correcta porque presentar información fidedigna genera confianza en la audiencia y organizar la presentación con una progresión temática clara facilita la comprensión del mensaje. La opción A es incorrecta porque prioriza lo llamativo sobre la veracidad, lo que puede confundir o engañar. La opción C es un error común: improvisar sin estructura puede dificultar la comprensión. La opción D refleja la falta de preparación, lo que afecta negativamente la calidad y claridad de la presentación."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{presentación,"información fidedigna","progresión temática",comprensión}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (640, 136, 'multiple_choice', 'all', '{"opciones": {"A": "Porque así se puede incluir cualquier dato sin necesidad de verificarlo, y saltar de un tema a otro para hacerlo más dinámico.", "B": "Porque garantiza que la audiencia reciba información confiable y pueda seguir el desarrollo del tema sin perderse.", "C": "Porque una presentación con información dudosa y desordenada genera más interés y sorpresa en la audiencia.", "D": "Porque mientras más larga sea la presentación, más importancia tendrá, sin importar la organización o veracidad."}, "pregunta": "¿Por qué es importante que una presentación tenga información fidedigna y una progresión temática clara?", "explicacion": "La respuesta correcta es la opción B, ya que presentar información fidedigna asegura que el contenido sea confiable y relevante, mientras que una progresión temática clara ayuda a la audiencia a entender y seguir el desarrollo de la presentación. La opción A es incorrecta porque la verificación de datos es fundamental y saltar entre temas puede confundir. La opción C es errónea porque la información dudosa y el desorden disminuyen la credibilidad y dificultan la comprensión. La opción D es falsa porque la duración no determina la calidad; sin organización y veracidad, la presentación pierde efectividad."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"Lengua y Literatura","Presentación oral","Información fidedigna","Progresión temática"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (641, 136, 'true_false', 'all', '{"afirmacion": "Es importante presentar información fidedigna en una presentación porque garantiza que el público reciba datos confiables y precisos.", "explicacion": "Esta afirmación es verdadera porque la información fidedigna asegura que el contenido presentado sea confiable y basado en hechos, lo que permite que la audiencia confíe en lo que se comunica y tome decisiones o forme opiniones fundamentadas."}', '{"es_verdadero": true}', 2, 0, true, '{presentación,"información fidedigna","comunicación clara"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (642, 136, 'drag_drop_matching', 'all', '{"explicacion": "La información fidedigna es aquella que está verificada y es confiable. La progresión temática clara implica organizar los temas de manera lógica. La investigación previa es fundamental para obtener datos que respalden la presentación. Presentar frente a una audiencia es comunicar un tema a un grupo. La opinión personal es un distractor porque no siempre es confiable ni basada en evidencia.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Datos verificados y confiables que respaldan lo que se comunica", "Orden lógico y coherente de los temas a tratar en la presentación", "Búsqueda y recopilación de datos antes de preparar la presentación", "Acto de comunicar un tema de interés a un grupo de personas", "Juicio subjetivo sin respaldo en evidencia"], "columna_izquierda": ["Información fidedigna", "Progresión temática clara", "Investigación previa", "Presentación frente a una audiencia", "Distractor: Opinión personal"]}', '{"emparejamientos_correctos": {"Investigación previa": "Búsqueda y recopilación de datos antes de preparar la presentación", "Información fidedigna": "Datos verificados y confiables que respaldan lo que se comunica", "Progresión temática clara": "Orden lógico y coherente de los temas a tratar en la presentación", "Presentación frente a una audiencia": "Acto de comunicar un tema de interés a un grupo de personas"}}', 2, 0, true, '{lengua_y_literatura,expresion_oral,presentacion,primer_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (643, 136, 'sequencing', 'all', '{"explicacion": "El orden correcto comienza con investigar información confiable para asegurar la veracidad. Luego, se organizan los puntos principales para tener una progresión temática clara. Después, se prepara el guion o esquema que guiará la presentación. Se revisa y verifica la precisión de la información para evitar errores. Finalmente, se presenta la información de forma clara y ordenada frente a la audiencia.", "instruccion": "Ordena los siguientes pasos para preparar una presentación con información fidedigna y progresión temática clara", "elementos_desordenados": ["Investigar información confiable sobre el tema", "Organizar los puntos principales en un orden lógico", "Preparar el guion o esquema de la presentación", "Presentar la información de forma clara y ordenada", "Revisar y verificar que la información sea precisa"]}', '{"orden_correcto": ["Investigar información confiable sobre el tema", "Organizar los puntos principales en un orden lógico", "Preparar el guion o esquema de la presentación", "Revisar y verificar que la información sea precisa", "Presentar la información de forma clara y ordenada"]}', 3, 0, true, '{presentación,organización,investigación,claridad}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (644, 137, 'multiple_choice', 'all', '{"opciones": {"A": "Primero, debemos pensar en que el planeta está malo y es importante actuar rápido.", "B": "Para comenzar, es fundamental comprender que el cuidado del medio ambiente es una responsabilidad colectiva.", "C": "Bueno, el medio ambiente está como que en problemas, y hay que hacer algo ya.", "D": "Al inicio, el asunto del medio ambiente es algo que a veces se olvida, pero es vital."}, "pregunta": "¿Cuál de las siguientes oraciones utiliza correctamente un conector adecuado y un vocabulario pertinente para iniciar una presentación sobre el cuidado del medio ambiente?", "explicacion": "La opción B es la correcta porque utiliza un conector adecuado (''Para comenzar'') que indica el inicio de la presentación y emplea un vocabulario formal y pertinente (''fundamental comprender'', ''responsabilidad colectiva'') acorde a una presentación oral clara y adecuada. La opción A usa un vocabulario poco preciso (''está malo'') y un conector simple (''Primero'') pero con una expresión informal. La opción C presenta un lenguaje coloquial y poco formal (''Bueno'', ''como que''), lo que no es adecuado para la situación. La opción D utiliza un conector incorrecto (''Al inicio'' no es un conector adecuado para presentar argumentos) y un vocabulario impreciso (''el asunto del medio ambiente es algo que a veces se olvida''), lo que afecta la claridad del mensaje."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"lengua y literatura",conectores,vocabulario,"presentación oral","primer medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (645, 137, 'multiple_choice', 'all', '{"opciones": {"A": "Primero, voy a hablar sobre la contaminación porque es un problema que afecta a muchas personas, pero también quiero contar algo sobre mis vacaciones.", "B": "En primer lugar, la contaminación ambiental es un tema crucial que afecta la salud pública; además, es importante tomar medidas para reducirla.", "C": "La contaminación es mala, y también hay que cuidar la naturaleza, porque es importante para todos.", "D": "Voy a hablar de la contaminación y después de cosas que me gustan sin mucho orden, porque así me sale mejor."}, "pregunta": "¿Cuál de las siguientes oraciones utiliza correctamente un conector adecuado y vocabulario pertinente para presentar un tema de interés en una presentación oral?", "explicacion": "La opción B es correcta porque utiliza conectores adecuados (''En primer lugar'', ''además'') que marcan una progresión temática clara y emplea vocabulario pertinente y formal para el tema presentado. La opción A mezcla temas sin una transición clara y usa un conector inapropiado (''porque'') para enlazar ideas diferentes, lo que puede generar confusión. La opción C es muy vaga y utiliza un vocabulario poco específico, sin conectores que organicen la información. La opción D refleja un orden desordenado y una expresión informal, sin conectores que den estructura a la presentación."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{conectores,vocabulario,"presentación oral","nivel aplicar"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (646, 137, 'drag_drop_matching', 'all', '{"explicacion": "Cada conector tiene una función específica para organizar y clarificar la información: ''Además'' agrega información; ''Por lo tanto'' introduce una consecuencia; ''Sin embargo'' señala contraste; ''Primero'' indica orden; ''Finalmente'' marca cierre; y ''Porque'' expresa causa.", "instruccion": "Empareja cada conector con su función en una presentación oral.", "columna_derecha": ["Introduce una consecuencia o resultado", "Agrega información adicional", "Indica un contraste o excepción", "Señala el orden de una acción o idea inicial", "Expresa la causa de algo", "Marca la conclusión o cierre de un tema"], "columna_izquierda": ["Además", "Por lo tanto", "Sin embargo", "Primero", "Finalmente", "Porque"]}', '{"emparejamientos_correctos": {"Porque": "Expresa la causa de algo", "Además": "Agrega información adicional", "Primero": "Señala el orden de una acción o idea inicial", "Finalmente": "Marca la conclusión o cierre de un tema", "Sin embargo": "Indica un contraste o excepción", "Por lo tanto": "Introduce una consecuencia o resultado"}}', 3, 0, true, '{conectores,"presentación oral",vocabulario}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (647, 137, 'sequencing', 'all', '{"explicacion": "El orden inicia con la selección del tema y la investigación, seguida de la organización lógica de las ideas. Luego, se incorporan conectores adecuados para relacionar las ideas, se ensaya para mejorar la fluidez y finalmente se realiza la presentación frente a la audiencia. Este orden asegura una comunicación clara y efectiva.", "instruccion": "Ordena los siguientes pasos para preparar una presentación oral clara y adecuada.", "elementos_desordenados": ["Ensayar la presentación para mejorar la fluidez y el uso de conectores", "Seleccionar un tema de interés y realizar una investigación previa", "Organizar las ideas en una estructura lógica con introducción, desarrollo y conclusión", "Utilizar conectores apropiados para relacionar las ideas durante la presentación", "Presentar la información frente a la audiencia de manera clara y adecuada"]}', '{"orden_correcto": ["Seleccionar un tema de interés y realizar una investigación previa", "Organizar las ideas en una estructura lógica con introducción, desarrollo y conclusión", "Utilizar conectores apropiados para relacionar las ideas durante la presentación", "Ensayar la presentación para mejorar la fluidez y el uso de conectores", "Presentar la información frente a la audiencia de manera clara y adecuada"]}', 3, 0, true, '{"presentación oral","organización de ideas","uso de conectores"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (648, 137, 'open_ended', 'all', '{"pregunta": "Elige un tema de interés personal y prepara una presentación oral breve. En tu presentación, utiliza conectores adecuados para organizar tus ideas y emplea un vocabulario pertinente que ayude a comunicar claramente el tema. Luego, explica cómo seleccionaste los conectores y el vocabulario para hacer tu mensaje más efectivo.", "explicacion": "El docente debe evaluar la capacidad del estudiante para aplicar conectores que den continuidad y orden a sus ideas, así como su selección de vocabulario adecuado que enriquezca el contenido y facilite la comprensión. Además, debe valorar la coherencia y claridad tanto en la exposición como en la explicación del recurso lingüístico utilizado.", "contexto_adicional": "La actividad busca que los estudiantes apliquen sus conocimientos sobre conectores y vocabulario para estructurar y comunicar un tema de su interés en una presentación oral.", "criterios_evaluacion": ["Uso correcto y variado de conectores para organizar la información (peso: 30%)", "Empleo de vocabulario pertinente y específico relacionado con el tema elegido (peso: 40%)", "Claridad y coherencia en la presentación oral y en la explicación del uso de conectores y vocabulario (peso: 30%)"]}', '{"puntos_clave": ["Selección de un tema claro y de interés personal", "Uso adecuado y variado de conectores para la progresión temática", "Vocabulario pertinente y específico que enriquece la exposición", "Explicación consciente de la elección de conectores y vocabulario"], "respuesta_modelo": "He elegido el tema de la importancia del reciclaje en mi comunidad. Para organizar mi presentación, usé conectores como ''primero'', ''además'' y ''finalmente'' para ordenar las ideas. Por ejemplo, comencé explicando qué es el reciclaje, luego mencioné sus beneficios y finalmente propuse acciones que todos podemos tomar. Utilicé vocabulario específico como ''materiales reciclables'', ''sostenibilidad'' y ''reducción de residuos'' para comunicarme con mayor precisión. Esto ayuda a que la audiencia entienda la relevancia del tema y mantenga el interés durante la presentación.", "requiere_revision_humana": true}', 3, 0, true, '{"lengua y literatura","presentación oral",conectores,vocabulario,"primer medio",aplicar}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (649, 138, 'compare_contrast', 'all', '{"conceptos": ["Información fidedigna", "Progresión temática clara"], "criterios": ["Definición", "Importancia en una exposición", "Ejemplo en una presentación"], "explicacion": "La información fidedigna se refiere a datos verídicos y comprobados que respaldan el contenido, mientras que la progresión temática clara es la organización lógica y coherente de los temas durante la exposición. Ambas son esenciales para una presentación efectiva: la primera garantiza la veracidad del mensaje y la segunda facilita la comprensión del público.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Información fidedigna": {"Definición": "Datos verdaderos y comprobables que respaldan el contenido presentado.", "Ejemplo en una presentación": "Citar estadísticas oficiales o resultados de investigaciones reconocidas.", "Importancia en una exposición": "Garantiza la credibilidad y confianza del público en la información entregada."}, "Progresión temática clara": {"Definición": "Organización lógica y secuencial de los temas para facilitar la comprensión.", "Ejemplo en una presentación": "Presentar primero la introducción, luego el desarrollo de ideas y finalmente la conclusión.", "Importancia en una exposición": "Permite que la audiencia siga el hilo argumental sin perderse."}}}', 3, 0, true, '{comparar,"exposición oral","coherencia temática"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (650, 138, 'compare_contrast', 'all', '{"conceptos": ["Información fidedigna", "Progresión temática clara"], "criterios": ["Definición", "Importancia en la exposición", "Ejemplo en una presentación"], "explicacion": "La información fidedigna se refiere a datos verídicos y comprobables, fundamentales para la credibilidad de la exposición. La progresión temática clara implica organizar las ideas de forma lógica para facilitar la comprensión del público. Ambas son esenciales para una comunicación efectiva, pero se enfocan en aspectos diferentes: contenido confiable versus estructura del discurso.", "instruccion": "Compara y contrasta los siguientes conceptos según los criterios dados"}', '{"tabla_correcta": {"Información fidedigna": {"Definición": "Datos verdaderos y comprobables presentados en la exposición.", "Ejemplo en una presentación": "Citar estadísticas oficiales sobre un tema social.", "Importancia en la exposición": "Garantiza la credibilidad y confianza del público en el mensaje."}, "Progresión temática clara": {"Definición": "Orden lógico y secuencial de las ideas durante la exposición.", "Ejemplo en una presentación": "Introducción, desarrollo y conclusión bien organizados.", "Importancia en la exposición": "Facilita la comprensión y mantiene la atención del público."}}}', 4, 0, true, '{comparar,"lengua y literatura","exposición oral"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (668, 142, 'multiple_choice', 'all', '{"opciones": {"A": "Identificando solo los personajes principales y su desarrollo en la historia.", "B": "Analizando cómo los temas y situaciones reflejan las circunstancias históricas y sociales de la época en que fue escrita.", "C": "Resumiendo la trama sin considerar el momento histórico ni el entorno social.", "D": "Describiendo únicamente el estilo del autor sin relacionarlo con el contenido de la obra."}, "pregunta": "¿Cuál es la mejor manera de explicar el sentido general de una obra literaria en relación con su contexto histórico y social?", "explicacion": "La opción B es correcta porque para explicar el sentido general de una obra literaria en relación con su contexto histórico y social, es necesario analizar cómo los temas y situaciones reflejan las circunstancias de la época, lo que permite comprender el mensaje o la crítica que el autor realiza. La opción A se enfoca solo en los personajes, sin conectar con el contexto. La opción C resume la trama pero ignora el contexto, y la opción D se limita al estilo sin relacionarlo con el contenido, lo que no cumple con el objetivo de explicar el sentido general en contexto."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"Lengua y Literatura","Comprensión lectora","Contexto histórico y social"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (651, 138, 'open_ended', 'all', '{"pregunta": "Después de investigar dos temas de tu interés y presentar información sobre ellos, ¿cómo puedes comparar y relacionar los datos para mantener una coherencia temática clara en tu exposición? Explica con ejemplos cómo organizarías la información para que tu audiencia pueda entender fácilmente la relación entre ambos temas.", "explicacion": "El docente debe evaluar que el estudiante pueda identificar similitudes y diferencias entre ambos temas, organizar la información de forma coherente y progresiva, y utilizar ejemplos que apoyen la relación temática, demostrando habilidades de análisis y síntesis.", "contexto_adicional": "El estudiante debe basar su respuesta en una investigación previa que haya realizado sobre dos temas de su interés y describir cómo establecerá conexiones claras entre ellos durante una presentación oral.", "criterios_evaluacion": ["Claridad en la comparación y relación entre los temas presentados (peso: 40%)", "Organización lógica y progresión temática en la explicación (peso: 35%)", "Uso de ejemplos concretos para ilustrar la coherencia temática (peso: 25%)"]}', '{"puntos_clave": ["Identificación de similitudes y diferencias entre los temas", "Organización clara y progresiva de la información", "Uso de ejemplos específicos para apoyar la relación temática"], "respuesta_modelo": "Para mantener una coherencia temática clara al presentar dos temas de interés, primero identificaría los puntos en común y las diferencias principales entre ambos. Por ejemplo, si los temas son el cambio climático y la energía renovable, podría destacar cómo la energía renovable es una solución para mitigar el cambio climático. Organizaría la información comenzando con una introducción general de ambos temas, luego compararía sus características principales y finalmente relacionaría cómo uno afecta o influye en el otro. Usaría ejemplos como el impacto de la contaminación y cómo la energía solar ayuda a reducirla, para que la audiencia entienda claramente la conexión entre los temas.", "requiere_revision_humana": true}', 4, 0, true, '{análisis,"exposición oral","coherencia temática"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (652, 138, 'concept_map', 'all', '{"conceptos": ["Información fidedigna", "Investigación previa", "Coherencia temática", "Progresión temática", "Presentación oral"], "explicacion": "Se espera que el estudiante demuestre cómo la investigación previa permite obtener información fidedigna, que a su vez es esencial para mantener la coherencia temática. Además, debe relacionar cómo la progresión temática ayuda a organizar la presentación oral de manera clara y lógica, favoreciendo la comunicación efectiva.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la exposición oral para comunicar temas de interés?"}', '{"conceptos_centrales": ["Información fidedigna", "Coherencia temática"], "relaciones_esperadas": [{"origen": "Investigación previa", "destino": "Información fidedigna", "relacion": "permite obtener"}, {"origen": "Información fidedigna", "destino": "Coherencia temática", "relacion": "contribuye a"}, {"origen": "Progresión temática", "destino": "Presentación oral", "relacion": "organiza"}, {"origen": "Coherencia temática", "destino": "Presentación oral", "relacion": "mejora"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Expresión oral","Mapa conceptual",Análisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (653, 139, 'criteria_evaluation', 'all', '{"rubrica": {"Claridad en destacar lo más relevante": {"Excelente": "Destaca claramente los puntos más importantes con apoyo visual y verbal, facilitando la comprensión del público.", "Satisfactorio": "Identifica algunos puntos relevantes, pero la forma de destacarlos es poco sistemática o poco clara.", "Necesita mejorar": "No logra enfatizar los aspectos clave, generando confusión en la audiencia."}, "Justificación de la elección de material visual": {"Excelente": "Explica claramente y con fundamentos sólidos por qué cada material visual fue seleccionado para reforzar y destacar los puntos relevantes de la presentación.", "Satisfactorio": "Ofrece una explicación general sobre la elección de los materiales visuales, aunque con argumentos poco profundos o algo vagos.", "Necesita mejorar": "No justifica o la justificación es confusa, sin relación clara con la relevancia de los materiales en la presentación."}, "Uso adecuado de conectores para la progresión temática": {"Excelente": "Utiliza conectores variados y adecuados que facilitan una progresión temática clara y coherente en toda la presentación.", "Satisfactorio": "Emplea algunos conectores apropiados, pero la progresión temática es a veces poco clara o interrumpida.", "Necesita mejorar": "Usa pocos o ningún conector adecuado, lo que dificulta la comprensión y la organización de las ideas."}}, "explicacion": "Se espera que el estudiante analice críticamente la efectividad y pertinencia del material visual y conectores usados, explicando cómo estas herramientas contribuyen a la claridad y coherencia de la presentación, y justificando su evaluación con argumentos claros y fundamentados.", "instruccion": "Evalúa la presentación según los criterios de la rúbrica y justifica tu calificación para cada uno de ellos.", "caso_a_evaluar": "Un estudiante presenta una exposición oral sobre el impacto de las redes sociales en los jóvenes. En su presentación, utiliza imágenes, gráficos estadísticos y conectores discursivos para organizar sus ideas y destacar los puntos más importantes. Además, explica brevemente la razón de elección de cada material visual y conecta adecuadamente las ideas para mantener la coherencia temática."}', '{"evaluacion_modelo": {"Claridad en destacar lo más relevante": "Satisfactorio", "Justificación de la elección de material visual": "Satisfactorio", "Uso adecuado de conectores para la progresión temática": "Excelente"}, "justificacion_modelo": "La justificación del material visual es general y no profundiza en los motivos específicos de cada elemento, por lo que se evalúa como satisfactorio. En cambio, el uso de conectores es variado y adecuado, facilitando la progresión temática, lo que corresponde a un nivel excelente. La claridad para destacar lo más relevante es adecuada pero podría ser más sistemática, por eso se califica como satisfactorio.", "requiere_revision_humana": true}', 4, 0, true, '{"presentación oral",evaluación,"material visual",conectores,"primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (654, 139, 'criteria_evaluation', 'all', '{"rubrica": {"Justificación del material visual": {"Excelente": "El estudiante explica claramente por qué cada imagen o gráfico fue seleccionado, mostrando cómo refuerzan y destacan la información clave de la presentación.", "Satisfactorio": "El estudiante menciona algunas razones para la elección del material visual, pero la relación con la información presentada es general o poco profunda.", "Necesita mejorar": "El estudiante no justifica o justifica de manera insuficiente la elección del material visual, sin conectar adecuadamente con el contenido de la presentación."}, "Justificación del uso de conectores": {"Excelente": "El estudiante identifica y explica con claridad cómo los conectores empleados organizan la información y facilitan la comprensión del tema.", "Satisfactorio": "El estudiante reconoce algunos conectores usados, pero la justificación de su función es superficial o incompleta.", "Necesita mejorar": "El estudiante no identifica ni justifica adecuadamente el uso de conectores en la presentación."}, "Coherencia y relevancia en la justificación": {"Excelente": "La justificación es lógica, coherente y se centra en destacar los elementos más relevantes para la presentación.", "Satisfactorio": "La justificación es en general coherente, pero algunas explicaciones no están claramente relacionadas con la relevancia del material o conectores.", "Necesita mejorar": "La justificación carece de coherencia o no se enfoca en la relevancia de los elementos seleccionados."}}, "explicacion": "Se espera que el estudiante analice críticamente las razones detrás de la selección de material visual y conectores, valorando su aporte a la claridad y relevancia de la presentación. La justificación debe ser clara, coherente y basada en la función comunicativa de los elementos evaluados.", "instruccion": "Evalúa la presentación oral según la rúbrica y justifica tu calificación en cada criterio, explicando tu razonamiento.", "caso_a_evaluar": "Se presenta el video de una exposición oral donde un estudiante expone un tema de interés personal, utilizando una presentación de diapositivas que incluye imágenes, gráficos y conectores discursivos para organizar la información."}', '{"evaluacion_modelo": {"Justificación del material visual": "Satisfactorio", "Justificación del uso de conectores": "Excelente", "Coherencia y relevancia en la justificación": "Satisfactorio"}, "justificacion_modelo": "La justificación del material visual es general y no profundiza en cada elemento, por lo que es satisfactoria. En cambio, la explicación sobre los conectores es clara y muestra comprensión de su función organizativa, alcanzando un nivel excelente. La coherencia general es adecuada, aunque hay espacios para mayor precisión, por eso se evalúa como satisfactorio.", "requiere_revision_humana": true}', 5, 0, true, '{"presentacion oral",justificacion,"material visual",conectores,evaluacion}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (655, 139, 'criteria_evaluation', 'all', '{"rubrica": {"Enfoque en lo más relevante": {"Excelente": "Destaca con precisión los elementos visuales y conectores que resaltan lo más importante de la presentación.", "Satisfactorio": "Reconoce algunos elementos relevantes, pero con justificaciones superficiales.", "Necesita mejorar": "No logra identificar ni justificar los elementos que destacan lo más importante."}, "Justificación del material visual": {"Excelente": "Explica claramente cómo cada imagen o gráfico refuerza puntos clave y ayuda a la comprensión del tema.", "Satisfactorio": "Menciona la función general del material visual, pero con explicaciones poco detalladas o parciales.", "Necesita mejorar": "No logra justificar adecuadamente la elección de material visual o su relación con el contenido."}, "Uso de conectores para progresión temática": {"Excelente": "Identifica y justifica el uso de conectores que crean una progresión lógica y coherente entre las ideas.", "Satisfactorio": "Reconoce algunos conectores usados, pero la justificación es incompleta o poco clara.", "Necesita mejorar": "No identifica ni justifica el uso de conectores en la presentación."}}, "explicacion": "Se espera que el estudiante analice críticamente cómo el presentador utiliza material visual y conectores para mejorar la comunicación, argumentando con ejemplos claros y relacionándolos con la relevancia del contenido.", "instruccion": "Evalúa la presentación oral según la rúbrica y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Una presentación oral sobre el impacto de las redes sociales en la comunicación juvenil, que incluye el uso de imágenes, gráficos y conectores discursivos para organizar la información."}', '{"evaluacion_modelo": {"Enfoque en lo más relevante": "Satisfactorio", "Justificación del material visual": "Satisfactorio", "Uso de conectores para progresión temática": "Excelente"}, "justificacion_modelo": "La justificación sobre el material visual es general pero adecuada, mientras que la identificación y explicación del uso de conectores es precisa y bien argumentada. El reconocimiento de elementos relevantes está presente aunque no profundiza suficientemente.", "requiere_revision_humana": true}', 5, 0, true, '{presentacion_oral,evaluacion,material_visual,conectores,comunicacion}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (656, 139, 'open_ended', 'all', '{"pregunta": "Después de realizar tu presentación oral, explica y justifica la elección del material visual y los conectores que utilizaste para reforzar y destacar los puntos más importantes de tu exposición. ¿Cómo crees que estos elementos contribuyeron a la claridad y efectividad de tu mensaje?", "explicacion": "El docente debe evaluar que el estudiante no solo identifique los recursos usados sino que también argumente su elección mostrando comprensión de cómo estos recursos mejoran la comunicación oral. Se valorará la capacidad crítica y la reflexión sobre la efectividad comunicativa.", "contexto_adicional": "Esta pregunta se realiza luego de una presentación oral en la que el estudiante utilizó recursos visuales y conectores para comunicar un tema investigado.", "criterios_evaluacion": ["Justificación clara y coherente de la selección del material visual, demostrando comprensión de su función en la presentación (peso: 40%)", "Análisis del uso de conectores y cómo estos facilitaron la progresión temática y la comprensión del contenido (peso: 35%)", "Reflexión sobre la contribución global de estos elementos a la claridad y efectividad del mensaje, mostrando pensamiento crítico (peso: 25%)"]}', '{"puntos_clave": ["Justificación del uso de material visual adecuado al tema y función comunicativa", "Explicación del uso de conectores para organizar y clarificar el discurso", "Reflexión sobre cómo estos elementos mejoran la claridad y efectividad del mensaje"], "respuesta_modelo": "En mi presentación, elegí utilizar gráficos y fotografías relacionadas con el tema porque facilitan la comprensión visual y captan la atención del público. Estos materiales visuales reforzaron los datos importantes que presenté y permitieron que la audiencia siguiera mejor la explicación. Además, usé conectores como ''por ejemplo'', ''además'' y ''en conclusión'' para organizar mi discurso y guiar a la audiencia a través de las ideas principales. Creo que estos elementos contribuyeron a que mi mensaje fuera claro y fácil de seguir, ya que ayudaron a destacar lo más relevante y a mantener una progresión lógica en la presentación.", "requiere_revision_humana": true}', 4, 0, true, '{"presentación oral","material visual",conectores,"evaluación crítica"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (657, 139, 'open_ended', 'all', '{"pregunta": "Después de preparar tu presentación oral, ¿cómo justificarías la elección de los materiales visuales y conectores que utilizaste para reforzar y destacar los puntos más importantes de tu exposición? Explica cómo cada uno contribuye a la claridad y efectividad del mensaje, considerando a la audiencia y la progresión temática.", "explicacion": "El docente debe evaluar si el estudiante puede identificar y argumentar adecuadamente por qué seleccionó ciertos recursos visuales y conectores, cómo estos mejoran la comunicación y si considera el impacto en la audiencia y la estructura temática. Se debe valorar la profundidad del análisis y la capacidad para justificar de forma crítica las elecciones hechas.", "contexto_adicional": "El estudiante debe imaginar que acaba de realizar una presentación oral sobre un tema de su interés y debe reflexionar sobre las decisiones que tomó respecto a los recursos visuales y el uso de conectores durante su exposición.", "criterios_evaluacion": ["Justificación clara y coherente de la selección de material visual, demostrando comprensión de su función en la presentación (peso: 40%)", "Análisis del uso de conectores y su aporte a la organización y fluidez de la presentación (peso: 35%)", "Capacidad para relacionar la elección de recursos con la audiencia y la progresión temática, mostrando pensamiento crítico (peso: 25%)"]}', '{"puntos_clave": ["Justificación del uso de materiales visuales específicos y su función comunicativa", "Explicación del papel de los conectores en la organización y claridad del discurso", "Relación entre las elecciones de recursos y las características de la audiencia y la estructura temática"], "respuesta_modelo": "Elegí incluir gráficos y fotografías relevantes porque ayudan a ilustrar los datos y ejemplos que mencioné, haciendo que la información sea más comprensible y atractiva para la audiencia. Por ejemplo, el gráfico que muestra las estadísticas permitió que mis oyentes visualicen claramente las tendencias, lo que refuerza mi argumento principal. Respecto a los conectores, utilicé palabras como ''además'', ''por otro lado'' y ''en conclusión'' para guiar a la audiencia a través de las diferentes ideas, asegurando una progresión temática clara y evitando confusiones. Estas elecciones respondieron a las características de mi audiencia, que es diversa y necesita apoyos visuales para mantener la atención, y también me ayudaron a destacar lo más relevante de mi presentación de forma ordenada y efectiva.", "requiere_revision_humana": true}', 5, 0, true, '{presentacion_oral,evaluacion_critica,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (658, 140, 'open_ended', 'all', '{"pregunta": "Diseña y elabora una presentación oral sobre un tema de tu interés que sea relevante para tu comunidad escolar. Debes integrar información investigada, utilizar vocabulario especializado, incluir conectores que aseguren una progresión temática clara y apoyar tu exposición con material visual relevante. ¿Cómo estructurarías tu presentación para que sea clara, coherente y adecuada a la audiencia y situación? Explica tu proceso de diseño y justifica las decisiones tomadas.", "explicacion": "El docente debe evaluar cómo el estudiante integra información confiable y especializada, cómo organiza su discurso con una progresión lógica y cómo utiliza recursos visuales para apoyar la comunicación. Es importante valorar también la adecuación de la presentación al público escolar y la claridad del mensaje.", "contexto_adicional": "El estudiante deberá imaginar que presentará frente a sus compañeros y profesores en un contexto escolar, con el objetivo de informar y generar interés en el tema elegido.", "criterios_evaluacion": ["Integración de información investigada y uso adecuado de vocabulario especializado que demuestre profundidad en el tema (peso: 40%)", "Claridad y coherencia en la estructura y progresión temática de la presentación, incluyendo uso efectivo de conectores (peso: 35%)", "Uso pertinente y apoyo del material visual para reforzar la comunicación oral y adecuación al público (peso: 25%)"]}', '{"puntos_clave": ["Integración de información confiable y vocabulario especializado", "Estructura clara con progresión temática y uso de conectores", "Material visual relevante que apoya y refuerza la presentación", "Adecuación al público y situación escolar"], "respuesta_modelo": "Para diseñar mi presentación oral sobre el cuidado del medio ambiente en la escuela, primero investigué fuentes confiables que explican la importancia de reciclar y reducir el consumo de plásticos. Utilicé vocabulario especializado como ''sostenibilidad'', ''huella ecológica'' y ''reciclaje selectivo''. Organicé la presentación en tres partes: introducción (contextualizando el problema), desarrollo (explicando causas y consecuencias) y conclusión (proponiendo acciones concretas). Utilicé conectores como ''en primer lugar'', ''por lo tanto'' y ''en conclusión'' para mantener una progresión clara. Además, diseñé una presentación con imágenes y gráficos que muestran estadísticas del impacto ambiental en Chile, lo que ayuda a captar la atención y fortalecer el mensaje. Todas estas decisiones fueron pensadas para comunicar de manera clara y adecuada a mis compañeros y profesores, motivándolos a participar activamente en el cuidado del medio ambiente.", "requiere_revision_humana": true}', 4, 0, true, '{"presentación oral",investigación,"vocabulario especializado","material visual","comunicación clara"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (659, 140, 'open_ended', 'all', '{"pregunta": "Diseña y elabora una presentación oral sobre un tema de tu interés que implique una investigación previa. Debes integrar información confiable, utilizar vocabulario especializado, conectar las ideas de manera coherente y apoyar tu exposición con material visual relevante. ¿Cómo organizaste la información y qué estrategias usaste para asegurar que tu presentación fuera clara y adecuada para tu audiencia?", "explicacion": "El docente debe valorar cómo el estudiante selecciona y presenta información fiable, el uso correcto de términos específicos, la estructura lógica de la presentación y la pertinencia del apoyo visual. Además, se debe considerar la adecuación del discurso para la audiencia y la intención comunicativa.", "contexto_adicional": "El estudiante debe elegir un tema que le interese y realizar una investigación previa para sustentar su presentación oral. Se espera que utilice conectores para enlazar ideas y que el material visual aporte a la comprensión del contenido.", "criterios_evaluacion": ["Integración de información investigada y uso de vocabulario especializado (peso: 40%)", "Claridad y coherencia en la organización y progresión temática de la presentación (peso: 35%)", "Adecuación del material visual y estrategias de comunicación para la audiencia (peso: 25%)"]}', '{"puntos_clave": ["Elección de un tema de interés con investigación previa fiable", "Uso de vocabulario especializado adecuado al tema", "Organización clara con conectores que facilitan la comprensión", "Material visual pertinente y bien integrado", "Adecuación del discurso para la audiencia"], "respuesta_modelo": "Elegí el tema de cambio climático porque me interesa su impacto en el mundo. Investigué en fuentes confiables como artículos científicos y páginas oficiales. Utilicé términos especializados como ''emisiones de gases de efecto invernadero'' y ''adaptación climática''. Organicé la presentación en introducción, desarrollo y conclusión, usando conectores como ''primero'', ''además'' y ''por último'' para mantener la coherencia. Diseñé diapositivas con gráficos que muestran el aumento de temperatura y fotos de efectos visibles del cambio climático. Para la audiencia, usé un lenguaje claro y ejemplos actuales para facilitar la comprensión y mantener su interés.", "requiere_revision_humana": true}', 5, 0, true, '{presentacion_oral,pensamiento_critico,investigacion,lenguaje_especializado}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (660, 140, 'concept_map', 'all', '{"conceptos": ["Presentación oral", "Información fidedigna", "Progresión temática", "Vocabulario especializado", "Material visual"], "explicacion": "El mapa conceptual debe mostrar cómo la presentación oral se fundamenta en información fidedigna y sigue una progresión temática clara. Además, debe integrar vocabulario especializado para enriquecer el discurso y utilizar material visual para apoyar y reforzar la comunicación, estableciendo así conexiones lógicas entre estos elementos para lograr una presentación efectiva.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de diseñar y elaborar una presentación oral clara y coherente?"}', '{"conceptos_centrales": ["Presentación oral", "Información fidedigna"], "relaciones_esperadas": [{"origen": "Presentación oral", "destino": "Información fidedigna", "relacion": "requiere"}, {"origen": "Presentación oral", "destino": "Progresión temática", "relacion": "se organiza mediante"}, {"origen": "Presentación oral", "destino": "Vocabulario especializado", "relacion": "incluye"}, {"origen": "Presentación oral", "destino": "Material visual", "relacion": "se apoya con"}, {"origen": "Información fidedigna", "destino": "Vocabulario especializado", "relacion": "enriquece"}], "requiere_revision_humana": true}', 4, 0, true, '{mapa_conceptual,presentación_oral,lengua_y_literatura,primer_medio,crear}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (661, 140, 'concept_map', 'all', '{"conceptos": ["Presentación Oral", "Información Investigada", "Vocabulario Especializado", "Conectores", "Material Visual", "Claridad", "Coherencia", "Progresión Temática"], "explicacion": "Se espera que el estudiante muestre cómo la ''Información Investigada'' se integra en la ''Presentación Oral'' usando ''Vocabulario Especializado'' y ''Conectores'' para lograr ''Claridad'' y ''Coherencia'', además de emplear ''Material Visual'' para apoyar la ''Progresión Temática'' y mejorar la comunicación.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de diseñar y elaborar una presentación oral efectiva?"}', '{"conceptos_centrales": ["Presentación Oral", "Información Investigada"], "relaciones_esperadas": [{"origen": "Información Investigada", "destino": "Presentación Oral", "relacion": "se integra en"}, {"origen": "Vocabulario Especializado", "destino": "Claridad", "relacion": "apoya"}, {"origen": "Conectores", "destino": "Coherencia", "relacion": "favorecen"}, {"origen": "Material Visual", "destino": "Progresión Temática", "relacion": "refuerza"}, {"origen": "Presentación Oral", "destino": "Claridad", "relacion": "debe tener"}, {"origen": "Presentación Oral", "destino": "Coherencia", "relacion": "debe tener"}], "requiere_revision_humana": true}', 5, 0, true, '{lengua_y_literatura,presentación_oral,mapa_conceptual,primer_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (662, 140, 'open_ended', 'all', '{"pregunta": "Diseña y elabora una presentación oral sobre un tema de tu interés, en la que integres información investigada, utilizando vocabulario especializado, conectores adecuados y material visual relevante. Explica cómo estructuraste tu presentación para asegurar una progresión temática clara y cómo seleccionaste la información y los recursos visuales para apoyar tu mensaje.", "explicacion": "La evaluación se centra en la capacidad del estudiante para organizar y comunicar información investigada de manera clara y coherente, utilizando recursos lingüísticos y visuales que faciliten la comprensión y el interés de la audiencia. Se valorará la selección pertinente del contenido, la precisión en el lenguaje y la calidad del soporte visual.", "contexto_adicional": "El estudiante debe preparar una presentación oral de aproximadamente 5 minutos, destinada a un público de compañeros de curso, sobre un tema que le interese y que haya investigado previamente.", "criterios_evaluacion": ["Claridad y coherencia en la estructura de la presentación, con progresión temática lógica y ordenada (peso: 30%)", "Integración adecuada de información investigada y uso correcto de vocabulario especializado y conectores (peso: 40%)", "Relevancia y efectividad del material visual para apoyar y complementar la comunicación oral (peso: 30%)"]}', '{"puntos_clave": ["Estructura clara y progresión lógica de la presentación", "Uso correcto y pertinente de vocabulario especializado y conectores", "Material visual relevante que complementa la información oral"], "respuesta_modelo": "He elegido presentar sobre la importancia del reciclaje en el cuidado del medio ambiente. Primero, estructuré mi presentación en tres partes: introducción, desarrollo y conclusión, asegurando una progresión clara. En la introducción presenté el concepto y relevancia del reciclaje; en el desarrollo, expliqué los tipos de materiales reciclables, su impacto ambiental y ejemplos prácticos; y en la conclusión, resumí los beneficios y llamé a la acción. Utilicé vocabulario especializado como ''sostenibilidad'', ''biodegradable'' y ''huella ecológica'', y conectores como ''además'', ''por lo tanto'' y ''en conclusión'' para enlazar las ideas. Para apoyar mi mensaje, incorporé gráficos sobre la cantidad de residuos reciclados en Chile y fotografías de centros de reciclaje, lo que ayudó a visualizar la información y mantuvo el interés de la audiencia.", "requiere_revision_humana": true}', 5, 0, true, '{presentacion_oral,pensamiento_critico,lengua_y_literatura,primer_medio}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (663, 141, 'multiple_choice', 'all', '{"opciones": {"A": "Los personajes que intervienen en la historia", "B": "El número de páginas del libro", "C": "El contexto histórico en el que se desarrolla la obra", "D": "Los símbolos que aparecen y su posible significado"}, "pregunta": "¿Cuál de los siguientes elementos NO es clave para identificar en un texto literario al analizar su contenido?", "explicacion": "La respuesta correcta es B, ya que el número de páginas no aporta información sobre el análisis literario en sí, sino que es un dato físico del libro. Los distractores A, C y D son elementos claves para comprender un texto literario: los personajes permiten entender quiénes participan en la historia; el contexto histórico ayuda a situar la obra en un tiempo y espacio específicos; y los símbolos enriquecen la interpretación del texto."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{"Lengua y Literatura","Análisis Literario","Elementos del Texto"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (664, 141, 'multiple_choice', 'all', '{"opciones": {"A": "Personajes", "B": "Contexto histórico", "C": "Tema", "D": "Símbolos"}, "pregunta": "En el análisis de un texto literario, ¿cuál de los siguientes elementos se refiere al escenario y época en que ocurre la historia?", "explicacion": "La opción B, ''Contexto histórico'', es correcta porque se refiere al escenario temporal y social en que se desarrolla la obra literaria. Los personajes (A) son los individuos que participan en la historia; el tema (C) es la idea central o mensaje del texto; y los símbolos (D) son elementos que representan conceptos o ideas más amplias. Confundir contexto histórico con estos otros elementos es un error común."}', '{"respuesta_correcta": "B"}', 2, 0, true, '{"Lengua y Literatura","Texto literario","Contexto histórico","Elementos literarios"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (665, 141, 'multiple_choice', 'all', '{"opciones": {"A": "Los personajes principales que actúan en la historia", "B": "El período y las circunstancias sociales en que se desarrolla la obra", "C": "Los símbolos que representan ideas profundas dentro del relato", "D": "El tema central que aborda el texto, como el amor o la justicia"}, "pregunta": "En un texto literario, ¿cuál de los siguientes elementos corresponde al contexto histórico?", "explicacion": "La opción B es correcta porque el contexto histórico se refiere al tiempo y las condiciones sociales, políticas y culturales en que se sitúa la obra literaria. La opción A es incorrecta porque los personajes son elementos narrativos, no contexto. La opción C se refiere a los símbolos, que son elementos literarios que representan ideas, no el contexto. La opción D habla del tema central, que es el asunto principal del texto, pero no corresponde al contexto histórico."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{literatura,"contexto histórico","elementos literarios","enseñanza media"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (666, 141, 'true_false', 'all', '{"afirmacion": "Los personajes en un texto literario son elementos que representan a los individuos que participan en la historia.", "explicacion": "Esta afirmación es verdadera porque los personajes son los seres, ya sean humanos, animales o incluso seres fantásticos, que llevan a cabo las acciones y desarrollan la trama dentro del texto literario."}', '{"es_verdadero": true}', 1, 0, true, '{personajes,"elementos clave",literatura,"primero medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (667, 141, 'fill_blanks', 'all', '{"texto": "En un texto literario, los _______ [BLANK_1] representan a los seres que participan en la historia, mientras que los _______ [BLANK_2] reflejan las ideas principales o mensajes que el autor quiere transmitir.", "pistas": ["BLANK_1 se refiere a quienes actúan dentro de la narrativa.", "BLANK_2 está relacionado con las ideas centrales o mensajes del texto."], "explicacion": "Los personajes son los protagonistas, antagonistas u otros seres que intervienen en la historia, y los temas son los conceptos o mensajes principales que se exploran en la obra literaria."}', '{"respuestas_correctas": {"BLANK_1": ["personajes"], "BLANK_2": ["temas"]}}', 2, 0, true, '{"lengua y literatura","primer año medio","elementos literarios",personajes,temas}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (669, 142, 'multiple_choice', 'all', '{"opciones": {"A": "Una historia de aventuras que refleja la búsqueda de tesoros en tiempos de prosperidad económica.", "B": "Una crítica a la decadencia social y política, mostrando la lucha de individuos marginados en una sociedad convulsionada.", "C": "Una novela romántica que destaca la importancia de las relaciones amorosas en la construcción de la identidad personal.", "D": "Un relato futurista que se centra en el avance tecnológico y su impacto positivo en la vida cotidiana."}, "pregunta": "¿Cuál es el sentido general de la novela \"Los Siete Locos\" de Roberto Arlt y cómo se relaciona con el contexto histórico y social de Argentina en la década de 1920?", "explicacion": "La respuesta correcta es la opción B, ya que \"Los Siete Locos\" es una obra que critica la realidad social y política de Argentina en los años 20, mostrando personajes marginados y sus conflictos internos frente a una sociedad en crisis. La opción A confunde el género y el contexto, la C malinterpreta el enfoque principal de la obra y la D asigna un tema futurista que no corresponde al texto."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{"interpretación literaria","contexto histórico","literatura argentina"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (670, 142, 'true_false', 'all', '{"afirmacion": "El sentido general de una obra literaria siempre está influenciado por el contexto histórico y social en el que fue creada.", "explicacion": "Esta afirmación es verdadera porque el contexto histórico y social proporciona el marco en el que el autor desarrolla su obra, influyendo en los temas, personajes y conflictos presentados. Comprender este contexto permite interpretar mejor el sentido y los mensajes de la obra."}', '{"es_verdadero": true}', 2, 0, true, '{interpretación,"contexto histórico","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (671, 142, 'drag_drop_matching', 'all', '{"explicacion": "Cada concepto está relacionado con la definición o ejemplo que clarifica su significado dentro del análisis literario: el contexto histórico refiere al tiempo en que se creó la obra; la interpretación personal es la opinión del lector; el sentido general es el mensaje principal; el contexto social involucra las condiciones sociales presentes; y el ejemplo es una obra reconocida que permite aplicar estos conceptos.", "instruccion": "Empareja cada concepto con su definición o ejemplo correspondiente", "columna_derecha": ["Circunstancias y eventos del tiempo en que se escribió la obra", "La idea o mensaje principal que transmite el texto", "Una opinión propia basada en el análisis del texto", "Condiciones y características de la sociedad en que se desarrolla la obra", "‘La casa de los espíritus’ de Isabel Allende"], "columna_izquierda": ["Contexto histórico", "Interpretación personal", "Sentido general de la obra", "Contexto social", "Ejemplo de obra literaria"]}', '{"emparejamientos_correctos": {"Contexto social": "Condiciones y características de la sociedad en que se desarrolla la obra", "Contexto histórico": "Circunstancias y eventos del tiempo en que se escribió la obra", "Interpretación personal": "Una opinión propia basada en el análisis del texto", "Ejemplo de obra literaria": "‘La casa de los espíritus’ de Isabel Allende", "Sentido general de la obra": "La idea o mensaje principal que transmite el texto"}}', 2, 0, true, '{"Lengua y Literatura","Interpretación de textos","Contexto histórico y social"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (672, 142, 'sequencing', 'all', '{"explicacion": "El orden correcto inicia con la lectura o visualización del texto literario para comprender su contenido. Luego, se identifican los elementos del contexto histórico y social presentes. Posteriormente, se formula una hipótesis sobre el sentido de la obra basada en este análisis. Después, se relaciona esta interpretación con el contexto para darle mayor profundidad, y finalmente se resume el sentido general de la obra.", "instruccion": "Ordena los siguientes pasos para explicar el sentido general de una obra literaria y su relación con el contexto histórico y social", "elementos_desordenados": ["Identificar elementos del contexto histórico y social presentes en la obra", "Formular una hipótesis sobre el sentido de la obra basada en el análisis", "Leer o ver el texto literario cuidadosamente", "Relacionar el sentido interpretado con el contexto histórico y social", "Resumir el sentido general de la obra"]}', '{"orden_correcto": ["Leer o ver el texto literario cuidadosamente", "Identificar elementos del contexto histórico y social presentes en la obra", "Formular una hipótesis sobre el sentido de la obra basada en el análisis", "Relacionar el sentido interpretado con el contexto histórico y social", "Resumir el sentido general de la obra"]}', 3, 0, true, '{interpretación,"contexto histórico",secuencia}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (673, 143, 'multiple_choice', 'all', '{"opciones": {"A": "Las leyendas de los héroes trágicos de la mitología griega, que enfrentan su destino inevitable.", "B": "Los mitos de los dioses rebeldes, como Prometeo, que desafían a la autoridad para obtener libertad o conocimiento.", "C": "Los cuentos folclóricos que promueven la obediencia y el respeto a las reglas comunitarias.", "D": "Los textos sagrados que enfatizan la sumisión y el orden divino sobre la voluntad individual."}, "pregunta": "En el poema \"La canción del pirata\" de José de Espronceda, el protagonista expresa un deseo de libertad absoluta y rechazo a las normas sociales. ¿Cuál de las siguientes tradiciones literarias o mitológicas se relaciona mejor con este tema central del poema?", "explicacion": "La respuesta correcta es la opción B, porque el poema refleja el espíritu rebelde y la búsqueda de libertad del pirata, similar a la figura de Prometeo en la mitología griega, quien desafió a los dioses para traer el fuego a la humanidad. Los distractores A, C y D representan conceptos relacionados con la tragedia, la obediencia o la sumisión, que no corresponden con la temática de rebeldía y libertad que se expresa en el poema."}', '{"respuesta_correcta": "B"}', 3, 0, true, '{literatura,mitología,interpretación,primeromedia}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (674, 143, 'multiple_choice', 'all', '{"opciones": {"A": "La presencia de un héroe que derrota a un dragón", "B": "La figura de una mujer que llora por la pérdida de sus hijos y se convierte en espíritu errante", "C": "La descripción detallada de una batalla histórica entre dos pueblos", "D": "Un diálogo entre dioses que deciden el destino del mundo"}, "pregunta": "En el cuento \"La Llorona\", una leyenda popular latinoamericana, ¿cuál de los siguientes elementos conecta directamente esta obra con los mitos o textos folclóricos?", "explicacion": "La opción B es correcta porque ''La Llorona'' es una leyenda que presenta a una mujer que, tras perder a sus hijos, vaga llorando como espíritu, un elemento típico de los mitos y leyendas folclóricas. La opción A confunde con relatos de héroes y dragones, típicos de otras tradiciones. La C refiere a relatos históricos, no a mitos o leyendas. La D se relaciona más con textos sagrados o mitológicos, pero no con ''La Llorona''."}', '{"respuesta_correcta": "B"}', 4, 0, true, '{mitos,leyendas,"cuento folclórico","interpretación literaria"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (675, 143, 'drag_drop_matching', 'all', '{"explicacion": "La creación del mundo se relaciona con el Popol Vuh, que es un texto sagrado maya que narra el origen del mundo. El héroe que desciende al inframundo está vinculado al mito de Osiris, quien es una deidad que resucita. La historia del diluvio universal corresponde a la leyenda del Arca de Noé en la Biblia. El objeto mágico que otorga poderes se relaciona con el cuento folclórico del Mago Merlín. La lucha entre el bien y el mal se ejemplifica en el Ramayana, un texto sagrado de la cultura hindú. El distractor es el héroe moderno sin relación mitológica, que no tiene pareja.", "instruccion": "Empareja cada elemento literario con el mito, leyenda, cuento folclórico o texto sagrado que se relaciona con él", "columna_derecha": ["El mito de Osiris en la mitología egipcia", "La leyenda del Arca de Noé en la Biblia", "El cuento folclórico del Mago Merlín", "El Popol Vuh de la cultura maya", "El relato del Ramayana en la cultura hindú", "Un héroe moderno sin relación mitológica"], "columna_izquierda": ["La creación del mundo en un poema épico", "Un héroe que desciende al inframundo", "La historia de un diluvio universal", "Un objeto mágico que otorga poderes", "La lucha entre el bien y el mal"]}', '{"emparejamientos_correctos": {"La lucha entre el bien y el mal": "El relato del Ramayana en la cultura hindú", "La historia de un diluvio universal": "La leyenda del Arca de Noé en la Biblia", "Un objeto mágico que otorga poderes": "El cuento folclórico del Mago Merlín", "Un héroe que desciende al inframundo": "El mito de Osiris en la mitología egipcia", "La creación del mundo en un poema épico": "El Popol Vuh de la cultura maya"}}', 3, 0, true, '{mitos,leyendas,"textos sagrados","relación literaria"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (676, 143, 'sequencing', 'all', '{"explicacion": "El orden correcto inicia con la lectura o visualización de la obra para conocer su contenido. Luego, se identifica el mito original que presenta semejanzas con la obra. Después, se observa cómo el autor incorpora estos elementos para desarrollar el conflicto. Posteriormente, se formula una hipótesis interpretativa basada en esa relación. Finalmente, se analiza el impacto social o universal reflejado en el uso del mito.", "instruccion": "Ordena los siguientes eventos para mostrar cómo un mito influye en una obra literaria.", "elementos_desordenados": ["El autor incluye elementos del mito para construir el conflicto principal.", "Se identifica el mito original que comparte temas con la obra literaria.", "El lector analiza cómo esos elementos mitológicos reflejan valores sociales universales.", "Se formula una hipótesis sobre el sentido de la obra basada en el mito.", "Se lee o visualiza la obra literaria seleccionada."]}', '{"orden_correcto": ["Se lee o visualiza la obra literaria seleccionada.", "Se identifica el mito original que comparte temas con la obra literaria.", "El autor incluye elementos del mito para construir el conflicto principal.", "Se formula una hipótesis sobre el sentido de la obra basada en el mito.", "El lector analiza cómo esos elementos mitológicos reflejan valores sociales universales."]}', 3, 0, true, '{secuencia,mitos,"interpretación literaria"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (677, 143, 'open_ended', 'all', '{"pregunta": "Elige una obra literaria que hayas leído en clase y analiza cómo algunos de sus elementos se relacionan con un mito, leyenda, cuento folclórico o texto sagrado específico. Explica qué similitudes encuentras en los temas, personajes o símbolos, y cómo esta conexión contribuye a una mejor comprensión del sentido de la obra.", "explicacion": "El docente debe valorar que el estudiante identifique correctamente las obras, realice conexiones pertinentes entre ellas y explique con claridad cómo estas relaciones enriquecen la interpretación del texto literario. Se debe fomentar que el alumno justifique sus ideas con ejemplos y argumentos consistentes.", "contexto_adicional": "Se sugiere que el estudiante utilice ejemplos concretos de la obra literaria y del texto relacionado para fundamentar su análisis.", "criterios_evaluacion": ["Identificación clara y precisa de la obra literaria y del mito, leyenda, cuento folclórico o texto sagrado relacionado (peso: 30%)", "Análisis coherente de las similitudes y relaciones entre los elementos de ambas obras (peso: 40%)", "Explicación del aporte de esta relación a la interpretación general de la obra literaria (peso: 30%)"]}', '{"puntos_clave": ["Mención específica de la obra literaria y del mito o leyenda relacionada", "Identificación de temas, personajes o símbolos similares", "Explicación de cómo esta relación amplía la interpretación del texto literario"], "respuesta_modelo": "En la obra \"La muerte y la doncella\" de Ariel Dorfman, se pueden encontrar conexiones con la leyenda folclórica de la justicia poética en la cultura popular. Por ejemplo, el personaje principal busca justicia y redención tras una experiencia traumática, similar a las leyendas donde los personajes enfrentan a sus opresores para restaurar el equilibrio. Esta relación permite entender la obra no solo como un drama personal, sino también como una reflexión sobre la justicia social y la memoria histórica en contextos de dictadura.", "requiere_revision_humana": true}', 3, 0, true, '{literatura,análisis,mitos,leyendas,interpretación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (678, 144, 'compare_contrast', 'all', '{"conceptos": ["Interpretación personal", "Interpretación histórica", "Interpretación social"], "criterios": ["Enfoque", "Tipo de evidencia utilizada", "Propósito de la interpretación", "Relación con el contexto del texto"], "explicacion": "Las interpretaciones personal, histórica y social difieren principalmente en su enfoque y en el tipo de evidencia que emplean para fundamentar su análisis. La interpretación personal se centra en la perspectiva individual del lector, usando emociones o experiencias propias; la histórica examina el contexto temporal y hechos históricos relacionados con la obra; y la social considera las estructuras, conflictos o valores sociales reflejados en el texto. Sin embargo, todas buscan comprender el sentido de la obra y se relacionan con el contexto del texto para sustentar sus hipótesis.", "instruccion": "Compara y contrasta las siguientes interpretaciones literarias según los criterios dados"}', '{"tabla_correcta": {"Interpretación social": {"Enfoque": "Estructuras, valores y conflictos sociales", "Tipo de evidencia utilizada": "Normas sociales, roles, problemas comunitarios reflejados en el texto", "Propósito de la interpretación": "Criticar o reflexionar sobre aspectos sociales presentes en la obra", "Relación con el contexto del texto": "Examen de la obra como reflejo o crítica de la sociedad"}, "Interpretación personal": {"Enfoque": "Perspectiva individual y subjetiva", "Tipo de evidencia utilizada": "Emociones, experiencias personales, opiniones", "Propósito de la interpretación": "Expresar un punto de vista único y reflexivo", "Relación con el contexto del texto": "Conexión emocional o simbólica con elementos del texto"}, "Interpretación histórica": {"Enfoque": "Contexto temporal y antecedentes históricos", "Tipo de evidencia utilizada": "Hechos históricos, referencias temporales, contexto del autor", "Propósito de la interpretación": "Comprender la obra en relación con su época y circunstancias", "Relación con el contexto del texto": "Análisis del entorno histórico que influye en la obra"}}}', 3, 0, true, '{"interpretación literaria","análisis crítico","comparar y contrastar"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (679, 144, 'compare_contrast', 'all', '{"conceptos": ["Interpretación Personal", "Interpretación Social"], "criterios": ["Punto de vista", "Fundamento", "Objetivo"], "explicacion": "La interpretación personal se basa en la experiencia y emociones individuales del lector, mientras que la interpretación social se fundamenta en el contexto histórico y las problemáticas colectivas. Ambas buscan comprender el texto, pero desde perspectivas distintas: una más subjetiva y otra más objetiva y contextual.", "instruccion": "Compara y contrasta las interpretaciones personal y social de un texto literario según los criterios dados"}', '{"tabla_correcta": {"Interpretación Social": {"Objetivo": "Entender el texto en relación a problemáticas y realidades colectivas", "Fundamento": "Análisis del entorno social, político y cultural", "Punto de vista": "Objetivo, basado en el contexto histórico y social"}, "Interpretación Personal": {"Objetivo": "Comprender el significado del texto desde una mirada individual", "Fundamento": "Emociones, vivencias y percepciones personales", "Punto de vista": "Subjetivo, basado en experiencias individuales"}}}', 4, 0, true, '{interpretación,"análisis crítico",comparación}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (680, 144, 'open_ended', 'all', '{"pregunta": "Después de leer el texto literario asignado y analizar al menos dos interpretaciones diferentes del mismo, ¿cómo compararías estas visiones en relación con el mensaje central de la obra? Fundamenta tu respuesta con citas o ejemplos específicos del texto y las interpretaciones consideradas.", "explicacion": "El docente debe valorar que el estudiante pueda identificar las diferencias y similitudes entre las interpretaciones, y que respalde sus ideas con evidencia textual y crítica, demostrando pensamiento analítico y capacidad para argumentar con fundamentos claros.", "contexto_adicional": "Se espera que los estudiantes hayan leído el texto literario asignado y revisado al menos dos interpretaciones críticas diferentes de la obra para el debate guiado.", "criterios_evaluacion": ["Claridad y coherencia en la comparación de las diferentes interpretaciones (peso: 30%)", "Uso adecuado y pertinente de citas o ejemplos para sustentar el análisis crítico (peso: 40%)", "Demostración de un punto de vista personal fundamentado y reflexivo sobre el sentido de la obra (peso: 30%)"]}', '{"puntos_clave": ["Identificación de las diferentes interpretaciones y sus enfoques", "Uso de citas o ejemplos específicos del texto y de las interpretaciones para sustentar la comparación", "Presencia de un punto de vista personal fundamentado sobre el sentido del texto"], "respuesta_modelo": "Las dos interpretaciones analizadas ofrecen perspectivas distintas sobre el mensaje central de la obra. La primera interpreta el texto desde un enfoque social, destacando la crítica hacia las desigualdades presentes en la sociedad, mientras que la segunda lo aborda desde una visión más universal, enfocándose en la condición humana y la búsqueda de sentido. Por ejemplo, la primera interpretación cita el pasaje en que el protagonista enfrenta la injusticia social (''...'') para argumentar su punto, mientras que la segunda destaca la reflexión existencial en el monólogo final (''...''). Comparando ambas, observo que si bien ambas reconocen el conflicto del individuo, la primera enfatiza el contexto histórico y social, mientras que la segunda hace una lectura más introspectiva y filosófica. Personalmente, considero que la obra logra integrar ambas dimensiones, mostrando cómo las circunstancias sociales afectan la experiencia individual, por lo que ambas interpretaciones enriquecen la comprensión del texto.", "requiere_revision_humana": true}', 4, 0, true, '{"análisis literario","interpretación crítica","debate guiado"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (681, 144, 'concept_map', 'all', '{"conceptos": ["Interpretación personal", "Contexto histórico", "Contexto social", "Análisis crítico", "Citas o ejemplos", "Sentido de la obra", "Comparación de interpretaciones"], "explicacion": "Se espera que el estudiante conecte cómo la interpretación personal se fundamenta en un análisis crítico sustentado por citas o ejemplos, considerando el contexto histórico y social para comprender el sentido de la obra, y cómo estas interpretaciones pueden ser comparadas para enriquecer la comprensión del texto.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el proceso de formular y comparar interpretaciones de un texto literario?"}', '{"conceptos_centrales": ["Interpretación personal", "Análisis crítico"], "relaciones_esperadas": [{"origen": "Interpretación personal", "destino": "Análisis crítico", "relacion": "se basa en"}, {"origen": "Análisis crítico", "destino": "Citas o ejemplos", "relacion": "incluye"}, {"origen": "Contexto histórico", "destino": "Sentido de la obra", "relacion": "influye en"}, {"origen": "Contexto social", "destino": "Sentido de la obra", "relacion": "influye en"}, {"origen": "Sentido de la obra", "destino": "Interpretación personal", "relacion": "es interpretado a través de"}, {"origen": "Interpretación personal", "destino": "Comparación de interpretaciones", "relacion": "se compara en"}, {"origen": "Comparación de interpretaciones", "destino": "Análisis crítico", "relacion": "enriquece"}], "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Interpretación literaria","Mapa conceptual","Análisis crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (682, 144, 'concept_map', 'all', '{"conceptos": ["Interpretación personal", "Contexto histórico", "Contexto social", "Citas o ejemplos", "Análisis crítico", "Hipótesis sobre el sentido", "Punto de vista universal"], "explicacion": "Se espera que el estudiante muestre cómo la interpretación personal se fundamenta en una hipótesis sobre el sentido de la obra, que puede estar influenciada por contextos históricos, sociales o universales. Además, debe conectar el uso de citas o ejemplos como evidencia que sustenta un análisis crítico, y cómo estas relaciones permiten comparar diferentes interpretaciones del texto literario.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el proceso de formular y comparar interpretaciones literarias?"}', '{"conceptos_centrales": ["Interpretación personal", "Hipótesis sobre el sentido"], "relaciones_esperadas": [{"origen": "Interpretación personal", "destino": "Hipótesis sobre el sentido", "relacion": "se fundamenta en"}, {"origen": "Hipótesis sobre el sentido", "destino": "Contexto histórico", "relacion": "considera"}, {"origen": "Hipótesis sobre el sentido", "destino": "Contexto social", "relacion": "considera"}, {"origen": "Hipótesis sobre el sentido", "destino": "Punto de vista universal", "relacion": "considera"}, {"origen": "Análisis crítico", "destino": "Citas o ejemplos", "relacion": "se sustenta en"}, {"origen": "Interpretación personal", "destino": "Análisis crítico", "relacion": "se apoya en"}], "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura","Interpretación literaria","Mapa conceptual","Análisis crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (683, 145, 'criteria_evaluation', 'all', '{"rubrica": {"Coherencia y argumentación": {"Excelente": "Los argumentos son coherentes, bien estructurados y establecen una relación lógica entre la hipótesis y las evidencias.", "Satisfactorio": "Los argumentos son generalmente coherentes, aunque algunos pueden ser poco claros o débiles en la relación con las evidencias.", "Necesita mejorar": "Los argumentos carecen de coherencia o no logran conectar la hipótesis con las evidencias presentadas."}, "Formulación de la hipótesis": {"Excelente": "La hipótesis es original, clara y demuestra un punto de vista personal profundo, integrando aspectos históricos, sociales o universales de forma pertinente.", "Satisfactorio": "La hipótesis es clara y pertinente, pero presenta un desarrollo limitado o general en cuanto a la integración de aspectos históricos, sociales o universales.", "Necesita mejorar": "La hipótesis es vaga, poco original o no considera aspectos históricos, sociales o universales relevantes."}, "Sustentación con evidencias del texto": {"Excelente": "Presenta múltiples evidencias textuales específicas y relevantes que respaldan la hipótesis de forma coherente y detallada.", "Satisfactorio": "Presenta algunas evidencias textuales que respaldan la hipótesis, aunque podrían ser más específicas o numerosas.", "Necesita mejorar": "No presenta evidencias claras o suficientes del texto para respaldar la hipótesis."}}, "explicacion": "Se espera que el estudiante formule una hipótesis personal bien fundamentada, que integre aspectos relevantes del contexto y que la apoye con evidencias textuales claras. Además, debe demostrar coherencia en la argumentación para justificar su interpretación.", "instruccion": "Evalúa tu respuesta según la rúbrica presentada y justifica por qué asignas cada nivel de desempeño a cada criterio.", "caso_a_evaluar": "Has leído el cuento \"La casa de Asterión\" de Jorge Luis Borges. Ahora, formula una hipótesis personal sobre el sentido de la obra, considerando aspectos históricos, sociales o universales, y justifícala con evidencias concretas extraídas del texto."}', '{"evaluacion_modelo": {"Coherencia y argumentación": "Excelente", "Formulación de la hipótesis": "Excelente", "Sustentación con evidencias del texto": "Satisfactorio"}, "justificacion_modelo": "La hipótesis planteada es original y demuestra un entendimiento profundo del texto y su contexto, aunque la sustentación con evidencias podría ser más amplia. La argumentación es clara y coherente, mostrando una buena conexión entre la hipótesis y las evidencias presentadas.", "requiere_revision_humana": true}', 4, 0, true, '{"interpretación literaria","evaluación crítica","formulación de hipótesis","justificación textual","Lengua y Literatura 1° Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (684, 145, 'criteria_evaluation', 'all', '{"rubrica": {"Formulación de la hipótesis": {"Excelente": "La hipótesis es clara, original y muestra un punto de vista personal, histórico, social o universal bien definido.", "Satisfactorio": "La hipótesis es clara pero general, con un punto de vista presente aunque poco desarrollado.", "Necesita mejorar": "La hipótesis es vaga, confusa o no se relaciona claramente con el texto."}, "Justificación con evidencias textuales": {"Excelente": "Se utilizan al menos tres evidencias textuales relevantes y bien explicadas que sustentan la hipótesis.", "Satisfactorio": "Se utilizan una o dos evidencias textuales que apoyan la hipótesis, aunque la explicación es superficial.", "Necesita mejorar": "No se presentan evidencias textuales o son insuficientes y poco relacionadas con la hipótesis."}, "Relación con contexto histórico, social o universal": {"Excelente": "La hipótesis y la justificación integran de manera coherente el contexto histórico, social o universal del poema.", "Satisfactorio": "Hay una mención superficial o poco clara del contexto en la hipótesis o justificación.", "Necesita mejorar": "No se considera el contexto histórico, social o universal en la hipótesis ni en la justificación."}}, "explicacion": "Se espera que el estudiante formule una hipótesis personal clara y bien argumentada sobre el sentido del texto, sustentada con evidencias textuales pertinentes, y que considere el contexto histórico, social o universal para profundizar su interpretación.", "instruccion": "Evalúa tu trabajo según la rúbrica proporcionada para cada criterio y justifica la calificación que te asignas en cada uno.", "caso_a_evaluar": "Has leído el poema \"La canción de los árboles\" de Pablo Neruda. Debes formular una hipótesis personal sobre el sentido del poema, considerando aspectos históricos, sociales o universales, y sustentarla con al menos tres evidencias textuales claras."}', '{"evaluacion_modelo": {"Formulación de la hipótesis": "Excelente", "Justificación con evidencias textuales": "Satisfactorio", "Relación con contexto histórico, social o universal": "Satisfactorio"}, "justificacion_modelo": "La hipótesis propuesta es original y claramente establece un punto de vista personal sobre el poema. Sin embargo, aunque se incluyen evidencias textuales, estas son solo dos y no están profundamente explicadas. La relación con el contexto está presente, pero es breve y podría estar mejor integrada.", "requiere_revision_humana": true}', 5, 0, true, '{"interpretación literaria","evaluación crítica","formulación de hipótesis","sustentación textual","contexto literario"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (685, 145, 'criteria_evaluation', 'all', '{"rubrica": {"Sustentación con evidencias": {"Excelente": "Presenta al menos tres evidencias del texto que apoyan la hipótesis, con explicaciones claras y bien fundamentadas que demuestran un análisis profundo.", "Satisfactorio": "Presenta dos evidencias del texto con explicaciones adecuadas, pero el análisis puede ser superficial o no totalmente convincente.", "Necesita mejorar": "Presenta una o ninguna evidencia del texto o las explicaciones son débiles y no sustentan adecuadamente la hipótesis."}, "Formulación de la hipótesis": {"Excelente": "La hipótesis es clara, original y refleja un punto de vista personal profundo que integra aspectos históricos, sociales o universales de forma coherente.", "Satisfactorio": "La hipótesis es clara y muestra un punto de vista personal, aunque la integración de aspectos históricos, sociales o universales es limitada o poco desarrollada.", "Necesita mejorar": "La hipótesis es vaga, poco clara o no refleja un punto de vista personal ni considera aspectos históricos, sociales o universales."}, "Coherencia y estructura del texto": {"Excelente": "La justificación está organizada de manera lógica, con un lenguaje claro y adecuado, facilitando la comprensión del análisis.", "Satisfactorio": "La justificación tiene una estructura básica y lenguaje adecuado, aunque con algunos errores que dificultan la claridad en algunos pasajes.", "Necesita mejorar": "La justificación es desorganizada, con uso inapropiado del lenguaje que dificulta la comprensión."}}, "explicacion": "Se espera que el estudiante formule una hipótesis personal y bien fundamentada sobre el sentido del cuento, sustentada con evidencias textuales claras. La coherencia y claridad en la exposición también serán evaluadas para valorar la capacidad de argumentación.", "instruccion": "Evalúa tu respuesta según la rúbrica presentada y justifica tu calificación para cada criterio.", "caso_a_evaluar": "Has leído el cuento \"La casa de Asterión\" de Jorge Luis Borges. Formula una hipótesis personal sobre el sentido de la obra, considerando aspectos históricos, sociales o universales. Luego, justifica tu hipótesis sustentándola con al menos tres evidencias concretas del texto."}', '{"evaluacion_modelo": {"Sustentación con evidencias": "Satisfactorio", "Formulación de la hipótesis": "Excelente", "Coherencia y estructura del texto": "Excelente"}, "justificacion_modelo": "La hipótesis propuesta es clara y demuestra un punto de vista personal con integración de aspectos universales, lo que justifica la calificación excelente. Las evidencias presentadas son dos y explicadas adecuadamente, pero no alcanzan la cantidad ni profundidad para un nivel excelente. La estructura y claridad del texto son muy buenas, facilitando la comprensión del análisis.", "requiere_revision_humana": true}', 5, 0, true, '{"interpretación literaria",argumentación,"evaluación de hipótesis","lengua y literatura","primer año medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (686, 145, 'open_ended', 'all', '{"pregunta": "Después de leer el texto literario asignado, formula una hipótesis personal sobre el sentido de la obra. Justifica tu hipótesis considerando aspectos históricos, sociales o universales presentes en el texto, y susténtala con evidencias específicas extraídas del mismo.", "explicacion": "El docente debe evaluar si el estudiante formula una hipótesis clara y personal que interprete el sentido de la obra, si conecta esta interpretación con dimensiones históricas, sociales o universales de manera adecuada, y si utiliza citas o referencias concretas del texto para justificar su punto de vista. Se debe valorar la profundidad del análisis y la capacidad crítica del alumno.", "contexto_adicional": "El estudiante debe basarse en un texto literario leído en clase, considerando tanto el contenido como el contexto en que fue escrito o los temas que aborda.", "criterios_evaluacion": ["Claridad y coherencia en la formulación de la hipótesis personal sobre el sentido de la obra (peso: 30%)", "Capacidad para relacionar la hipótesis con aspectos históricos, sociales o universales, demostrando comprensión contextual (peso: 40%)", "Uso adecuado y pertinente de evidencias textuales que sustentan la hipótesis propuesta (peso: 30%)"]}', '{"puntos_clave": ["Formulación clara y personal de la hipótesis sobre el sentido de la obra", "Relación explícita con aspectos históricos, sociales o universales presentes en el texto", "Evidencias textuales específicas que apoyan la hipótesis"], "respuesta_modelo": "En mi interpretación, el sentido principal de la obra es una crítica a la desigualdad social en el contexto histórico de la época, donde la autora expresa la tensión entre clases a través de los personajes y eventos. Por ejemplo, la descripción de los barrios pobres frente a los sectores acomodados refleja esta división social. Además, la obra aborda temas universales como la lucha por la justicia y la dignidad humana, lo que se evidencia en el discurso de uno de los protagonistas que cuestiona las normas establecidas. Estos elementos respaldan mi hipótesis de que la obra busca generar conciencia sobre las injusticias sociales y promover el cambio.", "requiere_revision_humana": true}', 4, 0, true, '{"interpretación literaria","pensamiento crítico","evaluación educativa"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (687, 145, 'open_ended', 'all', '{"pregunta": "Luego de leer el texto literario asignado, formula una hipótesis personal sobre el sentido de la obra. Justifica tu hipótesis considerando los aspectos históricos, sociales o universales presentes en el texto y susténtala con evidencias específicas extraídas de la lectura.", "explicacion": "El docente debe valorar que el estudiante no solo proponga una hipótesis original y clara, sino que también demuestre comprensión del contexto histórico, social o universal implicado en la obra y que utilice citas o ejemplos concretos del texto para apoyar su argumento. Se recomienda verificar la coherencia interna y la argumentación crítica en la respuesta.", "contexto_adicional": "El estudiante debe basarse en el texto literario leído en clase, integrando información contextual relevante para justificar su interpretación personal.", "criterios_evaluacion": ["Claridad y coherencia en la formulación de la hipótesis personal sobre el sentido de la obra (peso: 30%)", "Capacidad para relacionar la hipótesis con aspectos históricos, sociales o universales, mostrando un análisis profundo (peso: 40%)", "Uso adecuado y pertinente de evidencias textuales para sustentar la hipótesis propuesta (peso: 30%)"]}', '{"puntos_clave": ["Formulación clara y coherente de una hipótesis personal sobre el sentido de la obra", "Relación explícita entre la hipótesis y aspectos históricos, sociales o universales", "Evidencias textuales concretas que respaldan la interpretación"], "respuesta_modelo": "En mi interpretación, el sentido principal de la obra radica en la crítica a la desigualdad social que marcó la época en que fue escrita, reflejando las tensiones entre clases sociales. Esta hipótesis se sustenta en pasajes donde el autor describe con detalle las condiciones precarias de los personajes menos privilegiados y contrapone estas imágenes con la opulencia de la élite. Históricamente, la obra se sitúa en un periodo de fuertes transformaciones sociales, lo que refuerza la mirada crítica hacia las injusticias sociales. Así, el texto no solo refleja un momento histórico específico, sino que también plantea una denuncia universal sobre la desigualdad que sigue vigente en diversas sociedades.", "requiere_revision_humana": true}', 5, 0, true, '{"interpretación literaria","pensamiento crítico","evaluación escrita"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (688, 146, 'open_ended', 'all', '{"pregunta": "Elige una obra literaria leída en clase y diseña una interpretación original que incluya una hipótesis sobre el sentido de la obra desde un punto de vista personal, histórico, social o universal. Además, integra una crítica fundamentada, alusiones míticas presentes en el texto y contextualiza la obra en su época. ¿Cómo estos elementos contribuyen a enriquecer la comprensión global del texto?", "explicacion": "El docente debe evaluar si el estudiante logra crear una interpretación propia que muestre pensamiento crítico y creativo, valorando la profundidad del análisis, la fundamentación en el texto y el contexto, así como la originalidad y coherencia del trabajo.", "contexto_adicional": "Se espera que el estudiante utilice conocimientos previos, evidencias textuales y referencias externas para sustentar su interpretación. La respuesta debe ser un texto argumentativo de extensión adecuada para un proyecto corto.", "criterios_evaluacion": ["Planteamiento de una hipótesis original y coherente sobre el sentido de la obra (peso: 30%)", "Integración de una crítica fundamentada y alusiones míticas relevantes al análisis (peso: 40%)", "Contextualización histórica adecuada y coherencia general en el texto producido (peso: 30%)"]}', '{"puntos_clave": ["Hipótesis original sobre el sentido de la obra", "Crítica fundamentada que evidencie análisis profundo", "Incorporación de alusiones míticas y su significado", "Contextualización histórica adecuada", "Coherencia y fundamentación en el texto"], "respuesta_modelo": "En la obra ''La casa de los espíritus'' de Isabel Allende, se puede interpretar que el sentido principal es la exploración del destino individual y colectivo en el marco de la historia sociopolítica chilena. Desde un punto de vista histórico y social, la novela refleja las tensiones y transformaciones del siglo XX en Chile, mostrando cómo las decisiones personales están entrelazadas con el devenir histórico. La crítica hacia los abusos de poder y las desigualdades sociales se manifiesta claramente en la trama y los personajes. Además, la obra utiliza alusiones míticas, como la presencia de elementos sobrenaturales y la figura del espíritu, que remiten a creencias ancestrales y simbolizan la memoria y la conexión entre generaciones. Contextualizando la novela en su época, se entiende que Allende busca dar voz a las experiencias reprimidas durante la dictadura, enriqueciendo la comprensión de la obra como un testimonio histórico y literario. Estos elementos combinados permiten una interpretación profunda y multifacética que conecta lo personal con lo universal.", "requiere_revision_humana": true}', 4, 0, true, '{"interpretación literaria","análisis crítico","contexto histórico","creación textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (689, 146, 'open_ended', 'all', '{"pregunta": "Después de leer la obra literaria asignada, diseña una interpretación original que integre una hipótesis sobre su sentido, una crítica fundamentada, alusiones a elementos míticos presentes y una relación con el contexto histórico en que fue escrita. ¿Cómo estos componentes contribuyen a una comprensión más profunda de la obra?", "explicacion": "El docente debe evaluar que el estudiante haya creado una interpretación propia y fundamentada, que demuestre comprensión profunda del texto literario. Se valorará la originalidad de la hipótesis, la capacidad crítica para argumentar y la integración de elementos míticos y contexto histórico, además de la coherencia y claridad en la expresión escrita.", "contexto_adicional": "Se espera que el estudiante utilice la obra literaria leída en clase, considerando sus características, y que apoye su interpretación en ejemplos concretos del texto y en información sobre el momento histórico de su creación.", "criterios_evaluacion": ["Formulación clara y original de una hipótesis interpretativa que refleje un punto de vista personal, histórico, social o universal (peso: 30%)", "Análisis crítico que fundamenta la hipótesis mediante evidencias textuales y contexto histórico (peso: 40%)", "Integración coherente de alusiones míticas y contexto histórico en la interpretación final, expresada en un texto organizado y argumentado (peso: 30%)"]}', '{"puntos_clave": ["Hipótesis interpretativa personal y clara sobre el sentido de la obra", "Crítica fundamentada con evidencias textuales y contexto histórico", "Integración coherente de alusiones míticas y contexto histórico en la interpretación"], "respuesta_modelo": "En la obra literaria analizada, propongo que su sentido principal es una crítica a la opresión social, reflejada a través de la figura del héroe mítico que simboliza la lucha por la libertad. Esta hipótesis se sustenta en cómo el autor utiliza elementos mitológicos, como la referencia a Prometeo, para enfatizar el sacrificio y la resistencia. Además, el contexto histórico de la obra, ambientada en una época de dictadura, aporta una dimensión social que enriquece la lectura, mostrando cómo el sufrimiento individual refleja el colectivo. La interpretación integra estos aspectos para revelar que la obra no solo es un relato mitológico, sino una denuncia universal sobre la injusticia y la esperanza humana.", "requiere_revision_humana": true}', 5, 0, true, '{"interpretación literaria","análisis crítico","contexto histórico",mitología}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (690, 146, 'concept_map', 'all', '{"conceptos": ["Hipótesis sobre el sentido de la obra", "Crítica literaria", "Alusiones míticas", "Contexto histórico", "Interpretación coherente y fundamentada"], "explicacion": "El mapa conceptual debe mostrar cómo la hipótesis sobre el sentido de la obra se fundamenta en el contexto histórico y las alusiones míticas, cómo la crítica literaria aporta una valoración que complementa esta hipótesis, y cómo todos estos elementos se integran para construir una interpretación coherente y fundamentada.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el proceso de diseñar una interpretación original de una obra literaria?"}', '{"conceptos_centrales": ["Hipótesis sobre el sentido de la obra", "Interpretación coherente y fundamentada"], "relaciones_esperadas": [{"origen": "Hipótesis sobre el sentido de la obra", "destino": "Contexto histórico", "relacion": "se fundamenta en"}, {"origen": "Hipótesis sobre el sentido de la obra", "destino": "Alusiones míticas", "relacion": "incluye"}, {"origen": "Crítica literaria", "destino": "Hipótesis sobre el sentido de la obra", "relacion": "complementa"}, {"origen": "Hipótesis sobre el sentido de la obra", "destino": "Interpretación coherente y fundamentada", "relacion": "conduce a"}, {"origen": "Crítica literaria", "destino": "Interpretación coherente y fundamentada", "relacion": "apoya"}, {"origen": "Alusiones míticas", "destino": "Interpretación coherente y fundamentada", "relacion": "enriquecen"}, {"origen": "Contexto histórico", "destino": "Interpretación coherente y fundamentada", "relacion": "influye en"}], "requiere_revision_humana": true}', 4, 0, true, '{mapa_conceptual,interpretación_literaria,creación,análisis}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (691, 146, 'concept_map', 'all', '{"conceptos": ["Hipótesis interpretativa", "Crítica literaria", "Alusiones míticas", "Contexto histórico", "Sentido de la obra"], "explicacion": "Se espera que el estudiante conecte cómo la hipótesis interpretativa se basa en el contexto histórico y las alusiones míticas presentes en la obra, y cómo estos elementos contribuyen a formar una crítica literaria que fundamenta el sentido de la obra.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la interpretación de una obra literaria?"}', '{"conceptos_centrales": ["Hipótesis interpretativa", "Sentido de la obra"], "relaciones_esperadas": [{"origen": "Hipótesis interpretativa", "destino": "Contexto histórico", "relacion": "se fundamenta en"}, {"origen": "Hipótesis interpretativa", "destino": "Alusiones míticas", "relacion": "incluye"}, {"origen": "Crítica literaria", "destino": "Hipótesis interpretativa", "relacion": "analiza"}, {"origen": "Crítica literaria", "destino": "Sentido de la obra", "relacion": "explica"}, {"origen": "Contexto histórico", "destino": "Sentido de la obra", "relacion": "influye en"}], "requiere_revision_humana": true}', 5, 0, true, '{"interpretación literaria","mapa conceptual","pensamiento crítico","OA crear"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (692, 146, 'open_ended', 'all', '{"pregunta": "Elige una obra literaria que hayas leído en este curso y formula una interpretación original que integre una hipótesis sobre su sentido, una crítica personal fundamentada, alusiones a mitos o símbolos presentes en el texto, y el contexto histórico-social en que fue escrita. Explica cómo estos elementos se relacionan y aportan a una comprensión profunda de la obra.", "explicacion": "El docente debe evaluar que el estudiante no solo identifique elementos del texto, sino que construya una interpretación propia, sustentada en un análisis profundo que vincule hipótesis, crítica, simbolismos y contexto histórico, demostrando dominio de la materia y capacidad de creación.", "contexto_adicional": "La respuesta debe ser un texto argumentativo que demuestre análisis crítico y creatividad, con citas o referencias al texto literario seleccionado.", "criterios_evaluacion": ["Formulación de una hipótesis original y coherente sobre el sentido de la obra (peso: 30%)", "Integración fundamentada de una crítica personal que considere aspectos históricos, sociales o universales (peso: 40%)", "Incorporación clara y pertinente de alusiones míticas o simbólicas, y contextualización histórica (peso: 30%)"]}', '{"puntos_clave": ["Hipótesis original sobre el sentido de la obra", "Crítica personal fundamentada en contexto histórico-social", "Alusiones a mitos o símbolos presentes en el texto"], "respuesta_modelo": "En la obra \"La casa de Bernarda Alba\" de Federico García Lorca, propongo que el sentido principal es una crítica a la opresión social y patriarcal en la España rural de los años 30. Mi hipótesis es que la obra utiliza la figura de Bernarda como símbolo del autoritarismo implacable que limita la libertad individual, especialmente femenina. Esta interpretación se enriquece con alusiones míticas, como la referencia al mito de Antígona, donde la resistencia a la autoridad se convierte en un acto trágico. También es fundamental considerar el contexto histórico de la dictadura de Primo de Rivera y las tensiones sociales previas a la Guerra Civil española, que reflejan un ambiente de represión y conflicto. Así, la obra no solo critica una realidad histórica específica, sino que plantea una reflexión universal sobre la libertad y la opresión, haciendo que su mensaje siga vigente.", "requiere_revision_humana": true}', 5, 0, true, '{"interpretación literaria","pensamiento crítico","creación textual"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (694, 57, 'concept_map', 'all', '{"conceptos": ["Romanticismo", "Neoclasicismo", "Características del Romanticismo", "Características del Neoclasicismo", "Contexto histórico", "Temas literarios", "Emociones y sentimientos", "Racionalidad", "Naturaleza", "Individualismo"], "explicacion": "Se espera que el estudiante identifique las características y contextos históricos tanto del Romanticismo como del Neoclasicismo, y que analice cómo los temas literarios, las emociones, la racionalidad, la naturaleza y el individualismo se manifiestan en cada movimiento, estableciendo similitudes y diferencias entre ambos.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de los movimientos literarios del Romanticismo y Neoclasicismo?"}', '{"conceptos_centrales": ["Romanticismo", "Neoclasicismo"], "relaciones_esperadas": [{"origen": "Romanticismo", "destino": "Características del Romanticismo", "relacion": "se caracteriza por"}, {"origen": "Neoclasicismo", "destino": "Características del Neoclasicismo", "relacion": "se caracteriza por"}, {"origen": "Características del Romanticismo", "destino": "Emociones y sentimientos", "relacion": "incluyen"}, {"origen": "Características del Neoclasicismo", "destino": "Racionalidad", "relacion": "incluyen"}, {"origen": "Romanticismo", "destino": "Contexto histórico", "relacion": "se desarrolló en"}, {"origen": "Neoclasicismo", "destino": "Contexto histórico", "relacion": "se desarrolló en"}, {"origen": "Características del Romanticismo", "destino": "Naturaleza", "relacion": "valoran"}, {"origen": "Características del Romanticismo", "destino": "Individualismo", "relacion": "promueven"}, {"origen": "Características del Neoclasicismo", "destino": "Racionalidad", "relacion": "promueven"}, {"origen": "Romanticismo", "destino": "Neoclasicismo", "relacion": "contrasta con"}], "requiere_revision_humana": true}', 3, 0, true, '{Romanticismo,Neoclasicismo,MapaConceptual,AnálisisLiterario}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (695, 96, 'multiple_choice', 'all', '{"opciones": {"A": "Elegir un tema interesante sin considerar quién leerá el texto", "B": "Organizar las ideas y adecuar el vocabulario según el destinatario y propósito", "C": "Escribir sin hacer un esquema previo para que sea más espontáneo", "D": "Usar siempre un lenguaje técnico sin importar el público"}, "pregunta": "¿Cuál de los siguientes elementos es fundamental para planificar y escribir un texto considerando el contexto, destinatario y propósito?", "explicacion": "La respuesta correcta es la opción B porque planificar un texto implica organizar las ideas y ajustar el vocabulario según el destinatario y el propósito para que el mensaje sea claro y efectivo. La opción A es incorrecta porque el contexto y el destinatario sí deben considerarse para elegir el tema y la forma de abordar el texto. La opción C es errónea porque la planificación previa ayuda a estructurar mejor las ideas. La opción D es un error común ya que usar lenguaje técnico sin importar el público puede dificultar la comprensión del texto."}', '{"respuesta_correcta": "B"}', 1, 0, true, '{planificación,vocabulario,texto,contexto,destinatario}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (696, 138, 'concept_map', 'all', '{"conceptos": ["Información fidedigna", "Investigación previa", "Progresión temática", "Coherencia temática", "Audiencia"], "explicacion": "Se espera que el estudiante muestre cómo la información fidedigna se obtiene a través de una investigación previa, cómo esta información debe organizarse siguiendo una progresión temática clara para mantener la coherencia temática durante la exposición, y cómo esto se adapta para comunicar de manera adecuada a la audiencia.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de una exposición oral efectiva?"}', '{"conceptos_centrales": ["Información fidedigna", "Progresión temática"], "relaciones_esperadas": [{"origen": "Investigación previa", "destino": "Información fidedigna", "relacion": "genera"}, {"origen": "Información fidedigna", "destino": "Progresión temática", "relacion": "se organiza mediante"}, {"origen": "Progresión temática", "destino": "Coherencia temática", "relacion": "permite mantener"}, {"origen": "Coherencia temática", "destino": "Audiencia", "relacion": "asegura comunicación clara con"}], "requiere_revision_humana": true}', 3, 0, true, '{"Lengua y Literatura","Exposición Oral","Mapa Conceptual","Primer Año Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (697, 18, 'concept_map', 'all', '{"conceptos": ["Idea principal", "Idea secundaria", "Resumen", "Organización", "Propósito comunicativo"], "explicacion": "Se espera que los estudiantes conecten la ''Idea principal'' como núcleo central que se complementa con las ''Ideas secundarias''. La ''Organización'' es el proceso que permite ordenar estas ideas para construir un ''Resumen'' coherente que responda a un ''Propósito comunicativo'' específico.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de sintetizar y ordenar ideas de un texto?"}', '{"conceptos_centrales": ["Idea principal", "Resumen"], "relaciones_esperadas": [{"origen": "Idea principal", "destino": "Idea secundaria", "relacion": "se complementa con"}, {"origen": "Idea principal", "destino": "Organización", "relacion": "es organizada mediante"}, {"origen": "Organización", "destino": "Resumen", "relacion": "permite construir"}, {"origen": "Resumen", "destino": "Propósito comunicativo", "relacion": "cumple con"}], "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Mapa Conceptual",Síntesis,"Organización de Ideas"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (698, 35, 'criteria_evaluation', 'all', '{"rubrica": {"Justificación de la evaluación": {"Excelente": "Ofrece una justificación clara, coherente y argumentada que sustenta la calificación asignada a cada criterio, utilizando ejemplos concretos del texto.", "Satisfactorio": "Proporciona una justificación general para las calificaciones, con algunos ejemplos, pero que podría ser más detallada o precisa.", "Necesita mejorar": "No justifica adecuadamente las calificaciones o las justificaciones carecen de coherencia y ejemplos."}, "Relación con el contexto histórico y su relevancia actual": {"Excelente": "Establece una conexión precisa entre el contexto histórico de la narración y su repercusión en la sociedad actual, valorando críticamente la continuidad o cambio de dichas creencias y prejuicios.", "Satisfactorio": "Menciona el contexto histórico y su relación con la narración, señalando alguna relevancia actual, aunque con explicaciones limitadas o poco profundas.", "Necesita mejorar": "Presenta dificultades para relacionar el contexto histórico con la narración y su vigencia en la actualidad, con explicaciones confusas o insuficientes."}, "Análisis de la influencia de creencias, prejuicios y estereotipos": {"Excelente": "Identifica claramente las creencias, prejuicios y estereotipos presentes en la narración y explica con profundidad cómo afectan el desarrollo de la historia y las relaciones entre personajes.", "Satisfactorio": "Reconoce algunas creencias y prejuicios relevantes y explica de forma general su impacto en la trama y en los personajes.", "Necesita mejorar": "Muestra dificultad para identificar creencias o prejuicios y su influencia en la narración, realizando explicaciones superficiales o poco claras."}}, "explicacion": "Se espera que el estudiante identifique cómo las creencias, prejuicios y estereotipos condicionan la trama y los personajes, considerando el contexto histórico de Chile y reflexionando sobre su vigencia actual. Además, debe argumentar su evaluación en base a evidencias del texto y ofrecer una justificación coherente para cada criterio.", "instruccion": "Evalúa el fragmento de la narración según los criterios de la rúbrica y justifica cada una de tus calificaciones con argumentos claros y ejemplos del texto.", "caso_a_evaluar": "Lee el fragmento de la narración ''La casa de los espíritus'' de Isabel Allende, en el que se presentan conflictos entre personajes con creencias y prejuicios marcados por el contexto histórico de Chile en el siglo XX. Analiza cómo estas creencias y estereotipos influyen en el desarrollo de la trama y en las relaciones entre personajes, y evalúa su relevancia en la sociedad actual."}', '{"evaluacion_modelo": {"Justificación de la evaluación": "Satisfactorio", "Relación con el contexto histórico y su relevancia actual": "Excelente", "Análisis de la influencia de creencias, prejuicios y estereotipos": "Satisfactorio"}, "justificacion_modelo": "La evaluación reconoce que el estudiante identifica algunas creencias y prejuicios pero con explicaciones generales (Satisfactorio). Sin embargo, establece una conexión clara y profunda entre el contexto histórico y su relevancia actual (Excelente). La justificación es coherente pero podría ser más detallada, por lo que recibe Satisfactorio.", "requiere_revision_humana": true}', 4, 0, true, '{"Lengua y Literatura",Narración,"Análisis crítico",Evaluación,"Contexto histórico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (699, 52, 'open_ended', 'all', '{"pregunta": "Después de haber leído varias tragedias clásicas, analiza y argumenta cómo la visión de mundo expresada en estas obras refleja y critica el contexto social de su época. ¿De qué manera crees que estas tragedias siguen siendo relevantes para comprender problemas sociales actuales?", "explicacion": "El docente debe evaluar que el estudiante presente una argumentación estructurada que demuestre comprensión profunda de la visión de mundo en las tragedias y cómo esta crítica social está enmarcada en su contexto. Además, debe valorar la capacidad del alumno para conectar esos elementos con situaciones o problemas contemporáneos, valorando la originalidad y pertinencia de sus reflexiones.", "contexto_adicional": "Considera las características propias de las tragedias clásicas y su contexto cultural, histórico y social. Reflexiona también sobre cómo estos temas pueden resonar en la sociedad actual.", "criterios_evaluacion": ["Claridad y coherencia en la argumentación sobre la visión de mundo en las tragedias (peso: 30%)", "Capacidad para relacionar la visión de mundo con el contexto social histórico y su crítica (peso: 40%)", "Originalidad y profundidad en la reflexión sobre la relevancia contemporánea de las tragedias (peso: 30%)"]}', '{"puntos_clave": ["Relación entre la visión de mundo en las tragedias y el contexto social histórico", "Identificación de elementos críticos sobre la sociedad en las obras", "Reflexión sobre la relevancia actual de los temas tratados en las tragedias"], "respuesta_modelo": "Las tragedias clásicas, como las obras de Sófocles o Eurípides, muestran una visión de mundo marcada por la inevitabilidad del destino y el sufrimiento humano, lo que refleja las tensiones sociales y políticas de su tiempo, como el conflicto entre el poder y la moralidad. Estas obras critican la injusticia y la arrogancia de los líderes, mostrando las consecuencias trágicas de sus acciones. Esta visión sigue siendo relevante hoy, porque en la actualidad también enfrentamos problemas similares como el abuso de poder y la lucha por la justicia social. Por lo tanto, las tragedias clásicas nos invitan a reflexionar sobre la condición humana y los desafíos sociales que persisten, ayudándonos a comprender y cuestionar nuestro propio contexto.", "requiere_revision_humana": true}', 5, 0, true, '{"Lengua y Literatura","Evaluación de Tragedias","Pensamiento Crítico"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (700, 58, 'criteria_evaluation', 'all', '{"rubrica": {"Claridad y coherencia en la argumentación": {"Excelente": "El ensayo está bien estructurado, con ideas claras, coherentes y bien conectadas, facilitando la comprensión del lector.", "Satisfactorio": "El ensayo presenta una estructura básica y coherente, aunque algunas ideas pueden ser poco claras o desconectadas.", "Necesita mejorar": "El ensayo carece de estructura clara, con ideas confusas o desconectadas que dificultan la comprensión."}, "Valoración del legado actual del Romanticismo": {"Excelente": "El estudiante valora el legado del Romanticismo en la actualidad con argumentos convincentes y ejemplos que demuestran su pertinencia y vigencia.", "Satisfactorio": "El estudiante hace una valoración general del legado actual, pero sin fundamentación sólida.", "Necesita mejorar": "El estudiante no logra establecer una valoración clara o presenta ideas confusas respecto al legado actual."}, "Comprensión del contexto histórico y literario": {"Excelente": "El estudiante explica de manera clara y precisa el contexto histórico y literario del Romanticismo, mostrando un conocimiento profundo y detallado.", "Satisfactorio": "El estudiante menciona el contexto histórico y literario, pero con explicaciones generales o poco detalladas.", "Necesita mejorar": "El estudiante presenta confusiones o información incorrecta sobre el contexto histórico y literario."}, "Justificación del impacto en la sociedad de la época": {"Excelente": "El estudiante argumenta con ejemplos claros y pertinentes cómo el Romanticismo influyó en la sociedad de su tiempo, demostrando reflexión crítica.", "Satisfactorio": "El estudiante menciona el impacto en la sociedad de la época pero con argumentos superficiales o poco desarrollados.", "Necesita mejorar": "El estudiante no logra justificar adecuadamente el impacto en la sociedad de la época o presenta argumentos irrelevantes."}}, "explicacion": "Se espera que el estudiante demuestre capacidad de evaluación crítica, identificando fortalezas y debilidades en el análisis del Romanticismo, con fundamentación clara y coherente en relación con el contexto histórico, impacto social, legado actual y calidad argumentativa.", "instruccion": "Evalúa el ensayo presentado según la rúbrica proporcionada y justifica tu calificación para cada criterio, explicando los aspectos que sustentan tu valoración.", "caso_a_evaluar": "Se presenta un ensayo argumentativo donde un estudiante analiza la importancia del Romanticismo en la historia literaria y cultural, destacando sus características principales, el contexto histórico en que surgió, y su impacto tanto en la sociedad de esa época como en la actualidad."}', '{"evaluacion_modelo": {"Claridad y coherencia en la argumentación": "Excelente", "Valoración del legado actual del Romanticismo": "Satisfactorio", "Comprensión del contexto histórico y literario": "Excelente", "Justificación del impacto en la sociedad de la época": "Satisfactorio"}, "justificacion_modelo": "El ensayo presenta un conocimiento profundo y preciso del contexto histórico y literario, por lo que se califica como excelente en ese criterio. Sin embargo, la justificación del impacto en la sociedad y la valoración del legado actual son algo superficiales y poco desarrolladas, alcanzando un nivel satisfactorio. La estructura y coherencia del ensayo son muy buenas, con ideas claras y bien conectadas, por lo que se otorga excelente en claridad y coherencia.", "requiere_revision_humana": true}', 5, 0, true, '{Romanticismo,"Evaluación crítica","Lengua y Literatura","Primer Medio"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (701, 117, 'concept_map', 'all', '{"conceptos": ["Ortografía literal", "Ortografía acentual", "Errores ortográficos", "Puntuación", "Corrección de textos"], "explicacion": "Se espera que el estudiante conecte cómo la ortografía literal y acentual son fundamentales para evitar errores ortográficos, cómo la puntuación influye en la claridad del texto, y cómo la identificación de errores ortográficos y de puntuación permite realizar correcciones que mejoran la comprensión del lector.", "instruccion": "Crea un mapa conceptual que muestre las relaciones entre los siguientes conceptos", "pregunta_guia": "¿Cómo se relacionan estos conceptos en el contexto de la escritura correcta para facilitar la comprensión del lector?"}', '{"conceptos_centrales": ["Ortografía literal", "Ortografía acentual"], "relaciones_esperadas": [{"origen": "Ortografía literal", "destino": "Errores ortográficos", "relacion": "previene"}, {"origen": "Ortografía acentual", "destino": "Errores ortográficos", "relacion": "previene"}, {"origen": "Errores ortográficos", "destino": "Corrección de textos", "relacion": "afectan"}, {"origen": "Puntuación", "destino": "Corrección de textos", "relacion": "influye en"}, {"origen": "Corrección de textos", "destino": "Comprensión del lector", "relacion": "mejora"}], "requiere_revision_humana": true}', 3, 0, true, '{ortografía,puntuación,"mapa conceptual","comprensión lectora"}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
INSERT INTO questions (id, oa_bloom_objective_id, tipo, tipo_uso, question_data, validation_data, dificultad_relativa, veces_usada, activa, tags, created_at, updated_at) VALUES (702, 133, 'criteria_evaluation', 'all', '{"rubrica": {"Mantenimiento del foco": {"Excelente": "Mantiene el tema central durante todo el debate, sin desviarse ni perder el hilo argumentativo.", "Satisfactorio": "Generalmente se mantiene en el tema, pero presenta algunas desviaciones menores que no afectan el desarrollo del debate.", "Necesita mejorar": "Se desvía frecuentemente del tema, dificultando la continuidad y comprensión del debate."}, "Comprensión del interlocutor": {"Excelente": "Demuestra claramente que entiende las ideas y argumentos del otro, respondiendo de manera pertinente y respetuosa.", "Satisfactorio": "Muestra comprensión general de las ideas del interlocutor, aunque en ocasiones la respuesta no es completamente adecuada.", "Necesita mejorar": "No evidencia comprensión de los argumentos del otro, respondiendo de manera inapropiada o confusa."}, "Fundamentación de la postura": {"Excelente": "Presenta argumentos sólidos, bien fundamentados con información relevante y adecuada para apoyar su posición.", "Satisfactorio": "Da razones aceptables para su postura, aunque la fundamentación podría ser más clara o más vinculada al tema.", "Necesita mejorar": "Ofrece argumentos débiles o poco relacionados que no sustentan de manera efectiva su posición."}, "Negociación y toma de turnos": {"Excelente": "Justifica claramente la importancia de negociar turnos y busca acuerdos respetuosos para un diálogo constructivo.", "Satisfactorio": "Reconoce la necesidad de turnarse y negociar, pero la justificación o aplicación es limitada o poco clara.", "Necesita mejorar": "No considera la negociación ni la toma de turnos, dificultando el diálogo respetuoso."}}, "explicacion": "Se espera que el estudiante analice cómo se mantuvo el foco, si hubo comprensión del interlocutor, la calidad de la fundamentación y la negociación de turnos, justificando con ejemplos del caso la evaluación que otorga.", "instruccion": "Evalúa la actuación de los participantes en el debate descrito según los criterios de la rúbrica y justifica tu calificación para cada uno.", "caso_a_evaluar": "Durante un debate en clase sobre el impacto de las redes sociales en la comunicación juvenil, uno de los participantes propone una idea, pero otro interrumpe para presentar un argumento contrario. Luego, ambos logran negociar un acuerdo para turnarse y respetar la opinión del otro, fundamentando sus posturas con información pertinente y manteniendo el foco en el tema."}', '{"evaluacion_modelo": {"Mantenimiento del foco": "Excelente", "Comprensión del interlocutor": "Satisfactorio", "Fundamentación de la postura": "Excelente", "Negociación y toma de turnos": "Excelente"}, "justificacion_modelo": "El grupo mantuvo el tema principal durante todo el debate, mostrando un enfoque claro. Aunque hubo comprensión general de los argumentos, en algunos momentos la respuesta fue poco precisa. Los argumentos presentados fueron sólidos y bien fundamentados, y se justificó claramente la importancia de negociar y respetar los turnos para lograr un diálogo constructivo.", "requiere_revision_humana": true}', 5, 0, true, '{debate,dialogo_constructivo,evaluacion,primeromedia,lengua_y_literatura}',NOW(), NOW()) ON CONFLICT (id) DO UPDATE SET oa_bloom_objective_id = EXCLUDED.oa_bloom_objective_id, tipo = EXCLUDED.tipo, question_data = EXCLUDED.question_data, validation_data = EXCLUDED.validation_data, dificultad_relativa = EXCLUDED.dificultad_relativa, veces_usada = EXCLUDED.veces_usada, tags = EXCLUDED.tags, activa = EXCLUDED.activa, tipo_uso = EXCLUDED.tipo_uso, updated_at = NOW();
