-- Migración 20: Seed de Preguntas del Banco - Lengua y Literatura
-- Descripción: Carga de 672 preguntas asociadas a los OAs de Lengua
-- Fecha: 2025-11-22
-- Nota: Incluye question_data y validation_data en formato JSON

-- ============================================================================
-- QUESTIONS (Banco de Preguntas)
-- ============================================================================


-- ============================================================================
-- VERIFICACIÓN
-- ============================================================================

DO $$
DECLARE
    questions_count INTEGER;
BEGIN
    -- Contar preguntas asociadas a bloom objectives de Lengua
    SELECT COUNT(*) INTO questions_count
    FROM questions
    WHERE oa_bloom_objective_id IN (
        SELECT id FROM oa_bloom_objectives
        WHERE oa_id IN (SELECT id FROM objetivos_aprendizaje WHERE materia_id = 2)
    );

    IF questions_count != 672 THEN
        RAISE EXCEPTION 'Error: se esperaban 672 preguntas, pero hay %', questions_count;
    END IF;

    RAISE NOTICE 'Seed completado: 672 preguntas del banco de Lengua cargadas correctamente';
END $$;
